  INSERT INTO words (word, word_type, description) VALUES  (    'Divertive', 'a.', 'Tending to divert; diverting; amusing; interesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dives', 'n.', 'The name popularly given to the rich man in our Lords\n   parable of the \"Rich Man and Lazarus\" (Luke xvi. 19-31). Hence, a name\n   for a rich worldling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divested', 'imp. & p. p.', 'of Divest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divesting', 'p. pr. & vb. n.', 'of Divest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divest', 'v. t.', 'To unclothe; to strip, as of clothes, arms, or equipage;\n   -- opposed to invest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divest', 'v. t.', 'Fig.: To strip; to deprive; to dispossess; as, to divest\n   one of his rights or privileges; to divest ones self of prejudices,\n   passions, etc.');
