  INSERT INTO words (word, word_type, description) VALUES  (    'Dividing', 'p. pr. & vb. n.', 'of Divide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divide', 'v. t.', 'To part asunder (a whole); to sever into two or more\n   parts or pieces; to sunder; to separate into parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divide', 'v. t.', 'To cause to be separate; to keep apart by a partition,\n   or by an imaginary line or limit; as, a wall divides two houses; a\n   stream divides the towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divide', 'v. t.', 'To make partition of among a number; to apportion, as\n   profits of stock among proprietors; to give in shares; to distribute;\n   to mete out; to share.');
