  INSERT INTO words (word, word_type, description) VALUES  (    'Divide', 'v. i.', 'To vote, as in the British Parliament, by the members\n   separating themselves into two parties (as on opposite sides of the\n   hall or in opposite lobbies), that is, the ayes dividing from the noes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divide', 'n.', 'A dividing ridge of land between the tributaries of two\n   streams; a watershed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divided', 'a.', 'Parted; disunited; distributed.');
