  INSERT INTO words (word, word_type, description) VALUES  (    'Dividedly', 'adv.', 'Separately; in a divided manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dividend', 'n.', 'A sum of money to be divided and distributed; the share\n   of a sum divided that falls to each individual; a distribute sum,\n   share, or percentage; -- applied to the profits as appropriated among\n   shareholders, and to assets as apportioned among creditors; as, the\n   dividend of a bank, a railway corporation, or a bankrupt estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dividend', 'n.', 'A number or quantity which is to be divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divident', 'n.', 'Dividend; share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divider', 'n.', 'One who, or that which, divides; that which separates\n   anything into parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divider', 'n.', 'One who deals out to each his share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divider', 'n.', 'One who, or that which, causes division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divider', 'n.', 'An instrument for dividing lines, describing circles,\n   etc., compasses. See Compasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dividing', 'a.', 'That divides; separating; marking divisions; graduating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dividingly', 'adv.', 'By division.');
