  INSERT INTO words (word, word_type, description) VALUES  (    'Dividually', 'adv.', 'By dividing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dividuous', 'a.', 'Divided; dividual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divination', 'n.', 'The act of divining; a foreseeing or foretelling of\n   future events; the pretended art discovering secret or future by\n   preternatural means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divination', 'n.', 'An indication of what is future or secret; augury omen;\n   conjectural presage; prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinator', 'n.', 'One who practices or pretends to divination; a diviner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinatory', 'a.', 'Professing, or relating to, divination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'Of or belonging to God; as, divine perfections; the divine\n   will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'Proceeding from God; as, divine judgments.');
