  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'Presageful; foreboding; prescient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'Relating to divinity or theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'One skilled in divinity; a theologian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'a.', 'A minister of the gospel; a priest; a clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divined', 'imp. & p. p.', 'of Divine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divining', 'p. pr. & vb. n.', 'of Divine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. t.', 'To foresee or foreknow; to detect; to anticipate; to\n   conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. t.', 'To foretell; to predict; to presage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. t.', 'To render divine; to deify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. i.', 'To use or practice divination; to foretell by\n   divination; to utter prognostications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. i.', 'To have or feel a presage or foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divine', 'v. i.', 'To conjecture or guess; as, to divine rightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinely', 'adv.', 'In a divine or godlike manner; holily; admirably or\n   excellently in a supreme degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinely', 'adv.', 'By the agency or influence of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinement', 'n.', 'Divination.');
