  INSERT INTO words (word, word_type, description) VALUES  (    'Divineress', 'n.', 'A woman who divines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diving', 'a.', 'That dives or is used or diving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinify', 'v. t.', 'To render divine; to deify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divining', 'a.', 'That divines; for divining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diviningly', 'adv.', 'In a divining manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinistre', 'n.', 'A diviner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinities', 'pl. ', 'of Divinity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'The state of being divine; the nature or essence of God;\n   deity; godhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'The Deity; the Supreme Being; God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'A pretended deity of pagans; a false god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'A celestial being, inferior to the supreme God, but\n   superior to man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'Something divine or superhuman; supernatural power or\n   virtue; something which inspires awe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divinity', 'a.', 'The science of divine things; the science which treats of\n   God, his laws and moral government, and the way of salvation; theology.');
