  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'That which divides or keeps apart; a partition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'The portion separated by the divining of a mass or body;\n   a distinct segment or section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'Disunion; difference in opinion or feeling; discord;\n   variance; alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'Difference of condition; state of distinction;\n   distinction; contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'Separation of the members of a deliberative body, esp. of\n   the Houses of Parliament, to ascertain the vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'The process of finding how many times one number or\n   quantity is contained in another; the reverse of multiplication; also,\n   the rule by which the operation is performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'The separation of a genus into its constituent species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'Two or more brigades under the command of a general\n   officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'Two companies of infantry maneuvering as one subdivision\n   of a battalion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'One of the larger districts into which a country is\n   divided for administering military affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'One of the groups into which a fleet is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'A course of notes so running into each other as to form\n   one series or chain, to be sung in one breath to one syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'The distribution of a discourse into parts; a part so\n   distinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Division', 'n.', 'A grade or rank in classification; a portion of a tribe\n   or of a class; or, in some recent authorities, equivalent to a\n   subkingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisional', 'a.', 'That divides; pertaining to, making, or noting, a\n   division; as, a divisional line; a divisional general; a divisional\n   surgeon of police.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisionally', 'adv.', 'So as to be divisional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisionary', 'a.', 'Divisional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisionor', 'n.', 'One who divides or makes division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisive', 'a.', 'Indicating division or distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisive', 'a.', 'Creating, or tending to create, division, separation, or\n   difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divisor', 'n.', 'The number by which the dividend is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'A legal dissolution of the marriage contract by a court or\n   other body having competent authority. This is properly a divorce, and\n   called, technically, divorce a vinculo matrimonii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'The separation of a married woman from the bed and board\n   of her husband -- divorce a mensa et toro (/ thoro), \"from bed board.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'The decree or writing by which marriage is dissolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'Separation; disunion of things closely united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'That which separates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorced', 'imp. & p. p.', 'of Divorce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcing', 'p. pr. & vb. n.', 'of Divorce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'To dissolve the marriage contract of, either wholly or\n   partially; to separate by divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'To separate or disunite; to sunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorce', 'n.', 'To make away; to put away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorceable', 'a.', 'Capable of being divorced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcee', 'n.', 'A person divorced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorceless', 'a.', 'Incapable of being divorced or separated; free from\n   divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcement', 'n.', 'Dissolution of the marriage tie; divorce; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcer', 'n.', 'The person or cause that produces or effects a divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcible', 'a.', 'Divorceable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divorcive', 'a.', 'Having power to divorce; tending to divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divot', 'n.', 'A thin, oblong turf used for covering cottages, and also for\n   fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulgate', 'a.', 'Published.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulgate', 'v. t.', 'To divulge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulgater', 'n.', 'A divulger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulgation', 'n.', 'The act of divulging or publishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulged', 'imp. & p. p.', 'of Divulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulging', 'p. pr. & vb. n.', 'of Divulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulge', 'v. t.', 'To make public; to several or communicate to the\n   public; to tell (a secret) so that it may become generally known; to\n   disclose; -- said of that which had been confided as a secret, or had\n   been before unknown; as, to divulge a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulge', 'v. t.', 'To indicate publicly; to proclaim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulge', 'v. t.', 'To impart; to communicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulge', 'v. i.', 'To become publicly known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divulsive', 'a.', 'Tending to pull asunder, tear, or rend; distracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dixie', 'n.', 'A colloquial name for the Southern portion of the United\n   States, esp. during the Civil War.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizened', 'imp. & p. p.', 'of Dizen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizening', 'p. pr. & vb. n.', 'of Dizen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizen', 'v. t.', 'To dress; to attire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizen', 'v. t.', 'To dress gaudily; to overdress; to bedizen; to deck out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizz', 'v. t.', 'To make dizzy; to astonish; to puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzard', 'n.', 'A blockhead. [Obs.] [Written also dizard, and disard.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzily', 'adv.', 'In a dizzy manner or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizziness', 'n.', 'Giddiness; a whirling sensation in the head; vertigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzy', 'superl.', 'Having in the head a sensation of whirling, with a\n   tendency to fall; vertiginous; giddy; hence, confused; indistinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzy', 'superl.', 'Causing, or tending to cause, giddiness or vertigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzy', 'superl.', 'Without distinct thought; unreflecting; thoughtless;\n   heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzied', 'imp. & p. p.', 'of Dizzy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzying', 'p. pr. & vb. n.', 'of Dizzy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dizzy', 'v. t.', 'To make dizzy or giddy; to give the vertigo to; to\n   confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Djereed', 'n.', 'Alt. of Djerrid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Djerrid', 'n.', 'A blunt javelin used in military games in Moslem\n   countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Djerrid', 'n.', 'A game played with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jjinn', 'pl. ', 'of Djinnee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Djinns', 'pl. ', 'of Djinnee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Djinnee', 'n.', 'See Jinnee, Jinn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do.', 'n.', 'An abbreviation of Ditto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'n.', 'A syllable attached to the first tone of the major diatonic\n   scale for the purpose of solmization, or solfeggio. It is the first of\n   the seven syllables used by the Italians as manes of musical tones, and\n   replaced, for the sake of euphony, the syllable Ut, applied to the note\n   C. In England and America the same syllables are used by mane as a\n   scale pattern, while the tones in respect to absolute pitch are named\n   from the first seven letters of the alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Din', 'imp.', 'of Do');
  INSERT INTO words (word, word_type, description) VALUES  (    'Done', 'p. p.', 'of Do');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doing', 'p. pr. & vb. n.', 'of Do');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To place; to put.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To cause; to make; -- with an infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To bring about; to produce, as an effect or\n   result; to effect; to achieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To perform, as an action; to execute; to\n   transact to carry out in action; as, to do a good or a bad act; do our\n   duty; to do what I can.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To bring to an end by action; to perform\n   completely; to finish; to accomplish; -- a sense conveyed by the\n   construction, which is that of the past participle done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To make ready for an object, purpose, or use, as\n   food by cooking; to cook completely or sufficiently; as, the meat is\n   done on one side only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To put or bring into a form, state, or\n   condition, especially in the phrases, to do death, to put to death; to\n   slay; to do away (often do away with), to put away; to remove; to do\n   on, to put on; to don; to do off, to take off, as dress; to doff; to do\n   into, to put into the form of; to translate or transform into, as a\n   text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To cheat; to gull; to overreach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To see or inspect; to explore; as, to do all the\n   points of interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. t. / auxiliary', 'To cash or to advance money for, as a bill or\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. i.', 'To act or behave in any manner; to conduct ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. i.', 'To fare; to be, as regards health; as, they asked him how he\n   did; how do you do to-day?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'v. i.', 'To succeed; to avail; to answer the purpose; to serve; as,\n   if no better plan can be found, he will make this do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'n.', 'Deed; act; fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'n.', 'Ado; bustle; stir; to do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do', 'n.', 'A cheat; a swindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doab', '', 'A tongue or tract of land included between two rivers; as, the\n   doab between the Ganges and the Jumna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doable', 'a.', 'Capable of being done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-all', 'n.', 'General manager; factotum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doand', 'p. pr.', 'Doing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doat', 'v. i.', 'See Dote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobber', 'n.', 'See Dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobber', 'n.', 'A float to a fishing line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobbin', 'n.', 'An old jaded horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobbin', 'n.', 'Sea gravel mixed with sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobchick', 'n.', 'See Dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobson', 'n.', 'The aquatic larva of a large neuropterous insect (Corydalus\n   cornutus), used as bait in angling. See Hellgamite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dobule', 'n.', 'The European dace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docent', 'a.', 'Serving to instruct; teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docetae', 'n. pl.', 'Ancient heretics who held that Christs body was\n   merely a phantom or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docetic', 'a.', 'Pertaining to, held by, or like, the Docetae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docetism', 'n.', 'The doctrine of the Docetae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dochmiac', 'a.', 'Pertaining to, or containing, the dochmius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dochmius', 'n.', 'A foot of five syllables (usually / -- -/ -).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docibility', 'n.', 'Alt. of Docibleness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docibleness', 'n.', 'Aptness for being taught; teachableness; docility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docible', 'a.', 'Easily taught or managed; teachable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docile', 'a.', 'Teachable; easy to teach; docible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docile', 'a.', 'Disposed to be taught; tractable; easily managed; as, a\n   docile child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docility', 'n.', 'teachableness; aptness for being taught; docibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docility', 'n.', 'Willingness to be taught; tractableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docimacy', 'n.', 'The art or practice of applying tests to ascertain the\n   nature, quality, etc., of objects, as of metals or ores, of medicines,\n   or of facts pertaining to physiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docimastic', 'a.', 'Proving by experiments or tests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docimology', 'n.', 'A treatise on the art of testing, as in assaying\n   metals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docity', 'n.', 'Teachableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'A genus of plants (Rumex), some species of which are\n   well-known weeds which have a long taproot and are difficult of\n   extermination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'The solid part of an animals tail, as distinguished from the\n   hair; the stump of a tail; the part of a tail left after clipping or\n   cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'A case of leather to cover the clipped or cut tail of a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docked', 'imp. & p. p.', 'of Dock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docking', 'p. pr. & vb. n.', 'of Dock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'v. t.', 'to cut off, as the end of a thing; to curtail; to cut\n   short; to clip; as, to dock the tail of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'v. t.', 'To cut off a part from; to shorten; to deduct from; to\n   subject to a deduction; as, to dock ones wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'v. t.', 'To cut off, bar, or destroy; as, to dock an entail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'An artificial basin or an inclosure in connection with a\n   harbor or river, -- used for the reception of vessels, and provided\n   with gates for keeping in or shutting out the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'The slip or water way extending between two piers or\n   projecting wharves, for the reception of ships; -- sometimes including\n   the piers themselves; as, to be down on the dock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'n.', 'The place in court where a criminal or accused person stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock', 'v. t.', 'To draw, law, or place (a ship) in a dock, for repairing,\n   cleaning the bottom, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dockage', 'n.', 'A charge for the use of a dock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dock-cress', 'n.', 'Nipplewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'n.', 'A small piece of paper or parchment, containing the heads\n   of a writing; a summary or digest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'n.', 'A bill tied to goods, containing some direction, as the\n   name of the owner, or the place to which they are to be sent; a label.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'n.', 'An abridged entry of a judgment or proceeding in an action,\n   or register or such entries; a book of original, kept by clerks of\n   courts, containing a formal list of the names of parties, and minutes\n   of the proceedings, in each case in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'n.', 'A list or calendar of causes ready for hearing or trial,\n   prepared for the use of courts by the clerks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'n.', 'A list or calendar of business matters to be acted on in\n   any assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docketed', 'imp. & p. p.', 'of Docket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docketing', 'p. pr. & vb. n.', 'of Docket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'v. t.', 'To make a brief abstract of (a writing) and indorse it\n   on the back of the paper, or to indorse the title or contents on the\n   back of; to summarize; as, to docket letters and papers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'v. t.', 'To make a brief abstract of and inscribe in a book; as,\n   judgments regularly docketed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'v. t.', 'To enter or inscribe in a docket, or list of causes for\n   trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docket', 'v. t.', 'To mark with a ticket; as, to docket goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dockyard', 'n.', 'A yard or storage place for all sorts of naval stores and\n   timber for shipbuilding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docoglossa', 'n. pl.', 'An order of gastropods, including the true limpets,\n   and having the teeth on the odontophore or lingual ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Docquet', 'n. & v.', 'See Docket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'n.', 'A teacher; one skilled in a profession, or branch of\n   knowledge learned man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'n.', 'An academical title, originally meaning a men so well\n   versed in his department as to be qualified to teach it. Hence: One who\n   has taken the highest degree conferred by a university or college, or\n   has received a diploma of the highest degree; as, a doctor of divinity,\n   of law, of medicine, of music, or of philosophy. Such diplomas may\n   confer an honorary title only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'n.', 'One duly licensed to practice medicine; a member of the\n   medical profession; a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'n.', 'Any mechanical contrivance intended to remedy a difficulty\n   or serve some purpose in an exigency; as, the doctor of a\n   calico-printing machine, which is a knife to remove superfluous\n   coloring matter; the doctor, or auxiliary engine, called also donkey\n   engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'n.', 'The friar skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctored', 'imp. & p. p.', 'of Doctor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctoring', 'p. pr. & vb. n.', 'of Doctor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'v. t.', 'To treat as a physician does; to apply remedies to; to\n   repair; as, to doctor a sick man or a broken cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'v. t.', 'To confer a doctorate upon; to make a doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'v. t.', 'To tamper with and arrange for ones own purposes; to\n   falsify; to adulterate; as, to doctor election returns; to doctor\n   whisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctor', 'v. i.', 'To practice physic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctoral', 'a.', 'Of or relating to a doctor, or to the degree of doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctorally', 'adv.', 'In the manner of a doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctorate', 'n.', 'The degree, title, or rank, of a doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctorate', 'v. t.', 'To make (one) a doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctoress', 'n.', 'A female doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctorly', 'a.', 'Like a doctor or learned man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctorship', 'n.', 'Doctorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctress', 'n.', 'A female doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinable', 'a.', 'Of the nature of, or constituting, doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinaire', 'n.', 'One who would apply to political or other practical\n   concerns the abstract doctrines or the theories of his own\n   philosophical system; a propounder of a new set of opinions; a dogmatic\n   theorist. Used also adjectively; as, doctrinaire notions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinal', 'a.', 'Pertaining to, or containing, doctrine or something\n   taught and to be believed; as, a doctrinal observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinal', 'a.', 'Pertaining to, or having to do with, teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinal', 'n.', 'A matter of doctrine; also, a system of doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinally', 'adv.', 'In a doctrinal manner or for; by way of teaching or\n   positive direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinarian', 'n.', 'A doctrinaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrinarianism', 'n.', 'The principles or practices of the Doctrinaires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrine', 'n.', 'Teaching; instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doctrine', 'n.', 'That which is taught; what is held, put forth as true,\n   and supported by a teacher, a school, or a sect; a principle or\n   position, or the body of principles, in any branch of knowledge; any\n   tenet or dogma; a principle of faith; as, the doctrine of atoms; the\n   doctrine of chances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Document', 'n.', 'That which is taught or authoritatively set forth;\n   precept; instruction; dogma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Document', 'n.', 'An example for instruction or warning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Document', 'n.', 'An original or official paper relied upon as the basis,\n   proof, or support of anything else; -- in its most extended sense,\n   including any writing, book, or other instrument conveying information\n   in the case; any material substance on which the thoughts of men are\n   represented by any species of conventional mark or symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Document', 'v. t.', 'To teach; to school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Document', 'v. t.', 'To furnish with documents or papers necessary to\n   establish facts or give information; as, a a ship should be documented\n   according to the directions of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Documental', 'a.', 'Of or pertaining to instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Documental', 'a.', 'Of or pertaining to written evidence; documentary; as,\n   documental testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Documentary', 'a.', 'Pertaining to written evidence; contained or certified\n   in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodd', 'v. t.', 'Alt. of Dod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dod', 'v. t.', 'To cut off, as wool from sheeps tails; to lop or clip off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doddart', 'n.', 'A game much like hockey, played in an open field; also,\n   the, bent stick for playing the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodded', 'a.', 'Without horns; as, dodded cattle; without beards; as,\n   dodded corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodder', 'n.', 'A plant of the genus Cuscuta. It is a leafless parasitical\n   vine with yellowish threadlike stems. It attaches itself to some other\n   plant, as to flax, goldenrod, etc., and decaying at the root, is\n   nourished by the plant that supports it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodder', 'v. t. & i.', 'To shake, tremble, or totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doddered', 'a.', 'Shattered; infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecagon', 'n.', 'A figure or polygon bounded by twelve sides and\n   containing twelve angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecagynia', 'n. pl.', 'A Linnaean order of plants having twelve styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecagynian', 'a.', 'Alt. of Dodecagynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecagynous', 'a.', 'Of or pertaining to the Dodecagynia; having twelve\n   styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecahedral', 'a.', 'Pertaining to, or like, a dodecahedion; consisting of\n   twelve equal sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecahedron', 'n.', 'A solid having twelve faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecandria', 'n. pl.', 'A Linnaean class of plants including all that have\n   any number of stamens between twelve and nineteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecandrian', 'a.', 'Alt. of Dodecandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecandrous', 'a.', 'Of or pertaining to the Dodecandria; having twelve\n   stamens, or from twelve to nineteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecane', 'n.', 'Any one of a group of thick oily hydrocarbons, C12H26, of\n   the paraffin series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecastyle', 'a.', 'Having twelve columns in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecastyle', 'n.', 'A dodecastyle portico, or building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecasyllabic', 'a.', 'Having twelve syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecasyllable', 'n.', 'A word consisting of twelve syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodecatemory', 'n.', 'A tern applied to the twelve houses, or parts, of the\n   zodiac of the primum mobile, to distinguish them from the twelve signs;\n   also, any one of the twelve signs of the zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodged', 'imp. & p. p.', 'of Dodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodging', 'p. pr. & vb. n.', 'of Dodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'v. i.', 'To start suddenly aside, as to avoid a blow or a missile;\n   to shift place by a sudden start.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'v. i.', 'To evade a duty by low craft; to practice mean shifts; to\n   use tricky devices; to play fast and loose; to quibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'v. t.', 'To evade by a sudden shift of place; to escape by\n   starting aside; as, to dodge a blow aimed or a ball thrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'v. t.', 'Fig.: To evade by craft; as, to dodge a question; to\n   dodge responsibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'v. t.', 'To follow by dodging, or suddenly shifting from place to\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodge', 'n.', 'The act of evading by some skillful movement; a sudden\n   starting aside; hence, an artful device to evade, deceive, or cheat; a\n   cunning trick; an artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodger', 'n.', 'One who dodges or evades; one who plays fast and loose, or\n   uses tricky devices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodger', 'n.', 'A small handbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodger', 'n.', 'See Corndodger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodgery', 'n.', 'trickery; artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodipate', 'n.', 'Alt. of Dodipoll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodipoll', 'n.', 'A stupid person; a fool; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodkin', 'n.', 'A doit; a small coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodman', 'n.', 'A snail; also, a snail shell; a hodmandod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodman', 'n.', 'Any shellfish which casts its shell, as a lobster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodoes', 'pl. ', 'of Dodo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dodo', 'n.', 'A large, extinct bird (Didus ineptus), formerly inhabiting\n   the Island of Mauritius. It had short, half-fledged wings, like those\n   of the ostrich, and a short neck and legs; -- called also dronte. It\n   was related to the pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doe',    'n.',    'A female deer or antelope; specifically, the female of the\n   fallow deer, of which the male is called a buck. Also applied to the\n   female of other animals, as the rabbit. See the Note under Buck.');
