INSERT INTO words (word, word_type, description)VALUES  (    'Doe',    'n.',    'A feat. [Obs.] See Do, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doeglic', 'a.', 'Pertaining to, or obtained from, the doegling; as, doeglic\n   acid (Chem.), an oily substance resembling oleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doegling', 'n.', 'The beaked whale (Balaenoptera rostrata), from which\n   doegling oil is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doer', 'v. t. & i.', 'One who does; one performs or executes; one who is\n   wont and ready to act; an actor; an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doer', 'v. t. & i.', 'An agent or attorney; a factor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Does', '', 'The 3d pers. sing. pres. of Do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doeskin', 'n.', 'The skin of the doe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doeskin', 'n.', 'A firm woolen cloth with a smooth, soft surface like a\n   does skin; -- made for mens wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doffed', 'imp. & p. p.', 'of Doff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doffing', 'p. pr. & vb. n.', 'of Doff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doff', 'v. t.', 'To put off, as dress; to divest ones self of; hence,\n   figuratively, to put or thrust away; to rid ones self of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doff', 'v. t.', 'To strip; to divest; to undress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doff', 'v. i.', 'To put off dress; to take off the hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doffer', 'n.', 'A revolving cylinder, or a vibrating bar with teeth, in a\n   carding machine, which doffs, or strips off, the cotton from the cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'A quadruped of the genus Canis, esp. the domestic dog (C.\n   familiaris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'A mean, worthless fellow; a wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'A fellow; -- used humorously or contemptuously; as, a sly dog;\n   a lazy dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'One of the two constellations, Canis Major and Canis Minor, or\n   the Greater Dog and the Lesser Dog. Canis Major contains the Dog Star\n   (Sirius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'An iron for holding wood in a fireplace; a firedog; an\n   andiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'A grappling iron, with a claw or claws, for fastening into\n   wood or other heavy articles, for the purpose of raising or moving\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'An iron with fangs fastening a log in a saw pit, or on the\n   carriage of a sawmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'n.', 'A piece in machinery acting as a catch or clutch; especially,\n   the carrier of a lathe, also, an adjustable stop to change motion, as\n   in a machine tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogged', 'imp. & p. p.', 'of Dog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogging', 'p. pr. & vb. n.', 'of Dog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog', 'v. t.', 'To hunt or track like a hound; to follow insidiously or\n   indefatigably; to chase with a dog or dogs; to worry, as if by dogs; to\n   hound with importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogal', 'a.', 'Of or pertaining to a doge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogate', 'n.', 'The office or dignity of a doge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogbane', 'n.', 'A small genus of perennial herbaceous plants, with\n   poisonous milky juice, bearing slender pods pods in pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog bee', '', 'A male or drone bee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogberry', 'n.', 'The berry of the dogwood; -- called also dogcherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogbolt', 'n.', 'The bolt of the cap-square over the trunnion of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-brier', 'n.', 'The dog-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogcart', 'n.', 'A light one-horse carriage, commonly two-wheeled,\n   patterned after a cart. The original dogcarts used in England by\n   sportsmen had a box at the back for carrying dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog day', '', 'Alt. of Dogday');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogday', '', 'One of the dog days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog days', '', 'A period of from four to six weeks, in the summer,\n   variously placed by almanac makers between the early part of July and\n   the early part of September; canicular days; -- so called in reference\n   to the rising in ancient times of the Dog Star (Sirius) with the sun.\n   Popularly, the sultry, close part of the summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogdraw', 'n.', 'The act of drawing after, or pursuing, deer with a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doge', 'n.', 'The chief magistrate in the republics of Venice and Genoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-eared', 'a.', 'Having the corners of the leaves turned down and soiled\n   by careless or long-continued usage; -- said of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogeate', 'n.', 'Dogate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogeless', 'a.', 'Without a doge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-faced', 'a.', 'Having a face resembling that of a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog fancier', '', 'One who has an unusual fancy for, or interest in, dogs;\n   also, one who deals in dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogfish', 'n.', 'A small shark, of many species, of the genera Mustelus,\n   Scyllium, Spinax, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogfish', 'n.', 'The bowfin (Amia calva). See Bowfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogfish', 'n.', 'The burbot of Lake Erie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-fox', 'n.', 'A male fox. See the Note under Dog, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-fox', 'n.', 'The Arctic or blue fox; -- a name also applied to species\n   of the genus Cynalopex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogged', 'a.', 'Sullen; morose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogged', 'a.', 'Sullenly obstinate; obstinately determined or persistent;\n   as, dogged resolution; dogged work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggedly', 'adv.', 'In a dogged manner; sullenly; with obstinate\n   resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggedness', 'n.', 'Sullenness; moroseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggedness', 'n.', 'Sullen or obstinate determination; grim resolution or\n   persistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogger', 'n.', 'A two-masted fishing vessel, used by the Dutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogger', 'n.', 'A sort of stone, found in the mines with the true alum\n   rock, chiefly of silica and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggerel', 'a.', 'Low in style, and irregular in measure; as, doggerel\n   rhymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggerel', 'n.', 'A sort of loose or irregular verse; mean or undignified\n   poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggerman', 'n.', 'A sailor belonging to a dogger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogget', 'n.', 'Docket. See Docket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggish', 'a.', 'Like a dog; having the bad qualities of a dog; churlish;\n   growling; brutal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doggrel', 'a. & n.', 'Same as Doggerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-headed', 'a.', 'Having a head shaped like that of a dog; -- said of\n   certain baboons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-hearted', 'a.', 'Inhuman; cruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doghole', 'n.', 'A place fit only for dogs; a vile, mean habitation or\n   apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'dog-legged', 'a.', 'Noting a flight of stairs, consisting of two or more\n   straight portions connected by a platform (landing) or platforms, and\n   running in opposite directions without an intervening wellhole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmas', 'pl. ', 'of Dogma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmata', 'pl. ', 'of Dogma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogma', 'n.', 'That which is held as an opinion; a tenet; a doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogma', 'n.', 'A formally stated and authoritatively settled doctrine; a\n   definite, established, and authoritative tenet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogma', 'n.', 'A doctrinal notion asserted without regard to evidence or\n   truth; an arbitrary dictum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatic', 'n.', 'One of an ancient sect of physicians who went by general\n   principles; -- opposed to the Empiric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatic', 'a.', 'Alt. of Dogmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatical', 'a.', 'Pertaining to a dogma, or to an established and\n   authorized doctrine or tenet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatical', 'a.', 'Asserting a thing positively and authoritatively;\n   positive; magisterial; hence, arrogantly authoritative; overbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatically', 'adv.', 'In a dogmatic manner; positively; magisterially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmaticalness', 'n.', 'The quality of being dogmatical; positiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatician', 'n.', 'A dogmatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatics', 'n.', 'The science which treats of Christian doctrinal\n   theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatism', 'n.', 'The manner or character of a dogmatist; arrogance or\n   positiveness in stating opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatist', 'n.', 'One who dogmatizes; one who speaks dogmatically; a bold\n   and arrogant advancer of principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatized', 'imp. & p. p.', 'of Dogmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatizing', 'p. pr. & vb. n.', 'of Dogmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatize', 'v. i.', 'To assert positively; to teach magisterially or with\n   bold and undue confidence; to advance with arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatize', 'v. t.', 'To deliver as a dogma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogmatizer', 'n.', 'One who dogmatizes; a bold asserter; a magisterial\n   teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-rose', 'n.', 'A common European wild rose, with single pink or white\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogs-bane', 'n.', 'See Dogbane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogs-ear', 'n.', 'The corner of a leaf, in a book, turned down like the\n   ear of a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogship', 'n.', 'The character, or individuality, of a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogshore', 'n.', 'One of several shores used to hold a ship firmly and\n   prevent her moving while the blocks are knocked away before launching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogsick', 'a.', 'Sick as a dog sometimes is very sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogskin', 'n.', 'The skin of a dog, or leather made of the skin. Also used\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogsleep', 'n.', 'Pretended sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogsleep', 'n.', 'The fitful naps taken when all hands are kept up by\n   stress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogs-tail grass', 'n.', 'A hardy species of British grass (Cynosurus\n   cristatus) which abounds in grass lands, and is well suited for making\n   straw plait; -- called also goldseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog Star', '', 'Sirius, a star of the constellation Canis Major, or the\n   Greater Dog, and the brightest star in the heavens; -- called also\n   Canicula, and, in astronomical charts, / Canis Majoris. See Dog days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogs-tongue', 'n.', 'Hounds-tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogtie', 'n.', 'A cramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogteeth', 'pl. ', 'of Dogtooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogtooth', 'n.', 'See Canine tooth, under Canine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogtooth', 'n.', 'An ornament common in Gothic architecture, consisting of\n   pointed projections resembling teeth; -- also called tooth ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogtrick', 'n.', 'A gentle trot, like that of a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogvane', 'n.', 'A small vane of bunting, feathers, or any other light\n   material, carried at the masthead to indicate the direction of the\n   wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogwatch', 'n.', 'A half watch; a watch of two hours, of which there are\n   two, the first dogwatch from 4 to 6 oclock, p. m., and the second\n   dogwatch from 6 to 8 oclock, p. m.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dog-weary', 'a.', 'Extremely weary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dogwood', 'n.', 'The Cornus, a genus of large shrubs or small trees, the\n   wood of which is exceedingly hard, and serviceable for many purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dohtren', 'n. pl.', 'Daughters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doily', 'n.', 'A kind of woolen stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doily', 'n.', 'A small napkin, used at table with the fruit, etc.; --\n   commonly colored and fringed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doings', 'pl. ', 'of Doing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doing', 'n.', 'Anything done; a deed; an action good or bad; hence, in the\n   plural, conduct; behavior. See Do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doit', 'n.', 'A small Dutch coin, worth about half a farthing; also, a\n   similar small coin once used in Scotland; hence, any small piece of\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doit', 'n.', 'A thing of small value; as, I care not a doit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doitkin', 'n.', 'A very small coin; a doit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dokimastic', 'a.', 'Docimastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doko', 'n.', 'See Lepidosiren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolabra', 'n.', 'A rude ancient ax or hatchet, seen in museums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolabriform', 'a.', 'Shaped like the head of an ax or hatchet, as some\n   leaves, and also certain organs of some shellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolce', 'adv.', 'Alt. of Dolcemente');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolcemente', 'adv.', 'Softly; sweetly; with soft, smooth, and delicate\n   execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolcino', 'n.', 'Alt. of Dulcino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcino', 'n.', 'A small bassoon, formerly much used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doldrums', 'n. pl.', 'A part of the ocean near the equator, abounding in\n   calms, squalls, and light, baffling winds, which sometimes prevent all\n   progress for weeks; -- so called by sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'grief; sorrow; lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'See Dolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'Distribution; dealing; apportionment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'That which is dealt out; a part, share, or portion also, a\n   scanty share or allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'Alms; charitable gratuity or portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'A boundary; a landmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'n.', 'A void space left in tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doled', 'imp. & p. p.', 'of Dole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doling', 'p. pr. & vb. n.', 'of Dole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dole', 'v. t.', 'To deal out in small portions; to distribute, as a dole;\n   to deal out scantily or grudgingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doleful', 'a.', 'Full of dole or grief; expressing or exciting sorrow;\n   sorrowful; sad; dismal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolent', 'a.', 'Sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolente', 'a. & adv.', 'Plaintively. See Doloroso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolerite', 'n.', 'A dark-colored, basic, igneous rock, composed essentially\n   of pyroxene and a triclinic feldspar with magnetic iron. By many\n   authors it is considered equivalent to a coarse-grained basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doleritic', 'a.', 'Of the nature of dolerite; as, much lava is doleritic\n   lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolesome', 'a.', 'Doleful; dismal; gloomy; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolf', 'imp.', 'of Delve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolichocephalic', 'a.', 'Alt. of Dolichocephalous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolichocephalous', 'a.', 'Having the cranium, or skull, long to its\n   breadth; long-headed; -- opposed to brachycephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolichocephaly', 'n.', 'Alt. of Dolichocephalism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolichocephalism', 'n.', 'The quality or condition of being\n   dolichocephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolioform', 'a.', 'Barrel-shaped, or like a cask in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doliolum', 'n.', 'A genus of freeswimming oceanic tunicates, allied to\n   Salpa, and having alternate generations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-little', 'n.', 'One who performs little though professing much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolium', 'n.', 'A genus of large univalve mollusks, including the partridge\n   shell and tun shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doll', 'n.', 'A childs puppet; a toy baby for a little girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollar', 'n.', 'A silver coin of the United States containing 371.25 grains\n   of silver and 41.25 grains of alloy, that is, having a total weight of\n   412.5 grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollar', 'n.', 'A gold coin of the United States containing 23.22 grains of\n   gold and 2.58 grains of alloy, that is, having a total weight of 25.8\n   grains, nine-tenths fine. It is no longer coined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollar', 'n.', 'A coin of the same general weight and value, though\n   differing slightly in different countries, current in Mexico, Canada,\n   parts of South America, also in Spain, and several other European\n   countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollar', 'n.', 'The value of a dollar; the unit commonly employed in the\n   United States in reckoning money values.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollardee', 'n.', 'A species of sunfish (Lepomis pallidus), common in the\n   United States; -- called also blue sunfish, and copper-nosed bream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollman', 'n.', 'See Dolman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dollies', 'pl. ', 'of Dolly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'A contrivance, turning on a vertical axis by a handle or\n   winch, and giving a circular motion to the ore to be washed; a stirrer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'A tool with an indented head for shaping the head of a\n   rivet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'In pile driving, a block interposed between the head of the\n   pile and the ram of the driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'A small truck with a single wide roller used for moving\n   heavy beams, columns, etc., in bridge building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'A compact, narrow-gauge locomotive used for moving\n   construction trains, switching, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly', 'n.', 'A childs mane for a doll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly Varden', '', 'A character in Dickenss novel \"Barnaby Rudge,\" a\n   beautiful, lively, and coquettish girl who wore a cherry-colored mantle\n   and cherry-colored ribbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolly Varden', '', 'A style of light, bright-figured dress goods for women;\n   also, a style of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolman', 'n.', 'A long robe or outer garment, with long sleeves, worn by\n   the Turks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolman', 'n.', 'A cloak of a peculiar fashion worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolmen', 'n.', 'A cromlech. See Cromlech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolomite', 'n.', 'A mineral consisting of the carbonate of lime and\n   magnesia in varying proportions. It occurs in distinct crystals, and in\n   extensive beds as a compact limestone, often crystalline granular,\n   either white or clouded. It includes much of the common white marble.\n   Also called bitter spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolomitic', 'a.', 'Pertaining to dolomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolomize', 'v. t.', 'To convert into dolomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolor', 'n.', 'Pain; grief; distress; anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doloriferous', 'a.', 'Producing pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolorific', 'a.', 'Alt. of Dolorifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolorifical', 'a.', 'Causing pain or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doloroso', 'a. & adv.', 'Plaintive; pathetic; -- used adverbially as a\n   musical direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolorous', 'a.', 'Full of grief; sad; sorrowful; doleful; dismal; as, a\n   dolorous object; dolorous discourses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolorous', 'a.', 'Occasioning pain or grief; painful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A cetacean of the genus Delphinus and allied genera (esp.\n   D. delphis); the true dolphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'The Coryphaena hippuris, a fish of about five feet in\n   length, celebrated for its surprising changes of color when dying. It\n   is the fish commonly known as the dolphin. See Coryphaenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A mass of iron or lead hung from the yardarm, in readiness\n   to be dropped on the deck of an enemys vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A kind of wreath or strap of plaited cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A spar or buoy held by an anchor and furnished with a ring\n   to which ships may fasten their cables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A mooring post on a wharf or beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A permanent fender around a heavy boat just below the\n   gunwale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'In old ordnance, one of the handles above the trunnions by\n   which the gun was lifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphin', 'n.', 'A small constellation between Aquila and Pegasus. See\n   Delphinus, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolphinet', 'n.', 'A female dolphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolt', 'n.', 'A heavy, stupid fellow; a blockhead; a numskull; an\n   ignoramus; a dunce; a dullard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolt', 'v. i.', 'To behave foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doltish', 'a.', 'Doltlike; dull in intellect; stupid; blockish; as, a\n   doltish clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolus', 'n.', 'Evil intent, embracing both malice and fraud. See Culpa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dolven', 'p. p.', 'of Delve.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-dom', '', 'A suffix denoting');
  INSERT INTO words (word, word_type, description) VALUES  (    '-dom', '', 'Jurisdiction or property and jurisdiction, dominion, as in\n   kingdom earldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-dom', '', 'State, condition, or quality of being, as in wisdom, freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dom', 'n.', 'A title anciently given to the pope, and later to other church\n   dignitaries and some monastic orders. See Don, and Dan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dom', 'n.', 'In Portugal and Brazil, the title given to a member of the\n   higher classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domable', 'a.', 'Capable of being tamed; tamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domableness', 'n.', 'Tamableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domage', 'n.', 'Damage; hurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domage', 'n.', 'Subjugation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domain', 'n.', 'Dominion; empire; authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domain', 'n.', 'The territory over which dominion or authority is exerted;\n   the possessions of a sovereign or commonwealth, or the like. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domain', 'n.', 'Landed property; estate; especially, the land about the\n   mansion house of a lord, and in his immediate occupancy; demesne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domain', 'n.', 'Ownership of land; an estate or patrimony which one has in\n   his own right; absolute proprietorship; paramount or sovereign\n   ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domal', 'a.', 'Pertaining to a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domanial', 'a.', 'Of or relating to a domain or to domains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dome', 'n.', 'A building; a house; an edifice; -- used chiefly in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dome', 'n.', 'A cupola formed on a large scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dome', 'n.', 'Any erection resembling the dome or cupola of a building; as\n   the upper part of a furnace, the vertical steam chamber on the top of a\n   boiler, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dome', 'n.', 'A prism formed by planes parallel to a lateral axis which\n   meet above in a horizontal edge, like the roof of a house; also, one of\n   the planes of such a form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dome', 'n.', 'Decision; judgment; opinion; a court decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domebook', 'n.', 'A book said to have been compiled under the direction of\n   King Alfred. It is supposed to have contained the principal maxims of\n   the common law, the penalties for misdemeanors, and the forms of\n   judicial proceedings. Domebook was probably a general name for book of\n   judgments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domed', 'a.', 'Furnished with a dome; shaped like a dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesday', 'n.', 'A day of judgment. See Doomsday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesmen', 'pl. ', 'of Domesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesman', 'n.', 'A judge; an umpire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'a.', 'Of or pertaining to ones house or home, or ones\n   household or family; relating to home life; as, domestic concerns,\n   life, duties, cares, happiness, worship, servants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'a.', 'Of or pertaining to a nation considered as a family or\n   home, or to ones own country; intestine; not foreign; as, foreign wars\n   and domestic dissensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'a.', 'Remaining much at home; devoted to home duties or\n   pleasures; as, a domestic man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'a.', 'Living in or near the habitations of man; domesticated;\n   tame as distinguished from wild; as, domestic animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'a.', 'Made in ones own house, nation, or country; as, domestic\n   manufactures, wines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'n.', 'One who lives in the family of an other, as hired\n   household assistant; a house servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestic', 'n.', 'Articles of home manufacture, especially cotton goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestical', 'a.', 'Domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestical', 'n.', 'A family; a household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestically', 'adv.', 'In a domestic manner; privately; with reference to\n   domestic affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticant', 'a.', 'Forming part of the same family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticated', 'imp. & p. p.', 'of Domesticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticating.', 'p. pr. & vb. n.', 'of Domesticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticate', 'a.', 'To make domestic; to habituate to home life; as, to\n   domesticate ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticate', 'a.', 'To cause to be, as it were, of ones family or\n   country; as, to domesticate a foreign custom or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticate', 'a.', 'To tame or reclaim from a wild state; as, to\n   domesticate wild animals; to domesticate a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domestication', 'n.', 'The act of domesticating, or accustoming to home;\n   the action of taming wild animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticator', 'n.', 'One who domesticates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domesticity', 'n.', 'The state of being domestic; domestic character;\n   household life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domett', 'n.', 'A kind of baize of which the ward is cotton and the weft\n   woolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domeykite', 'n.', 'A massive mineral of tin-white or steel-gray color, an\n   arsenide of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domical', 'a.', 'Relating to, or shaped like, a dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domicile', 'n.', 'An abode or mansion; a place of permanent residence,\n   either of an individual or a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domicile', 'n.', 'A residence at a particular place accompanied with an\n   intention to remain there for an unlimited time; a residence accepted\n   as a final abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciled', 'imp. & p. p.', 'of Domicile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciling', 'p. pr. & vb. n.', 'of Domicile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domicile', 'v. t.', 'To establish in a fixed residence, or a residence that\n   constitutes habitancy; to domiciliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliar', 'n.', 'A member of a household; a domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domicillary', 'a.', 'Of or pertaining to a domicile, or the residence of a\n   person or family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliated', 'imp. & p. p.', 'of Domiciliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliating', 'p. pr. & vb. n.', 'of Domiciliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliate', 'v. t.', 'To establish in a permanent residence; to domicile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliate', 'v. t.', 'To domesticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiciliation', 'n.', 'The act of domiciliating; permanent residence;\n   inhabitancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domiculture', 'n.', 'The art of house-keeping, cookery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domify', 'v. t.', 'To divide, as the heavens, into twelve houses. See\n   House, in astrological sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domify', 'v. t.', 'To tame; to domesticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domina', 'n.', 'Lady; a lady; -- a title formerly given to noble ladies who\n   held a barony in their own right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominance', 'n.', 'Alt. of Dominancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominancy', 'n.', 'Predominance; ascendency; authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominant', 'a.', 'Ruling; governing; prevailing; controlling; predominant;\n   as, the dominant party, church, spirit, power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominant', 'n.', 'The fifth tone of the scale; thus G is the dominant of C,\n   A of D, and so on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominated', 'imp. & p. p.', 'of Dominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominating', 'p. pr. & vb. n.', 'of Dominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominate', 'v. t.', 'To predominate over; to rule; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominate', 'v. i.', 'To be dominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domination', 'n.', 'The act of dominating; exercise of power in ruling;\n   dominion; supremacy; authority; often, arbitrary or insolent sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domination', 'n.', 'A ruling party; a party in power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domination', 'n.', 'A high order of angels in the celestial hierarchy; -- a\n   meaning given by the schoolmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominative', 'a.', 'Governing; ruling; imperious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominator', 'n.', 'A ruler or ruling power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domine', 'n.', 'A name given to a pastor of the Reformed Church. The word\n   is also applied locally in the United States, in colloquial speech, to\n   any clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domine', 'n.', 'A West Indian fish (Epinula magistralis), of the family\n   Trichiuridae. It is a long-bodied, voracious fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domineered', 'imp. & p. p.', 'of Domineer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domineering', 'p. pr. & vb. n.', 'of Domineer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domineer', 'v. t.', 'To rule with insolence or arbitrary sway; to play the\n   master; to be overbearing; to tyrannize; to bluster; to swell with\n   conscious superiority or haughtiness; -- often with over; as, to\n   domineer over dependents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domineering', 'a.', 'Ruling arrogantly; overbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominical', 'a.', 'Indicating, or pertaining to, the Lords day, or Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominical', 'a.', 'Relating to, or given by, our Lord; as, the dominical\n   (or Lords) prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominical', 'n.', 'The Lords day or Sunday; also, the Lords prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominican', 'a.', 'Of or pertaining to St. Dominic (Dominic de Guzman), or\n   to the religions communities named from him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominican', 'n.', 'One of an order of mendicant monks founded by Dominic de\n   Guzman, in 1215. A province of the order was established in England in\n   1221. The first foundation in the United States was made in 1807. The\n   Master of the Sacred Palace at Rome is always a Dominican friar. The\n   Dominicans are called also preaching friars, friars preachers, black\n   friars (from their black cloak), brothers of St. Mary, and in France,\n   Jacobins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominicide', 'n.', 'The act of killing a master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominicide', 'n.', 'One who kills his master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominie', 'n.', 'A schoolmaster; a pedagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominie', 'n.', 'A clergyman. See Domine, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominion', 'n.', 'Sovereign or supreme authority; the power of governing\n   and controlling; independent right of possession, use, and control;\n   sovereignty; supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominion', 'n.', 'Superior prominence; predominance; ascendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominion', 'n.', 'That which is governed; territory over which authority is\n   exercised; the tract, district, or county, considered as subject; as,\n   the dominions of a king. Also used figuratively; as, the dominion of\n   the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominion', 'n.', 'A supposed high order of angels; dominations. See\n   Domination, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominos', 'pl. ', 'of Domino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominoes', 'pl. ', 'of Domino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A kind of hood worn by the canons of a cathedral church; a\n   sort of amice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A mourning veil formerly worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A kind of mask; particularly, a half mask worn at\n   masquerades, to conceal the upper part of the face. Dominos were\n   formerly worn by ladies in traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A costume worn as a disguise at masquerades, consisting of\n   a robe with a hood adjustable at pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A person wearing a domino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'A game played by two or more persons, with twenty-eight\n   pieces of wood, bone, or ivory, of a flat, oblong shape, plain at the\n   back, but on the face divided by a line in the middle, and either left\n   blank or variously dotted after the manner of dice. The game is played\n   by matching the spots or the blank of an unmatched half of a domino\n   already played');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domino', 'n.', 'One of the pieces with which the game of dominoes is\n   played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domini', 'pl. ', 'of Dominus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dominus', 'n.', 'Master; sir; -- a title of respect formerly applied to a\n   knight or a clergyman, and sometimes to the lord of a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domitable', 'a.', 'That can be tamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Domite', 'n.', 'A grayish variety of trachyte; -- so called from the\n   Puy-de-Dome in Auvergne, France, where it is found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Don', 'n.', 'Sir; Mr; Signior; -- a title in Spain, formerly given to\n   noblemen and gentlemen only, but now common to all classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Don', 'n.', 'A grand personage, or one making pretension to consequence;\n   especially, the head of a college, or one of the fellows at the English\n   universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donned', 'imp. & p. p.', 'of Don');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donning', 'p. pr. & vb. n.', 'of Don');
  INSERT INTO words (word, word_type, description) VALUES  (    'Don', 'v. t.', 'To put on; to dress in; to invest ones self with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doa', 'n.', 'Lady; mistress; madam; -- a title of respect used in Spain,\n   prefixed to the Christian name of a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donable', 'a.', 'Capable of being donated or given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donary', 'n.', 'A thing given to a sacred use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donat', 'n.', 'A grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donatary', 'n.', 'See Donatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donated', 'imp. & p. p.', 'of Donate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donating', 'p. pr. & vb. n.', 'of Donate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donate', 'v. t.', 'To give; to bestow; to present; as, to donate fifty\n   thousand dollars to a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donation', 'n.', 'The act of giving or bestowing; a grant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donation', 'n.', 'That which is given as a present; that which is\n   transferred to another gratuitously; a gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donation', 'n.', 'The act or contract by which a person voluntarily\n   transfers the title to a thing of which be is the owner, from himself\n   to another, without any consideration, as a free gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donatism', 'n.', 'The tenets of the Donatists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donatist', 'n.', 'A follower of Donatus, the leader of a body of North\n   African schismatics and purists, who greatly disturbed the church in\n   the 4th century. They claimed to be the true church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donatistic', 'a.', 'Pertaining to Donatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donative', 'n.', 'A gift; a largess; a gratuity; a present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donative', 'n.', 'A benefice conferred on a person by the founder or\n   patron, without either presentation or institution by the ordinary, or\n   induction by his orders. See the Note under Benefice, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donative', 'a.', 'Vested or vesting by donation; as, a donative advowson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donator', 'n.', 'One who makes a gift; a donor; a giver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donatory', 'n.', 'A donee of the crown; one the whom, upon certain\n   condition, escheated property is made over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-naught', 'n.', 'A lazy, good-for-nothing fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donax', 'n.', 'A canelike grass of southern Europe (Arundo Donax), used for\n   fishing rods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doncella', 'n.', 'A handsome fish of Florida and the West Indies\n   (Platyglossus radiatus). The name is applied also to the ladyfish\n   (Harpe rufa) of the same region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Done', '', 'p. p. from Do, and formerly the infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Done', 'infinitive.', 'Performed; executed; finished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Done', 'infinitive.', 'It is done or agreed; let it be a match or bargain;\n   -- used elliptically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Done', 'a.', 'Given; executed; issued; made public; -- used chiefly in the\n   clause giving the date of a proclamation or public act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donee', 'n.', 'The person to whom a gift or donation is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donee', 'n.', 'Anciently, one to whom lands were given; in later use, one\n   to whom lands and tenements are given in tail; in modern use, one on\n   whom a power is conferred for execution; -- sometimes called the\n   appointor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donet', 'n.', 'Same as Donat. Piers Plowman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doni', 'n.', 'A clumsy craft, having one mast with a long sail, used for\n   trading purposes on the coasts of Coromandel and Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doniferous', 'a.', 'Bearing gifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donjon', 'n.', 'The chief tower, also called the keep; a massive tower in\n   ancient castles, forming the strongest part of the fortifications. See\n   Illust. of Castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donkeys', 'pl. ', 'of Donkey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donkey', 'n.', 'An ass; or (less frequently) a mule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donkey', 'n.', 'A stupid or obstinate fellow; an ass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donna', 'n.', 'A lady; madam; mistress; -- the title given a lady in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donnat', 'n.', 'See Do-naught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donnism', 'n', 'Self-importance; loftiness of carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donor', 'n.', 'One who gives or bestows; one who confers anything\n   gratuitously; a benefactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donor', 'n.', 'One who grants an estate; in later use, one who confers a\n   power; -- the opposite of donee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-nothing', 'a.', 'Doing nothing; inactive; idle; lazy; as, a do-nothing\n   policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-nothingism', 'n.', 'Alt. of Do-nothingness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Do-nothingness', 'n.', 'Inactivity; habitual sloth; idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donship', 'n.', 'The quality or rank of a don, gentleman, or knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Donzel', 'n.', 'A young squire, or knights attendant; a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doo', 'n.', 'A dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doob grass', '', 'A perennial, creeping grass (Cynodon dactylon), highly\n   prized, in Hindostan, as food for cattle, and acclimated in the United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doodle', 'n.', 'A trifler; a simple fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doodlesack', 'n.', 'The Scotch bagpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doole', 'n.', 'Sorrow; dole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doolies', 'pl. ', 'of Dooly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dooly', 'n.', 'A kind of litter suspended from mens shoulders, for\n   carrying persons or things; a palanquin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'Judgment; judicial sentence; penal decree; condemnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'That to which one is doomed or sentenced; destiny or fate,\n   esp. unhappy destiny; penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'Ruin; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'Discriminating opinion or judgment; discrimination;\n   discernment; decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomed', 'imp. & p. p.', 'of Doom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dooming', 'p. pr. & vb. n.', 'of Doom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'To judge; to estimate or determine as a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'To pronounce sentence or judgment on; to condemn; to\n   consign by a decree or sentence; to sentence; as, a criminal doomed to\n   chains or death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'To ordain as penalty; hence, to mulct or fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'To assess a tax upon, by estimate or at discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom', 'v. t.', 'To destine; to fix irrevocably the destiny or fate of; to\n   appoint, as by decree or by fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomage', 'n.', 'A penalty or fine for neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomful', 'a.', 'Full of condemnation or destructive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doom palm', '', 'A species of palm tree (Hyphaene Thebaica), highly valued\n   for the fibrous pulp of its fruit, which has the flavor of gingerbread,\n   and is largely eaten in Egypt and Abyssinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomsday', 'n.', 'A day of sentence or condemnation; day of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomsday', 'n.', 'The day of the final judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomsman', 'n.', 'A judge; an umpire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doomster', 'n.', 'Same as Dempster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Door', 'n.', 'An opening in the wall of a house or of an apartment, by\n   which to go in and out; an entrance way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Door', 'n.', 'The frame or barrier of boards, or other material, usually\n   turning on hinges, by which an entrance way into a house or apartment\n   is closed and opened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Door', 'n.', 'Passage; means of approach or access.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Door', 'n.', 'An entrance way, but taken in the sense of the house or\n   apartment to which it leads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorcase', 'n.', 'The surrounding frame into which a door shuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorcheek', 'n.', 'The jamb or sidepiece of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorga', 'n.', 'A Hindoo divinity, the consort of Siva, represented with\n   ten arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dooring', 'n.', 'The frame of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorkeeper', 'n.', 'One who guards the entrance of a house or apartment; a\n   porter; a janitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorless', 'a.', 'Without a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doornail', 'n.', 'The nail or knob on which in ancient doors the knocker\n   struck; -- hence the old saying, \"As dead as a doornail.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorplane', 'n.', 'A plane on a door, giving the name, and sometimes the\n   employment, of the occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorpost', 'n.', 'The jamb or sidepiece of a doorway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorsill', 'n.', 'The sill or threshold of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorstead', 'n.', 'Entrance or place of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorstep', 'n.', 'The stone or plank forming a step before an outer door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorstone', 'n.', 'The stone forming a threshold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorstop', 'n.', 'The block or strip of wood or similar material which\n   stops, at the right place, the shutting of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doorway', 'n.', 'The passage of a door; entrance way into a house or a\n   room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dooryard', 'n.', 'A yard in front of a house or around the door of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dop', 'n.', 'Alt. of Doop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doop', 'n.', 'A little copper cup in which a diamond is held while being\n   cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dop', 'v. i.', 'To dip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dop', 'n.', 'A dip; a low courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dopper', 'n.', 'An Anabaptist or Baptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dopplerite', 'n.', 'A brownish black native hydrocarbon occurring in\n   elastic or jellylike masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doquet', 'n.', 'A warrant. See Docket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dor', 'n.', 'A large European scaraboid beetle (Geotrupes stercorarius),\n   which makes a droning noise while flying. The name is also applied to\n   allied American species, as the June bug. Called also dorr, dorbeetle,\n   or dorrbeetle, dorbug, dorrfly, and buzzard clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dor', 'n.', 'A trick, joke, or deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dor', 'v. t.', 'To make a fool of; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorado', 'n.', 'A southern constellation, within which is the south pole of\n   the ecliptic; -- called also sometimes Xiphias, or the Swordfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorado', 'n.', 'A large, oceanic fish of the genus Coryphaena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorbeetle', 'n.', 'See 1st Dor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doree', 'n.', 'A European marine fish (Zeus faber), of a yellow color. See\n   Illust. of John Doree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doretree', 'n.', 'A doorpost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorhawk', 'n.', 'The European goatsucker; -- so called because it eats the\n   dor beetle. See Goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorian', 'a.', 'Of or pertaining to the ancient Greeks of Doris; Doric; as,\n   a Dorian fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorian', 'a.', 'Same as Doric, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorian', 'n.', 'A native or inhabitant of Doris in Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doric', 'a.', 'Pertaining to Doris, in ancient Greece, or to the Dorians;\n   as, the Doric dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doric', 'a.', 'Belonging to, or resembling, the oldest and simplest of the\n   three orders of architecture used by the Greeks, but ranked as second\n   of the five orders adopted by the Romans. See Abacus, Capital, Order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doric', 'a.', 'Of or relating to one of the ancient Greek musical modes or\n   keys. Its character was adapted both to religions occasions and to war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doric', 'n.', 'The Doric dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doricism', 'n.', 'A Doric phrase or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doris', 'n.', 'A genus of nudibranchiate mollusks having a wreath of\n   branchiae on the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorism', 'n.', 'A Doric phrase or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorking fowl', '', 'One of a breed of large-bodied domestic fowls, having\n   five toes, or the hind toe double. There are several strains, as the\n   white, gray, and silver-gray. They are highly esteemed for the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormancy', 'n.', 'The state of being dormant; quiescence; abeyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormant', 'a.', 'Sleeping; as, a dormant animal; hence, not in action or\n   exercise; quiescent; at rest; in abeyance; not disclosed, asserted, or\n   insisted on; as, dormant passions; dormant claims or titles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormant', 'a.', 'In a sleeping posture; as, a lion dormant; --\n   distinguished from couchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormant', 'a.', 'A large beam in the roof of a house upon which portions of\n   the other timbers rest or \" sleep.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormer', 'n.', 'Alt. of Dormer window');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormer window', 'n.', 'A window pierced in a roof, and so set as to be\n   vertical while the roof slopes away from it. Also, the gablet, or\n   houselike structure, in which it is contained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormitive', 'a.', 'Causing sleep; as, the dormitive properties of opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormitive', 'n.', 'A medicine to promote sleep; a soporific; an opiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormitories', 'pl. ', 'of Dormitory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormitory', 'n.', 'A sleeping room, or a building containing a series of\n   sleeping rooms; a sleeping apartment capable of containing many beds;\n   esp., one connected with a college or boarding school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormitory', 'n.', 'A burial place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormice', 'pl. ', 'of Dormouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dormouse', 'n.', 'A small European rodent of the genus Myoxus, of several\n   species. They live in trees and feed on nuts, acorns, etc.; -- so\n   called because they are usually torpid in winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorn', 'n.', 'A British ray; the thornback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dornick', 'n.', 'Alt. of Dornock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dornock', 'n.', 'A coarse sort of damask, originally made at Tournay (in\n   Flemish, Doornick), Belgium, and used for hangings, carpets, etc. Also,\n   a stout figured linen manufactured in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorp', 'n.', 'A hamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorr', 'n.', 'The dorbeetle; also, a drone or an idler. See 1st Dor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorr', 'v. t.', 'To deceive. [Obs.] See Dor, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorr', 'v. t.', 'To deafen with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorrfly', 'n.', 'See 1st Dor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorrhawk', 'n.', 'See Dorhawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsad', 'adv.', 'Toward the dorsum or back; on the dorsal side; dorsally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsal', 'a.', 'Pertaining to, or situated near, the back, or dorsum, of an\n   animal or of one of its parts; notal; tergal; neural; as, the dorsal\n   fin of a fish; the dorsal artery of the tongue; -- opposed to ventral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsal', 'a.', 'Pertaining to the surface naturally inferior, as of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsal', 'a.', 'Pertaining to the surface naturally superior, as of a\n   creeping hepatic moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsal', 'a.', 'A hanging, usually of rich stuff, at the back of a throne,\n   or of an altar, or in any similar position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsale', 'n.', 'Same as Dorsal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsally', 'adv.', 'On, or toward, the dorsum, or back; on the dorsal side\n   of; dorsad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorse', 'n.', 'Same as dorsal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorse', 'n.', 'The back of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorse', 'n.', 'The Baltic or variable cod (Gadus callarias), by some\n   believed to be the young of the common codfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsel', 'n.', 'A pannier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsel', 'n.', 'Same as Dorsal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorser', 'n.', 'See Dosser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'dorsibranchiata', 'n. pl.', 'A division of chaetopod annelids in which the\n   branchiae are along the back, on each side, or on the parapodia. [See\n   Illusts. under Annelida and Chaetopoda.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsibranchiate', 'a.', 'Having branchiae along the back; belonging to the\n   Dorsibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsibranchiate', 'n.', 'One of the Dorsibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsiferous', '', 'Bearing, or producing, on the back; -- applied to ferns\n   which produce seeds on the back of the leaf, and to certain Batrachia,\n   the ova of which become attached to the skin of the back of the parent,\n   where they develop; dorsiparous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsimeson', 'n.', '(Anat.) See Meson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsiparous', 'a.', 'Same as Dorsiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsiventral', 'a.', 'Having distinct upper and lower surfaces, as most\n   common leaves. The leaves of the iris are not dorsiventral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsiventral', 'a.', 'See Dorsoventral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsoventral', 'a.', 'From the dorsal to the ventral side of an animal; as,\n   the dorsoventral axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsum', 'n.', 'The ridge of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorsum', 'n.', 'The back or dorsal region of an animal; the upper side of\n   an appendage or part; as, the dorsum of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dortour', 'n.', 'Alt. of Dorture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dorture', 'n.', 'A dormitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dories', 'pl. ', 'of Dory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dory', 'n.', 'A European fish. See Doree, and John Doree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dory', 'n.', 'The American wall-eyed perch; -- called also dore. See Pike\n   perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dories', 'pl. ', 'of Dory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dory', 'n.', 'A small, strong, flat-bottomed rowboat, with sharp prow and\n   flaring sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doryphora', 'n.', 'A genus of plant-eating beetles, including the potato\n   beetle. See Potato beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doryphoros', 'n.', 'A spear bearer; a statue of a man holding a spear or in\n   the attitude of a spear bearer. Several important sculptures of this\n   subject existed in antiquity, copies of which remain to us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'The quantity of medicine given, or prescribed to be taken, at\n   one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'A sufficient quantity; a portion; as much as one can take, or\n   as falls to one to receive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'Anything nauseous that one is obliged to take; a disagreeable\n   portion thrust upon one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dosed', 'imp. & p. p.', 'of Dose');
  INSERT INTO words (word, word_type, description) VALUES  (    'dosing', 'p. pr. & vb. n.', 'of Dose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'To proportion properly (a medicine), with reference to the\n   patient or the disease; to form into suitable doses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'To give doses to; to medicine or physic to; to give potions\n   to, constantly and without need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dose', 'n.', 'To give anything nauseous to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dosel', 'n.', 'Same as Dorsal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dosology', 'n.', 'Posology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dossel', 'n.', 'Same as Dorsal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dosser', 'n.', 'A pannier, or basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dosser', 'n.', 'A hanging tapestry; a dorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dossil', 'n.', 'A small ovoid or cylindrical roil or pledget of lint, for\n   keeping a sore, wound, etc., open; a tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dossil', 'n.', 'A roll of cloth for wiping off the face of a copperplate,\n   leaving the ink in the engraved lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dost', '2d pers. sing. pres.', 'of Do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'n.', 'A marriage portion; dowry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'n.', 'A small point or spot, made with a pen or other pointed\n   instrument; a speck, or small mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'n.', 'Anything small and like a speck comparatively; a small portion\n   or specimen; as, a dot of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotted', 'imp. & p. p.', 'of Dot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotting', 'p. pr. & vb. n.', 'of Dot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'v. t.', 'To mark with dots or small spots; as, to dot a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'v. t.', 'To mark or diversify with small detached objects; as, a\n   landscape dotted with cottages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dot', 'v. i.', 'To make dots or specks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotage', 'v. i.', 'Feebleness or imbecility of understanding or mind,\n   particularly in old age; the childishness of old age; senility; as, a\n   venerable man, now in his dotage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotage', 'v. i.', 'Foolish utterance; drivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotage', 'v. i.', 'Excessive fondness; weak and foolish affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotal', 'a.', 'Pertaining to dower, or a womans marriage portion;\n   constituting dower, or comprised in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotant', 'n.', 'A dotard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotard', 'v. i.', 'One whose mind is impaired by age; one in second\n   childhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotardly', 'a.', 'Foolish; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotary', 'n.', 'A dotards weakness; dotage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotation', 'n.', 'The act of endowing, or bestowing a marriage portion on a\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotation', 'n.', 'Endowment; establishment of funds for support, as of a\n   hospital or eleemosynary corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'n.', 'A marriage portion. [Obs.] See 1st Dot, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'n.', 'Natural endowments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doted', 'imp. & p. p.', 'of Dote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doting', 'p. pr. & vb. n.', 'of Dote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'v. i.', 'To act foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'v. i.', 'To be weak-minded, silly, or idiotic; to have the\n   intellect impaired, especially by age, so that the mind wanders or\n   wavers; to drivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'v. i.', 'To be excessively or foolishly fond; to love to excess; to\n   be weakly affectionate; -- with on or upon; as, the mother dotes on her\n   child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dote', 'n.', 'An imbecile; a dotard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doted', 'a.', 'Stupid; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doted', 'a.', 'Half-rotten; as, doted wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotehead', 'n.', 'A dotard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doter', 'n.', 'One who dotes; a man whose understanding is enfeebled by\n   age; a dotard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doter', 'n.', 'One excessively fond, or weak in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotery', 'n.', 'The acts or speech of a dotard; drivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doth', '3d pers. sing. pres.', 'of Do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doting', 'a.', 'That dotes; silly; excessively fond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotish', 'a.', 'Foolish; weak; imbecile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dottard', 'n.', 'An old, decayed tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotted', 'a.', 'Marked with, or made of, dots or small spots; diversified\n   with small, detached objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotterel', 'a.', 'Decayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotterel', 'v. i.', 'A European bird of the Plover family (Eudromias, /\n   Charadrius, morinellus). It is tame and easily taken, and is popularly\n   believed to imitate the movements of the fowler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotterel', 'v. i.', 'A silly fellow; a dupe; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dotting pen', '', 'See under Pun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dottrel', 'n.', 'See Dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doty', 'a.', 'Half-rotten; as, doty timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douane', 'n.', 'A customhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douanier', 'n.', 'An officer of the French customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douar', 'n.', 'A village composed of Arab tents arranged in streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douay Bible', '', 'A translation of the Scriptures into the English\n   language for the use of English-speaking Roman Catholics; -- done from\n   the Latin Vulgate by English scholars resident in France. The New\n   Testament portion was published at Rheims, A. D. 1582, the Old\n   Testament at Douai, A. D. 1609-10. Various revised editions have since\n   been published.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doub grass', '', 'Doob grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'Twofold; multiplied by two; increased by its equivalent;\n   made twice as large or as much, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'Being in pairs; presenting two of a kind, or two in a set\n   together; coupled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'Divided into two; acting two parts, one openly and the\n   other secretly; equivocal; deceitful; insincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'Having the petals in a flower considerably increased beyond\n   the natural number, usually as the result of cultivation and the\n   expense of the stamens, or stamens and pistils. The white water lily\n   and some other plants have their blossoms naturally double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'adv.', 'Twice; doubly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubled', 'imp. & p. p.', 'of Double');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubling', 'p. pr. & vb. n.', 'of Double');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'To increase by adding an equal number, quantity, length,\n   value, or the like; multiply by two; to double a sum of money; to\n   double a number, or length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'To make of two thicknesses or folds by turning or bending\n   together in the middle; to fold one part upon another part of; as, to\n   double the leaf of a book, and the like; to clinch, as the fist; --\n   often followed by up; as, to double up a sheet of paper or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'To be the double of; to exceed by twofold; to contain or be\n   worth twice as much as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'To pass around or by; to march or sail round, so as to\n   reverse the direction of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'a.', 'To unite, as ranks or files, so as to form one from each\n   two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'v. i.', 'To be increased to twice the sum, number, quantity,\n   length, or value; to increase or grow to twice as much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'v. i.', 'To return upon ones track; to turn and go back over the\n   same ground, or in an opposite direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'v. i.', 'To play tricks; to use sleights; to play false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'v. i.', 'To set up a word or words a second time by mistake; to\n   make a doublet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'Twice as much; twice the number, sum, quantity, length,\n   value, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'Among compositors, a doublet (see Doublet, 2.); among\n   pressmen, a sheet that is twice pulled, and blurred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'That which is doubled over or together; a doubling; a\n   plait; a fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'A turn or circuit in running to escape pursues; hence, a\n   trick; a shift; an artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'Something precisely equal or counterpart to another; a\n   counterpart. Hence, a wraith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'A player or singer who prepares to take the part of another\n   player in his absence; a substitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'Double beer; strong beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'A feast in which the antiphon is doubled, hat is, said\n   twice, before and after the Psalms, instead of only half being said, as\n   in simple feasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'A game between two pairs of players; as, a first prize for\n   doubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double', 'n.', 'An old term for a variation, as in Bachs Suites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-acting', 'a.', 'Acting or operating in two directions or with both\n   motions; producing a twofold result; as, a double-acting engine or\n   pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-bank', 'v. t.', 'To row by rowers sitting side by side in twos on a\n   bank or thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-banked', 'a.', 'Applied to a kind of rowing in which the rowers sit\n   side by side in twos, a pair of oars being worked from each bank or\n   thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-barreled', 'a.', 'Alt. of -barrelled');
  INSERT INTO words (word, word_type, description) VALUES  (    '-barrelled', 'a.', 'Having two barrels; -- applied to a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-beat valve', '', 'See under Valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-breasted', 'a.', 'Folding or lapping over on the breast, with a row\n   of buttons and buttonholes on each side; as, a double-breasted coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-charge', 'v. t.', 'To load with a double charge, as of gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-charge', 'v. t.', 'To overcharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double dealer', '', 'One who practices double dealing; a deceitful,\n   trickish person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double dealing', '', 'False or deceitful dealing. See Double dealing, under\n   Dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-decker', 'n.', 'A man-of-war having two gun decks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-decker', 'n.', 'A public conveyance, as a street car, with seats on\n   the roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-dye', 'v. t.', 'To dye again or twice over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-dyed', 'a.', 'Dyed twice; thoroughly or intensely colored; hence;\n   firmly fixed in opinions or habits; as, a double-dyed villain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-ender', 'n.', 'A vessel capable of moving in either direction,\n   having bow and rudder at each end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-ender', 'n.', 'A locomotive with pilot at each end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-entendre', 'n.', 'A word or expression admitting of a double\n   interpretation, one of which is often obscure or indelicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-eyed', 'a.', 'Having a deceitful look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-faced', 'a.', 'Having two faces designed for use; as, a double-faced\n   hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-faced', 'a.', 'Deceitful; hypocritical; treacherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double first', '', 'A degree of the first class both in classics and\n   mathematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double first', '', 'One who gains at examinations the highest honor both in\n   the classics and the mathematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-handed', 'a.', 'Having two hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-handed', 'a.', 'Deceitful; deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-headed', 'a.', 'Having two heads; bicipital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublehearted', 'a.', 'Having a false heart; deceitful; treacherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-hung', 'a.', 'Having both sashes hung with weights and cords; --\n   said of a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-lock', 'v. t.', 'To lock with two bolts; to fasten with double\n   security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-milled', 'a.', 'Twice milled or fulled, to render more compact or\n   fine; -- said of cloth; as, double-milled kerseymere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubleminded', 'a.', 'Having different minds at different times; unsettled;\n   undetermined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubleness', 'n.', 'The state of being double or doubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubleness', 'n.', 'Duplicity; insincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-quick', 'a.', 'Of, or performed in, the fastest time or step in\n   marching, next to the run; as, a double-quick step or march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-quick', 'n.', 'Double-quick time, step, or march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-quick', 'v. i. & t.', 'To move, or cause to move, in double-quick\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubler', 'n.', 'One who, or that which, doubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubler', 'n.', 'An instrument for augmenting a very small quantity of\n   electricity, so as to render it manifest by sparks or the electroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-ripper', 'n.', 'A kind of coasting sled, made of two sleds fastened\n   together with a board, one before the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-shade', 'v. t.', 'To double the natural darkness of (a place).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'Two of the same kind; a pair; a couple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'A word or words unintentionally doubled or set up a second\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'A close-fitting garment for men, covering the body from\n   the neck to the waist or a little below. It was worn in Western Europe\n   from the 15th to the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'A counterfeit gem, composed of two pieces of crystal, with\n   a color them, and thus giving the appearance of a naturally colored\n   gem. Also, a piece of paste or glass covered by a veneer of real stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'An arrangement of two lenses for a microscope, designed to\n   correct spherical aberration and chromatic dispersion, thus rendering\n   the image of an object more clear and distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'Two dice, each of which, when thrown, has the same number\n   of spots on the face lying uppermost; as, to throw doublets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'A game somewhat like backgammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublet', 'a.', 'One of two or more words in the same language derived by\n   different courses from the same original from; as, crypt and grot are\n   doublets; also, guard and ward; yard and garden; abridge and\n   abbreviate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublethreaded', 'a.', 'Consisting of two threads twisted together; using\n   two threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublethreaded', 'a.', 'Having two screw threads instead of one; -- said of\n   a screw in which the pitch is equal to twice the distance between the\n   centers of adjacent threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-tongue', 'n.', 'Deceit; duplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-tongued', 'a.', 'Making contrary declarations on the same subject;\n   deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Double-tonguing', 'n.', 'A peculiar action of the tongue by flute players\n   in articulating staccato notes; also, the rapid repetition of notes in\n   cornet playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubletree', 'n.', 'The bar, or crosspiece, of a carriage, to which the\n   singletrees are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doublets', 'n. pl.', 'See Doublet, 6 and 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubling', 'n.', 'The act of one that doubles; a making double;\n   reduplication; also, that which is doubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubling', 'n.', 'A turning and winding; as, the doubling of a hunted hare;\n   shift; trick; artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubling', 'n.', 'The lining of the mantle borne about the shield or\n   escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubling', 'n.', 'The process of redistilling spirits, to improve the\n   strength and flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubloon', 'a.', 'A Spanish gold coin, no longer issued, varying in value\n   at different times from over fifteen dollars to about five. See Doblon\n   in Sup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubly', 'adv.', 'In twice the quantity; to twice the degree; as, doubly\n   wise or good; to be doubly sensible of an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubly', 'adv.', 'Deceitfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dou/ted', 'imp. & p. p.', 'of Doubt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubting', 'p. pr. & vb. n.', 'of Doubt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'To waver in opinion or judgment; to be in uncertainty as\n   to belief respecting anything; to hesitate in belief; to be undecided\n   as to the truth of the negative or the affirmative proposition; to b e\n   undetermined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'To suspect; to fear; to be apprehensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. t.', 'To question or hold questionable; to withhold assent to;\n   to hesitate to believe, or to be inclined not to believe; to withhold\n   confidence from; to distrust; as, I have heard the story, but I doubt\n   the truth of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. t.', 'To suspect; to fear; to be apprehensive of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. t.', 'To fill with fear; to affright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'A fluctuation of mind arising from defect of knowledge or\n   evidence; uncertainty of judgment or mind; unsettled state of opinion\n   concerning the reality of an event, or the truth of an assertion, etc.;\n   hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'Uncertainty of condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'Suspicion; fear; apprehension; dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubt', 'v. i.', 'Difficulty expressed or urged for solution; point\n   unsettled; objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtable', 'a.', 'Capable of being doubted; questionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtable', 'a.', 'Worthy of being feared; redoubtable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtance', 'n.', 'State of being in doubt; uncertainty; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubter', 'n.', 'One who doubts; one whose opinion is unsettled; one who\n   scruples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtful', 'a.', 'Not settled in opinion; undetermined; wavering;\n   hesitating in belief; also used, metaphorically, of the body when its\n   action is affected by such a state of mind; as, we are doubtful of a\n   fact, or of the propriety of a measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtful', 'a.', 'Admitting of doubt; not obvious, clear, or certain;\n   questionable; not decided; not easy to be defined, classed, or named;\n   as, a doubtful case, hue, claim, title, species, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtful', 'a.', 'Characterized by ambiguity; dubious; as, a doubtful\n   expression; a doubtful phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtful', 'a.', 'Of uncertain issue or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtful', 'a.', 'Fearful; apprehensive; suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtfully', 'adv.', 'In a doubtful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtfulness', 'n.', 'State of being doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtfulness', 'n.', 'Uncertainty of meaning; ambiguity; indefiniteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtfulness', 'n.', 'Uncertainty of event or issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubting', 'a.', 'That is uncertain; that distrusts or hesitates; having\n   doubts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtless', 'a.', 'Free from fear or suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtless', 'adv.', 'Undoubtedly; without doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtlessly', 'adv.', 'Unquestionably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doubtous', 'a.', 'Doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douc', 'n.', 'A monkey (Semnopithecus nemaeus), remarkable for its varied\n   and brilliant colors. It is a native of Cochin China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douce', 'a.', 'Sweet; pleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douce', 'a.', 'Sober; prudent; sedate; modest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doucepere', 'n.', 'One of the twelve peers of France, companions of\n   Charlemagne in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doucet', 'n.', 'Alt. of Dowset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowset', 'n.', 'A custard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowset', 'n.', 'A dowcet, or deeps testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douceur', 'n.', 'Gentleness and sweetness of manner; agreeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douceur', 'n.', 'A gift for service done or to be done; an honorarium; a\n   present; sometimes, a bribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douche', 'n.', 'A jet or current of water or vapor directed upon some part\n   of the body to benefit it medicinally; a douche bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douche', 'n.', 'A syringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doucine', 'n.', 'Same as Cyma/recta, under Cyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doucker', 'v. t.', 'A grebe or diver; -- applied also to the golden-eye,\n   pochard, scoter, and other ducks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dough', 'n.', 'Paste of bread; a soft mass of moistened flour or meal,\n   kneaded or unkneaded, but not yet baked; as, to knead dough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dough', 'n.', 'Anything of the consistency of such paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dough-baked', 'a.', 'Imperfectly baked; hence, not brought to perfection;\n   unfinished; also, of weak or dull understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughbird', 'n.', 'The Eskimo curlew (Numenius borealis). See Curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughface', 'n.', 'A contemptuous nickname for a timid, yielding\n   politician, or one who is easily molded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dough-faced', 'a.', 'Easily molded; pliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughfaceism', 'n.', 'The character of a doughface; truckling pliability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughiness', 'n.', 'The quality or state of being doughy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dough-kneaded', 'a.', 'Like dough; soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughnut', 'n.', 'A small cake (usually sweetened) fried in a kettle of\n   boiling lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughtily', 'adv.', 'In a doughty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughtiness', 'n.', 'The quality of being doughty; valor; bravery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughtren', 'n. pl.', 'Daughters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughty', 'superl.', 'Able; strong; valiant; redoubtable; as, a doughty\n   hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doughy', 'a.', 'Like dough; soft and heavy; pasty; crude; flabby and pale;\n   as, a doughy complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doulocracy', 'n.', 'A government by slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doum palm', '', 'See Doom palm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doupe', 'n.', 'The carrion crow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dour', 'a.', 'Hard; inflexible; obstinate; sour in aspect; hardy; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doura', 'n.', 'A kind of millet. See Durra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douroucouli', 'n.', 'See Durukuli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doused', 'imp. & p. p.', 'of Douse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dousing', 'p. pr. & vb. n.', 'of Douse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douse', 'v. t.', 'To plunge suddenly into water; to duck; to immerse; to\n   dowse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douse', 'v. t.', 'To strike or lower in haste; to slacken suddenly; as,\n   douse the topsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douse', 'v. i.', 'To fall suddenly into water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douse', 'v. t.', 'To put out; to extinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dousing-chock', 'n.', 'One of several pieces fayed across the apron and\n   lapped in the knightheads, or inside planking above the upper deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dout', 'v. t.', 'To put out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Douter', 'n.', 'An extinguisher for candles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dove', 'n.', 'A pigeon of the genus Columba and various related genera. The\n   species are numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dove', 'n.', 'A word of endearment for one regarded as pure and gentle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovecot', 'n.', 'Alt. of Dovecote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovecote', 'n.', 'A small house or box, raised to a considerable height\n   above the ground, and having compartments, in which domestic pigeons\n   breed; a dove house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dove-eyed', 'a.', 'Having eyes like a dove; meekeyed; as, dove-eyed Peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovekie', 'n.', 'A guillemot (Uria grylle), of the arctic regions. Also\n   applied to the little auk or sea dove. See under Dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovelet', 'n.', 'A young or small dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovelike', 'a.', 'Mild as a dove; gentle; pure and lovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dove plant', '', 'A Central American orchid (Peristeria elata), having a\n   flower stem five or six feet high, with numerous globose white fragrant\n   flowers. The column in the center of the flower resembles a dove; --\n   called also Holy Spirit plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovers Powder', '', 'A powder of ipecac and opium, compounded, in the\n   United States, with sugar of milk, but in England (as formerly in the\n   United States) with sulphate of potash, and in France (as in Dr.\n   Dovers original prescription) with nitrate and sulphate of potash and\n   licorice. It is an anodyne diaphoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doves-foot', 'n.', 'A small annual species of Geranium, native in England;\n   -- so called from the shape of the leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doves-foot', 'n.', 'The columbine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doveship', 'n.', 'The possession of dovelike qualities, harmlessness and\n   innocence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetail', 'n.', 'A flaring tenon, or tongue (shaped like a birds tail\n   spread), and a mortise, or socket, into which it fits tightly, making\n   an interlocking joint between two pieces which resists pulling a part\n   in all directions except one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetailed', 'imp. & p. p.', 'of Dovetail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetailing', 'p. pr. & vb. n.', 'of Dovetail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetail', 'v. t.', 'To cut to a dovetail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetail', 'v. t.', 'To join by means of dovetails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovetail', 'v. t.', 'To fit in or connect strongly, skillfully, or nicely;\n   to fit ingeniously or complexly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dovish', 'a.', 'Like a dove; harmless; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dow', 'n.', 'A kind of vessel. See Dhow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dow', 'v. t.', 'To furnish with a dower; to endow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowable', 'v. t.', 'Capable of being endowed; entitled to dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowager', 'n.', 'A widow endowed, or having a jointure; a widow who either\n   enjoys a dower from her deceased husband, or has property of her own\n   brought by her to her husband on marriage, and settled on her after his\n   decease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowager', 'n.', 'A title given in England to a widow, to distinguish her\n   from the wife of her husbands heir bearing the same name; -- chiefly\n   applied to widows of personages of rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowagerism', 'n.', 'The rank or condition of a dowager; formality, as that\n   of a dowager. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowcet', 'n.', 'One of the testicles of a hart or stag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowdy', 'superl.', 'Showing a vulgar taste in dress; awkward and slovenly\n   in dress; vulgar-looking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowdies', 'pl. ', 'of Dowdy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowdy', 'n.', 'An awkward, vulgarly dressed, inelegant woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowdyish', 'a.', 'Like a dowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowel', 'n.', 'A pin, or block, of wood or metal, fitting into holes in the\n   abutting portions of two pieces, and being partly in one piece and\n   partly in the other, to keep them in their proper relative position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowel', 'n.', 'A piece of wood driven into a wall, so that other pieces may\n   be nailed to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doweled', 'imp. & p. p.', 'of Dowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowelled', '', 'of Dowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doweling', 'p. pr. & vb. n.', 'of Dowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowelling', '', 'of Dowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowel', 'v. t.', 'To fasten together by dowels; to furnish with dowels; as,\n   a cooper dowels pieces for the head of a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dower', 'n.', 'That with which one is gifted or endowed; endowment; gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dower', 'n.', 'The property with which a woman is endowed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dower', 'n.', 'That which a woman brings to a husband in marriage; dowry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dower', 'n.', 'That portion of the real estate of a man which his widow\n   enjoys during her life, or to which a woman is entitled after the death\n   of her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowered', 'p. a.', 'Furnished with, or as with, dower or a marriage\n   portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowerless', 'a.', 'Destitute of dower; having no marriage portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowery', 'n.', 'See Dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowitcher', 'n.', 'The red-breasted or gray snipe (Macrorhamphus griseus);\n   -- called also brownback, and grayback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowl', 'n.', 'Same as Dowle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowlas', 'n.', 'A coarse linen cloth made in the north of England and in\n   Scotland, now nearly replaced by calico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowle', 'n.', 'Feathery or wool-like down; filament of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'n.', 'Fine, soft, hairy outgrowth from the skin or surface of\n   animals or plants, not matted and fleecy like wool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'n.', 'The soft under feathers of birds. They have short stems with\n   soft rachis and bards and long threadlike barbules, without hooklets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'n.', 'The pubescence of plants; the hairy crown or envelope of the\n   seeds of certain plants, as of the thistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'n.', 'The soft hair of the face when beginning to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'n.', 'That which is made of down, as a bed or pillow; that which\n   affords ease and repose, like a bed of down');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'v. t.', 'To cover, ornament, line, or stuff with down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'prep.', 'A bank or rounded hillock of sand thrown up by the wind\n   along or near the shore; a flattish-topped hill; -- usually in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'prep.', 'A tract of poor, sandy, undulating or hilly land near the\n   sea, covered with fine turf which serves chiefly for the grazing of\n   sheep; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'prep.', 'A road for shipping in the English Channel or Straits of\n   Dover, near Deal, employed as a naval rendezvous in time of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'prep.', 'A state of depression; low state; abasement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'In the direction of gravity or toward the center of the\n   earth; toward or in a lower place or position; below; -- the opposite\n   of up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'From a higher to a lower position, literally or\n   figuratively; in a descending direction; from the top of an ascent;\n   from an upright position; to the ground or floor; to or into a lower or\n   an inferior condition; as, into a state of humility, disgrace, misery,\n   and the like; into a state of rest; -- used with verbs indicating\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'In a low or the lowest position, literally or figuratively;\n   at the bottom of a decent; below the horizon; of the ground; in a\n   condition of humility, dejection, misery, and the like; in a state of\n   quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'From a remoter or higher antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'From a greater to a less bulk, or from a thinner to a\n   thicker consistence; as, to boil down in cookery, or in making\n   decoctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'In a descending direction along; from a higher to a lower\n   place upon or within; at a lower place in or on; as, down a hill; down\n   a well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'adv.', 'Hence: Towards the mouth of a river; towards the sea; as,\n   to sail or swim down a stream; to sail down the sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downed', 'imp. & p. p.', 'of Down');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downing', 'p. pr. & vb. n.', 'of Down');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'v. t.', 'To cause to go down; to make descend; to put down; to\n   overthrow, as in wrestling; hence, to subdue; to bring down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'v. i.', 'To go down; to descend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'a.', 'Downcast; as, a down look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'a.', 'Downright; absolute; positive; as, a down denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down', 'a.', 'Downward; going down; sloping; as, a down stroke; a down\n   grade; a down train on a railway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downbear', 'v. t.', 'To bear down; to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downcast', 'a.', 'Cast downward; directed to the ground, from bashfulness,\n   modesty, dejection, or guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downcast', 'n.', 'Downcast or melancholy look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downcast', 'n.', 'A ventilating shaft down which the air passes in\n   circulating through a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downcome', 'n.', 'Sudden fall; downfall; overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downcome', 'n.', 'A pipe for leading combustible gases downward from the\n   top of the blast furnace to the hot-blast stoves, boilers, etc., where\n   they are burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downfall', 'n.', 'A sudden fall; a body of things falling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downfall', 'n.', 'A sudden descent from rank or state, reputation or\n   happiness; destruction; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downfallen', 'a.', 'Fallen; ruined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downfalling', 'a.', 'Falling down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downgyved', 'a.', 'Hanging down like gyves or fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downhaul', 'n.', 'A rope to haul down, or to assist in hauling down, a\n   sail; as, a staysail downhaul; a trysail downhaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downhearted', 'a.', 'Dejected; low-spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downhill', 'adv.', 'Towards the bottom of a hill; as, water runs downhill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downhill', 'a.', 'Declivous; descending; sloping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downhill', 'n.', 'Declivity; descent; slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downiness', 'n.', 'The quality or state of being downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downlooked', 'a.', 'Having a downcast countenance; dejected; gloomy;\n   sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downlying', 'n.', 'The time of retiring to rest; time of repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downpour', 'n.', 'A pouring or streaming downwards; esp., a heavy or\n   continuous shower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downright', 'adv.', 'Straight down; perpendicularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downright', 'adv.', 'In plain terms; without ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downright', 'adv.', 'Without delay; at once; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downright', 'a.', 'Plain; direct; unceremonious; blunt; positive; as, he\n   spoke in his downright way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downright', 'a.', 'Open; artless; undisguised; absolute; unmixed; as,\n   downright atheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Down-share', 'n.', 'A breastplow used in paring off turf on downs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downsitting', 'n.', 'The act of sitting down; repose; a resting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downstairs', 'adv.', 'Down the stairs; to a lower floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downstairs', 'a.', 'Below stairs; as, a downstairs room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downsteepy', 'a.', 'Very steep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downstream', 'adv.', 'Down the stream; as, floating downstream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downstroke', 'n.', 'A stroke made with a downward motion of the pen or\n   pencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downthrow', 'n.', 'The sudden drop or depression of the strata of rocks on\n   one side of a fault. See Throw, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downtrod', 'a.', 'Alt. of Downtrodden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downtrodden', 'a.', 'Trodden down; trampled down; abused by superior power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downward', 'adv.', 'Alt. of Downwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downwards', 'adv.', 'From a higher place to a lower; in a descending\n   course; as, to tend, move, roll, look, or take root, downward or\n   downwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downwards', 'adv.', 'From a higher to a lower condition; toward misery,\n   humility, disgrace, or ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downwards', 'adv.', 'From a remote time; from an ancestor or predecessor;\n   from one to another in a descending line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downward', 'a.', 'Moving or extending from a higher to a lower place;\n   tending toward the earth or its center, or toward a lower level;\n   declivous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downward', 'a.', 'Descending from a head, origin, or source; as, a downward\n   line of descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downward', 'a.', 'Tending to a lower condition or state; depressed;\n   dejected; as, downward thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downweed', 'n.', 'Cudweed, a species of Gnaphalium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downweigh', 'v. t.', 'To weigh or press down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downy', 'a.', 'Covered with down, or with pubescence or soft hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downy', 'a.', 'Made of, or resembling, down. Hence, figuratively: Soft;\n   placid; soothing; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Downy', 'a.', 'Cunning; wary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowral', 'a.', 'Of or relating to a dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowress', 'n.', 'A woman entitled to dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowries', 'pl. ', 'of Dowry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowry', 'n.', 'A gift; endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowry', 'n.', 'The money, goods, or estate, which a woman brings to her\n   husband in marriage; a brides portion on her marriage. See Note under\n   Dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowry', 'n.', 'A gift or presents for the bride, on espousal. See Dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowse', 'v. t.', 'To plunge, or duck into water; to immerse; to douse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowse', 'v. t.', 'To beat or thrash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowse', 'v. i.', 'To use the dipping or divining rod, as in search of\n   water, ore, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowse', 'n.', 'A blow on the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowser', 'n.', 'A divining rod used in searching for water, ore, etc., a\n   dowsing rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowser', 'n.', 'One who uses the dowser or divining rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowst', 'n.', 'A dowse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dowve', 'n.', 'A dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxological', 'a.', 'Pertaining to doxology; giving praise to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxologized', 'imp. & p. p.', 'of Doxologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxologizing', 'p. pr. & vb. n.', 'of Doxologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxologize', 'v. i.', 'To give glory to God, as in a doxology; to praise\n   God with doxologies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxologies', 'pl. ', 'of Doxology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxology', 'n.', 'In Christian worship: A hymn expressing praise and honor\n   to God; a form of praise to God designed to be sung or chanted by the\n   choir or the congregation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxies', 'pl. ', 'of Doxy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doxy', 'n.', 'A loose wench; a disreputable sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doyly', 'n.', 'See Doily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozed', 'imp. & p. p.', 'of Doze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozing', 'p. pr. & vb. n.', 'of Doze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doze', 'v. i.', 'To slumber; to sleep lightly; to be in a dull or stupefied\n   condition, as if half asleep; to be drowsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doze', 'v. t.', 'To pass or spend in drowsiness; as, to doze away ones\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doze', 'v. t.', 'To make dull; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doze', 'n.', 'A light sleep; a drowse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozen', 'pl. ', 'of Dozen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozens', 'pl. ', 'of Dozen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozen', 'n.', 'A collection of twelve objects; a tale or set of twelve;\n   with or without of before the substantive which follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozen', 'n.', 'An indefinite small number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozenth', 'a.', 'Twelfth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozer', 'n.', 'One who dozes or drowses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Doziness', 'n.', 'The state of being dozy; drowsiness; inclination to\n   sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozy', 'a.', 'Drowsy; inclined to doze; sleepy; sluggish; as, a dozy head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dozzled', 'a.', 'Stupid; heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A low, sluttish woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A lewd wench; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A wooden box, used in salt works for holding the salt when\n   taken out of the boiling pans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbed', 'imp. & p. p.', 'of Drab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbing', 'p. pr. & vb. n.', 'of Drab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'v. i.', 'To associate with strumpets; to wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A kind of thick woolen cloth of a dun, or dull brownish\n   yellow, or dull gray, color; -- called also drabcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A dull brownish yellow or dull gray color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'a.', 'Of a color between gray and brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drab', 'n.', 'A drab color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabber', 'n.', 'One who associates with drabs; a wencher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbet', 'n.', 'A coarse linen fabric, or duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbish', 'a.', 'Somewhat drab in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbish', 'a.', 'Having the character of a drab or low wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbled', 'imp. & p. p.', 'of Drabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbling', 'p. pr. & vb. n.', 'of Drabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabble', 'v. t.', 'To draggle; to wet and befoul by draggling; as, to\n   drabble a gown or cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabble', 'v. i.', 'To fish with a long line and rod; as, to drabble for\n   barbels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabbler', 'n.', 'A piece of canvas fastened by lacing to the bonnet of a\n   sail, to give it a greater depth, or more drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drabble-tail', 'n.', 'A draggle-tail; a slattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracaena', 'n.', 'A genus of liliaceous plants with woody stems and\n   funnel-shaped flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracanth', 'n.', 'A kind of gum; -- called also gum tragacanth, or\n   tragacanth. See Tragacanth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachm', 'n.', 'A drachma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachm', 'n.', 'Same as Dram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachmas', 'pl. ', 'of Drachma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachmae', 'pl. ', 'of Drachma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachma', 'n.', 'A silver coin among the ancient Greeks, having a different\n   value in different States and at different periods. The average value\n   of the Attic drachma is computed to have been about 19 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachma', 'n.', 'A gold and silver coin of modern Greece worth 19.3 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachma', 'n.', 'Among the ancient Greeks, a weight of about 66.5 grains;\n   among the modern Greeks, a weight equal to a gram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drachme', 'n.', 'See Drachma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracin', 'n.', 'See Draconin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draco', 'n.', 'The Dragon, a northern constellation within which is the\n   north pole of the ecliptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draco', 'n.', 'A luminous exhalation from marshy grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draco', 'n.', 'A genus of lizards. See Dragon, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draconian', 'a.', 'Pertaining to Draco, a famous lawgiver of Athens, 621 b.\n   c.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draconic', 'a.', 'Relating to Draco, the Athenian lawgiver; or to the\n   constellation Draco; or to dragons blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draconin', 'n.', 'A red resin forming the essential basis of dragons\n   blood; -- called also dracin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracontic', 'a.', 'Belonging to that space of time in which the moon\n   performs one revolution, from ascending node to ascending node. See\n   Dragons head, under Dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracontine', 'a.', 'Belonging to a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracunculi', 'pl. ', 'of Dracunculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracunculus', 'n.', 'A fish; the dragonet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dracunculus', 'n.', 'The Guinea worm (Filaria medinensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drad', 'p. p. & a.', 'Dreaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dradde', 'imp.', 'of Dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dradge', 'n.', 'Inferior ore, separated from the better by cobbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'Refuse; lees; dregs; the wash given to swine or cows;\n   hogwash; waste matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draffish', 'a.', 'Worthless; draffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draffy', 'a.', 'Dreggy; waste; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'The act of drawing; also, the thing drawn. Same as Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'A selecting or detaching of soldiers from an army, or from\n   any part of it, or from a military post; also from any district, or any\n   company or collection of persons, or from the people at large; also,\n   the body of men thus drafted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'An order from one person or party to another, directing the\n   payment of money; a bill of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'An allowance or deduction made from the gross veight of\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'A drawing of lines for a plan; a plan delineated, or drawn\n   in outline; a delineation. See Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'The form of any writing as first drawn up; the first rough\n   sketch of written composition, to be filled in, or completed. See\n   Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'A narrow border left on a finished stone, worked differently\n   from the rest of its face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'A narrow border worked to a plane surface along the edge of\n   a stone, or across its face, as a guide to the stone-cutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'The slant given to the furrows in the dress of a millstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'Depth of water necessary to float a ship. See Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draff', 'n.', 'A current of air. Same as Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'a.', 'Pertaining to, or used for, drawing or pulling (as vehicles,\n   loads, etc.). Same as Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'a.', 'Relating to, or characterized by, a draft, or current of\n   air. Same as Draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drafted', 'imp. & p. p.', 'of Draft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drafting', 'p. pr. & vb. n.', 'of Draft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'v. t.', 'To draw the outline of; to delineate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'v. t.', 'To compose and write; as, to draft a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'v. t.', 'To draw from a military band or post, or from any\n   district, company, or society; to detach; to select.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draft', 'v. t.', 'To transfer by draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draftsman', 'n.', 'See Draughtsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'n.', 'A confection; a comfit; a drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragged', 'imp. & p. p.', 'of Drag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragging', 'p. pr. & vb. n.', 'of Drag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'To draw slowly or heavily onward; to pull along the ground\n   by main force; to haul; to trail; -- applied to drawing heavy or\n   resisting bodies or those inapt for drawing, with labor, along the\n   ground or other surface; as, to drag stone or timber; to drag a net in\n   fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'To break, as land, by drawing a drag or harrow over it; to\n   harrow; to draw a drag along the bottom of, as a stream or other water;\n   hence, to search, as by means of a drag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'To draw along, as something burdensome; hence, to pass in\n   pain or with difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. i.', 'To be drawn along, as a rope or dress, on the ground; to\n   trail; to be moved onward along the ground, or along the bottom of the\n   sea, as an anchor that does not hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. i.', 'To move onward heavily, laboriously, or slowly; to advance\n   with weary effort; to go on lingeringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. i.', 'To serve as a clog or hindrance; to hold back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. i.', 'To fish with a dragnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'The act of dragging; anything which is dragged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'A net, or an apparatus, to be drawn along the bottom under\n   water, as in fishing, searching for drowned persons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'A kind of sledge for conveying heavy bodies; also, a kind\n   of low car or handcart; as, a stone drag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'A heavy coach with seats on top; also, a heavy carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'A heavy harrow, for breaking up ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'Anything towed in the water to retard a ships progress,\n   or to keep her head up to the wind; esp., a canvas bag with a hooped\n   mouth, so used. See Drag sail (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'Also, a skid or shoe, for retarding the motion of a\n   carriage wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'Hence, anything that retards; a clog; an obstacle to\n   progress or enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'Motion affected with slowness and difficulty, as if\n   clogged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'The bottom part of a flask or mold, the upper part being\n   the cope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'A steel instrument for completing the dressing of soft\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drag', 'v. t.', 'The difference between the speed of a screw steamer under\n   sail and that of the screw when the ship outruns the screw; or between\n   the propulsive effects of the different floats of a paddle wheel. See\n   Citation under Drag, v. i., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragantine', 'n.', 'A mucilage obtained from, or containing, gum\n   tragacanth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragbar', 'n.', 'Same as Drawbar (b). Called also draglink, and drawlink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragbolt', 'n.', 'A coupling pin. See under Coupling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragees', 'n. pl.', 'Sugar-coated medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggled', 'imp. & p. p.', 'of Draggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggling', 'p. pr. & vb. n.', 'of Draggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggle', 'v. t.', 'To wet and soil by dragging on the ground, mud, or wet\n   grass; to drabble; to trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggle', 'v. i.', 'To be dragged on the ground; to become wet or dirty by\n   being dragged or trailed in the mud or wet grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggle-tail', 'n.', 'A slattern who suffers her gown to trail in the mire;\n   a drabble-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draggle-tailed', 'a.', 'Untidy; sluttish; slatternly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draglink', 'n.', 'A link connecting the cranks of two shafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draglink', 'n.', 'A drawbar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragmen', 'pl. ', 'of Dragman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragman', 'n.', 'A fisherman who uses a dragnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragnet', 'n.', 'A net to be drawn along the bottom of a body of water, as\n   in fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragomans', 'pl. ', 'of Dragoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoman', 'n.', 'An interpreter; -- so called in the Levant and other\n   parts of the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A fabulous animal, generally represented as a monstrous\n   winged serpent or lizard, with a crested head and enormous claws, and\n   regarded as very powerful and ferocious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A fierce, violent person, esp. a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A constellation of the northern hemisphere figured as a\n   dragon; Draco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A luminous exhalation from marshy grounds, seeming to move\n   through the air as a winged serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A short musket hooked to a swivel attached to a soldiers\n   belt; -- so called from a representation of a dragons head at the\n   muzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A small arboreal lizard of the genus Draco, of several\n   species, found in the East Indies and Southern Asia. Five or six of the\n   hind ribs, on each side, are prolonged and covered with weblike skin,\n   forming a sort of wing. These prolongations aid them in making long\n   leaps from tree to tree. Called also flying lizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A variety of carrier pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragon', 'n.', 'A fabulous winged creature, sometimes borne as a charge in\n   a coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragonet', 'n.', 'A little dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragonet', 'n.', 'A small British marine fish (Callionymuslyra); -- called\n   also yellow sculpin, fox, and gowdie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragonish', 'a.', 'resembling a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragonlike', 'a.', 'Like a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragonnade', 'n.', 'The severe persecution of French Protestants under\n   Louis XIV., by an armed force, usually of dragoons; hence, a rapid and\n   devastating incursion; dragoonade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragons blood', '', 'Alt. of Dragons tail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragons head', '', 'Alt. of Dragons tail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragons tail', '', 'See Dragons blood, Dragons head, etc., under Dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoon', 'n.', 'Formerly, a soldier who was taught and armed to serve\n   either on horseback or on foot; now, a mounted soldier; a cavalry man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoon', 'n.', 'A variety of pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragooned', 'imp. & p. p.', 'of Dragoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragooning', 'p. pr. & vb. n.', 'of Dragoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoon', 'v. t.', 'To harass or reduce to subjection by dragoons; to\n   persecute by abandoning a place to the rage of soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoon', 'v. t.', 'To compel submission by violent measures; to harass; to\n   persecute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragoonade', 'n.', 'See Dragonnade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dragooner', 'n.', 'A dragoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drail', 'v. t. & i.', 'To trail; to draggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drained', 'imp. & p. p.', 'of Drain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draining', 'p. pr. & vb. n.', 'of Drain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'v. t.', 'To draw off by degrees; to cause to flow gradually out or\n   off; hence, to cause the exhaustion of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'v. t.', 'To exhaust of liquid contents by drawing them off; to\n   make gradually dry or empty; to remove surface water, as from streets,\n   by gutters, etc.; to deprive of moisture; hence, to exhaust; to empty\n   of wealth, resources, or the like; as, to drain a country of its\n   specie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'v. t.', 'To filter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'v. i.', 'To flow gradually; as, the water of low ground drains\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'v. i.', 'To become emptied of liquor by flowing or dropping; as,\n   let the vessel stand and drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'n.', 'The act of draining, or of drawing off; gradual and\n   continuous outflow or withdrawal; as, the drain of specie from a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'n.', 'That means of which anything is drained; a channel; a\n   trench; a water course; a sewer; a sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drain', 'n.', 'The grain from the mashing tub; as, brewers drains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainable', 'a.', 'Capable of being drained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainage', 'n.', 'A draining; a gradual flowing off of any liquid; also,\n   that which flows out of a drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainage', 'n.', 'The mode in which the waters of a country pass off by its\n   streams and rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainage', 'n.', 'The system of drains and their operation, by which\n   superfluous water is removed from towns, railway beds, mines, and other\n   works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainage', 'n.', 'Area or district drained; as, the drainage of the Po, the\n   Thames, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainage', 'n.', 'The act, process, or means of drawing off the pus or\n   fluids from a wound, abscess, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draine', 'n.', 'The missel thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainer', 'n.', 'One who, or that which, drains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draining', 'v. t.', 'The art of carrying off surplus water, as from land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drainpipe', 'n.', 'A pipe used for carrying off surplus water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draintile', 'n.', 'A hollow tile used in making drains; -- called also\n   draining tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draintrap', 'n.', 'See 4th Trap, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drake', 'n.', 'The male of the duck kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drake', 'n.', 'The drake fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drake', 'n.', 'A dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drake', 'n.', 'A small piece of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drake', 'n.', 'Wild oats, brome grass, or darnel grass; -- called also\n   drawk, dravick, and drank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drakestone', 'n.', 'A flat stone so thrown along the surface of water as to\n   skip from point to point before it sinks; also, the sport of so\n   throwing stones; -- sometimes called ducks and drakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dram', 'n.', 'A weight; in Apothecaries weight, one eighth part of an\n   ounce, or sixty grains; in Avoirdupois weight, one sixteenth part of an\n   ounce, or 27.34375 grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dram', 'n.', 'A minute quantity; a mite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dram', 'n.', 'As much spirituous liquor as is usually drunk at once; as, a\n   dram of brandy; hence, a potation or potion; as, a dram of poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dram', 'n.', 'A Persian daric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dram', 'v. i. & t.', 'To drink drams; to ply with drams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drama', 'n.', 'A composition, in prose or poetry, accommodated to action,\n   and intended to exhibit a picture of human life, or to depict a series\n   of grave or humorous actions of more than ordinary interest, tending\n   toward some striking result. It is commonly designed to be spoken and\n   represented by actors on the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drama', 'n.', 'A series of real events invested with a dramatic unity and\n   interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drama', 'n.', 'Dramatic composition and the literature pertaining to or\n   illustrating it; dramatic literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatic', 'a.', 'Alt. of Dramatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatical', 'a.', 'Of or pertaining to the drama; appropriate to, or\n   having the qualities of, a drama; theatrical; vivid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatically', 'adv.', 'In a dramatic manner; theatrically; vividly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatis personae', '', 'The actors in a drama or play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatist', 'n.', 'The author of a dramatic composition; a writer of plays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatizable', 'a.', 'Capable of being dramatized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatization', 'n.', 'Act of dramatizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatized', 'imp. & p. p.', 'of Dramatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatizing', 'p. pr. & vb. n.', 'of Dramatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramatize', 'v. t.', 'To compose in the form of the drama; to represent in\n   a drama; to adapt to dramatic representation; as, to dramatize a novel,\n   or an historical episode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramaturgic', 'a.', 'Relating to dramaturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramaturgist', 'n.', 'One versed in dramaturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramaturgy', 'n.', 'The art of dramatic composition and representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramming', 'n.', 'The practice of drinking drams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramseller', 'n.', 'One who sells distilled liquors by the dram or glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dramshop', 'n.', 'A shop or barroom where spirits are sold by the dram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drank', 'imp.', 'of Drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drank', 'n.', 'Wild oats, or darnel grass. See Drake a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drap dete', '', 'A thin woolen fabric, twilled like merino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draped', 'imp. & p. p.', 'of Drape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draping', 'p. pr. & vb. n.', 'of Drape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drape', 'v. t.', 'To cover or adorn with drapery or folds of cloth, or as\n   with drapery; as, to drape a bust, a building, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drape', 'v. t.', 'To rail at; to banter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drape', 'v. i.', 'To make cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drape', 'v. i.', 'To design drapery, arrange its folds, etc., as for\n   hangings, costumes, statues, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draper', 'n.', 'One who sells cloths; a dealer in cloths; as, a draper and\n   tailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draperied', 'a.', 'Covered or supplied with drapery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draperies', 'pl. ', 'of Drapery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drapery', 'n.', 'The occupation of a draper; cloth-making, or dealing in\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drapery', 'n.', 'Cloth, or woolen stuffs in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drapery', 'n.', 'A textile fabric used for decorative purposes, especially\n   when hung loosely and in folds carefully disturbed; as: (a) Garments or\n   vestments of this character worn upon the body, or shown in the\n   representations of the human figure in art. (b) Hangings of a room or\n   hall, or about a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drapet', 'n.', 'Cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drastic', 'a.', 'Acting rapidly and violently; efficacious; powerful; --\n   opposed to bland; as, drastic purgatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drastic', 'n.', 'A violent purgative. See Cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drasty', 'a.', 'Filthy; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draugh', 'n.', 'See Draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The act of drawing or pulling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The act of moving loads by drawing, as by beasts of\n   burden, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The drawing of a bowstring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'Act of drawing a net; a sweeping the water for fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The act of drawing liquor into the mouth and throat; the\n   act of drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A sudden attack or drawing upon an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The act of selecting or detaching soldiers; a draft (see\n   Draft, n., 2)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The act of drawing up, marking out, or delineating;\n   representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'That which is drawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'That which is taken by sweeping with a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The force drawn; a detachment; -- in this sense usually\n   written draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The quantity drawn in at once in drinking; a potion or\n   potation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A sketch, outline, or representation, whether written,\n   designed, or drawn; a delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'An order for the payment of money; -- in this sense almost\n   always written draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A current of air moving through an inclosed place, as\n   through a room or up a chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'That which draws');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A team of oxen or horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A sink or drain; a privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A mild vesicatory; a sinapism; as, to apply draughts to\n   the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'Capacity of being drawn; force necessary to draw;\n   traction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The depth of water necessary to float a ship, or the depth\n   a ship sinks in water, especially when laden; as, a ship of twelve feet\n   draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'An allowance on weighable goods. [Eng.] See Draft, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'A move, as at chess or checkers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'The bevel given to the pattern for a casting, in order\n   that it may be drawn from the sand without injury to the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'n.', 'See Draft, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'a.', 'Used for drawing vehicles, loads, etc.; as, a draught\n   beast; draught hooks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'a.', 'Relating to, or characterized by, a draft, or current of\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'a.', 'Used in making drawings; as, draught compasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'a.', 'Drawn directly from the barrel, or other receptacle, in\n   distinction from bottled; on draught; -- said of ale, cider, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughted', 'imp. & p. p.', 'of Draught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughting', 'p. pr. & vb. n.', 'of Draught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'v. t.', 'To draw out; to call forth. See Draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'v. t.', 'To diminish or exhaust by drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draught', 'v. t.', 'To draw in outline; to make a draught, sketch, or plan\n   of, as in architectural and mechanical drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtboard', 'n.', 'A checkered board on which draughts are played. See\n   Checkerboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughthouse', 'n.', 'A house for the reception of waste matter; a privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughts', 'n. pl.', 'A mild vesicatory. See Draught, n., 3 (c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughts', 'n. pl.', 'A game, now more commonly called checkers. See\n   Checkers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsmen', 'pl. ', 'of Draughtsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsman', 'n.', 'One who draws pleadings or other writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsman', 'n.', 'One who draws plans and sketches of machinery,\n   structures, and places; also, more generally, one who makes drawings of\n   any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsman', 'n.', 'A \"man\" or piece used in the game of draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsman', 'n.', 'One who drinks drams; a tippler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughtsmanship', 'n.', 'The office, art, or work of a draughtsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draughty', 'a.', 'Pertaining to a draught, or current of air; as, a\n   draughtly, comfortless room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drave', '', 'old imp. of Drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dravida', 'n. pl.', 'A race of Hindostan, believed to be the original\n   people who occupied the land before the Hindoo or Aryan invasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dravidian', 'a.', 'Of or pertaining to the Dravida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drew', 'imp.', 'of Draw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawn', 'p. p.', 'of Draw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'p. pr. & vb. n.', 'of Draw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To cause to move continuously by force applied in advance\n   of the thing moved; to pull along; to haul; to drag; to cause to\n   follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To influence to move or tend toward ones self; to\n   exercise an attracting force upon; to call towards itself; to attract;\n   hence, to entice; to allure; to induce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To cause to come out for ones use or benefit; to extract;\n   to educe; to bring forth; as: (a) To bring or take out, or to let out,\n   from some receptacle, as a stick or post from a hole, water from a cask\n   or well, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To pull from a sheath, as a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To extract; to force out; to elicit; to derive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To obtain from some cause or origin; to infer from\n   evidence or reasons; to deduce from premises; to derive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To take or procure from a place of deposit; to call for\n   and receive from a fund, or the like; as, to draw money from a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To take from a box or wheel, as a lottery ticket; to\n   receive from a lottery by the drawing out of the numbers for prizes or\n   blanks; hence, to obtain by good fortune; to win; to gain; as, he drew\n   a prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To select by the drawing of lots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To remove the contents of');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To drain by emptying; to suck dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To extract the bowels of; to eviscerate; as, to draw a\n   fowl; to hang, draw, and quarter a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To take into the lungs; to inhale; to inspire; hence,\n   also, to utter or produce by an inhalation; to heave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To extend in length; to lengthen; to protract; to stretch;\n   to extend, as a mass of metal into wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To run, extend, or produce, as a line on any surface;\n   hence, also, to form by marking; to make by an instrument of\n   delineation; to produce, as a sketch, figure, or picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To represent by lines drawn; to form a sketch or a picture\n   of; to represent by a picture; to delineate; hence, to represent by\n   words; to depict; to describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To write in due form; to prepare a draught of; as, to draw\n   a memorial, a deed, or bill of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To require (so great a depth, as of water) for floating;\n   -- said of a vessel; to sink so deep in (water); as, a ship draws ten\n   feet of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. t.', 'To trace by scent; to track; -- a hunting term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To pull; to exert strength in drawing anything; to have\n   force to move anything by pulling; as, a horse draws well; the sails of\n   a ship draw well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To draw a liquid from some receptacle, as water from a\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To exert an attractive force; to act as an inducement or\n   enticement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To have efficiency as an epispastic; to act as a sinapism;\n   -- said of a blister, poultice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To have draught, as a chimney, flue, or the like; to\n   furnish transmission to smoke, gases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To unsheathe a weapon, especially a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To perform the act, or practice the art, of delineation;\n   to sketch; to form figures or pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To become contracted; to shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To move; to come or go; literally, to draw ones self; --\n   with prepositions and adverbs; as, to draw away, to move off, esp. in\n   racing, to get in front; to obtain the lead or increase it; to draw\n   back, to retreat; to draw level, to move up even (with another); to\n   come up to or overtake another; to draw off, to retire or retreat; to\n   draw on, to advance; to draw up, to form in array; to draw near, nigh,\n   or towards, to approach; to draw together, to come together, to\n   collect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To make a draft or written demand for payment of money\n   deposited or due; -- usually with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To admit the action of pulling or dragging; to undergo\n   draught; as, a carriage draws easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'v. i.', 'To sink in water; to require a depth for floating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'n.', 'The act of drawing; draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'n.', 'A lot or chance to be drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'n.', 'A drawn game or battle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw', 'n.', 'That part of a bridge which may be raised, swung round, or\n   drawn aside; the movable part of a drawbridge. See the Note under\n   Drawbridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawable', 'a.', 'Capable of being drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawback', 'n.', 'A loss of advantage, or deduction from profit, value,\n   success, etc.; a discouragement or hindrance; objectionable feature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawback', 'n.', 'Money paid back or remitted; especially, a certain amount\n   of duties or customs, sometimes the whole, and sometimes only a part,\n   remitted or paid back by the government, on the exportation of the\n   commodities on which they were levied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbar', 'n.', 'An openmouthed bar at the end of a car, which receives a\n   coupling link and pin by which the car is drawn. It is usually provided\n   with a spring to give elasticity to the connection between the cars of\n   a train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbar', 'n.', 'A bar of iron with an eye at each end, or a heavy link,\n   for coupling a locomotive to a tender or car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbench', 'n.', 'A machine in which strips of metal are drawn through a\n   drawplate; especially, one in which wire is thus made; -- also called\n   drawing bench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbolt', 'n.', 'A coupling pin. See under Coupling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbore', 'n.', 'A hole bored through a tenon nearer to the shoulder than\n   the holes through the cheeks are to the edge or abutment against which\n   the shoulder is to rest, so that a pin or bolt, when driven into it,\n   will draw these parts together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbore', 'v. t.', 'To make a drawbore in; as, to drawbore a tenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbore', 'v. t.', 'To enlarge the bore of a gun barrel by drawing,\n   instead of thrusting, a revolving tool through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawboy', 'n.', 'A boy who operates the harness cords of a hand loom; also,\n   a part of power loom that performs the same office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawbridge', 'n.', 'A bridge of which either the whole or a part is made to\n   be raised up, let down, or drawn or turned aside, to admit or hinder\n   communication at pleasure, as before the gate of a town or castle, or\n   over a navigable river or canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawcansir', 'n.', 'A blustering, bullying fellow; a pot-valiant braggart;\n   a bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draw-cut', 'n.', 'A single cut with a knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawee', 'n.', 'The person on whom an order or bill of exchange is drawn;\n   -- the correlative of drawer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'One who, or that which, draws');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'One who draws liquor for guests; a waiter in a taproom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'One who delineates or depicts; a draughtsman; as, a good\n   drawer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'One who draws a bill of exchange or order for payment; --\n   the correlative of drawee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'That which is drawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'A sliding box or receptacle in a case, which is opened by\n   pulling or drawing out, and closed by pushing in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawer', 'n.', 'An under-garment worn on the lower limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawfiling', 'n.', 'The process of smooth filing by working the file\n   sidewise instead of lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawgear', 'n.', 'A harness for draught horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawgear', 'n.', 'The means or parts by which cars are connected to be\n   drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawgloves', 'n. pl.', 'An old game, played by holding up the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawhead', 'n.', 'The flanged outer end of a drawbar; also, a name applied\n   to the drawgear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'n.', 'The act of pulling, or attracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'n.', 'The act or the art of representing any object by means of\n   lines and shades; especially, such a representation when in one color,\n   or in tints used not to represent the colors of natural objects, but\n   for effect only, and produced with hard material such as pencil, chalk,\n   etc.; delineation; also, the figure or representation drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'n.', 'The process of stretching or spreading metals as by\n   hammering, or, as in forming wire from rods or tubes and cups from\n   sheet metal, by pulling them through dies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'n.', 'The process of pulling out and elongating the sliver from\n   the carding machine, by revolving rollers, to prepare it for spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing', 'n.', 'The distribution of prizes and blanks in a lottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing knife', 'n.', 'Alt. of Drawknife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawknife', 'n.', 'A joiners tool having a blade with a handle at each\n   end, used to shave off surfaces, by drawing it toward one; a shave; --\n   called also drawshave, and drawing shave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawknife', 'n.', 'A tool used for the purpose of making an incision along\n   the path a saw is to follow, to prevent it from tearing the surface of\n   the wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing-room', 'n.', 'A room appropriated for the reception of company; a\n   room to which company withdraws from the dining room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawing-room', 'n.', 'The company assembled in such a room; also, a\n   reception of company in it; as, to hold a drawing-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawled', 'imp. & p. p.', 'of Drawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawling', 'p. pr. & vb. n.', 'of Drawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawl', 'v. t.', 'To utter in a slow, lengthened tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawl', 'v. i.', 'To speak with slow and lingering utterance, from\n   laziness, lack of spirit, affectation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawl', 'n.', 'A lengthened, slow monotonous utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawlatch', 'n.', 'A housebreaker or thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawling', 'n.', 'The act of speaking with a drawl; a drawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawlink', 'n.', 'Same as Drawbar (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawloom', 'n.', 'A kind of loom used in weaving figured patterns; --\n   called also drawboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawloom', 'n.', 'A species of damask made on the drawloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawn', 'p. p. & a.', 'See Draw, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawnet', 'n.', 'A net for catching the larger sorts of birds; also, a\n   dragnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawplate', 'n.', 'A hardened steel plate having a hole, or a gradation of\n   conical holes, through which wires are drawn to be reduced and\n   elongated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawrod', 'n.', 'A rod which unites the drawgear at opposite ends of the\n   car, and bears the pull required to draw the train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawshave', 'n.', 'See Drawing knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drawspring', 'n.', 'The spring to which a drawbar is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dray', 'n.', 'A squirrels nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dray', 'n.', 'A strong low cart or carriage used for heavy burdens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dray', 'n.', 'A kind of sledge or sled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drayage', 'n.', 'Use of a dray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drayage', 'n.', 'The charge, or sum paid, for the use of a dray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Draymen', 'pl. ', 'of Drayman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drayman', 'n.', 'A man who attends a dray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drazel', 'n.', 'A slut; a vagabond wench. Same as Drossel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreaded', 'imp. & p. p.', 'of Dread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreading', 'p. pr. & vb. n.', 'of Dread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'v. t.', 'To fear in a great degree; to regard, or look forward to,\n   with terrific apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'v. i.', 'To be in dread, or great fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'Great fear in view of impending evil; fearful apprehension\n   of danger; anticipatory terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'Reverential or respectful fear; awe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'An object of terrified apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'A person highly revered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'Fury; dreadfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'n.', 'Doubt; as, out of dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'a.', 'Exciting great fear or apprehension; causing terror;\n   frightful; dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread', 'a.', 'Inspiring with reverential fear; awful venerable; as, dread\n   sovereign; dread majesty; dread tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadable', 'a.', 'Worthy of being dreaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dread-bolted', 'a.', 'Armed with dreaded bolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreader', 'n.', 'One who fears, or lives in fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadful', 'a.', 'Full of dread or terror; fearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadful', 'a.', 'Inspiring dread; impressing great fear; fearful;\n   terrible; as, a dreadful storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadful', 'a.', 'Inspiring awe or reverence; awful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadfully', 'adv.', 'In a dreadful manner; terribly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadfulness', 'n.', 'The quality of being dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadingly', 'adv.', 'With dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadless', 'a.', 'Free from dread; fearless; intrepid; dauntless; as,\n   dreadless heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadless', 'a.', 'Exempt from danger which causes dread; secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadless', 'adv.', 'Without doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadlessness', 'n.', 'Freedom from dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadly', 'a.', 'Dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadly', 'adv.', 'With dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadnaught', 'n.', 'A fearless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreadnaught', 'n.', 'Hence: A garment made of very thick cloth, that can\n   defend against storm and cold; also, the cloth itself; fearnaught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dream', 'n.', 'The thoughts, or series of thoughts, or imaginary\n   transactions, which occupy the mind during sleep; a sleeping vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dream', 'n.', 'A visionary scheme; a wild conceit; an idle fancy; a vagary;\n   a revery; -- in this sense, applied to an imaginary or anticipated\n   state of happiness; as, a dream of bliss; the dream of his youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamed', 'imp. & p. p.', 'of Dream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamt', '', 'of Dream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreaming', 'p. pr. & vb. n.', 'of Dream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dream', 'n.', 'To have ideas or images in the mind while in the state of\n   sleep; to experience sleeping visions; -- often with of; as, to dream\n   of a battle, or of an absent friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dream', 'n.', 'To let the mind run on in idle revery or vagary; to\n   anticipate vaguely as a coming and happy reality; to have a visionary\n   notion or idea; to imagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dream', 'v. t.', 'To have a dream of; to see, or have a vision of, in\n   sleep, or in idle fancy; -- often followed by an objective clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamer', 'n.', 'One who dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamer', 'n.', 'A visionary; one lost in wild imaginations or vain schemes\n   of some anticipated good; as, a political dreamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamful', 'a.', 'Full of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamily', 'adv.', 'As if in a dream; softly; slowly; languidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreaminess', 'n.', 'The state of being dreamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamingly', 'adv.', 'In a dreamy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamland', 'n.', 'An unreal, delightful country such as in sometimes\n   pictured in dreams; region of fancies; fairyland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamless', 'a.', 'Free from, or without, dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreamy', 'superl.', 'Abounding in dreams or given to dreaming; appropriate\n   to, or like, dreams; visionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drear', 'a.', 'Dismal; gloomy with solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drear', 'n.', 'Sadness; dismalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drearihead', 'n.', 'Alt. of Drearihood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drearihood', 'n.', 'Affliction; dreariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drearily', 'adv.', 'Gloomily; dismally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreariment', 'n.', 'Dreariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreariness', 'n.', 'Sorrow; wretchedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreariness', 'n.', 'Dismalness; gloomy solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drearing', 'n.', 'Sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drearisome', 'a.', 'Very dreary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreary', 'superl.', 'Sorrowful; distressful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreary', 'superl.', 'Exciting cheerless sensations, feelings, or\n   associations; comfortless; dismal; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drecche', 'v. t.', 'To vex; to torment; to trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drecche', 'v. i.', 'To delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredge', 'n.', 'Any instrument used to gather or take by dragging; as: (a)\n   A dragnet for taking up oysters, etc., from their beds. (b) A dredging\n   machine. (c) An iron frame, with a fine net attached, used in\n   collecting animals living at the bottom of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredge', 'n.', 'Very fine mineral matter held in suspension in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredged', 'imp. & p. p.', 'of Dredge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredging', 'p. pr. & vb. n.', 'of Dredge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredge', 'v. t.', 'To catch or gather with a dredge; to deepen with a\n   dredging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredge', 'n.', 'A mixture of oats and barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredge', 'v. t.', 'To sift or sprinkle flour, etc., on, as on roasting\n   meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredger', 'n.', 'One who fishes with a dredge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredger', 'n.', 'A dredging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dredger', 'n.', 'A box with holes in its lid; -- used for sprinkling flour,\n   as on meat or a breadboard; -- called also dredging box, drudger, and\n   drudging box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dree', 'v. t.', 'To endure; to suffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dree', 'v. i.', 'To be able to do or endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dree', 'a.', 'Wearisome; tedious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreg', 'n.', 'Corrupt or defiling matter contained in a liquid, or\n   precipitated from it; refuse; feculence; lees; grounds; sediment;\n   hence, the vilest and most worthless part of anything; as, the dregs of\n   society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dregginess', 'n.', 'Fullness of dregs or lees; foulness; feculence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreggish', 'a.', 'Foul with lees; feculent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreggy', 'a.', 'Containing dregs or lees; muddy; foul; feculent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drein', 'v. i.', 'To drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreint', '', 'p. p. of Drench to drown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreissena', 'n.', 'A genus of bivalve shells of which one species (D.\n   polymorpha) is often so abundant as to be very troublesome in the fresh\n   waters of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drenched', 'imp. & p. p.', 'of Drench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drenching', 'p. pr. & vb. n.', 'of Drench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drench', 'v. t.', 'To cause to drink; especially, to dose by force; to put\n   a potion down the throat of, as of a horse; hence. to purge violently\n   by physic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drench', 'v. t.', 'To steep in moisture; to wet thoroughly; to soak; to\n   saturate with water or other liquid; to immerse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drench', 'v. t.', 'A drink; a draught; specifically, a potion of medicine\n   poured or forced down the throat; also, a potion that causes purging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drench', 'n.', 'A military vassal mentioned in Domesday Book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drenche', 'v. t. & i.', 'To drown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drencher', 'n.', 'One who, or that which, west or steeps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drencher', 'n.', 'One who administers a drench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drengage', 'n.', 'The tenure by which a drench held land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drent', 'p. p.', 'Drenched; drowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresden ware', '', 'A superior kind of decorated porcelain made near\n   Dresden in Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressed', 'imp. & p. p.', 'of Dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drest', '', 'of Dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'p. pr. & vb. n.', 'of Dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To direct; to put right or straight; to regulate; to\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To arrange in exact continuity of line, as soldiers;\n   commonly to adjust to a straight line and at proper distance; to align;\n   as, to dress the ranks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To treat methodically with remedies, bandages, or\n   curative appliances, as a sore, an ulcer, a wound, or a wounded or\n   diseased part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To adjust; to put in good order; to arrange;\n   specifically: (a) To prepare for use; to fit for any use; to render\n   suitable for an intended purpose; to get ready; as, to dress a slain\n   animal; to dress meat; to dress leather or cloth; to dress or trim a\n   lamp; to dress a garden; to dress a horse, by currying and rubbing; to\n   dress grain, by cleansing it; in mining and metallurgy, to dress ores,\n   by sorting and separating them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To cut to proper dimensions, or give proper shape to, as\n   to a tool by hammering; also, to smooth or finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To put in proper condition by appareling, as the body; to\n   put clothes upon; to apparel; to invest with garments or rich\n   decorations; to clothe; to deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. t.', 'To break and train for use, as a horse or other animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. i.', 'To arrange ones self in due position in a line of\n   soldiers; -- the word of command to form alignment in ranks; as, Right,\n   dress!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'v. i.', 'To clothe or apparel ones self; to put on ones\n   garments; to pay particular regard to dress; as, to dress quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'n.', 'That which is used as the covering or ornament of the body;\n   clothes; garments; habit; apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'n.', 'A ladys gown; as, silk or a velvet dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'n.', 'Attention to apparel, or skill in adjusting it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress', 'n.', 'The system of furrows on the face of a millstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress coat', '', 'A coat with skirts behind only, as distinct from the\n   frock coat, of which the skirts surround the body. It is worn on\n   occasions of ceremony. The dress coat of officers of the United States\n   army is a full-skirted frock coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresser', 'n.', 'One who dresses; one who put in order or makes ready for\n   use; one who on clothes or ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresser', 'n.', 'A kind of pick for shaping large coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresser', 'n.', 'An assistant in a hospital, whose office it is to dress\n   wounds, sores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresser', 'v. t.', 'A table or bench on which meat and other things are\n   dressed, or prepared for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dresser', 'v. t.', 'A cupboard or set of shelves to receive dishes and\n   cooking utensils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dress goods', '', 'A term applied to fabrics for the gowns of women and\n   girls; -- most commonly to fabrics of mixed materials, but also\n   applicable to silks, printed linens, and calicoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressiness', 'n.', 'The state of being dressy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'Dress; raiment; especially, ornamental habiliment or\n   attire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'An application (a remedy, bandage, etc.) to a sore or\n   wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'Manure or compost over land. When it remains on the\n   surface, it is called a top-dressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'A preparation to fit food for use; a condiment; as, a\n   dressing for salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'The stuffing of fowls, pigs, etc.; forcemeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'Gum, starch, and the like, used in stiffening or\n   finishing silk, linen, and other fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'An ornamental finish, as a molding around doors, windows,\n   or on a ceiling, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressing', 'n.', 'Castigation; scolding; -- often with down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressmaker', 'n.', 'A maker of gowns, or similar garments; a mantuamaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressmaking', 'n.', 'The art, process, or occupation, of making dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dressy', 'a.', 'Showy in dress; attentive to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drest', 'p. p.', 'of Dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dretch', 'v. t. & i.', 'See Drecche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreul', 'v. i.', 'To drool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drevil', 'n.', 'A fool; a drudge. See Drivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drew', 'imp.', 'of Draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drey', 'n.', 'A squirrels nest. See Dray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreye', 'a.', 'Dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dreynt', '', 'p. p., of Drench to drown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribbed', 'imp. & p. p.', 'of Drib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribbing', 'p. pr. & vb. n.', 'of Drib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'v. t.', 'To do by little and little');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'v. t.', 'To cut off by a little at a time; to crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'v. t.', 'To appropriate unlawfully; to filch; to defalcate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'v. t.', 'To lead along step by step; to entice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'v. t. & i.', 'To shoot (a shaft) so as to pierce on the descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drib', 'n.', 'A drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribber', 'n.', 'One who dribs; one who shoots weakly or badly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribbled', 'imp. & p. p.', 'of Dribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribbing', 'p. pr. & vb. n.', 'of Dribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribble', 'v. i.', 'To fall in drops or small drops, or in a quick\n   succession of drops; as, water dribbles from the eaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribble', 'v. i.', 'To slaver, as a child or an idiot; to drivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribble', 'v. i.', 'To fall weakly and slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribble', 'v. t.', 'To let fall in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribble', 'n.', 'A drizzling shower; a falling or leaking in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribbler', 'n.', 'One who dribbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dribblet', 'n.', 'Alt. of Driblet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driblet', 'n.', 'A small piece or part; a small sum; a small quantity of\n   money in making up a sum; as, the money was paid in dribblets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drie', 'v. t.', 'To endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dried', 'imp. & p. p.', 'of Day. Also adj.; as, dried apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drier', 'n.', 'One who, or that which, dries; that which may expel or\n   absorb moisture; a desiccative; as, the sun and a northwesterly wind\n   are great driers of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drier', 'n.', 'Drying oil; a substance mingled with the oil used in oil\n   painting to make it dry quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drier', 'superl.', 'Alt. of Driest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driest', 'superl.', 'of Dry, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A driving; a violent movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The act or motion of drifting; the force which impels or\n   drives; an overpowering influence or impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'Course or direction along which anything is driven; setting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The tendency of an act, argument, course of conduct, or the\n   like; object aimed at or intended; intention; hence, also, import or\n   meaning of a sentence or discourse; aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'That which is driven, forced, or urged along');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'Anything driven at random.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A mass of matter which has been driven or forced onward\n   together in a body, or thrown together in a heap, etc., esp. by wind or\n   water; as, a drift of snow, of ice, of sand, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A drove or flock, as of cattle, sheep, birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The horizontal thrust or pressure of an arch or vault upon\n   the abutments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A collection of loose earth and rocks, or boulders, which\n   have been distributed over large portions of the earths surface,\n   especially in latitudes north of forty degrees, by the agency of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'In South Africa, a ford in a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A slightly tapered tool of steel for enlarging or shaping a\n   hole in metal, by being forced or driven into or through it; a broach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A tool used in driving down compactly the composition\n   contained in a rocket, or like firework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A deviation from the line of fire, peculiar to oblong\n   projectiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'A passage driven or cut between shaft and shaft; a driftway;\n   a small subterranean gallery; an adit or tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The distance through which a current flows in a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The angle which the line of a ships motion makes with the\n   meridian, in drifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The distance to which a vessel is carried off from her\n   desired course by the wind, currents, or other causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The place in a deep-waisted vessel where the sheer is raised\n   and the rail is cut off, and usually terminated with a scroll, or\n   driftpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The distance between the two blocks of a tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'n.', 'The difference between the size of a bolt and the hole into\n   which it is driven, or between the circumference of a hoop and that of\n   the mast on which it is to be driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drifted', 'imp. & p. p.', 'of Drift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drifting', 'p. pr. & vb. n.', 'of Drift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. i.', 'To float or be driven along by, or as by, a current of\n   water or air; as, the ship drifted astern; a raft drifted ashore; the\n   balloon drifts slowly east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. i.', 'To accumulate in heaps by the force of wind; to be driven\n   into heaps; as, snow or sand drifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. i.', 'to make a drift; to examine a vein or ledge for the\n   purpose of ascertaining the presence of metals or ores; to follow a\n   vein; to prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. t.', 'To drive or carry, as currents do a floating body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. t.', 'To drive into heaps; as, a current of wind drifts snow or\n   sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'v. t.', 'To enlarge or shape, as a hole, with a drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drift', 'a.', 'That causes drifting or that is drifted; movable by wind or\n   currents; as, drift currents; drift ice; drift mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftage', 'n.', 'Deviation from a ships course due to leeway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftage', 'n.', 'Anything that drifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftbolt', 'n.', 'A bolt for driving out other bolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftless', 'a.', 'Having no drift or direction; without aim; purposeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftpiece', 'n.', 'An upright or curved piece of timber connecting the\n   plank sheer with the gunwale; also, a scroll terminating a rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftpin', 'n.', 'A smooth drift. See Drift, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftway', 'n.', 'A common way, road, or path, for driving cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftway', 'n.', 'Same as Drift, 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftweed', 'n.', 'Seaweed drifted to the shore by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftwind', 'n.', 'A driving wind; a wind that drives snow, sand, etc.,\n   into heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftwood', 'n.', 'Wood drifted or floated by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driftwood', 'n.', 'Fig.: Whatever is drifting or floating as on water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drifty', 'a.', 'Full of drifts; tending to form drifts, as snow, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilled', 'imp. & p. p.', 'of Drill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilling', 'p. pr. & vb. n.', 'of Drill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To pierce or bore with a drill, or a with a drill; to\n   perforate; as, to drill a hole into a rock; to drill a piece of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To train in the military art; to exercise diligently, as\n   soldiers, in military evolutions and exercises; hence, to instruct\n   thoroughly in the rudiments of any art or branch of knowledge; to\n   discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. i.', 'To practice an exercise or exercises; to train ones\n   self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'An instrument with an edged or pointed end used for making\n   holes in hard substances; strictly, a tool that cuts with its end, by\n   revolving, as in drilling metals, or by a succession of blows, as in\n   drilling stone; also, a drill press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'The act or exercise of training soldiers in the military\n   art, as in the manual of arms, in the execution of evolutions, and the\n   like; hence, diligent and strict instruction and exercise in the\n   rudiments and methods of any business; a kind or method of military\n   exercises; as, infantry drill; battalion drill; artillery drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'Any exercise, physical or mental, enforced with regularity\n   and by constant repetition; as, a severe drill in Latin grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'A marine gastropod, of several species, which kills oysters\n   and other bivalves by drilling holes through the shell. The most\n   destructive kind is Urosalpinx cinerea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To cause to flow in drills or rills or by trickling; to\n   drain by trickling; as, waters drilled through a sandy stratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To sow, as seeds, by dribbling them along a furrow or in\n   a row, like a trickling rill of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To entice; to allure from step; to decoy; -- with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. t.', 'To cause to slip or waste away by degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. i.', 'To trickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'v. i.', 'To sow in drills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'A small trickling stream; a rill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'An implement for making holes for sowing seed, and sometimes\n   so formed as to contain seeds and drop them into the hole made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'A light furrow or channel made to put seed into sowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'A row of seed sown in a furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'A large African baboon (Cynocephalus leucophaeus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill', 'n.', 'Same as Drilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driller', 'n.', 'One who, or that which, drills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilling', 'n.', 'The act of piercing with a drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilling', 'n.', 'A training by repeated exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilling', 'n.', 'The act of using a drill in sowing seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drilling', 'n.', 'A heavy, twilled fabric of linen or cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drillmaster', 'n.', 'One who teaches drill, especially in the way of\n   gymnastics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drill press', '', 'A machine for drilling holes in metal, the drill being\n   pressed to the metal by the action of a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drillstock', 'n.', 'A contrivance for holding and turning a drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drily', 'adv.', 'See Dryly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drimys', 'n.', 'A genus of magnoliaceous trees. Drimys aromatica furnishes\n   Winters bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drank', 'imp.', 'of Drink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunk', '', 'of Drink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunk', 'p. p.', 'of Drink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunken', '', 'of Drink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinking', 'p. pr. & vb. n.', 'of Drink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. i.', 'To swallow anything liquid, for quenching thirst or other\n   purpose; to imbibe; to receive or partake of, as if in satisfaction of\n   thirst; as, to drink from a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. i.', 'To quaff exhilarating or intoxicating liquors, in\n   merriment or feasting; to carouse; to revel; hence, to lake alcoholic\n   liquors to excess; to be intemperate in the /se of intoxicating or\n   spirituous liquors; to tipple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. t.', 'To swallow (a liquid); to receive, as a fluid, into the\n   stomach; to imbibe; as, to drink milk or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. t.', 'To take in (a liquid), in any manner; to suck up; to\n   absorb; to imbibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. t.', 'To take in; to receive within one, through the senses; to\n   inhale; to hear; to see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'v. t.', 'To smoke, as tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'n.', 'Liquid to be swallowed; any fluid to be taken into the\n   stomach for quenching thirst or for other purposes, as water, coffee,\n   or decoctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drink', 'n.', 'Specifically, intoxicating liquor; as, when drink is on, wit\n   is out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinkable', 'a.', 'Capable of being drunk; suitable for drink; potable.\n   Macaulay. Also used substantively, esp. in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinkableness', 'n.', 'State of being drinkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinker', 'n.', 'One who drinks; as, the effects of tea on the drinker;\n   also, one who drinks spirituous liquors to excess; a drunkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinking', 'n.', 'The act of one who drinks; the act of imbibing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinking', 'n.', 'The practice of partaking to excess of intoxicating\n   liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinking', 'n.', 'An entertainment with liquors; a carousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drinkless', 'a.', 'Destitute of drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripped', 'imp. & p. p.', 'of Drip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dript', '', 'of Drip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripping', 'p. pr. & vb. n.', 'of Drip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drip', 'v. i.', 'To fall in drops; as, water drips from the eaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drip', 'v. i.', 'To let fall drops of moisture or liquid; as, a wet garment\n   drips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drip', 'v. t.', 'To let fall in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drip', 'n.', 'A falling or letting fall in drops; a dripping; that which\n   drips, or falls in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drip', 'n.', 'That part of a cornice, sill course, or other horizontal\n   member, which projects beyond the rest, and is of such section as to\n   throw off the rain water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripping', 'n.', 'A falling in drops, or the sound so made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripping', 'n.', 'That which falls in drops, as fat from meat in roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripple', 'a.', 'Weak or rare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dripstone', 'n.', 'A drip, when made of stone. See Drip, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'imp.', 'of Drive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drave', '', 'of Drive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driven', 'p. p.', 'of Drive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driving', 'p. pr. & vb. n.', 'of Drive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To impel or urge onward by force in a direction away from\n   one, or along before one; to push forward; to compel to move on; to\n   communicate motion to; as, to drive cattle; to drive a nail; smoke\n   drives persons from a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To urge on and direct the motions of, as the beasts which\n   draw a vehicle, or the vehicle borne by them; hence, also, to take in a\n   carriage; to convey in a vehicle drawn by beasts; as, to drive a pair\n   of horses or a stage; to drive a person to his own door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To urge, impel, or hurry forward; to force; to constrain;\n   to urge, press, or bring to a point or state; as, to drive a person by\n   necessity, by persuasion, by force of circumstances, by argument, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To carry or; to keep in motion; to conduct; to prosecute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To clear, by forcing away what is contained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To dig Horizontally; to cut a horizontal gallery or\n   tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. t.', 'To pass away; -- said of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. i.', 'To rush and press with violence; to move furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. i.', 'To be forced along; to be impelled; to be moved by any\n   physical force or agent; to be driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. i.', 'To go by carriage; to pass in a carriage; to proceed by\n   directing or urging on a vehicle or the animals that draw it; as, the\n   coachman drove to my door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. i.', 'To press forward; to aim, or tend, to a point; to make an\n   effort; to strive; -- usually with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'v. i.', 'To distrain for rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'p. p.', 'Driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'n.', 'The act of driving; a trip or an excursion in a carriage, as\n   for exercise or pleasure; -- distinguished from a ride taken on\n   horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'n.', 'A place suitable or agreeable for driving; a road prepared\n   for driving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'n.', 'Violent or rapid motion; a rushing onward or away; esp., a\n   forced or hurried dispatch of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'n.', 'In type founding and forging, an impression or matrix,\n   formed by a punch drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drive', 'n.', 'A collection of objects that are driven; a mass of logs to\n   be floated down a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivebolt', 'n.', 'A drift; a tool for setting bolts home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driveled', 'imp. & p. p.', 'of Drivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivelled', '', 'of Drivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driveling', 'p. pr. & vb. n.', 'of Drivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivelling', '', 'of Drivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'v. i.', 'To slaver; to let spittle drop or flow from the mouth,\n   like a child, idiot, or dotard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'v. i.', 'To be weak or foolish; to dote; as, a driveling hero;\n   driveling love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'n.', 'Slaver; saliva flowing from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'n.', 'Inarticulate or unmeaning utterance; foolish talk; babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'n.', 'A driveler; a fool; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivel', 'n.', 'A servant; a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driveler', 'n.', 'A slaverer; a slabberer; an idiot; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driven', 'p. p.', 'of Drive. Also adj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drivepipe', 'n.', 'A pipe for forcing into the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'One who, or that which, drives; the person or thing that\n   urges or compels anything else to move onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'The person who drives beasts or a carriage; a coachman; a\n   charioteer, etc.; hence, also, one who controls the movements of a\n   locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'An overseer of a gang of slaves or gang of convicts at\n   their work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'A part that transmits motion to another part by contact\n   with it, or through an intermediate relatively movable part, as a gear\n   which drives another, or a lever which moves another through a link,\n   etc. Specifically:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'The driving wheel of a locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'An attachment to a lathe, spindle, or face plate to turn a\n   carrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'A crossbar on a grinding mill spindle to drive the upper\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driver', 'n.', 'The after sail in a ship or bark, being a fore-and-aft sail\n   attached to a gaff; a spanker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driveway', 'n.', 'A passage or way along or through which a carriage may be\n   driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driving', 'a.', 'Having great force of impulse; as, a driving wind or\n   storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driving', 'a.', 'Communicating force; impelling; as, a driving shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driving', 'n.', 'The act of forcing or urging something along; the act of\n   pressing or moving on furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Driving', 'n.', 'Tendency; drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzled', 'imp. & p. p.', 'of Drizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzling', 'p. pr. & vb. n.', 'of Drizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzle', 'v. i.', 'To rain slightly in very small drops; to fall, as water\n   from the clouds, slowly and in fine particles; as, it drizzles;\n   drizzling drops or rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzle', 'v. t.', 'To shed slowly in minute drops or particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzle', 'n.', 'Fine rain or mist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drizzly', 'a.', 'Characterized by small rain, or snow; moist and\n   disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drock', 'n.', 'A water course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drofland', 'n.', 'Alt. of Dryfland');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryfland', 'n.', 'An ancient yearly payment made by some tenants to the\n   king, or to their landlords, for the privilege of driving their cattle\n   through a manor to fairs or markets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drogher', 'n.', 'A small craft used in the West India Islands to take off\n   sugars, rum, etc., to the merchantmen; also, a vessel for transporting\n   lumber, cotton, etc., coastwise; as, a lumber drogher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drogman', 'n.', 'Alt. of Drogoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drogoman', 'n.', 'See Dragoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drogue', 'n.', 'See Drag, n., 6, and Drag sail, under Drag, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droh', 'imp.', 'of Draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droil', 'v. i.', 'To work sluggishly or slowly; to plod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droil', 'n.', 'A drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droil', 'n.', 'Mean labor; toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droit', 'n.', 'A right; law in its aspect of the foundation of rights;\n   also, in old law, the writ of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droitural', 'a.', 'relating to the mere right of property, as distinguished\n   from the right of possession; as, droitural actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droitzschka', 'n.', 'See Drosky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'superl.', 'Queer, and fitted to provoke laughter; ludicrous from\n   oddity; amusing and strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'n.', 'One whose practice it is to raise mirth by odd tricks; a\n   jester; a buffoon; a merry-andrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'n.', 'Something exhibited to raise mirth or sport, as a puppet, a\n   farce, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drolled', 'imp. & p. p.', 'of Droll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drolling', 'p. pr. & vb. n.', 'of Droll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'v. i.', 'To jest; to play the buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'v. t.', 'To lead or influence by jest or trick; to banter or jest;\n   to cajole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droll', 'v. t.', 'To make a jest of; to set in a comical light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droller', 'n.', 'A jester; a droll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drolleries', 'pl. ', 'of Drollery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollery', 'n.', 'The quality of being droll; sportive tricks; buffoonery;\n   droll stories; comical gestures or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollery', 'n.', 'Something which serves to raise mirth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollery', 'n.', 'A puppet show; also, a puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollery', 'n.', 'A lively or comic picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollingly', 'adv.', 'In a jesting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollish', 'a.', 'Somewhat droll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drollist', 'n.', 'A droll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromaeognathous', 'a.', 'Having the structure of the palate like that of\n   the ostrich and emu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromatherium', 'n.', 'A small extinct triassic mammal from North Carolina,\n   the earliest yet found in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drome', 'n.', 'The crab plover (Dromas ardeola), a peculiar North African\n   bird, allied to the oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromedaries', 'pl. ', 'of Dromedary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromedary', 'n.', 'The Arabian camel (Camelus dromedarius), having one hump\n   or protuberance on the back, in distinction from the Bactrian camel,\n   which has two humps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromond', '', 'Alt. of Dromon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dromon', '', 'In the Middle Ages, a large, fast-sailing galley, or cutter;\n   a large, swift war vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'v. i.', 'The male of bees, esp. of the honeybee. It gathers no\n   honey. See Honeybee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'v. i.', 'One who lives on the labors of others; a lazy, idle\n   fellow; a sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'v. i.', 'That which gives out a grave or monotonous tone or dull\n   sound; as: (a) A drum. [Obs.] Halliwell. (b) The part of the bagpipe\n   containing the two lowest tubes, which always sound the key note and\n   the fifth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'v. i.', 'A humming or deep murmuring sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'v. i.', 'A monotonous bass, as in a pastoral composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droned', 'imp. & p. p.', 'of Drone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droning', 'p. pr. & vb. n.', 'of Drone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'n.', 'To utter or make a low, dull, monotonous, humming or\n   murmuring sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone', 'n.', 'To love in idleness; to do nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone bee', '', 'The male of the honeybee; a drone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drone fly', '', 'A dipterous insect (Eristalis tenax), resembling the drone\n   bee. See Eristalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dronepipe', 'n.', 'One of the low-toned tubes of a bagpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drongos', 'pl. ', 'of Drongo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drongo', 'n.', 'A passerine bird of the family Dicruridae. They are usually\n   black with a deeply forked tail. They are natives of Asia, Africa, and\n   Australia; -- called also drongo shrikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dronish', 'a.', 'Like a drone; indolent; slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dronkelewe', 'a.', 'Given to drink; drunken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dronte', 'n.', 'The dodo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drony', 'a.', 'Like a drone; sluggish; lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drooled', 'imp. & p. p.', 'of Drool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drooling', 'p. pr. & vb. n.', 'of Drool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drool', 'v. i.', 'To drivel, or drop saliva; as, the child drools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drooped', 'imp. & p. p.', 'of Droop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drooping', 'p. pr. & vb. n.', 'of Droop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droop', 'v. i.', 'To hang bending downward; to sink or hang down, as an\n   animal, plant, etc., from physical inability or exhaustion, want of\n   nourishment, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droop', 'v. i.', 'To grow weak or faint with disappointment, grief, or like\n   causes; to be dispirited or depressed; to languish; as, her spirits\n   drooped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droop', 'v. i.', 'To proceed downward, or toward a close; to decline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droop', 'v. t.', 'To let droop or sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droop', 'n.', 'A drooping; as, a droop of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drooper', 'n.', 'One who, or that which, droops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droopingly', 'adv.', 'In a drooping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'The quantity of fluid which falls in one small spherical\n   mass; a liquid globule; a minim; hence, also, the smallest easily\n   measured portion of a fluid; a small quantity; as, a drop of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'That which resembles, or that which hangs like, a liquid\n   drop; as a hanging diamond ornament, an earring, a glass pendant on a\n   chandelier, a sugarplum (sometimes medicated), or a kind of shot or\n   slug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'Same as Gutta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'Any small pendent ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'Whatever is arranged to drop, hang, or fall from an elevated\n   position; also, a contrivance for lowering something');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'A door or platform opening downward; a trap door; that part\n   of the gallows on which a culprit stands when he is to be hanged;\n   hence, the gallows itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'A machine for lowering heavy weights, as packages, coal\n   wagons, etc., to a ships deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'A contrivance for temporarily lowering a gas jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'A curtain which drops or falls in front of the stage of a\n   theater, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'A drop press or drop hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'The distance of the axis of a shaft below the base of a\n   hanger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'Any medicine the dose of which is measured by drops; as,\n   lavender drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'The depth of a square sail; -- generally applied to the\n   courses only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'Act of dropping; sudden fall or descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropped', 'imp. & p. p.', 'of Drop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropt', '', 'of Drop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropping', 'p. pr. & vb. n.', 'of Drop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To pour or let fall in drops; to pour in small globules; to\n   distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To cause to fall in one portion, or by one motion, like a\n   drop; to let fall; as, to drop a line in fishing; to drop a courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To let go; to dismiss; to set aside; to have done with; to\n   discontinue; to forsake; to give up; to omit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To bestow or communicate by a suggestion; to let fall in an\n   indirect, cautious, or gentle manner; as, to drop hint, a word of\n   counsel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To lower, as a curtain, or the muzzle of a gun, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To send, as a letter; as, please drop me a line, a letter,\n   word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To give birth to; as, to drop a lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'n.', 'To cover with drops; to variegate; to bedrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To fall in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To fall, in general, literally or figuratively; as, ripe\n   fruit drops from a tree; wise words drop from the lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To let drops fall; to discharge itself in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To fall dead, or to fall in death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To come to an end; to cease; to pass out of mind; as, the\n   affair dropped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To come unexpectedly; -- with in or into; as, my old\n   friend dropped in a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To fall or be depressed; to lower; as, the point of the\n   spear dropped a little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To fall short of a mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drop', 'v. i.', 'To be deep in extent; to descend perpendicularly; as, her\n   main topsail drops seventeen yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droplet', 'n.', 'A little drop; a tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droplight', 'n.', 'An apparatus for bringing artificial light down from a\n   chandelier nearer to a table or desk; a pendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropmeal', 'adv.', 'Alt. of Dropmele');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropmele', 'adv.', 'By drops or small portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropper', 'n.', 'One who, or that which, drops. Specif.: (Fishing) A fly\n   that drops from the leaden above the bob or end fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropper', 'n.', 'A dropping tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropper', 'n.', 'A branch vein which drops off from, or leaves, the main\n   lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropper', 'n.', 'A dog which suddenly drops upon the ground when it sights\n   game, -- formerly a common, and still an occasional, habit of the\n   setter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropping', 'n.', 'The action of causing to drop or of letting drop;\n   falling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropping', 'n.', 'That which falls in drops; the excrement or dung of\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droppinly', 'adv.', 'In drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsical', 'a.', 'Diseased with dropsy; hydropical; tending to dropsy; as,\n   a dropsical patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsical', 'a.', 'Of or pertaining to dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsicalness', 'n.', 'State of being dropsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsied', 'a.', 'Diseased with drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsies', 'pl. ', 'of Dropsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropsy', 'n.', 'An unnatural collection of serous fluid in any serous\n   cavity of the body, or in the subcutaneous cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropt', '', 'imp. & p. p. of Drop, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropwise', 'adv.', 'After the manner of a drop; in the form of drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropworm', 'n.', 'The larva of any geometrid moth, which drops from trees\n   by means of a thread of silk, as the cankerworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dropwort', 'n.', 'An Old World species of Spiraea (S. filipendula), with\n   finely cut leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drosera', 'n.', 'A genus of low perennial or biennial plants, the leaves of\n   which are beset with gland-tipped bristles. See Sundew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droskies', 'pl. ', 'of Drosky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drosky', 'n.', 'A low, four-wheeled, open carriage, used in Russia,\n   consisting of a kind of long, narrow bench, on which the passengers\n   ride as on a saddle, with their feet reaching nearly to the ground.\n   Other kinds of vehicles are now so called, esp. a kind of victoria\n   drawn by one or two horses, and used as a public carriage in German\n   cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drosometer', 'n.', 'An instrument for measuring the quantity of dew on the\n   surface of a body in the open air. It consists of a balance, having a\n   plate at one end to receive the dew, and at the other a weight\n   protected from the deposit of dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dross', 'n.', 'The scum or refuse matter which is thrown off, or falls\n   from, metals in smelting the ore, or in the process of melting;\n   recrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dross', 'n.', 'Rust of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dross', 'n.', 'Waste matter; any worthless matter separated from the better\n   part; leavings; dregs; refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drossel', 'n.', 'A slut; a hussy; a drazel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drossless', 'a.', 'Free from dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drossy', 'superl.', 'Of, pertaining to, resembling, dross; full of dross;\n   impure; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drotchel', 'n.', 'See Drossel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drough', 'imp.', 'of Draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drought', 'n.', 'Dryness; want of rain or of water; especially, such\n   dryness of the weather as affects the earth, and prevents the growth of\n   plants; aridity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drought', 'n.', 'Thirst; want of drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drought', 'n.', 'Scarcity; lack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droughtiness', 'n.', 'A state of dryness of the weather; want of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droughty', 'a.', 'Characterized by drought; wanting rain; arid; adust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droughty', 'a.', 'Dry; thirsty; wanting drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droumy', 'a.', 'Troubled; muddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drouth', 'n.', 'Same as Drought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drouthy', 'a.', 'Droughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'imp.', 'of Drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'A collection of cattle driven, or cattle collected for\n   driving; a number of animals, as oxen, sheep, or swine, driven in a\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'Any collection of irrational animals, moving or driving\n   forward; as, a finny drove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'A crowd of people in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'A road for driving cattle; a driftway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'A narrow drain or channel used in the irrigation of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'A broad chisel used to bring stone to a nearly smooth\n   surface; -- called also drove chisel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drove', 'n.', 'The grooved surface of stone finished by the drove chisel;\n   -- called also drove work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droven', 'p. p.', 'of Drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drover', 'n.', 'One who drives cattle or sheep to market; one who makes it\n   his business to purchase cattle, and drive them to market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drover', 'n.', 'A boat driven by the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drovy', 'a.', 'Turbid; muddy; filthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drow', 'imp.', 'of Draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowned', 'imp. & p. p.', 'of Drown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowning', 'p. pr. & vb. n.', 'of Drown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drown', 'v. i.', 'To be suffocated in water or other fluid; to perish in\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drown', 'v. t.', 'To overwhelm in water; to submerge; to inundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drown', 'v. t.', 'To deprive of life by immersion in water or other liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drown', 'v. t.', 'To overpower; to overcome; to extinguish; -- said\n   especially of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drownage', 'n.', 'The act of drowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowner', 'n.', 'One who, or that which, drowns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsed', 'imp. & p. p.', 'of Drowse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsing', 'p. pr. & vb. n.', 'of Drowse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowse', 'v. i.', 'To sleep imperfectly or unsoundly; to slumber; to be\n   heavy with sleepiness; to doze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowse', 'v. t.', 'To make heavy with sleepiness or imperfect sleep; to\n   make dull or stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowse', 'n.', 'A slight or imperfect sleep; a doze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsihead', 'n.', 'Drowsiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsihed', 'n.', 'Drowsihead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsily', 'adv.', 'In a drowsy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsiness', 'n.', 'State of being drowsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsy', 'superl.', 'Inclined to drowse; heavy with sleepiness; lethargic;\n   dozy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsy', 'superl.', 'Disposing to sleep; lulling; soporific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowsy', 'superl.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drowth', 'n.', 'See Drought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Droyle', 'v. i.', 'See Droil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drubbed', 'imp. & p. p.', 'of Drub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drubbing', 'p. pr. & vb. n.', 'of Drub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drub', 'v. t.', 'To beat with a stick; to thrash; to cudgel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drub', 'n.', 'A blow with a cudgel; a thump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drubber', 'n.', 'One who drubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudged', 'imp. & p. p.', 'of Drudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudging', 'p. pr. & vb. n.', 'of Drudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudge', 'v. i.', 'To perform menial work; to labor in mean or unpleasant\n   offices with toil and fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudge', 'v. t.', 'To consume laboriously; -- with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudge', 'n.', 'One who drudges; one who works hard in servile employment;\n   a mental servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudger', 'n.', 'One who drudges; a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudger', 'n.', 'A dredging box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudgery', 'n.', 'The act of drudging; disagreeable and wearisome labor;\n   ignoble or slavish toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudging box', '', 'See Dredging box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drudgingly', 'adv.', 'In a drudging manner; laboriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druery', 'n.', 'Courtship; gallantry; love; an object of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'v. i.', 'To drudge; to toil laboriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'n.', 'A drudge (?).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'n.', 'Any animal, vegetable, or mineral substance used in the\n   composition of medicines; any stuff used in dyeing or in chemical\n   operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'n.', 'Any commodity that lies on hand, or is not salable; an\n   article of slow sale, or in no demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugged', 'imp. & p. p.', 'of Drug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugging', 'p. pr. & vb. n.', 'of Drug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'v. i.', 'To prescribe or administer drugs or medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'v. t.', 'To affect or season with drugs or ingredients; esp., to\n   stupefy by a narcotic drug. Also Fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'v. t.', 'To tincture with something offensive or injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drug', 'v. t.', 'To dose to excess with, or as with, drugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugger', 'n.', 'A druggist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugget', 'n.', 'A coarse woolen cloth dyed of one color or printed on one\n   side; generally used as a covering for carpets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugget', 'n.', 'By extension, any material used for the same purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druggist', 'n.', 'One who deals in drugs; especially, one who buys and\n   sells drugs without compounding them; also, a pharmaceutist or\n   apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drugster', 'n.', 'A druggist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druid', 'n.', 'One of an order of priests which in ancient times existed\n   among certain branches of the Celtic race, especially among the Gauls\n   and Britons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druid', 'n.', 'A member of a social and benevolent order, founded in London\n   in 1781, and professedly based on the traditions of the ancient Druids.\n   Lodges or groves of the society are established in other countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druidess', 'n.', 'A female Druid; a prophetess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druidic', 'a.', 'Alt. of Druidical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druidical', 'a.', 'Pertaining to, or resembling, the Druids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druidish', 'a.', 'Druidic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druidism', 'n.', 'The system of religion, philosophy, and instruction,\n   received and taught by the Druids; the rites and ceremonies of the\n   Druids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'An instrument of percussion, consisting either of a hollow\n   cylinder, over each end of which is stretched a piece of skin or\n   vellum, to be beaten with a stick; or of a metallic hemisphere\n   (kettledrum) with a single piece of skin to be so beaten; the common\n   instrument for marking time in martial music; one of the pair of\n   tympani in an orchestra, or cavalry band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'Anything resembling a drum in form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'A sheet iron radiator, often in the shape of a drum, for\n   warming an apartment by means of heat received from a stovepipe, or a\n   cylindrical receiver for steam, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'A small cylindrical box in which figs, etc., are packed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'The tympanum of the ear; -- often, but incorrectly, applied\n   to the tympanic membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'One of the cylindrical, or nearly cylindrical, blocks, of\n   which the shaft of a column is composed; also, a vertical wall, whether\n   circular or polygonal in plan, carrying a cupola or dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'A cylinder on a revolving shaft, generally for the purpose of\n   driving several pulleys, by means of belts or straps passing around its\n   periphery; also, the barrel of a hoisting machine, on which the rope or\n   chain is wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'See Drumfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'A noisy, tumultuous assembly of fashionable people at a\n   private house; a rout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'n.', 'A tea party; a kettledrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummed', 'imp. & p. p.', 'of Drum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumming', 'p. pr. & vb. n.', 'of Drum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. i.', 'To beat a drum with sticks; to beat or play a tune on a\n   drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. i.', 'To beat with the fingers, as with drumsticks; to beat with\n   a rapid succession of strokes; to make a noise like that of a beaten\n   drum; as, the ruffed grouse drums with his wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. i.', 'To throb, as the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. i.', 'To go about, as a drummer does, to gather recruits, to\n   draw or secure partisans, customers, etc,; -- with for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. t.', 'To execute on a drum, as a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. t.', '(With out) To expel ignominiously, with beat of drum; as,\n   to drum out a deserter or rogue from a camp, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum', 'v. t.', '(With up) To assemble by, or as by, beat of drum; to\n   collect; to gather or draw by solicitation; as, to drum up recruits; to\n   drum up customers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumbeat', 'n.', 'The sound of a beaten drum; drum music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumble', 'v. i.', 'To be sluggish or lazy; to be confused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumble', 'v. i.', 'To mumble in speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumfish', 'n.', 'Any fish of the family Sciaenidae, which makes a loud\n   noise by means of its air bladder; -- called also drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumhead', 'n.', 'The parchment or skin stretched over one end of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumhead', 'n.', 'The top of a capstan which is pierced with sockets for\n   levers used in turning it. See Illust. of Capstan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumlin', 'n.', 'A hill of compact, unstratified, glacial drift or till,\n   usually elongate or oval, with the larger axis parallel to the former\n   local glacial motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumly', 'a.', 'Turbid; muddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum major', '', '.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum major', '', 'The chief or first drummer of a regiment; an instructor\n   of drummers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum major', '', 'The marching leader of a military band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drum major', '', 'A noisy gathering. [R.] See under Drum, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'One whose office is to best the drum, as in military\n   exercises and marching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'One who solicits custom; a commercial traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'A fish that makes a sound when caught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'The squeteague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'A California sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummer', 'n.', 'A large West Indian cockroach (Blatta gigantea) which\n   drums on woodwork, as a sexual call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumming', 'n.', 'The act of beating upon, or as if upon, a drum; also, the\n   noise which the male of the ruffed grouse makes in spring, by beating\n   his wings upon his sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drummond light', '', 'A very intense light, produced by turning two streams\n   of gas, one oxygen and the other hydrogen, or coal gas, in a state of\n   ignition, upon a ball of lime; or a stream of oxygen gas through a\n   flame of alcohol upon a ball or disk of lime; -- called also oxycalcium\n   light, or lime light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumstick', 'n.', 'A stick with which a drum is beaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drumstick', 'n.', 'Anything resembling a drumstick in form, as the\n   tibiotarsus, or second joint, of the leg of a fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunk', 'a.', 'Intoxicated with, or as with, strong drink; inebriated;\n   drunken; -- never used attributively, but always predicatively; as, the\n   man is drunk (not, a drunk man).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunk', 'a.', 'Drenched or saturated with moisture or liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunk', 'n.', 'A drunken condition; a spree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkard', 'n.', 'One who habitually drinks strong liquors immoderately;\n   one whose habit it is to get drunk; a toper; a sot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunken', 'v. i.', 'Overcome by strong drink; intoxicated by, or as by,\n   spirituous liquor; inebriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunken', 'v. i.', 'Saturated with liquid or moisture; drenched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunken', 'v. i.', 'Pertaining to, or proceeding from, intoxication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkenhead', 'n.', 'Drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkenly', 'adv.', 'In a drunken manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkenness', 'n.', 'The state of being drunken with, or as with, alcoholic\n   liquor; intoxication; inebriety; -- used of the casual state or the\n   habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkenness', 'n.', 'Disorder of the faculties, resembling intoxication by\n   liquors; inflammation; frenzy; rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkenship', 'n.', 'Alt. of Drunkship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drunkship', 'n.', 'The state of being drunk; drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drupaceous', 'a.', 'Producing, or pertaining to, drupes; having the form of\n   drupes; as, drupaceous trees or fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drupal', 'a.', 'Drupaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drupe', 'n.', 'A fruit consisting of pulpy, coriaceous, or fibrous exocarp,\n   without valves, containing a nut or stone with a kernel. The exocarp is\n   succulent in the plum, cherry, apricot, peach, etc.; dry and\n   subcoriaceous in the almond; and fibrous in the cocoanut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drupel', 'n.', 'Alt. of Drupelet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drupelet', 'n.', 'A small drupe, as one of the pulpy grains of the\n   blackberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druse', 'n.', 'A cavity in a rock, having its interior surface studded with\n   crystals and sometimes filled with water; a geode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druse', 'n.', 'One of a people and religious sect dwelling chiefly in the\n   Lebanon mountains of Syria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drusy', 'a.', 'Alt. of Drused');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drused', 'a.', 'Covered with a large number of minute crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druxey', 'a.', 'Alt. of Druxy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Druxy', 'a.', 'Having decayed spots or streaks of a whitish color; -- said\n   of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Free from moisture; having little humidity or none; arid;\n   not wet or moist; deficient in the natural or normal supply of\n   moisture, as rain or fluid of any kind; -- said especially: (a) Of the\n   weather: Free from rain or mist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Of vegetable matter: Free from juices or sap; not\n   succulent; not green; as, dry wood or hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Of animals: Not giving milk; as, the cow is dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Of persons: Thirsty; needing drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Of the eyes: Not shedding tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Of certain morbid conditions, in which there is entire or\n   comparative absence of moisture; as, dry gangrene; dry catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Destitute of that which interests or amuses; barren;\n   unembellished; jejune; plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Characterized by a quality somewhat severe, grave, or\n   hard; hence, sharp; keen; shrewd; quaint; as, a dry tone or manner; dry\n   wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'superl.', 'Exhibiting a sharp, frigid preciseness of execution, or\n   the want of a delicate contour in form, and of easy transition in\n   coloring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dried', 'imp. & p. p.', 'of Dry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drying', 'p. pr. & vb. n.', 'of Dry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'a.', 'To make dry; to free from water, or from moisture of any kind,\n   and by any means; to exsiccate; as, to dry the eyes; to dry ones\n   tears; the wind dries the earth; to dry a wet cloth; to dry hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'v. i.', 'To grow dry; to become free from wetness, moisture, or\n   juice; as, the road dries rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'v. i.', 'To evaporate wholly; to be exhaled; -- said of moisture, or\n   a liquid; -- sometimes with up; as, the stream dries, or dries up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry', 'v. i.', 'To shrivel or wither; to lose vitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryad', 'n.', 'A wood nymph; a nymph whose life was bound up with that of\n   her tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryandra', 'n.', 'A genus of shrubs growing in Australia, having beautiful,\n   hard, dry, evergreen leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryades', 'pl. ', 'of Dryas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryas', 'n.', 'A dryad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-beat', 'v. t.', 'To beat severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-boned', 'a.', 'Having dry bones, or bones without flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry dock', '', 'See under Dock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryer', 'n.', 'See Drier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-eyed', 'a.', 'Not having tears in the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-fisted', 'a.', 'Niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryfoot', 'n.', 'The scent of the game, as far as it can be traced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry goods', '', 'A commercial name for textile fabrics, cottons, woolens,\n   linen, silks, laces, etc., -- in distinction from groceries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drying', 'a.', 'Adapted or tending to exhaust moisture; as, a drying wind\n   or day; a drying room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drying', 'a.', 'Having the quality of rapidly becoming dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryly', 'adv.', 'In a dry manner; not succulently; without interest;\n   without sympathy; coldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryness', 'n.', 'The state of being dry. See Dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry nurse', '', 'A nurse who attends and feeds a child by hand; -- in\n   distinction from a wet nurse, who suckles it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drynurse', 'v. t.', 'To feed, attend, and bring up without the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryobalanops', 'n.', 'The genus to which belongs the single species D.\n   Camphora, a lofty resinous tree of Borneo and Sumatra, yielding Borneo\n   camphor and camphor oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-rubbed', 'imp. & p. p.', 'of Dry-rub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-rubbing', 'p. pr. & vb. n.', 'of Dry-rub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-rub', 'v. t.', 'To rub and cleanse without wetting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drysalter', 'n.', 'A dealer in salted or dried meats, pickles, sauces,\n   etc., and in the materials used in pickling, salting, and preserving\n   various kinds of food Hence drysalters usually sell a number of saline\n   substances and miscellaneous drugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drysaltery', 'n.', 'The articles kept by a drysalter; also, the business of\n   a drysalter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-shod', 'a.', 'Without wetting the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dry-stone', 'a.', 'Constructed of uncemented stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dryth', 'n.', 'Alt. of Drith');
  INSERT INTO words (word, word_type, description) VALUES  (    'Drith', 'n.', 'Drought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duad', 'n.', 'A union of two; duality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dual', 'a.', 'Expressing, or consisting of, the number two; belonging to\n   two; as, the dual number of nouns, etc. , in Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualin', 'n.', 'An explosive substance consisting essentially of sawdust or\n   wood pulp, saturated with nitroglycerin and other similar nitro\n   compounds. It is inferior to dynamite, and is more liable to explosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualism', 'n.', 'State of being dual or twofold; a twofold division; any\n   system which is founded on a double principle, or a twofold distinction');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualism', 'n.', 'A view of man as constituted of two original and\n   independent elements, as matter and spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualism', 'n.', 'A system which accepts two gods, or two original\n   principles, one good and the other evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualism', 'n.', 'The doctrine that all mankind are divided by the arbitrary\n   decree of God, and in his eternal foreknowledge, into two classes, the\n   elect and the reprobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualism', 'n.', 'The theory that each cerebral hemisphere acts\n   independently of the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualist', 'n.', 'One who believes in dualism; a ditheist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualist', 'n.', 'One who administers two offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dualistic', 'a.', 'Consisting of two; pertaining to dualism or duality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duality', 'n.', 'The quality or condition of being two or twofold; dual\n   character or usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duan', 'n.', 'A division of a poem corresponding to a canto; a poem or\n   song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duarchy', 'n.', 'Government by two persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbed', 'imp. & p. p.', 'of Dub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbing', 'p. pr. & vb. n.', 'of Dub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To confer knighthood upon; as, the king dubbed his son\n   Henry a knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To invest with any dignity or new character; to entitle; to\n   call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To clothe or invest; to ornament; to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To strike, rub, or dress smooth; to dab;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To dress with an adz; as, to dub a stick of timber smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To strike cloth with teasels to raise a nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To rub or dress with grease, as leather in the process of\n   cyrrying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. t.', 'To prepare for fighting, as a gamecock, by trimming the\n   hackles and cutting off the comb and wattles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'v. i.', 'To make a noise by brisk drumbeats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'n.', 'A blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dub', 'n.', 'A pool or puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubb', 'n.', 'The Syrian bear. See under Bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubber', 'n.', 'One who, or that which, dubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubber', 'n.', 'A globular vessel or bottle of leather, used in India to\n   hold ghee, oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbing', 'n.', 'The act of dubbing, as a knight, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbing', 'n.', 'The act of rubbing, smoothing, or dressing; a dressing off\n   smooth with an adz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbing', 'n.', 'A dressing of flour and water used by weavers; a mixture\n   of oil and tallow for dressing leather; daubing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubbing', 'n.', 'The body substance of an anglers fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubieties', 'pl. ', 'of Dubiety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubiety', 'n.', 'Doubtfulness; uncertainty; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubiosities', 'pl. ', 'of Dubiosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubiosity', 'n.', 'The state of being doubtful; a doubtful statement or\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubious', 'a.', 'Doubtful or not settled in opinion; being in doubt;\n   wavering or fluctuating; undetermined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubious', 'a.', 'Occasioning doubt; not clear, or obvious; equivocal;\n   questionable; doubtful; as, a dubious answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubious', 'a.', 'Of uncertain event or issue; as, in dubious battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubiously', 'adv.', 'In a dubious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubiousness', 'n.', 'State of being dubious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubitable', 'a.', 'Liable to be doubted; uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubitancy', 'n.', 'Doubt; uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubitate', 'v. i.', 'To doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubitation', 'n.', 'Act of doubting; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dubitative', 'a.', 'Tending to doubt; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duboisia', 'n.', 'Same as Duboisine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duboisine', 'n.', 'An alkaloid obtained from the leaves of an Australian\n   tree (Duboisia myoporoides), and regarded as identical with\n   hyoscyamine. It produces dilation of the pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducal', 'a.', 'Of or pertaining to a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducally', 'adv.', 'In the manner of a duke, or in a manner becoming the\n   rank of a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducat', 'n.', 'A coin, either of gold or silver, of several countries in\n   Europe; originally, one struck in the dominions of a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducatoon', 'n.', 'A silver coin of several countries of Europe, and of\n   different values.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duces tecum', '', 'A judicial process commanding a person to appear in\n   court and bring with him some piece of evidence or other thing to be\n   produced to the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duchess', 'n.', 'The wife or widow of a duke; also, a lady who has the\n   sovereignty of a duchy in her own right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duchesse dAngouleme', '', 'A variety of pear of large size and excellent\n   flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duchies', 'pl. ', 'of Duchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duchy', 'n.', 'The territory or dominions of a duke; a dukedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'n.', 'A pet; a darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'n.', 'A linen (or sometimes cotton) fabric, finer and lighter than\n   canvas, -- used for the lighter sails of vessels, the sacking of beds,\n   and sometimes for mens clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'n.', 'The light clothes worn by sailors in hot climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducked', 'imp. & p. p.', 'of Duck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducking', 'p. pr. & vb. n.', 'of Duck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. t.', 'To thrust or plunge under water or other liquid and\n   suddenly withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. t.', 'To plunge the head of under water, immediately withdrawing\n   it; as, duck the boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. t.', 'To bow; to bob down; to move quickly with a downward\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. i.', 'To go under the surface of water and immediately reappear;\n   to dive; to plunge the head in water or other liquid; to dip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. i.', 'To drop the head or person suddenly; to bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. t.', 'Any bird of the subfamily Anatinae, family Anatidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck', 'v. t.', 'A sudden inclination of the bead or dropping of the\n   person, resembling the motion of a duck in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duckbill', 'n.', 'See Duck mole, under Duck, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck-billed', 'a.', 'Having a bill like that of a duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducker', 'n.', 'One who, or that which, ducks; a plunger; a diver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducker', 'n.', 'A cringing, servile person; a fawner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducking', '', 'n. & a., from Duck, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duck-legged', 'a.', 'Having short legs, like a waddling duck; short-legged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duckling', 'n.', 'A young or little duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duckmeat', 'n.', 'Alt. of Ducks-meat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducks-meat', 'n.', 'Duckweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducks-bill', 'a.', 'Having the form of a ducks bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducks-foot', 'n.', 'The May apple (Podophyllum peltatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duckweed', 'n.', 'A genus (Lemna) of small plants, seen floating in great\n   quantity on the surface of stagnant pools fresh water, and supposed to\n   furnish food for ducks; -- called also duckmeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duct', 'n.', 'Any tube or canal by which a fluid or other substance is\n   conducted or conveyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duct', 'n.', 'One of the vessels of an animal body by which the products of\n   glandular secretion are conveyed to their destination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duct', 'n.', 'A large, elongated cell, either round or prismatic, usually\n   found associated with woody fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duct', 'n.', 'Guidance; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductible', 'a.', 'Capable of being drawn out');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductile', 'a.', 'Easily led; tractable; complying; yielding to motives,\n   persuasion, or instruction; as, a ductile people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductile', 'a.', 'Capable of being elongated or drawn out, as into wire or\n   threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductilimeter', 'n.', 'An instrument for accurately determining the\n   ductility of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductility', 'n.', 'The property of a metal which allows it to be drawn into\n   wires or filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductility', 'n.', 'Tractableness; pliableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duction', 'n.', 'Guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductless', 'a.', 'Having to duct or outlet; as, a ductless gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductor', 'n.', 'One who leads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ductor', 'n.', 'A contrivance for removing superfluous ink or coloring\n   matter from a roller. See Doctor, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ducture', 'n.', 'Guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudder', 'v. t.', 'To confuse or confound with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudder', 'v. i.', 'To shiver or tremble; to dodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudder', 'n.', 'A peddler or hawker, especially of cheap and flashy goods\n   pretended to be smuggled; a duffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duddery', 'n.', 'A place where rags are bought and kept for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dude', 'n.', 'A kind of dandy; especially, one characterized by an\n   ultrafashionable style of dress and other affectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudeen', 'n.', 'A short tobacco pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudgeon', 'n.', 'The root of the box tree, of which hafts for daggers were\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudgeon', 'n.', 'The haft of a dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudgeon', 'n.', 'A dudgeon-hafted dagger; a dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudgeon', 'n.', 'Resentment; ill will; anger; displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudgeon', 'a.', 'Homely; rude; coarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dudish', 'a.', 'Like, or characterized of, a dude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duds', 'n. pl.', 'Old or inferior clothes; tattered garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duds', 'n. pl.', 'Effects, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'a.', 'Owed, as a debt; that ought to be paid or done to or for\n   another; payable; owing and demandable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'a.', 'Justly claimed as a right or property; proper; suitable;\n   becoming; appropriate; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'a.', 'Such as (a thing) ought to be; fulfilling obligation; proper;\n   lawful; regular; appointed; sufficient; exact; as, due process of law;\n   due service; in due time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'a.', 'Appointed or required to arrive at a given time; as, the\n   steamer was due yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'a.', 'Owing; ascribable, as to a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'adv.', 'Directly; exactly; as, a due east course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'n.', 'That which is owed; debt; that which one contracts to pay, or\n   do, to or for another; that which belongs or may be claimed as a right;\n   whatever custom, law, or morality requires to be done; a fee; a toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'n.', 'Right; just title or claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Due', 'v. t.', 'To endue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duebill', 'n.', 'A brief written acknowledgment of a debt, not made payable\n   to order, like a promissory note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dueful', 'a.', 'Fit; becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duel', 'n.', 'A combat between two persons, fought with deadly weapons, by\n   agreement. It usually arises from an injury done or an affront given by\n   one to the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duel', 'v. i. & t.', 'To fight in single combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dueler', 'n.', 'One who engages in a duel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dueling', 'n.', 'The act or practice of fighting in single combat. Also\n   adj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duelist', 'n.', 'One who fights in single combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duelo', 'n.', 'A duel; also, the rules of dueling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duea', 'n.', 'See Doa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dueness', 'n.', 'Quality of being due; debt; what is due or becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duennas', 'pl. ', 'of Duenna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duenna', 'n.', 'The chief lady in waiting on the queen of Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duenna', 'n.', 'An elderly lady holding a station between a governess and\n   companion, and appointed to have charge over the younger ladies in a\n   Spanish or a Portuguese family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duenna', 'n.', 'Any old woman who is employed to guard a younger one; a\n   governess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duet', 'n.', 'A composition for two performers, whether vocal or\n   instrumental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duettino', 'n.', 'A duet of short extent and concise form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duetto', 'n.', 'See Duet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duff', 'n.', 'Dough or paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duff', 'n.', 'A stiff flour pudding, boiled in a bag; -- a term used\n   especially by seamen; as, plum duff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duffel', 'n.', 'A kind of coarse woolen cloth, having a thick nap or\n   frieze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duffer', 'n.', 'A peddler or hawker, especially of cheap, flashy articles,\n   as sham jewelry; hence, a sham or cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duffer', 'n.', 'A stupid, awkward, inefficient person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duffle', 'n.', 'See Duffel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dufrenite', 'n.', 'A mineral of a blackish green color, commonly massive or\n   in nodules. It is a hydrous phosphate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dug', 'n.', 'A teat, pap, or nipple; -- formerly that of a human mother,\n   now that of a cow or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dug', 'imp. & p. p.', 'of Dig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dugong', 'n.', 'An aquatic herbivorous mammal (Halicore dugong), of the\n   order Sirenia, allied to the manatee, but with a bilobed tail. It\n   inhabits the Red Sea, Indian Ocean, East Indies, and Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dugout', 'n.', 'A canoe or boat dug out from a large log.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dugout', 'n.', 'A place dug out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dugout', 'n.', 'A house made partly in a hillside or slighter elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dugway', 'n.', 'A way or road dug through a hill, or sunk below the surface\n   of the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duke', 'n.', 'A leader; a chief; a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duke', 'n.', 'In England, one of the highest order of nobility after\n   princes and princesses of the royal blood and the four archbishops of\n   England and Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duke', 'n.', 'In some European countries, a sovereign prince, without the\n   title of king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duke', 'v. i.', 'To play the duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dukedom', 'n.', 'The territory of a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dukedom', 'n.', 'The title or dignity of a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dukeling', 'n.', 'A little or insignificant duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dukeship', 'n.', 'The quality or condition of being a duke; also, the\n   personality of a duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcamara', 'n.', 'A plant (Solanum Dulcamara). See Bittersweet, n., 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcamarin', 'n.', 'A glucoside extracted from the bittersweet (Solanum\n   Dulcamara), as a yellow amorphous substance. It probably occasions the\n   compound taste. See Bittersweet, 3(a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulce', 'v. t.', 'To make sweet; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulceness', 'n.', 'Sweetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcet', 'a.', 'Sweet to the taste; luscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcet', 'a.', 'Sweet to the ear; melodious; harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulciana', 'n.', 'A sweet-toned stop of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcification', 'n.', 'The act of dulcifying or sweetening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcified', 'a.', 'Sweetened; mollified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcifluous', 'a.', 'Flowing sweetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcified', 'imp. & p. p.', 'of Dulcify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcifying', 'p. pr. & vb. n.', 'of Dulcify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcify', 'v. t.', 'To sweeten; to free from acidity, saltness, or\n   acrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcify', 'v. t.', 'Fig. : To mollify; to sweeten; to please.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulciloquy', 'n.', 'A soft manner of speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcimer', 'n.', 'An instrument, having stretched metallic wires which are\n   beaten with two light hammers held in the hands of the performer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcimer', 'n.', 'An ancient musical instrument in use among the Jews. Dan.\n   iii. 5. It is supposed to be the same with the psaltery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcinea', 'n.', 'A mistress; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulciness', 'n.', 'See Dulceness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcite', 'n.', 'A white, sugarlike substance, C6H8.(OH)2, occurring\n   naturally in a manna from Madagascar, and in certain plants, and\n   produced artificially by the reduction of galactose and lactose or milk\n   sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcino', 'n.', 'See Dolcino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcitude', 'n.', 'Sweetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcorate', 'v. t.', 'To sweeten; to make less acrimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulcoration', 'n.', 'The act of sweetening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duledge', 'n.', 'One of the dowels joining the ends of the fellies which\n   form the circle of the wheel of a gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulia', 'n.', 'An inferior kind of veneration or worship, given to the\n   angels and saints as the servants of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Slow of understanding; wanting readiness of\n   apprehension; stupid; doltish; blockish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Slow in action; sluggish; unready; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Insensible; unfeeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Not keen in edge or point; lacking sharpness; blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Not bright or clear to the eye; wanting in liveliness of\n   color or luster; not vivid; obscure; dim; as, a dull fire or lamp; a\n   dull red or yellow; a dull mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Heavy; gross; cloggy; insensible; spiritless; lifeless;\n   inert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'superl.', 'Furnishing little delight, spirit, or variety;\n   uninteresting; tedious; cheerless; gloomy; melancholy; depressing; as,\n   a dull story or sermon; a dull occupation or period; hence, cloudy;\n   overcast; as, a dull day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duller', 'imp. & p. p.', 'of Dull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulling', 'p. pr. & vb. n.', 'of Dull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'v. t.', 'To deprive of sharpness of edge or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'v. t.', 'To make dull, stupid, or sluggish; to stupefy, as the\n   senses, the feelings, the perceptions, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'v. t.', 'To render dim or obscure; to sully; to tarnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'v. t.', 'To deprive of liveliness or activity; to render heavy; to\n   make inert; to depress; to weary; to sadden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull', 'v. i.', 'To become dull or stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullard', 'n.', 'A stupid person; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullard', 'a.', 'Stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull-brained', 'a.', 'Stupid; doltish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull-browed', 'a.', 'Having a gloomy look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duller', 'n.', 'One who, or that which, dulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull-eyed', 'a.', 'Having eyes wanting brightness, liveliness, or vivacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullhead', 'n.', 'A blockhead; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullish', 'a.', 'Somewhat dull; uninteresting; tiresome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullness', 'n.', 'The state of being dull; slowness; stupidity; heaviness;\n   drowsiness; bluntness; obtuseness; dimness; want of luster; want of\n   vividness, or of brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull-sighted', 'a.', 'Having poor eyesight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dullsome', 'a.', 'Dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dull-witted', 'a.', 'Stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dully', 'adv.', 'In a dull manner; stupidly; slowly; sluggishly; without\n   life or spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulocracy', 'n.', 'See Doulocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulse', 'n.', 'A seaweed of a reddish brown color, which is sometimes\n   eaten, as in Scotland. The true dulse is Sarcophyllis edulis; the\n   common is Rhodymenia. [Written also dillisk.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dulwilly', 'n.', 'The ring plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duly', 'adv.', 'In a due, fit, or becoming manner; as it (anything) ought\n   to be; properly; regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumal', 'a.', 'Pertaining to, or set with, briers or bushes; brambly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb', 'a.', 'Destitute of the power of speech; unable; to utter articulate\n   sounds; as, the dumb brutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb', 'a.', 'Not willing to speak; mute; silent; not speaking; not\n   accompanied by words; as, dumb show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb', 'a.', 'Lacking brightness or clearness, as a color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb', 'v. t.', 'To put to silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb-bell', 'n.', 'A weight, consisting of two spheres or spheroids,\n   connected by a short bar for a handle; used (often in pairs) for\n   gymnastic exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumbledor', 'n.', 'A bumblebee; also, a cockchafer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumbly', 'adv.', 'In silence; mutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumbness', 'n.', 'The quality or state of being dumb; muteness; silence;\n   inability to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumb-waiter', 'n.', 'A framework on which dishes, food, etc., are passed\n   from one room or story of a house to another; a lift for dishes, etc.;\n   also, a piece of furniture with movable or revolving shelves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumetose', 'a.', 'Dumose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumfounded', 'imp. & p. p.', 'of Dumfound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumfounding', 'p. pr. & vb. n.', 'of Dumfound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumfound', 'v. t.', 'To strike dumb; to confuse with astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumfounder', 'v. t.', 'To dumfound; to confound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummador', 'n.', 'A dumbledor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummerer', 'n.', 'One who feigns dumbness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'a.', 'Silent; mute; noiseless; as a dummy engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'a.', 'Fictitious or sham; feigned; as, a dummy watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummies', 'pl. ', 'of Dummy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'One who is dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'A sham package in a shop, or one which does not contain what\n   its exterior indicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'An imitation or copy of something, to be used as a\n   substitute; a model; a lay figure; as, a figure on which clothing is\n   exhibited in shop windows; a blank paper copy used to show the size of\n   the future book, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'One who plays a merely nominal part in any action; a sham\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'A thick-witted person; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'A locomotive with condensing engines, and, hence, without\n   the noise of escaping steam; also, a dummy car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'The fourth or exposed hand when three persons play at a\n   four-handed game of cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dummy', 'n.', 'A floating barge connected with a pier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumose', 'a.', 'Alt. of Dumous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumous', 'a.', 'Abounding with bushes and briers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumous', 'a.', 'Having a compact, bushy form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'n.', 'A thick, ill-shapen piece; a clumsy leaden counter used by\n   boys in playing chuck farthing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'A dull, gloomy state of the mind; sadness; melancholy; low\n   spirits; despondency; ill humor; -- now used only in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'Absence of mind; revery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'A melancholy strain or tune in music; any tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'An old kind of dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumped', 'imp. & p. p.', 'of Dump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumping', 'p. pr. & vb. n.', 'of Dump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'To knock heavily; to stump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'v. t.', 'To put or throw down with more or less of violence; hence,\n   to unload from a cart by tilting it; as, to dump sand, coal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'n.', 'A car or boat for dumping refuse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'n.', 'A ground or place for dumping ashes, refuse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'n.', 'That which is dumped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dump', 'n.', 'A pile of ore or rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpage', 'n.', 'The act of dumping loads from carts, especially loads of\n   refuse matter; also, a heap of dumped matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpage', 'n.', 'A fee paid for the privilege of dumping loads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpiness', 'n.', 'The state of being dumpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpish', 'a.', 'Dull; stupid; sad; moping; melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumple', 'v. t.', 'To make dumpy; to fold, or bend, as one part over\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpling', 'n.', 'A roundish mass of dough boiled in soup, or as a sort of\n   pudding; often, a cover of paste inclosing an apple or other fruit, and\n   boiled or baked; as, an apple dumpling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpy', 'superl.', 'Short and thick; of low stature and disproportionately\n   stout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dumpy', 'superl.', 'Sullen or discontented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'n.', 'A mound or small hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'v. t.', 'To cure, as codfish, in a particular manner, by laying\n   them, after salting, in a pile in a dark place, covered with salt grass\n   or some like substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunned', 'imp. & p. p.', 'of Dun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunning', 'p. pr. & vb. n.', 'of Dun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'v. t. & i.', 'To ask or beset, as a debtor, for payment; to urge\n   importunately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'n.', 'One who duns; a dunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'n.', 'An urgent request or demand of payment; as, he sent his debtor\n   a dun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dun', 'a.', 'Of a dark color; of a color partaking of a brown and black; of\n   a dull brown color; swarthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunbird', 'n.', 'The pochard; -- called also dunair, and dunker, or\n   dun-curre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunbird', 'n.', 'An American duck; the ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunce', 'n.', 'One backward in book learning; a child or other person dull\n   or weak in intellect; a dullard; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duncedom', 'n.', 'The realm or domain of dunces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duncery', 'n.', 'Dullness; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duncical', 'a.', 'Like a dunce; duncish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duncify', 'v. t.', 'To make stupid in intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duncish', 'a.', 'Somewhat like a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunder', 'n.', 'The lees or dregs of cane juice, used in the distillation\n   of rum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunderhead', 'n.', 'A dunce; a numskull; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunder-headed', 'a.', 'Thick-headed; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunderpate', 'n.', 'See Dunderhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dune', 'n.', 'A low hill of drifting sand usually formed on the coats, but\n   often carried far inland by the prevailing winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunfish', 'n.', 'Codfish cured in a particular manner, so as to be of a\n   superior quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dung', 'n.', 'The excrement of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunged', 'imp. & p. p.', 'of Dung');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunging', 'p. pr. & vb. n.', 'of Dung');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dung', 'v. t.', 'To manure with dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dung', 'v. t.', 'To immerse or steep, as calico, in a bath of hot water\n   containing cow dung; -- done to remove the superfluous mordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dung', 'v. i.', 'To void excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungaree', 'n.', 'A coarse kind of unbleached cotton stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungeon', 'n.', 'A close, dark prison, common/, under ground, as if the\n   lower apartments of the donjon or keep of a castle, these being used as\n   prisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungeon', 'v. t.', 'To shut up in a dungeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungfork', 'n.', 'A fork for tossing dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunghill', 'n.', 'A heap of dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunghill', 'n.', 'Any mean situation or condition; a vile abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungmeer', 'n.', 'A pit where dung and weeds rot for manure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungy', 'a.', 'Full of dung; filthy; vile; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dungyard', 'n.', 'A yard where dung is collected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunker', 'n.', 'One of a religious denomination whose tenets and practices\n   are mainly those of the Baptists, but partly those of the Quakers; --\n   called also Tunkers, Dunkards, Dippers, and, by themselves, Brethren,\n   and German Baptists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunlin', 'n.', 'A species of sandpiper (Tringa alpina); -- called also\n   churr, dorbie, grass bird, and red-backed sandpiper. It is found both\n   in Europe and America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunnage', 'n.', 'Fagots, boughs, or loose materials of any kind, laid on\n   the bottom of the hold for the cargo to rest upon to prevent injury by\n   water, or stowed among casks and other cargo to prevent their motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunner', 'n.', 'One employed in soliciting the payment of debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunnish', 'a.', 'Inclined to a dun color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunnock', 'a.', 'The hedge sparrow or hedge accentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunny', 'a.', 'Deaf; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunt', 'n.', 'A blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunted', 'a.', 'Beaten; hence, blunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dunter', 'n.', 'A porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duo', 'n.', 'A composition for two performers; a duet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecahedral', 'n.', 'Alt. of Duodecahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecahedron', 'n.', 'See Dodecahedral, and Dodecahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecennial', 'a.', 'Consisting of twelve years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimal', 'a.', 'Proceeding in computation by twelves; expressed in the\n   scale of twelves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimal', 'n.', 'A twelfth part; as, the duodecimals of an inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimal', 'n.', 'A system of numbers, whose denominations rise in a\n   scale of twelves, as of feet and inches. The system is used chiefly by\n   artificers in computing the superficial and solid contents of their\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimfid', 'a.', 'Divided into twelve parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimo', 'a.', 'Having twelve leaves to a sheet; as, a duodecimo from,\n   book, leaf, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimos', 'pl. ', 'of Duodecimo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecimo', 'n.', 'A book consisting of sheets each of which is folded into\n   twelve leaves; hence, indicating, more or less definitely, a size of a\n   book; -- usually written 12mo or 12¡.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodecuple', 'a.', 'Consisting of twelves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodenal', 'a.', 'Of or pertaining to the duodenum; as, duodenal digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodenary', 'a.', 'Containing twelve; twelvefold; increasing by twelves;\n   duodecimal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duodenum', 'n.', 'The part of the small intestines between the stomach and\n   the jejunum. See Illust. of Digestive apparatus, under Digestive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duoliteral', 'a.', 'Consisting of two letters only; biliteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duomo', 'n.', 'A cathedral. See Dome, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dup', 'v. t.', 'To open; as, to dup the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupable', 'a.', 'Capable of being duped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupe', 'n.', 'One who has been deceived or who is easily deceived; a gull;\n   as, the dupe of a schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duped', 'imp. & p. p.', 'of Dupe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duping', 'p. pr. & vb. n.', 'of Dupe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupe', 'n.', 'To deceive; to trick; to mislead by imposing on ones\n   credulity; to gull; as, dupe one by flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duper', 'n.', 'One who dupes another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupery', 'n.', 'The act or practice of duping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupion', 'n.', 'A double cocoon, made by two silkworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duple', 'a.', 'Double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplex', 'a.', 'Double; twofold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'a.', 'Double; twofold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'n.', 'That which exactly resembles or corresponds to something\n   else; another, correspondent to the first; hence, a copy; a transcript;\n   a counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'n.', 'An original instrument repeated; a document which is the\n   same as another in all essential particulars, and differing from a mere\n   copy in having all the validity of an original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicated', 'imp. & p. p.', 'of Duplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicating', 'p. pr. & vb. n.', 'of Duplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'v. t.', 'To double; to fold; to render double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'v. t.', 'To make a duplicate of (something); to make a copy or\n   transcript of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicate', 'v. t.', 'To divide into two by natural growth or spontaneous\n   action; as, infusoria duplicate themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplication', 'n.', 'The act of duplicating, or the state of being\n   duplicated; a doubling; a folding over; a fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplication', 'n.', 'The act or process of dividing by natural growth or\n   spontaneous action; as, the duplication of cartilage cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicative', 'a.', 'Having the quality of duplicating or doubling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicative', 'a.', 'Having the quality of subdividing into two by natural\n   growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicature', 'n.', 'A doubling; a fold, as of a membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicities', 'pl. ', 'of Duplicity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicity', 'n.', 'Doubleness; a twofold state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicity', 'n.', 'Doubleness of heart or speech; insincerity; a sustained\n   form of deception which consists in entertaining or pretending to\n   entertain one of feelings, and acting as if influenced by another; bad\n   faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicity', 'n.', 'The use of two or more distinct allegations or answers,\n   where one is sufficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duplicity', 'n.', 'In indictments, the union of two incompatible offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dupper', 'n.', 'See 2d Dubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dur', 'a.', 'Major; in the major mode; as, C dur, that is, C major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dura', 'n.', 'Short form for Dura mater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durability', 'n.', 'The state or quality of being durable; the power of\n   uninterrupted or long continuance in any condition; the power of\n   resisting agents or influences which tend to cause changes, decay, or\n   dissolution; lastingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durable', 'a.', 'Able to endure or continue in a particular condition;\n   lasting; not perishable or changeable; not wearing out or decaying\n   soon; enduring; as, durable cloth; durable happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durableness', 'n.', 'Power of lasting, enduring, or resisting; durability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durably', 'adv.', 'In a lasting manner; with long continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dural', 'a.', 'Pertaining to the dura, or dura mater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dura mater', '', 'The tough, fibrous membrane, which lines the cavity of\n   the skull and spinal column, and surrounds the brain and spinal cord;\n   -- frequently abbreviated to dura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duramen', 'n.', 'The heartwood of an exogenous tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durance', 'n.', 'Continuance; duration. See Endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durance', 'n.', 'Imprisonment; restraint of the person; custody by a\n   jailer; duress. Shak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durance', 'n.', 'A stout cloth stuff, formerly made in imitation of buff\n   leather and used for garments; a sort of tammy or everlasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durance', 'n.', 'In modern manufacture, a worsted of one color used for\n   window blinds and similar purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durancy', 'n.', 'Duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durant', 'n.', 'See Durance, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durante', 'prep.', 'During; as, durante vita, during life; durante bene\n   placito, during pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duration', 'n.', 'The state or quality of lasting; continuance in time; the\n   portion of time during which anything exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durative', 'a.', 'Continuing; not completed; implying duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durbar', 'n.', 'An audience hall; the court of a native prince; a state\n   levee; a formal reception of native princes, given by the governor\n   general of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dure', 'a.', 'Hard; harsh; severe; rough; toilsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dure', 'a.', 'To last; to continue; to endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dureful', 'a.', 'Lasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dureless', 'a.', 'Not lasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durene', 'n.', 'A colorless, crystalline, aromatic hydrocarbon, C6H2(CH3)4,\n   off artificial production, with an odor like camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duress', 'n.', 'Hardship; constraint; pressure; imprisonment; restraint of\n   liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duress', 'n.', 'The state of compulsion or necessity in which a person is\n   influenced, whether by the unlawful restrain of his liberty or by\n   actual or threatened physical violence, to incur a civil liability or\n   to commit an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duress', 'v. t.', 'To subject to duress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duressor', 'n.', 'One who subjects another to duress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durga', 'n.', 'Same as Doorga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durham', 'n.', 'One or a breed of short-horned cattle, originating in the\n   county of Durham, England. The Durham cattle are noted for their\n   beef-producing quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durian', 'n.', 'Alt. of Durion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durion', 'n.', 'The fruit of the durio. It is oval or globular, and eight\n   or ten inches long. It has a hard prickly rind, containing a soft,\n   cream-colored pulp, of a most delicious flavor and a very offensive\n   odor. The seeds are roasted and eaten like chestnuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'During', 'prep.', 'In the time of; as long as the action or existence of;\n   as, during life; during the space of a year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durio', 'n.', 'A fruit tree (D. zibethinus, the only species known) of the\n   Indian Archipelago. It bears the durian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durity', 'n.', 'Hardness; firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durity', 'n.', 'Harshness; cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durometer', 'n.', 'An instrument for measuring the degree of hardness;\n   especially, an instrument for testing the relative hardness of steel\n   rails and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durous', 'a.', 'Hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durra', 'n.', 'A kind of millet, cultivated throughout Asia, and introduced\n   into the south of Europe; a variety of Sorghum vulgare; -- called also\n   Indian millet, and Guinea corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durst', 'imp.', 'of Dare. See Dare, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durukuli', 'n.', 'A small, nocturnal, South American monkey (Nyctipthecus\n   trivirgatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Durylic', 'a.', 'Pertaining to, allied to, or derived from, durene; as,\n   durylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duse', 'n.', 'A demon or spirit. See Deuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusk', 'a.', 'Tending to darkness or blackness; moderately dark or black;\n   dusky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusk', 'n.', 'Imperfect obscurity; a middle degree between light and\n   darkness; twilight; as, the dusk of the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusk', 'n.', 'A darkish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusk', 'v. t.', 'To make dusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusk', 'v. i.', 'To grow dusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusken', 'v. t.', 'To make dusk or obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duskily', 'adv.', 'In a dusky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duskiness', 'n.', 'The state of being dusky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duskish', 'a.', 'Somewhat dusky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duskness', 'n.', 'Duskiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusky', 'a.', 'Partially dark or obscure; not luminous; dusk; as, a dusky\n   valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusky', 'a.', 'Tending to blackness in color; partially black;\n   dark-colored; not bright; as, a dusky brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusky', 'a.', 'Gloomy; sad; melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusky', 'a.', 'Intellectually clouded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'Fine, dry particles of earth or other matter, so comminuted\n   that they may be raised and wafted by the wind; that which is crumbled\n   too minute portions; fine powder; as, clouds of dust; bone dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'A single particle of earth or other matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'The earth, as the resting place of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'The earthy remains of bodies once alive; the remains of the\n   human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'Figuratively, a worthless thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'Figuratively, a low or mean condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'Gold dust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'n.', 'Coined money; cash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusted', 'imp. & p. p.', 'of Dust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusting', 'p. pr. & vb. n.', 'of Dust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'v. t.', 'To free from dust; to brush, wipe, or sweep away dust\n   from; as, to dust a table or a floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'v. t.', 'To sprinkle with dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust', 'v. t.', 'To reduce to a fine powder; to levigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustbrush', 'n.', 'A brush of feathers, bristles, or hair, for removing\n   dust from furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duster', 'n.', 'One who, or that which, dusts; a utensil that frees from\n   dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duster', 'n.', 'A revolving wire-cloth cylinder which removes the dust from\n   rags, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duster', 'n.', 'A blowing machine for separating the flour from the bran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duster', 'n.', 'A light over-garment, worn in traveling to protect the\n   clothing from dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustiness', 'n.', 'The state of being dusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustless', 'a.', 'Without dust; as a dustless path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustmen', 'pl. ', 'of Dustman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustman', 'p.', 'One whose employment is to remove dirt and defuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dustpan', 'n.', 'A shovel-like utensil for conveying away dust brushed from\n   the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dust-point', 'n.', 'An old rural game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusty', 'superl.', 'Filled, covered, or sprinkled with dust; clouded with\n   dust; as, a dusty table; also, reducing to dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dusty', 'superl.', 'Like dust; of the color of dust; as a dusty white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutch', 'a.', 'Pertaining to Holland, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutch', 'n.', 'The people of Holland; Dutchmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutch', 'n.', 'The language spoken in Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutchmen', 'pl. ', 'of Dutchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutchman', 'n.', 'A native, or one of the people, of Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duteous', 'a.', 'Fulfilling duty; dutiful; having the sentiments due to a\n   superior, or to one to whom respect or service is owed; obedient; as, a\n   duteous son or daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duteous', 'a.', 'Subservient; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutiable', 'a.', 'Subject to the payment of a duty; as dutiable goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutied', 'a.', 'Subjected to a duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutiful', 'a.', 'Performing, or ready to perform, the duties required by\n   one who has the right to claim submission, obedience, or deference;\n   submissive to natural or legal superiors; obedient, as to parents or\n   superiors; as, a dutiful son or daughter; a dutiful ward or servant; a\n   dutiful subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dutiful', 'a.', 'Controlled by, proceeding from, a sense of duty;\n   respectful; deferential; as, dutiful affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duties', 'pl. ', 'of Duty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'That which is due; payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'That which a person is bound by moral obligation to do, or\n   refrain from doing; that which one ought to do; service morally\n   obligatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'Hence, any assigned service or business; as, the duties of a\n   policeman, or a soldier; to be on duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'Specifically, obedience or submission due to parents and\n   superiors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'Respect; reverence; regard; act of respect; homage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'The efficiency of an engine, especially a steam pumping\n   engine, as measured by work done by a certain quantity of fuel;\n   usually, the number of pounds of water lifted one foot by one bushel of\n   coal (94 lbs. old standard), or by 1 cwt. (112 lbs., England, or 100\n   lbs., United States).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duty', 'n.', 'Tax, toll, impost, or customs; excise; any sum of money\n   required by government to be paid on the importation, exportation, or\n   consumption of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duumvirs', 'pl. ', 'of Duumvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duumviri', 'pl. ', 'of Duumvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duumvir', 'n.', 'One of two Roman officers or magistrates united in the\n   same public functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duumviral', 'a.', 'Of or belonging to the duumviri or the duumvirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duumvirate', 'n.', 'The union of two men in the same office; or the office,\n   dignity, or government of two men thus associated, as in ancient Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dux', 'n.', 'The scholastic name for the theme or subject of a fugue, the\n   answer being called the comes, or companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duykerbok', 'n.', 'A small South African antelope (Cephalous mergens); --\n   called also impoon, and deloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Duyoung', 'n.', 'See Dugong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'D valve', '', 'A kind of slide valve. See Slide valve, under Slide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dvergar', 'pl. ', 'of Dvergr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dvergr', 'n.', 'A dwarf supposed to dwell in rocks and hills and to be\n   skillful in working metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwale', 'a.', 'The deadly nightshade (Atropa Belladonna), having stupefying\n   qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwale', 'a.', 'The tincture sable or black when blazoned according to the\n   fantastic system in which plants are substituted for the tinctures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwale', 'a.', 'A sleeping potion; an opiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwang', 'n.', 'A piece of wood set between two studs, posts, etc., to\n   stiffen and support them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwang', 'n.', 'A kind of crowbar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwang', 'n.', 'A large wrench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfs', 'pl. ', 'of Dwarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarf', 'n.', 'An animal or plant which is much below the ordinary size of\n   its species or kind; especially, a diminutive human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfed', 'imp. & p. p.', 'of Dwarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfing', 'p. pr. & vb. n.', 'of Dwarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarf', 'v. t.', 'To hinder from growing to the natural size; to make or\n   keep small; to stunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarf', 'v. i.', 'To become small; to diminish in size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfish', 'a.', 'Like a dwarf; below the common stature or size; very\n   small; petty; as, a dwarfish animal, shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfling', 'n.', 'A diminutive dwarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwarfy', 'a.', 'Much undersized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwaul', 'v. i.', 'Alt. of Dwaule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwaule', 'v. i.', 'To be delirious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwelled', 'imp. & p. p.', 'of Dwell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwelt', '', 'of Dwell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwelling', 'p. pr. & vb. n.', 'of Dwell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwell', 'v. i.', 'To delay; to linger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwell', 'v. i.', 'To abide; to remain; to continue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwell', 'v. i.', 'To abide as a permanent resident, or for a time; to live\n   in a place; to reside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwell', 'v. t.', 'To inhabit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dweller', 'n.', 'An inhabitant; a resident; as, a cave dweller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwelling', 'n.', 'Habitation; place or house in which a person lives;\n   abode; domicile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwelt', 'imp. & p. p.', 'of Dwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindled', 'imp. & p. p.', 'of Dwindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindling', 'p. pr. & vb. n.', 'of Dwindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindle', 'v. i.', 'To diminish; to become less; to shrink; to waste or\n   consume away; to become degenerate; to fall away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindle', 'v. t.', 'To make less; to bring low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindle', 'v. t.', 'To break; to disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindle', 'n.', 'The process of dwindling; dwindlement; decline;\n   degeneracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwindlement', 'n.', 'The act or process of dwindling; a dwindling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dwine', 'v. i.', 'To waste away; to pine; to languish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyad', 'n.', 'Two units treated as one; a couple; a pair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyad', 'n.', 'An element, atom, or radical having a valence or combining\n   power of two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyad', 'a.', 'Having a valence or combining power of two; capable of being\n   substituted for, combined with, or replaced by, two atoms of hydrogen;\n   as, oxygen and calcium are dyad elements. See Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyadic', 'a.', 'Pertaining to the number two; of two parts or elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyaks', 'n. pl.', 'The aboriginal and most numerous inhabitants of Borneo.\n   They are partially civilized, but retain many barbarous practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyas', 'n.', 'A name applied in Germany to the Permian formation, there\n   consisting of two principal groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyed', 'imp. & p. p.', 'of Dye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyeing', 'p. pr. & vb. n.', 'of Dye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dye', 'v. t.', 'To stain; to color; to give a new and permanent color to,\n   as by the application of dyestuffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dye', 'n.', 'Color produced by dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dye', 'n.', 'Material used for dyeing; a dyestuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dye', 'n.', 'Same as Die, a lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyehouse', 'n.', 'A building in which dyeing is carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyeing', 'n.', 'The process or art of fixing coloring matters permanently\n   and uniformly in the fibers of wool, cotton, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyer', 'n.', 'One whose occupation is to dye cloth and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyestuff', 'n.', 'A material used for dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyewood', 'n.', 'Any wood from which coloring matter is extracted for\n   dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dying', 'a.', 'In the act of dying; destined to death; mortal; perishable;\n   as, dying bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dying', 'a.', 'Of or pertaining to dying or death; as, dying bed; dying\n   day; dying words; also, simulating a dying state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dying', 'n.', 'The act of expiring; passage from life to death; loss of\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyingly', 'adv.', 'In a dying manner; as if at the point of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyingness', 'n.', 'The state of dying or the stimulation of such a state;\n   extreme languor; languishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyke', 'n.', 'See Dike. The spelling dyke is restricted by some to the\n   geological meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynactinometer', 'n.', 'An instrument for measuring the intensity of the\n   photogenic (light-producing) rays, and computing the power of object\n   glasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynam', 'n.', 'A unit of measure for dynamical effect or work; a foot\n   pound. See Foot pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynameter', 'n.', 'A dynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynameter', 'n.', 'An instrument for determining the magnifying power of\n   telescopes, consisting usually of a doubleimage micrometer applied to\n   the eye end of a telescope for measuring accurately the diameter of the\n   image of the object glass there formed; which measurement, compared\n   with the actual diameter of the glass, gives the magnifying power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynametrical', 'a.', 'Pertaining to a dynameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamic', 'a.', 'Alt. of Dynamical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamical', 'a.', 'Of or pertaining to dynamics; belonging to energy or\n   power; characterized by energy or production of force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamical', 'a.', 'Relating to physical forces, effects, or laws; as,\n   dynamical geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamically', 'adv.', 'In accordance with the principles of dynamics or\n   moving forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamics', 'n.', 'That branch of mechanics which treats of the motion of\n   bodies (kinematics) and the action of forces in producing or changing\n   their motion (kinetics). Dynamics is held by some recent writers to\n   include statics and not kinematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamics', 'n.', 'The moving moral, as well as physical, forces of any\n   kind, or the laws which relate to them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamics', 'n.', 'That department of musical science which relates to, or\n   treats of, the power of tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamism', 'n.', 'The doctrine of Leibnitz, that all substance involves\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamist', 'n.', 'One who accounts for material phenomena by a theory of\n   dynamics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamitard', 'n.', 'A political dynamiter. [A form found in some\n   newspapers.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamite', 'n.', 'An explosive substance consisting of nitroglycerin\n   absorbed by some inert, porous solid, as infusorial earth, sawdust,\n   etc. It is safer than nitroglycerin, being less liable to explosion\n   from moderate shocks, or from spontaneous decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamiter', 'n.', 'One who uses dynamite; esp., one who uses it for the\n   destruction of life and property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamiting', 'n.', 'Destroying by dynamite, for political ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamitism', 'n.', 'The work of dynamiters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamization', '', 'The act of setting free the dynamic powers of a\n   medicine, as by shaking the bottle containing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamo', 'n.', 'A dynamo-electric machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamo-electric', 'a.', 'Pertaining to the development of electricity,\n   especially electrical currents, by power; producing electricity or\n   electrical currents by mechanical power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamograph', 'n.', 'A dynamometer to which is attached a device for\n   automatically registering muscular power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamometer', 'n.', 'An apparatus for measuring force or power; especially,\n   muscular effort of men or animals, or the power developed by a motor,\n   or that required to operate machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamometric', 'a.', 'Alt. of Dynamometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamometrical', 'a.', 'Relating to a dynamometer, or to the measurement of\n   force doing work; as, dynamometrical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynamometry', 'n.', 'The art or process of measuring forces doing work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynast', 'n.', 'A ruler; a governor; a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynast', 'n.', 'A dynasty; a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynasta', 'n.', 'A tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynastic', 'a.', 'Of or relating to a dynasty or line of kings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynastical', 'a.', 'Dynastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynastidan', 'n.', 'One of a group of gigantic, horned beetles, including\n   Dynastus Neptunus, and the Hercules beetle (D. Hercules) of tropical\n   America, which grow to be six inches in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynasties', 'pl. ', 'of Dynasty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynasty', 'n.', 'Sovereignty; lordship; dominion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dynasty', 'n.', 'A race or succession of kings, of the same line or family;\n   the continued lordship of a race of rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyne', 'n.', 'The unit of force, in the C. G. S. (Centimeter Gram Second)\n   system of physical units; that is, the force which, acting on a gram\n   for a second, generates a velocity of a centimeter per second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dys-', '', 'An inseparable prefix, fr. the Greek / hard, ill, and\n   signifying ill, bad, hard, difficult, and the like; cf. the prefixes,\n   Skr. dus-, Goth. tuz-, OHG. zur-, G. zer-, AS. to-, Icel. tor-, Ir.\n   do-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysaesthesia', 'n.', 'Impairment of any of the senses, esp. of touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyscrasia', 'n.', 'An ill habit or state of the constitution; -- formerly\n   regarded as dependent on a morbid condition of the blood and humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyscrasite', 'n.', 'A mineral consisting of antimony and silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discrasies', 'pl. ', 'of Dyscrasy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyscrasy', 'n.', 'Dycrasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysenteric', 'a.', 'Alt. of Dysenterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysenterical', 'a.', 'Of or pertaining to dysentery; having dysentery; as,\n   a dysenteric patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysentery', 'n.', 'A disease attended with inflammation and ulceration of\n   the colon and rectum, and characterized by griping pains, constant\n   desire to evacuate the bowels, and the discharge of mucus and blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysgenesic', 'a.', 'Not procreating or breeding freely; as, one race may be\n   dysgenesic with respect to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysgenesis', 'n.', 'A condition of not generating or breeding freely;\n   infertility; a form homogenesis in which the hybrids are sterile among\n   themselves, but are fertile with members of either parent race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyslogistic', 'a.', 'Unfavorable; not commendatory; -- opposed to\n   eulogistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysluite', 'n.', 'A variety of the zinc spinel or gahnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyslysin', 'n.', 'A resinous substance formed in the decomposition of\n   cholic acid of bile; -- so called because it is difficult to solve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysmenorrhea', 'n.', 'Difficult and painful menstruation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysnomy', 'n.', 'Bad legislation; the enactment of bad laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysodile', 'n.', 'An impure earthy or coaly bitumen, which emits a highly\n   fetid odor when burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspepsia', '', 'Alt. of Dyspepsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspepsy', '', 'A kind of indigestion; a state of the stomach in which its\n   functions are disturbed, without the presence of other diseases, or, if\n   others are present, they are of minor importance. Its symptoms are loss\n   of appetite, nausea, heartburn, acrid or fetid eructations, a sense of\n   weight or fullness in the stomach, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspeptic', 'a.', 'Alt. of Dyspeptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspeptical', 'a.', 'Pertaining to dyspepsia; having dyspepsia; as, a\n   dyspeptic or dyspeptical symptom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspeptic', 'n.', 'A person afflicted with dyspepsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspeptone', 'n.', 'An insoluble albuminous body formed from casein and\n   other proteid substances by the action of gastric juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysphagia', 'n.', 'Alt. of Dysphagy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysphagy', 'n.', 'Difficulty in swallowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysphonia', 'n.', 'Alt. of Dysphony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysphony', 'n.', 'A difficulty in producing vocal sounds; enfeebled or\n   depraved voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysphoria', 'n.', 'Impatience under affliction; morbid restlessness;\n   dissatisfaction; the fidgets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspnoea', 'n.', 'Difficulty of breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dyspnoic', 'a.', 'Affected with shortness of breath; relating to dyspnoea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysteleology', 'n.', 'The doctrine of purposelessness; a term applied by\n   Haeckel to that branch of physiology which treats of rudimentary\n   organs, in view of their being useless to the life of the organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dystocia', 'n.', 'Difficult delivery pr parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dystome', 'a.', 'Cleaving with difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysuria', 'n.', 'Alt. of Dysury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysury', 'n.', 'Difficult or painful discharge of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dysuric', 'a.', 'Pertaining to, or afflicted with, dysury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dzeren', 'n.', 'Alt. of Dzeron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dzeron', 'n.', 'The Chinese yellow antelope (Procapra gutturosa), a\n   remarkably swift-footed animal, inhabiting the deserts of Central Asia,\n   Thibet, and China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dziggetai', 'n.', 'The kiang, a wild horse or wild ass of Thibet (Asinus\n   hemionus).\n   E () The fifth letter of the English alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'E', '', 'E is the third tone of the model diatonic scale. E/ (E flat) is a\n   tone which is intermediate between D and E.');
  INSERT INTO words (word, word_type, description) VALUES  (    'E-', '', 'A Latin prefix meaning out, out of, from; also, without. See Ex-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Each', 'a. / a. pron.', 'Every one of the two or more individuals composing\n   a number of objects, considered separately from the rest. It is used\n   either with or without a following noun; as, each of you or each one of\n   you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Each', 'a. / a. pron.', 'Every; -- sometimes used interchangeably with\n   every.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eachwhere', 'adv.', 'Everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eadish', 'n.', 'See Eddish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eager', 'a.', 'Sharp; sour; acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eager', 'a.', 'Sharp; keen; bitter; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eager', 'a.', 'Excited by desire in the pursuit of any object; ardent to\n   pursue, perform, or obtain; keenly desirous; hotly longing; earnest;\n   zealous; impetuous; vehement; as, the hounds were eager in the chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eager', 'a.', 'Brittle; inflexible; not ductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eager', 'n.', 'Same as Eagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagerly', 'adv.', 'In an eager manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagerness', 'n.', 'The state or quality of being eager; ardent desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagerness', 'n.', 'Tartness; sourness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle', 'n.', 'Any large, rapacious bird of the Falcon family, esp. of the\n   genera Aquila and Haliaeetus. The eagle is remarkable for strength,\n   size, graceful figure, keenness of vision, and extraordinary flight.\n   The most noted species are the golden eagle (Aquila chrysaetus); the\n   imperial eagle of Europe (A. mogilnik / imperialis); the American bald\n   eagle (Haliaeetus leucocephalus); the European sea eagle (H.\n   albicilla); and the great harpy eagle (Thrasaetus harpyia). The figure\n   of the eagle, as the king of birds, is commonly used as an heraldic\n   emblem, and also for standards and emblematic devices. See Bald eagle,\n   Harpy, and Golden eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle', 'n.', 'A gold coin of the United States, of the value of ten\n   dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle', 'n.', 'A northern constellation, containing Altair, a star of the\n   first magnitude. See Aquila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle', 'n.', 'The figure of an eagle borne as an emblem on the standard of\n   the ancient Romans, or so used upon the seal or standard of any people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle-eyed', 'a.', 'Sharp-sighted as an eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle-sighted', 'a.', 'Farsighted and strong-sighted; sharp-sighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagless', 'n.', 'A female or hen eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaglestone', 'n.', 'A concretionary nodule of clay ironstone, of the size\n   of a walnut or larger, so called by the ancients, who believed that the\n   eagle transported these stones to her nest to facilitate the laying of\n   her eggs; aetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaglet', 'n.', 'A young eagle, or a diminutive eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagle-winged', 'a.', 'Having the wings of an eagle; swift, or soaring high,\n   like an eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaglewood', 'n.', 'A kind of fragrant wood. See Agallochum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagrass', 'n.', 'See Eddish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eagre', 'n.', 'A wave, or two or three successive waves, of great height\n   and violence, at flood tide moving up an estuary or river; -- commonly\n   called the bore. See Bore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ealderman', 'n.', 'Alt. of Ealdorman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ealdorman', 'n.', 'An alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eale', 'n.', 'Ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eame', 'n.', 'Uncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ean', 'v. t. & i.', 'To bring forth, as young; to yean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eanling', 'n.', 'A lamb just brought forth; a yeanling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'The organ of hearing; the external ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'The sense of hearing; the perception of sounds; the power of\n   discriminating between different tones; as, a nice ear for music; -- in\n   the singular only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'That which resembles in shape or position the ear of an\n   animal; any prominence or projection on an object, -- usually one for\n   support or attachment; a lug; a handle; as, the ears of a tub, a\n   skillet, or dish. The ears of a boat are outside kneepieces near the\n   bow. See Illust. of Bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'Same as Acroterium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'Same as Crossette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'Privilege of being kindly heard; favor; attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eared', 'imp. & p. p.', 'of Ear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'p. pr. & vb. n.', 'of Ear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'v. t.', 'To take in with the ears; to hear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'n.', 'The spike or head of any cereal (as, wheat, rye, barley,\n   Indian corn, etc.), containing the kernels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'v. i.', 'To put forth ears in growing; to form ears, as grain; as,\n   this corn ears well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear', 'v. t.', 'To plow or till; to cultivate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earable', 'a.', 'Arable; tillable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earache', 'n.', 'Ache or pain in the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earal', 'a.', 'Receiving by the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear-bored', 'a.', 'Having the ear perforated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earcap', 'n.', 'A cap or cover to protect the ear from cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earcockle', 'n.', 'A disease in wheat, in which the blackened and\n   contracted grain, or ear, is filled with minute worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eardrop', 'n.', 'A pendant for the ear; an earring; as, a pair of eardrops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eardrop', 'n.', 'A species of primrose. See Auricula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eardrum', 'n.', 'The tympanum. See Illust. of Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eared', 'a.', 'Having (such or so many) ears; -- used in composition; as,\n   long-eared-eared; sharp-eared; full-eared; ten-eared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eared', 'a.', 'Having external ears; having tufts of feathers resembling\n   ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eariness', 'n.', 'Fear or timidity, especially of something supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'n.', 'A line used to fasten the upper corners of a sail to the\n   yard or gaff; -- also called head earing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'n.', 'A line for hauling the reef cringle to the yard; -- also\n   called reef earing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'n.', 'A line fastening the corners of an awning to the rigging or\n   stanchions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'n.', 'Coming into ear, as corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earing', 'n.', 'A plowing of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earl', 'n.', 'A nobleman of England ranking below a marquis, and above a\n   viscount. The rank of an earl corresponds to that of a count (comte) in\n   France, and graf in Germany. Hence the wife of an earl is still called\n   countess. See Count.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earl', 'n.', 'The needlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earlap', 'n.', 'The lobe of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earldom', 'n.', 'The jurisdiction of an earl; the territorial possessions\n   of an earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earldom', 'n.', 'The status, title, or dignity of an earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earldorman', 'n.', 'Alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earlduck', 'n.', 'The red-breasted merganser (Merganser serrator).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earles penny', '', 'Earnest money. Same as Arles penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earless', 'a.', 'Without ears; hence, deaf or unwilling to hear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earlet', 'n.', 'An earring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earliness', 'n.', 'The state of being early or forward; promptness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earl marshal', '', 'An officer of state in England who marshals and orders\n   all great ceremonials, takes cognizance of matters relating to honor,\n   arms, and pedigree, and directs the proclamation of peace and war. The\n   court of chivalry was formerly under his jurisdiction, and he is still\n   the head of the heralds office or college of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earlock', 'n.', 'A lock or curl of hair near the ear; a lovelock. See\n   Lovelock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Early', 'adv.', 'Soon; in good season; seasonably; betimes; as, come early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Early', 'adv.', 'In advance of the usual or appointed time; in good season;\n   prior in time; among or near the first; -- opposed to late; as, the\n   early bird; an early spring; early fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Early', 'adv.', 'Coming in the first part of a period of time, or among the\n   first of successive acts, events, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earmark', 'n.', 'A mark on the ear of sheep, oxen, dogs, etc., as by\n   cropping or slitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earmark', 'n.', 'A mark for identification; a distinguishing mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earmarked', 'imp. & p. p.', 'of Earmark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earmarking', 'p. pr. & vb. n.', 'of Earmark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earmark', 'v. t.', 'To mark, as sheep, by cropping or slitting the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'n.', 'See Ern, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earned', 'imp. & p. p.', 'of Earn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earning', 'p. pr. & vb. n.', 'of Earn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'v. t.', 'To merit or deserve, as by labor or service; to do that\n   which entitles one to (a reward, whether the reward is received or\n   not).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'v. t.', 'To acquire by labor, service, or performance; to deserve\n   and receive as compensation or wages; as, to earn a good living; to\n   earn honors or laurels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'v. t. & i.', 'To grieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'v. i.', 'To long; to yearn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earn', 'v. i.', 'To curdle, as milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'n.', 'Seriousness; reality; fixed determination; eagerness;\n   intentness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'a.', 'Ardent in the pursuit of an object; eager to obtain or do;\n   zealous with sincerity; with hearty endeavor; heartfelt; fervent;\n   hearty; -- used in a good sense; as, earnest prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'a.', 'Intent; fixed closely; as, earnest attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'a.', 'Serious; important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'v. t.', 'To use in earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'n.', 'Something given, or a part paid beforehand, as a pledge;\n   pledge; handsel; a token of what is to come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnest', 'n.', 'Something of value given by the buyer to the seller, by\n   way of token or pledge, to bind the bargain and prove the sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnestful', 'a.', 'Serious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnestly', 'adv.', 'In an earnest manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnestness', 'n.', 'The state or quality of being earnest; intentness;\n   anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnful', 'a.', 'Full of anxiety or yearning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earnings', 'pl. ', 'of Earning');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earning', 'n.', 'That which is earned; wages gained by work or services;\n   money earned; -- used commonly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earpick', 'n.', 'An instrument for removing wax from the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear-piercer', 'n.', 'The earwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earreach', 'n.', 'Earshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earring', 'n.', 'An ornament consisting of a ring passed through the lobe\n   of the ear, with or without a pendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earsh', 'n.', 'See Arrish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear-shell', 'n.', 'A flattened marine univalve shell of the genus Haliotis;\n   -- called also sea-ear. See Abalone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earshot', 'n.', 'Reach of the ear; distance at which words may be heard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earshrift', 'n.', 'A nickname for auricular confession; shrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earsore', 'n.', 'An annoyance to the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ear-splitting', 'a.', 'Deafening; disagreeably loud or shrill; as,\n   ear-splitting strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earst', 'adv.', 'See Erst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'The globe or planet which we inhabit; the world, in\n   distinction from the sun, moon, or stars. Also, this world as the\n   dwelling place of mortals, in distinction from the dwelling place of\n   spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'The solid materials which make up the globe, in distinction\n   from the air or water; the dry land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'The softer inorganic matter composing part of the surface of\n   the globe, in distinction from the firm rock; soil of all kinds,\n   including gravel, clay, loam, and the like; sometimes, soil favorable\n   to the growth of plants; the visible surface of the globe; the ground;\n   as, loose earth; rich earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'A part of this globe; a region; a country; land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'Worldly things, as opposed to spiritual things; the\n   pursuits, interests, and allurements of this life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'The people on the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'Any earthy-looking metallic oxide, as alumina, glucina,\n   zirconia, yttria, and thoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'A similar oxide, having a slight alkaline reaction, as lime,\n   magnesia, strontia, baryta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'A hole in the ground, where an animal hides himself; as, the\n   earth of a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthed', 'imp. & p. p.', 'of Earth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthing', 'p. pr. & vb. n.', 'of Earth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'v. t.', 'To hide, or cause to hide, in the earth; to chase into a\n   burrow or den.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'v. t.', 'To cover with earth or mold; to inter; to bury; --\n   sometimes with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'v. i.', 'To burrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth', 'n.', 'A plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthbag', 'n.', 'A bag filled with earth, used commonly to raise or repair\n   a parapet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthbank', 'n.', 'A bank or mound of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthboard', 'n.', 'The part of a plow, or other implement, that turns over\n   the earth; the moldboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthborn', 'a.', 'Born of the earth; terrigenous; springing originally\n   from the earth; human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthborn', 'a.', 'Relating to, or occasioned by, earthly objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthbred', 'a.', 'Low; grovelling; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthdin', 'n.', 'An earthquake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthdrake', 'n.', 'A mythical monster of the early Anglo-Saxon literature;\n   a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthen', 'a.', 'Made of earth; made of burnt or baked clay, or other like\n   substances; as, an earthen vessel or pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthen-hearted', 'a.', 'Hard-hearted; sordid; gross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthenware', 'n.', 'Vessels and other utensils, ornaments, or the like,\n   made of baked clay. See Crockery, Pottery, Stoneware, and Porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth flax', '', 'A variety of asbestus. See Amianthus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthfork', 'n.', 'A pronged fork for turning up the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthiness', 'n.', 'The quality or state of being earthy, or of containing\n   earth; hence, grossness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthliness', 'n.', 'The quality or state of being earthly; worldliness;\n   grossness; perishableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthling', 'n.', 'An inhabitant of the earth; a mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthly', 'a.', 'Pertaining to the earth; belonging to this world, or to\n   mans existence on the earth; not heavenly or spiritual; carnal;\n   worldly; as, earthly joys; earthly flowers; earthly praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthly', 'a.', 'Of all things on earth; possible; conceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthly', 'a.', 'Made of earth; earthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthly', 'adv.', 'In the manner of the earth or its people; worldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthly-minded', 'a.', 'Having a mind devoted to earthly things;\n   worldly-minded; -- opposed to spiritual-minded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthmad', 'n.', 'The earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthnut', 'n.', 'A name given to various roots, tubers, or pods grown\n   under or on the ground');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthnut', 'n.', 'The esculent tubers of the umbelliferous plants Bunium\n   flexuosum and Carum Bulbocastanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthnut', 'n.', 'The peanut. See Peanut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthpea', 'n.', 'A species of pea (Amphicarpaea monoica). It is a climbing\n   leguminous plant, with hairy underground pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthquake', 'n.', 'A shaking, trembling, or concussion of the earth, due\n   to subterranean causes, often accompanied by a rumbling noise. The wave\n   of shock sometimes traverses half a hemisphere, destroying cities and\n   many thousand lives; -- called also earthdin, earthquave, and\n   earthshock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthquake', 'a.', 'Like, or characteristic of, an earthquake; loud;\n   starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthquave', 'n.', 'An earthquake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth shine', '', 'See Earth light, under Earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthshock', 'n.', 'An earthquake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthstar', 'n.', 'A curious fungus of the genus Geaster, in which the\n   outer coating splits into the shape of a star, and the inner one forms\n   a ball containing the dustlike spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earth-tongue', 'n.', 'A fungus of the genus Geoglossum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthward', 'adv.', 'Alt. of Earthwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthwards', 'adv.', 'Toward the earth; -- opposed to heavenward or\n   skyward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthwork', 'n.', 'Any construction, whether a temporary breastwork or\n   permanent fortification, for attack or defense, the material of which\n   is chiefly earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthwork', 'n.', 'The operation connected with excavations and embankments\n   of earth in preparing foundations of buildings, in constructing canals,\n   railroads, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthwork', 'n.', 'An embankment or construction made of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthworm', 'n.', 'Any worm of the genus Lumbricus and allied genera, found\n   in damp soil. One of the largest and most abundant species in Europe\n   and America is L. terrestris; many others are known; -- called also\n   angleworm and dewworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthworm', 'n.', 'A mean, sordid person; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthy', 'a.', 'Consisting of, or resembling, earth; terrene; earthlike;\n   as, earthy matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthy', 'a.', 'Of or pertaining to the earth or to, this world; earthly;\n   terrestrial; carnal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthy', 'a.', 'Gross; low; unrefined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earthy', 'a.', 'Without luster, or dull and roughish to the touch; as, an\n   earthy fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwax', 'n.', 'See Cerumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwig', 'n.', 'Any insect of the genus Forticula and related genera,\n   belonging to the order Euplexoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwig', 'n.', 'In America, any small chilopodous myriapod, esp. of the\n   genus Geophilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwig', 'n.', 'A whisperer of insinuations; a secret counselor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwigged', 'imp. & p. p.', 'of Earwig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwigging', 'p. pr. & vb. n.', 'of Earwig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwig', 'v. t.', 'To influence, or attempt to influence, by whispered\n   insinuations or private talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Earwitness', 'n.', 'A witness by means of his ears; one who is within\n   hearing and does hear; a hearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'Satisfaction; pleasure; hence, accommodation; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'Freedom from anything that pains or troubles; as: (a) Relief\n   from labor or effort; rest; quiet; relaxation; as, ease of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'Freedom from care, solicitude, or anything that annoys or\n   disquiets; tranquillity; peace; comfort; security; as, ease of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'Freedom from constraint, formality, difficulty,\n   embarrassment, etc.; facility; liberty; naturalness; -- said of manner,\n   style, etc.; as, ease of style, of behavior, of address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eased', 'imp. & p. p.', 'of Ease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easing', 'p. pr. & vb. n.', 'of Ease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'To free from anything that pains, disquiets, or oppresses; to\n   relieve from toil or care; to give rest, repose, or tranquility to; --\n   often with of; as, to ease of pain; ease the body or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'To render less painful or oppressive; to mitigate; to\n   alleviate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'To release from pressure or restraint; to move gently; to\n   lift slightly; to shift a little; as, to ease a bar or nut in\n   machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ease', 'n.', 'To entertain; to furnish with accommodations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easeful', 'a.', 'Full of ease; suitable for affording ease or rest; quiet;\n   comfortable; restful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easel', 'n.', 'A frame (commonly) of wood serving to hold a canvas upright,\n   or nearly upright, for the painters convenience or for exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easeless', 'a.', 'Without ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easement', 'n.', 'That which gives ease, relief, or assistance;\n   convenience; accommodation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easement', 'n.', 'A liberty, privilege, or advantage, which one proprietor\n   has in the estate of another proprietor, distinct from the ownership of\n   the soil, as a way, water course, etc. It is a species of what the\n   civil law calls servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easement', 'n.', 'A curved member instead of an abrupt change of direction,\n   as in a baseboard, hand rail, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easily', 'adv.', 'With ease; without difficulty or much effort; as, this\n   task may be easily performed; that event might have been easily\n   foreseen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easily', 'adv.', 'Without pain, anxiety, or disturbance; as, to pass life\n   well and easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easily', 'adv.', 'Readily; without reluctance; willingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easily', 'adv.', 'Smoothly; quietly; gently; gracefully; without /umult or\n   discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easily', 'adv.', 'Without shaking or jolting; commodiously; as, a carriage\n   moves easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easiness', 'n.', 'The state or condition of being easy; freedom from\n   distress; rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easiness', 'n.', 'Freedom from difficulty; ease; as the easiness of a task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easiness', 'n.', 'Freedom from emotion; compliance; disposition to yield\n   without opposition; unconcernedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easiness', 'n.', 'Freedom from effort, constraint, or formality; -- said of\n   style, manner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easiness', 'n.', 'Freedom from jolting, jerking, or straining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'n.', 'The point in the heavens where the sun is seen to rise at the\n   equinox, or the corresponding point on the earth; that one of the four\n   cardinal points of the compass which is in a direction at right angles\n   to that of north and south, and which is toward the right hand of one\n   who faces the north; the point directly opposite to the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'n.', 'The eastern parts of the earth; the regions or countries\n   which lie east of Europe; the orient. In this indefinite sense, the\n   word is applied to Asia Minor, Syria, Chaldea, Persia, India, China,\n   etc.; as, the riches of the East; the diamonds and pearls of the East;\n   the kings of the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'n.', 'Formerly, the part of the United States east of the Alleghany\n   Mountains, esp. the Eastern, or New England, States; now, commonly, the\n   whole region east of the Mississippi River, esp. that which is north of\n   Maryland and the Ohio River; -- usually with the definite article; as,\n   the commerce of the East is not independent of the agriculture of the\n   West.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'a.', 'Toward the rising sun; or toward the point where the sun\n   rises when in the equinoctial; as, the east gate; the east border; the\n   east side; the east wind is a wind that blows from the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'adv.', 'Eastward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East', 'v. i.', 'To move toward the east; to veer from the north or south\n   toward the east; to orientate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easter', 'n.', 'An annual church festival commemorating Christs\n   resurrection, and occurring on Sunday, the second day after Good\n   Friday. It corresponds to the pasha or passover of the Jews, and most\n   nations still give it this name under the various forms of pascha,\n   pasque, paque, or pask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easter', 'n.', 'The day on which the festival is observed; Easter day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easter', 'v. i.', 'To veer to the east; -- said of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterling', 'n.', 'A native of a country eastward of another; -- used, by\n   the English, of traders or others from the coasts of the Baltic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterling', 'n.', 'A piece of money coined in the east by Richard II. of\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterling', 'n.', 'The smew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterling', 'a.', 'Relating to the money of the Easterlings, or Baltic\n   traders. See Sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterly', 'a.', 'Coming from the east; as, it was easterly wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterly', 'a.', 'Situated, directed, or moving toward the east; as, the\n   easterly side of a lake; an easterly course or voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easterly', 'adv.', 'Toward, or in the direction of, the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eastern', 'a.', 'Situated or dwelling in the east; oriental; as, an eastern\n   gate; Eastern countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eastern', 'a.', 'Going toward the east, or in the direction of east; as, an\n   eastern voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easternmost', 'a.', 'Most eastern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East Indian', '', 'Belonging to, or relating to, the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East Indian', 'n.', 'A native of, or a dweller in, the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easting', 'n.', 'The distance measured toward the east between two\n   meridians drawn through the extremities of a course; distance of\n   departure eastward made by a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'East-insular', 'a.', 'Relating to the Eastern Islands; East Indian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eastward', 'adv.', 'Alt. of Eastwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eastwards', 'adv.', 'Toward the east; in the direction of east from some\n   point or place; as, New Haven lies eastward from New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'At ease; free from pain, trouble, or constraint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Free from pain, distress, toil, exertion, and the like;\n   quiet; as, the patient is easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Free from care, responsibility, discontent, and the like;\n   not anxious; tranquil; as, an easy mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Free from constraint, harshness, or formality;\n   unconstrained; smooth; as, easy manners; an easy style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Not causing, or attended with, pain or disquiet, or much\n   exertion; affording ease or rest; as, an easy carriage; a ship having\n   an easy motion; easy movements, as in dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Not difficult; requiring little labor or effort; slight;\n   inconsiderable; as, an easy task; an easy victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Causing ease; giving freedom from care or labor;\n   furnishing comfort; commodious; as, easy circumstances; an easy chair\n   or cushion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Not making resistance or showing unwillingness; tractable;\n   yielding; complying; ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Moderate; sparing; frugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy', 'v. t.', 'Not straitened as to money matters; as, the market is\n   easy; -- opposed to tight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy-chair', 'n.', 'An armchair for ease or repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Easy-going', 'a.', 'Moving easily; hence, mild-tempered; ease-loving;\n   inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ate', 'imp.', 'of Eat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', '', 'of Eat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaten', 'p. p.', 'of Eat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', '', 'of Eat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eating', 'p. pr. & vb. n.', 'of Eat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', 'v. t.', 'To chew and swallow as food; to devour; -- said especially\n   of food not liquid; as, to eat bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', 'v. t.', 'To corrode, as metal, by rust; to consume the flesh, as a\n   cancer; to waste or wear away; to destroy gradually; to cause to\n   disappear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', 'v. i.', 'To take food; to feed; especially, to take solid, in\n   distinction from liquid, food; to board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', 'v. i.', 'To taste or relish; as, it eats like tender beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eat', 'v. i.', 'To make ones way slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eatable', 'a.', 'Capable of being eaten; fit to be eaten; proper for food;\n   esculent; edible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eatable', 'n.', 'Something fit to be eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eatage', 'n.', 'Eatable growth of grass for horses and cattle, esp. that of\n   aftermath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eater', 'n.', 'One who, or that which, eats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eath', 'a. & adv.', 'Easy or easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eating', 'n.', 'The act of tasking food; the act of consuming or corroding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eating', 'n.', 'Something fit to be eaten; food; as, a peach is good\n   eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eau de Cologne', '', 'Same as Cologne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eau de vie', '', 'French name for brandy. Cf. Aqua vitae, under Aqua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eavedrop', 'n.', 'A drop from the eaves; eavesdrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaves', 'n. pl.', 'The edges or lower borders of the roof of a building,\n   which overhang the walls, and cast off the water that falls on the\n   roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaves', 'n. pl.', 'Brow; ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eaves', 'n. pl.', 'Eyelids or eyelashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eavesdrop', 'v. i.', 'To stand under the eaves, near a window or at the\n   door, of a house, to listen and learn what is said within doors; hence,\n   to listen secretly to what is said in private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eavesdrop', 'n.', 'The water which falls in drops from the eaves of a\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eavesdropper', 'n.', 'One who stands under the eaves, or near the window or\n   door of a house, to listen; hence, a secret listener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eavesdropping', 'n.', 'The habit of lurking about dwelling houses, and\n   other places where persons meet fro private intercourse, secretly\n   listening to what is said, and then tattling it abroad. The offense is\n   indictable at common law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'n.', 'The European bunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'n.', 'The reflux or flowing back of the tide; the return of the\n   tidal wave toward the sea; -- opposed to flood; as, the boats will go\n   out on the ebb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'n.', 'The state or time of passing away; a falling from a better to\n   a worse state; low state or condition; decline; decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebbed', 'imp. & p. p.', 'of Ebb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebbing', 'p. pr. & vb. n.', 'of Ebb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'v. i.', 'To flow back; to return, as the water of a tide toward the\n   ocean; -- opposed to flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'v. i.', 'To return or fall back from a better to a worse state; to\n   decline; to decay; to recede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'v. t.', 'To cause to flow back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb', 'a.', 'Receding; going out; falling; shallow; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebb tide', '', 'The reflux of tide water; the retiring tide; -- opposed to\n   flood tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebionite', 'n.', 'One of a sect of heretics, in the first centuries of the\n   church, whose doctrine was a mixture of Judaism and Christianity. They\n   denied the divinity of Christ, regarding him as an inspired messenger,\n   and rejected much of the New Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebionitism', 'n.', 'The system or doctrine of the Ebionites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eblanin', 'n.', 'See Pyroxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eblis', 'n.', 'The prince of the evil spirits; Satan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebon', 'a.', 'Consisting of ebony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebon', 'a.', 'Like ebony, especially in color; black; dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebon', 'n.', 'Ebony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonist', 'n.', 'One who works in ebony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonite', 'n.', 'A hard, black variety of vulcanite. It may be cut and\n   polished, and is used for many small articles, as combs and buttons,\n   and for insulating material in electric apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonized', 'imp. & p. p.', 'of Ebonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonizing', 'p. pr. & vb. n.', 'of Ebonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonize', 'v. t.', 'To make black, or stain black, in imitation of ebony;\n   as, to ebonize wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebonies', 'pl. ', 'of Ebony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebony', 'n.', 'A hard, heavy, and durable wood, which admits of a fine\n   polish or gloss. The usual color is black, but it also occurs red or\n   green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebony', 'a.', 'Made of ebony, or resembling ebony; black; as, an ebony\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebracteate', 'a.', 'Without bracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebracteolate', 'a.', 'Without bracteoles, or little bracts; -- said of a\n   pedicel or flower stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebrauke', 'a.', 'Hebrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebrieties', 'pl. ', 'of Ebriety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebriety', 'n.', 'Drunkenness; intoxication by spirituous liquors;\n   inebriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebrillade', 'n.', 'A bridle check; a jerk of one rein, given to a horse\n   when he refuses to turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebriosity', 'n.', 'Addiction to drink; habitual drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebrious', 'a.', 'Inclined to drink to excess; intoxicated; tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebulliate', 'v. i.', 'To boil or bubble up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullience', 'n.', 'Alt. of Ebulliency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebulliency', 'n.', 'A boiling up or over; effervescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullient', 'a.', 'Boiling up or over; hence, manifesting exhilaration or\n   excitement, as of feeling; effervescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullioscope', 'n.', 'An instrument for observing the boiling point of\n   liquids, especially for determining the alcoholic strength of a mixture\n   by the temperature at which it boils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullition', 'n.', 'A boiling or bubbling up of a liquid; the motion\n   produced in a liquid by its rapid conversion into vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullition', 'n.', 'Effervescence occasioned by fermentation or by any\n   other process which causes the liberation of a gas or an aeriform\n   fluid, as in the mixture of an acid with a carbonated alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ebullition', 'n.', 'A sudden burst or violent display; an outburst; as, an\n   ebullition of anger or ill temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eburin', 'n.', 'A composition of dust of ivory or of bone with a cement; --\n   used for imitations of valuable stones and in making moldings, seals,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eburnation', 'n.', 'A condition of bone cartilage occurring in certain\n   diseases of these tissues, in which they acquire an unnatural density,\n   and come to resemble ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eburnean', 'a.', 'Made of or relating to ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eburnification', 'n.', 'The conversion of certain substances into others\n   which have the appearance or characteristics of ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eburnine', 'a.', 'Of or pertaining to ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecardines', 'n. pl.', 'An order of Brachiopoda; the Lyopomata. See\n   Brachiopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecarte', 'n.', 'A game at cards, played usually by two persons, in which\n   the players may discard any or all of the cards dealt and receive\n   others from the pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecaudate', 'a.', 'Without a tail or spur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecaudate', 'a.', 'Tailless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecballium', 'n.', 'A genus of cucurbitaceous plants consisting of the\n   single species Ecballium agreste (or Elaterium), the squirting\n   cucumber. Its fruit, when ripe, bursts and violently ejects its seeds,\n   together with a mucilaginous juice, from which elaterium, a powerful\n   cathartic medicine, is prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecbasis', 'n.', 'A figure in which the orator treats of things according to\n   their events consequences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecbatic', 'a.', 'Denoting a mere result or consequence, as distinguished\n   from telic, which denotes intention or purpose; thus the phrase / /, if\n   rendered \"so that it was fulfilled,\" is ecbatic; if rendered \"in order\n   that it might be.\" etc., is telic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecbole', 'n.', 'A digression in which a person is introduced speaking his\n   own words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecbolic', 'n.', 'A drug, as ergot, which by exciting uterine contractions\n   promotes the expulsion of the contents of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecboline', 'n.', 'An alkaloid constituting the active principle of ergot;\n   -- so named from its power of producing abortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccaleobion', 'n.', 'A contrivance for hatching eggs by artificial heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecce homo', '', 'A picture which represents the Savior as given up to the\n   people by Pilate, and wearing a crown of thorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'a.', 'Deviating or departing from the center, or from the line\n   of a circle; as, an eccentric or elliptical orbit; pertaining to\n   deviation from the center or from true circular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'a.', 'Not having the same center; -- said of circles,\n   ellipses, spheres, etc., which, though coinciding, either in whole or\n   in part, as to area or volume, have not the same center; -- opposed to\n   concentric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'a.', 'Pertaining to an eccentric; as, the eccentric rod in a\n   steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'a.', 'Not coincident as to motive or end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'a.', 'Deviating from stated methods, usual practice, or\n   established forms or laws; deviating from an appointed sphere or way;\n   departing from the usual course; irregular; anomalous; odd; as,\n   eccentric conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'n.', 'A circle not having the same center as another contained\n   in some measure within the first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'n.', 'One who, or that which, deviates from regularity; an\n   anomalous or irregular person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'n.', 'In the Ptolemaic system, the supposed circular orbit of\n   a planet about the earth, but with the earth not in its center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'n.', 'A circle described about the center of an elliptical\n   orbit, with half the major axis for radius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentric', 'n.', 'A disk or wheel so arranged upon a shaft that the center\n   of the wheel and that of the shaft do not coincide. It is used for\n   operating valves in steam engines, and for other purposes. The motion\n   derived is precisely that of a crank having the same throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentrical', 'a.', 'See Eccentric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentrically', 'adv.', 'In an eccentric manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentricities', 'pl. ', 'of Eccentricity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentricity', 'n.', 'The state of being eccentric; deviation from the\n   customary line of conduct; oddity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentricity', 'n.', 'The ratio of the distance between the center and the\n   focus of an ellipse or hyperbola to its semi-transverse axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentricity', 'n.', 'The ratio of the distance of the center of the orbit\n   of a heavenly body from the center of the body round which it revolves\n   to the semi-transverse axis of the orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccentricity', 'n.', 'The distance of the center of figure of a body, as of\n   an eccentric, from an axis about which it turns; the throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecchymose', 'v. t.', 'To discolor by the production of an ecchymosis, or\n   effusion of blood, beneath the skin; -- chiefly used in the passive\n   form; as, the parts were much ecchymosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecchymoses', 'pl. ', 'of Ecchymosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecchymosis', 'n.', 'A livid or black and blue spot, produced by the\n   extravasation or effusion of blood into the areolar tissue from a\n   contusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecchymotic', 'a.', 'Pertaining to ecchymosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccle', 'n.', 'The European green woodpecker; -- also called ecall,\n   eaquall, yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiae', 'pl. ', 'of Ecclesia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesia', 'n.', 'The public legislative assembly of the Athenians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesia', 'n.', 'A church, either as a body or as a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesial', 'a.', 'Ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiarch', 'n.', 'An official of the Eastern Church, resembling a\n   sacrist in the Western Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiast', 'n.', 'An ecclesiastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiast', 'n.', 'The Apocryphal book of Ecclesiasticus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiastes', 'a.', 'One of the canonical books of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiastic', 'v. t.', 'Of or pertaining to the church. See\n   Ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiastic', 'n.', 'A person in holy orders, or consecrated to the\n   service of the church and the ministry of religion; a clergyman; a\n   priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiastical', 'a.', 'Of or pertaining to the church; relating to the\n   organization or government of the church; not secular; as,\n   ecclesiastical affairs or history; ecclesiastical courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiastically', 'adv.', 'In an ecclesiastical manner; according\n   ecclesiastical rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiasticism', 'n.', 'Strong attachment to ecclesiastical usages, forms,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiasticus', 'n.', 'A book of the Apocrypha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiological', 'a.', 'Belonging to ecclesiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiologist', 'n.', 'One versed in ecclesiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecclesiology', 'n.', 'The science or theory of church building and\n   decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccritic', 'n.', 'A remedy which promotes discharges, as an emetic, or a\n   cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecderon', 'n.', 'See Ecteron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecdyses', 'pl. ', 'of Ecdysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecdysis', 'n.', 'The act of shedding, or casting off, an outer cuticular\n   layer, as in the case of serpents, lobsters, etc.; a coming out; as,\n   the ecdysis of the pupa from its shell; exuviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecgonine', 'n.', 'A colorless, crystalline, nitrogenous base, obtained by\n   the decomposition of cocaine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echauguette', 'n.', 'A small chamber or place of protection for a sentinel,\n   usually in the form of a projecting turret, or the like. See Castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eche', 'a. / a. pron.', 'Each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echelon', 'n.', 'An arrangement of a body of troops when its divisions are\n   drawn up in parallel lines each to the right or the left of the one in\n   advance of it, like the steps of a ladder in position for climbing.\n   Also used adjectively; as, echelon distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echelon', 'n.', 'An arrangement of a fleet in a wedge or V formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echelon', 'v. t.', 'To place in echelon; to station divisions of troops in\n   echelon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echelon', 'v. i.', 'To take position in echelon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echidna', 'n.', 'A monster, half maid and half serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echidna', 'n.', 'A genus of Monotremata found in Australia, Tasmania, and\n   New Guinea. They are toothless and covered with spines; -- called also\n   porcupine ant-eater, and Australian ant-eater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echidnine', 'n.', 'The clear, viscid fluid secreted by the poison glands of\n   certain serpents; also, a nitrogenous base contained in this, and\n   supposed to be the active poisonous principle of the virus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinate', 'a.', 'Alt. of Echinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinated', 'a.', 'Set with prickles; prickly, like a hedgehog; bristled;\n   as, an echinated pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinid', 'a. & n.', 'Same as Echinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinidan', 'n.', 'One the Echinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinital', 'a.', 'Of, or like, an echinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinite', 'n.', 'A fossil echinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinococcus', 'n.', 'A parasite of man and of many domestic and wild\n   animals, forming compound cysts or tumors (called hydatid cysts) in\n   various organs, but especially in the liver and lungs, which often\n   cause death. It is the larval stage of the Taenia echinococcus, a small\n   tapeworm peculiar to the dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinoderm', 'n.', 'One of the Echinodermata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinodermal', 'a.', 'Relating or belonging to the echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinodermata', 'n. pl.', 'One of the grand divisions of the animal\n   kingdom. By many writers it was formerly included in the Radiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinodermatous', 'a.', 'Relating to Echinodermata; echinodermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinoid', 'a.', 'Of or pertaining to the Echinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinoid', 'n.', 'One of the Echinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinoidea', 'n. pl.', 'The class Echinodermata which includes the sea\n   urchins. They have a calcareous, usually more or less spheroidal or\n   disk-shaped, composed of many united plates, and covered with movable\n   spines. See Spatangoid, Clypeastroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinozoa', 'n. pl.', 'The Echinodermata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinulate', 'a.', 'Set with small spines or prickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echini', 'pl. ', 'of Echinus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinus', 'n.', 'A hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinus', 'n.', 'A genus of echinoderms, including the common edible sea\n   urchin of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinus', 'n.', 'The rounded molding forming the bell of the capital of the\n   Grecian Doric style, which is of a peculiar elastic curve. See\n   Entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinus', 'n.', 'The quarter-round molding (ovolo) of the Roman Doric\n   style. See Illust. of Column');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echinus', 'n.', 'A name sometimes given to the egg and anchor or egg and\n   dart molding, because that ornament is often identified with Roman\n   Doric capital. The name probably alludes to the shape of the shell of\n   the sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echiuroidea', 'n. pl.', 'A division of Annelida which includes the genus\n   Echiurus and allies. They are often classed among the Gephyrea, and\n   called the armed Gephyreans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoes', 'pl. ', 'of Echo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'n.', 'A sound reflected from an opposing surface and repeated to\n   the ear of a listener; repercussion of sound; repetition of a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'n.', 'Fig.: Sympathetic recognition; response; answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'n.', 'A wood or mountain nymph, regarded as repeating, and causing\n   the reverberation of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'n.', 'A nymph, the daughter of Air and Earth, who, for love of\n   Narcissus, pined away until nothing was left of her but her voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoed', 'imp. & p. p.', 'of Echo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoing', 'p. pr. & vb. n.', 'of Echo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoes', '3d pers. sing. pres.', 'of Echo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'v. t.', 'To send back (a sound); to repeat in sound; to\n   reverberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'v. t.', 'To repeat with assent; to respond; to adopt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echo', 'v. i.', 'To give an echo; to resound; to be sounded back; as, the\n   hall echoed with acclamations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoer', 'n.', 'One who, or that which, echoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoless', 'a.', 'Without echo or response.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echometer', 'n.', 'A graduated scale for measuring the duration of sounds,\n   and determining their different, and the relation of their intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echometry', 'n.', 'The art of measuring the duration of sounds or echoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echometry', 'n.', 'The art of constructing vaults to produce echoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echon', 'pron.', 'Alt. of Echoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoon', 'pron.', 'Each one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Echoscope', 'n.', 'An instrument for intensifying sounds produced by\n   percussion of the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclair', 'n.', 'A kind of frosted cake, containing flavored cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclaircise', 'v. t.', 'To make clear; to clear up what is obscure or not\n   understood; to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclaircissement', 'v. t.', 'The clearing up of anything which is obscure or\n   not easily understood; an explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclampsia', 'n.', 'A fancied perception of flashes of light, a symptom of\n   epilepsy; hence, epilepsy itself; convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclampsy', 'n.', 'Same as Eclampsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclat', 'n.', 'Brilliancy of success or effort; splendor; brilliant show;\n   striking effect; glory; renown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclat', 'n.', 'Demonstration of admiration and approbation; applause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclectic', 'a.', 'Selecting; choosing (what is true or excellent in\n   doctrines, opinions, etc.) from various sources or systems; as, an\n   eclectic philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclectic', 'a.', 'Consisting, or made up, of what is chosen or selected;\n   as, an eclectic method; an eclectic magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclectic', 'n.', 'One who follows an eclectic method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclectically', 'adv.', 'In an eclectic manner; by an eclectic method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclecticism', 'n.', 'Theory or practice of an eclectic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclegm', 'n.', 'A medicine made by mixing oils with sirups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipse', 'n.', 'An interception or obscuration of the light of the sun,\n   moon, or other luminous body, by the intervention of some other body,\n   either between it and the eye, or between the luminous body and that\n   illuminated by it. A lunar eclipse is caused by the moon passing\n   through the earths shadow; a solar eclipse, by the moon coming between\n   the sun and the observer. A satellite is eclipsed by entering the\n   shadow of its primary. The obscuration of a planet or star by the moon\n   or a planet, though of the nature of an eclipse, is called an\n   occultation. The eclipse of a small portion of the sun by Mercury or\n   Venus is called a transit of the planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipse', 'n.', 'The loss, usually temporary or partial, of light,\n   brilliancy, luster, honor, consciousness, etc.; obscuration; gloom;\n   darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipsed', 'imp. & p. p.', 'of Eclipse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipsing', 'p. pr. & vb. n.', 'of Eclipse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipse', 'v. t.', 'To cause the obscuration of; to darken or hide; -- said\n   of a heavenly body; as, the moon eclipses the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipse', 'v. t.', 'To obscure, darken, or extinguish the beauty, luster,\n   honor, etc., of; to sully; to cloud; to throw into the shade by\n   surpassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclipse', 'v. i.', 'To suffer an eclipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecliptic', 'a.', 'A great circle of the celestial sphere, making an angle\n   with the equinoctial of about 23¡ 28. It is the apparent path of the\n   sun, or the real path of the earth as seen from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecliptic', 'a.', 'A great circle drawn on a terrestrial globe, making an\n   angle of 23¡ 28 with the equator; -- used for illustrating and solving\n   astronomical problems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecliptic', 'a.', 'Pertaining to the ecliptic; as, the ecliptic way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecliptic', 'a.', 'Pertaining to an eclipse or to eclipses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclogite', 'n.', 'A rock consisting of granular red garnet, light green\n   smaragdite, and common hornblende; -- so called in reference to its\n   beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eclogue', 'n.', 'A pastoral poem, in which shepherds are introduced\n   conversing with each other; a bucolic; an idyl; as, the Ecloques of\n   Virgil, from which the modern usage of the word has been established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economic', 'a.', 'Alt. of Economical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Pertaining to the household; domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Relating to domestic economy, or to the management of\n   household affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Managing with frugality; guarding against waste or\n   unnecessary expense; careful and frugal in management and in\n   expenditure; -- said of character or habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Managed with frugality; not marked with waste or\n   extravagance; frugal; -- said of acts; saving; as, an economical use of\n   money or of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Relating to the means of living, or the resources and\n   wealth of a country; relating to political economy; as, economic\n   purposes; economical truths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economical', 'a.', 'Regulative; relating to the adaptation of means to an\n   end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economically', 'adv.', 'With economy; with careful management; with\n   prudence in expenditure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economics', 'n.', 'The science of household affairs, or of domestic\n   management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economics', 'n.', 'Political economy; the science of the utilities or the\n   useful application of wealth or material resources. See Political\n   economy, under Political.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economist', 'n.', 'One who economizes, or manages domestic or other\n   concerns with frugality; one who expends money, time, or labor,\n   judiciously, and without waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economist', 'n.', 'One who is conversant with political economy; a student\n   of economics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economization', 'n.', 'The act or practice of using to the best effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economized', 'imp. & p. p.', 'of Economize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economizing', 'p. pr. & vb. n.', 'of Economize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economize', 'v. t.', 'To manage with economy; to use with prudence; to\n   expend with frugality; as, to economize ones income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economize', 'v. i.', 'To be prudently sparing in expenditure; to be frugal\n   and saving; as, to economize in order to grow rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economizer', 'n.', 'One who, or that which, economizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economizer', 'n.', 'Specifically: (Steam Boilers) An arrangement of pipes\n   for heating feed water by waste heat in the gases passing to the\n   chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economies', 'pl. ', 'of Economy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economy', 'n.', 'The management of domestic affairs; the regulation and\n   government of household matters; especially as they concern expense or\n   disbursement; as, a careful economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economy', 'n.', 'Orderly arrangement and management of the internal affairs\n   of a state or of any establishment kept up by production and\n   consumption; esp., such management as directly concerns wealth; as,\n   political economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economy', 'n.', 'The system of rules and regulations by which anything is\n   managed; orderly system of regulating the distribution and uses of\n   parts, conceived as the result of wise and economical adaptation in the\n   author, whether human or divine; as, the animal or vegetable economy;\n   the economy of a poem; the Jewish economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Economy', 'n.', 'Thrifty and frugal housekeeping; management without loss\n   or waste; frugality in expenditure; prudence and disposition to save;\n   as, a housekeeper accustomed to economy but not to parsimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecorche', 'n.', 'A manikin, or image, representing an animal, especially\n   man, with the skin removed so that the muscles are exposed for purposes\n   of study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecossaise', 'n.', 'A dancing tune in the Scotch style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecostate', 'a.', 'Having no ribs or nerves; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecoute', 'n.', 'One of the small galleries run out in front of the glacis.\n   They serve to annoy the enemys miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphasis', 'n.', 'An explicit declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphonema', 'n.', 'A breaking out with some interjectional particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphoneme', 'n.', 'A mark (!) used to indicate an exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphonesis', 'n.', 'An animated or passionate exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphractic', 'a.', 'Serving to dissolve or attenuate viscid matter, and so\n   to remove obstructions; deobstruent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecphractic', 'n.', 'An ecphractic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecrasement', 'n.', 'The operation performed with an ecraseur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecraseur', 'n.', 'An instrument intended to replace the knife in many\n   operations, the parts operated on being severed by the crushing effect\n   produced by the gradual tightening of a steel chain, so that hemorrhage\n   rarely follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecru', 'a.', 'Having the color or appearance of unbleached stuff, as silk,\n   linen, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasies', 'pl. ', 'of Ecstasy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasy', 'n.', 'The state of being beside ones self or rapt out of ones\n   self; a state in which the mind is elevated above the reach of ordinary\n   impressions, as when under the influence of overpowering emotion; an\n   extraordinary elevation of the spirit, as when the soul, unconscious of\n   sensible objects, is supposed to contemplate heavenly mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasy', 'n.', 'Excessive and overmastering joy or enthusiasm; rapture;\n   enthusiastic delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasy', 'n.', 'Violent distraction of mind; violent emotion; excessive\n   grief of anxiety; insanity; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasy', 'n.', 'A state which consists in total suspension of sensibility,\n   of voluntary motion, and largely of mental power. The body is erect and\n   inflexible; the pulsation and breathing are not affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstasy', 'v. t.', 'To fill ecstasy, or with rapture or enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatic', 'n.', 'Pertaining to, or caused by, ecstasy or excessive\n   emotion; of the nature, or in a state, of ecstasy; as, ecstatic gaze;\n   ecstatic trance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatic', 'n.', 'Delightful beyond measure; rapturous; ravishing; as,\n   ecstatic bliss or joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatic', 'n.', 'An enthusiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatical', 'a.', 'Ecstatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatical', 'a.', 'Tending to external objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecstatically', 'adv.', 'Rapturously; ravishingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ect-', '', 'Alt. of Ecto-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecto-', '', 'A combining form signifying without, outside, external.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectad', 'adv.', 'Toward the outside or surface; -- opposed to entad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectal', 'a.', 'Pertaining to, or situated near, the surface; outer; --\n   opposed to ental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectasia', 'n.', 'A dilatation of a hollow organ or of a canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectasis', 'n.', 'The lengthening of a syllable from short to long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectental', 'a.', 'Relating to, or connected with, the two primitive germ\n   layers, the ectoderm and ectoderm; as, the \"ectental line\" or line of\n   juncture of the two layers in the segmentation of the ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecteron', 'n.', 'The external layer of the skin and mucous membranes;\n   epithelium; ecderon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectethmoid', 'a.', 'External to the ethmoid; prefrontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthlipsis', 'n.', 'The dropping out or suppression from a word of a\n   consonant, with or without a vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthlipsis', 'n.', 'The elision of a final m, with the preceding vowel,\n   before a word beginning with a vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthorea', 'pl. ', 'of Ecthoreum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthoreum', 'n.', 'The slender, hollow thread of a nettling cell or cnida.\n   See Nettling cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthymata', 'pl. ', 'of Ecthyma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecthyma', 'n.', 'A cutaneous eruption, consisting of large, round pustules,\n   upon an indurated and inflamed base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecto-', '', 'See Ect-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoblast', 'n.', 'The outer layer of the blastoderm; the epiblast; the\n   ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoblast', 'n.', 'The outer envelope of a cell; the cell wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectobronchia', 'pl. ', 'of Ectobronchium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectobronchium', 'n.', 'One of the dorsal branches of the main bronchi in\n   the lungs of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectocuneriform', 'n.', 'Alt. of Ectocuniform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectocuniform', 'n.', 'One of the bones of the tarsus. See Cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectocyst', 'n.', 'The outside covering of the Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoderm', 'n.', 'The outer layer of the blastoderm; epiblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoderm', 'n.', 'The external skin or outer layer of an animal or plant,\n   this being formed in an animal from the epiblast. See Illust. of\n   Blastoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectodermal', 'a.', 'Alt. of Ectodermic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectodermic', 'a.', 'Of or relating to the ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectolecithal', 'a.', 'Having the food yolk, at the commencement of\n   segmentation, in a peripheral position, and the cleavage process\n   confined to the center of the egg; as, ectolecithal ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectomere', 'n.', 'The more transparent cells, which finally become\n   external, in many segmenting ova, as those of mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoparasite', 'n.', 'Any parasite which lives on the exterior of animals;\n   -- opposed to endoparasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectopia', 'n.', 'A morbid displacement of parts, especially such as is\n   congenial; as, ectopia of the heart, or of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectopic', 'a.', 'Out of place; congenitally displaced; as, an ectopic\n   organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoplasm', 'n.', 'The outer transparent layer of protoplasm in a\n   developing ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoplasm', 'n.', 'The outer hyaline layer of protoplasm in a vegetable\n   cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoplasm', 'n.', 'The ectosarc of protozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoplastic', 'a.', 'Pertaining to, or composed of, ectoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectoprocta', 'n. pl.', 'An order of Bryozoa in which the anus lies outside\n   the circle of tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectopy', 'n.', 'Same as Ectopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectorganism', 'n.', 'An external parasitic organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectosarc', 'n.', 'The semisolid external layer of protoplasm in some\n   unicellular organisms, as the amoeba; ectoplasm; exoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectosteal', 'a.', 'Of or pertaining to ectostosis; as, ectosteal\n   ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectostosis', 'n.', 'A process of bone formation in which ossification takes\n   place in the perichondrium and either surrounds or gradually replaces\n   the cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectozoic', 'a.', 'See Epizoic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectozoa', 'pl. ', 'of Ectozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectozoon', 'n.', 'See Epizoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectropion', 'n.', 'An unnatural eversion of the eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectropium', 'n.', 'Same as Ectropion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectrotic', 'a.', 'Having a tendency to prevent the development of anything,\n   especially of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectypal', 'a.', 'Copied, reproduced as a molding or cast, in\n   contradistinction from the original model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectype', 'n.', 'A copy, as in pottery, of an artists original work. Hence:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectype', 'n.', 'A work sculptured in relief, as a cameo, or in bas-relief\n   (in this sense used loosely).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectype', 'n.', 'A copy from an original; a type of something that has\n   previously existed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ectypography', 'n.', 'A method of etching in which the design upon the\n   plate is produced in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecumenic', 'a.', 'Alt. of Ecumenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecumenical', 'a.', 'General; universal; in ecclesiastical usage, that which\n   concerns the whole church; as, an ecumenical council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ecurie', 'n.', 'A stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eczema', 'n.', 'An inflammatory disease of the skin, characterized by the\n   presence of redness and itching, an eruption of small vesicles, and the\n   discharge of a watery exudation, which often dries up, leaving the skin\n   covered with crusts; -- called also tetter, milk crust, and salt rheum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eczematous', 'a.', 'Pertaining to eczema; having the characteristic of\n   eczema.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ed', '', 'The termination of the past participle of regular, or weak,\n   verbs; also, of analogous participial adjectives from nouns; as,\n   pigmented; talented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edacious', 'a.', 'Given to eating; voracious; devouring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edacity', 'n.', 'Greediness; voracity; ravenousness; rapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddas', 'pl. ', 'of Edda');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edda', 'n.', 'The religious or mythological book of the old Scandinavian\n   tribes of German origin, containing two collections of Sagas (legends,\n   myths) of the old northern gods and heroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddaic', 'a.', 'Alt. of Eddic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddic', 'a.', 'Relating to the Eddas; resembling the Eddas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edder', 'n.', 'An adder or serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edder', 'n.', 'Flexible wood worked into the top of hedge stakes, to bind\n   them together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edder', 'v. t.', 'To bind the top interweaving edder; as, to edder a hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddish', 'n.', 'Aftermath; also, stubble and stubble field. See Arrish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddoes', 'n. pl.', 'The tubers of Colocasia antiquorum. See Taro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddies', 'pl. ', 'of Eddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddy', 'n.', 'A current of air or water running back, or in a direction\n   contrary to the main current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddy', 'n.', 'A current of water or air moving in a circular direction; a\n   whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddied', 'imp. & p. p.', 'of Eddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddying', 'p. pr. & vb. n.', 'of Eddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddy', 'v. i.', 'To move as an eddy, or as in an eddy; to move in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eddy', 'v. t.', 'To collect as into an eddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edelweiss', 'n.', 'A little, perennial, white, woolly plant (Leontopodium\n   alpinum), growing at high elevations in the Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edema', 'n.', 'Same as oedema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edematous', 'a.', 'Alt. of Edematose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edematose', 'a.', 'Same as oedematous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eden', 'n.', 'The garden where Adam and Eve first dwelt; hence, a\n   delightful region or residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edenic', 'a.', 'Of or pertaining to Eden; paradisaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edenite', 'n.', 'A variety of amphibole. See Amphibole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edenized', 'a.', 'Admitted to a state of paradisaic happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edental', 'a.', 'See Edentate, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edental', 'n.', 'One of the Edentata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentalous', 'a.', 'See Edentate, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentata', 'n. pl.', 'An order of mammals including the armadillos, sloths,\n   and anteaters; -- called also Bruta. The incisor teeth are rarely\n   developed, and in some groups all the teeth are lacking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentate', 'a.', 'Destitute of teeth; as, an edentate quadruped; an\n   edentate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentate', 'a.', 'Belonging to the Edentata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentate', 'n.', 'One of the Edentata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentated', 'a.', 'Same as Edentate, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentation', 'n.', 'A depriving of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edentulous', 'a.', 'Toothless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'The thin cutting side of the blade of an instrument; as,\n   the edge of an ax, knife, sword, or scythe. Hence, figuratively, that\n   which cuts as an edge does, or wounds deeply, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'Any sharp terminating border; a margin; a brink; extreme\n   verge; as, the edge of a table, a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'Sharpness; readiness of fitness to cut; keenness;\n   intenseness of desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'The border or part adjacent to the line of division; the\n   beginning or early part; as, in the edge of evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edged', 'imp. & p. p.', 'of Edge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edging', 'p. pr. & vb. n.', 'of Edge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'To furnish with an edge as a tool or weapon; to sharpen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'To shape or dress the edge of, as with a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'To furnish with a fringe or border; as, to edge a dress;\n   to edge a garden with box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'To make sharp or keen, figuratively; to incite; to\n   exasperate; to goad; to urge or egg on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. t.', 'To move by little and little or cautiously, as by pressing\n   forward edgewise; as, edging their chairs forwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. i.', 'To move sideways; to move gradually; as, edge along this\n   way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edge', 'v. i.', 'To sail close to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgebone', 'n.', 'Same as Aitchbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgeless', 'a.', 'Without an edge; not sharp; blunt; obtuse; as, an\n   edgeless sword or weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgelong', 'adv.', 'In the direction of the edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgeshot', 'a.', 'Having an edge planed, -- said of a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgeways', 'adv.', 'Alt. of Edgewise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgewise', 'adv.', 'With the edge towards anything; in the direction of the\n   edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edging', 'n.', 'That which forms an edge or border, as the fringe,\n   trimming, etc., of a garment, or a border in a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edging', 'n.', 'The operation of shaping or dressing the edge of anything,\n   as of a piece of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgingly', 'adv.', 'Gradually; gingerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgy', 'a.', 'Easily irritated; sharp; as, an edgy temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edgy', 'a.', 'Having some of the forms, such as drapery or the like, too\n   sharply defined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edh', 'n.', 'The name of the Anglo-Saxon letter /, capital form /. It is\n   sounded as \"English th in a similar word: //er, other, d//, doth.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edibility', 'n.', 'Suitableness for being eaten; edibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edible', 'a.', 'Fit to be eaten as food; eatable; esculent; as, edible\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edible', 'n.', 'Anything edible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edibleness', 'n.', 'Suitableness for being eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edict', 'n.', 'A public command or ordinance by the sovereign power; the\n   proclamation of a law made by an absolute authority, as if by the very\n   act of announcement; a decree; as, the edicts of the Roman emperors;\n   the edicts of the French monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edictal', 'a.', 'Relating to, or consisting of, edicts; as, the Roman\n   edictal law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edificant', 'a.', 'Building; constructing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edification', 'n.', 'The act of edifying, or the state of being edified; a\n   building up, especially in a moral or spiritual sense; moral,\n   intellectual, or spiritual improvement; instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edification', 'n.', 'A building or edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edificatory', 'a.', 'Tending to edification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edifice', 'n.', 'A building; a structure; an architectural fabric; --\n   chiefly applied to elegant houses, and other large buildings; as, a\n   palace, a church, a statehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edificial', 'a.', 'Pertaining to an edifice; structural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edifier', 'n.', 'One who builds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edifier', 'n.', 'One who edifies, builds up, or strengthens another by\n   moral or religious instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edified', 'imp. & p. p.', 'of Edify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edifying', 'p. pr. & vb. n.', 'of Edify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edify', 'v. i.', 'To build; to construct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edify', 'v. i.', 'To instruct and improve, especially in moral and\n   religious knowledge; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edify', 'v. i.', 'To teach or persuade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edify', 'v. i.', 'To improve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edifying', 'a.', 'Instructing; improving; as, an edifying conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edile', 'n.', 'See Aedile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edileship', 'n.', 'The office of aedile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edingtonite', 'n.', 'A grayish white zeolitic mineral, in tetragonal\n   crystals. It is a hydrous silicate of alumina and baryta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edited', 'imp. & p. p.', 'of Edit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editing', 'p. pr. & vb. n.', 'of Edit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edit', 'v. t.', 'To superintend the publication of; to revise and prepare\n   for publication; to select, correct, arrange, etc., the matter of, for\n   publication; as, to edit a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edition', 'n.', 'A literary work edited and published, as by a certain\n   editor or in a certain manner; as, a good edition of Chaucer; Chalmers\n   edition of Shakespeare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edition', 'n.', 'The whole number of copies of a work printed and published\n   at one time; as, the first edition was soon sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edition de luxe', '', 'See Luxe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editioner', 'n.', 'An editor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editor', 'n.', 'One who edits; esp., a person who prepares, superintends,\n   revises, and corrects a book, magazine, or newspaper, etc., for\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editorial', 'a.', 'Of or pertaining to an editor; written or sanctioned by\n   an editor; as, editorial labors; editorial remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editorial', 'n.', 'A leading article in a newspaper or magazine; an\n   editorial article; an article published as an expression of the views\n   of the editor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editorially', 'adv.', 'In the manner or character of an editor or of an\n   editorial article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editorship', 'n.', 'The office or charge of an editor; care and\n   superintendence of a publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Editress', 'n.', 'A female editor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edituate', 'v. t.', 'To guard as a churchwarden does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edomite', 'n.', 'One of the descendants of Esau or Edom, the brother of\n   Jacob; an Idumean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edriophthalma', 'n. pl.', 'A group of Crustacea in which the eyes are\n   without stalks; the Arthrostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edriophthalmous', 'a.', 'Pertaining to the Edriophthalma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educability', 'n.', 'Capability of being educated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educable', 'a.', 'Capable of being educated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educated', 'imp. & p. p.', 'of Educate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educating', 'p. pr. & vb. n.', 'of Educate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educate', 'v. t.', 'To bring /// or guide the powers of, as a child; to\n   develop and cultivate, whether physically, mentally, or morally, but\n   more commonly limited to the mental activities or senses; to expand,\n   strengthen, and discipline, as the mind, a faculty, etc.,; to form and\n   regulate the principles and character of; to prepare and fit for any\n   calling or business by systematic instruction; to cultivate; to train;\n   to instruct; as, to educate a child; to educate the eye or the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educated', 'a.', 'Formed or developed by education; as, an educated man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Education', 'n.', 'The act or process of educating; the result of\n   educating, as determined by the knowledge skill, or discipline of\n   character, acquired; also, the act or process of training by a\n   prescribed or customary course of study or discipline; as, an education\n   for the bar or the pulpit; he has finished his education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educational', 'a.', 'Of or pertaining to education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educationist', 'n.', 'One who is versed in the theories of, or who\n   advocates and promotes, education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educative', 'a.', 'Tending to educate; that gives education; as, an\n   educative process; an educative experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educator', 'n.', 'One who educates; a teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educed', 'imp. & p. p.', 'of Educe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educing', 'p. pr. & vb. n.', 'of Educe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educe', 'v. t.', 'To bring or draw out; to cause to appear; to produce\n   against counter agency or influence; to extract; to evolve; as, to\n   educe a form from matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educible', 'a.', 'Capable of being educed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Educt', 'n.', 'That which is educed, as by analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eduction', 'n.', 'The act of drawing out or bringing into view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eductive', 'a.', 'Tending to draw out; extractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eductor', 'n.', 'One who, or that which, brings forth, elicits, or\n   extracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorant', 'a.', 'Having a tendency to purify or to sweeten by removing\n   or correcting acidity and acrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorant', 'n.', 'An edulcorant remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorated', 'imp. & p. p.', 'of Edulcorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorating', 'p. pr. & vb. n.', 'of Edulcorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorate', 'v. t.', 'To render sweet; to sweeten; to free from acidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorate', 'v. t.', 'To free from acids, salts, or other soluble\n   substances, by washing; to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcoration', 'n.', 'The act of sweetening or edulcorating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcoration', 'n.', 'The act of freeing from acids or any soluble\n   substances, by affusions of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorative', 'a.', 'Tending to /weeten or purify by affusions of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulcorator', 'n.', 'A contrivance used to supply small quantities of\n   sweetened liquid, water, etc., to any mixture, or to test tubes, etc.;\n   a dropping bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Edulious', 'a.', 'Edible.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ee', '', 'A suffix used, chiefly in law terms, in a passive signification,\n   to indicate the direct or indirect object of an action, or the one to\n   whom an act is done or on whom a right is conferred; as in assignee,\n   donee, alienee, grantee, etc. It is correlative to -or, the agent or\n   doer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eek', 'v. t.', 'Alt. of Eeke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eeke', 'v. t.', 'See Eke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eel', 'n.', 'An elongated fish of many genera and species. The common eels\n   of Europe and America belong to the genus Anguilla. The electrical eel\n   is a species of Gymnotus. The so called vinegar eel is a minute\n   nematode worm. See Conger eel, Electric eel, and Gymnotus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelbuck', 'n.', 'An eelpot or eel basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelfare', 'n.', 'A brood of eels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelgrass', 'n.', 'A plant (Zostera marina), with very long and narrow\n   leaves, growing abundantly in shallow bays along the North Atlantic\n   coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eel-mother', 'n.', 'The eelpout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelpot', 'n.', 'A boxlike structure with funnel-shaped traps for catching\n   eels; an eelbuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelpout', 'n.', 'A European fish (Zoarces viviparus), remarkable for\n   producing living young; -- called also greenbone, guffer, bard, and\n   Maroona eel. Also, an American species (Z. anguillaris), -- called also\n   mutton fish, and, erroneously, congo eel, ling, and lamper eel. Both\n   are edible, but of little value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelpout', 'n.', 'A fresh-water fish, the burbot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eelspear', 'n.', 'A spear with barbed forks for spearing eels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Een', 'adv.', 'A contraction for even. See Even.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Een', 'n.', 'The old plural of Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eer', 'adv.', 'A contraction for ever. See Ever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eerie', 'a.', 'Alt. of Eery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eery', 'a.', 'Serving to inspire fear, esp. a dread of seeing ghosts; wild;\n   weird; as, eerie stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eery', 'a.', 'Affected with fear; affrighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eerily', 'adv.', 'In a strange, unearthly way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eerisome', 'a.', 'Causing fear; eerie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eet', 'obs. imp.', 'of Eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effable', 'a.', 'Capable of being uttered or explained; utterable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effaced', 'imp. & p. p.', 'of Efface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effacing', 'p. pr. & vb. n.', 'of Efface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efface', 'v. t.', 'To cause to disappear (as anything impresses or\n   inscribed upon a surface) by rubbing out, striking out, etc.; to erase;\n   to render illegible or indiscernible; as, to efface the letters on a\n   monument, or the inscription on a coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efface', 'v. t.', 'To destroy, as a mental impression; to wear away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effaceable', 'a.', 'Capable of being effaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effacement', 'n.', 'The act if effacing; also, the result of the act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effascinate', 'v. t.', 'To charm; to bewitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effascination', 'n.', 'A charming; state of being bewitched or deluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Execution; performance; realization; operation; as, the law\n   goes into effect in May.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Manifestation; expression; sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'In general: That which is produced by an agent or cause;\n   the event which follows immediately from an antecedent, called the\n   cause; result; consequence; outcome; fruit; as, the effect of luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Impression left on the mind; sensation produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Power to produce results; efficiency; force; importance;\n   account; as, to speak with effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Consequence intended; purpose; meaning; general intent; --\n   with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'The purport; the sum and substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Reality; actual meaning; fact, as distinguished from mere\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'n.', 'Goods; movables; personal estate; -- sometimes used to\n   embrace real as well as personal property; as, the people escaped from\n   the town with their effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effected', 'imp. & p. p.', 'of Effect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effecting', 'p. pr. & vb. n.', 'of Effect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'v. t.', 'To produce, as a cause or agent; to cause to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effect', 'v. t.', 'To bring to pass; to execute; to enforce; to achieve; to\n   accomplish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effecter', 'n.', 'One who effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectible', 'a.', 'Capable of being done or achieved; practicable;\n   feasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effection', 'n.', 'Creation; a doing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effective', 'a.', 'Having the power to produce an effect or effects;\n   producing a decided or decisive effect; efficient; serviceable;\n   operative; as, an effective force, remedy, speech; the effective men in\n   a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effective', 'n.', 'That which produces a given effect; a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effective', 'n.', 'One who is capable of active service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effective', 'n.', 'Specie or coin, as distinguished from paper currency; --\n   a term used in many parts of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectively', 'adv.', 'With effect; powerfully; completely; thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectiveness', 'n.', 'The quality of being effective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectless', 'a.', 'Without effect or advantage; useless; bootless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effector', 'n.', 'An effecter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectual', 'n.', 'Producing, or having adequate power or force to produce,\n   an intended effect; adequate; efficient; operative; decisive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectually', 'adv.', 'With effect; efficaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectually', 'adv.', 'Actually; in effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectualness', 'n.', 'The quality of being effectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuated', 'imp. & p. p.', 'of Effectuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuating', 'p. pr. & vb. n.', 'of Effectuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuate', 'v. t.', 'To bring to pass; to effect; to achieve; to\n   accomplish; to fulfill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuation', 'n.', 'Act of effectuating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuose', 'a.', 'Alt. of Effectuous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuous', 'a.', 'Effective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effectuously', 'adv.', 'Effectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminacies', 'pl. ', 'of Effeminacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminacy', 'n.', 'Characteristic quality of a woman, such as softness,\n   luxuriousness, delicacy, or weakness, which is unbecoming a man;\n   womanish delicacy or softness; -- used reproachfully of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminate', 'a.', 'Having some characteristic of a woman, as delicacy,\n   luxuriousness, etc.; soft or delicate to an unmanly degree; womanish;\n   weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminate', 'a.', 'Womanlike; womanly; tender; -- in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminated', 'imp. & p. p.', 'of Effeminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminating', 'p. pr. & vb. n.', 'of Effeminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminate', 'v. t.', 'To make womanish; to make soft and delicate; to\n   weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminate', 'v. i.', 'To grow womanish or weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminately', 'adv.', 'In an effeminate or womanish manner; weakly;\n   softly; delicately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminately', 'adv.', 'By means of a woman; by the power or art of a\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminateness', 'n.', 'The state of being effeminate; unmanly softness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effemination', 'n.', 'Effeminacy; womanishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effeminize', 'v. t.', 'To make effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effendi', 'n.', 'Master; sir; -- a title of a Turkish state official and\n   man of learning, especially one learned in the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efferent', 'a.', 'Conveying outward, or discharging; -- applied to certain\n   blood vessels, lymphatics, nerves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efferent', 'a.', 'Conveyed outward; as, efferent impulses, i. e., such as\n   are conveyed by the motor or efferent nerves from the central nervous\n   organ outwards; -- opposed to afferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efferent', 'n.', 'An efferent duct or stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efferous', 'a.', 'Like a wild beast; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervesced', 'imp. & p. p.', 'of Effervesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescing', 'p. pr. & vb. n.', 'of Effervesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervesce', 'v. i.', 'To be in a state of natural ebullition; to bubble\n   and hiss, as fermenting liquors, or any fluid, when some part escapes\n   in a gaseous form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervesce', 'v. i.', 'To exhibit, in lively natural expression, feelings\n   that can not be repressed or concealed; as, to effervesce with joy or\n   merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescence', 'n.', 'Alt. of Effervescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescency', 'n.', 'A kind of natural ebullition; that commotion of a\n   fluid which takes place when some part of the mass flies off in a\n   gaseous form, producing innumerable small bubbles; as, the\n   effervescence of a carbonate with citric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescent', 'a.', 'Gently boiling or bubbling, by means of the\n   disengagement of gas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescible', 'a.', 'Capable of effervescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effervescive', 'a.', 'Tending to produce effervescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effet', 'n.', 'The common newt; -- called also asker, eft, evat, and ewt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effete', 'a.', 'No longer capable of producing young, as an animal, or\n   fruit, as the earth; hence, worn out with age; exhausted of energy;\n   incapable of efficient action; no longer productive; barren; sterile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficacious', 'n.', 'Possessing the quality of being effective; productive\n   of, or powerful to produce, the effect intended; as, an efficacious\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficacity', 'n.', 'Efficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficacy', 'n.', 'Power to produce effects; operation or energy of an agent\n   or force; production of the effect intended; as, the efficacy of\n   medicine in counteracting disease; the efficacy of prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficience', 'n.', 'Alt. of Efficiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficiency', 'n.', 'The quality of being efficient or producing an effect\n   or effects; efficient power; effectual agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficiency', 'n.', 'The ratio of useful work to energy expended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficient', 'n.', 'Causing effects; producing results; that makes the\n   effect to be what it is; actively operative; not inactive, slack, or\n   incapable; characterized by energetic and useful activity; as, an\n   efficient officer, power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficient', 'n.', 'An efficient cause; a prime mover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efficiently', 'adv.', 'With effect; effectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effierce', 'v. t.', 'To make fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigial', 'a.', 'Relating to an effigy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigiate', 'v. t.', 'To form as an effigy; hence, to fashion; to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigiation', 'n.', 'The act of forming in resemblance; an effigy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigies', 'n.', 'See Effigy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigies', 'pl. ', 'of Effigy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effigy', 'n.', 'The image, likeness, or representation of a person, whether\n   a full figure, or a part; an imitative figure; -- commonly applied to\n   sculptured likenesses, as those on monuments, or to those of the heads\n   of princes on coins and medals, sometimes applied to portraits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflagitate', 'v. t.', 'To ask urgently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflate', 'v. t.', 'To fill with breath; to puff up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflation', 'n.', 'The act of filling with wind; a breathing or puffing\n   out; a puff, as of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effloresced', 'imp. & p. p.', 'of Effloresce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescing', 'p. pr. & vb. n.', 'of Effloresce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effloresce', 'v. i.', 'To blossom forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effloresce', 'v. i.', 'To change on the surface, or throughout, to a\n   whitish, mealy, or crystalline powder, from a gradual decomposition,\n   esp. from the loss of water, on simple exposure to the air; as,\n   Glaubers salts, and many others, effloresce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effloresce', 'v. i.', 'To become covered with a whitish crust or light\n   crystallization, from a slow chemical change between some of the\n   ingredients of the matter covered and an acid proceeding commonly from\n   an external source; as, the walls of limestone caverns sometimes\n   effloresce with nitrate of calcium in consequence of the action in\n   consequence of nitric acid formed in the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescence', 'n.', 'Flowering, or state of flowering; the blooming of\n   flowers; blowth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescence', 'n.', 'A redness of the skin; eruption, as in rash,\n   measles, smallpox, scarlatina, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescence', 'n.', 'The formation of the whitish powder or crust on the\n   surface of efflorescing bodies, as salts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescence', 'n.', 'The powder or crust thus formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescency', 'n.', 'The state or quality of being efflorescent;\n   efflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescent', 'v. i.', 'That effloresces, or is liable to effloresce on\n   exposure; as, an efflorescent salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflorescent', 'v. i.', 'Covered with an efflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflower', 'v. t.', 'To remove the epidermis of (a skin) with a concave\n   knife, blunt in its middle part, -- as in making chamois leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluence', 'n.', 'A flowing out, or emanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluence', 'n.', 'That which flows or issues from any body or substance;\n   issue; efflux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluency', 'n.', 'Effluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluent', 'a.', 'Flowing out; as, effluent beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluent', 'n.', 'A stream that flows out of another stream or lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluviable', 'a.', 'Capable of being given off as an effluvium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluvial', 'a.', 'Belonging to effluvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluviate', 'v. i.', 'To give forth effluvium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluvia', 'pl. ', 'of Effluvium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluvium', 'a.', 'Subtile or invisible emanation; exhalation perceived by\n   the sense of smell; especially, noisome or noxious exhalation; as, the\n   effluvium from diseased or putrefying bodies, or from ill drainage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflux', 'n.', 'The act or process of flowing out, or issuing forth;\n   effusion; outflow; as, the efflux of matter from an ulcer; the efflux\n   of mens piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflux', 'n.', 'That which flows out; emanation; effluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efflux', 'v. i.', 'To run out; to flow forth; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluxion', 'n.', 'The act of flowing out; effusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effluxion', 'n.', 'That which flows out; effluvium; emanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effodient', 'a.', 'Digging up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efforced', 'imp. & p. p.', 'of Efforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efforcing', 'p. pr. & vb. n.', 'of Efforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efforce', 'v. t.', 'To force; to constrain; to compel to yield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efform', 'v. t.', 'To form; to shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efformation', 'n.', 'The act of giving shape or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effort', 'n.', 'An exertion of strength or power, whether physical or\n   mental, in performing an act or aiming at an object; more or less\n   strenuous endeavor; struggle directed to the accomplishment of an\n   object; as, an effort to scale a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effort', 'n.', 'A force acting on a body in the direction of its motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effort', 'v. t.', 'To stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effortless', 'a.', 'Making no effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effossion', 'n.', 'A digging out or up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effranchise', 'v. t.', 'To enfranchise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effray', 'v. t.', 'To frighten; to scare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effrayable', 'a.', 'Frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effrenation', 'n.', 'Unbridled license; unruliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effront', 'v. t.', 'To give assurance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effronteries', 'pl. ', 'of Effrontery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effrontery', 'n.', 'Impudence or boldness in confronting or in\n   transgressing the bounds of duty or decorum; insulting\n   presumptuousness; shameless boldness; barefaced assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effrontit', 'a.', 'Marked by impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effrontuously', 'adv.', 'Impudently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulged', 'imp. & p. p.', 'of Effulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulging', 'p. pr. & vb. n.', 'of Effulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulge', 'v. t.', 'To cause to shine with abundance of light; to radiate;\n   to beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulge', 'v. i.', 'To shine forth; to beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulgence', 'n.', 'The state of being effulgent; extreme brilliancy; a\n   flood of light; great luster or brightness; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulgent', 'a.', 'Diffusing a flood of light; shining; luminous; beaming;\n   bright; splendid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effulgently', 'adv.', 'In an effulgent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effumability', 'n.', 'The capability of flying off in fumes or vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effume', 'v. t.', 'To breathe or puff out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effund', 'v. t.', 'To pour out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'a.', 'Poured out freely; profuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'a.', 'Disposed to pour out freely; prodigal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'a.', 'Spreading loosely, especially on one side; as, an effuse\n   inflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'a.', 'Having the lips, or edges, of the aperture abruptly\n   spreading; -- said of certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'n.', 'Effusion; loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effused', 'imp. & p. p.', 'of Effuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusing', 'p. pr. & vb. n.', 'of Effuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'v. t.', 'To pour out like a stream or freely; to cause to exude;\n   to shed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effuse', 'v. i.', 'To emanate; to issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusion', 'n.', 'The act of pouring out; as, effusion of water, of blood,\n   of grace, of words, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusion', 'n.', 'That which is poured out, literally or figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusion', 'n.', 'The escape of a fluid out of its natural vessel, either\n   by rupture of the vessel, or by exudation through its walls. It may\n   pass into the substance of an organ, or issue upon a free surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusion', 'n.', 'The liquid escaping or exuded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Effusive', 'a.', 'Pouring out; pouring forth freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Efreet', 'n.', 'See Afrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eft', 'n.', 'A European lizard of the genus Seps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eft', 'n.', 'A salamander, esp. the European smooth newt (Triton\n   punctatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eft', 'adv.', 'Again; afterwards; soon; quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eftsoon', 'adv.', 'Alt. of Eftsoons');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eftsoons', 'adv.', 'Again; anew; a second time; at once; speedily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egad', 'interj.', 'An exclamation expressing exultation or surprise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egal', 'a.', 'Equal; impartial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egality', 'n.', 'Equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egean', 'a.', 'See Aegean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egence', 'n.', 'The state of needing, or of suffering a natural want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eger', 'a.', 'Alt. of Egre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egre', 'a.', 'Sharp; bitter; acid; sour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eger', 'n.', 'An impetuous flood; a bore. See Eagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egerminate', 'v. i.', 'To germinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egest', 'v. t.', 'To cast or throw out; to void, as excrement; to excrete,\n   as the indigestible matter of the food; in an extended sense, to\n   excrete by the lungs, skin, or kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egesta', 'n. pl.', 'That which is egested or thrown off from the body by\n   the various excretory channels; excrements; -- opposed to ingesta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egestion', 'n.', 'Act or process of egesting; a voiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg', 'n.', 'The oval or roundish body laid by domestic poultry and other\n   birds, tortoises, etc. It consists of a yolk, usually surrounded by the\n   \"white\" or albumen, and inclosed in a shell or strong membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg', 'n.', 'A simple cell, from the development of which the young of\n   animals are formed; ovum; germ cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg', 'n.', 'Anything resembling an egg in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egged', 'imp. & p. p.', 'of Egg');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egging', 'p. pr. & vb. n.', 'of Egg');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg', 'v. t.', 'To urge on; to instigate; to incite/');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggar', 'n.', 'Any bombycid moth of the genera Eriogaster and Lasiocampa;\n   as, the oak eggar (L. roboris) of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg-bird', 'n.', 'A species of tern, esp. the sooty tern (Sterna\n   fuliginosa) of the West Indies. In the Bahama Islands the name is\n   applied to the tropic bird, Phaethon flavirostris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg-cup', 'n.', 'A cup used for holding an egg, at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggement', 'n.', 'Instigation; incitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egger', 'n.', 'One who gathers eggs; an eggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egger', 'v. t.', 'One who eggs or incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggery', 'n.', 'A place where eggs are deposited (as by sea birds) or kept;\n   a nest of eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg-glass', 'n.', 'A small sandglass, running about three minutes, for\n   marking time in boiling eggs; also, a small glass for holding an egg,\n   at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egghot', 'n.', 'A kind of posset made of eggs, brandy, sugar, and ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggler', 'n.', 'One who gathers, or deals in, eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggnog', 'n.', 'A drink consisting of eggs beaten up with sugar, milk, and\n   (usually) wine or spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggplant', 'n.', 'A plant (Solanum Melongena), of East Indian origin,\n   allied to the tomato, and bearing a large, smooth, edible fruit, shaped\n   somewhat like an egg; mad-apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg-shaped', 'a.', 'Resembling an egg in form; ovoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggshell', 'n.', 'The shell or exterior covering of an egg. Also used\n   figuratively for anything resembling an eggshell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eggshell', 'n.', 'A smooth, white, marine, gastropod shell of the genus\n   Ovulum, resembling an egg in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egg squash', '', 'A variety of squash with small egg-shaped fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eghen', 'n. pl.', 'Eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egilopical', 'a.', 'Pertaining to, of the nature of, or affected with, an\n   aegilops, or tumor in the corner of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egilops', 'n.', 'See Aegilops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglandulose', 'a.', 'Alt. of Eglandulous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglandulous', 'a.', 'Destitute of glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglantine', 'n.', 'A species of rose (Rosa Eglanteria), with fragrant\n   foliage and flowers of various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglantine', 'n.', 'The sweetbrier (R. rubiginosa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglatere', 'n.', 'Eglantine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egling', 'n.', 'The European perch when two years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eglomerate', 'v. t.', 'To unwind, as a thread from a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ego', 'n.', 'The conscious and permanent subject of all psychical\n   experiences, whether held to be directly known or the product of\n   reflective thought; -- opposed to non-ego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoical', 'a.', 'Pertaining to egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoism', 'n.', 'The doctrine of certain extreme adherents or disciples of\n   Descartes and Johann Gottlieb Fichte, which finds all the elements of\n   knowledge in the ego and the relations which it implies or provides\n   for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoism', 'n.', 'Excessive love and thought of self; the habit of regarding\n   ones self as the center of every interest; selfishness; -- opposed to\n   altruism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoist', 'n.', 'One given overmuch to egoism or thoughts of self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoist', 'n.', 'A believer in egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoistic', 'a.', 'Alt. of Egoistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoistical', 'a.', 'Pertaining to egoism; imbued with egoism or excessive\n   thoughts of self; self-loving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoistically', 'adv.', 'In an egoistic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egoity', 'n.', 'Personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egomism', 'n.', 'Egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egophonic', 'a.', 'Belonging to, or resembling, egophony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egophony', 'n.', 'The sound of a patients voice so modified as to resemble\n   the bleating of a goat, heard on applying the ear to the chest in\n   certain diseases within its cavity, as in pleurisy with effusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotheism', 'n.', 'The deification of self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotism', 'n.', 'The practice of too frequently using the word I; hence, a\n   speaking or writing overmuch of ones self; self-exaltation;\n   self-praise; the act or practice of magnifying ones self or parading\n   ones own doings. The word is also used in the sense of egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotist', 'n.', 'One addicted to egotism; one who speaks much of himself or\n   magnifies his own achievements or affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotistic', 'a.', 'Alt. of Egotistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotistical', 'a.', 'Addicted to, or manifesting, egotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotistically', 'adv.', 'With egotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotized', 'imp. & p. p.', 'of Egotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotizing', 'p. pr. & vb. n.', 'of Egotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egotize', 'v. i.', 'To talk or write as an egotist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egranulose', 'a.', 'Having no granules, as chlorophyll in certain\n   conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egre', 'a. & n.', 'See Eager, and Eagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egregious', 'a.', 'Surpassing; extraordinary; distinguished (in a bad\n   sense); -- formerly used with words importing a good quality, but now\n   joined with words having a bad sense; as, an egregious rascal; an\n   egregious ass; an egregious mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egregiously', 'adv.', 'Greatly; enormously; shamefully; as, egregiously\n   cheated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egregiousness', 'n.', 'The state of being egregious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egremoin', 'n.', 'Agrimony (Agrimonia Eupatoria).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egress', 'n.', 'The act of going out or leaving, or the power to leave;\n   departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egress', 'n.', 'The passing off from the suns disk of an inferior planet,\n   in a transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egress', 'v. i.', 'To go out; to depart; to leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egression', 'n.', 'The act of going; egress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egressor', 'n.', 'One who goes out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egret', 'n.', 'The name of several species of herons which bear plumes on\n   the back. They are generally white. Among the best known species are\n   the American egret (Ardea, / Herodias, egretta); the great egret (A.\n   alba); the little egret (A. garzetta), of Europe; and the American\n   snowy egret (A. candidissima).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egret', 'n.', 'A plume or tuft of feathers worn as a part of a headdress,\n   or anything imitating such an ornament; an aigrette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egret', 'n.', 'The flying feathery or hairy crown of seeds or achenes, as\n   the down of the thistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egret', 'n.', 'A kind of ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egrette', 'n.', 'Same as Egret, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egrimony', '', 'The herb agrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egrimony', 'n.', 'Sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egriot', 'n.', 'A kind of sour cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egritude', 'n.', 'Sickness; ailment; sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptian', 'a.', 'Pertaining to Egypt, in Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptian', 'n.', 'A native, or one of the people, of Egypt; also, the\n   Egyptian language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptian', 'n.', 'A gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptized', 'imp. & p. p.', 'of Egyptize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptizing', 'p. pr. & vb. n.', 'of Egyptize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptize', 'v. t.', 'To give an Egyptian character or appearance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptologer', 'n.', 'Alt. of Egyptologist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptologist', 'n.', 'One skilled in the antiquities of Egypt; a student of\n   Egyptology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptological', 'a.', 'Of, pertaining to, or devoted to, Egyptology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Egyptology', 'n.', 'The science or study of Egyptian antiquities, esp. the\n   hieroglyphics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eh', 'interj.', 'An expression of inquiry or slight surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ehlite', 'n.', 'A mineral of a green color and pearly luster; a hydrous\n   phosphate of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eider', 'n.', 'Any species of sea duck of the genus Somateria, esp.\n   Somateria mollissima, which breeds in the northern parts of Europe and\n   America, and lines its nest with fine down (taken from its own body)\n   which is an article of commerce; -- called also eider duck. The\n   American eider (S. Dresseri), the king eider (S. spectabilis), and the\n   spectacled eider (Arctonetta Fischeri) are related species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eidograph', 'n.', 'An instrument for copying drawings on the same or a\n   different scale; a form of the pantograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eidolon', 'n.', 'An image or representation; a form; a phantom; an\n   apparition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eigh', 'interj.', 'An exclamation expressing delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eight', 'n.', 'An island in a river; an ait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eight', 'a.', 'Seven and one; as, eight years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eight', 'n.', 'The number greater by a unit than seven; eight units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eight', 'n.', 'A symbol representing eight units, as 8 or viii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteen', 'a.', 'Eight and ten; as, eighteen pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteen', 'n.', 'The number greater by a unit than seventeen; eighteen\n   units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteen', 'n.', 'A symbol denoting eighteen units, as 18 or xviii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteenmo', 'a. & n.', 'See Octodecimo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteenth', 'a.', 'Next in order after the seventeenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteenth', 'a.', 'Consisting of one of eighteen equal parts or divisions\n   of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteenth', 'n.', 'The quotient of a unit divided by eighteen; one of\n   eighteen equal parts or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighteenth', 'n.', 'The eighth after the tenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightetethe', 'a.', 'Eighteenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightfold', 'a.', 'Eight times a quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighth', 'a.', 'Next in order after the seventh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighth', 'a.', 'Consisting of one of eight equal divisions of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighth', 'n.', 'The quotient of a unit divided by eight; one of eight equal\n   parts; an eighth part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighth', 'n.', 'The interval of an octave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighthly', 'adv.', 'As the eighth in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightieth', 'a.', 'The next in order after seventy-ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightieth', 'a.', 'Consisting of one of eighty equal parts or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightieth', 'n.', 'The quotient of a unit divided by eighty; one of eighty\n   equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightling', 'n.', 'A compound or twin crystal made up of eight individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eightscore', 'a. & n.', 'Eight times twenty; a hundred and sixty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighty', 'a.', 'Eight times ten; fourscore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighty', 'n.', 'The sum of eight times ten; eighty units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eighty', 'n.', 'A symbol representing eighty units, or ten eight times\n   repeated, as 80 or lxxx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eigne', 'a.', 'Eldest; firstborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eigne', 'a.', 'Entailed; belonging to the eldest son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eiking', 'n.', 'See Eking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eikon', 'n.', 'An image or effigy; -- used rather in an abstract sense, and\n   rarely for a work of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eikosane', 'n.', 'A solid hydrocarbon, C20H42, of the paraffine series, of\n   artificial production, and also probably occurring in petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eikosylene', 'n.', 'A liquid hydrocarbon, C20H38, of the acetylene series,\n   obtained from brown coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eild', 'n.', 'Age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eire', 'n.', 'Air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eirenarch', 'n.', 'A justice of the peace; irenarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eirenic', 'a.', 'Pacific. See Irenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eirie', 'n.', 'See Aerie, and Eyrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eisel', 'n.', 'Vinegar; verjuice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eisteddfod', 'n.', 'Am assembly or session of the Welsh bards; an annual\n   congress of bards, minstrels and literati of Wales, -- being a\n   patriotic revival of the old custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Either', 'a. & pron.', 'One of two; the one or the other; -- properly used\n   of two things, but sometimes of a larger number, for any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Either', 'a. & pron.', 'Each of two; the one and the other; both; --\n   formerly, also, each of any number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Either', 'conj. Either', 'precedes two, or more, coordinate words or\n   phrases, and is introductory to an alternative. It is correlative to\n   or.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculated', 'imp. & p. p.', 'of Ejaculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculating', 'p. pr. & vb. n.', 'of Ejaculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculate', 'v. t.', 'To throw out suddenly and swiftly, as if a dart; to\n   dart; to eject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculate', 'v. t.', 'To throw out, as an exclamation; to utter by a brief\n   and sudden impulse; as, to ejaculate a prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculate', 'v. i.', 'To utter ejaculations; to make short and hasty\n   exclamations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculation', 'n.', 'The act of throwing or darting out with a sudden force\n   and rapid flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculation', 'n.', 'The uttering of a short, sudden exclamation or prayer,\n   or the exclamation or prayer uttered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculation', 'n.', 'The act of ejecting or suddenly throwing, as a fluid\n   from a duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculator', 'n.', 'A muscle which helps ejaculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculatory', 'a.', 'Casting or throwing out; fitted to eject; as,\n   ejaculatory vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculatory', 'a.', 'Suddenly darted out; uttered in short sentences; as,\n   an ejaculatory prayer or petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejaculatory', 'a.', 'Sudden; hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejected', 'imp. & p. p.', 'of Eject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejecting', 'p. pr. & vb. n.', 'of Eject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eject', 'v. t.', 'To expel; to dismiss; to cast forth; to thrust or drive\n   out; to discharge; as, to eject a person from a room; to eject a\n   traitor from the country; to eject words from the language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eject', 'v. t.', 'To cast out; to evict; to dispossess; as, to eject\n   tenants from an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejection', 'n.', 'The act of ejecting or casting out; discharge; expulsion;\n   evacuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejection', 'n.', 'The act or process of discharging anything from the body,\n   particularly the excretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejection', 'n.', 'The state of being ejected or cast out; dispossession;\n   banishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejectment', 'n.', 'A casting out; a dispossession; an expulsion; ejection;\n   as, the ejectment of tenants from their homes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejectment', 'n.', 'A species of mixed action, which lies for the recovery\n   of possession of real property, and damages and costs for the wrongful\n   withholding of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejector', 'n.', 'One who, or that which, ejects or dispossesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejector', 'n.', 'A jet jump for lifting water or withdrawing air from a\n   space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejoo', 'n.', 'Gomuti fiber. See Gomuti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ejulation', 'n.', 'A wailing; lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekabor', 'n.', 'Alt. of Ekaboron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekaboron', 'n.', 'The name given by Mendelejeff in accordance with the\n   periodic law, and by prediction, to a hypothetical element then\n   unknown, but since discovered and named scandium; -- so called because\n   it was a missing analogue of the boron group. See Scandium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekaluminium', 'n.', 'The name given to a hypothetical element, -- later\n   discovered and called gallium. See Gallium, and cf. Ekabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekasilicon', 'n.', 'The name of a hypothetical element predicted and\n   afterwards discovered and named germanium; -- so called because it was\n   a missing analogue of the silicon group. See Germanium, and cf. Ekabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eked', 'imp. & p. p.', 'of Eke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eking', 'p. pr. & vb. n.', 'of Eke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eke', 'v. t.', 'To increase; to add to; to augment; -- now commonly used\n   with out, the notion conveyed being to add to, or piece out by a\n   laborious, inferior, or scanty addition; as, to eke out a scanty supply\n   of one kind with some other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eke', 'adv.', 'In addition; also; likewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eke', 'n.', 'An addition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekebergite', 'n.', 'A variety of scapolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ekename', 'n.', 'An additional or epithet name; a nickname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eking', 'v. t.', 'A lengthening or filling piece to make good a deficiency\n   in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eking', 'v. t.', 'The carved work under the quarter piece at the aft part\n   of the quarter gallery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'E-la', 'n.', 'Originally, the highest note in the scale of Guido; hence,\n   proverbially, any extravagant saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborate', 'a.', 'Wrought with labor; finished with great care; studied;\n   executed with exactness or painstaking; as, an elaborate discourse; an\n   elaborate performance; elaborate research.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborated', 'imp. & p. p.', 'of Elaborate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborating', 'p. pr. & vb. n.', 'of Elaborate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborate', 'v. t.', 'To produce with labor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborate', 'v. t.', 'To perfect with painstaking; to improve or refine\n   with labor and study, or by successive operations; as, to elaborate a\n   painting or a literary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaboration', 'n.', 'The act or process of producing or refining with\n   labor; improvement by successive operations; refinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaboration', 'n.', 'The natural process of formation or assimilation,\n   performed by the living organs in animals and vegetables, by which a\n   crude substance is changed into something of a higher order; as, the\n   elaboration of food into chyme; the elaboration of chyle, or sap, or\n   tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborative', 'a.', 'Serving or tending to elaborate; constructing with\n   labor and minute attention to details.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaborator', 'n.', 'One who, or that which, elaborates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaboratory', 'a.', 'Tending to elaborate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaboratory', 'n.', 'A laboratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaeagnus', 'n.', 'A genus of shrubs or small trees, having the foliage\n   covered with small silvery scales; oleaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaeis', 'n.', 'A genus of palms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaeolite', 'n.', 'A variety of hephelite, usually massive, of greasy\n   luster, and gray to reddish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaeoptene', 'n.', 'The more liquid or volatile portion of certain oily\n   substance, as distinguished from stearoptene, the more solid parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaidate', 'n.', 'A salt of elaidic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaidic', 'a.', 'Relating to oleic acid, or elaine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaidin', 'n.', 'A solid isomeric modification of olein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaine', 'n.', 'Alt. of Elain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elain', 'n.', 'Same as Olein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaiodic', 'a.', 'Derived from castor oil; ricinoleic; as, elaiodic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaiometer', 'n.', 'An apparatus for determining the amount of oil\n   contained in any substance, or for ascertaining the degree of purity of\n   oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elamite', 'n.', 'A dweller in Flam (or Susiana), an ancient kingdom of\n   Southwestern Asia, afterwards a province of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elamping', 'a.', 'Shining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elan', 'b.', 'Ardor inspired by passion or enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elanced', 'imp. & p. p.', 'of Elance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elancing', 'p. pr. & vb. n.', 'of Elance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elance', 'v. t.', 'To throw as a lance; to hurl; to dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eland', 'n.', 'A species of large South African antelope (Oreas canna). It\n   is valued both for its hide and flesh, and is rapidly disappearing in\n   the settled districts; -- called also Cape elk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eland', 'n.', 'The elk or moose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elanet', 'n.', 'A kite of the genus Elanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaolite', 'n.', 'See Elaeolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaoptene', 'n.', 'See Elaeoptene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaphine', 'a.', 'Pertaining to, resembling, or characteristic of, the\n   stag, or Cervus elaphus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaphure', 'n.', 'A species of deer (Elaphurus Davidianus) found in china.\n   It is about four feet high at the shoulder and has peculiar antlers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapidation', 'n.', 'A clearing away of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapine', 'a.', 'Like or pertaining to the Elapidae, a family of poisonous\n   serpents, including the cobras. See Ophidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaps', 'n.', 'A genus of venomous snakes found both in America and the Old\n   World. Many species are known. See Coral snake, under Coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapsed', 'imp. & p. p.', 'of Elapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapsing', 'p. pr. & vb. n.', 'of Elapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapse', 'v. i.', 'To slip or glide away; to pass away silently, as time;\n   -- used chiefly in reference to time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elapsion', 'n.', 'The act of elapsing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaqueate', 'v. t.', 'To disentangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasipoda', 'n. pl.', 'An order of holothurians mostly found in the deep\n   sea. They are remarkable for their bilateral symmetry and curious\n   forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmobranch', 'a.', 'Of or pertaining to the Elasmobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmobranch', 'n.', 'One of the Elasmobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmobranchiate', 'a.', 'Of or pertaining to Elasmobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmobranchiate', 'n.', 'One of the Elasmobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmobranchii', 'n. pl.', 'A subclass of fishes, comprising the sharks,\n   the rays, and the Chimaera. The skeleton is mainly cartilaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasmosaurus', 'n.', 'An extinct, long-necked, marine, cretaceous reptile\n   from Kansas, allied to Plesiosaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastic', 'a.', 'Springing back; having a power or inherent property of\n   returning to the form from which a substance is bent, drawn, pressed,\n   or twisted; springy; having the power of rebounding; as, a bow is\n   elastic; the air is elastic; India rubber is elastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastic', 'a.', 'Able to return quickly to a former state or condition,\n   after being depressed or overtaxed; having power to recover easily from\n   shocks and trials; as, elastic spirits; an elastic constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastic', 'n.', 'An elastic woven fabric, as a belt, braces or suspenders,\n   etc., made in part of India rubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastical', 'a.', 'Elastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastically', 'adv.', 'In an elastic manner; by an elastic power; with a\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasticity', 'n.', 'The quality of being elastic; the inherent property in\n   bodies by which they recover their former figure or dimensions, after\n   the removal of external pressure or altering force; springiness;\n   tendency to rebound; as, the elasticity of caoutchouc; the elasticity\n   of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasticity', 'n.', 'Power of resistance to, or recovery from, depression or\n   overwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elasticness', 'n.', 'The quality of being elastic; elasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elastin', 'n.', 'A nitrogenous substance, somewhat resembling albumin,\n   which forms the chemical basis of elastic tissue. It is very insoluble\n   in most fluids, but is gradually dissolved when digested with either\n   pepsin or trypsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elate', 'a.', 'Lifted up; raised; elevated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elate', 'a.', 'Having the spirits raised by success, or by hope; flushed or\n   exalted with confidence; elated; exultant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elated', 'imp. & p. p.', 'of Elate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elating', 'p. pr. & vb. n.', 'of Elate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elate', 'v. t.', 'To raise; to exalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elate', 'v. t.', 'To exalt the spirit of; to fill with confidence or\n   exultation; to elevate or flush with success; to puff up; to make\n   proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elatedly', 'adv.', 'With elation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elatedness', 'n.', 'The state of being elated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elater', 'n.', 'One who, or that which, elates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elater', 'n.', 'An elastic spiral filament for dispersing the spores, as in\n   some liverworts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elater', 'n.', 'Any beetle of the family Elateridae, having the habit, when\n   laid on the back, of giving a sudden upward spring, by a quick movement\n   of the articulation between the abdomen and thorax; -- called also\n   click beetle, spring beetle, and snapping beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elater', 'n.', 'The caudal spring used by Podura and related insects for\n   leaping. See Collembola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elater', 'n.', 'The active principle of elaterium, being found in the juice\n   of the wild or squirting cucumber (Ecballium agreste, formerly\n   Motordica Elaterium) and other related species. It is extracted as a\n   bitter, white, crystalline substance, which is a violent purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaterite', 'n.', 'A mineral resin, of a blackish brown color, occurring in\n   soft, flexible masses; -- called also mineral caoutchouc, and elastic\n   bitumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaterium', 'n.', 'A cathartic substance obtained, in the form of yellowish\n   or greenish cakes, as the dried residue of the juice of the wild or\n   squirting cucumber (Ecballium agreste, formerly called Momordica\n   Elaterium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elaterometer', 'n.', 'Same as Elatrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elatery', 'n.', 'Acting force; elasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elation', 'n.', 'A lifting up by success; exaltation; inriation with pride\n   of prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elative', 'a.', 'Raised; lifted up; -- a term applied to what is also\n   called the absolute superlative, denoting a high or intense degree of a\n   quality, but not excluding the idea that an equal degree may exist in\n   other cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elatrometer', 'n.', 'An instrument for measuring the degree of rarefaction\n   of air contained in the receiver of an air pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elayl', 'n.', 'Olefiant gas or ethylene; -- so called by Berzelius from its\n   forming an oil combining with chlorine. [Written also elayle.] See\n   Ethylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'n.', 'The joint or bend of the arm; the outer curve in the middle\n   of the arm when bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'n.', 'Any turn or bend like that of the elbow, in a wall,\n   building, and the like; a sudden turn in a line of coast or course of a\n   river; also, an angular or jointed part of any structure, as the raised\n   arm of a chair or sofa, or a short pipe fitting, turning at an angle or\n   bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'n.', 'A sharp angle in any surface of wainscoting or other\n   woodwork; the upright sides which flank any paneled work, as the sides\n   of windows, where the jamb makes an elbow with the window back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbowed', 'imp. & p. p.', 'of Elbow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbowing', 'p. pr. & vb. n.', 'of Elbow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'v. t.', 'To push or hit with the elbow, as when one pushes by\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'v. i.', 'To jut into an angle; to project or to bend after the\n   manner of an elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbow', 'v. i.', 'To push rudely along; to elbow ones way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbowboard', 'n.', 'The base of a window casing, on which the elbows may\n   rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbowchair', 'n.', 'A chair with arms to support the elbows; an armchair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elbowroom', 'n.', 'Room to extend the elbows on each side; ample room for\n   motion or action; free scope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elcaja', 'n.', 'An Arabian tree (Trichilia emetica). The fruit, which is\n   emetic, is sometimes employed in the composition of an ointment for the\n   cure of the itch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elcesaite', 'n.', 'One of a sect of Asiatic Gnostics of the time of the\n   Emperor Trajan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eld', 'a.', 'Old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eld', 'n.', 'Age; esp., old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eld', 'n.', 'Old times; former days; antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eld', 'v. i.', 'To age; to grow old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eld', 'v. t.', 'To make old or ancient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'Older; more aged, or existing longer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'Born before another; prior in years; senior; earlier; older;\n   as, his elder brother died in infancy; -- opposed to younger, and now\n   commonly applied to a son, daughter, child, brother, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'One who is older; a superior in age; a senior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'An aged person; one who lived at an earlier period; a\n   predecessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'A person who, on account of his age, occupies the office of\n   ruler or judge; hence, a person occupying any office appropriate to\n   such as have the experience and dignity which age confers; as, the\n   elders of Israel; the elders of the synagogue; the elders in the\n   apostolic church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'a.', 'A clergyman authorized to administer all the sacraments; as,\n   a traveling elder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elder', 'n.', 'A genus of shrubs (Sambucus) having broad umbels of white\n   flowers, and small black or red berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elderish', 'a.', 'Somewhat old; elderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elderly', 'a.', 'Somewhat old; advanced beyond middle age; bordering on old\n   age; as, elderly people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldern', 'a.', 'Made of elder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldership', 'n.', 'The state of being older; seniority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldership', 'n.', 'Office of an elder; collectively, a body of elders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elderwort', 'n.', 'Danewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldest', 'a.', 'Oldest; longest in duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldest', 'a.', 'Born or living first, or before the others, as a son,\n   daughter, brother, etc.; first in origin. See Elder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elding', 'n.', 'Fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'El Doradoes', 'pl. ', 'of El Dorado');
  INSERT INTO words (word, word_type, description) VALUES  (    'El Dorado', '', 'A name given by the Spaniards in the 16th century to an\n   imaginary country in the interior of South America, reputed to abound\n   in gold and precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'El Dorado', '', 'Any region of fabulous wealth; exceeding richness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eldritch', 'a.', 'Hideous; ghastly; as, an eldritch shriek or laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleatic', 'a.', 'Of or pertaining to a certain school of Greek philosophers\n   who taught that the only certain science is that which owes nothing to\n   the senses, and all to the reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleatic', 'n.', 'A philosopher of the Eleatic school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleaticism', 'n.', 'The Eleatic doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elecampane', 'n.', 'A large, coarse herb (Inula Helenium), with composite\n   yellow flowers. The root, which has a pungent taste, is used as a\n   tonic, and was formerly of much repute as a stomachic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elecampane', 'n.', 'A sweetmeat made from the root of the plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'a.', 'Chosen; taken by preference from among two or more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'a.', 'Chosen as the object of mercy or divine favor; set apart to\n   eternal life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'a.', 'Chosen to an office, but not yet actually inducted into it;\n   as, bishop elect; governor or mayor elect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'n.', 'One chosen or set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'n.', 'Those who are chosen for salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elected', 'imp. & p. p.', 'of Elect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electing', 'p. pr. & vb. n.', 'of Elect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'v. t.', 'To pick out; to select; to choose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'v. t.', 'To select or take for an office; to select by vote; as,\n   to elect a representative, a president, or a governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elect', 'v. t.', 'To designate, choose, or select, as an object of mercy or\n   favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electant', 'n.', 'One who has the power of choosing; an elector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electary', 'n.', 'See Electuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electic', 'a.', 'See Eclectic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electicism', 'n.', 'See Eclecticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'The act of choosing; choice; selection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'The act of choosing a person to fill an office, or to\n   membership in a society, as by ballot, uplifted hands, or viva voce;\n   as, the election of a president or a mayor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'Power of choosing; free will; liberty to choose or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'Discriminating choice; discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'Divine choice; predestination of individuals as objects\n   of mercy and salvation; -- one of the \"five points\" of Calvinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'The choice, made by a party, of two alternatives, by\n   taking one of which, the chooser is excluded from the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Election', 'a.', 'Those who are elected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electionered', 'imp. & p. p.', 'of Electioneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electioneering', 'p. pr. & vb. n.', 'of Electioneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electioneer', 'v. i.', 'To make interest for a candidate at an election; to\n   use arts for securing the election of a candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electioneerer', 'n.', 'One who electioneers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elective', 'a.', 'Exerting the power of choice; selecting; as, an elective\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elective', 'a.', 'Pertaining to, or consisting in, choice, or right of\n   choosing; electoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elective', 'a.', 'Dependent on choice; bestowed or passing by election; as,\n   an elective study; an elective office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elective', 'n.', 'In an American college, an optional study or course of\n   study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electively', 'adv.', 'In an elective manner; by choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elector', 'n.', 'One who elects, or has the right of choice; a person who\n   is entitled to take part in an election, or to give his vote in favor\n   of a candidate for office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elector', 'n.', 'Hence, specifically, in any country, a person legally\n   qualified to vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elector', 'n.', 'In the old German empire, one of the princes entitled to\n   choose the emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elector', 'n.', 'One of the persons chosen, by vote of the people in the\n   United States, to elect the President and Vice President.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elector', 'a.', 'Pertaining to an election or to electors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electorality', 'n.', 'The territory or dignity of an elector; electorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electorate', 'n.', 'The territory, jurisdiction, or dignity of an elector,\n   as in the old German empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electorate', 'n.', 'The whole body of persons in a nation or state who are\n   entitled to vote in an election, or any distinct class or division of\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electoress', 'n.', 'An electress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electorial', 'a.', 'Electoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electorship', 'n.', 'The office or status of an elector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electre', 'n.', 'Alt. of Electer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electer', 'n.', 'Amber. See Electrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electer', 'n.', 'A metallic substance compounded of gold and silver; an\n   alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrepeter', 'n.', 'An instrument used to change the direction of\n   electric currents; a commutator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electress', 'n.', 'The wife or widow of an elector in the old German\n   empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electric', 'a.', 'Alt. of Electrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrical', 'a.', 'Pertaining to electricity; consisting of, containing,\n   derived from, or produced by, electricity; as, electric power or\n   virtue; an electric jar; electric effects; an electric spark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrical', 'a.', 'Capable of occasioning the phenomena of electricity;\n   as, an electric or electrical machine or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrical', 'a.', 'Electrifying; thrilling; magnetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electric', 'n.', 'A nonconductor of electricity, as amber, glass, resin,\n   etc., employed to excite or accumulate electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrically', 'adv.', 'In the manner of electricity, or by means of it;\n   thrillingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electricalness', 'a.', 'The state or quality of being electrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrician', 'n.', 'An investigator of electricity; one versed in the\n   science of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electricities', 'pl. ', 'of Electricity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electricity', 'n.', 'A power in nature, a manifestation of energy,\n   exhibiting itself when in disturbed equilibrium or in activity by a\n   circuit movement, the fact of direction in which involves polarity, or\n   opposition of properties in opposite directions; also, by attraction\n   for many substances, by a law involving attraction between surfaces of\n   unlike polarity, and repulsion between those of like; by exhibiting\n   accumulated polar tension when the circuit is broken; and by producing\n   heat, light, concussion, and often chemical changes when the circuit\n   passes between the poles or through any imperfectly conducting\n   substance or space. It is generally brought into action by any\n   disturbance of molecular equilibrium, whether from a chemical,\n   physical, or mechanical, cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electricity', 'n.', 'The science which unfolds the phenomena and laws of\n   electricity; electrical science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electricity', 'n.', 'Fig.: Electrifying energy or characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrifiable', 'a.', 'Capable of receiving electricity, or of being\n   charged with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrification', 'n.', 'The act of electrifying, or the state of being\n   charged with electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrified', 'imp. & p. p.', 'of Electrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrifying', 'p. pr. & vb. n.', 'of Electrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrify', 'v. t.', 'To communicate electricity to; to charge with\n   electricity; as, to electrify a jar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrify', 'v. t.', 'To cause electricity to pass through; to affect by\n   electricity; to give an electric shock to; as, to electrify a limb, or\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrify', 'v. t.', 'To excite suddenly and violently, esp. by something\n   highly delightful or inspiriting; to thrill; as, this patriotic\n   sentiment electrified the audience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrify', 'v. i.', 'To become electric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrine', 'a.', 'Belonging to, or made of, amber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrine', 'a.', 'Made of electrum, an alloy used by the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrition', 'n.', 'The recognition by an animal body of the electrical\n   condition of external objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrization', 'n.', 'The act of electrizing; electrification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrized', 'imp. & p. p.', 'of Electrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrizing', 'p. pr. & vb. n.', 'of Electrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrize', 'v. t.', 'To electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrizer', 'n.', 'One who, or that which, electrizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-', '', 'A prefix or combining form signifying pertaining to\n   electricity, produced by electricity, producing or employing\n   electricity, etc.; as, electro-negative; electro-dynamic;\n   electro-magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro', 'n.', 'An electrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-ballistic', 'a.', 'Pertaining to electro-ballistics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-ballistics', 'n.', 'The art or science of measuring the force or\n   velocity of projectiles by means of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-biologist', 'n.', 'One versed in electro-biology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-biology', 'n.', 'That branch of biology which treats of the\n   electrical phenomena of living organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-biology', 'n.', 'That phase of mesmerism or animal magnetism, the\n   phenomena of which are supposed to be produced by a form of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-bioscopy', 'n.', 'A method of determining the presence or absence\n   of life in an animal organism with a current of electricity, by noting\n   the presence or absence of muscular contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-capillarity', 'n.', 'The occurrence or production of certain\n   capillary effects by the action of an electrical current or charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-capillary', 'a.', 'Pert. to, or caused by, electro-capillarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-chemical', 'a.', 'Of or pertaining to electro-chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-chemistry', 'n.', 'That branch of science which treats of the\n   relation of electricity to chemical changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-chronograph', 'n.', 'An instrument for obtaining an accurate record\n   of the time at which any observed phenomenon occurs, or of its\n   duration. It has an electro-magnetic register connected with a clock.\n   See Chronograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-chronographic', 'a.', 'Belonging to the electro-chronograph, or\n   recorded by the aid of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrocute', 'v. t.', 'To execute or put to death by electricity. --\n   E*lec`tro*cu\"tion, n. [Recent; Newspaper words]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrode', 'n.', 'The path by which electricity is conveyed into or from a\n   solution or other conducting medium; esp., the ends of the wires or\n   conductors, leading from source of electricity, and terminating in the\n   medium traversed by the current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-dynamic', 'a.', 'Alt. of Electro-dynamical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-dynamical', 'a.', 'Pertaining to the movements or force of electric\n   or galvanic currents; dependent on electric force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-dynamics', 'n.', 'The phenomena of electricity in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-dynamics', 'n.', 'The branch of science which treats of the\n   properties of electric currents; dynamical electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-dynamometer', 'n.', 'An instrument for measuring the strength of\n   electro-dynamic currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-engraving', 'n.', 'The art or process of engraving by means of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-etching', 'n.', 'A mode of etching upon metals by electrolytic\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrogenesis', 'n.', 'Same as Electrogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrogenic', 'a.', 'Of or pertaining to electrogenesis; as, an\n   electrogenic condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrogeny', 'n.', 'A term sometimes applied to the effects (tetanus)\n   produced in the muscles of the limbs, when a current of electricity is\n   passed along the spinal cord or nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-gilding', 'n.', 'The art or process of gilding copper, iron, etc.,\n   by means of voltaic electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-gilt', 'a.', 'Gilded by means of voltaic electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrograph', 'n.', 'A mark, record, or tracing, made by the action of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-kinetic', 'a.', 'Of or pertaining to electro-kinetics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-kinetics', 'n.', 'That branch of electrical science which treats of\n   electricity in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolier', 'n.', 'A branching frame, often of ornamental design, to\n   support electric illuminating lamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrology', 'n.', 'That branch of physical science which treats of the\n   phenomena of electricity and its properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolysis', 'n.', 'The act or process of chemical decomposition, by the\n   action of electricity; as, the electrolysis of silver or nickel for\n   plating; the electrolysis of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyte', 'n.', 'A compound decomposable, or subjected to\n   decomposition, by an electric current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolytic', 'a.', 'Alt. of Electrolytical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolytical', 'a.', 'Pertaining to electrolysis; as, electrolytic\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyzable', 'a.', 'Capable of being electrolyzed, or decomposed by\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyzation', 'n.', 'The act or the process of electrolyzing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyzed', 'imp. & p. p.', 'of Electrolyze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyzing', 'p. pr. & vb. n.', 'of Electrolyze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrolyze', 'v. t.', 'To decompose by the direct action of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-magnet', 'n.', 'A mass, usually of soft iron, but sometimes of some\n   other magnetic metal, as nickel or cobalt, rendered temporarily\n   magnetic by being placed within a coil of wire through which a current\n   of electricity is passing. The metal is generally in the form of a bar,\n   either straight, or bent into the shape of a horseshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-magnetic', 'a.', 'Of, Pertaining to, or produced by, magnetism\n   which is developed by the passage of an electric current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-magnetism', 'n.', 'The magnetism developed by a current of\n   electricity; the science which treats of the development of magnetism\n   by means of voltaic electricity, and of the properties or actions of\n   the currents evolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-metallurgy', 'n.', 'The act or art precipitating a metal\n   electro-chemical action, by which a coating is deposited, on a prepared\n   surface, as in electroplating and electrotyping; galvanoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrometer', 'n.', 'An instrument for measuring the quantity or intensity\n   of electricity; also, sometimes, and less properly, applied to an\n   instrument which indicates the presence of electricity (usually called\n   an electroscope).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-metric', 'a.', 'Alt. of Electro-metrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-metrical', 'a.', 'Pertaining to electrometry; made by means of\n   electrometer; as, an electrometrical experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elextrometry', 'n.', 'The art or process of making electrical measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-motion', 'n.', 'The motion of electricity or its passage from one\n   metal to another in a voltaic circuit; mechanical action produced by\n   means of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-motive', 'a.', 'Producing electro-motion; producing, or tending to\n   produce, electricity or an electric current; causing electrical action\n   or effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electromotor', 'n.', 'A mover or exciter of electricity; as apparatus for\n   generating a current of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electromotor', 'n.', 'An apparatus or machine for producing motion and\n   mechanical effects by the action of electricity; an electro-magnetic\n   engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-muscular', 'a.', 'Pertaining the reaction (contraction) of the\n   muscles under electricity, or their sensibility to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electron', 'n.', 'Amber; also, the alloy of gold and silver, called\n   electrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-negative', 'a.', 'Having the property of being attracted by an\n   electro-positive body, or a tendency to pass to the positive pole in\n   electrolysis, by the law that opposite electricities attract each\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-negative', 'a.', 'Negative; nonmetallic; acid; -- opposed to\n   positive, metallic, or basic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-negative', 'n.', 'A body which passes to the positive pole in\n   electrolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electropathy', 'n.', 'The treatment of disease by electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrophone', 'n.', 'An instrument for producing sound by means of\n   electric currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrophori', 'pl. ', 'of Electrophorus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrophorus', 'n.', 'An instrument for exciting electricity, and\n   repeating the charge indefinitely by induction, consisting of a flat\n   cake of resin, shelllac, or ebonite, upon which is placed a plate of\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-physiological', 'a.', 'Pertaining to electrical results produced\n   through physiological agencies, or by change of action in a living\n   organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-physiology', 'n.', 'That branch of physiology which treats of\n   electric phenomena produced through physiological agencies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroplating', 'imp. & p. p.', 'of Electroplate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroplate', 'v. t.', 'To plate or cover with a coating of metal, usually\n   silver, nickel, or gold, by means of electrolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroplater', 'n.', 'One who electroplates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroplating', 'n.', 'The art or process of depositing a coating\n   (commonly) of silver, gold, or nickel on an inferior metal, by means of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-polar', 'a.', 'Possessing electrical polarity; positively\n   electrified at one end, or on one surface, and negatively at the other;\n   -- said of a conductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-positive', 'a.', 'Of such a nature relatively to some other\n   associated body or bodies, as to tend to the negative pole of a voltaic\n   battery, in electrolysis, while the associated body tends to the\n   positive pole; -- the converse or correlative of electro-negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-positive', 'a.', 'Hence: Positive; metallic; basic; --\n   distinguished from negative, nonmetallic, or acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-positive', 'n.', 'A body which passes to the negative pole in\n   electrolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-puncturation', 'n.', 'Alt. of Electro-puncturing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-puncturing', 'n.', 'See Electropuncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-puncture', 'n.', 'An operation that consists in inserting needless\n   in the part affected, and connecting them with the poles of a galvanic\n   apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroscope', 'n.', 'An instrument for detecting the presence of\n   electricity, or changes in the electric state of bodies, or the species\n   of electricity present, as by means of pith balls, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electroscopic', 'a.', 'Relating to, or made by means of, the electroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrostatic', 'a.', 'Pertaining to electrostatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrostatics', 'n.', 'That branch of science which treats of statical\n   electricity or electric force in a state of rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-stereotype', 'n.', 'Same as Electrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-telegraphic', 'a.', 'Pertaining to the electric telegraph, or by\n   means of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-telegraphy', 'n.', 'The art or science of constructing or using the\n   electric telegraph; the transmission of messages by means of the\n   electric telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-therapeutics', 'n.', 'The branch of medical science which treats of\n   the applications agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-thermancy', 'n.', 'That branch of electrical science which treats\n   of the effect of an electric current upon the temperature of a\n   conductor, or a part of a circuit composed of two different metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-tint', 'n.', 'A style of engraving in relief by means of voltaic\n   electricity. A picture is drawn on a metallic plate with some material\n   which resists the fluids of a battery; so that, in electro-typing, the\n   parts not covered by the varnish, etc., receive a deposition of metal,\n   and produce the required copy in intaglio. A cast of this is then the\n   plate for printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotonic', 'a.', 'Of or pertaining to electrical tension; -- said of a\n   supposed peculiar condition of a conducting circuit during its exposure\n   to the action of another conducting circuit traversed by a uniform\n   electric current when both circuits remain stationary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotonic', 'a.', 'Relating to electrotonus; as, the electrotonic\n   condition of a nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotonize', 'v. t.', 'To cause or produce electrotonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotonous', 'a.', 'Electrotonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotonus', 'n.', 'The modified condition of a nerve, when a constant\n   current of electricity passes through any part of it. See\n   Anelectrotonus, and Catelectrotonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotype', 'n.', 'A facsimile plate made by electrotypy for use in\n   printing; also, an impression or print from such plate. Also used\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotyped', 'imp. & p. p.', 'of Electrotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotyping', 'p. pr. & vb. n.', 'of Electrotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotype', 'v. t.', 'To make facsimile plates of by the electrotype\n   process; as, to electrotype a page of type, a book, etc. See\n   Electrotype, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotyper', 'n.', 'One who electrotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotypic', 'a.', 'Pertaining to, or effected by means of, electrotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotyping', 'n.', 'The act or the process of making electrotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrotypy', 'n.', 'The process of producing electrotype plates. See Note\n   under Electrotype, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-vital', 'a.', 'Derived from, or dependent upon, vital processes; --\n   said of certain electric currents supposed by some physiologists to\n   circulate in the nerves of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electro-vitalism', 'n.', 'The theory that the functions of living organisms\n   are dependent upon electricity or a kindred force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrum', 'n.', 'Amber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrum', 'n.', 'An alloy of gold and silver, of an amber color, used by\n   the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electrum', 'n.', 'German-silver plate. See German silver, under German.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electuaries', 'pl. ', 'of Electuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Electuary', 'n.', 'A medicine composed of powders, or other ingredients,\n   incorporated with some convserve, honey, or sirup; a confection. See\n   the note under Confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynarily', 'adv.', 'In an eleemosynary manner; by charity;\n   charitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynary', 'a.', 'Relating to charity, alms, or almsgiving; intended\n   for the distribution of charity; as, an eleemosynary corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynary', 'a.', 'Given in charity or alms; having the nature of alms;\n   as, eleemosynary assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynary', 'a.', 'Supported by charity; as, eleemosynary poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynaries', 'pl. ', 'of Eleemosynary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleemosynary', 'n.', 'One who subsists on charity; a dependent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegance', 'n.', 'Alt. of Elegancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegancy', 'n.', 'The state or quality of being elegant; beauty as\n   resulting from choice qualities and the complete absence of what\n   deforms or impresses unpleasantly; grace given by art or practice; fine\n   polish; refinement; -- said of manners, language, style, form,\n   architecture, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegancy', 'n.', 'That which is elegant; that which is tasteful and highly\n   attractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegant', 'a.', 'Very choice, and hence, pleasing to good taste;\n   characterized by grace, propriety, and refinement, and the absence of\n   every thing offensive; exciting admiration and approbation by symmetry,\n   completeness, freedom from blemish, and the like; graceful; tasteful\n   and highly attractive; as, elegant manners; elegant style of\n   composition; an elegant speaker; an elegant structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegant', 'a.', 'Exercising a nice choice; discriminating beauty or\n   sensitive to beauty; as, elegant taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegantly', 'adv.', 'In a manner to please nice taste; with elegance; with\n   due symmetry; richly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiac', 'a.', 'Belonging to elegy, or written in elegiacs; plaintive;\n   expressing sorrow or lamentation; as, an elegiac lay; elegiac strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiac', 'a.', 'Used in elegies; as, elegiac verse; the elegiac distich or\n   couplet, consisting of a dactylic hexameter and pentameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiac', 'n.', 'Elegiac verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiacal', 'a.', 'Elegiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiast', 'n.', 'One who composes elegies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegiographer', 'n.', 'An elegist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegist', 'n.', 'A write of elegies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegit', 'n.', 'A judicial writ of execution, by which a defendants goods\n   are appraised and delivered to the plaintiff, and, if not sufficient to\n   satisfy the debt, all of his lands are delivered, to be held till the\n   debt is paid by the rents and profits, or until the defendants\n   interest has expired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegize', 'v. t.', 'To lament in an elegy; to celebrate in elegiac verse;\n   to bewail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegies', 'pl. ', 'of Elegy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elegy', 'n.', 'A mournful or plaintive poem; a funereal song; a poem of\n   lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleidin', 'n.', 'Lifeless matter deposited in the form of minute granules\n   within the protoplasm of living cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the simplest or essential parts or principles of\n   which anything consists, or upon which the constitution or fundamental\n   powers of anything are based.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the ultimate, undecomposable constituents of any\n   kind of matter. Specifically: (Chem.) A substance which cannot be\n   decomposed into different kinds of matter by any means at present\n   employed; as, the elements of water are oxygen and hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the ultimate parts which are variously combined in\n   anything; as, letters are the elements of written language; hence,\n   also, a simple portion of that which is complex, as a shaft, lever,\n   wheel, or any simple part in a machine; one of the essential\n   ingredients of any mixture; a constituent part; as, quartz, feldspar,\n   and mica are the elements of granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One out of several parts combined in a system of\n   aggregation, when each is of the nature of the whole; as, a single cell\n   is an element of the honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the smallest natural divisions of the organism, as\n   a blood corpuscle, a muscular fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the simplest essential parts, more commonly called\n   cells, of which animal and vegetable organisms, or their tissues and\n   organs, are composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'An infinitesimal part of anything of the same nature as\n   the entire magnitude considered; as, in a solid an element may be the\n   infinitesimal portion between any two planes that are separated an\n   indefinitely small distance. In the calculus, element is sometimes used\n   as synonymous with differential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'Sometimes a curve, or surface, or volume is considered as\n   described by a moving point, or curve, or surface, the latter being at\n   any instant called an element of the former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the terms in an algebraic expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the necessary data or values upon which a system of\n   calculations depends, or general conclusions are based; as, the\n   elements of a planets orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'The simplest or fundamental principles of any system in\n   philosophy, science, or art; rudiments; as, the elements of geometry,\n   or of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'Any outline or sketch, regarded as containing the\n   fundamental ideas or features of the thing in question; as, the\n   elements of a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'One of the simple substances, as supposed by the ancient\n   philosophers; one of the imaginary principles of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'The four elements were, air, earth, water, and fire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'the conditions and movements of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'The elements of the alchemists were salt, sulphur, and\n   mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'The whole material composing the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'n.', 'The bread and wine used in the eucharist or Lords supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'v. t.', 'To compound of elements or first principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Element', 'v. t.', 'To constitute; to make up with elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elemental', 'a.', 'Pertaining to the elements, first principles, and\n   primary ingredients, or to the four supposed elements of the material\n   world; as, elemental air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elemental', 'a.', 'Pertaining to rudiments or first principles;\n   rudimentary; elementary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementalism', 'a.', 'The theory that the heathen divinities originated in\n   the personification of elemental powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementality', 'n.', 'The condition of being composed of elements, or a\n   thing so composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementally', 'adv.', 'According to elements; literally; as, the words,\n   \"Take, eat; this is my body,\" elementally understood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementar', 'a.', 'Elementary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementariness', 'n.', 'The state of being elementary; original simplicity;\n   uncompounded state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementarity', 'n.', 'Elementariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementary', 'a.', 'Having only one principle or constituent part;\n   consisting of a single element; simple; uncompounded; as, an elementary\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementary', 'a.', 'Pertaining to, or treating of, the elements, rudiments,\n   or first principles of anything; initial; rudimental; introductory; as,\n   an elementary treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementary', 'a.', 'Pertaining to one of the four elements, air, water,\n   earth, fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementation', 'n.', 'Instruction in the elements or first principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elementoid', 'a.', 'Resembling an element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elemi', 'n.', 'A fragrant gum resin obtained chiefly from tropical trees of\n   the genera Amyris and Canarium. A. elemifera yields Mexican elemi; C.\n   commune, the Manila elemi. It is used in the manufacture of varnishes,\n   also in ointments and plasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elemin', 'n.', 'A transparent, colorless oil obtained from elemi resin by\n   distillation with water; also, a crystallizable extract from the resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchs', 'pl. ', 'of Elench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elench', 'n.', 'That part of an argument on which its conclusiveness\n   depends; that which convinces of refutes an antagonist; a refutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elench', 'n.', 'A specious but fallacious argument; a sophism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchical', 'a.', 'Pertaining to an elench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchically', 'adv.', 'By means of an elench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchize', 'v. i.', 'To dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchtic', 'a.', 'Alt. of Elenchtical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchtical', 'a.', 'Same as Elenctic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenchus', 'n.', 'Same as Elench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenctic', 'a.', 'Alt. of Elenctical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenctical', 'a.', 'Serving to refute; refutative; -- applied to indirect\n   modes of proof, and opposed to deictic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elenge', 'a.', 'Sorrowful; wretched; full of trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elengeness', 'n.', 'Loneliness; misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephansy', 'n.', 'Elephantiasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephant', 'n.', 'A mammal of the order Proboscidia, of which two living\n   species, Elephas Indicus and E. Africanus, and several fossil species,\n   are known. They have a proboscis or trunk, and two large ivory tusks\n   proceeding from the extremity of the upper jaw, and curving upwards.\n   The molar teeth are large and have transverse folds. Elephants are the\n   largest land animals now existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephant', 'n.', 'Ivory; the tusk of the elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephantiac', 'a.', 'Affected with elephantiasis; characteristic of\n   elephantiasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephantiasis', 'n.', 'A disease of the skin, in which it become enormously\n   thickened, and is rough, hard, and fissured, like an elephants hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephantine', 'a.', 'Pertaining to the elephant, or resembling an elephant\n   (commonly, in size); hence, huge; immense; heavy; as, of elephantine\n   proportions; an elephantine step or tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephantoid', 'a.', 'Alt. of Elephantoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elephantoidal', 'a.', 'Resembling an elephant in form or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleusinian', 'a.', 'Pertaining to Eleusis, in Greece, or to secret rites in\n   honor of Ceres, there celebrated; as, Eleusinian mysteries or\n   festivals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleutheromania', 'n.', 'A mania or frantic zeal for freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleutheromaniac', 'a.', 'Mad for freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleuthero-petalous', 'a.', 'Having the petals free, that is, entirely\n   separate from each other; -- said of both plant and flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'a.', 'Elevated; raised aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevated', 'imp. & p. p.', 'of Elevate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevating', 'p. pr. & vb. n.', 'of Elevate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To bring from a lower place to a higher; to lift up; to\n   raise; as, to elevate a weight, a flagstaff, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To raise to a higher station; to promote; as, to\n   elevate to an office, or to a high social position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To raise from a depressed state; to animate; to cheer;\n   as, to elevate the spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To exalt; to ennoble; to dignify; as, to elevate the\n   mind or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To raise to a higher pitch, or to a greater degree of\n   loudness; -- said of sounds; as, to elevate the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To intoxicate in a slight degree; to render tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevate', 'v. t.', 'To lessen; to detract from; to disparage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevated', 'a.', 'Uplifted; high; lofty; also, animated; noble; as,\n   elevated thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevatedness', 'n.', 'The quality of being elevated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'The act of raising from a lower place, condition, or\n   quality to a higher; -- said of material things, persons, the mind, the\n   voice, etc.; as, the elevation of grain; elevation to a throne;\n   elevation of mind, thoughts, or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'Condition of being elevated; height; exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'That which is raised up or elevated; an elevated place\n   or station; as, an elevation of the ground; a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'The distance of a celestial object above the horizon, or\n   the arc of a vertical circle intercepted between it and the horizon;\n   altitude; as, the elevation of the pole, or of a star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'The angle which the style makes with the substylar line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'The movement of the axis of a piece in a vertical plane;\n   also, the angle of elevation, that is, the angle between the axis of\n   the piece and the line o/ sight; -- distinguished from direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevation', 'n.', 'A geometrical projection of a building, or other object,\n   on a plane perpendicular to the horizon; orthographic projection on a\n   vertical plane; -- called by the ancients the orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'One who, or that which, raises or lifts up anything');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'A mechanical contrivance, usually an endless belt or\n   chain with a series of scoops or buckets, for transferring grain to an\n   upper loft for storage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'A cage or platform and the hoisting machinery in a hotel,\n   warehouse, mine, etc., for conveying persons, goods, etc., to or from\n   different floors or levels; -- called in England a lift; the cage or\n   platform itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'A building for elevating, storing, and discharging,\n   grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'A muscle which serves to raise a part of the body, as the\n   leg or the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevator', 'n.', 'An instrument for raising a depressed portion of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevatory', 'a.', 'Tending to raise, or having power to elevate; as,\n   elevatory forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elevatory', 'n.', 'See Elevator, n. (e).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleve', 'n.', 'A pupil; a student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleven', 'a.', 'Ten and one added; as, eleven men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleven', 'n.', 'The sum of ten and one; eleven units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleven', 'n.', 'A symbol representing eleven units, as 11 or xi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleven', 'n.', 'The eleven men selected to play on one side in a match, as\n   the representatives of a club or a locality; as, the all-England\n   eleven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleventh', 'a.', 'Next after the tenth; as, the eleventh chapter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleventh', 'a.', 'Constituting one of eleven parts into which a thing is\n   divided; as, the eleventh part of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleventh', 'a.', 'Of or pertaining to the interval of the octave and the\n   fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleventh', 'n.', 'The quotient of a unit divided by eleven; one of eleven\n   equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eleventh', 'n.', 'The interval consisting of ten conjunct degrees; the\n   interval made up of an octave and a fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elves', 'pl. ', 'of Elf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elf', 'n.', 'An imaginary supernatural being, commonly a little sprite,\n   much like a fairy; a mythological diminutive spirit, supposed to haunt\n   hills and wild places, and generally represented as delighting in\n   mischievous tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elf', 'n.', 'A very diminutive person; a dwarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elf', 'v. t.', 'To entangle mischievously, as an elf might do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfin', 'a.', 'Relating to elves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfin', 'n.', 'A little elf or urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfish', 'a.', 'Of or relating to the elves; elflike; implike; weird;\n   scarcely human; mischievous, as though caused by elves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfishly', 'adv.', 'In an elfish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfishness', 'n.', 'The quality of being elfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfkin', 'n.', 'A little elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elfland', 'n.', 'Fairyland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elflock', 'n.', 'Hair matted, or twisted into a knot, as if by elves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elgin marbles', '', 'Greek sculptures in the British Museum. They were\n   obtained at Athens, about 1811, by Lord Elgin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elicit', 'a.', 'Elicited; drawn out; made real; open; evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elicited', 'imp. & p. p.', 'of Elicit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliciting', 'p. pr. & vb. n.', 'of Elicit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elicit', 'v. t.', 'To draw out or entice forth; to bring to light; to bring\n   out against the will; to deduce by reason or argument; as, to elicit\n   truth by discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elicitate', 'v. t.', 'To elicit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elicitation', 'n.', 'The act of eliciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elided', 'imp. & p. p.', 'of Elide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliding', 'p. pr. & vb. n.', 'of Elide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elide', 'v. t.', 'To break or dash in pieces; to demolish; as, to elide the\n   force of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elide', 'v. t.', 'To cut off, as a vowel or a syllable, usually the final\n   one; to subject to elision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eligibility', 'n.', 'The quality of being eligible; eligibleness; as, the\n   eligibility of a candidate; the eligibility of an offer of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eligible', 'a.', 'That may be selected; proper or qualified to be chosen;\n   legally qualified to be elected and to hold office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eligible', 'a.', 'Worthy to be chosen or selected; suitable; desirable; as,\n   an eligible situation for a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eligibleness', 'n.', 'The quality worthy or qualified to be chosen;\n   suitableness; desirableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eligibly', 'adv.', 'In an eligible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elimate', 'v. t.', 'To render smooth; to polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminant', 'n.', 'The result of eliminating n variables between n\n   homogeneous equations of any degree; -- called also resultant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminated', 'imp. & p. p.', 'of Eliminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminating', 'p. pr. & vb. n.', 'of Eliminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminate', 'v. t.', 'To put out of doors; to expel; to discharge; to\n   release; to set at liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminate', 'v. t.', 'To cause to disappear from an equation; as, to\n   eliminate an unknown quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminate', 'v. t.', 'To set aside as unimportant in a process of inductive\n   inquiry; to leave out of consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminate', 'v. t.', 'To obtain by separating, as from foreign matters; to\n   deduce; as, to eliminate an idea or a conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminate', 'v. t.', 'To separate; to expel from the system; to excrete;\n   as, the kidneys eliminate urea, the lungs carbonic acid; to eliminate\n   poison from the system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elimination', 'n.', 'The act of expelling or throwing off');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elimination', 'n.', 'the act of discharging or excreting waste products or\n   foreign substances through the various emunctories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elimination', 'n.', 'Act of causing a quantity to disappear from an\n   equation; especially, in the operation of deducing from several\n   equations containing several unknown quantities a less number of\n   equations containing a less number of unknown quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elimination', 'n.', 'The act of obtaining by separation, or as the result\n   of eliminating; deduction. [See Eliminate, 4.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliminative', 'a.', 'Relating to, or carrying on, elimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elinguate', 'v. t.', 'To deprive of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elinguation', 'n.', 'Punishment by cutting out the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elinguid', 'a.', 'Tongue-tied; dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliquament', 'n.', 'A liquid obtained from fat, or fat fish, by pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eliquation', 'n.', 'The process of separating a fusible substance from one\n   less fusible, by means of a degree of heat sufficient to melt the one\n   and not the other, as an alloy of copper and lead; liquation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elison', 'n.', 'Division; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elison', 'n.', 'The cutting off or suppression of a vowel or syllable, for\n   the sake of meter or euphony; esp., in poetry, the dropping of a final\n   vowel standing before an initial vowel in the following word, when the\n   two words are drawn together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elisor', 'n.', 'An elector or chooser; one of two persons appointed by a\n   court to return a jury or serve a writ when the sheriff and the\n   coroners are disqualified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elite', 'n.', 'A choice or select body; the flower; as, the elite of\n   society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elix', 'v. t.', 'To extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixate', 'v. t.', 'To boil; to seethe; hence, to extract by boiling or\n   seething.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixation', 'n.', 'A seething; digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixir', 'n.', 'A tincture with more than one base; a compound tincture or\n   medicine, composed of various substances, held in solution by alcohol\n   in some form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixir', 'n.', 'An imaginary liquor capable of transmuting metals into\n   gold; also, one for producing life indefinitely; as, elixir vitae, or\n   the elixir of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixir', 'n.', 'The refined spirit; the quintessence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elixir', 'n.', 'Any cordial or substance which invigorates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elizabethan', 'a.', 'Pertaining to Queen Elizabeth or her times, esp. to\n   the architecture or literature of her reign; as, the Elizabethan\n   writers, drama, literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elizabethan', 'n.', 'One who lived in England in the time of Queen\n   Elizabeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elk', 'n.', 'A large deer, of several species. The European elk (Alces\n   machlis or Cervus alces) is closely allied to the American moose. The\n   American elk, or wapiti (Cervus Canadensis), is closely related to the\n   European stag. See Moose, and Wapiti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elk', 'n.', 'Alt. of Elke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elke', 'n.', 'The European wild or whistling swan (Cygnus ferus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elknut', 'n.', 'The buffalo nut. See under Buffalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elkwood', 'n.', 'The soft, spongy wood of a species of Magnolia (M.\n   Umbrella).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ell', 'n.', 'A measure for cloth; -- now rarely used. It is of different\n   lengths in different countries; the English ell being 45 inches, the\n   Dutch or Flemish ell 27, the Scotch about 37.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ell', 'n.', 'See L.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellachick', 'n.', 'A fresh-water tortoise (Chelopus marmoratus) of\n   California; -- used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellagic', 'a.', 'Pertaining to, or derived from, gallnuts or gallic acid;\n   as, ellagic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellebore', 'n.', 'Hellebore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elleborin', 'n.', 'See Helleborin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elleck', 'n.', 'The red gurnard or cuckoo fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellenge', 'n', 'Alt. of Ellingeness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellinge', 'n', 'Alt. of Ellingeness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellengeness', 'n', 'Alt. of Ellingeness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellingeness', 'n', 'See Elenge, Elengeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elles', 'adv. & conj.', 'See Else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipse', 'n.', 'An oval or oblong figure, bounded by a regular curve,\n   which corresponds to an oblique projection of a circle, or an oblique\n   section of a cone through its opposite sides. The greatest diameter of\n   the ellipse is the major axis, and the least diameter is the minor\n   axis. See Conic section, under Conic, and cf. Focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipse', 'n.', 'Omission. See Ellipsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipse', 'n.', 'The elliptical orbit of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipses', 'pl. ', 'of Ellipsis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsis', 'n.', 'Omission; a figure of syntax, by which one or more words,\n   which are obviously understood, are omitted; as, the virtues I admire,\n   for, the virtues which I admire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsis', 'n.', 'An ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsograph', 'n.', 'An instrument for describing ellipses; -- called also\n   trammel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsoid', 'n.', 'A solid, all plane sections of which are ellipses or\n   circles. See Conoid, n., 2 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsoid', 'a.', 'Alt. of Ellipsoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipsoidal', 'a.', 'Pertaining to, or shaped like, an ellipsoid; as,\n   ellipsoid or ellipsoidal form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptic', 'a.', 'Alt. of Elliptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptical', 'a.', 'Of or pertaining to an ellipse; having the form of an\n   ellipse; oblong, with rounded ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptical', 'a.', 'Having a part omitted; as, an elliptical phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptically', 'adv.', 'In the form of an ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptically', 'adv.', 'With a part omitted; as, elliptically expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellipticity', 'n.', 'Deviation of an ellipse or a spheroid from the form of\n   a circle or a sphere; especially, in reference to the figure of the\n   earth, the difference between the equatorial and polar semidiameters,\n   divided by the equatorial; thus, the ellipticity of the earth is /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptic-lanceolate', 'a.', 'Having a form intermediate between elliptic\n   and lanceolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elliptograph', 'n.', 'Same as Ellipsograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ellwand', 'n.', 'Formerly, a measuring rod an ell long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elm', 'n.', 'A tree of the genus Ulmus, of several species, much used as a\n   shade tree, particularly in America. The English elm is Ulmus\n   campestris; the common American or white elm is U. Americana; the\n   slippery or red elm, U. fulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elmen', 'a.', 'Belonging to elms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elmos fire', '', 'See Corposant; also Saint Elmos Fire, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elmy', 'a.', 'Abounding with elms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocation', 'n.', 'A removal from the usual place of residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocation', 'n.', 'Departure from the usual state; an ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocular', 'a.', 'Having but one cell, or cavity; not divided by a septum\n   or partition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocution', 'n.', 'Utterance by speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocution', 'n.', 'Oratorical or expressive delivery, including the graces\n   of intonation, gesture, etc.; style or manner of speaking or reading in\n   public; as, clear, impressive elocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocution', 'n.', 'Suitable and impressive writing or style; eloquent\n   diction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocutionary', 'a.', 'Pertaining to elocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocutionist', 'n.', 'One who is versed in elocution; a teacher of\n   elocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elocutive', 'a.', 'Pertaining to oratorical expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elodian', 'n.', 'One of a tribe of tortoises, including the terrapins,\n   etc., in which the head and neck can be withdrawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloge', 'n.', 'A panegyrical funeral oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elogist', 'n.', 'One who pronounces an eloge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elogium', 'n.', 'Alt. of Elogy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elogy', 'n.', 'The praise bestowed on a person or thing; panegyric; eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elohim', 'n.', 'One of the principal names by which God is designated in\n   the Hebrew Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elohist', 'n.', 'The writer, or one of the writers, of the passages of the\n   Old Testament, notably those of Elohim instead of Jehovah, as the name\n   of the Supreme Being; -- distinguished from Jehovist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elohistic', 'a.', 'Relating to Elohim as a name of God; -- said of passages\n   in the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloigned', 'imp. & p. p.', 'of Eloign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloigning', 'p. pr. & vb. n.', 'of Eloign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloign', 'v. t.', 'To remove afar off; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloign', 'v. t.', 'To convey to a distance, or beyond the jurisdiction, or\n   to conceal, as goods liable to distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloignate', 'v. t.', 'To remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloignment', 'n.', 'Removal to a distance; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloin', 'v. t.', 'See Eloign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloinate', 'v. t.', 'See Eloignate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloinment', 'n.', 'See Eloignment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elong', 'v. t.', 'To lengthen out; to prolong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elong', 'v. t.', 'To put away; to separate; to keep off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongated', 'imp. & p. p.', 'of Elongate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongating', 'p. pr. & vb. n.', 'of Elongate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongate', 'a.', 'To lengthen; to extend; to stretch; as, to elongate a\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongate', 'a.', 'To remove further off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongate', 'v. i.', 'To depart to, or be at, a distance; esp., to recede\n   apparently from the sun, as a planet in its orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongate', 'a.', 'Drawn out at length; elongated; as, an elongate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongation', 'n.', 'The act of lengthening, or the state of being\n   lengthened; protraction; extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongation', 'n.', 'That which lengthens out; continuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongation', 'n.', 'Removal to a distance; withdrawal; a being at a\n   distance; distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elongation', 'n.', 'The angular distance of a planet from the sun; as, the\n   elongation of Venus or Mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloped', 'imp. & p. p.', 'of Elope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloping', 'p. pr. & vb. n.', 'of Elope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elope', 'v. t.', 'To run away, or escape privately, from the place or\n   station to which one is bound by duty; -- said especially of a woman or\n   a man, either married or unmarried, who runs away with a paramour or a\n   sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elopement', 'n.', 'The act of eloping; secret departure; -- said of a woman\n   and a man, one or both, who run away from their homes for marriage or\n   for cohabitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloper', 'n.', 'One who elopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elops', 'n.', 'A genus of fishes. See Saury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elops', 'n.', 'A mythical serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquence', 'n.', 'Fluent, forcible, elegant, and persuasive speech in\n   public; the power of expressing strong emotions in striking and\n   appropriate language either spoken or written, thereby producing\n   conviction or persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquence', 'n.', 'Fig.: Whatever produces the effect of moving and\n   persuasive speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquence', 'n.', 'That which is eloquently uttered or written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquent', 'a.', 'Having the power of expressing strong emotions or\n   forcible arguments in an elevated, impassioned, and effective manner;\n   as, an eloquent orator or preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquent', 'a.', 'Adapted to express strong emotion or to state facts\n   arguments with fluency and power; as, an eloquent address or statement;\n   an eloquent appeal to a jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eloquently', 'adv.', 'In an eloquent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elrich', 'a.', 'Alt. of Elritch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elritch', 'a.', 'Ghastly; preternatural. Same as Eldritch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Else', 'a. & pron.', 'Other; one or something beside; as, Who else is\n   coming? What else shall I give? Do you expect anything else?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Else', 'adv. & conj.', 'Besides; except that mentioned; in addition; as,\n   nowhere else; no one else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Else', 'adv. & conj.', 'Otherwise; in the other, or the contrary, case; if\n   the facts were different.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elsewhere', 'adv.', 'In any other place; as, these trees are not to be\n   found elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elsewhere', 'adv.', 'In some other place; in other places, indefinitely;\n   as, it is reported in town and elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elsewhither', 'adv.', 'To some, or any, other place; as, you will have to\n   go elsewhither for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elsewise', 'adv.', 'Otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elsin', 'n.', 'A shoemakers awl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidated', 'imp. & p. p.', 'of Elucidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidating', 'p. pr. & vb. n.', 'of Elucidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidate', 'v. t.', 'To make clear or manifest; to render more\n   intelligible; to illustrate; as, an example will elucidate the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidation', 'n.', 'A making clear; the act of elucidating or that which\n   elucidates, as an explanation, an exposition, an illustration; as, one\n   example may serve for further elucidation of the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidative', 'a.', 'Making clear; tending to elucidate; as, an elucidative\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidator', 'n.', 'One who explains or elucidates; an expositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucidatory', 'a.', 'Tending to elucidate; elucidative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluctate', 'v. i.', 'To struggle out; -- with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluctation', 'n.', 'A struggling out of any difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucubrate', 'v. i.', 'See Lucubrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elucubration', 'n.', 'See Lucubration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluded', 'imp. & p. p.', 'of Elude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluding', 'p. pr. & vb. n.', 'of Elude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elude', 'v. t.', 'To avoid slyly, by artifice, stratagem, or dexterity; to\n   escape from in a covert manner; to mock by an unexpected escape; to\n   baffle; as, to elude an officer; to elude detection, inquiry, search,\n   comprehension; to elude the force of an argument or a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eludible', 'a.', 'Capable of being eluded; evadible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elul', 'n.', 'The sixth month of the Jewish year, by the sacred reckoning,\n   or the twelfth, by the civil reckoning, corresponding nearly to the\n   month of September.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elumbated', 'a.', 'Weak or lame in the loins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elusion', 'n.', 'Act of eluding; adroit escape, as by artifice; a mockery;\n   a cheat; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elusive', 'a.', 'Tending to elude; using arts or deception to escape;\n   adroitly escaping or evading; eluding the grasp; fallacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elusory', 'a.', 'Tending to elude or deceive; evasive; fraudulent;\n   fallacious; deceitful; deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elute', 'v. t.', 'To wash out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elutriated', 'imp. & p. p.', 'of Elutriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elutriating', 'p. pr. & vb. n.', 'of Elutriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elutriate', 'v. t.', 'To wash or strain out so as to purify; as, to\n   elutriate the blood as it passes through the lungs; to strain off or\n   decant, as a powder which is separated from heavier particles by being\n   drawn off with water; to cleanse, as by washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elutriation', 'n.', 'The process of elutriating; a decanting or racking off\n   by means of water, as finer particles from heavier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluxate', 'v. t.', 'To dislocate; to luxate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eluxation', 'n.', 'Dislocation; luxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvan', 'a.', 'Pertaining to elves; elvish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvan', 'a.', 'Of or pertaining to certain veins of feldspathic or\n   porphyritic rock crossing metalliferous veins in the mining districts\n   of Cornwall; as, an elvan course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvan', 'n.', 'Alt. of Elvanite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvanite', 'n.', 'The rock of an elvan vein, or the elvan vein itself; an\n   elvan course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elve', 'n.', 'An old form of Elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elver', 'n.', 'A young eel; a young conger or sea eel; -- called also\n   elvene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elf', 'pl. ', 'of Elves');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvish', 'a.', 'Pertaining to elves; implike; mischievous; weird; also,\n   vacant; absent in demeanor. See Elfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvish', 'a.', 'Mysterious; also, foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elvishly', 'adv.', 'In an elvish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elwand', 'n.', 'See Ellwand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elysian', 'a.', 'Pertaining, or the abode of the blessed after death;\n   hence, yielding the highest pleasures; exceedingly delightful;\n   beatific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elysiums', 'pl. ', 'of Elysium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elysia', 'pl. ', 'of Elysium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elysium', 'n.', 'A dwelling place assigned to happy souls after death; the\n   seat of future happiness; Paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elysium', 'n.', 'Hence, any delightful place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytriform', 'a.', 'Having the form, or structure, of an elytron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytrin', 'n.', 'See Chitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytroid', 'a.', 'Resembling a beetles wing case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytra', 'pl. ', 'of Elytrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytron', 'n.', 'Alt. of Elytrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytrum', 'n.', 'One of the anterior pair of wings in the Coleoptera and\n   some other insects, when they are thick and serve only as a protection\n   for the posterior pair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elytrum', 'n.', 'One of the shieldlike dorsal scales of certain annelids.\n   See Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Elzevir', 'a.', 'Applied to books or editions (esp. of the Greek New\n   Testament and the classics) printed and published by the Elzevir family\n   at Amsterdam, Leyden, etc., from about 1592 to 1680; also, applied to a\n   round open type introduced by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Em', '', 'An obsolete or colloquial contraction of the old form hem, them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Em', 'n.', 'The portion of a line formerly occupied by the letter m, then a\n   square type, used as a unit by which to measure the amount of printed\n   matter on a page; the square of the body of a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Em-', '', 'A prefix. See En-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emacerate', 'v. t. & i.', 'To make lean or to become lean; to emaciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaceration', 'n.', 'Emaciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciated', 'imp. & p. p.', 'of Emaciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciating', 'p. pr. & vb. n.', 'of Emaciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciate', 'v. i.', 'To lose flesh gradually and become very lean; to waste\n   away in flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciate', 'v. t.', 'To cause to waste away in flesh and become very lean;\n   as, his sickness emaciated him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciate', 'a.', 'Emaciated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciation', 'n.', 'The act of making very lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaciation', 'n.', 'The state of being emaciated or reduced to excessive\n   leanness; an excessively lean condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaculate', 'v. t.', 'To clear from spots or stains, or from any\n   imperfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emaculation', 'n.', 'The act of clearing from spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Aemail ombrant', '', 'An art or process of flooding transparent colored\n   glaze over designs stamped or molded on earthenware or porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanant', 'a.', 'Issuing or flowing forth; emanating; passing forth into an\n   act, or making itself apparent by an effect; -- said of mental acts;\n   as, an emanant volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanated', 'imp. & p. p.', 'of Emanate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanating', 'p. pr. & vb. n.', 'of Emanate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanate', 'v. i.', 'To issue forth from a source; to flow out from more or\n   less constantly; as, fragrance emanates from flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanate', 'v. i.', 'To proceed from, as a source or fountain; to take\n   origin; to arise, to originate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanate', 'a.', 'Issuing forth; emanant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanation', 'n.', 'The act of flowing or proceeding from a fountain head or\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanation', 'n.', 'That which issues, flows, or proceeds from any object as\n   a source; efflux; an effluence; as, perfume is an emanation from a\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanative', 'a.', 'Issuing forth; effluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanatively', 'adv.', 'By an emanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emanatory', 'a.', 'Emanative; of the nature of an emanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipated', 'imp. & p. p.', 'of Emancipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipating', 'p. pr. & vb. n.', 'of Emancipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipate', 'v. t.', 'To set free from the power of another; to liberate;\n   as: (a) To set free, as a minor from a parent; as, a father may\n   emancipate a child. (b) To set free from bondage; to give freedom to;\n   to manumit; as, to emancipate a slave, or a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipate', 'v. t.', 'To free from any controlling influence, especially\n   from anything which exerts undue or evil influence; as, to emancipate\n   one from prejudices or error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipate', 'a.', 'Set at liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipation', 'n.', 'The act of setting free from the power of another,\n   from slavery, subjection, dependence, or controlling influence; also,\n   the state of being thus set free; liberation; as, the emancipation of\n   slaves; the emancipation of minors; the emancipation of a person from\n   prejudices; the emancipation of the mind from superstition; the\n   emancipation of a nation from tyranny or subjection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipationist', 'n.', 'An advocate of emancipation, esp. the emancipation\n   of slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipator', 'n.', 'One who emancipates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipatory', 'a.', 'Pertaining to emancipation, or tending to effect\n   emancipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emancipist', 'n.', 'A freed convict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginate', 'v. t.', 'To take away the margin of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginate', 'a.', 'Alt. of Emarginated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginated', 'a.', 'Having the margin interrupted by a notch or shallow\n   sinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginated', 'a.', 'Notched at the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginated', 'a.', 'Having the edges truncated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emarginately', 'adv.', 'In an emarginate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emargination', 'n.', 'The act of notching or indenting the margin, or the\n   state of being so notched; also, a notch or shallow sinus in a margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculated', 'imp. & p. p.', 'of Emasculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculating', 'p. pr. & vb. n.', 'of Emasculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculate', 'v. t.', 'To deprive of virile or procreative power; to\n   castrate power; to castrate; to geld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculate', 'v. t.', 'To deprive of masculine vigor or spirit; to weaken;\n   to render effeminate; to vitiate by unmanly softness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculate', 'a.', 'Deprived of virility or vigor; unmanned; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculation', 'n.', 'The act of depriving of virility, or the state of\n   being so deprived; castration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculation', 'n.', 'The act of depriving, or state of being deprived, of\n   vigor or strength; unmanly weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculator', 'n.', 'One who, or that which, emasculates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emasculatory', 'a.', 'Serving or tending to emasculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embace', 'v. t.', 'See Embase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embale', 'v. t.', 'To make up into a bale or pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embale', 'v. t.', 'To bind up; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emball', 'v. t.', 'To encircle or embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalmed', 'imp. & p. p.', 'of Embalm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalming', 'p. pr. & vb. n.', 'of Embalm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalm', 'v. t.', 'To anoint all over with balm; especially, to preserve\n   from decay by means of balm or other aromatic oils, or spices; to fill\n   or impregnate (a dead body), with aromatics and drugs that it may\n   resist putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalm', 'v. t.', 'To fill or imbue with sweet odor; to perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalm', 'v. t.', 'To preserve from decay or oblivion as if with balm; to\n   perpetuate in remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalmer', 'n.', 'One who embalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embalmment', 'n.', 'The act of embalming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embanked', 'imp. & p. p.', 'of Embank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embanking', 'p. pr. & vb. n.', 'of Embank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embank', 'v. t.', 'To throw up a bank so as to confine or to defend; to\n   protect by a bank of earth or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embankment', 'n.', 'The act of surrounding or defending with a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embankment', 'n.', 'A structure of earth, gravel, etc., raised to prevent\n   water from overflowing a level tract of country, to retain water in a\n   reservoir, or to carry a roadway, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarred', 'imp. & p. p.', 'of Embar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embanking', 'p. pr. & vb. n.', 'of Embar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embar', 'v. t.', 'To bar or shut in; to inclose securely, as with bars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embar', 'v. t.', 'To stop; to hinder by prohibition; to block up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarcation', 'n.', 'Same as Embarkation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarge', 'v. t.', 'To put in a barge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embargoes', 'pl. ', 'of Embargo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embargo', 'n.', 'An edict or order of the government prohibiting the\n   departure of ships of commerce from some or all of the ports within its\n   dominions; a prohibition to sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embargoed', 'imp. & p. p.', 'of Embargo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embargoing', 'p. pr. & vb. n.', 'of Embargo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embargo', 'v. t.', 'To lay an embargo on and thus detain; to prohibit from\n   leaving port; -- said of ships, also of commerce and goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarked', 'imp. & p. p.', 'of Embark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarking', 'p. pr. & vb. n.', 'of Embark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embark', 'v. t.', 'To cause to go on board a vessel or boat; to put on\n   shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embark', 'v. t.', 'To engage, enlist, or invest (as persons, money, etc.)\n   in any affair; as, he embarked his fortune in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embark', 'v. i.', 'To go on board a vessel or a boat for a voyage; as, the\n   troops embarked for Lisbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embark', 'v. i.', 'To engage in any affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarkation', 'n.', 'The act of putting or going on board of a vessel; as,\n   the embarkation of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarkation', 'n.', 'That which is embarked; as, an embarkation of Jesuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarkment', 'n.', 'Embarkation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrassed', 'imp. & p. p.', 'of Embarrass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrassing', 'p. pr. & vb. n.', 'of Embarrass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrass', 'v. t.', 'To hinder from freedom of thought, speech, or action\n   by something which impedes or confuses mental action; to perplex; to\n   discompose; to disconcert; as, laughter may embarrass an orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrass', 'v. t.', 'To hinder from liberty of movement; to impede; to\n   obstruct; as, business is embarrassed; public affairs are embarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrass', 'v. t.', 'To involve in difficulties concerning money matters;\n   to incumber with debt; to beset with urgent claims or demands; -- said\n   of a person or his affairs; as, a man or his business is embarrassed\n   when he can not meet his pecuniary engagements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrass', 'v. t.', 'Embarrassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrassment', 'n.', 'A state of being embarrassed; perplexity; impediment\n   to freedom of action; entanglement; hindrance; confusion or\n   discomposure of mind, as from not knowing what to do or to say;\n   disconcertedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embarrassment', 'n.', 'Difficulty or perplexity arising from the want of\n   money to pay debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embase', 'v. t.', 'To bring down or lower, as in position, value, etc.; to\n   debase; to degrade; to deteriorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embasement', 'v. t.', 'Act of bringing down; depravation; deterioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassade', 'n.', 'An embassy. See Ambassade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassador', 'n.', 'Same as Ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassadorial', 'a.', 'Same as Ambassadorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassadress', 'n.', 'Same as Ambassadress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassadry', 'n.', 'Embassy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassage', 'n.', 'An embassy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassage', 'n.', 'Message; errand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassies', 'pl. ', 'of Embassy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassy', 'n.', 'The public function of an ambassador; the charge or\n   business intrusted to an ambassador or to envoys; a public message to;\n   foreign court concerning state affairs; hence, any solemn message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassy', 'n.', 'The person or persons sent as ambassadors or envoys; the\n   ambassador and his suite; envoys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embassy', 'n.', 'The residence or office of an ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embastardize', 'v. t.', 'To bastardize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embathe', 'v. t.', 'To bathe; to imbathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattail', 'v. t.', 'To furnish with battlements; to fortify as with\n   battlements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattled', 'imp. & p. p.', 'of Embattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattling', 'p. pr. & vb. n.', 'of Embattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattle', 'v. t.', 'To arrange in order of battle; to array for battle;\n   also, to prepare or arm for battle; to equip as for battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattle', 'v. i.', 'To be arrayed for battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattle', 'v. t.', 'To furnish with battlements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattled', 'a.', 'Having indentations like a battlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattled', 'a.', 'Having the edge broken like battlements; -- said of a\n   bearing such as a fess, bend, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattled', 'a.', 'Having been the place of battle; as, an embattled plain\n   or field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattlement', 'n.', 'An intended parapet; a battlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embattlement', 'n.', 'The fortifying of a building or a wall by means of\n   battlements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embay', 'v. t.', 'To bathe; to soothe or lull as by bathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embayed', 'imp. & p. p.', 'of Embay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embaying', 'p. pr. & vb. n.', 'of Embay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embay', 'v. t.', 'To shut in, or shelter, as in a bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embayment', 'n.', 'A bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embeam', 'v. t.', 'To make brilliant with beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embedded', 'imp. & p. p.', 'of Embed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embedding', 'p. pr. & vb. n.', 'of Embed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embed', 'v. t.', 'To lay as in a bed; to lay in surrounding matter; to bed;\n   as, to embed a thing in clay, mortar, or sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embedment', 'n.', 'The act of embedding, or the state of being embedded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellished', 'imp. & p. p.', 'of Embellish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellishing', 'p. pr. & vb. n.', 'of Embellish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellish', 'v. t.', 'To make beautiful or elegant by ornaments; to\n   decorate; to adorn; as, to embellish a book with pictures, a garden\n   with shrubs and flowers, a narrative with striking anecdotes, or style\n   with metaphors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellisher', 'n.', 'One who embellishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellishment', 'n.', 'The act of adorning, or the state of being adorned;\n   adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embellishment', 'n.', 'That which adds beauty or elegance; ornament;\n   decoration; as, pictorial embellishments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ember', 'n.', 'A lighted coal, smoldering amid ashes; -- used chiefly in\n   the plural, to signify mingled coals and ashes; the smoldering remains\n   of a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ember', 'a.', 'Making a circuit of the year of the seasons; recurring in\n   each quarter of the year; as, ember fasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ember-goose', 'n.', 'The loon or great northern diver. See Loon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emberings', 'n. pl.', 'Ember days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embetter', 'v. t.', 'To make better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzled', 'imp. & p. p.', 'of Embezzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzling', 'p. pr. & vb. n.', 'of Embezzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzle', 'v. t.', 'To appropriate fraudulently to ones own use, as\n   property intrusted to ones care; to apply to ones private uses by a\n   breach of trust; as, to embezzle money held in trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzle', 'v. t.', 'To misappropriate; to waste; to dissipate in\n   extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzlement', 'n.', 'The fraudulent appropriation of property by a person\n   to whom it has been intrusted; as, the embezzlement by a clerk of his\n   employers; embezzlement of public funds by the public officer having\n   them in charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embezzler', 'n.', 'One who embezzles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embillow', 'v. i.', 'To swell or heave like a ///// of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embiotocoid', 'a.', 'Belonging to, or resembling, the Embiotocidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embiotocoid', 'n.', 'One of a family of fishes (Embiotocidae) abundant on\n   the coast of California, remarkable for being viviparous; -- also\n   called surf fishes and viviparous fishes. See Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embitter', 'v. t.', 'To make bitter or sad. See Imbitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embitterment', 'n.', 'The act of embittering; also, that which embitters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblanch', 'v. t.', 'To whiten. See Blanch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazed', 'imp. & p. p.', 'of Emblaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazing', 'p. pr. & vb. n.', 'of Emblaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblaze', 'v. t.', 'To adorn with glittering embellishments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblaze', 'v. t.', 'To paint or adorn with armorial figures; to blazon, or\n   emblazon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazoned', 'imp. & p. p.', 'of Emblazon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazoning', 'p. pr. & vb. n.', 'of Emblazon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazon', 'v. t.', 'To depict or represent; -- said of heraldic bearings.\n   See Blazon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazon', 'v. t.', 'To deck in glaring colors; to set off conspicuously;\n   to display pompously; to decorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazoner', 'n.', 'One who emblazons; also, one who publishes and displays\n   anything with pomp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazoning', 'n.', 'The act or art of heraldic decoration; delineation of\n   armorial bearings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazonment', 'n.', 'An emblazoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazonries', 'pl. ', 'of Emblazonry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblazonry', 'n.', 'The act or art of an emblazoner; heraldic or ornamental\n   decoration, as pictures or figures on shields, standards, etc.;\n   emblazonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblem', 'n.', 'Inlay; inlaid or mosaic work; something ornamental inserted\n   in a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblem', 'n.', 'A visible sign of an idea; an object, or the figure of an\n   object, symbolizing and suggesting another object, or an idea, by\n   natural aptness or by association; a figurative representation; a\n   typical designation; a symbol; as, a balance is an emblem of justice; a\n   scepter, the emblem of sovereignty or power; a circle, the emblem of\n   eternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblem', 'n.', 'A picture accompanied with a motto, a set of verse, or the\n   like, intended as a moral lesson or meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblemed', 'imp. & p. p.', 'of Emblem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embleming', 'p. pr. & vb. n.', 'of Emblem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblem', 'v. t.', 'To represent by an emblem; to symbolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematic', 'a.', 'Alt. of Emblematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematical', 'a.', 'Pertaining to, containing, or consisting in, an\n   emblem; symbolic; typically representative; representing as an emblem;\n   as, emblematic language or ornaments; a crown is emblematic of royalty;\n   white is emblematic of purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematiccize', 'v. t.', 'To render emblematic; as, to emblematicize a\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematist', 'n.', 'A writer or inventor of emblems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematized', 'imp. & p. p.', 'of Emblematize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematizing', 'p. pr. & vb. n.', 'of Emblematize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblematize', 'v. t.', 'To represent by, or as by, an emblem; to symbolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblement', 'n.', 'The growing crop, or profits of a crop which has been\n   sown or planted; -- used especially in the plural. The produce of\n   grass, trees, and the like, is not emblement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblemized', 'imp. & p. p.', 'of Emblemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblemizing', 'p. pr. & vb. n.', 'of Emblemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblemize', 'v. t.', 'To represent by an emblem; to emblematize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embloom', 'v. t.', 'To emblossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emblossom', 'v. t.', 'To cover or adorn with blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embodier', 'n.', 'One who embodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embodiment', 'n.', 'The act of embodying; the state of being embodied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embodiment', 'n.', 'That which embodies or is embodied; representation in a\n   physical body; a completely organized system, like the body; as, the\n   embodiment of courage, or of courtesy; the embodiment of true piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embodied', 'imp. & p. p.', 'of Embody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embodying', 'p. pr. & vb. n.', 'of Embody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embody', 'v. t.', 'To form into a body; to invest with a body; to collect\n   into a body, a united mass, or a whole; to incorporate; as, to embody\n   ones ideas in a treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embody', 'v. i.', 'To unite in a body, a mass, or a collection; to\n   coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embogue', 'v. i.', 'To disembogue; to discharge, as a river, its waters\n   into the sea or another river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboguing', 'n.', 'The mouth of a river, or place where its waters are\n   discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboil', 'v. i.', 'To boil with anger; to effervesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboil', 'v. t.', 'To cause to boil with anger; to irritate; to chafe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboitement', 'n.', 'The hypothesis that all living things proceed from\n   preexisting germs, and that these encase the germs of all future living\n   things, inclosed one within another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboldened', 'imp. & p. p.', 'of Embolden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboldening', 'p. pr. & vb. n.', 'of Embolden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolden', 'v. t.', 'To give boldness or courage to; to encourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboldener', 'n.', 'One who emboldens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolic', 'a.', 'Embolismic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolic', 'a.', 'Pertaining to an embolism; produced by an embolism; as, an\n   embolic abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolic', 'a.', 'Pushing or growing in; -- said of a kind of invagination.\n   See under Invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolism', 'n.', 'Intercalation; the insertion of days, months, or years,\n   in an account of time, to produce regularity; as, the embolism of a\n   lunar month in the Greek year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolism', 'n.', 'Intercalated time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolism', 'n.', 'The occlusion of a blood vessel by an embolus. Embolism\n   in the brain often produces sudden unconsciousness and paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolismal', 'a.', 'Pertaining to embolism; intercalary; as, embolismal\n   months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolismatic', 'a.', 'Alt. of Embolismatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolismatical', 'a.', 'Embolismic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolismic', 'a.', 'Alt. of Embolismical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolismical', 'a.', 'Pertaining to embolism or intercalation;\n   intercalated; as, an embolismic year, i. e., the year in which there is\n   intercalation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolite', 'n.', 'A mineral consisting of both the chloride and the bromide\n   of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboli', 'pl. ', 'of Embolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolus', 'n.', 'Something inserted, as a wedge; the piston or sucker of a\n   pump or syringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embolus', 'n.', 'A plug of some substance lodged in a blood vessel, being\n   brought thither by the blood current. It consists most frequently of a\n   clot of fibrin, a detached shred of a morbid growth, a globule of fat,\n   or a microscopic organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboly', 'n.', 'Embolic invagination. See under Invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embonpoint', 'n.', 'Plumpness of person; -- said especially of persons\n   somewhat corpulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emborder', 'v. t.', 'To furnish or adorn with a border; to imborder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embosom', 'v. t.', 'To take into, or place in, the bosom; to cherish; to\n   foster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embosom', 'v. t.', 'To inclose or surround; to shelter closely; to place in\n   the midst of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embossed', 'imp. & p. p.', 'of Emboss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Embossing', 'p. pr. & vb. n.', 'of Emboss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboss', 'v. t.', 'To arise the surface of into bosses or protuberances;\n   particularly, to ornament with raised work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboss', 'v. t.', 'To raise in relief from a surface, as an ornament, a\n   head on a coin, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboss', 'v. t.', 'To make to foam at the mouth, like a hunted animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emboss', 'v. t.', 'To hide or conceal in a thicket; to imbosk; to inclose,\n   shelter, or shroud in a wood.');
