  INSERT INTO words (word, word_type, description) VALUES  (    'Emigrational', 'a.', 'Relating to emigration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emigrationist', 'n.', 'An advocate or promoter of emigration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emigrator', 'n.', 'One who emigrates; am emigrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emigre', 'n.', 'One of the natives of France who were opposed to the first\n   Revolution, and who left their country in consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminence', 'n.', 'That which is eminent or lofty; a high ground or place; a\n   height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminence', 'n.', 'An elevated condition among men; a place or station above\n   men in general, either in rank, office, or celebrity; social or moral\n   loftiness; high rank; distinction; preferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminence', 'n.', 'A title of honor, especially applied to a cardinal in the\n   Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminences', 'pl. ', 'of Eminency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminency', 'n.', 'State of being eminent; eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminent', 'a.', 'High; lofty; towering; prominent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminent', 'a.', 'Being, metaphorically, above others, whether by birth,\n   high station, merit, or virtue; high in public estimation;\n   distinguished; conspicuous; as, an eminent station; an eminent\n   historian, statements, statesman, or saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eminently', 'adv.', 'In an eminent manner; in a high degree; conspicuously;\n   as, to be eminently learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emir', 'n.', 'Alt. of Emeer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emeer', 'n.', 'An Arabian military commander, independent chieftain, or\n   ruler of a province; also, an honorary title given to the descendants\n   of Mohammed, in the line of his daughter Fatima; among the Turks,\n   likewise, a title of dignity, given to certain high officials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emirship', 'n.', 'Alt. of Emeership');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emeership', 'n.', 'The rank or office of an Emir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissaries', 'pl. ', 'of Emissary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissary', 'n.', 'An agent employed to advance, in a covert manner, the\n   interests of his employers; one sent out by any power that is at war\n   with another, to create dissatisfaction among the people of the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissary', 'a.', 'Exploring; spying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissary', 'a.', 'Applied to the veins which pass out of the cranium\n   through apertures in its walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissaryship', 'n.', 'The office of an emissary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emission', 'n.', 'The act of sending or throwing out; the act of sending\n   forth or putting into circulation; issue; as, the emission of light\n   from the sun; the emission of heat from a fire; the emission of bank\n   notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emission', 'n.', 'That which is sent out, issued, or put in circulation at\n   one time; issue; as, the emission was mostly blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissitious', 'a.', 'Looking, or narrowly examining; prying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissive', 'a.', 'Sending out; emitting; as, emissive powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissivity', 'n.', 'Tendency to emission; comparative facility of emission,\n   or rate at which emission takes place, as of heat from the surface of a\n   heated body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emissory', 'a.', 'Same as Emissary, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emitted', 'imp. & p. p.', 'of Emit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emitting', 'p. pr. & vb. n.', 'of Emit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emit', 'v. t.', 'To send forth; to throw or give out; to cause to issue; to\n   give vent to; to eject; to discharge; as, fire emits heat and smoke;\n   boiling water emits steam; the sun emits light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emit', 'v. t.', 'To issue forth, as an order or decree; to print and send\n   into circulation, as notes or bills of credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emittent', 'a.', 'Sending forth; emissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmantle', 'v. t.', 'To cover over with, or as with, a mantle; to put about\n   as a protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmanuel', 'n.', 'See Immanuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmarble', 'v. t.', 'To turn to marble; to harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmenagogue', 'n.', 'A medicine that promotes the menstrual discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmet', 'n.', 'An ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmetropia', 'n.', 'That refractive condition of the eye in which the rays\n   of light are all brought accurately and without undue effort to a focus\n   upon the retina; -- opposed to hypermetropia, myopia, an astigmatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmetropic', 'a.', 'Pertaining to, or characterized by, emmetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmetropy', 'n.', 'Same as Emmetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmew', 'v. t.', 'To mew or coop up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emmove', 'v. t.', 'To move; to rouse; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emodin', 'n.', 'An orange-red crystalline substance, C15H10O5, obtained\n   from the buckthorn, rhubarb, etc., and regarded as a derivative of\n   anthraquinone; -- so called from a species of rhubarb (Rheum emodei).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emollescence', 'n.', 'That degree of softness in a body beginning to melt\n   which alters its shape; the first or lowest degree of fusibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emolliated', 'imp. & p. p.', 'of Emolliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emolliating', 'p. pr. & vb. n.', 'of Emolliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emolliate', 'a.', 'To soften; to render effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emollient', 'a.', 'Softening; making supple; acting as an emollient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emollient', 'n.', 'An external something or soothing application to allay\n   irritation, soreness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emollition', 'n.', 'The act of softening or relaxing; relaxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emolument', 'n.', 'The profit arising from office, employment, or labor;\n   gain; compensation; advantage; perquisites, fees, or salary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emolumental', 'a.', 'Pertaining to an emolument; profitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emong', 'prep.', 'Alt. of Emongst');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emongst', 'prep.', 'Among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotion', 'n.', 'A moving of the mind or soul; excitement of the feelings,\n   whether pleasing or painful; disturbance or agitation of mind caused by\n   a specific exciting cause and manifested by some sensible effect on the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotioned', 'a.', 'Affected with emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotional', 'a.', 'Pertaining to, or characterized by, emotion; excitable;\n   easily moved; sensational; as, an emotional nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotionalism', 'n.', 'The cultivation of an emotional state of mind;\n   tendency to regard things in an emotional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotionalize', 'v. t.', 'To give an emotional character to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotive', 'a.', 'Attended by, or having the character of, emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotiveness', 'n.', 'Susceptibility to emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emotivity', 'n.', 'Emotiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emove', 'v. t.', 'To move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empair', 'v. t.', 'To impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empaistic', 'a.', 'Having to do with inlaid work; -- especially used with\n   reference to work of the ancient Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empale', 'v. t.', 'To make pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empaled', 'imp. & p. p.', 'of Empale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empaling', 'p. pr. & vb. n.', 'of Empale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empale', 'v. t.', 'To fence or fortify with stakes; to surround with a line\n   of stakes for defense; to impale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empale', 'v. t.', 'To inclose; to surround. See Impale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empale', 'v. t.', 'To put to death by thrusting a sharpened stake through\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empale', 'v. t.', 'Same as Impale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empalement', 'n.', 'A fencing, inclosing, or fortifying with stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empalement', 'n.', 'A putting to death by thrusting a sharpened stake\n   through the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empalement', 'n.', 'Same as Impalement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empanel', 'n.', 'A list of jurors; a panel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empanel', 'v. t.', 'See Impanel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empanoplied', 'a.', 'Completely armed; panoplied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emparadise', 'v. t.', 'Same as Imparadise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empark', 'v. t.', 'To make a park of; to inclose, as with a fence; to\n   impark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emparlance', 'n.', 'Parley; imparlance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empasm', 'n.', 'A perfumed powder sprinkled upon the body to mask the odor\n   of sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empassion', 'v. t.', 'To move with passion; to affect strongly. See\n   Impassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empassionate', 'a.', 'Strongly affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empawn', 'v. t.', 'To put in pawn; to pledge; to impawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empeach', 'v. t.', 'To hinder. See Impeach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empearl', 'v. t.', 'To form like pearls; to decorate with, or as with,\n   pearls; to impearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empeople', 'v. t.', 'To form into a people or community; to inhabit; to\n   people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperess', 'n.', 'See Empress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperice', 'n.', 'An empress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperil', 'v. t.', 'To put in peril. See Imperil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperished', 'a.', 'Perished; decayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperor', 'n.', 'The sovereign or supreme monarch of an empire; -- a title\n   of dignity superior to that of king; as, the emperor of Germany or of\n   Austria; the emperor or Czar of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emperorship', 'n.', 'The rank or office of an emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empery', 'n.', 'Empire; sovereignty; dominion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphases', 'pl. ', 'of Emphasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphasis', 'n.', 'A particular stress of utterance, or force of voice,\n   given in reading and speaking to one or more words whose signification\n   the speaker intends to impress specially upon his audience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphasis', 'n.', 'A peculiar impressiveness of expression or weight of\n   thought; vivid representation, enforcing assent; as, to dwell on a\n   subject with great emphasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphasized', 'imp. & p. p.', 'of Emphasize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphasizing', 'p. pr. & vb. n.', 'of Emphasize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphasize', 'v. t.', 'To utter or pronounce with a particular stress of\n   voice; to make emphatic; as, to emphasize a word or a phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphatic', 'a.', 'Alt. of Emphatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphatical', 'a.', 'Uttered with emphasis; made prominent and impressive by\n   a peculiar stress of voice; laying stress; deserving of stress or\n   emphasis; forcible; impressive; strong; as, to remonstrate in am\n   emphatic manner; an emphatic word; an emphatic tone; emphatic\n   reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphatical', 'a.', 'Striking the sense; attracting special attention;\n   impressive; forcible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphatically', 'adv.', 'With emphasis; forcibly; in a striking manner or\n   degree; preeminently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphatically', 'adv.', 'Not really, but apparently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphaticalness', 'n.', 'The quality of being emphatic; emphasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphractic', 'a.', 'Having the quality of closing the pores of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphrensy', 'v. t.', 'To madden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphysema', 'n.', 'A swelling produced by gas or air diffused in the\n   cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphysematous', 'a.', 'Pertaining to, or of the nature of, emphysema;\n   swelled; bloated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphyteusis', 'n.', 'A real right, susceptible of assignment and of\n   descent, charged on productive real estate, the right being coupled\n   with the enjoyment of the property on condition of taking care of the\n   estate and paying taxes, and sometimes a small rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphyteutic', 'a.', 'Of or pertaining to an emphyteusis; as, emphyteutic\n   lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emphyteuticary', 'n.', 'One who holds lands by emphyteusis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empierce', 'v. t.', 'To pierce; to impierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empight', 'a.', 'Fixed; settled; fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empire', 'n.', 'Supreme power; sovereignty; sway; dominion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empire', 'n.', 'The dominion of an emperor; the territory or countries\n   under the jurisdiction and dominion of an emperor (rarely of a king),\n   usually of greater extent than a kingdom, always comprising a variety\n   in the nationality of, or the forms of administration in, constituent\n   and subordinate portions; as, the Austrian empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empire', 'n.', 'Any dominion; supreme control; governing influence; rule;\n   sway; as, the empire of mind or of reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiric', 'n.', 'One who follows an empirical method; one who relies upon\n   practical experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiric', 'n.', 'One who confines himself to applying the results of mere\n   experience or his own observation; especially, in medicine, one who\n   deviates from the rules of science and regular practice; an ignorant\n   and unlicensed pretender; a quack; a charlatan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiric', 'a.', 'Alt. of Empirical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empirical', 'a.', 'Pertaining to, or founded upon, experiment or\n   experience; depending upon the observation of phenomena; versed in\n   experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empirical', 'a.', 'Depending upon experience or observation alone, without\n   due regard to science and theory; -- said especially of medical\n   practice, remedies, etc.; wanting in science and deep insight; as,\n   empiric skill, remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empirically', 'adv.', 'By experiment or experience; without science; in the\n   manner of quacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiricism', 'n.', 'The method or practice of an empiric; pursuit of\n   knowledge by observation and experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiricism', 'n.', 'Specifically, a practice of medicine founded on mere\n   experience, without the aid of science or a knowledge of principles;\n   ignorant and unscientific practice; charlatanry; quackery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiricism', 'n.', 'The philosophical theory which attributes the origin of\n   all our knowledge to experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiricist', 'n.', 'An empiric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empiristic', 'a.', 'Relating to, or resulting from, experience, or\n   experiment; following from empirical methods or data; -- opposed to\n   nativistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplaster', 'n.', 'See Plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplaster', 'n.', 'To plaster over; to cover over so as to present a good\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplastic', 'a.', 'Fit to be applied as a plaster; glutinous; adhesive; as,\n   emplastic applications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplastic', 'n.', 'A medicine causing constipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplastration', 'n.', 'The act or process of grafting by inoculation;\n   budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplastration', 'n.', 'The application of a plaster or salve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplead', 'v. t.', 'To accuse; to indict. See Implead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplection', 'n.', 'See Emplecton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplecton', 'n.', 'A kind of masonry in which the outer faces of the wall\n   are ashlar, the space between being filled with broken stone and\n   mortar. Cross layers of stone are interlaid as binders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplore', 'v. t.', 'See Implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employed', 'imp. & p. p.', 'of Employ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employing', 'p. pr. & vb. n.', 'of Employ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employ', 'v. t.', 'To inclose; to infold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employ', 'v. t.', 'To use; to have in service; to cause to be engaged in\n   doing something; -- often followed by in, about, on, or upon, and\n   sometimes by to; as: (a) To make use of, as an instrument, a means, a\n   material, etc., for a specific purpose; to apply; as, to employ the pen\n   in writing, bricks in building, words and phrases in speaking; to\n   employ the mind; to employ ones energies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employ', 'v. t.', 'To occupy; as, to employ time in study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employ', 'v. t.', 'To have or keep at work; to give employment or\n   occupation to; to intrust with some duty or behest; as, to employ a\n   hundred workmen; to employ an envoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employ', 'n.', 'That which engages or occupies a person; fixed or regular\n   service or business; employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employable', 'a.', 'Capable of being employed; capable of being used; fit\n   or proper for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employe', 'n.', 'One employed by another; a clerk or workman in the service\n   of an employer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employee', 'n.', 'One employed by another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employer', 'n.', 'One who employs another; as, an employer of workmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employment', 'n.', 'The act of employing or using; also, the state of being\n   employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Employment', 'n.', 'That which engages or occupies; that which consumes\n   time or attention; office or post of business; service; as,\n   agricultural employments; mechanical employments; public employments;\n   in the employment of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplumed', 'a.', 'Plumed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emplunge', 'v. t.', 'To plunge; to implunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empoison', 'v. t.', 'To poison; to impoison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empoison', 'n.', 'Poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empoisoner', 'n.', 'Poisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empoisonment', 'n.', 'The act of poisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporetic', 'a.', 'Alt. of Emporetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporetical', 'a.', 'Pertaining to an emporium; relating to merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporiums', 'pl. ', 'of Emporium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporia', 'pl. ', 'of Emporium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporium', 'n.', 'A place of trade; a market place; a mart; esp., a city or\n   town with extensive commerce; the commercial center of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emporium', 'n.', 'The brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empoverish', 'v. t.', 'See Impoverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empowered', 'imp. & p. p.', 'of Empower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empowering', 'p. pr. & vb. n.', 'of Empower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empower', 'v. t.', 'To give authority to; to delegate power to; to\n   commission; to authorize (having commonly a legal force); as, the\n   Supreme Court is empowered to try and decide cases, civil or criminal;\n   the attorney is empowered to sign an acquittance, and discharge the\n   debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empower', 'v. t.', 'To give moral or physical power, faculties, or\n   abilities to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empress', 'n.', 'The consort of an emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empress', 'n.', 'A female sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empress', 'n.', 'A sovereign mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprint', 'v. t.', 'See Imprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprise', 'n.', 'An enterprise; endeavor; adventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprise', 'n.', 'The qualifies which prompt one to undertake difficult and\n   dangerous exploits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprise', 'v. t.', 'To undertake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprising', 'v. t.', 'Full of daring; adventurous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprison', 'v. t.', 'See Imprison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emprosthotonos', 'n.', 'A drawing of the body forward, in consequence of\n   the spasmodic action of some of the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empte', 'v. t.', 'To empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptier', 'n.', 'One who, or that which, empties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptier', 'compar.', 'of Empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptiness', 'n.', 'The state of being empty; absence of contents; void\n   space; vacuum; as, the emptiness of a vessel; emptiness of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptiness', 'n.', 'Want of solidity or substance; unsatisfactoriness;\n   inability to satisfy desire; vacuity; hollowness; the emptiness of\n   earthly glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptiness', 'n.', 'Want of knowledge; lack of sense; vacuity of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emption', 'n.', 'The act of buying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptional', 'a.', 'Capable of being purchased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Containing nothing; not holding or having anything\n   within; void of contents or appropriate contents; not filled; -- said\n   of an inclosure, as a box, room, house, etc.; as, an empty chest, room,\n   purse, or pitcher; an empty stomach; empty shackles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Free; clear; devoid; -- often with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Having nothing to carry; unburdened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Destitute of effect, sincerity, or sense; -- said of\n   language; as, empty words, or threats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Unable to satisfy; unsatisfactory; hollow; vain; --\n   said of pleasure, the world, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Producing nothing; unfruitful; -- said of a plant or\n   tree; as, an empty vine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Destitute of, or lacking, sense, knowledge, or\n   courtesy; as, empty brains; an empty coxcomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'superl.', 'Destitute of reality, or real existence; unsubstantial;\n   as, empty dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empties', 'pl. ', 'of Empty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'n.', 'An empty box, crate, cask, etc.; -- used in commerce, esp.\n   in transportation of freight; as, \"special rates for empties.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptied', 'imp. & p. p.', 'of Empty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptying', 'p. pr. & vb. n.', 'of Empty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'v. t.', 'To deprive of the contents; to exhaust; to make void or\n   destitute; to make vacant; to pour out; to discharge; as, to empty a\n   vessel; to empty a well or a cistern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'v. i.', 'To discharge itself; as, a river empties into the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empty', 'v. i.', 'To become empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptying', 'n.', 'The act of making empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emptying', 'n.', 'The lees of beer, cider, etc.; yeast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empugn', 'v. t.', 'See Impugn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empurpled', 'imp. & p. p.', 'of Empurple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empurpling', 'p. pr. & vb. n.', 'of Empurple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empurple', 'v. t.', 'To tinge or dye of a purple color; to color with\n   purple; to impurple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empuse', 'n.', 'A phantom or specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empuzzle', 'v. t.', 'To puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyema', 'n.', 'A collection of blood, pus, or other fluid, in some cavity\n   of the body, especially that of the pleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyesis', 'n.', 'An eruption of pustules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreal', 'a.', 'Formed of pure fire or light; refined beyond aerial\n   substance; pertaining to the highest and purest region of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreal', 'n.', 'Empyrean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyrean', 'n.', 'The highest heaven, where the pure element of fire was\n   supposed by the ancients to subsist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyrean', 'a.', 'Empyreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreuma', 'n.', 'The peculiar smell and taste arising from products of\n   decomposition of animal or vegetable substances when burnt in close\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreumatic', 'a.', 'Alt. of Empyreumatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreumatical', 'a.', 'Of or pertaining to empyreuma; as, an empyreumatic\n   odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyreumatize', 'v. t.', 'To render empyreumatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyrical', 'a.', 'Containing the combustible principle of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Empyrosis', 'n.', 'A general fire; a conflagration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emrods', 'n. pl.', 'See Emerods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emu', 'n.', 'A large Australian bird, of two species (Dromaius\n   Novae-Hollandiae and D. irroratus), related to the cassowary and the\n   ostrich. The emu runs swiftly, but is unable to fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulable', 'a.', 'Capable of being emulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulate', 'a.', 'Striving to excel; ambitious; emulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulated', 'imp. & p. p.', 'of Emulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulating', 'p. pr. & vb. n.', 'of Emulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulate', 'v. t.', 'To strive to equal or to excel in qualities or actions;\n   to imitate, with a view to equal or to outdo, to vie with; to rival;\n   as, to emulate the good and the great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulation', 'n.', 'The endeavor to equal or to excel another in qualities\n   or actions; an assiduous striving to equal or excel another; rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulation', 'n.', 'Jea/ous rivalry; envy; envious contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulative', 'a.', 'Inclined to emulation; aspiring to competition;\n   rivaling; as, an emulative person or effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulatively', 'adv.', 'In an emulative manner; with emulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulator', 'n.', 'One who emulates, or strives to equal or surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulatory', 'a.', 'Pertaining to emulation; connected with rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulatress', 'n.', 'A female emulator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emule', 'v. t.', 'To emulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulge', 'v. t.', 'To milk out; to drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulgent', 'a.', 'Pertaining to the kidneys; renal; as, emulgent arteries\n   and veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulgent', 'n.', 'An emulgent vessel, as a renal artery or vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulgent', 'n.', 'A medicine that excites the flow of bile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulous', 'a.', 'Ambitiously desirous to equal or even to excel another;\n   eager to emulate or vie with another; desirous of like excellence with\n   another; -- with of; as, emulous of anothers example or virtues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulous', 'a.', 'Vying with; rivaling; hence, contentious, envious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulously', 'adv.', 'In an emulous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulousness', 'n.', 'The quality of being emulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsic', 'a.', 'Pertaining to, or produced from, emulsin; as, emulsic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsify', 'v. t.', 'To convert into an emulsion; to form an emulsion; to\n   reduce from an oily substance to a milky fluid in which the fat\n   globules are in a very finely divided state, giving it the semblance of\n   solution; as, the pancreatic juice emulsifies the oily part of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsin', 'n.', 'The white milky pulp or extract of bitter almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsin', 'n.', 'An unorganized ferment (contained in this extract and in\n   other vegetable juices), which effects the decomposition of certain\n   glucosides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsion', 'n.', 'Any liquid preparation of a color and consistency\n   resembling milk; as: (a) In pharmacy, an extract of seeds, or a mixture\n   of oil and water united by a mucilaginous substance. (b) In\n   photography, a liquid preparation of collodion holding salt of silver,\n   used in the photographic process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsive', 'a.', 'Softening; milklike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsive', 'a.', 'Yielding oil by expression; as, emulsive seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emulsive', 'a.', 'Producing or yielding a milklike substance; as, emulsive\n   acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emunctories', 'pl. ', 'of Emunctory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emunctory', 'n.', 'Any organ or part of the body (as the kidneys, skin,\n   etc.,) which serves to carry off excrementitious or waste matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emuscation', 'n.', 'A freeing from moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emu wren', '', 'A small wrenlike Australian bird (Stipiturus malachurus),\n   having the tail feathers long and loosely barbed, like emu feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emyds', 'pl. ', 'of Emyd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emyd//', 'pl. ', 'of Emyd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emyd', 'n.', 'A fresh-water tortoise of the family Emydidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Emydea', 'n. pl.', 'A group of chelonians which comprises many species of\n   fresh-water tortoises and terrapins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'En-', '', 'A prefix signifying in or into, used in many English words,\n   chiefly those borrowed from the French. Some English words are written\n   indifferently with en-or in-. For ease of pronunciation it is commonly\n   changed to em-before p, b, and m, as in employ, embody, emmew. It is\n   sometimes used to give a causal force, as in enable, enfeeble, to cause\n   to be, or to make, able, or feeble; and sometimes merely gives an\n   intensive force, as in enchasten. See In-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'En-', '', 'A prefix from Gr. / in, meaning in; as, encephalon, entomology.\n   See In-.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-en', '', 'A suffix from AS. -an, formerly used to form the plural of many\n   nouns, as in ashen, eyen, oxen, all obs. except oxen. In some cases,\n   such as children and brethren, it has been added to older plural forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-en', '', 'A suffix corresponding to AS. -en and -on, formerly used to form\n   the plural of verbs, as in housen, escapen.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-en', '', 'A suffix signifying to make, to cause, used to form verbs from\n   nouns and adjectives; as in strengthen, quicken, frighten. This must\n   not be confused with -en corresponding in Old English to the AS.\n   infinitive ending -an.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-en', '', 'An adjectival suffix, meaning made of; as in golden, leaden,\n   wooden.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-en', '', 'The termination of the past participle of many strong verbs; as,\n   in broken, gotten, trodden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'En', 'n.', 'Half an em, that is, half of the unit of space in measuring\n   printed matter. See Em.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enabled', 'imp. & p. p.', 'of Enable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enabling', 'p. pr. & vb. n.', 'of Enable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enable', 'v. t.', 'To give strength or ability to; to make firm and strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enable', 'v. t.', 'To make able (to do, or to be, something); to confer\n   sufficient power upon; to furnish with means, opportunities, and the\n   like; to render competent for; to empower; to endow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enablement', 'n.', 'The act of enabling, or the state of being enabled;\n   ability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enacted', 'imp. & p. p.', 'of Enact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enacting', 'p. pr. & vb. n.', 'of Enact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enact', 'v. t.', 'To decree; to establish by legal and authoritative act;\n   to make into a law; especially, to perform the legislative act with\n   reference to (a bill) which gives it the validity of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enact', 'v. t.', 'To act; to perform; to do; to effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enact', 'v. t.', 'To act the part of; to represent; to play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enact', 'n.', 'Purpose; determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enactive', 'a.', 'Having power to enact or establish as a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enactment', 'n.', 'The passing of a bill into a law; the giving of\n   legislative sanction and executive approval to a bill whereby it is\n   established as a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enactment', 'n.', 'That which is enacted or passed into a law; a law; a\n   decree; a statute; a prescribed requirement; as, a prohibitory\n   enactment; a social enactment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enactor', 'n.', 'One who enacts a law; one who decrees or establishes as a\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enacture', 'n.', 'Enactment; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enaliosaur', 'n.', 'One of the Enaliosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enaliosauria', 'n. pl.', 'An extinct group of marine reptiles, embracing\n   both the Ichthyosauria and the Plesiosauria, now regarded as distinct\n   orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enaliosaurian', 'a.', 'Pertaining to the Enaliosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enaliosaurian', 'n.', 'One of the Enaliosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enallage', 'n.', 'A substitution, as of one part of speech for another, of\n   one gender, number, case, person, tense, mode, or voice, of the same\n   word, for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enambush', 'v. t.', 'To ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'A variety of glass, used in ornament, to cover a\n   surface, as of metal or pottery, and admitting of after decoration in\n   color, or used itself for inlaying or application in varied colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'A glassy, opaque bead obtained by the blowpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'That which is enameled; also, any smooth, glossy\n   surface, resembling enamel, especially if variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'The intensely hard calcified tissue entering into the\n   composition of teeth. It merely covers the exposed parts of the teeth\n   of man, but in many animals is intermixed in various ways with the\n   dentine and cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enameled', 'imp. & p. p.', 'of Enamel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamelled', '', 'of Enamel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enameling', 'p. pr. & vb. n.', 'of Enamel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamelling', '', 'of Enamel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'To lay enamel upon; to decorate with enamel whether\n   inlaid or painted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'To variegate with colors as if with enamel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'To form a glossy surface like enamel upon; as, to enamel\n   card paper; to enamel leather or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. t.', 'To disguise with cosmetics, as a womans complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'v. i.', 'To practice the art of enameling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamel', 'a.', 'Relating to the art of enameling; as, enamel painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamelar', 'a.', 'Consisting of enamel; resembling enamel; smooth; glossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enameled', 'a.', 'Coated or adorned with enamel; having a glossy or\n   variegated surface; glazed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enameler', 'n.', 'Alt. of Enamelist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamelist', 'n.', 'One who enamels; a workman or artist who applies enamels\n   in ornamental work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamored', 'imp. & p. p.', 'of Enamor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamoring', 'p. pr. & vb. n.', 'of Enamor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamor', 'v. t.', 'To inflame with love; to charm; to captivate; -- with\n   of, or with, before the person or thing; as, to be enamored with a\n   lady; to be enamored of books or science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enamorment', 'n.', 'The state of being enamored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enantiomorphous', 'a.', 'Similar, but not superposable, i. e., related to\n   each other as a right-handed to a left-handed glove; -- said of certain\n   hemihedral crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enantiopathic', 'a.', 'Serving to palliate; palliative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enantiopathy', 'n.', 'An opposite passion or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enantiopathy', 'n.', 'Allopathy; -- a term used by followers of Hahnemann,\n   or homeopathists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enantiosis', 'n.', 'A figure of speech by which what is to be understood\n   affirmatively is stated negatively, and the contrary; affirmation by\n   contraries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarch', 'v. t.', 'To arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarched', 'a.', 'Bent into a curve; -- said of a bend or other ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enargite', 'n.', 'An iron-black mineral of metallic luster, occurring in\n   small orthorhombic crystals, also massive. It contains sulphur,\n   arsenic, copper, and often silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarmed', 'a.', 'Same as Armed, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarration', 'n.', 'A detailed exposition; relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarthrodia', 'n.', 'See Enarthrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enarthrosis', 'n.', 'A ball and socket joint, or the kind of articulation\n   represented by such a joint. See Articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enascent', 'a.', 'Coming into being; nascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enatation', 'n.', 'A swimming out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enate', 'a.', 'Growing out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enation', 'n.', 'Any unusual outgrowth from the surface of a thing, as of a\n   petal; also, the capacity or act of producing such an outgrowth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enaunter', 'adv.', 'Lest that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enavigate', 'v. t.', 'To sail away or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enbattled', 'a.', 'Embattled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enbibe', 'v. t.', 'To imbibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enbroude', 'v. t.', 'See Embroude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encaged', 'imp. & p. p.', 'of Encage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaging', 'p. pr. & vb. n.', 'of Encage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encage', 'v. t.', 'To confine in a cage; to coop up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encalendar', 'v. t.', 'To register in a calendar; to calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encamped', 'imp. & p. p.', 'of Encamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encamping', 'p. pr. & vb. n.', 'of Encamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encamp', 'v. i.', 'To form and occupy a camp; to prepare and settle in\n   temporary habitations, as tents or huts; to halt on a march, pitch\n   tents, or form huts, and remain for the night or for a longer time, as\n   an army or a company traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encamp', 'v. t.', 'To form into a camp; to place in a temporary habitation,\n   or quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encampment', 'n.', 'The act of pitching tents or forming huts, as by an\n   army or traveling company, for temporary lodging or rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encampment', 'n.', 'The place where an army or a company is encamped; a\n   camp; tents pitched or huts erected for temporary lodgings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encanker', 'v. t.', 'To canker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encapsulation', 'n.', 'The act of inclosing in a capsule; the growth of a\n   membrane around (any part) so as to inclose it in a capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encarnalize', 'v. t.', 'To carnalize; to make gross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encarpus', 'n.', 'An ornament on a frieze or capital, consisting of\n   festoons of fruit, flowers, leaves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encase', 'v. t.', 'To inclose as in a case. See Incase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encasement', 'n.', 'The act of encasing; also, that which encases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encasement', 'n.', 'An old theory of generation similar to embo/tement. See\n   Ovulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encash', 'v. t.', 'To turn into cash; to cash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encashment', 'n.', 'The payment in cash of a note, draft, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encauma', 'n.', 'An ulcer in the eye, upon the cornea, which causes the\n   loss of the humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encaustic', 'a.', 'Prepared by means of heat; burned in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encaustic', 'a.', 'The method of painting in heated wax, or in any way\n   where heat is used to fix the colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encave', 'v. t.', 'To hide in, or as in, a cave or recess.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ence', '', 'A noun suffix signifying action, state, or quality; also, that\n   which relates to the action or state; as in emergence, diffidence,\n   diligence, influence, difference, excellence. See -ance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enceinte', 'n.', 'The line of works which forms the main inclosure of a\n   fortress or place; -- called also body of the place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enceinte', 'n.', 'The area or town inclosed by a line of fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enceinte', 'a.', 'Pregnant; with child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encenia', 'n. pl.', 'A festival commemorative of the founding of a city or\n   the consecration of a church; also, the ceremonies (as at Oxford and\n   Cambridge, England) commemorative of founders or benefactors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encense', 'n.', 'To offer incense to or upon; to burn incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalic', 'a.', 'Pertaining to the encephalon or brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalitis', 'n.', 'Inflammation of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalocele', 'n.', 'Hernia of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephaloid', 'a.', 'Resembling the material of the brain; cerebriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephaloid', 'n.', 'An encephaloid cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalology', 'n.', 'The science which treats of the brain, its structure\n   and functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalon', 'n.', 'The contents of the cranium; the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalopathy', 'n.', 'Any disease or symptoms of disease referable to\n   disorders of the brain; as, lead encephalopathy, the cerebral symptoms\n   attending chronic lead poisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalos', 'n.', 'The encephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalotomy', 'n.', 'The act or art of dissecting the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encephalous', 'a.', 'Having a head; -- said of most Mollusca; -- opposed to\n   acephalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchafe', 'v. t.', 'To chafe; to enrage; to heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchafing', 'n.', 'Heating; burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchain', 'v. t.', 'To bind with a chain; to hold in chains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchain', 'v. t.', 'To hold fast; to confine; as, to enchain attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchain', 'v. t.', 'To link together; to connect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchainment', 'n.', 'The act of enchaining, or state of being enchained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchair', 'v. t.', 'To seat in a chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchannel', 'v. t.', 'To make run in a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchanted', 'imp. & p. p.', 'of Enchant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchanting', 'p. pr. & vb. n.', 'of Enchant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchant', 'v. t.', 'To charm by sorcery; to act on by enchantment; to get\n   control of by magical words and rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchant', 'v. t.', 'To delight in a high degree; to charm; to enrapture;\n   as, music enchants the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchanted', 'a.', 'Under the power of enchantment; possessed or exercised\n   by enchanters; as, an enchanted castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchanter', 'n.', 'One who enchants; a sorcerer or magician; also, one who\n   delights as by an enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchanting', 'a.', 'Having a power of enchantment; charming; fascinating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchantment', 'n.', 'The act of enchanting; the production of certain\n   wonderful effects by the aid of demons, or the agency of supposed\n   spirits; the use of magic arts, spells, or charms; incantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchantment', 'n.', 'The effect produced by the act; the state of being\n   enchanted; as, to break an enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchantment', 'n.', 'That which captivates the heart and senses; an\n   influence or power which fascinates or highly delights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchantress', 'n.', 'A woman versed in magical arts; a sorceress; also, a\n   woman who fascinates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encharged', 'imp. & p. p.', 'of Encharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encharging', 'p. pr. & vb. n.', 'of Encharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encharge', 'v. t.', 'To charge (with); to impose (a charge) upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encharge', 'n.', 'A charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchased', 'imp. & p. p.', 'of Enchase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchasing', 'p. pr. & vb. n.', 'of Enchase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchase', 'v. t.', 'To incase or inclose in a border or rim; to surround\n   with an ornamental casing, as a gem with gold; to encircle; to inclose;\n   to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchase', 'v. t.', 'To chase; to ornament by embossing or engraving; as, to\n   enchase a watch case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchase', 'v. t.', 'To delineate or describe, as by writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchaser', 'n.', 'One who enchases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchasten', 'v. t.', 'To chasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encheson', 'n.', 'Alt. of Encheason');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encheason', 'n.', 'Occasion, cause, or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchest', 'v. t.', 'To inclose in a chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchiridion', 'n.', 'Handbook; a manual of devotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchisel', 'v. t.', 'To cut with a chisel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchodus', 'n.', 'A genus of extinct Cretaceous fishes; -- so named from\n   their spear-shaped teeth. They were allied to the pike (Esox).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchondroma', 'n.', 'A cartilaginous tumor growing from the interior of a\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchorial', 'a.', 'Alt. of Enchoric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchoric', 'a.', 'Belonging to, or used in, a country; native; domestic;\n   popular; common; -- said especially of the written characters employed\n   by the common people of ancient Egypt, in distinction from the\n   hieroglyphics. See Demotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchylemma', 'n.', 'The basal substance of the cell nucleus; a hyaline or\n   granular substance, more or less fluid during life, in which the other\n   parts of the nucleus are imbedded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enchyma', 'n.', 'The primitive formative juice, from which the tissues,\n   particularly the cellular tissue, are formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encincture', 'n.', 'A cincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encindered', 'a.', 'Burnt to cinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encircled', 'imp. & p. p.', 'of Encircle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encircling', 'p. pr. & vb. n.', 'of Encircle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encircle', 'v. t.', 'To form a circle about; to inclose within a circle or\n   ring; to surround; as, to encircle one in the arms; the army encircled\n   the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encirclet', 'n.', 'A small circle; a ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclasp', 'v. t.', 'To clasp. See Inclasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclave', 'n.', 'A tract of land or a territory inclosed within another\n   territory of which it is independent. See Exclave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclave', 'v. t.', 'To inclose within an alien territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclavement', 'n.', 'The state of being an enclave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclitic', 'v. i.', 'Alt. of Enclitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclitical', 'v. i.', 'Affixed; subjoined; -- said of a word or particle\n   which leans back upon the preceding word so as to become a part of it,\n   and to lose its own independent accent, generally varying also the\n   accent of the preceding word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclitic', 'n.', 'A word which is joined to another so closely as to lose\n   its proper accent, as the pronoun thee in prithee (pray thee).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclitically', 'adv.', 'In an enclitic manner; by throwing the accent back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclitics', 'n.', 'The art of declining and conjugating words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encloister', 'v. t.', 'To shut up in a cloister; to cloister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclose', 'v. t.', 'To inclose. See Inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclosure', 'n.', 'Inclosure. See Inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enclothe', 'v. t.', 'To clothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encloud', 'v. t.', 'To envelop in clouds; to cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encoach', 'v. t.', 'To carry in a coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encoffin', 'v. t.', 'To put in a coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encolden', 'v. t.', 'To render cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encollar', 'v. t.', 'To furnish or surround with a collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encolor', 'v. t.', 'To color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encolure', 'n.', 'The neck of horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomber', 'v. t.', 'See Encumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomberment', 'n.', 'Hindrance; molestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomiast', 'n.', 'One who praises; a panegyrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomiastic', 'a.', 'Alt. of Encomiastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomiastical', 'a.', 'Bestowing praise; praising; eulogistic; laudatory;\n   as, an encomiastic address or discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomiastic', 'n.', 'A panegyric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomion', 'n.', 'Encomium; panegyric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomiums', 'pl. ', 'of Encomium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encomium', 'n.', 'Warm or high praise; panegyric; strong commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encompassed', 'imp. & p. p.', 'of Encompass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encompassing', 'p. pr. & vb. n.', 'of Encompass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encompass', 'v. t.', 'To circumscribe or go round so as to surround\n   closely; to encircle; to inclose; to environ; as, a ring encompasses\n   the finger; an army encompasses a city; a voyage encompassing the\n   world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encompassment', 'n.', 'The act of surrounding, or the state of being\n   surrounded; circumvention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encore', 'adv. / interj.', 'Once more; again; -- used by the auditors and\n   spectators of plays, concerts, and other entertainments, to call for a\n   repetition of a particular part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encore', 'n.', 'A call or demand (as, by continued applause) for a\n   repetition; as, the encores were numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encored', 'imp. & p. p.', 'of Encore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encoring', 'p. pr. & vb. n.', 'of Encore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encore', 'v. t.', 'To call for a repetition or reappearance of; as, to\n   encore a song or a singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encorporing', 'n.', 'Incorporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encoubert', 'n.', 'One of several species of armadillos of the genera\n   Dasypus and Euphractus, having five toes both on the fore and hind\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encountered', 'imp. & p. p.', 'of Encounter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encountering', 'p. pr. & vb. n.', 'of Encounter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encounter', 'adv.', 'To come against face to face; to meet; to confront,\n   either by chance, suddenly, or deliberately; especially, to meet in\n   opposition or with hostile intent; to engage in conflict with; to\n   oppose; to struggle with; as, to encounter a friend in traveling; two\n   armies encounter each other; to encounter obstacles or difficulties, to\n   encounter strong evidence of a truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encounter', 'v. i.', 'To meet face to face; to have a meeting; to meet,\n   esp. as enemies; to engage in combat; to fight; as, three armies\n   encountered at Waterloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encounter', 'v. t.', 'A meeting face to face; a running against; a sudden\n   or incidental meeting; an interview.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encounter', 'v. t.', 'A meeting, with hostile purpose; hence, a combat; a\n   battle; as, a bloody encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encounterer', 'n.', 'One who encounters; an opponent; an antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encouraged', 'imp. & p. p.', 'of Encourage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encouraging', 'p. pr. & vb. n.', 'of Encourage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encourage', 'v. t.', 'To give courage to; to inspire with courage, spirit,\n   or hope; to raise, or to increase, the confidence of; to animate;\n   enhearten; to incite; to help forward; -- the opposite of discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encouragement', 'n.', 'The act of encouraging; incitement to action or to\n   practice; as, the encouragement of youth in generosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encouragement', 'n.', 'That which serves to incite, support, promote, or\n   advance, as favor, countenance, reward, etc.; incentive; increase of\n   confidence; as, the fine arts find little encouragement among a rude\n   people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encourager', 'n.', 'One who encourages, incites, or helps forward; a\n   favorer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encouraging', 'a.', 'Furnishing ground to hope; inspiriting; favoring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encowl', 'v. t.', 'To make a monk (or wearer of a cowl) of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encradle', 'v. t.', 'To lay in a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encratite', 'n.', 'One of a sect in the 2d century who abstained from\n   marriage, wine, and animal food; -- called also Continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrease', 'v. t. &', 'i. [Obs.] See Increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrimson', 'v. t.', 'To give a crimson or red color to; to crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinic', 'a.', 'Alt. of Encrinital');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinal', 'a.', 'Alt. of Encrinital');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinital', 'a.', 'Relating to encrinites; containing encrinites, as\n   certain kinds of limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinite', 'n.', 'A fossil crinoid, esp. one belonging to, or resembling,\n   the genus Encrinus. Sometimes used in a general sense for any crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinitic', 'a.', 'Alt. of Encrinitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinitical', 'a.', 'Pertaining to encrinites; encrinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinoidea', 'n. pl.', 'That order of the Crinoidea which includes most of\n   the living and many fossil forms, having jointed arms around the margin\n   of the oral disk; -- also called Brachiata and Articulata. See Illusts.\n   under Comatula and Crinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrini', 'pl. ', 'of Encrinus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrinus', 'n.', 'A genus of fossil encrinoidea, from the Mesozoic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrisped', 'a.', 'Curled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroached', 'imp. & p. p.', 'of Encroach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroaching', 'p. pr. & vb. n.', 'of Encroach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroach', 'v. i.', 'To enter by gradual steps or by stealth into the\n   possessions or rights of another; to trespass; to intrude; to trench;\n   -- commonly with on or upon; as, to encroach on a neighbor; to encroach\n   on the highway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroach', 'n.', 'Encroachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroacher', 'n.', 'One who by gradual steps enters on, and takes\n   possession of, what is not his own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroachingly', 'adv.', 'By way of encroachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroachment', 'n.', 'The act of entering gradually or silently upon the\n   rights or possessions of another; unlawful intrusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroachment', 'n.', 'That which is taken by encroaching on another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encroachment', 'n.', 'An unlawful diminution of the possessions of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrust', 'v. t.', 'To incrust. See Incrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encrustment', 'n.', 'That which is formed as a crust; incrustment;\n   incrustation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumbered', 'imp. & p. p.', 'of Encumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumbering', 'p. pr. & vb. n.', 'of Encumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumber', 'v. t.', 'To impede the motion or action of, as with a burden;\n   to retard with something superfluous; to weigh down; to obstruct or\n   embarrass; as, his movements were encumbered by his mantle; his mind is\n   encumbered with useless learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumber', 'v. t.', 'To load with debts, or other legal claims; as, to\n   encumber an estate with mortgages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumberment', 'n.', 'Encumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumbrance', 'n.', 'That which encumbers; a burden which impedes action,\n   or renders it difficult and laborious; a clog; an impediment. See\n   Incumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumbrance', 'n.', 'Same as Incumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encumbrancer', 'n.', 'Same as Incumbrancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encurtain', 'v. t.', 'To inclose with curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ency', '', 'A noun suffix having much the same meaning as -ence, but more\n   commonly signifying the quality or state; as, emergency, efficiency.\n   See -ancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclic', 'a.', 'Alt. of Encyclical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclical', 'a.', 'Sent to many persons or places; intended for many, or\n   for a whole order of men; general; circular; as, an encyclical letter\n   of a council, of a bishop, or the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclic', 'n.', 'Alt. of Encyclical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclical', 'n.', 'An encyclical letter, esp. one from a pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedia', 'n.', 'Alt. of Encyclopaedia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopaedia', 'n.', 'The circle of arts and sciences; a comprehensive\n   summary of knowledge, or of a branch of knowledge; esp., a work in\n   which the various branches of science or art are discussed separately,\n   and usually in alphabetical order; a cyclopedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopediacal', 'a.', 'Encyclopedic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedian', 'a.', 'Embracing the whole circle of learning, or a wide\n   range of subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedic', 'a.', 'Alt. of Encyclopedical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedical', 'a.', 'Pertaining to, or of the nature of, an\n   encyclopedia; embracing a wide range of subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedism', 'n.', 'The art of writing or compiling encyclopedias; also,\n   possession of the whole range of knowledge; encyclopedic learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyclopedist', 'n.', 'The compiler of an encyclopedia, or one who assists\n   in such compilation; also, one whose knowledge embraces the whole range\n   of the sciences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encyst', 'v. t.', 'To inclose in a cyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encystation', 'n.', 'Encystment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encysted', 'a.', 'Inclosed in a cyst, or a sac, bladder, or vesicle; as, an\n   encysted tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encystment', 'n.', 'A process which, among some of the lower forms of life,\n   precedes reproduction by budding, fission, spore formation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Encystment', 'n.', 'A process by which many internal parasites, esp. in\n   their larval states, become inclosed within a cyst in the muscles,\n   liver, etc. See Trichina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'The extreme or last point or part of any material thing\n   considered lengthwise (the extremity of breadth being side); hence,\n   extremity, in general; the concluding part; termination; close; limit;\n   as, the end of a field, line, pole, road; the end of a year, of a\n   discourse; put an end to pain; -- opposed to beginning, when used of\n   anything having a first part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'Point beyond which no procession can be made; conclusion;\n   issue; result, whether successful or otherwise; conclusive event;\n   consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'Termination of being; death; destruction; extermination; also,\n   cause of death or destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'The object aimed at in any effort considered as the close and\n   effect of exertion; ppurpose; intention; aim; as, to labor for private\n   or public ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'That which is left; a remnant; a fragment; a scrap; as, odds\n   and ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'n.', 'One of the yarns of the worsted warp in a Brussels carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ended', 'imp. & p. p.', 'of End');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ending', 'p. pr. & vb. n.', 'of End');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'v. t.', 'To bring to an end or conclusion; to finish; to close; to\n   terminate; as, to end a speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'v. t.', 'To form or be at the end of; as, the letter k ends the word\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'v. t.', 'To destroy; to put to death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End', 'v. i.', 'To come to the ultimate point; to be finished; to come to a\n   close; to cease; to terminate; as, a voyage ends; life ends; winter\n   ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endable', 'a.', 'That may be ended; terminable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'End-all', 'n.', 'Complete termination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamaged', 'imp. & p. p.', 'of Endamage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamaging', 'p. pr. & vb. n.', 'of Endamage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamage', 'v. t.', 'To bring loss or damage to; to harm; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamageable', 'a.', 'Capable of being damaged, or injured; damageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamagement', 'n.', 'Damage; injury; harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endamnify', 'v. t.', 'To damnify; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endangered', 'imp. & p. p.', 'of Endanger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endangering', 'p. pr. & vb. n.', 'of Endanger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endanger', 'v. t.', 'To put to hazard; to bring into danger or peril; to\n   expose to loss or injury; as, to endanger life or peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endanger', 'v. t.', 'To incur the hazard of; to risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endangerment', 'n.', 'Hazard; peril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endark', 'v. t.', 'To darken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endaspidean', 'a.', 'Having the anterior scutes extending around the tarsus\n   on the inner side; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endazzle', 'v. t.', 'To dazzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeared', 'imp. & p. p.', 'of Endear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endearing', 'p. pr. & vb. n.', 'of Endear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endear', 'v. t.', 'To make dear or beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endear', 'v. t.', 'To raise the price or cost of; to make costly or\n   expensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endearedly', 'adv.', 'With affection or endearment; dearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endearedness', 'n.', 'State of being endeared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endearing', 'a.', 'Making dear or beloved; causing love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endearment', 'n.', 'The act of endearing or the state of being endeared;\n   also, that which manifests, excites, or increases, affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavored', 'imp. & p. p.', 'of Endeavor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavoring', 'p. pr. & vb. n.', 'of Endeavor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavor', 'v. t.', 'To exert physical or intellectual strength for the\n   attainment of; to use efforts to effect; to strive to achieve or reach;\n   to try; to attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavor', 'v. i.', 'To exert ones self; to work for a certain end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavor', 'n.', 'An exertion of physical or intellectual strength toward\n   the attainment of an object; a systematic or continuous attempt; an\n   effort; a trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavorer', 'n.', 'One who makes an effort or attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeavorment', 'n.', 'Act of endeavoring; endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endecagon', 'n.', 'A plane figure of eleven sides and angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endecagynous', 'a.', 'Having eleven pistils; as, an endecagynous flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endecane', 'n.', 'One of the higher hydrocarbons of the paraffin series,\n   C11H24, found as a constituent of petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endecaphyllous', 'a.', 'Composed of eleven leaflets; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeictic', 'a.', 'Serving to show or exhibit; as, an endeictic dialogue,\n   in the Platonic philosophy, is one which exhibits a specimen of skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endeixis', 'n.', 'An indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemial', 'a.', 'Endemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemic', 'a.', 'Alt. of Endemical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemical', 'a.', 'Peculiar to a district or particular locality, or class\n   of persons; as, an endemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemic', 'n.', 'An endemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemically', 'adv.', 'In an endemic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endemiology', 'n.', 'The science which treats of endemic affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endenization', 'n.', 'The act of naturalizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endenize', 'v. t.', 'To endenizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endenizen', 'v. t.', 'To admit to the privileges of a denizen; to\n   naturalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ender', 'n.', 'One who, or that which, makes an end of something; as, the\n   ender of my life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endermatic', 'a.', 'Endermic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endermic', 'a.', 'Acting through the skin, or by direct application to the\n   skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endermically', 'adv.', 'By the endermic method; as, applied endermically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enderon', 'n.', 'The deep sensitive and vascular layer of the skin and\n   mucous membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endiademed', 'a.', 'Diademed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endiaper', 'v. t.', 'To decorate with a diaper pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endict', 'v. t.', 'See Indict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endictment', 'n.', 'See Indictment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ending', 'n.', 'Termination; concluding part; result; conclusion;\n   destruction; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ending', 'n.', 'The final syllable or letter of a word; the part joined to\n   the stem. See 3d Case, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endite', 'v. t.', 'See Indite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endive', 'n.', 'A composite herb (Cichorium Endivia). Its finely divided\n   and much curled leaves, when blanched, are used for salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endless', 'a.', 'Without end; having no end or conclusion; perpetual;\n   interminable; -- applied to length, and to duration; as, an endless\n   line; endless time; endless bliss; endless praise; endless clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endless', 'a.', 'Infinite; excessive; unlimited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endless', 'a.', 'Without profitable end; fruitless; unsatisfying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endless', 'a.', 'Void of design; objectless; as, an endless pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endlessly', 'adv.', 'In an endless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endlessness', 'n.', 'The quality of being endless; perpetuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endlong', 'adv. & prep.', 'Lengthwise; along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endmost', 'a.', 'Farthest; remotest; at the very end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endo-', '', 'Alt. of End-');
  INSERT INTO words (word, word_type, description) VALUES  (    'End-', '', 'A combining form signifying within; as, endocarp, endogen,\n   endocuneiform, endaspidean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoblast', 'n.', 'Entoblast; endoplast. See Nucleus,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoblastic', 'a.', 'Relating to the endoblast; as, the endoblastic layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocardiac', 'a.', 'Alt. of Endocardial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocardial', 'a.', 'Pertaining to the endocardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocardial', 'a.', 'Seated or generated within the heart; as, endocardial\n   murmurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocarditis', 'n.', 'Inflammation of the endocardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocardium', 'n.', 'The membrane lining the cavities of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocarp', 'n.', 'The inner layer of a ripened or fructified ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endochondral', 'a.', 'Growing or developing within cartilage; -- applied\n   esp. to developing bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endochrome', 'n.', 'The coloring matter within the cells of plants, whether\n   green, red, yellow, or any other color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoctrine', 'v. t.', 'To teach; to indoctrinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endocyst', 'n.', 'The inner layer of the cells of Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoderm', 'n.', 'The inner layer of the skin or integument of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoderm', 'n.', 'The innermost layer of the blastoderm and the structures\n   derived from it; the hypoblast; the entoblast. See Illust. of Ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endodermal', 'a.', 'Alt. of Endodermic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endodermic', 'a.', 'Of or pertaining to the endoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endodermis', 'n.', 'A layer of cells forming a kind of cuticle inside of\n   the proper cortical layer, or surrounding an individual fibrovascular\n   bundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogamous', 'a.', 'Marrying within the same tribe; -- opposed to\n   exogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogamy', 'n.', 'Marriage only within the tribe; a custom restricting a\n   man in his choice of a wife to the tribe to which he belongs; --\n   opposed to exogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogen', 'n.', 'A plant which increases in size by internal growth and\n   elongation at the summit, having the wood in the form of bundles or\n   threads, irregularly distributed throughout the whole diameter, not\n   forming annual layers, and with no distinct pith. The leaves of the\n   endogens have, usually, parallel veins, their flowers are mostly in\n   three, or some multiple of three, parts, and their embryos have but a\n   single cotyledon, with the first leaves alternate. The endogens\n   constitute one of the great primary classes of plants, and included all\n   palms, true lilies, grasses, rushes, orchids, the banana, pineapple,\n   etc. See Exogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogenesis', 'n.', 'Endogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogenetic', 'a.', 'Endogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogenous', 'a.', 'Increasing by internal growth and elongation at the\n   summit, instead of externally, and having no distinction of pith, wood,\n   and bark, as the rattan, the palm, the cornstalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogenous', 'a.', 'Originating from within; increasing by internal growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogenously', 'adv.', 'By endogenous growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endogeny', 'n.', 'Growth from within; multiplication of cells by endogenous\n   division, as in the development of one or more cells in the interior of\n   a parent cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endognath', 'n.', 'The inner or principal branch of the oral appendages of\n   Crustacea. See Maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endognathal', 'a.', 'Pertaining to the endognath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endolymph', 'n.', 'The watery fluid contained in the membranous labyrinth\n   of the internal ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endolymphangial', 'a.', 'Within a lymphatic vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endolymphatic', 'a.', 'Pertaining to, or containing, endolymph; as, the\n   endolymphatic duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endolymphatic', 'a.', 'Within a lymphatic vessel; endolymphangial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endome', 'v. t.', 'To cover as with a dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endometritis', 'n.', 'Inflammation of the endometrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endometrium', 'n.', 'The membrane lining the inner surface of the uterus,\n   or womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endomorph', 'n.', 'A crystal of one species inclosed within one of another,\n   as one of rutile inclosed in quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endomysium', 'n.', 'The delicate bands of connective tissue interspersed\n   among muscular fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoneurium', 'n.', 'The delicate bands of connective tissue among nerve\n   fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoparasite', 'n.', 'Any parasite which lives in the internal organs of an\n   animal, as the tapeworms, Trichina, etc.; -- opposed to ectoparasite.\n   See Entozoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endophloeum', 'n.', 'The inner layer of the bark of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endophragma', 'n.', 'A chitinous structure above the nervous cord in the\n   thorax of certain Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endophragmal', 'a.', 'Of or pertaining to the endophragma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endophyllous', 'a.', 'Wrapped up within a leaf or sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoplasm', 'n.', 'The protoplasm in the interior of a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoplasma', 'n.', 'Same as Entoplasm and Endosarc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoplast', 'n.', 'See Nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoplastica', 'n. pl.', 'A group of Rhizopoda having a distinct nucleus,\n   as the am/ba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoplastule', 'n.', 'See Nucleolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endopleura', 'n.', 'The inner coating of a seed. See Tegmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endopleurite', 'n.', 'The portion of each apodeme developed from the\n   interepimeral membrane in certain crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endopodite', 'n.', 'The internal or principal branch of the locomotive\n   appendages of Crustacea. See Maxilliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorhizae', 'pl. ', 'of Endorhiza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorhiza', 'n.', 'Any monocotyledonous plant; -- so named because many\n   monocotyledons have an endorhizal embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorhizal', 'a.', 'Alt. of Endorhizous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorhizous', 'a.', 'Having the radicle of the embryo sheathed by the\n   cotyledon, through which the embryo bursts in germination, as in many\n   monocotyledonous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorsed', 'imp. & p. p.', 'of Endorse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorsing', 'p. pr. & vb. n.', 'of Endorse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorse', 'v. t.', 'Same as Indorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorse', 'n.', 'A subordinary, resembling the pale, but of one fourth its\n   width (according to some writers, one eighth).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endorsee', 'n.', 'Same as Indorsee.');
