  INSERT INTO words (word, word_type, description) VALUES  (    'Endosmometric', 'a.', 'Pertaining to, or designed for, the measurement of\n   endosmotic action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endosmose', 'n.', 'Alt. of Endosmosis');
