  INSERT INTO words (word, word_type, description) VALUES  (    'Endospore', 'n.', 'The thin inner coat of certain spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endosporous', 'a.', 'Having the spores contained in a case; -- applied to\n   fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endoss', 'v. t.', 'To put upon the back or outside of anything; -- the\n   older spelling of endorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endosteal', 'a.', 'Relating to endostosis; as, endosteal ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endosternite', 'n.', 'The part of each apodeme derived from the\n   intersternal membrane in Crustacea and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endosteum', 'n.', 'The layer of vascular connective tissue lining the\n   medullary cavities of bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endostoma', 'n.', 'A plate which supports the labrum in certain Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endostome', 'n.', 'The foramen or passage through the inner integument of\n   an ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endostome', 'n.', 'And endostoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endostosis', 'n.', 'A process of bone formation in which ossification takes\n   place within the substance of the cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endostyle', 'n.', 'A fold of the endoderm, which projects into the blood\n   cavity of ascidians. See Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endotheca', 'n.', 'The tissue which partially fills the interior of the\n   interseptal chambers of most madreporarian corals. It usually consists\n   of a series of oblique tranverse septa, one above another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endothecium', 'n.', 'The inner lining of an anther cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endothelial', 'a.', 'Of, or relating to, endothelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endothelia', 'pl. ', 'of Endothelium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endothelium', 'n.', 'The thin epithelium lining the blood vessels,\n   lymphatics, and serous cavities. See Epithelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endotheloid', 'a.', 'Like endothelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endothorax', 'n.', 'An internal process of the sternal plates in the thorax\n   of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endowed', 'imp. & p. p.', 'of Endow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endowing', 'p. pr. & vb. n.', 'of Endow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endow', 'v. t.', 'To furnish with money or its equivalent, as a permanent\n   fund for support; to make pecuniary provision for; to settle an income\n   upon; especially, to furnish with dower; as, to endow a wife; to endow\n   a public institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endow', 'v. t.', 'To enrich or furnish with anything of the nature of a\n   gift (as a quality or faculty); -- followed by with, rarely by of; as,\n   man is endowed by his Maker with reason; to endow with privileges or\n   benefits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endower', 'v. t.', 'To endow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endower', 'n.', 'One who endows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endowment', 'n.', 'The act of bestowing a dower, fund, or permanent\n   provision for support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endowment', 'n.', 'That which is bestowed or settled on a person or an\n   institution; property, fund, or revenue permanently appropriated to any\n   object; as, the endowment of a church, a hospital, or a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endowment', 'n.', 'That which is given or bestowed upon the person or mind;\n   gift of nature; accomplishment; natural capacity; talents; -- usually\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endozoa', 'n. pl.', 'See Entozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endrudge', 'v. t.', 'To make a drudge or slave of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endued', 'imp. & p. p.', 'of Endue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enduing', 'p. pr. & vb. n.', 'of Endue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endue', 'v. t.', 'To invest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endue', 'v. t.', 'An older spelling of Endow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enduement', 'n.', 'Act of enduing; induement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurable', 'a.', 'Capable of being endured or borne; sufferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurably', 'adv.', 'In an endurable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurance', 'n.', 'A state or quality of lasting or duration; lastingness;\n   continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurance', 'n.', 'The act of bearing or suffering; a continuing under pain\n   or distress without resistance, or without being overcome; sufferance;\n   patience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurant', 'a.', 'Capable of enduring fatigue, pain, hunger, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endured', 'imp. & p. p.', 'of Endure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enduring', 'p. pr. & vb. n.', 'of Endure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endure', 'v. i.', 'To continue in the same state without perishing; to\n   last; to remain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endure', 'v. i.', 'To remain firm, as under trial or suffering; to suffer\n   patiently or without yielding; to bear up under adversity; to hold out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endure', 'v. t.', 'To remain firm under; to sustain; to undergo; to support\n   without breaking or yielding; as, metals endure a certain degree of\n   heat without melting; to endure wind and weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endure', 'v. t.', 'To bear with patience; to suffer without opposition or\n   without sinking under the pressure or affliction; to bear up under; to\n   put up with; to tolerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endure', 'v. t.', 'To harden; to toughen; to make hardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurement', 'n.', 'Endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endurer', 'n.', 'One who, or that which, endures or lasts; one who bears,\n   suffers, or sustains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enduring', 'a.', 'Lasting; durable; long-suffering; as, an enduring\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endways', 'adv.', 'Alt. of Endwise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endwise', 'adv.', 'On end; erectly; in an upright position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endwise', 'adv.', 'With the end forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endyma', 'n.', 'See Ependyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endyses', 'pl. ', 'of Endysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Endysis', 'n.', 'The act of developing a new coat of hair, a new set of\n   feathers, scales, etc.; -- opposed to ecdysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enecate', 'v. t.', 'To kill off; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eneid', 'n.', 'Same as Aeneid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enemata', 'pl. ', 'of Enema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enema', 'n.', 'An injection, or clyster, thrown into the rectum as a\n   medicine, or to impart nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enemies', 'pl. ', 'of Enemy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enemy', 'n.', 'One hostile to another; one who hates, and desires or\n   attempts the injury of, another; a foe; an adversary; as, an enemy of\n   or to a person; an enemy to truth, or to falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enemy', 'a.', 'Hostile; inimical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enepidermic', 'a.', 'Applied to the skin without friction; -- said of\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energetic', 'a.', 'Alt. of Energetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energetical', 'a.', 'Having energy or energies; possessing a capacity for\n   vigorous action or for exerting force; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energetical', 'a.', 'Exhibiting energy; operating with force, vigor, and\n   effect; forcible; powerful; efficacious; as, energetic measures;\n   energetic laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energetics', 'n.', 'That branch of science which treats of the laws\n   governing the physical or mechanical, in distinction from the vital,\n   forces, and which comprehends the consideration and general\n   investigation of the whole range of the forces concerned in physical\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energic', 'a.', 'Alt. of Energical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energical', 'a.', 'In a state of action; acting; operating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energical', 'a.', 'Having energy or great power; energetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energized', 'imp. & p. p.', 'of Energize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energizing', 'p. pr. & vb. n.', 'of Energize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energize', 'v. i.', 'To use strength in action; to act or operate with\n   force or vigor; to act in producing an effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energize', 'v. t.', 'To give strength or force to; to make active; to\n   alacrify; as, to energize the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energizer', 'n.', 'One who, or that which, gives energy, or acts in\n   producing an effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energizing', 'a.', 'Capable of imparting or exercising energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energumen', 'n.', 'One possessed by an evil spirit; a demoniac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energies', 'pl. ', 'of Energy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energy', 'n.', 'Internal or inherent power; capacity of acting, operating,\n   or producing an effect, whether exerted or not; as, men possessing\n   energies may suffer them to lie inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energy', 'n.', 'Power efficiently and forcibly exerted; vigorous or\n   effectual operation; as, the energy of a magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energy', 'n.', 'Strength of expression; force of utterance; power to\n   impress the mind and arouse the feelings; life; spirit; -- said of\n   speech, language, words, style; as, a style full of energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Energy', 'n.', 'Capacity for performing work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervated', 'imp. & p. p.', 'of Enervate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervating', 'p. pr. & vb. n.', 'of Enervate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervate', 'v. t.', 'To deprive of nerve, force, strength, or courage; to\n   render feeble or impotent; to make effeminate; to impair the moral\n   powers of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervate', 'a.', 'Weakened; weak; without strength of force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervation', 'n.', 'The act of weakening, or reducing strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervation', 'n.', 'The state of being weakened; effeminacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervative', 'a.', 'Having power, or a tendency, to enervate; weakening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enerve', 'v. t.', 'To weaken; to enervate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enervous', 'a.', 'Lacking nerve or force; enervated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfamish', 'v. t.', 'To famish; to starve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfect', 'a.', 'Contaminated with illegality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeebled', 'imp. & p. p.', 'of Enfeeble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeebling', 'p. pr. & vb. n.', 'of Enfeeble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeeble', 'v. t.', 'To make feeble; to deprive of strength; to reduce the\n   strength or force of; to weaken; to debilitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeeblement', 'n.', 'The act of weakening; enervation; weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeebler', 'n.', 'One who, or that which, weakens or makes feeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeeblish', 'v. i.', 'To enfeeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeloned', 'a.', 'Rendered fierce or frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoffed', 'imp. & p. p.', 'of Enfeoff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoffing', 'p. pr. & vb. n.', 'of Enfeoff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoff', 'v. t.', 'To give a feud, or right in land, to; to invest with a\n   fief or fee; to invest (any one) with a freehold estate by the process\n   of feoffment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoff', 'v. t.', 'To give in vassalage; to make subservient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoffment', 'n.', 'The act of enfeoffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfeoffment', 'n.', 'The instrument or deed by which one is invested with\n   the fee of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfester', 'v. t.', 'To fester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfetter', 'v. t.', 'To bind in fetters; to enchain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfever', 'v. t.', 'To excite fever in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfierced', 'imp. & p. p.', 'of Enfierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfiercing', 'p. pr. & vb. n.', 'of Enfierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfierce', 'v. t.', 'To make fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfilade', 'n.', 'A line or straight passage, or the position of that which\n   lies in a straight line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfilade', 'n.', 'A firing in the direction of the length of a trench, or a\n   line of parapet or troops, etc.; a raking fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfiladed', 'imp. & p. p.', 'of Enfilade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfilading', 'p. pr. & vb. n.', 'of Enfilade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfilade', 'v. t.', 'To pierce, scour, or rake with shot in the direction\n   of the length of, as a work, or a line of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfiled', 'p. a.', 'Having some object, as the head of a man or beast,\n   impaled upon it; as, a sword which is said to be \"enfiled of\" the thing\n   which it pierces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfire', 'v. t.', 'To set on fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enflesh', 'v. t.', 'To clothe with flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enflowered', 'imp. & p. p.', 'of Enflower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enflowering', 'p. pr. & vb. n.', 'of Enflower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enflower', 'v. t.', 'To cover or deck with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfold', 'v. t.', 'To infold. See Infold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfoldment', 'n.', 'The act of infolding. See Infoldment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforced', 'imp. & p. p.', 'of Enforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcing', 'p. pr. & vb. n.', 'of Enforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To put force upon; to force; to constrain; to compel;\n   as, to enforce obedience to commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To make or gain by force; to obtain by force; as, to\n   enforce a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To put in motion or action by violence; to drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To give force to; to strengthen; to invigorate; to urge\n   with energy; as, to enforce arguments or requests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To put in force; to cause to take effect; to give\n   effect to; to execute with vigor; as, to enforce the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. t.', 'To urge; to ply hard; to lay much stress upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. i.', 'To attempt by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. i.', 'To prove; to evince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'v. i.', 'To strengthen; to grow strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforce', 'n.', 'Force; strength; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforceable', 'a.', 'Capable of being enforced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforced', 'a.', 'Compelled; forced; not voluntary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcement', 'n.', 'The act of enforcing; compulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcement', 'n.', 'A giving force to; a putting in execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcement', 'n.', 'That which enforces, constraints, gives force,\n   authority, or effect to; constraint; force applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcer', 'n.', 'One who enforces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcible', 'a.', 'That may be enforced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforcive', 'a.', 'Serving to enforce or constrain; compulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enforest', 'v. t.', 'To turn into a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enform', 'v. t.', 'To form; to fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfouldred', 'a.', 'Mixed with, or emitting, lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enframe', 'v. t.', 'To inclose, as in a frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchised', 'imp. & p. p.', 'of Enfranchise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchising', 'p. pr. & vb. n.', 'of Enfranchise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchise', 'v. t.', 'To set free; to liberate from slavery, prison, or\n   any binding power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchise', 'v. t.', 'To endow with a franchise; to incorporate into a\n   body politic and thus to invest with civil and political privileges; to\n   admit to the privileges of a freeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchise', 'v. t.', 'To receive as denizens; to naturalize; as, to\n   enfranchise foreign words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchisement', 'n.', 'Releasing from slavery or custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchisement', 'n.', 'Admission to the freedom of a corporation or body\n   politic; investiture with the privileges of free citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfranchiser', 'n.', 'One who enfranchises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfree', 'v. t.', 'To set free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfreedom', 'v. t.', 'To set free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfreeze', 'v. t.', 'To freeze; to congeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enfroward', 'v. t.', 'To make froward, perverse, or ungovernable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaged', 'imp. & p. p.', 'of Engage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaging', 'p. pr. & vb. n.', 'of Engage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To put under pledge; to pledge; to place under\n   obligations to do or forbear doing something, as by a pledge, oath, or\n   promise; to bind by contract or promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To gain for service; to bring in as associate or aid; to\n   enlist; as, to engage friends to aid in a cause; to engage men for\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To gain over; to win and attach; to attract and hold; to\n   draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To employ the attention and efforts of; to occupy; to\n   engross; to draw on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To enter into contest with; to encounter; to bring to\n   conflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. t.', 'To come into gear with; as, the teeth of one cogwheel\n   engage those of another, or one part of a clutch engages the other\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. i.', 'To promise or pledge ones self; to enter into an\n   obligation; to become bound; to warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. i.', 'To embark in a business; to take a part; to employ or\n   involve ones self; to devote attention and effort; to enlist; as, to\n   engage in controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. i.', 'To enter into conflict; to join battle; as, the armies\n   engaged in a general battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engage', 'v. i.', 'To be in gear, as two cogwheels working together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaged', 'a.', 'Occupied; employed; busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaged', 'a.', 'Pledged; promised; especially, having the affections\n   pledged; promised in marriage; affianced; betrothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaged', 'a.', 'Greatly interested; of awakened zeal; earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Engaged', 'a.', 'Involved; esp., involved in a hostile encounter; as, the\n   engaged ships continued the fight.');
