  INSERT INTO words (word, word_type, description) VALUES  (    'Enharmonical', 'a.', 'Pertaining to a scale of perfect intonation which\n   recognizes all the notes and intervals that result from the exact\n   tuning of diatonic scales and their transposition into other keys.');
