  INSERT INTO words (word, word_type, description) VALUES  (    'Enigma', 'n.', 'A dark, obscure, or inexplicable saying; a riddle; a\n   statement, the hidden meaning of which is to be discovered or guessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigma', 'n.', 'An action, mode of action, or thing, which cannot be\n   satisfactorily explained; a puzzle; as, his conduct is an enigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatic', 'a.', 'Alt. of Enigmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatical', 'a.', 'Relating to or resembling an enigma; not easily\n   explained or accounted for; darkly expressed; obscure; puzzling; as, an\n   enigmatical answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatically', 'adv.', 'Darkly; obscurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatist', 'n.', 'One who makes, or talks in, enigmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatized', 'imp. & p. p.', 'of Enigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatizing', 'p. pr. & vb. n.', 'of Enigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatize', 'v. i.', 'To make, or talk in, enigmas; to deal in riddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatography', 'n.', 'Alt. of Enigmatology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enigmatology', 'n.', 'The art of making or of solving enigmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enisled', 'p. a.', 'Placed alone or apart, as if on an island; severed, as\n   an island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjailed', 'imp. & p. p.', 'of Enjall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjailing', 'p. pr. & vb. n.', 'of Enjall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjall', 'v. t.', 'To put into jail; to imprison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoined', 'imp. & p. p.', 'of Enjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoining', 'p. pr. & vb. n.', 'of Enjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoin', 'v. t.', 'To lay upon, as an order or command; to give an\n   injunction to; to direct with authority; to order; to charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoin', 'v. t.', 'To prohibit or restrain by a judicial order or decree;\n   to put an injunction on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoin', 'v. t.', 'To join or unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoiner', 'n.', 'One who enjoins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoinment', 'n.', 'Direction; command; authoritative admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoyed', 'imp. & p. p.', 'of Enjoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoying', 'p. pr. & vb. n.', 'of Enjoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoy', 'v. t.', 'To take pleasure or satisfaction in the possession or\n   experience of; to feel or perceive with pleasure; to be delighted with;\n   as, to enjoy the dainties of a feast; to enjoy conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoy', 'v. t.', 'To have, possess, and use with satisfaction; to occupy or\n   have the benefit of, as a good or profitable thing, or as something\n   desirable; as, to enjoy a free constitution and religious liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoy', 'v. t.', 'To have sexual intercourse with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoy', 'v. i.', 'To take satisfaction; to live in happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoyable', 'a.', 'Capable of being enjoyed or of giving joy; yielding\n   enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoyer', 'n.', 'One who enjoys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoyment', 'n.', 'The condition of enjoying anything; pleasure or\n   satisfaction, as in the possession or occupancy of anything; possession\n   and use; as, the enjoyment of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enjoyment', 'n.', 'That which gives pleasure or keen satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkennel', 'v. t.', 'To put into a kennel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkerchiefed', 'a.', 'Bound with a kerchief; draped; hooded; covered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkindled', 'imp. & p. p.', 'of Enkindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkindling', 'p. pr. & vb. n.', 'of Enkindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkindle', 'v. t.', 'To set on fire; to inflame; to kindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enkindle', 'v. t.', 'To excite; to rouse into action; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlace', 'v. t.', 'To bind or encircle with lace, or as with lace; to lace;\n   to encircle; to enfold; hence, to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlacement', 'n.', 'The act of enlacing, or state of being enlaced; a\n   surrounding as with a lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlard', 'v. t.', 'To cover or dress with lard or grease; to fatten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarged', 'imp. & p. p.', 'of Enlarge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarging', 'p. pr. & vb. n.', 'of Enlarge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. t.', 'To make larger; to increase in quantity or dimensions;\n   to extend in limits; to magnify; as, the body is enlarged by nutrition;\n   to enlarge ones house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. t.', 'To increase the capacity of; to expand; to give free\n   scope or greater scope to; also, to dilate, as with joy, affection, and\n   the like; as, knowledge enlarges the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. t.', 'To set at large or set free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. i.', 'To grow large or larger; to be further extended; to\n   expand; as, a plant enlarges by growth; an estate enlarges by good\n   management; a volume of air enlarges by rarefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. i.', 'To speak or write at length; to be diffuse in speaking\n   or writing; to expatiate; to dilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarge', 'v. i.', 'To get more astern or parallel with the vessels\n   course; to draw aft; -- said of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarged', 'a.', 'Made large or larger; extended; swollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlargement', 'n.', 'The act of increasing in size or bulk, real or\n   apparent; the state of being increased; augmentation; further\n   extension; expansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlargement', 'n.', 'Expansion or extension, as of the powers of the mind;\n   ennoblement, as of the feelings and character; as, an enlargement of\n   views, of knowledge, of affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlargement', 'n.', 'A setting at large, or being set at large; release\n   from confinement, servitude, or distress; liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlargement', 'n.', 'Diffusiveness of speech or writing; expatiation; a\n   wide range of discourse or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlarger', 'n.', 'One that enlarges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlay', 'v. t.', 'See Inlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlengthen', 'v. t.', 'To lengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enleven', 'n.', 'Eleven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlight', 'v. t.', 'To illumine; to enlighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlighten', 'v. t.', 'To supply with light; to illuminate; as, the sun\n   enlightens the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlighten', 'v. t.', 'To make clear to the intellect or conscience; to shed\n   the light of truth and knowledge upon; to furnish with increase of\n   knowledge; to instruct; as, to enlighten the mind or understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlightener', 'n.', 'One who enlightens or illuminates; one who, or that\n   which, communicates light to the eye, or clear views to the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlightenment', 'n.', 'Act of enlightening, or the state of being\n   enlightened or instructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlimn', 'v. t.', 'To adorn by illuminating or ornamenting with colored and\n   decorated letters and figures, as a book or manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlink', 'v. t.', 'To chain together; to connect, as by links.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlisted', 'imp. & p. p.', 'of Enlist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlisting', 'p. pr. & vb. n.', 'of Enlist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlist', 'v. t.', 'To enter on a list; to enroll; to register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlist', 'v. t.', 'To engage for military or naval service, the name being\n   entered on a list or register; as, to enlist men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlist', 'v. t.', 'To secure the support and aid of; to employ in advancing\n   interest; as, to enlist persons in the cause of truth, or in a\n   charitable enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlist', 'v. i.', 'To enroll and bind ones self for military or naval\n   service; as, he enlisted in the regular army; the men enlisted for the\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlist', 'v. i.', 'To enter heartily into a cause, as if enrolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlistment', 'n.', 'The act or enlisting, or the state of being enlisted;\n   voluntary enrollment to serve as a soldier or a sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlistment', 'n.', 'The writing by which an enlisted man is bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlive', 'v. t.', 'To enliven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlivened', 'imp. & p. p.', 'of Enliven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlivening', 'p. pr. & vb. n.', 'of Enliven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enliven', 'v. t.', 'To give life, action, or motion to; to make vigorous or\n   active; to excite; to quicken; as, fresh fuel enlivens a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enliven', 'v. t.', 'To give spirit or vivacity to; to make sprightly, gay,\n   or cheerful; to animate; as, mirth and good humor enliven a company;\n   enlivening strains of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlivener', 'n.', 'One who, or that which, enlivens, animates, or\n   invigorates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlock', 'v. t.', 'To lock; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlumine', 'v. t.', 'To illumine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enlute', 'v. t.', 'To coat with clay; to lute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmanche', 'a.', 'Resembling, or covered with, a sleeve; -- said of the\n   chief when lines are drawn from the middle point of the upper edge\n   upper edge to the sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmarble', 'v. t.', 'To make hard as marble; to harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmesh', 'v. t.', 'To catch or entangle in, or as in, meshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmew', 'v. t.', 'See Emmew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmist', 'v. t.', 'To infold, as in a mist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmities', 'pl. ', 'of Enmity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmity', 'n.', 'The quality of being an enemy; hostile or unfriendly\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmity', 'n.', 'A state of opposition; hostility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmossed', 'a.', 'Covered with moss; mossed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmove', 'v. t.', 'See Emmove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmuffle', 'v. t.', 'To muffle up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enmure', 'v. t.', 'To immure.');
