  INSERT INTO words (word, word_type, description) VALUES  (    'Ennead', 'n.', 'The number nine or a group of nine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneagon', 'n.', 'A polygon or plane figure with nine sides and nine\n   angles; a nonagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneagonal', 'a.', 'Belonging to an enneagon; having nine angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneagynous', 'a.', 'Having or producing nine pistils or styles; -- said of\n   a flower or plant.');
