  INSERT INTO words (word, word_type, description) VALUES  (    'Enheahedria', 'n.', 'Alt. of Enheahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enheahedron', 'n.', 'A figure having nine sides; a nonagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneandria', 'n.', 'A Linnaean class of plants having nine stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneandrian', 'a.', 'Alt. of Enneandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneandrous', 'a.', 'Having nine stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneapetalous', 'a.', 'Having nine petals, or flower leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneaspermous', 'a.', 'Having nine seeds; -- said of fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneatic', 'a.', 'Alt. of Enneatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enneatical', 'a.', 'Occurring once in every nine times, days, years, etc.;\n   every ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennew', 'v. t.', 'To make new.');
