  INSERT INTO words (word, word_type, description) VALUES  (    'Ennobled', 'imp. & p. p.', 'of Ennoble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennobling', 'p. pr. & vb. n.', 'of Ennoble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennoble', 'v. t.', 'To make noble; to elevate in degree, qualities, or\n   excellence; to dignify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennoble', 'v. t.', 'To raise to the rank of nobility; as, to ennoble a\n   commoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennoblement', 'n.', 'The act of making noble, or of exalting, dignifying,\n   or advancing to nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennoblement', 'n.', 'That which ennobles; excellence; dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennobler', 'n.', 'One who ennobles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennui', 'n.', 'A feeling of weariness and disgust; dullness and languor of\n   spirits, arising from satiety or want of interest; tedium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennuye', 'a.', 'Affected with ennui; weary in spirits; emotionally\n   exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennuye', 'n.', 'One who is affected with ennui.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ennuyee', 'n.', 'A woman affected with ennui.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enodal', 'a.', 'Without a node.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enodation', 'n.', 'The act or operation of clearing of knots, or of\n   untying; hence, also, the solution of a difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enode', 'v. t.', 'To clear of knots; to make clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enoint', 'a.', 'Anointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enomotarch', 'n.', 'The commander of an enomoty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enomoty', 'n.', 'A band of sworn soldiers; a division of the Spartan army\n   ranging from twenty-five to thirty-six men, bound together by oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enopla', 'n. pl.', 'One of the orders of Nemertina, characterized by the\n   presence of a peculiar armature of spines or plates in the proboscis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enoptomancy', 'n.', 'Divination by the use of a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enorm', 'a.', 'Enormous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormities', 'pl. ', 'of Enormity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormity', 'n.', 'The state or quality of exceeding a measure or rule, or\n   of being immoderate, monstrous, or outrageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormity', 'n.', 'That which is enormous; especially, an exceeding offense\n   against order, right, or decency; an atrocious crime; flagitious\n   villainy; an atrocity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormous', 'a.', 'Exceeding the usual rule, norm, or measure; out of due\n   proportion; inordinate; abnormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormous', 'a.', 'Exceedingly wicked; outrageous; atrocious; monstrous; as,\n   an enormous crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormously', 'adv.', 'In an enormous degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enormousness', 'n.', 'The state of being enormous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enorthotrope', 'n.', 'An optical toy; a card on which confused or imperfect\n   figures are drawn, but which form to the eye regular figures when the\n   card is rapidly revolved. See Thaumatrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'a.', 'Satisfying desire; giving content; adequate to meet the\n   want; sufficient; -- usually, and more elegantly, following the noun to\n   which it belongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'adv.', 'In a degree or quantity that satisfies; to satisfaction;\n   sufficiently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'adv.', 'Fully; quite; -- used to express slight augmentation of\n   the positive degree, and sometimes equivalent to very; as, he is ready\n   enough to embrace the offer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'adv.', 'In a tolerable degree; -- used to express mere\n   acceptableness or acquiescence, and implying a degree or quantity\n   rather less than is desired; as, the song was well enough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'n.', 'A sufficiency; a quantity which satisfies desire, is\n   adequate to the want, or is equal to the power or ability; as, he had\n   enough to do take care of himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enough', 'interj.', 'An exclamation denoting sufficiency, being a shortened\n   form of it is enough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enounced', 'imp. & p. p.', 'of Enounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enouncing', 'p. pr. & vb. n.', 'of Enounce');
