  INSERT INTO words (word, word_type, description) VALUES  (    'Enquiry', 'n.', 'See Inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrace', 'v. t.', 'To enroot; to implant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enraged', 'imp. & p. p.', 'of Enrage');
