  INSERT INTO words (word, word_type, description) VALUES  (    'Enragement', 'n.', 'Act of enraging or state of being enraged; excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrange', 'v. t.', 'To range in order; to put in rank; to arrange.');
