  INSERT INTO words (word, word_type, description) VALUES  (    'Enrank', 'v. t.', 'To place in ranks or in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrapt', 'p. a.', 'Thrown into ecstasy; transported; enraptured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enraptured', 'imp. & p. p.', 'of Enrapture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrapturing', 'p. pr. & vb. n.', 'of Enrapture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrapture', 'v. t.', 'To transport with pleasure; to delight beyond\n   measure; to enravish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enravish', 'v. t.', 'To transport with delight; to enrapture; to fascinate.');
