  INSERT INTO words (word, word_type, description) VALUES  (    'Enriching', 'p. pr. & vb. n.', 'of Enrich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrich', 'v. t.', 'To make rich with any kind of wealth; to render opulent;\n   to increase the possessions of; as, to enrich the understanding with\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrich', 'v. t.', 'To supply with ornament; to adorn; as, to enrich a\n   ceiling by frescoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrich', 'v. t.', 'To make rich with manure; to fertilize; -- said of the\n   soil; as, to enrich land by irrigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrich', 'v. t.', 'To supply with knowledge; to instruct; to store; -- said\n   of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enricher', 'n.', 'One who enriches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrichment', 'n.', 'The act of making rich, or that which enriches;\n   increase of value by improvements, embellishment, etc.; decoration;\n   embellishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enridge', 'v. t.', 'To form into ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enring', 'v. t.', 'To encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enripen', 'v. t.', 'To ripen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrive', 'v. t.', 'To rive; to cleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrobe', 'v. t.', 'To invest or adorn with a robe; to attire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrockment', 'n.', 'A mass of large stones thrown into water at random to\n   form bases of piers, breakwaters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrolled', 'imp. & p. p.', 'of Enroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrolling', 'p. pr. & vb. n.', 'of Enroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enroll', 'n.', 'To insert in a roil; to register or enter in a list or\n   catalogue or on rolls of court; hence, to record; to insert in records;\n   to leave in writing; as, to enroll men for service; to enroll a decree\n   or a law; also, reflexively, to enlist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enroll', 'n.', 'To envelop; to inwrap; to involve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enroller', 'n.', 'One who enrolls or registers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrollment', 'n.', 'The act of enrolling; registration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enrollment', 'n.', 'A writing in which anything is enrolled; a register; a\n   record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enroot', 'v. t.', 'To fix by the root; to fix fast; to implant deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enround', 'v. t.', 'To surround.');
  INSERT INTO words (word, word_type, description) VALUES  (    'En route', '', 'On the way or road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ens', 'n.', 'Entity, being, or existence; an actually existing being; also,\n   God, as the Being of Beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ens', 'n.', 'Something supposed to condense within itself all the virtues\n   and qualities of a substance from which it is extracted; essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensafe', 'v. t.', 'To make safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensample', 'n.', 'An example; a pattern or model for imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensample', 'v. t.', 'To exemplify, to show by example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensanguine', 'v. t.', 'To stain or cover with blood; to make bloody, or of\n   a blood-red color; as, an ensanguined hue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensate', 'a.', 'Having sword-shaped leaves, or appendages; ensiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enscale', 'v. t.', 'To cover with scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshedule', 'v. t.', 'To insert in a schedule. See Schedule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensconced', 'imp. & p. p.', 'of Ensconce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensconcing', 'imp. & p. p.', 'of Ensconce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensconce', 'v. t.', 'To cover or shelter, as with a sconce or fort; to\n   place or hide securely; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enseal', 'v. t.', 'To impress with a seal; to mark as with a seal; hence,\n   to ratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enseam', 'v. t.', 'To sew up; to inclose by a seam; hence, to include; to\n   contain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enseam', 'v. t.', 'To cover with grease; to defile; to pollute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensear', 'v. t.', 'To sear; to dry up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensearch', 'v. i.', 'To make search; to try to find something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensearch', 'v. t. ', 'To search for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enseel', 'v. t.', 'To close eyes of; to seel; -- said in reference to a\n   hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enseint', 'a.', 'With child; pregnant. See Enceinte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensemble', 'n.', 'The whole; all the parts taken together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensemble', 'adv.', 'All at once; together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshelter', 'v. t.', 'To shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshield', 'v. t.', 'To defend, as with a shield; to shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshield', 'a.', 'Shielded; enshielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshrined', 'imp. & p. p.', 'of Enshrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshrining', 'p. pr. & vb. n.', 'of Enshrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshrine', 'v. t.', 'To inclose in a shrine or chest; hence, to preserve or\n   cherish as something sacred; as, to enshrine something in memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enshroud', 'v. t.', 'To cover with, or as with, a shroud; to shroud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensiferous', 'a.', 'Bearing a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensiform', 'a.', 'Having the form of a sword blade; sword-shaped; as, an\n   ensiform leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'n.', 'A flag; a banner; a standard; esp., the national flag, or a\n   banner indicating nationality, carried by a ship or a body of soldiers;\n   -- as distinguished from flags indicating divisions of the army, rank\n   of naval officers, or private signals, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'n.', 'A signal displayed like a standard, to give notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'n.', 'Sign; badge of office, rank, or power; symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'n.', 'Formerly, a commissioned officer of the army who carried\n   the ensign or flag of a company or regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'n.', 'A commissioned officer of the lowest grade in the navy,\n   corresponding to the grade of second lieutenant in the army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'v. t.', 'To designate as by an ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensign', 'v. t.', 'To distinguish by a mark or ornament; esp. (Her.), by a\n   crown; thus, any charge which has a crown immediately above or upon it,\n   is said to be ensigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensigncies', 'pl. ', 'of Ensigncy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensigncy', 'n.', 'The rank or office of an ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensignship', 'n.', 'The state or rank of an ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensilage', 'n.', 'The process of preserving fodder (such as cornstalks,\n   rye, oats, millet, etc.) by compressing it while green and fresh in a\n   pit or vat called a silo, where it is kept covered from the air; as the\n   ensilage of fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensilage', 'n.', 'The fodder preserved in a silo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensilaged', 'imp. & p. p.', 'of Ensilage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensilaging', 'p. pr. & vb. n.', 'of Ensilage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensilage', 'v. t.', 'To preserve in a silo; as, to ensilage cornstalks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensky', 'v. t.', 'To place in the sky or in heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslaved', 'imp. & p. p.', 'of Enslave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslaving', 'p. pr. & vb. n.', 'of Enslave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslave', 'v. t.', 'To reduce to slavery; to make a slave of; to subject to\n   a dominant influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslavedness', 'n.', 'State of being enslaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslavement', 'n.', 'The act of reducing to slavery; state of being\n   enslaved; bondage; servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enslaver', 'n.', 'One who enslaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensnare', 'v. t.', 'To catch in a snare. See Insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensnarl', 'v. t.', 'To entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensober', 'v. t.', 'To make sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensoul', 'v. t.', 'To indue or imbue (a body) with soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensphere', 'v. t.', 'To place in a sphere; to envelop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensphere', 'v. t.', 'To form into a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstamp', 'v. t.', 'To stamp; to mark as /ith a stamp; to impress deeply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstate', 'v. t.', 'See Instate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstatite', 'n.', 'A mineral of the pyroxene group, orthorhombic in\n   crystallization; often fibrous and massive; color grayish white or\n   greenish. It is a silicate of magnesia with some iron. Bronzite is a\n   ferriferous variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstatitic', 'a.', 'Relating to enstatite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstore', 'v. t.', 'To restore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enstyle', 'v. t.', 'To style; to name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensuable', 'a.', 'Ensuing; following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensued', 'imp. & p. p.', 'of Ensue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensuing', 'p. pr. & vb. n.', 'of Ensue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensue', 'v. t.', 'To follow; to pursue; to follow and overtake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensue', 'v. i.', 'To follow or come afterward; to follow as a consequence\n   or in chronological succession; to result; as, an ensuing conclusion or\n   effect; the year ensuing was a cold one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensure', 'v. t.', 'To make sure. See Insure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensure', 'v. t.', 'To betroth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensurer', 'n.', 'See Insurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enswathe', 'v. t.', 'To swathe; to envelop, as in swaddling clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enswathement', 'n.', 'The act of enswathing, or the state of being\n   enswathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ensweep', 'v. t.', 'To sweep over or across; to pass over rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ent-', '', 'A prefix signifying within. See Ento-.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ent', '', 'An adjective suffix signifying action or being; as, corrodent,\n   excellent, emergent, continent, quiescent. See -ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entablature', 'n.', 'The superstructure which lies horizontally upon the\n   columns. See Illust. of Column, Cornice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entablement', 'n.', 'See Entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entackle', 'v. t.', 'To supply with tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entad', 'adv.', 'Toward the inside or central part; away from the surface;\n   -- opposed to ectad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'That which is entailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'An estate in fee entailed, or limited in descent to a\n   particular class of issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'The rule by which the descent is fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'Delicately carved ornamental work; intaglio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entailed', 'imp. & p. p.', 'of Entail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entailing', 'p. pr. & vb. n.', 'of Entail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'To settle or fix inalienably on a person or thing, or on a\n   person and his descendants or a certain line of descendants; -- said\n   especially of an estate; to bestow as an heritage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'To appoint hereditary possessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entail', 'n.', 'To cut or carve in a ornamental way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entailment', 'n.', 'The act of entailing or of giving, as an estate, and\n   directing the mode of descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entailment', 'n.', 'The condition of being entailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entailment', 'n.', 'A thing entailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ental', 'a.', 'Pertaining to, or situated near, central or deep parts;\n   inner; -- opposed to ectal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entame', 'v. t.', 'To tame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entangled', 'imp. & p. p.', 'of Entangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entangling', 'p. pr. & vb. n.', 'of Entangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entangle', 'v. t.', 'To twist or interweave in such a manner as not to be\n   easily separated; to make tangled, confused, and intricate; as, to\n   entangle yarn or the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entangle', 'v. t.', 'To involve in such complications as to render\n   extrication a bewildering difficulty; hence, metaphorically, to\n   insnare; to perplex; to bewilder; to puzzle; as, to entangle the feet\n   in a net, or in briers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entanglement', 'n.', 'State of being entangled; intricate and confused\n   involution; that which entangles; intricacy; perplexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entangler', 'n.', 'One that entangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entasia', 'n.', 'Tonic spasm; -- applied generically to denote any disease\n   characterized by tonic spasms, as tetanus, trismus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entasis', 'n.', 'A slight convex swelling of the shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entasis', 'n.', 'Same as Entasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entassment', 'n.', 'A heap; accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entastic', 'a.', 'Relating to any disease characterized by tonic spasms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entelechy', 'n.', 'An actuality; a conception completely actualized, in\n   distinction from mere potential existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entellus', 'n.', 'An East Indian long-tailed bearded monkey (Semnopithecus\n   entellus) regarded as sacred by the natives. It is remarkable for the\n   caplike arrangement of the hair on the head. Called also hoonoomaun and\n   hungoor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entend', 'v. i.', 'To attend to; to apply ones self to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entender', 'v. t.', 'To make tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entender', 'v. t.', 'To treat with tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ententive', 'a.', 'Attentive; zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter-', '', 'A prefix signifying between, among, part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entered', 'imp. & p. p.', 'of Enter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entering', 'p. pr. & vb. n.', 'of Enter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To come or go into; to pass into the interior of; to pass\n   within the outer cover or shell of; to penetrate; to pierce; as, to\n   enter a house, a closet, a country, a door, etc.; the river enters the\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To unite in; to join; to be admitted to; to become a\n   member of; as, to enter an association, a college, an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To engage in; to become occupied with; as, to enter the\n   legal profession, the book trade, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To pass within the limits of; to attain; to begin; to\n   commence upon; as, to enter ones teens, a new era, a new dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To cause to go (into), or to be received (into); to put\n   in; to insert; to cause to be admitted; as, to enter a knife into a\n   piece of wood, a wedge into a log; to enter a boy at college, a horse\n   for a race, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To inscribe; to enroll; to record; as, to enter a name,\n   or a date, in a book, or a book in a catalogue; to enter the\n   particulars of a sale in an account, a manifest of a ship or of\n   merchandise at the customhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To go into or upon, as lands, and take actual possession\n   of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To place in regular form before the court, usually in\n   writing; to put upon record in proper from and order; as, to enter a\n   writ, appearance, rule, or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To make report of (a vessel or her cargo) at the\n   customhouse; to submit a statement of (imported goods), with the\n   original invoices, to the proper officer of the customs for estimating\n   the duties. See Entry, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To file or inscribe upon the records of the land office\n   the required particulars concerning (a quantity of public land) in\n   order to entitle a person to a right pf preemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To deposit for copyright the title or description of (a\n   book, picture, map, etc.); as, \"entered according to act of Congress.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. t.', 'To initiate; to introduce favorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. i.', 'To go or come in; -- often with in used pleonastically;\n   also, to begin; to take the first steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. i.', 'To get admission; to introduce ones self; to penetrate;\n   to form or constitute a part; to become a partaker or participant; to\n   share; to engage; -- usually with into; sometimes with on or upon; as,\n   a ball enters into the body; water enters into a ship; he enters into\n   the plan; to enter into a quarrel; a merchant enters into partnership\n   with some one; to enter upon anothers land; the boy enters on his\n   tenth year; to enter upon a task; lead enters into the composition of\n   pewter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enter', 'v. i.', 'To penetrate mentally; to consider attentively; -- with\n   into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteradenography', 'n.', 'A treatise upon, or description of, the\n   intestinal glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteradenology', 'n.', 'The science which treats of the glands of the\n   alimentary canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteralgia', 'n.', 'Pain in the intestines; colic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterdeal', 'n.', 'Mutual dealings; intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterer', 'n.', 'One who makes an entrance or beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteric', 'a.', 'Of or pertaining to the enteron, or alimentary canal;\n   intestinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteritis', 'n.', 'An inflammation of the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterlace', 'v. t.', 'See Interlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entermete', 'v. i.', 'To interfere; to intermeddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entermewer', 'n.', 'A hawk gradually changing the color of its feathers,\n   commonly in the second year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entermise', 'n.', 'Mediation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterocele', 'n.', 'A hernial tumor whose contents are intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterocoele', 'n.', 'A perivisceral cavity which arises as an outgrowth or\n   outgrowths from the digestive tract; distinguished from a schizocoele,\n   which arises by a splitting of the mesoblast of the embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterography', 'n.', 'A treatise upon, or description of, the intestines;\n   enterology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterolith', 'n.', 'An intestinal concretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterology', 'n.', 'The science which treats of the viscera of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteron', 'n.', 'The whole alimentary, or enteric, canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteropathy', 'n.', 'Disease of the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enteropneusta', 'n. pl.', 'A group of wormlike invertebrates having, along\n   the sides of the body, branchial openings for the branchial sacs, which\n   are formed by diverticula of the alimentary canal. Balanoglossus is the\n   only known genus. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterorrhaphy', 'n.', 'The operation of sewing up a rent in the intestinal\n   canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterotome', 'n.', 'A kind of scissors used for opening the intestinal\n   canal, as in post-mortem examinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterotomy', 'n.', 'Incision of the intestines, especially in reducing\n   certain cases of hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterparlance', 'n.', 'Mutual talk or conversation; conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterplead', 'v. i.', 'Same as Interplead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprise', 'n.', 'That which is undertaken; something attempted to be\n   performed; a work projected which involves activity, courage, energy,\n   and the like; a bold, arduous, or hazardous attempt; an undertaking;\n   as, a manly enterprise; a warlike enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprise', 'n.', 'Willingness or eagerness to engage in labor which\n   requires boldness, promptness, energy, and like qualities; as, a man of\n   great enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprise', 'v. t.', 'To undertake; to begin and attempt to perform; to\n   venture upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprise', 'v. t.', 'To treat with hospitality; to entertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprise', 'v. i.', 'To undertake an enterprise, or something hazardous\n   or difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterpriser', 'n.', 'One who undertakes enterprises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enterprising', 'a.', 'Having a disposition for enterprise; characterized by\n   enterprise; resolute, active or prompt to attempt; as, an enterprising\n   man or firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertained', 'imp. & p. p.', 'of Entertain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertaining', 'p. pr. & vb. n.', 'of Entertain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To be at the charges of; to take or keep in ones\n   service; to maintain; to support; to harbor; to keep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To give hospitable reception and maintenance to; to\n   receive at ones board, or into ones house; to receive as a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To engage the attention of agreeably; to amuse with\n   that which makes the time pass pleasantly; to divert; as, to entertain\n   friends with conversation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To give reception to; to receive, in general; to\n   receive and take into consideration; to admit, treat, or make use of;\n   as, to entertain a proposal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To meet or encounter, as an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To keep, hold, or maintain in the mind with favor; to\n   keep in the mind; to harbor; to cherish; as, to entertain sentiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. t.', 'To lead on; to bring along; to introduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'v. i.', 'To receive, or provide entertainment for, guests; as,\n   he entertains generously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertain', 'n.', 'Entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertainer', 'n.', 'One who entertains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertaining', 'a.', 'Affording entertainment; pleasing; amusing;\n   diverting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertainment', 'n.', 'The act of receiving as host, or of amusing,\n   admitting, or cherishing; hospitable reception; also, reception or\n   treatment, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertainment', 'n.', 'That which entertains, or with which one is\n   entertained; as: (a) Hospitality; hospitable provision for the wants of\n   a guest; especially, provision for the table; a hospitable repast; a\n   feast; a formal or elegant meal. (b) That which engages the attention\n   agreeably, amuses or diverts, whether in private, as by conversation,\n   etc., or in public, by performances of some kind; amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertainment', 'n.', 'Admission into service; service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertainment', 'n.', 'Payment of soldiers or servants; wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertake', 'v. t.', 'To entertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entertissued', 'a.', 'Same as Intertissued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entheal', 'a.', 'Alt. of Enthean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthean', 'a.', 'Divinely inspired; wrought up to enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entheasm', 'n.', 'Inspiration; enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entheastic', 'a.', 'Of godlike energy; inspired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entheat', 'a.', 'Divinely inspired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthelmintha', 'n. pl.', 'Alt. of Enthelminthes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthelminthes', 'n. pl.', 'Intestinal worms. See Helminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entheic', 'a.', 'Caused by a morbifie virus implanted in the system; as, an\n   enthetic disease like syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthrall', 'v. t.', 'To hold in thrall; to enslave. See Inthrall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthrallment', 'n.', 'The act of enthralling, or state of being enthralled.\n   See Inthrallment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthrill', 'v. t.', 'To pierce; to thrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthrone', 'v. t.', 'To seat on a throne; to exalt to the seat of royalty\n   or of high authority; hence, to invest with sovereign authority or\n   dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthrone', 'v. t.', 'To induct, as a bishop, into the powers and privileges\n   of a vacant see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthronement', 'n.', 'The act of enthroning, or state of being enthroned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthronization', 'n.', 'The act of enthroning; hence, the admission of a\n   bishop to his stall or throne in his cathedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthronized', 'imp. & p. p.', 'of Enthronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthronizing', 'p. pr. & vb. n.', 'of Enthronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthronize', 'v. t.', 'To place on a throne; hence, to induct into office,\n   as a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthuse', 'v. t. & i.', 'To make or become enthusiastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiasm', 'n.', 'Inspiration as if by a divine or superhuman power;\n   ecstasy; hence, a conceit of divine possession and revelation, or of\n   being directly subject to some divine impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiasm', 'n.', 'A state of impassioned emotion; transport; elevation of\n   fancy; exaltation of soul; as, the poetry of enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiasm', 'n.', 'Enkindled and kindling fervor of soul; strong\n   excitement of feeling on behalf of a cause or a subject; ardent and\n   imaginative zeal or interest; as, he engaged in his profession with\n   enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiasm', 'n.', 'Lively manifestation of joy or zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiast', 'n.', 'One moved or actuated by enthusiasm; as: (a) One who\n   imagines himself divinely inspired, or possessed of some special\n   revelation; a religious madman; a fanatic. (b) One whose mind is wholly\n   possessed and heated by what engages it; one who is influenced by a\n   peculiar; fervor of mind; an ardent and imaginative person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiastic', 'a.', 'Alt. of Enthusiastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiastical', 'a.', 'Filled with enthusiasm; characterized by\n   enthusiasm; zealous; as, an enthusiastic lover of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthusiastic', 'n.', 'An enthusiast; a zealot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthymematic', 'a.', 'Alt. of Enthymematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthymematical', 'a.', 'Pertaining to, or of the form of, an enthymeme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enthymeme', 'n.', 'An argument consisting of only two propositions, an\n   antecedent and consequent deduced from it; a syllogism with one premise\n   omitted; as, We are dependent; therefore we should be humble. Here the\n   major proposition is suppressed. The complete syllogism would be,\n   Dependent creatures should be humble; we are dependent creatures;\n   therefore we should be humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticed', 'imp. & p. p.', 'of Entice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticing', 'p. pr. & vb. n.', 'of Entice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entice', 'v. t.', 'To draw on, by exciting hope or desire; to allure; to\n   attract; as, the bait enticed the fishes. Often in a bad sense: To lead\n   astray; to induce to evil; to tempt; as, the sirens enticed them to\n   listen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticeable', 'a.', 'Capable of being enticed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticement', 'n.', 'The act or practice of alluring or tempting; as, the\n   enticements of evil companions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticement', 'n.', 'That which entices, or incites to evil; means of\n   allurement; alluring object; as, an enticement to sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticer', 'n.', 'One who entices; one who incites or allures to evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticing', 'a.', 'That entices; alluring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enticingly', 'adv.', 'In an enticing manner; charmingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entierty', 'n.', 'See Entirety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Complete in all parts; undivided; undiminished; whole; full\n   and perfect; not deficient; as, the entire control of a business;\n   entire confidence, ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Without mixture or alloy of anything; unqualified; morally\n   whole; pure; faithful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Consisting of a single piece, as a corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Having an evenly continuous edge, as a leaf which has no\n   kind of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Not gelded; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'a.', 'Internal; interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'n.', 'Entirely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entire', 'n.', 'A name originally given to a kind of beer combining\n   qualities of different kinds of beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entirely', 'adv.', 'In an entire manner; wholly; completely; fully; as, the\n   trace is entirely lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entirely', 'adv.', 'Without alloy or mixture; truly; sincerely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entireness', 'n.', 'The state or condition of being entire; completeness;\n   fullness; totality; as, the entireness of an arch or a bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entireness', 'n.', 'Integrity; wholeness of heart; honesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entireness', 'n.', 'Oneness; unity; -- applied to a condition of intimacy\n   or close association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entireness', 'pl. ', 'of Entirety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entirety', 'n.', 'The state of being entire; completeness; as, entirely of\n   interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entirety', 'n.', 'That which is entire; the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitative', 'a.', 'Considered as pure entity; abstracted from all\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitled', 'imp. & p. p.', 'of Entitle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitling', 'p. pr. & vb. n.', 'of Entitle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitle', 'v. t.', 'To give a title to; to affix to as a name or\n   appellation; hence, also, to dignify by an honorary designation; to\n   denominate; to call; as, to entitle a book \"Commentaries;\" to entitle a\n   man \"Honorable.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitle', 'v. t.', 'To give a claim to; to qualify for, with a direct\n   object of the person, and a remote object of the thing; to furnish with\n   grounds for seeking or claiming with success; as, an officers talents\n   entitle him to command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitle', 'v. t.', 'To attribute; to ascribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entitule', 'v. t.', 'To entitle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entities', 'pl. ', 'of Entity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entity', 'n.', 'A real being, whether in thought (as an ideal conception)\n   or in fact; being; essence; existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ento-', '', 'A combining form signifying within; as, entoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoblast', 'n.', 'The inner germ layer; endoderm. See Nucleolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entobronchia', 'pl. ', 'of Entobronchium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entobronchium', 'n.', 'One of the main bronchi in the lungs of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entocuneiform', 'n.', 'Alt. of Entocuniform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entocuniform', 'n.', 'One of the bones of the tarsus. See Cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoderm', 'n.', 'See Endoderm, and Illust. of Blastoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entodermal', 'a.', 'Alt. of Entodermic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entodermic', 'a.', 'Relating to the entoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entogastric', 'a.', 'Pertaining to the interior of the stomach; -- applied\n   to a mode of budding from the interior of the gastric cavity, in\n   certain hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entogenous', 'a.', 'See Endogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoglossal', 'a.', 'Within the tongue; -- applied to the glossohyal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoiled', 'imp. & p. p.', 'of Entoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoiling', 'p. pr. & vb. n.', 'of Entoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoil', 'v. t.', 'To take with toils or bring into toils; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entombed', 'imp. & p. p.', 'of Entomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entombing', 'p. pr. & vb. n.', 'of Entomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomb', 'v. t.', 'To deposit in a tomb, as a dead body; to bury; to inter;\n   to inhume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entombment', 'n.', 'The act of entombing or burying, or state of being\n   entombed; burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomere', 'n.', 'The more granular cells, which finally become internal,\n   in many segmenting ova, as those of mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomic', 'a.', 'Alt. of Entomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomical', 'a.', 'Relating to insects; entomological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomoid', 'a.', 'Resembling an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomoid', 'n.', 'An object resembling an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomolin', 'n.', 'See Chitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomolite', 'n.', 'A fossil insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomologic', 'a.', 'Alt. of Entomological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomological', 'a.', 'Of or relating to entomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomologist', 'n.', 'One versed in entomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomologize', 'v. i.', 'To collect specimens in the study of entomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomologies', 'pl. ', 'of Entomology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomology', 'n.', 'That part of zoology which treats of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomology', 'n.', 'A treatise on the science of entomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophaga', 'n. pl.', 'One of a group of hymenopterous insects whose\n   larvae feed parasitically upon living insects. See Ichneumon, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophaga', 'n. pl.', 'A group of marsupials which are partly\n   insectivorous, as the opossum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophaga', 'n. pl.', 'A group of edentates, including the ant-eaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophagan', 'a.', 'Relating to the Entomophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophagan', 'n.', 'One of the Entomophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophagous', 'a.', 'Feeding on insects; insectivorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomophilous', 'a.', 'Fertilized by the agency of insects; -- said of\n   plants in which the pollen is carried to the stigma by insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomostraca', 'n. pl.', 'One of the subclasses of Crustacea, including a\n   large number of species, many of them minute. The group embraces\n   several orders; as the Phyllopoda, Ostracoda, Copepoda, and\n   Pectostraca. See Copepoda, Phyllopoda, and Cladocera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomostracan', 'a.', 'Relating to the Entomostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomostracan', 'n.', 'One of the Entomostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomostracous', 'a.', 'Belonging to the Entomostracans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomotomist', 'n.', 'One who practices entomotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entomotomy', 'n.', 'The science of the dissection of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entonic', 'a.', 'Having great tension, or exaggerated action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoperipheral', 'a.', 'Being, or having its origin, within the external\n   surface of the body; -- especially applied to feelings, such as hunger,\n   produced by internal disturbances. Opposed to epiperipheral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entophyte', 'n.', 'A vegetable parasite subsisting in the interior of the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entophytic', 'a.', 'Of or pertaining to entophytes; as, an entophytic\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoplasm', 'n.', 'The inner granular layer of protoplasm in a developing\n   ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoplasm', 'n.', 'Endosarc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoplastic', 'a.', 'Pertaining to, or composed of, entoplasm; as, the\n   entoplastic products of some Protozoa, or the entoplastic modification\n   of the cell protoplasm, by which a nucleus is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoplastra', 'pl. ', 'of Entoplastron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoplastron', 'n.', 'The median plate of the plastron of turtles; --\n   called also entosternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoprocta', 'n. pl.', 'A group of Bryozoa in which the anus is within the\n   circle of tentacles. See Pedicellina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entoptic', 'a.', 'Relating to objects situated within the eye; esp.,\n   relating to the perception of objects in ones own eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entorganism', 'n.', 'An internal parasitic organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entortilation', 'n.', 'A turning into a circle; round figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entosterna', 'pl. ', 'of Entosternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entosternum', 'n.', 'See Entoplastron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entosthoblast', 'n.', 'The granule within the nucleolus or entoblast of a\n   nucleated cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entothorax', 'n.', 'See Endothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entotic', 'a.', 'Pertaining to the interior of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoa', 'n. pl.', 'A group of worms, including the tapeworms, flukes,\n   roundworms, etc., most of which live parasitically in the interior of\n   other animals; the Helminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoa', 'n. pl.', 'An artificial group, including all kinds of animals\n   living parasitically in others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoal', 'a.', 'Alt. of Entozoic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoic', 'a.', 'Pertaining to, or consisting of, the Entozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoologist', 'n.', 'One versed in the science of the Entozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoa', 'pl. ', 'of Entozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entozoon', 'n.', 'One of the Entozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entracte', 'n.', 'The interval of time which occurs between the\n   performance of any two acts of a drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entracte', 'n.', 'A dance, piece of music, or interlude, performed between\n   two acts of a drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrail', 'v. t.', 'To interweave; to intertwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrail', 'n.', 'Entanglement; fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrails', 'n. pl.', 'The internal parts of animal bodies; the bowels; the\n   guts; viscera; intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrails', 'n. pl.', 'The internal parts; as, the entrails of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrain', 'v. t.', 'To draw along as a current does; as, water entrained by\n   steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrain', 'v. t.', 'To put aboard a railway train; as, to entrain a\n   regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrain', 'v. i.', 'To go aboard a railway train; as, the troops entrained\n   at the station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrammel', 'v. t.', 'To trammel; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The act of entering or going into; ingress; as, the\n   entrance of a person into a house or an apartment; hence, the act of\n   taking possession, as of property, or of office; as, the entrance of an\n   heir upon his inheritance, or of a magistrate into office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'Liberty, power, or permission to enter; as, to give\n   entrance to friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The passage, door, or gate, for entering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The entering upon; the beginning, or that with which the\n   beginning is made; the commencement; initiation; as, a difficult\n   entrance into business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The causing to be entered upon a register, as a ship or\n   goods, at a customhouse; an entering; as, his entrance of the arrival\n   was made the same day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The angle which the bow of a vessel makes with the water\n   at the water line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'n.', 'The bow, or entire wedgelike forepart of a vessel, below\n   the water line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entranced', 'imp. & p. p.', 'of Entrance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrancing', 'p. pr. & vb. n.', 'of Entrance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'v. t.', 'To put into a trance; to make insensible to present\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrance', 'v. t.', 'To put into an ecstasy; to ravish with delight or\n   wonder; to enrapture; to charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrancement', 'n.', 'The act of entrancing, or the state of trance or\n   ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrant', 'n.', 'One who enters; a beginner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrant', 'n.', 'An applicant for admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrapped', 'imp. & p. p.', 'of Entrap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrapping', 'p. pr. & vb. n.', 'of Entrap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrap', 'v. t.', 'To catch in a trap; to insnare; hence, to catch, as in a\n   trap, by artifices; to involve in difficulties or distresses; to catch\n   or involve in contradictions; as, to be entrapped by the devices of\n   evil men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreated', 'imp. & p. p.', 'of Entreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreating', 'p. pr. & vb. n.', 'of Entreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. t.', 'To treat, or conduct toward; to deal with; to use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. t.', 'To treat with, or in respect to, a thing desired;\n   hence, to ask earnestly; to beseech; to petition or pray with urgency;\n   to supplicate; to importune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. t.', 'To beseech or supplicate successfully; to prevail upon\n   by prayer or solicitation; to persuade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. t.', 'To invite; to entertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. i.', 'To treat or discourse; hence, to enter into\n   negotiations, as for a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'v. i.', 'To make an earnest petition or request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreat', 'n.', 'Entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreatable', 'a.', 'That may be entreated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreatance', 'n.', 'Entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreater', 'n.', 'One who entreats; one who asks earnestly; a beseecher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreatful', 'a.', 'Full of entreaty. [R.] See Intreatful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreatingly', 'adv.', 'In an entreating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreative', 'a.', 'Used in entreaty; pleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreatment', 'n.', 'Entreaty; invitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreaties', 'pl. ', 'of Entreaty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreaty', 'n.', 'Treatment; reception; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entreaty', 'n.', 'The act of entreating or beseeching; urgent prayer;\n   earnest petition; pressing solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entree', 'n.', 'A coming in, or entrance; hence, freedom of access;\n   permission or right to enter; as, to have the entree of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entree', 'n.', 'In French usage, a dish served at the beginning of dinner\n   to give zest to the appetite; in English usage, a side dish, served\n   with a joint, or between the courses, as a cutlet, scalloped oysters,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entremets', 'n. sing. & pl.', 'A side dish; a dainty or relishing dish\n   usually eaten after the joints or principal dish; also, a sweetmeat,\n   served with a dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entremets', 'n. sing. & pl.', 'Any small entertainment between two greater\n   ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrench', 'v. t.', 'See Intrench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrepot', 'n.', 'A warehouse; a magazine for depositing goods, stores,\n   etc.; a mart or place where merchandise is deposited; as, an entrepot\n   for shipping goods in transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrepreneur', 'n.', 'One who creates a product on his own account; whoever\n   undertakes on his own account an industrial enterprise in which workmen\n   are employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entresol', 'n.', 'A low story between two higher ones, usually between the\n   ground floor and the first story; mezzanine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrick', 'v. t.', 'To trick, to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrochal', 'a.', 'Pertaining to, or consisting of, entrochites, or the\n   joints of encrinites; -- used of a kind of stone or marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrochite', 'n.', 'A fossil joint of a crinoid stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entropion', 'n.', 'Same as Entropium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entropium', 'n.', 'The inversion or turning in of the border of the\n   eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entropy', 'n.', 'A certain property of a body, expressed as a measurable\n   quantity, such that when there is no communication of heat the quantity\n   remains constant, but when heat enters or leaves the body the quantity\n   increases or diminishes. If a small amount, h, of heat enters the body\n   when its temperature is t in the thermodynamic scale the entropy of the\n   body is increased by h / t. The entropy is regarded as measured from\n   some standard temperature and pressure. Sometimes called the\n   thermodynamic function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entrust', 'v. t.', 'See Intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entries', 'pl. ', 'of Entry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'The act of entering or passing into or upon; entrance;\n   ingress; hence, beginnings or first attempts; as, the entry of a person\n   into a house or city; the entry of a river into the sea; the entry of\n   air into the blood; an entry upon an undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'The act of making or entering a record; a setting down in\n   writing the particulars, as of a transaction; as, an entry of a sale;\n   also, that which is entered; an item.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'That by which entrance is made; a passage leading into a\n   house or other building, or to a room; a vestibule; an adit, as of a\n   mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'The exhibition or depositing of a ships papers at the\n   customhouse, to procure license to land goods; or the giving an account\n   of a ships cargo to the officer of the customs, and obtaining his\n   permission to land the goods. See Enter, v. t., 8, and Entrance, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'The actual taking possession of lands or tenements, by\n   entering or setting foot on them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'A putting upon record in proper form and order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entry', 'n.', 'The act in addition to breaking essential to constitute the\n   offense or burglary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entryng', 'n.', 'Am entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entune', 'v. t.', 'To tune; to intone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entwine', 'v. t.', 'To twine, twist, or wreathe together or round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entwine', 'v. i.', 'To be twisted or twined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entwinement', 'n.', 'A twining or twisting together or round; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Entwist', 'v. t.', 'To twist or wreathe round; to intwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enubilate', 'v. t.', 'To clear from mist, clouds, or obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enubilous', 'a.', 'Free from fog, mist, or clouds; clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleated', 'imp. & p. p.', 'of Enucleate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleating', 'p. pr. & vb. n.', 'of Enucleate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleate', 'v. t.', 'To bring or peel out, as a kernel from its enveloping\n   husks its enveloping husks or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleate', 'v. t.', 'To remove without cutting (as a tumor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleate', 'v. t.', 'To bring to light; to make clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enucleation', 'n.', 'The act of enucleating; elucidation; exposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumerated', 'imp. & p. p.', 'of Enumerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumerating', 'p. pr. & vb. n.', 'of Enumerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumerate', 'v. t.', 'To count; to tell by numbers; to count over, or tell\n   off one after another; to number; to reckon up; to mention one by one;\n   to name over; to make a special and separate account of; to recount;\n   as, to enumerate the stars in a constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumeration', 'n.', 'The act of enumerating, making separate mention, or\n   recounting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumeration', 'n.', 'A detailed account, in which each thing is specially\n   noticed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumeration', 'n.', 'A recapitulation, in the peroration, of the heads of\n   an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumerative', 'a.', 'Counting, or reckoning up, one by one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enumerator', 'n.', 'One who enumerates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciable', 'a.', 'Capable of being enunciated or expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciated', 'imp. & p. p.', 'of Enunciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciating', 'p. pr. & vb. n.', 'of Enunciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciate', 'v. t.', 'To make a formal statement of; to announce; to\n   proclaim; to declare, as a truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciate', 'v. t.', 'To make distinctly audible; to utter articulately; to\n   pronounce; as, to enunciate a word distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciate', 'v. i.', 'To utter words or syllables articulately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciation', 'n.', 'The act of enunciating, announcing, proclaiming, or\n   making known; open attestation; declaration; as, the enunciation of an\n   important truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciation', 'n.', 'Mode of utterance or pronunciation, especially as\n   regards fullness and distinctness or articulation; as, to speak with a\n   clear or impressive enunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciation', 'n.', 'That which is enunciated or announced; words in which\n   a proposition is expressed; an announcement; a formal declaration; a\n   statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciative', 'a.', 'Pertaining to, or containing, enunciation;\n   declarative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciator', 'n.', 'One who enunciates or proclaims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enunciatory', 'a.', 'Pertaining to, or containing, enunciation or\n   utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enure', 'v. t.', 'See Inure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enuresis', 'n.', 'An involuntary discharge of urine; incontinence of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envassal', 'v. t.', 'To make a vassal of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envault', 'v. t.', 'To inclose in a vault; to entomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enveigle', 'v. t.', 'To entice. See Inveigle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enveloped', 'imp. & p. p.', 'of Envelop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enveloping', 'p. pr. & vb. n.', 'of Envelop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'v. t.', 'To put a covering about; to wrap up or in; to inclose\n   within a case, wrapper, integument or the like; to surround entirely;\n   as, to envelop goods or a letter; the fog envelops a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelope', 'n.', 'Alt. of Envelop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'n.', 'That which envelops, wraps up, encases, or surrounds; a\n   wrapper; an inclosing cover; esp., the cover or wrapper of a document,\n   as of a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'n.', 'The nebulous covering of the head or nucleus of a comet;\n   -- called also coma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'n.', 'A work of earth, in the form of a single parapet or of a\n   small rampart. It is sometimes raised in the ditch and sometimes beyond\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'n.', 'A curve or surface which is tangent to each member of a\n   system of curves or surfaces, the form and position of the members of\n   the system being allowed to vary according to some continuous law.\n   Thus, any curve is the envelope of its tangents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelop', 'n.', 'A set of limits for the performance capabilities of some\n   type of machine, originally used to refer to aircraft. Now also used\n   metaphorically to refer to capabilities of any system in general,\n   including human organizations, esp. in the phrase push the envelope. It\n   is used to refer to the maximum performance available at the current\n   state of the technology, and therefore refers to a class of machines in\n   general, not a specific machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelopment', 'n.', 'The act of enveloping or wrapping; an inclosing or\n   covering on all sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envelopment', 'n.', 'That which envelops or surrounds; an envelop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envenime', 'v. t.', 'To envenom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envenomed', 'imp. & p. p.', 'of Envenom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envenoming', 'p. pr. & vb. n.', 'of Envenom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envenom', 'v. t.', 'To taint or impregnate with venom, or any substance\n   noxious to life; to poison; to render dangerous or deadly by poison, as\n   food, drink, a weapon; as, envenomed meat, wine, or arrow; also, to\n   poison (a person) by impregnating with venom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envenom', 'v. t.', 'To taint or impregnate with bitterness, malice, or\n   hatred; to imbue as with venom; to imbitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envermeil', 'v. t.', 'To color with, or as with, vermilion; to dye red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enviable', 'a.', 'Fitted to excite envy; capable of awakening an ardent\n   desire to posses or to resemble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envie', 'v. i.', 'To vie; to emulate; to strive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envier', 'n.', 'One who envies; one who desires inordinately what another\n   possesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envigor', 'v. t.', 'To invigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envious', 'a.', 'Malignant; mischievous; spiteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envious', 'a.', 'Feeling or exhibiting envy; actuated or directed by, or\n   proceeding from, envy; -- said of a person, disposition, feeling, act,\n   etc.; jealously pained by the excellence or good fortune of another;\n   maliciously grudging; -- followed by of, at, and against; as, an\n   envious man, disposition, attack; envious tongues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envious', 'a.', 'Inspiring envy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envious', 'a.', 'Excessively careful; cautious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environed', 'imp. & p. p.', 'of Environ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environing', 'p. pr. & vb. n.', 'of Environ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environ', 'v. t.', 'To surround; to encompass; to encircle; to hem in; to\n   be round about; to involve or envelop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environ', 'adv.', 'About; around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environment', 'n.', 'Act of environing; state of being environed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environment', 'n.', 'That which environs or surrounds; surrounding\n   conditions, influences, or forces, by which living forms are influenced\n   and modified in their growth and development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Environs', 'n. pl.', 'The parts or places which surround another place, or\n   lie in its neighborhood; suburbs; as, the environs of a city or town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envisaged', 'imp. & p. p.', 'of Envisage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envisaging', 'p. pr. & vb. n.', 'of Envisage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envisage', 'v. t.', 'To look in the face of; to apprehend; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envisagement', 'n.', 'The act of envisaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envolume', 'v. t.', 'To form into, or incorporate with, a volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envolup', 'v. t.', 'To wrap up; to envelop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envoy', 'n.', 'One dispatched upon an errand or mission; a messenger; esp.,\n   a person deputed by a sovereign or a government to negotiate a treaty,\n   or transact other business, with a foreign sovereign or government; a\n   minister accredited to a foreign government. An envoys rank is below\n   that of an ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envoy', 'n.', 'An explanatory or commendatory postscript to a poem, essay,\n   or book; -- also in the French from, lenvoi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envoyship', 'n.', 'The office or position of an envoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envies', 'pl. ', 'of Envy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'n.', 'Malice; ill will; spite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'n.', 'Chagrin, mortification, discontent, or uneasiness at the\n   sight of anothers excellence or good fortune, accompanied with some\n   degree of hatred and a desire to possess equal advantages; malicious\n   grudging; -- usually followed by of; as, they did this in envy of\n   Caesar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'n.', 'Emulation; rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'n.', 'Public odium; ill repute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'n.', 'An object of envious notice or feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envied', 'imp. & p. p.', 'of Envy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envying', 'p. pr. & vb. n.', 'of Envy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To feel envy at or towards; to be envious of; to have a\n   feeling of uneasiness or mortification in regard to (any one), arising\n   from the sight of anothers excellence or good fortune and a longing to\n   possess it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To feel envy on account of; to have a feeling of grief or\n   repining, with a longing to possess (some excellence or good fortune of\n   another, or an equal good fortune, etc.); to look with grudging upon;\n   to begrudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To long after; to desire strongly; to covet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To do harm to; to injure; to disparage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To hate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. t.', 'To emulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. i.', 'To be filled with envious feelings; to regard anything\n   with grudging and longing eyes; -- used especially with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envy', 'v. i.', 'To show malice or ill will; to rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Envyned', 'a.', 'Stored or furnished with wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwall', 'v. t.', 'See Inwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwallow', 'v. t.', 'To plunge into, or roll in, flith; to wallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwheel', 'v. t.', 'To encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwiden', 'v. t.', 'To widen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwind', 'v. t.', 'To wind about; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwoman', 'v. t.', 'To endow with the qualities of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwombed', 'imp. & p. p.', 'of Enwomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwombing', 'p. pr. & vb. n.', 'of Enwomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwomb', 'v. t.', 'To conceive in the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwomb', 'v. t.', 'To bury, as it were in a womb; to hide, as in a gulf,\n   pit, or cavern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwrap', 'v. t.', 'To envelop. See Inwrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwrapment', 'n.', 'Act of enwrapping; a wrapping or an envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enwreathe', 'v. t.', 'See Inwreathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enzootic', 'a.', 'Afflicting animals; -- used of a disease affecting the\n   animals of a district. It corresponds to an endemic disease among men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Enzyme', 'n.', 'An unorganized or unformed ferment, in distinction from an\n   organized or living ferment; a soluble, or chemical, ferment. Ptyalin,\n   pepsin, diastase, and rennet are good examples of enzymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eocene', 'a.', 'Pertaining to the first in time of the three subdivisions\n   into which the Tertiary formation is divided by geologists, and\n   alluding to the approximation in its life to that of the present era;\n   as, Eocene deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eocene', 'n.', 'The Eocene formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eolian', 'a.', 'Aeolian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eolian', 'a.', 'Formed, or deposited, by the action of wind, as dunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eolic', 'a. & n.', 'See Aeolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eolipile', 'n.', 'Same as Aeolipile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eolis', 'n.', 'A genus of nudibranch mollusks having clusters of branchial\n   papillae along the back. See Ceratobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eon', 'n.', 'Alt. of Aeon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Aeon', 'n.', 'An immeasurable or infinite space of time; eternity; a long\n   space of time; an age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Aeon', 'n.', 'One of the embodiments of the divine attributes of the\n   Eternal Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eophyte', 'n.', 'A fossil plant which is found in the lowest beds of the\n   Silurian age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eophytic', 'a.', 'Of or pertaining to eophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eos', 'n.', 'Aurora, the goddess of morn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eosaurus', 'n.', 'An extinct marine reptile from the coal measures of Nova\n   Scotia; -- so named because supposed to be of the earliest known\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eosin', 'n.', 'A yellow or brownish red dyestuff obtained by the action of\n   bromine on fluorescein, and named from the fine rose-red which it\n   imparts to silk. It is also used for making a fine red ink. Its\n   solution is fluorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eosphorite', 'n.', 'A hydrous phosphate of alumina and manganese. It is\n   generally of a rose-pink color, -- whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eozoic', 'a.', 'Of or pertaining to rocks or strata older than the\n   Paleozoic, in many of which the eozoon has been found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eozoons', 'pl. ', 'of Eozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eozoa', 'pl. ', 'of Eozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eozoon', 'n.', 'A peculiar structure found in the Archaean limestones of\n   Canada and other regions. By some geologists it is believed to be a\n   species of gigantic Foraminifera, but others consider it a concretion,\n   without organic structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eozoonal', 'a.', 'Pertaining to the eozoon; containing eozoons; as,\n   eozoonal limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ep-', '', 'See Epi-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epacris', 'n.', 'A genus of shrubs, natives of Australia, New Zealand,\n   etc., having pretty white, red, or purple blossoms, and much resembling\n   heaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epact', 'n.', 'The moons age at the beginning of the calendar year, or the\n   number of days by which the last new moon has preceded the beginning of\n   the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epagoge', 'n.', 'The adducing of particular examples so as to lead to a\n   universal conclusion; the argument by induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epagogic', 'a.', 'Inductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epalate', 'a.', 'Without palpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanadiplosis', 'n.', 'A figure by which the same word is used both at the\n   beginning and at the end of a sentence; as, \"Rejoice in the Lord\n   always: and again I say, Rejoice.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanalepsis', 'n.', 'A figure by which the same word or clause is repeated\n   after intervening matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanaphora', 'n.', 'Same as Anaphora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanastrophe', 'n.', 'Same as Anadiplosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanodos', 'n.', 'A figure of speech in which the parts of a sentence or\n   clause are repeated in inverse order');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanody', 'n.', 'The abnormal change of an irregular flower to a regular\n   form; -- considered by evolutionists to be a reversion to an ancestral\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanorthosis', 'n.', 'A figure by which a speaker recalls a word or words,\n   in order to substitute something else stronger or more significant; as,\n   Most brave! Brave, did I say? most heroic act!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epanthous', 'a.', 'Growing upon flowers; -- said of certain species of\n   fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eparch', 'n.', 'In ancient Greece, the governor or perfect of a province;\n   in modern Greece, the ruler of an eparchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eparchy', 'n.', 'A province, prefecture, or territory, under the\n   jurisdiction of an eparch or governor; esp., in modern Greece, one of\n   the larger subdivisions of a monarchy or province of the kingdom; in\n   Russia, a diocese or archdiocese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eparterial', 'a.', 'Situated upon or above an artery; -- applied esp. to\n   the branches of the bronchi given off above the point where the\n   pulmonary artery crosses the bronchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epaule', 'n.', 'The shoulder of a bastion, or the place where its face and\n   flank meet and form the angle, called the angle of the shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epaulement', 'n.', 'A side work, made of gabions, fascines, or bags, filled\n   with earth, or of earth heaped up, to afford cover from the flanking\n   fire of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epaulet', 'n.', 'Alt. of Epaulette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epaulette', 'n.', 'A shoulder ornament or badge worn by military and naval\n   officers, differences of rank being marked by some peculiar form or\n   device, as a star, eagle, etc.; a shoulder knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epauleted', 'a.', 'Alt. of Epauletted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epauletted', 'a.', 'Wearing epaulets; decorated with epaulets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epaxial', 'a.', 'Above, or on the dorsal side of, the axis of the skeleton;\n   episkeletal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epeira', 'n.', 'A genus of spiders, including the common garden spider (E.\n   diadema). They spin geometrical webs. See Garden spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epen', 'n.', 'See Epencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epencephalic', 'a.', 'Pertaining to the epencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epencephalic', 'a.', 'Situated on or over the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epencephalon', 'n.', 'The segment of the brain next behind the midbrain,\n   including the cerebellum and pons; the hindbrain. Sometimes abbreviated\n   to epen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ependyma', 'n.', 'The epithelial lining of the ventricles of the brain and\n   the canal of the spinal cord; endyma; ependymis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ependymis', 'n.', 'See Ependyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epenetic', 'a.', 'Bestowing praise; eulogistic; laudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epentheses', 'pl. ', 'of Epenthesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epenthesis', 'n.', 'The insertion of a letter or a sound in the body of a\n   word; as, the b in \"nimble\" from AS. n/mol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epenthetic', 'a.', 'Inserted in the body of a word; as, an epenthetic\n   letter or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epergne', 'n.', 'A centerpiece for table decoration, usually consisting of\n   several dishes or receptacles of different sizes grouped together in an\n   ornamental design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eperlan', 'n.', 'The European smelt (Osmerus eperlanus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epexegesis', 'n.', 'A full or additional explanation; exegesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epexegetical', 'a.', 'Relating to epexegesis; explanatory; exegetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephah', 'n.', 'Alt. of Epha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epha', 'n.', 'A Hebrew dry measure, supposed to be equal to two pecks and\n   five quarts. ten ephahs make one homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemera', 'n.', 'A fever of one days continuance only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemera', 'n.', 'A genus of insects including the day flies, or ephemeral\n   flies. See Ephemeral fly, under Ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeral', 'a.', 'Beginning and ending in a day; existing only, or no\n   longer than, a day; diurnal; as, an ephemeral flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeral', 'a.', 'Short-lived; existing or continuing for a short time\n   only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeral', 'n.', 'Anything lasting but a day, or a brief time; an\n   ephemeral plant, insect, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeran', 'n.', 'One of the ephemeral flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeric', 'a.', 'Ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemerides', 'pl. ', 'of Ephemeris');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeris', 'n.', 'A diary; a journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeris', 'n.', 'A publication giving the computed places of the heavenly\n   bodies for each day of the year, with other numerical data, for the use\n   of the astronomer and navigator; an astronomical almanac; as, the\n   \"American Ephemeris and Nautical Almanac.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeris', 'n.', 'Any tabular statement of the assigned places of a\n   heavenly body, as a planet or comet, on several successive days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeris', 'n.', 'A collective name for reviews, magazines, and all kinds\n   of periodical literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemerist', 'n.', 'One who studies the daily motions and positions of the\n   planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemerist', 'n.', 'One who keeps an ephemeris; a journalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemera', 'pl. ', 'of Ephemeron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemeron', 'n.', 'One of the ephemeral flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephemerous', 'a.', 'Ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephesian', 'a.', 'Of or pertaining to Ephesus, an ancient city of Ionia, in\n   Asia Minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephesian', 'n.', 'A native of Ephesus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephesian', 'n.', 'A jolly companion; a roisterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephialtes', 'n.', 'The nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephippial', 'a.', 'Saddle-shaped; occupying an ephippium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephippium', 'n.', 'A depression in the sphenoid bone; the pituitary fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephippium', 'n.', 'A saddle-shaped cavity to contain the winter eggs,\n   situated on the back of Cladocera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephod', 'n.', 'A part of the sacerdotal habit among Jews, being a covering\n   for the back and breast, held together on the shoulders by two clasps\n   or brooches of onyx stones set in gold, and fastened by a girdle of the\n   same stuff as the ephod. The ephod for the priests was of plain linen;\n   that for the high priest was richly embroidered in colors. The\n   breastplate of the high priest was worn upon the ephod in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephors', 'pl. ', 'of Ephor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephori', 'pl. ', 'of Ephor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephor', 'n.', 'A magistrate; one of a body of five magistrates chosen by\n   the people of ancient Sparta. They exercised control even over the\n   king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephoral', 'a.', 'Pertaining to an ephor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephoralty', 'n.', 'The office of an ephor, or the body of ephors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephraim', 'n.', 'A hunters name for the grizzly bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ephyra', 'n.', 'A stage in the development of discophorous medusae, when\n   they first begin to swim about after being detached from the strobila.\n   See Strobila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epi-', '', 'A prefix, meaning upon, beside, among, on the outside, above,\n   over. It becomes ep-before a vowel, as in epoch, and eph-before a Greek\n   aspirate, as in ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiblast', 'n.', 'The outer layer of the blastoderm; the ectoderm. See\n   Blastoderm, Delamination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiblastic', 'a.', 'Of or relating to, or consisting of, the epiblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiblema', 'n.', 'The epidermal cells of rootlets, specially adapted to\n   absorb liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epibolic', 'a.', 'Growing or covering over; -- said of a kind of\n   invagination. See under Invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiboly', 'n.', 'Epibolic invagination. See under Invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epibranchial', 'a.', 'Pertaining to the segment between the ceratobranchial\n   and pharyngobranchial in a branchial arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epibranchial', 'n.', 'An epibranchial cartilage or bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epic', 'a.', 'Narrated in a grand style; pertaining to or designating a\n   kind of narrative poem, usually called an heroic poem, in which real or\n   fictitious events, usually the achievements of some hero, are narrated\n   in an elevated style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epic', 'n.', 'An epic or heroic poem. See Epic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epical', 'a.', 'Epic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicardiac', 'a.', 'Of or relating to the epicardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicardium', 'n.', 'That of the pericardium which forms the outer surface\n   of the heart; the cardiac pericardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicarican', 'n.', 'An isopod crustacean, parasitic on shrimps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicarp', '', 'The external or outermost layer of a fructified or ripened\n   ovary. See Illust. under Endocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicede', 'n.', 'A funeral song or discourse; an elegy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicedial', 'a.', 'Elegiac; funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicedian', 'a.', 'Epicedial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicedian', 'n.', 'An epicede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicedium', 'n.', 'An epicede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicene', 'a. & n.', 'Common to both sexes; -- a term applied, in grammar,\n   to such nouns as have but one form of gender, either the masculine or\n   feminine, to indicate animals of both sexes; as boy^s, bos, for the ox\n   and cow; sometimes applied to eunuchs and hermaphrodites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicene', 'a. & n.', 'Fig.: Sexless; neither one thing nor the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicentral', 'a.', 'Arising from the centrum of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicerastic', 'a.', 'Lenient; assuaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epichiremata', 'pl. ', 'of Epichirema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epichirema', 'n.', 'A syllogism in which the proof of the major or minor\n   premise, or both, is introduced with the premises themselves, and the\n   conclusion is derived in the ordinary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epichordal', 'a.', 'Upon or above the notochord; -- applied esp. to a\n   vertebral column which develops upon the dorsal side of the notochord,\n   as distinguished from a perichordal column, which develops around it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epichorial', 'a.', 'In or of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicleidium', 'n.', 'A projection, formed by a separate ossification, at\n   the scapular end of the clavicle of many birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiclinal', 'a.', 'Situated on the receptacle or disk of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicoele', 'n.', 'A cavity formed by the invagination of the outer wall of\n   the body, as the atrium of an amphioxus and possibly the body cavity of\n   vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicoene', 'a.', 'Epicene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicolic', 'a.', 'Situated upon or over the colon; -- applied to the region\n   of the abdomen adjacent to the colon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicondylar', 'n.', 'Pertaining to, or resembling, an epicondyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicondyle', 'n.', 'A projection on the inner side of the distal end of the\n   numerus; the internal condyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicoracoid', 'n.', 'A ventral cartilaginous or bony element of the\n   coracoid in the shoulder girdle of some vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicranial', 'a.', 'Pertaining to the epicranium; as epicranial muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicranium', 'n.', 'The upper and superficial part of the head, including\n   the scalp, muscles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicranium', 'n.', 'The dorsal wall of the head of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epictetain', 'a.', 'Pertaining to Epictetus, the Roman Stoic philosopher,\n   whose conception of life was to be passionless under whatever\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicure', 'n.', 'A follower of Epicurus; an Epicurean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicure', 'n.', 'One devoted to dainty or luxurious sensual enjoyments,\n   esp. to the luxuries of the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurean', 'a.', 'Pertaining to Epicurus, or following his philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurean', 'a.', 'Given to luxury; adapted to luxurious tastes; luxurious;\n   pertaining to good eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurean', 'n.', 'A follower or Epicurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurean', 'n.', 'One given to epicurean indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicureanism', 'n.', 'Attachment to the doctrines of Epicurus; the\n   principles or belief of Epicurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurely', 'adv.', 'Luxuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicureous', 'a.', 'Epicurean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurism', 'n.', 'The doctrines of Epicurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurism', 'n.', 'Epicurean habits of living; luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurize', 'v. i.', 'To profess or tend towards the doctrines of Epicurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicurize', 'v. i.', 'To feed or indulge like an epicure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicycle', 'n.', 'A circle, whose center moves round in the circumference\n   of a greater circle; or a small circle, whose center, being fixed in\n   the deferent of a planet, is carried along with the deferent, and yet,\n   by its own peculiar motion, carries the body of the planet fastened to\n   it round its proper center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicycle', 'n.', 'A circle which rolls on the circumference of another\n   circle, either externally or internally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicyclic', 'a.', 'Pertaining to, resembling, or having the motion of, an\n   epicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicycloid', 'n.', 'A curve traced by a point in the circumference of a\n   circle which rolls on the convex side of a fixed circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epicycloidal', 'a.', 'Pertaining to the epicycloid, or having its\n   properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epideictic', 'a.', 'Serving to show forth, explain, or exhibit; -- applied\n   by the Greeks to a kind of oratory, which, by full amplification, seeks\n   to persuade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemic', 'a.', 'Alt. of Epidemical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemical', 'a.', 'Common to, or affecting at the same time, a large\n   number in a community; -- applied to a disease which, spreading widely,\n   attacks many persons at the same time; as, an epidemic disease; an\n   epidemic catarrh, fever, etc. See Endemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemical', 'a.', 'Spreading widely, or generally prevailing; affecting\n   great numbers, as an epidemic does; as, epidemic rage; an epidemic\n   evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemic', 'n.', 'An epidemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemic', 'n.', 'Anything which takes possession of the minds of people as\n   an epidemic does of their bodies; as, an epidemic of terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemically', 'adv.', 'In an epidemic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemiography', 'n.', 'A treatise upon, or history of, epidemic diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemiological', 'a.', 'Connected with, or pertaining to, epidemiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemiologist', 'n.', 'A person skilled in epidemiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemiology', 'n.', 'That branch of science which treats of epidemics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidemy', 'n.', 'An epidemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiderm', 'n.', 'The epidermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermal', 'a.', 'Of or pertaining to the epidermis; epidermic; cuticular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermatic', 'a.', 'Epidermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermatoid', 'a.', 'Epidermoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermeous', 'a.', 'Epidermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermic', 'a.', 'Epidermal; connected with the skin or the bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermical', 'a.', 'Epidermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermidal', 'a.', 'Epidermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermis', 'v. t.', 'The outer, nonsensitive layer of the skin; cuticle;\n   scarfskin. See Dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermis', 'v. t.', 'The outermost layer of the cells, which covers both\n   surfaces of leaves, and also the surface of stems, when they are first\n   formed. As stems grow old this layer is lost, and never replaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermoid', 'a.', 'Like epidermis; pertaining to the epidermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidermose', 'n.', 'Keratin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidictic', 'a.', 'Alt. of Epidictical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidictical', 'a.', 'Serving to explain; demonstrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epididymis', 'n.', 'An oblong vermiform mass on the dorsal side of the\n   testicle, composed of numerous convolutions of the excretory duct of\n   that organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epididymitis', 'n.', 'Inflammation of the epididymis, one of the common\n   results of gonorrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidote', 'n.', 'A mineral, commonly of a yellowish green (pistachio)\n   color, occurring granular, massive, columnar, and in monoclinic\n   crystals. It is a silicate of alumina, lime, and oxide of iron, or\n   manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epidotic', 'a.', 'Related to, resembling, or containing epidote; as, an\n   epidotic granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigaea', 'n.', 'An American genus of plants, containing but a single\n   species (E. repens), the trailing arbutus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigaeous', 'a.', 'Growing on, or close to, the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigastrial', 'a.', 'Epigastric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigastric', 'a.', 'Pertaining to the epigastrium, or to the epigastric\n   region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigastric', 'a.', 'Over the stomach; -- applied to two of the areas of the\n   carapace of crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigastrium', 'n.', 'The upper part of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigeal', 'a.', 'Epigaeous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigee', 'n.', 'See Perigee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigene', 'a.', 'Foreign; unnatural; unusual; -- said of forms of crystals\n   not natural to the substances in which they are found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigene', 'a.', 'Formed originating on the surface of the earth; -- opposed\n   to hypogene; as, epigene rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigenesis', 'n.', 'The theory of generation which holds that the germ is\n   created entirely new, not merely expanded, by the procreative power of\n   the parents. It is opposed to the theory of evolution, also to\n   syngenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigenesist', 'n.', 'One who believes in, or advocates the theory of,\n   epigenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigenetic', 'a.', 'Of or pertaining to the epigenesis; produced according\n   to the theory of epigenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigeous', 'a.', 'Same as Epigaeous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigeum', 'n.', 'See Perigee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiglottic', 'a.', 'Pertaining to, or connected with, the epiglottis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiglottidean', 'a.', 'Same as Epiglottic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiglottis', 'n.', 'A cartilaginous lidlike appendage which closes the\n   glottis while food or drink is passing while food or drink is passing\n   through the pharynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epignathous', 'a.', 'Hook-billed; having the upper mandible longer than the\n   lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigram', 'n.', 'A short poem treating concisely and pointedly of a single\n   thought or event. The modern epigram is so contrived as to surprise the\n   reader with a witticism or ingenious turn of thought, and is often\n   satirical in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigram', 'n.', 'An effusion of wit; a bright thought tersely and sharply\n   expressed, whether in verse or prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigram', 'n.', 'The style of the epigram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatic', '', 'Alt. of Epigrammatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatical', '', 'Writing epigrams; dealing in epigrams; as, an\n   epigrammatical poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatical', '', 'Suitable to epigrams; belonging to epigrams; like an\n   epigram; pointed; piquant; as, epigrammatic style, wit, or sallies of\n   fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatically', 'adv.', 'In the way of epigram; in an epigrammatic\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatist', 'n.', 'One who composes epigrams, or makes use of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatized', 'imp. & p. p.', 'of Epigrammatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatizing', 'p. pr. & vb. n.', 'of Epigrammatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatize', 'v. t.', 'To represent by epigrams; to express by epigrams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammatizer', 'n.', 'One who writes in an affectedly pointed style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigrammist', 'n.', 'An epigrammatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraph', 'n.', 'Any inscription set upon a building; especially, one\n   which has to do with the building itself, its founding or dedication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraph', 'n.', 'A citation from some author, or a sentence framed for the\n   purpose, placed at the beginning of a work or of its separate\n   divisions; a motto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraphic', 'a.', 'Alt. of Epigraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraphical', 'a.', 'Of or pertaining to epigraphs or to epigraphy; as, an\n   epigraphic style; epigraphical works or studies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraphics', 'n.', 'The science or study of epigraphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraphist', 'n.', 'A student of, or one versed in, epigraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigraphy', 'n.', 'The science of inscriptions; the art of engraving\n   inscriptions or of deciphering them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epigynous', 'a.', 'Adnate to the surface of the ovary, so as to be\n   apparently inserted upon the top of it; -- said of stamens, petals,\n   sepals, and also of the disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epihyal', 'n.', 'A segment next above the ceratohyal in the hyoidean arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilepsy', 'n.', 'The \"falling sickness,\" so called because the patient\n   falls suddenly to the ground; a disease characterized by paroxysms (or\n   fits) occurring at interval and attended by sudden loss of\n   consciousness, and convulsive motions of the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptic', 'a.', 'Pertaining to, affected with, or of the nature of,\n   epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptic', 'n.', 'One affected with epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptic', 'n.', 'A medicine for the cure of epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptical', 'a.', 'Epileptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptiform', 'a.', 'Resembling epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptogenous', 'a.', 'Producing epilepsy or epileptoid convulsions; --\n   applied to areas of the body or of the nervous system, stimulation of\n   which produces convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epileptoid', 'a.', 'Resembling epilepsy; as, epileptoid convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogation', 'n.', 'A summing up in a brief account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogic', 'a.', 'Alt. of Epilogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogical', 'a.', 'Of or pertaining to an epilogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogism', 'n.', 'Enumeration; computation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogistic', 'a.', 'Of or pertaining to epilogue; of the nature of an\n   epilogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogize', 'v. i. & t.', 'To speak an epilogue to; to utter as an\n   epilogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogue', 'n.', 'A speech or short poem addressed to the spectators and\n   recited by one of the actors, after the conclusion of the play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epilogue', 'n.', 'The closing part of a discourse, in which the principal\n   matters are recapitulated; a conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiloguize', 'v. i. & t.', 'Same as Epilogize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimachus', 'n.', 'A genus of highly ornate and brilliantly colored birds\n   of Australia, allied to the birds of Paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimera', 'n. pl.', 'See Epimeron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimeral', 'a.', 'Pertaining to the epimera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimere', 'n.', 'One of the segments of the transverse axis, or the so\n   called homonymous parts; as, for example, one of the several segments\n   of the extremities in vertebrates, or one of the similar segments in\n   plants, such as the segments of a segmented leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimera', 'pl. ', 'of Epimeron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimeron', 'n.', 'In crustaceans: The part of the side of a somite external\n   to the basal joint of each appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epimeron', 'n.', 'In insects: The lateral piece behind the episternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epinastic', 'a.', 'A term applied to that phase of vegetable growth in\n   which an organ grows more rapidly on its upper than on its under\n   surface. See Hyponastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epineural', 'a.', 'Arising from the neurapophysis of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epineurium', 'n.', 'The connective tissue framework and sheath of a nerve\n   which bind together the nerve bundles, each of which has its own\n   special sheath, or perineurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epinglette', 'n.', 'An iron needle for piercing the cartridge of a cannon\n   before priming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epinicial', 'a.', 'Relating to victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epinicion', 'n.', 'A song of triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epinikian', 'a.', 'Epinicial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiornis', 'n.', 'One of the gigantic ostrichlike birds of the genus\n   Aepiornis, only recently extinct. Its remains have been found in\n   Madagascar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiotic', 'n.', 'The upper and outer element of periotic bone, -- in man\n   forming a part of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipedometry', 'n.', 'The mensuration of figures standing on the same base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiperipheral', 'a.', 'Connected with, or having its origin upon, the\n   external surface of the body; -- especially applied to the feelings\n   which originate at the extremities of nerves distributed on the outer\n   surface, as the sensation produced by touching an object with the\n   finger; -- opposed to entoperipheral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipetalous', 'a.', 'Borne on the petals or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphany', 'n.', 'An appearance, or a becoming manifest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphany', 'n.', 'A church festival celebrated on the 6th of January, the\n   twelfth day after Christmas, in commemoration of the visit of the Magi\n   of the East to Bethlehem, to see and worship the child Jesus; or, as\n   others maintain, to commemorate the appearance of the star to the Magi,\n   symbolizing the manifestation of Christ to the Gentles; Twelfthtide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipharyngeal', 'a.', 'Pertaining to the segments above the epibranchial in\n   the branchial arches of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipharyngeal', 'n.', 'An epipharyngeal bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipharynx', 'n.', 'A structure which overlaps the mouth of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphonema', 'n.', 'An exclamatory sentence, or striking reflection, which\n   sums up or concludes a discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphoneme', 'n.', 'Epiphonema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphora', 'n.', 'The watery eye; a disease in which the tears accumulate\n   in the eye, and trickle over the cheek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphora', 'n.', 'The emphatic repetition of a word or phrase, at the end\n   of several sentences or stanzas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphragm', 'n.', 'A membranaceous or calcareous septum with which some\n   mollusks close the aperture of the shell during the time of\n   hibernation, or aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphylospermous', 'a.', 'Bearing fruit on the back of the leaves, as\n   ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyllous', 'a.', 'Growing upon, or inserted into, the leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyllum', 'n.', 'A genus of cactaceous plants having flattened, jointed\n   stems, and petals united in a tube. The flowers are very showy, and\n   several species are in cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyseal', '', 'Alt. of Epiphysial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphysial', '', 'Pertaining to, or having the nature of, an epiphysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyses', 'pl. ', 'of Epiphysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphysis', 'n.', 'The end, or other superficial part, of a bone, which\n   ossifies separately from the central portion, or diaphysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphysis', 'n.', 'The cerebral epiphysis, or pineal gland. See Pineal\n   gland, under Pineal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphytal', 'a.', 'Pertaining to an epiphyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyte', 'n.', 'An air plant which grows on other plants, but does not\n   derive its nourishment from them. See Air plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphyte', 'n.', 'A vegetable parasite growing on the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphytic', 'a.', 'Alt. of Epiphytical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiphytical', 'a.', 'Pertaining to, or having the nature of, an epiphyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiplastra', 'pl. ', 'of Epiplastron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiplastron', 'n.', 'One of the first pair of lateral plates in the\n   plastron of turtles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipleural', 'a.', 'Arising from the pleurapophysis of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiplexis', 'n.', 'A figure by which a person seeks to convince and move by\n   an elegant kind of upbraiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiploce', 'n.', 'A figure by which one striking circumstance is added, in\n   due gradation, to another; climax; e. g., \"He not only spared his\n   enemies, but continued them in employment; not only continued, but\n   advanced them.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiploic', 'a.', 'Relating to the epiploon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiploa', 'pl. ', 'of Epiploon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epiploon', 'n.', 'See Omentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodial', 'a.', 'Pertaining to the epipodialia or the parts of the limbs\n   to which they belong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodial', 'a.', 'Pertaining to the epipodium of Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodialia', 'pl. ', 'of Epipodiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodiale', 'n.', 'One of the bones of either the forearm or shank, the\n   epipodialia being the radius, ulna, tibia, and fibula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodite', 'n.', 'The outer branch of the legs in certain Crustacea. See\n   Maxilliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodia', 'pl. ', 'of Epipodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipodium', 'n.', 'One of the lateral lobes of the foot in certain\n   gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipolic', 'a.', 'Producing, or relating to, epipolism or fluorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipolism', 'n.', 'See Fluorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipolized', 'a.', 'Changed to the epipolic condition, or that in which the\n   phenomenon of fluorescence is presented; produced by fluorescence; as,\n   epipolized light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipteric', 'a.', 'Pertaining to a small Wormian bone sometimes present in\n   the human skull between the parietal and the great wing of the\n   sphenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipteric', 'n.', 'The epipteric bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipterygoid', 'a.', 'Situated upon or above the pterygoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipterygoid', 'n.', 'An epipterygoid bone or cartilage; the columella in\n   the skulls of many lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipubic', 'a.', 'Relating to the epipubis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipubes', 'pl. ', 'of Epipubis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epipubis', 'n.', 'A cartilage or bone in front of the pubis in some\n   amphibians and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopacy', 'n.', 'Government of the church by bishops; church government\n   by three distinct orders of ministers -- bishops, priests, and deacons\n   -- of whom the bishops have an authority superior and of a different\n   kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopal', 'a.', 'Governed by bishops; as, an episcopal church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopal', 'a.', 'Belonging to, or vested in, bishops; as, episcopal\n   jurisdiction or authority; the episcopal system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopalian', 'a.', 'Pertaining to bishops, or government by bishops;\n   episcopal; specifically, of or relating to the Protestant Episcopal\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopalian', 'n.', 'One who belongs to an episcopal church, or adheres to\n   the episcopal form of church government and discipline; a churchman;\n   specifically, in the United States, a member of the Protestant\n   Episcopal Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopalianism', 'n.', 'The doctrine and usages of Episcopalians;\n   episcopacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopally', 'adv.', 'By episcopal authority; in an episcopal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopant', 'n.', 'A bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcoparian', 'a.', 'Episcopal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopate', 'n.', 'A bishopric; the office and dignity of a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopate', 'n.', 'The collective body of bishops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopate', 'n.', 'The time of a bishops rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopated', 'imp. & p. p.', 'of Episcopate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopating', 'p. pr. & vb. n.', 'of Episcopate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopate', 'v. i.', 'To act as a bishop; to fill the office of a prelate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopicide', 'n.', 'The killing of a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopize', 'v. t.', 'To make a bishop of by consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopize', 'v. i.', 'To perform the duties of a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopy', 'n.', 'Survey; superintendence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episcopy', 'n.', 'Episcopacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episepalous', 'a.', 'Growing on the sepals or adnate to them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episkeletal', 'a.', 'Above or outside of the endoskeleton; epaxial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episodal', 'a.', 'Same as Episodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episode', 'n.', 'A separate incident, story, or action, introduced for the\n   purpose of giving a greater variety to the events related; an\n   incidental narrative, or digression, separable from the main subject,\n   but naturally arising from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episodial', 'a.', 'Pertaining to an episode; by way of episode; episodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episodic', 'a.', 'Alt. of Episodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episodical', 'a.', 'Of or pertaining to an episode; adventitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epispadias', 'n.', 'A deformity in which the urethra opens upon the top of\n   the penis, instead of at its extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epispastic', 'a.', 'Attracting the humors to the skin; exciting action in\n   the skin; blistering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epispastic', 'n.', 'An external application to the skin, which produces a\n   puriform or serous discharge by exciting inflammation; a vesicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episperm', 'n.', 'The skin or coat of a seed, especially the outer coat.\n   See Testa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epispermic', 'a.', 'Pertaining, or belonging, to the episperm, or covering\n   of a seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epispore', 'n.', 'The thickish outer coat of certain spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistaxis', 'n.', 'Bleeding at the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistemology', 'n.', 'The theory or science of the method or grounds of\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episternal', 'a.', 'Of or pertaining to the episternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episterna', 'pl. ', 'of Episternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episternum', 'n.', 'A median bone connected with the sternum, in many\n   vertebrates; the interclavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episternum', 'n.', 'Same as Epiplastron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episternum', 'n.', 'One of the lateral pieces next to the sternum in the\n   thorax of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistilbite', 'n.', 'A crystallized, transparent mineral of the Zeolite\n   family. It is a hydrous silicate of alumina and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistle', 'n.', 'A writing directed or sent to a person or persons; a\n   written communication; a letter; -- applied usually to formal,\n   didactic, or elegant letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistle', 'n.', 'One of the letters in the New Testament which were\n   addressed to their Christian brethren by Apostles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistle', 'v. t.', 'To write; to communicate in a letter or by writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistler', 'n.', 'A writer of epistles, or of an epistle of the New\n   Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistler', 'n.', 'The ecclesiastic who reads the epistle at the communion\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolar', 'a.', 'Epistolary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolary', 'a.', 'Pertaining to epistles or letters; suitable to letters\n   and correspondence; as, an epistolary style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolary', 'a.', 'Contained in letters; carried on by letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolean', 'n.', 'One who writes epistles; a correspondent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistoler', 'n.', 'One of the clergy who reads the epistle at the communion\n   service; an epistler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolet', 'n.', 'A little epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolic', 'a.', 'Alt. of Epistolical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolical', 'a.', 'Pertaining to letters or epistles; in the form or\n   style of letters; epistolary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolize', 'v. i.', 'To write epistles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolizer', 'n.', 'A writer of epistles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolographic', 'a.', 'Pertaining to the writing of letters; used in\n   writing letters; epistolary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistolography', 'n.', 'The art or practice of writing epistles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistoma', 'n.', 'Alt. of Epistome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistome', 'n.', 'The region between the antennae and the mouth, in\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistome', 'n.', 'A liplike organ that covers the mouth, in most Bryozoa.\n   See Illust., under Entoprocta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistrophe', 'n.', 'A figure in which successive clauses end with the same\n   word or affirmation; e. g., \"Are they Hebrews? so am I. Are they\n   Israelites? so am I.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epistyle', 'n.', 'A massive piece of stone or wood laid immediately on the\n   abacus of the capital of a column or pillar; -- now called architrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Episyllogism', 'n.', 'A syllogism which assumes as one of its premises a\n   proposition which was the conclusion of a preceding syllogism, called,\n   in relation to this, the prosyllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaph', 'n.', 'An inscription on, or at, a tomb, or a grave, in memory or\n   commendation of the one buried there; a sepulchral inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaph', 'n.', 'A brief writing formed as if to be inscribed on a\n   monument, as that concerning Alexander: \"Sufficit huic tumulus, cui non\n   sufficeret orbis.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaph', 'v. t.', 'To commemorate by an epitaph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaph', 'v. i.', 'To write or speak after the manner of an epitaph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitapher', 'n.', 'A writer of epitaphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaphial', 'a.', 'Alt. of Epitaphian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaphian', 'a.', 'Relating to, or of the nature of, an epitaph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaphic', 'a.', 'Pertaining to an epitaph; epitaphian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaphic', 'n.', 'An epitaph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitaphist', 'n.', 'An epitapher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitasis', 'n.', 'That part which embraces the main action of a play, poem,\n   and the like, and leads on to the catastrophe; -- opposed to protasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitasis', 'n.', 'The period of violence in a fever or disease; paroxysm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamic', 'a.', 'Belonging to, or designed for, an epithalamium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamiums', 'pl. ', 'of Epithalamium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamia', 'pl. ', 'of Epithalamium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamium', 'n.', 'A nuptial song, or poem in honor of the bride and\n   bridegroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamies', 'pl. ', 'of Epithalamy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithalamy', 'n.', 'Epithalamium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitheca', 'n.', 'A continuous and, usually, structureless layer which\n   covers more or less of the exterior of many corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithelial', 'a.', 'Of or pertaining to epithelium; as, epithelial cells;\n   epithelial cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithelioid', 'a.', 'Like epithelium; as, epithelioid cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithelioma', 'n.', 'A malignant growth containing epithelial cells; --\n   called also epithelial cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitheliums', 'pl. ', 'of Epithelium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithelia', 'pl. ', 'of Epithelium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithelium', 'n.', 'The superficial layer of cells lining the alimentary\n   canal and all its appendages, all glands and their ducts, blood vessels\n   and lymphatics, serous cavities, etc. It often includes the epidermis\n   (i. e., keratin-producing epithelial cells), and it is sometimes\n   restricted to the alimentary canal, the glands and their appendages, --\n   the term endothelium being applied to the lining membrane of the blood\n   vessels, lymphatics, and serous cavities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitheloid', 'a.', 'Epithelioid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithem', 'n.', 'Any external topical application to the body, except\n   ointments and plasters, as a poultice, lotion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithema', 'n.', 'A horny excrescence upon the beak of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithesis', 'n.', 'The addition of a letter at the end of a word, without\n   changing its sense; as, numb for num, whilst for whiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithet', 'n.', 'An adjective expressing some quality, attribute, or\n   relation, that is properly or specially appropriate to a person or\n   thing; as, a just man; a verdant lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithet', 'n.', 'Term; expression; phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithet', 'v. t.', 'To describe by an epithet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithetic', 'a.', 'Alt. of Epithetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithetical', 'a.', 'Pertaining to, or abounding with, epithets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithite', 'n.', 'A lazy, worthless fellow; a vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithumetic', 'a.', 'Epithumetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epithumetical', 'a.', 'Pertaining to sexual desire; sensual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitithides', 'n.', 'The uppermost member of the cornice of an entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomator', 'n.', 'An epitomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomes', 'pl. ', 'of Epitome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitome', 'n.', 'A work in which the contents of a former work are reduced\n   within a smaller space by curtailment and condensation; a brief\n   summary; an abridgement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitome', 'n.', 'A compact or condensed representation of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomist', 'n.', 'One who makes an epitome; one who abridges; an\n   epitomizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomized', 'imp. & p. p.', 'of Epitomize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomizing', 'p. pr. & vb. n.', 'of Epitomize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomize', 'v. t.', 'To make an epitome of; to shorten or abridge, as a\n   writing or discourse; to reduce within a smaller space; as, to\n   epitomize the works of Justin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomize', 'v. t.', 'To diminish, as by cutting off something; to curtail;\n   as, to epitomize words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitomizer', 'n.', 'An epitomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitrite', 'n.', 'A foot consisting of three long syllables and one short\n   syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitrochlea', 'n.', 'A projection on the outer side of the distal end of\n   the humerus; the external condyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitrochlear', 'a.', 'Relating to the epitrochlea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitrochoid', 'n.', 'A kind of curve. See Epicycloid, any Trochoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epitrope', 'n.', 'A figure by which permission is either seriously or\n   ironically granted to some one, to do what he proposes to do; e. g.,\n   \"He that is unjust, let him be unjust still.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizeuxis', 'n.', 'A figure by which a word is repeated with vehemence or\n   emphasis, as in the following lines: -');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epozoan', 'n.', 'An epizoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epozoic', 'a.', 'Living upon the exterior of another animal; ectozoic; --\n   said of external parasites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizoa', 'pl. ', 'of Epizoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizoon', 'n.', 'One of the artificial group of invertebrates of various\n   kinds, which live parasitically upon the exterior of other animals; an\n   ectozoon. Among them are the lice, ticks, many acari, the lerneans, or\n   fish lice, and other crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizootic', 'a.', 'Of or pertaining to an epizoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizootic', 'a.', 'Containing fossil remains; -- said of rocks, formations,\n   mountains, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizootic', 'a.', 'Of the nature of a disease which attacks many animals at\n   the same time; -- corresponding to epidemic diseases among men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizooty', 'n.', 'Alt. of Epizootic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epizootic', 'n.', 'An epizootic disease; a murrain; an epidemic influenza\n   among horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoch', 'n.', 'A fixed point of time, established in history by the\n   occurrence of some grand or remarkable event; a point of time marked by\n   an event of great subsequent influence; as, the epoch of the creation;\n   the birth of Christ was the epoch which gave rise to the Christian era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoch', 'n.', 'A period of time, longer or shorter, remarkable for events\n   of great subsequent influence; a memorable period; as, the epoch of\n   maritime discovery, or of the Reformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoch', 'n.', 'A division of time characterized by the prevalence of\n   similar conditions of the earth; commonly a minor division or part of a\n   period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoch', 'n.', 'The date at which a planet or comet has a longitude or\n   position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoch', 'n.', 'An arbitrary fixed date, for which the elements used in\n   computing the place of a planet, or other heavenly body, at any other\n   date, are given; as, the epoch of Mars; lunar elements for the epoch\n   March 1st, 1860.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epocha', 'n.', 'See Epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epochal', 'a.', 'Belonging to an epoch; of the nature of an epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epode', 'n.', 'The after song; the part of a lyric ode which follows the\n   strophe and antistrophe, -- the ancient ode being divided into strophe,\n   antistrophe, and epode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epode', 'n.', 'A species of lyric poem, invented by Archilochus, in which a\n   longer verse is followed by a shorter one; as, the Epodes of Horace. It\n   does not include the elegiac distich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epodic', 'a.', 'Pertaining to, or resembling, an epode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponym', 'n.', 'Alt. of Eponyme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponyme', 'n.', 'The hypothetical individual who is assumed as the person\n   from whom any race, city, etc., took its name; as, Hellen is an eponym\n   of the Hellenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponyme', 'n.', 'A name, as of a people, country, and the like, derived\n   from that of an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponymic', 'a.', 'Same as Eponymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponymist', 'n.', 'One from whom a race, tribe, city, or the like, took its\n   name; an eponym.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponymous', 'a.', 'Relating to an eponym; giving ones name to a tribe,\n   people, country, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eponymy', 'n.', 'The derivation of the name of a race, tribe, etc., from\n   that of a fabulous hero, progenitor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epoophoron', 'n.', 'See Parovarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epopee', 'n.', 'Alt. of Epopoeia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epopoeia', 'n.', 'An epic poem; epic poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epopt', 'n.', 'One instructed in the mysteries of a secret system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epos', 'n.', 'An epic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epotation', 'n.', 'A drinking up; a quaffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eprouvette', 'n.', 'An apparatus for testing or proving the strength of\n   gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epsomite', 'n.', 'Native sulphate of magnesia or Epsom salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epsom salts', '', 'Alt. of salt');
  INSERT INTO words (word, word_type, description) VALUES  (    'salt', '', 'Sulphate of magnesia having cathartic qualities; -- originally\n   prepared by boiling down the mineral waters at Epsom, England, --\n   whence the name; afterwards prepared from sea water; but now from\n   certain minerals, as from siliceous hydrate of magnesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulary', 'a.', 'Of or pertaining to a feast or banquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulation', 'n.', 'A feasting or feast; banquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulis', 'n.', 'A hard tumor developed from the gums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulose', 'a.', 'Feasting to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulosity', 'n.', 'A feasting to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulotic', 'a.', 'Promoting the skinning over or healing of sores; as, an\n   epulotic ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epulotic', 'n.', 'An epulotic agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epuration', 'n.', 'Purification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Epure', 'n.', 'A draught or model from which to build; especially, one of\n   the full size of the work to be done; a detailed drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equability', 'n.', 'The quality or condition of being equable; evenness or\n   uniformity; as, equability of temperature; the equability of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equable', 'a.', 'Equal and uniform; continuing the same at different times;\n   -- said of motion, and the like; uniform in surface; smooth; as, an\n   equable plain or globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equable', 'a.', 'Uniform in action or intensity; not variable or changing;\n   -- said of the feelings or temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equableness', 'n.', 'Quality or state of being equable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equably', 'adv.', 'In an equable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Agreeing in quantity, size, quality, degree, value, etc.;\n   having the same magnitude, the same value, the same degree, etc.; --\n   applied to number, degree, quantity, and intensity, and to any subject\n   which admits of them; neither inferior nor superior, greater nor less,\n   better nor worse; corresponding; alike; as, equal quantities of land,\n   water, etc. ; houses of equal size; persons of equal stature or\n   talents; commodities of equal value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Bearing a suitable relation; of just proportion; having\n   competent power, abilities, or means; adequate; as, he is not equal to\n   the task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Not variable; equable; uniform; even; as, an equal movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Evenly balanced; not unduly inclining to either side;\n   characterized by fairness; unbiased; impartial; equitable; just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Of the same interest or concern; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Intended for voices of one kind only, either all male or all\n   female; -- opposed to mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'a.', 'Exactly agreeing with respect to quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'n.', 'One not inferior or superior to another; one having the same\n   or a similar age, rank, station, office, talents, strength, or other\n   quality or condition; an equal quantity or number; as, \"If equals be\n   taken from equals the remainders are equal.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'n.', 'State of being equal; equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equaled', 'imp. & p. p.', 'of Equal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalled', '', 'of Equal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equaling', 'p. pr. & vb. n.', 'of Equal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalling', '', 'of Equal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'v. t.', 'To be or become equal to; to have the same quantity, the\n   same value, the same degree or rank, or the like, with; to be\n   commen/urate with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'v. t.', 'To make equal return to; to recompense fully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equal', 'v. t.', 'To make equal or equal to; to equalize; hence, to compare\n   or regard as equals; to put on equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalitarian', 'n.', 'One who believes in equalizing the condition of men;\n   a leveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalities', 'pl. ', 'of Equality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equality', 'n.', 'The condition or quality of being equal; agreement in\n   quantity or degree as compared; likeness in bulk, value, rank,\n   properties, etc.; as, the equality of two bodies in length or\n   thickness; an equality of rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equality', 'n.', 'Sameness in state or continued course; evenness;\n   uniformity; as, an equality of temper or constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equality', 'n.', 'Evenness; uniformity; as, an equality of surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equality', 'n.', 'Exact agreement between two expressions or magnitudes\n   with respect to quantity; -- denoted by the symbol =; thus, a = x\n   signifies that a contains the same number and kind of units of measure\n   that x does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalization', 'n.', 'The act of equalizing, or state of being equalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalized', 'imp. & p. p.', 'of Equalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalizing', 'p. pr. & vb. n.', 'of Equalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalize', 'v. t.', 'To make equal; to cause to correspond, or be like, in\n   amount or degree as compared; as, to equalize accounts, burdens, or\n   taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalize', 'v. t.', 'To pronounce equal; to compare as equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalize', 'v. t.', 'To be equal to; equal; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalizer', 'n.', 'One who, or that which, equalizes anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equally', 'adv.', 'In an equal manner or degree in equal shares or\n   proportion; with equal and impartial justice; without difference;\n   alike; evenly; justly; as, equally taxed, furnished, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equalness', 'n.', 'Equality; evenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equangular', 'a.', 'Having equal angles; equiangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equanimity', 'n.', 'Evenness of mind; that calm temper or firmness of mind\n   which is not easily elated or depressed; patience; calmness; composure;\n   as, to bear misfortunes with equanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equanimous', 'a.', 'Of an even, composed frame of mind; of a steady temper;\n   not easily elated or depressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equant', 'n.', 'A circle around whose circumference a planet or the center\n   of ann epicycle was conceived to move uniformly; -- called also\n   eccentric equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equated', 'imp. & p. p.', 'of Equate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equating', 'p. pr. & vb. n.', 'of Equate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equate', 'v. t.', 'To make equal; to reduce to an average; to make such an\n   allowance or correction in as will reduce to a common standard of\n   comparison; to reduce to mean time or motion; as, to equate payments;\n   to equate lines of railroad for grades or curves; equated distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equation', 'n.', 'A making equal; equal division; equality; equilibrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equation', 'n.', 'An expression of the condition of equality between two\n   algebraic quantities or sets of quantities, the sign = being placed\n   between them; as, a binomial equation; a quadratic equation; an\n   algebraic equation; a transcendental equation; an exponential equation;\n   a logarithmic equation; a differential equation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equation', 'n.', 'A quantity to be applied in computing the mean place or\n   other element of a celestial body; that is, any one of the several\n   quantities to be added to, or taken from, its position as calculated on\n   the hypothesis of a mean uniform motion, in order to find its true\n   position as resulting from its actual and unequal motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equator', 'n.', 'The imaginary great circle on the earths surface,\n   everywhere equally distant from the two poles, and dividing the earths\n   surface into two hemispheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equator', 'n.', 'The great circle of the celestial sphere, coincident with\n   the plane of the earths equator; -- so called because when the sun is\n   in it, the days and nights are of equal length; hence called also the\n   equinoctial, and on maps, globes, etc., the equinoctial line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equatorial', 'a.', 'Of or pertaining to the equator; as, equatorial\n   climates; also, pertaining to an equatorial instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equatorial', 'n.', 'An instrument consisting of a telescope so mounted as\n   to have two axes of motion at right angles to each other, one of them\n   parallel to the axis of the earth, and each carrying a graduated\n   circle, the one for measuring declination, and the other right\n   ascension, or the hour angle, so that the telescope may be directed,\n   even in the daytime, to any star or other object whose right ascension\n   and declination are known. The motion in right ascension is sometimes\n   communicated by clockwork, so as to keep the object constantly in the\n   field of the telescope. Called also an equatorial telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equatorially', 'adv.', 'So as to have motion or direction parallel to the\n   equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equerries', 'pl. ', 'of Equerry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equerry', 'n.', 'A large stable or lodge for horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equerry', 'n.', 'An officer of princes or nobles, charged with the care of\n   their horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equery', 'n.', 'Same as Equerry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrian', 'a.', 'Of or pertaining to horses or horsemen, or to\n   horsemanship; as, equestrian feats, or games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrian', 'a.', 'Being or riding on horseback; mounted; as, an\n   equestrian statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrian', 'a.', 'Belonging to, or composed of, the ancient Roman\n   equities or knights; as, the equestrian order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrian', 'n.', 'One who rides on horseback; a horseman; a rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrianism', 'n.', 'The art of riding on horseback; performance on\n   horseback; horsemanship; as, feats equestrianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equestrienne', 'n.', 'A woman skilled in equestrianism; a horsewoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equi-', '', 'A prefix, meaning equally; as, equidistant; equiangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiangled', 'a.', 'Equiangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiangular', 'a.', 'Having equal angles; as, an equiangular figure; a\n   square is equiangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equibalance', 'n.', 'Equal weight; equiponderance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equibalanced', 'imp. & p. p.', 'of Equibalance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equibalancing', 'p. pr. & vb. n.', 'of Equibalance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equibalance', 'v. t.', 'To make of equal weight; to balance equally; to\n   counterbalance; to equiponderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equicrescent', 'a.', 'Increasing by equal increments; as, an equicrescent\n   variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equicrural', 'a.', 'Having equal legs or sides; isosceles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equicrure', 'a.', 'Equicrural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equidifferent', 'a.', 'Having equal differences; as, the terms of\n   arithmetical progression are equidifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equidistance', 'n.', 'Equal distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equidistant', 'a.', 'Being at an equal distance from the same point or\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equidiurnal', 'a.', 'Pertaining to the time of equal day and night; --\n   applied to the equinoctial line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiform', 'a.', 'Having the same form; uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilateral', 'a.', 'Having all the sides equal; as, an equilateral\n   triangle; an equilateral polygon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilateral', 'n.', 'A side exactly corresponding, or equal, to others;\n   also, a figure of equal sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrated', 'imp. & p. p.', 'of Equilibrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrating', 'p. pr. & vb. n.', 'of Equilibrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrate', 'v. t.', 'To balance two scales, sides, or ends; to keep even\n   with equal weight on each side; to keep in equipoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibration', 'n.', 'Act of keeping a balance, or state of being\n   balanced; equipoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibration', 'n.', 'The process by which animal and vegetable organisms\n   preserve a physiological balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrious', 'a.', 'Evenly poised; balanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrist', 'n.', 'One who balances himself in unnatural positions and\n   hazardous movements; a balancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrity', 'n.', 'The state of being balanced; equality of weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibriums', 'pl. ', 'of Equilibrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibria', 'pl. ', 'of Equilibrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrium', 'n.', 'Equality of weight or force; an equipoise or a state\n   of rest produced by the mutual counteraction of two or more forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrium', 'n.', 'A level position; a just poise or balance in respect\n   to an object, so that it remains firm; equipoise; as, to preserve the\n   equilibrium of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equilibrium', 'n.', 'A balancing of the mind between motives or reasons,\n   with consequent indecision and doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equimomental', 'a.', 'Having equal moments of inertia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equimultiple', 'a.', 'Multiplied by the same number or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equimultiple', 'n.', 'One of the products arising from the multiplication\n   of two or more quantities by the same number or quantity. Thus, seven\n   times 2, or 14, and seven times 4, or 28, are equimultiples of 2 and 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinal', 'a.', 'See Equine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equine', 'a.', 'Of, pertaining to, or resembling, a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinia', 'n.', 'Glanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinoctial', 'a.', 'Pertaining to an equinox, or the equinoxes, or to the\n   time of equal day and night; as, the equinoctial line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinoctial', 'a.', 'Pertaining to the regions or climate of the\n   equinoctial line or equator; in or near that line; as, equinoctial\n   heat; an equinoctial sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinoctial', 'a.', 'Pertaining to the time when the sun enters the\n   equinoctial points; as, an equinoctial gale or storm, that is, one\n   happening at or near the time of the equinox, in any part of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinoctial', 'n.', 'The equinoctial line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinoctially', 'adv.', 'Towards the equinox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinox', 'n.', 'The time when the sun enters one of the equinoctial\n   points, that is, about March 21 and September 22. See Autumnal equinox,\n   Vernal equinox, under Autumnal and Vernal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinox', 'n.', 'Equinoctial wind or storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equinumerant', 'a.', 'Equal as to number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipped', 'imp. & p. p.', 'of Equip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipping', 'p. pr. & vb. n.', 'of Equip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equip', 'v. t.', 'To furnish for service, or against a need or exigency; to\n   fit out; to supply with whatever is necessary to efficient action in\n   any way; to provide with arms or an armament, stores, munitions,\n   rigging, etc.; -- said esp. of ships and of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equip', 'v. t.', 'To dress up; to array; accouter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipage', 'n.', 'Furniture or outfit, whether useful or ornamental;\n   especially, the furniture and supplies of a vessel, fitting her for a\n   voyage or for warlike purposes, or the furniture and necessaries of an\n   army, a body of troops, or a single soldier, including whatever is\n   necessary for efficient service; equipments; accouterments;\n   habiliments; attire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipage', 'n.', 'Retinue; train; suite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipage', 'n.', 'A carriage of state or of pleasure with all that\n   accompanies it, as horses, liveried servants, etc., a showy turn-out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipaged', 'a.', 'Furnished with equipage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiparable', 'a.', 'Comparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiparate', 'v. t.', 'To compare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipedal', 'a.', 'Equal-footed; having the pairs of feet equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipendency', 'n.', 'The act or condition of hanging in equipoise; not\n   inclined or determined either way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipensate', 'v. t.', 'To weigh equally; to esteem alike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipment', 'n.', 'The act of equipping, or the state of being equipped, as\n   for a voyage or expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipment', 'n.', 'Whatever is used in equipping; necessaries for an\n   expedition or voyage; the collective designation for the articles\n   comprising an outfit; equipage; as, a railroad equipment (locomotives,\n   cars, etc. ; for carrying on business); horse equipments; infantry\n   equipments; naval equipments; laboratory equipments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipoise', 'n.', 'Equality of weight or force; hence, equilibrium; a state\n   in which the two ends or sides of a thing are balanced, and hence\n   equal; state of being equally balanced; -- said of moral, political, or\n   social interests or forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipoise', 'n.', 'Counterpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollence', 'n.', 'Alt. of Equipollency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollency', 'n.', 'Equality of power, force, signification, or\n   application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollency', 'n.', 'Sameness of signification of two or more propositions\n   which differ in language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollent', 'a.', 'Having equal power or force; equivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollent', 'a.', 'Having equivalent signification and reach; expressing\n   the same thing, but differently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipollently', 'adv.', 'With equal power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderance', 'n.', 'Alt. of Equiponderancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderancy', 'n.', 'Equality of weight; equipoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderant', 'a.', 'Being of the same weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderate', 'v. i.', 'To be equal in weight; to weigh as much as\n   another thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderate', 'v. t.', 'To make equal in weight; to counterbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiponderous', 'a.', 'Having equal weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipondious', 'a.', 'Of equal weight on both sides; balanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equipotential', 'a.', 'Having the same potential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiradical', 'a.', 'Equally radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equirotal', 'a.', 'Having wheels of the same size or diameter; having equal\n   rotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equisetaceous', 'a.', 'Belonging to the Equisetaceae, or Horsetail family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equisetiform', 'a.', 'Having the form of the equisetum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equiseta', 'pl. ', 'of Equisetum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equisetum', 'n.', 'A genus of vascular, cryptogamic, herbaceous plants; --\n   also called horsetails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equisonance', 'n.', 'An equal sounding; the consonance of the unison and\n   its octaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equisonant', 'a.', 'Of the same or like sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitable', 'a.', 'Possessing or exhibiting equity; according to natural\n   right or natural justice; marked by a due consideration for what is\n   fair, unbiased, or impartial; just; as an equitable decision; an\n   equitable distribution of an estate; equitable men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitable', 'a.', 'That can be sustained or made available or effective in\n   a court of equity, or upon principles of equity jurisprudence; as, an\n   equitable estate; equitable assets, assignment, mortgage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitableness', 'n.', 'The quality of being equitable, just, or impartial;\n   as, the equitableness of a judge, a decision, or distribution of\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitably', 'adv.', 'In an equitable manner; justly; as, the laws should be\n   equitably administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitancy', 'n.', 'Horsemanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitant', 'a.', 'Mounted on, or sitting upon, a horse; riding on\n   horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitant', 'a.', 'Overlapping each other; -- said of leaves whose bases are\n   folded so as to overlap and bestride the leaves within or above them,\n   as in the iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitation', 'n.', 'A riding, or the act of riding, on horseback;\n   horsemanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equitemporaneous', 'a.', 'Contemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equites', 'n. pl', 'An order of knights holding a middle place between the\n   senate and the commonalty; members of the Roman equestrian order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equities', 'pl. ', 'of Equity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equity', 'n.', 'Equality of rights; natural justice or right; the giving,\n   or desiring to give, to each man his due, according to reason, and the\n   law of God to man; fairness in determination of conflicting claims;\n   impartiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equity', 'n.', 'An equitable claim; an equity of redemption; as, an equity\n   to a settlement, or wifes equity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equity', 'n.', 'A system of jurisprudence, supplemental to law, properly so\n   called, and complemental of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalence', 'n.', 'The condition of being equivalent or equal; equality\n   of worth, value, signification, or force; as, an equivalence of\n   definitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalence', 'n.', 'Equal power or force; equivalent amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalence', 'n.', 'The quantity of the combining power of an atom,\n   expressed in hydrogen units; the number of hydrogen atoms can combine\n   with, or be exchanged for; valency. See Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalence', 'n.', 'The degree of combining power as determined by\n   relative weight. See Equivalent, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalence', 'v. t.', 'To be equivalent or equal to; to counterbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalency', 'n.', 'Same as Equivalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'a.', 'Equal in wortir or value, force, power, effect, import,\n   and the like; alike in significance and value; of the same import or\n   meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'a.', 'Equal in measure but not admitting of superposition; --\n   applied to magnitudes; as, a square may be equivalent to a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'a.', 'Contemporaneous in origin; as, the equivalent strata of\n   different countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'n.', 'Something equivalent; that which is equal in value,\n   worth, weight, or force; as, to offer an equivalent for damage done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'n.', 'That comparative quantity by weight of an element which\n   possesses the same chemical value as other elements, as determined by\n   actual experiment and reference to the same standard. Specifically: (a)\n   The comparative proportions by which one element replaces another in\n   any particular compound; thus, as zinc replaces hydrogen in\n   hydrochloric acid, their equivalents are 32.5 and 1. (b) The combining\n   proportion by weight of a substance, or the number expressing this\n   proportion, in any particular compound; as, the equivalents of hydrogen\n   and oxygen in water are respectively 1 and 8, and in hydric dioxide 1\n   and 16.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'n.', 'A combining unit, whether an atom, a radical, or a\n   molecule; as, in acid salt two or more equivalents of acid unite with\n   one or more equivalents of base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalent', 'v. t.', 'To make the equivalent to; to equal; equivalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalently', 'adv.', 'In an equal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalue', 'v. t.', 'To put an equal value upon; to put (something) on a\n   par with another thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalve', 'a.', 'Alt. of Equivalved');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalved', 'a.', 'Having the valves equal in size and from, as in most\n   bivalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivalvular', 'a.', 'Same as Equivalve or Equivalved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocacy', 'n.', 'Equivocalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocal', 'a.', '(Literally, called equally one thing or the other;\n   hence:) Having two significations equally applicable; capable of double\n   interpretation; of doubtful meaning; ambiguous; uncertain; as,\n   equivocal words; an equivocal sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocal', 'a.', 'Capable of being ascribed to different motives, or of\n   signifying opposite feelings, purposes, or characters; deserving to be\n   suspected; as, his actions are equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocal', 'a.', 'Uncertain, as an indication or sign; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocal', 'n.', 'A word or expression capable of different meanings; an\n   ambiguous term; an equivoque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocally', 'adv.', 'In an equivocal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocalness', 'n.', 'The state of being equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocated', 'imp. & p. p.', 'of Equivocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocating', 'p. pr. & vb. n.', 'of Equivocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocate', 'a.', 'To use words of equivocal or doubtful signification; to\n   express ones opinions in terms which admit of different senses, with\n   intent to deceive; to use ambiguous expressions with a view to mislead;\n   as, to equivocate is the work of duplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocate', 'v. t.', 'To render equivocal or ambiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocation', 'n.', 'The use of expressions susceptible of a double\n   signification, with a purpose to mislead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocator', 'n.', 'One who equivocates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivocatory', 'a.', 'Indicating, or characterized by, equivocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivoque', 'n.', 'Alt. of Equivoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivoke', 'n.', 'An ambiguous term; a word susceptible of different\n   significations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivoke', 'n.', 'An equivocation; a guibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equivorous', 'a.', 'Feeding on horseflesh; as, equivorous Tartars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Equus', 'n.', 'A genus of mammals, including the horse, ass, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-er', '', '.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-er', '', 'The termination of many English words, denoting the agent; --\n   applied either to men or things; as in hater, farmer, heater, grater.\n   At the end of names of places, -er signifies a man of the place; as,\n   Londoner, i. e., London man.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-er', '', 'A suffix used to form the comparative degree of adjectives and\n   adverbs; as, warmer, sooner, lat(e)er, earl(y)ier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eras', 'pl. ', 'of Era');
  INSERT INTO words (word, word_type, description) VALUES  (    'Era', 'n.', 'A fixed point of time, usually an epoch, from which a series\n   of years is reckoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Era', 'n.', 'A period of time reckoned from some particular date or epoch;\n   a succession of years dating from some important event; as, the era of\n   Alexander; the era of Christ, or the Christian era (see under\n   Christian).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Era', 'n.', 'A period of time in which a new order of things prevails; a\n   signal stage of history; an epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradiated', 'imp. & p. p.', 'of Eradiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradiating', 'p. pr. & vb. n.', 'of Eradiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradiate', 'v. i.', 'To shoot forth, as rays of light; to beam; to radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradiation', 'n.', 'Emission of radiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicable', 'a.', 'Capable of being eradicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicated', 'imp. & p. p.', 'of Eradicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicating', 'p. pr. & vb. n.', 'of Eradicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicate', 'v. t.', 'To pluck up by the roots; to root up; as, an oak tree\n   eradicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicate', 'v. t.', 'To root out; to destroy utterly; to extirpate; as, to\n   eradicate diseases, or errors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradication', 'n.', 'The act of plucking up by the roots; a rooting out;\n   extirpation; utter destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradication', 'n.', 'The state of being plucked up by the roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicative', 'a.', 'Tending or serving to eradicate; curing or destroying\n   thoroughly, as a disease or any evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eradicative', 'n.', 'A medicine that effects a radical cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erasable', 'a.', 'Capable of being erased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erased', 'imp. & p. p.', 'of Erase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erasing', 'p. pr. & vb. n.', 'of Erase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erase', 'v. t.', 'To rub or scrape out, as letters or characters written,\n   engraved, or painted; to efface; to expunge; to cross out; as, to erase\n   a word or a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erase', 'v. t.', 'Fig.: To obliterate; to expunge; to blot out; -- used of\n   ideas in the mind or memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erased', 'p. pr. & a.', 'Rubbed or scraped out; effaced; obliterated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erased', 'p. pr. & a.', 'Represented with jagged and uneven edges, as is\n   torn off; -- used esp. of the head or limb of a beast. Cf. Couped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erasement', 'n.', 'The act of erasing; a rubbing out; expunction;\n   obliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eraser', 'n.', 'One who, or that which, erases; esp., a sharp instrument or\n   a piece of rubber used to erase writings, drawings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erasion', 'n.', 'The act of erasing; a rubbing out; obliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erastian', 'n.', 'One of the followers of Thomas Erastus, a German\n   physician and theologian of the 16th century. He held that the\n   punishment of all offenses should be referred to the civil power, and\n   that holy communion was open to all. In the present day, an Erastian is\n   one who would see the church placed entirely under the control of the\n   State.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erastianism', 'n.', 'The principles of the Erastains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erasure', 'n.', 'The act of erasing; a scratching out; obliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erative', 'a.', 'Pertaining to the Muse Erato who presided over amatory\n   poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erato', 'n.', 'The Muse who presided over lyric and amatory poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erbium', 'n.', 'A rare metallic element associated with several other rare\n   elements in the mineral gadolinite from Ytterby in Sweden. Symbol Er.\n   Atomic weight 165.9. Its salts are rose-colored and give characteristic\n   spectra. Its sesquioxide is called erbia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ercedeken', 'n.', 'An archdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erd', 'n.', 'The earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ere', 'adv.', 'Before; sooner than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ere', 'adv.', 'Rather than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ere', 'v. t.', 'To plow. [Obs.] See Ear, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erebus', 'n.', 'A place of nether darkness, being the gloomy space through\n   which the souls passed to Hades. See Miltons \"Paradise Lost,\" Book\n   II., line 883.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erebus', 'n.', 'The son of Chaos and brother of Nox, who dwelt in Erebus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Upright, or having a vertical position; not inverted; not\n   leaning or bent; not prone; as, to stand erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Directed upward; raised; uplifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Bold; confident; free from depression; undismayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Watchful; alert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Standing upright, with reference to the earths surface, or\n   to the surface to which it is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'a.', 'Elevated, as the tips of wings, heads of serpents, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erected', 'imp. & p. p.', 'of Erect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erecting', 'p. pr. & vb. n.', 'of Erect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To raise and place in an upright or perpendicular\n   position; to set upright; to raise; as, to erect a pole, a flagstaff, a\n   monument, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To raise, as a building; to build; to construct; as, to\n   erect a house or a fort; to set up; to put together the component parts\n   of, as of a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To lift up; to elevate; to exalt; to magnify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To animate; to encourage; to cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To set up as an assertion or consequence from premises,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. t.', 'To set up or establish; to found; to form; to institute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erect', 'v. i.', 'To rise upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erectable', 'a.', 'Capable of being erected; as, an erectable feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erecter', 'n.', 'An erector; one who raises or builds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erectile', 'a.', 'Capable of being erected; susceptible of being erected of\n   dilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erectility', 'n.', 'The quality or state of being erectile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erection', 'n.', 'The act of erecting, or raising upright; the act of\n   constructing, as a building or a wall, or of fitting together the parts\n   of, as a machine; the act of founding or establishing, as a\n   commonwealth or an office; also, the act of rousing to excitement or\n   courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erection', 'n.', 'The state of being erected, lifted up, built,\n   established, or founded; exaltation of feelings or purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erection', 'n.', 'State of being stretched to stiffness; tension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erection', 'n.', 'Anything erected; a building of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erection', 'n.', 'The state of a part which, from having been soft, has\n   become hard and swollen by the accumulation of blood in the erectile\n   tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erective', 'a.', 'Making erect or upright; raising; tending to erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erectly', 'adv.', 'In an erect manner or posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erectness', 'n.', 'Uprightness of posture or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erecto-patent', 'a.', 'Having a position intermediate between erect and\n   patent, or spreading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erecto-patent', 'a.', 'Standing partially spread and erect; -- said of the\n   wings of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erector', 'n.', 'One who, or that which, erects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erector', 'n.', 'A muscle which raises any part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erector', 'n.', 'An attachment to a microscope, telescope, or other optical\n   instrument, for making the image erect instead of inverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erelong', 'adv.', 'Before the /apse of a long time; soon; -- usually\n   separated, ere long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremacausis', 'n.', 'A gradual oxidation from exposure to air and moisture,\n   as in the decay of old trees or of dead animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremitage', 'n.', 'See Hermitage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremite', 'n.', 'A hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremitic', 'a.', 'Alt. of Eremitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremitical', 'a.', 'Of or pertaining to an eremite; hermitical; living in\n   solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremitish', 'a.', 'Eremitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eremitism', 'n.', 'The state of a hermit; a living in seclusion from social\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eretation', 'n.', 'A creeping forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ereption', 'n.', 'A snatching away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erethism', 'n.', 'A morbid degree of excitement or irritation in an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erethistic', 'a.', 'Relating to erethism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erewhile', 'adv.', 'Alt. of Erewhiles');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erewhiles', 'adv.', 'Some time ago; a little while before; heretofore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erven', 'pl. ', 'of Erf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erf', 'n.', 'A garden plot, usually about half an acre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erg', 'n.', 'The unit of work or energy in the C. G. S. system, being the\n   amount of work done by a dyne working through a distance of one\n   centimeter; the amount of energy expended in moving a body one\n   centimeter against a force of one dyne. One foot pound is equal to\n   13,560,000 ergs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergat', 'v. t.', 'To deduce logically, as conclusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergo', 'conj. / adv.', 'Therefore; consequently; -- often used in a jocular\n   way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergot', 'n.', 'A diseased condition of rye and other cereals, in which the\n   grains become black, and often spur-shaped. It is caused by a parasitic\n   fungus, Claviceps purpurea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergot', 'n.', 'The mycelium or spawn of this fungus infecting grains of rye\n   and wheat. It is a powerful remedial agent, and also a dangerous\n   poison, and is used as a means of hastening childbirth, and to arrest\n   bleeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergot', 'n.', 'A stub, like soft horn, about the size of a chestnut,\n   situated behind and below the pastern joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergot', 'n.', 'See 2d Calcar, 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotic', 'a.', 'Pertaining to, or derived from, ergot; as, ergotic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotin', 'n.', 'An extract made from ergot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotine', '', 'A powerful astringent alkaloid extracted from ergot as a\n   brown, amorphous, bitter substance. It is used to produce contraction\n   of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotism', 'n.', 'A logical deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotism', 'n.', 'A diseased condition produced by eating rye affected with\n   the ergot fungus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ergotized', 'a.', 'Affected with the ergot fungus; as, ergotized rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eriach', 'n.', 'Alt. of Eric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eric', 'n.', 'A recompense formerly given by a murderer to the relatives of\n   the murdered person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erica', 'n.', 'A genus of shrubby plants, including the heaths, many of\n   them producing beautiful flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ericaceous', 'a.', 'Belonging to the Heath family, or resembling plants of\n   that family; consisting of heats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ericinol', 'n.', 'A colorless oil (quickly becoming brown), with a pleasant\n   odor, obtained by the decomposition of ericolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ericius', 'n.', 'The Vulgate rendering of the Hebrew word qip/d, which in\n   the \"Authorized Version\" is translated bittern, and in the Revised\n   Version, porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ericolin', 'n.', 'A glucoside found in the bearberry (and others of the\n   Ericaceae), and extracted as a bitter, yellow, amorphous mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eridanus', 'n.', 'A long, winding constellation extending southward from\n   Taurus and containing the bright star Achernar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erigible', 'a.', 'Capable of being erected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erin', 'n.', 'An early, and now a poetic, name of Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erinaceous', 'a.', 'Of the Hedgehog family; like, or characteristic of, a\n   hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eringo', 'n.', 'The sea holly. See Eryngo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erinite', 'n.', 'A hydrous arseniate of copper, of an emerald-green color;\n   -- so called from Erin, or Ireland, where it occurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erinyes', 'pl. ', 'of Erinys');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erinys', 'n.', 'An avenging deity; one of the Furies; sometimes, conscience\n   personified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eriometer', 'n.', 'An instrument for measuring the diameters of minute\n   particles or fibers, from the size of the colored rings produced by the\n   diffraction of the light in which the objects are viewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eristalis', 'n.', 'A genus of dipterous insects whose young (called\n   rat-tailed larvae) are remarkable for their long tapering tail, which\n   spiracles at the tip, and for their ability to live in very impure and\n   salt waters; -- also called drone fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eristic', 'a.', 'Alt. of Eristical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eristical', 'a.', 'Controversial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erke', 'a.', 'ASlothful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erlking', 'n.', 'A personification, in German and Scandinavian mythology,\n   of a spirit natural power supposed to work mischief and ruin, esp. to\n   children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erme', 'v. i.', 'To grieve; to feel sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermelin', 'n.', 'Alt. of Ermilin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermilin', 'n.', 'See Ermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermin', 'n.', 'An Armenian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermine', 'n.', 'A valuable fur-bearing animal of the genus Mustela (M.\n   erminea), allied to the weasel; the stoat. It is found in the northern\n   parts of Asia, Europe, and America. In summer it is brown, but in\n   winter it becomes white, except the tip of the tail, which is always\n   black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermine', 'n.', 'The fur of the ermine, as prepared for ornamenting garments\n   of royalty, etc., by having the tips of the tails, which are black,\n   arranged at regular intervals throughout the white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermine', 'n.', 'By metonymy, the office or functions of a judge, whose\n   state robe, lined with ermine, is emblematical of purity and honor\n   without stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermine', 'n.', 'One of the furs. See Fur (Her.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermine', 'v. t.', 'To clothe with, or as with, ermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermined', 'a.', 'Clothed or adorned with the fur of the ermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermines', 'n.', 'Alt. of Erminois');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erminois', 'n.', 'See Note under Ermine, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ermit', 'n.', 'A hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ern', 'n.', 'Alt. of Erne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erne', 'n.', 'A sea eagle, esp. the European white-tailed sea eagle\n   (Haliaeetus albicilla).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ern', 'v. i.', 'To stir with strong emotion; to grieve; to mourn.\n   [Corrupted into yearn in modern editions of Shakespeare.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ernest', 'n.', 'See Earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ernestful', 'a.', 'Serious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroded', 'imp. & p. p.', 'of Erode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroding', 'p. pr. & vb. n.', 'of Erode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erode', 'v. t.', 'To eat into or away; to corrode; as, canker erodes the\n   flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroded', 'p. p. & a.', 'Eaten away; gnawed; irregular, as if eaten or worn\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroded', 'p. p. & a.', 'Having the edge worn away so as to be jagged or\n   irregularly toothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erodent', 'n.', 'A medicine which eats away extraneous growths; a caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erogated', 'imp. & p. p.', 'of Erogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erogating', 'p. pr. & vb. n.', 'of Erogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erogate', 'v. t.', 'To lay out, as money; to deal out; to expend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erogation', 'n.', 'The act of giving out or bestowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eros', 'n.', 'Love; the god of love; -- by earlier writers represented as\n   one of the first and creative gods, by later writers as the son of\n   Aphrodite, equivalent to the Latin god Cupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erose', 'a.', 'Irregular or uneven as if eaten or worn away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erose', 'a.', 'Jagged or irregularly toothed, as if nibbled out or gnawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erosion', 'n.', 'The act or operation of eroding or eating away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erosion', 'n.', 'The state of being eaten away; corrosion; canker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erosive', 'a.', 'That erodes or gradually eats away; tending to erode;\n   corrosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erostrate', 'a.', 'Without a beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroteme', 'n.', 'A mark indicating a question; a note of interrogation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erotesis', 'n.', 'A figure o/ speech by which a strong affirmation of the\n   contrary, is implied under the form o/ an earnest interrogation, as in\n   the following lines; -');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erotic', 'a.', 'Alt. of Erotical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erotical', 'a.', 'Of or pertaining to the passion of love; treating of\n   love; amatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erotic', 'n.', 'An amorous composition or poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eroticism', 'n.', 'Erotic quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erpetologist', 'n.', 'Herpetologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erpetology', 'n.', 'Herpetology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erred', 'imp. & p. p.', 'of Err');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erring', 'p. pr. & vb. n.', 'of Err');
  INSERT INTO words (word, word_type, description) VALUES  (    'Err', 'v. i.', 'To wander; to roam; to stray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Err', 'v. i.', 'To deviate from the true course; to miss the thing aimed\n   at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Err', 'v. i.', 'To miss intellectual truth; to fall into error; to mistake\n   in judgment or opinion; to be mistaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Err', 'v. i.', 'To deviate morally from the right way; to go astray, in a\n   figurative sense; to do wrong; to sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Err', 'v. i.', 'To offend, as by erring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errable', 'a.', 'Liable to error; fallible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errableness', 'n.', 'Liability to error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errabund', 'a.', 'Erratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errancy', 'n.', 'A wandering; state of being in error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errand', 'n.', 'A special business intrusted to a messenger; something to\n   be told or done by one sent somewhere for the purpose; often, a verbal\n   message; a commission; as, the servant was sent on an errand; to do an\n   errand. Also, ones purpose in going anywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errant', 'a.', 'Wandering; deviating from an appointed course, or from a\n   direct path; roving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errant', 'a.', 'Notorious; notoriously bad; downright; arrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errant', 'a.', 'Journeying; itinerant; -- formerly applied to judges who\n   went on circuit and to bailiffs at large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errant', 'n.', 'One who wanders about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errantia', 'n. pl.', 'A group of chaetopod annelids, including those that\n   are not confined to tubes. See Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errantry', 'n.', 'A wandering; a roving; esp., a roving in quest of\n   adventures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errantry', 'n.', 'The employment of a knight-errant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errata', 'n. pl.', 'See Erratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'a.', 'Having no certain course; roving about without a fixed\n   destination; wandering; moving; -- hence, applied to the planets as\n   distinguished from the fixed stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'a.', 'Deviating from a wise of the common course in opinion or\n   conduct; eccentric; strange; queer; as, erratic conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'a.', 'Irregular; changeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'n.', 'One who deviates from common and accepted opinions; one\n   who is eccentric or preserve in his intellectual character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'n.', 'A rogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratic', 'n.', 'Any stone or material that has been borne away from its\n   original site by natural agencies; esp., a large block or fragment of\n   rock; a bowlder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratical', 'a.', 'Erratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erration', 'n.', 'A wandering; a roving about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errata', 'pl. ', 'of Erratum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erratum', 'n.', 'An error or mistake in writing or printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erthine', 'n.', 'A medicine designed to be snuffed up the nose, to promote\n   discharges of mucus; a sternutatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erthine', 'a.', 'Causing or increasing secretion of nasal mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erroneous', 'a.', 'Wandering; straying; deviating from the right course; --\n   hence, irregular; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erroneous', 'a.', 'Misleading; misled; mistaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erroneous', 'a.', 'Containing error; not conformed to truth or justice;\n   incorrect; false; mistaken; as, an erroneous doctrine; erroneous\n   opinion, observation, deduction, view, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A wandering; a roving or irregular course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A wandering or deviation from the right course or standard;\n   irregularity; mistake; inaccuracy; something made wrong or left wrong;\n   as, an error in writing or in printing; a clerical error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A departing or deviation from the truth; falsity; false\n   notion; wrong opinion; mistake; misapprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A moral offense; violation of duty; a sin or transgression;\n   iniquity; fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'The difference between the approximate result and the true\n   result; -- used particularly in the rule of double position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'The difference between an observed value and the true value\n   of a quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'The difference between the observed value of a quantity and\n   that which is taken or computed to be the true value; -- sometimes\n   called residual error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A mistake in the proceedings of a court of record in matters\n   of law or of fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Error', 'n.', 'A fault of a player of the side in the field which results\n   in failure to put out a player on the other side, or gives him an\n   unearned base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errorful', 'a.', 'Full of error; wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Errorist', 'n.', 'One who encourages and propagates error; one who holds to\n   error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ers', 'n.', 'The bitter vetch (Ervum Ervilia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erse', 'n.', 'A name sometimes given to that dialect of the Celtic which is\n   spoken in the Highlands of Scotland; -- called, by the Highlanders,\n   Gaelic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erse', 'a.', 'Of or pertaining to the Celtic race in the Highlands of\n   Scotland, or to their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ersh', 'n.', 'See Arrish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erst', 'adv.', 'First.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erst', 'adv.', 'Previously; before; formerly; heretofore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erstwhile', 'adv.', 'Till then or now; heretofore; formerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erubescence', 'n.', 'Alt. of Erubescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erubescency', 'n.', 'The act of becoming red; redness of the skin or\n   surface of anything; a blushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erubescent', 'a.', 'Red, or reddish; blushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erubescite', 'n.', 'See Bornite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erucae', 'pl. ', 'of Eruca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruca', 'n.', 'An insect in the larval state; a caterpillar; a larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erucic', 'a.', 'Pertaining to, or derived from, a genus of cruciferous\n   Mediterranean herbs (Eruca or Brassica); as, erucic acid, a fatty acid\n   resembling oleic acid, and found in colza oil, mustard oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erucifrom', 'a.', 'Having the form of a caterpillar; -- said of insect\n   larvae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruct', 'v. t.', 'Alt. of Eructate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eructate', 'v. t.', 'To eject, as wind, from the stomach; to belch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eructation', 'n.', 'The act of belching wind from the stomach; a belch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eructation', 'n.', 'A violent belching out or emitting, as of gaseous or\n   other matter from the crater of a volcano, geyser, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erudiate', 'v. t.', 'To instruct; to educate; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erudite', 'a.', 'Characterized by extensive reading or knowledge; well\n   instructed; learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erudition', 'n.', 'The act of instructing; the result of thorough\n   instruction; the state of being erudite or learned; the acquisitions\n   gained by extensive reading or study; particularly, learning in\n   literature or criticism, as distinct from the sciences; scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erugate', 'a.', 'Freed from wrinkles; smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruginous', 'a.', 'Partaking of the substance or nature of copper, or of\n   the rust copper; resembling the trust of copper or verdigris;\n   aeruginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erumpent', 'a.', 'Breaking out; -- said of certain fungi which burst\n   through the texture of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erupt', 'v. t.', 'To cause to burst forth; to eject; as, to erupt lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruption', 'n.', 'The act of breaking out or bursting forth; as: (a) A\n   violent throwing out of flames, lava, etc., as from a volcano of a\n   fissure in the earths crust. (b) A sudden and overwhelming hostile\n   movement of armed men from one country to another. Milton. (c) A\n   violent commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruption', 'n.', 'That which bursts forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruption', 'n.', 'A violent exclamation; ejaculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruption', 'n.', 'The breaking out of pimples, or an efflorescence, as in\n   measles, scarlatina, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruptional', 'a.', 'Eruptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruptive', 'a.', 'Breaking out or bursting forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruptive', 'a.', 'Attended with eruption or efflorescence, or producing it;\n   as, an eruptive fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruptive', 'a.', 'Produced by eruption; as, eruptive rocks, such as the\n   igneous or volcanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eruptive', 'n.', 'An eruptive rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eryngium', 'n.', 'A genus of umbelliferous plants somewhat like thistles in\n   appearance. Eryngium maritimum, or sea holly, has been highly esteemed\n   as an aphrodisiac, the roots being formerly candied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eryngo', 'n.', 'A plant of the genus Eryngium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erysipelas', 'n.', 'St. Anthonys fire; a febrile disease accompanied with\n   a diffused inflammation of the skin, which, starting usually from a\n   single point, spreads gradually over its surface. It is usually\n   regarded as contagious, and often occurs epidemically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erysipelatoid', 'a.', 'Resembling erysipelas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erysipelatous', 'a.', 'Resembling erysipelas, or partaking of its nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erysipelous', 'a.', 'Erysipelatous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythema', 'n.', 'A disease of the skin, in which a diffused inflammation\n   forms rose-colored patches of variable size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythematic', 'a.', 'Characterized by, or causing, a morbid redness of the\n   skin; relating to erythema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythematous', 'a.', 'Relating to, or causing, erythema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrean', 'a.', 'Alt. of Erythraean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythraean', 'a.', 'Red in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythric', 'a.', 'Pertaining to, derived from, or resembling, erythrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrin', 'n.', 'Alt. of Erythrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrine', 'n.', 'A colorless crystalline substance, C20H22O10, extracted\n   from certain lichens, as the various species of Rocella. It is a\n   derivative of orsellinic acid. So called because of certain red\n   compounds derived from it. Called also erythric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrine', 'n.', 'See Erythrite, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrina', 'n.', 'A genus of leguminous plants growing in the tropics;\n   coral tree; -- so called from its red flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrism', 'n.', 'A condition of excessive redness. See Erythrochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrite', 'n.', 'A colorless crystalline substance, C4H6.(OH)4, of a\n   sweet, cooling taste, extracted from certain lichens, and obtained by\n   the decomposition of erythrin; -- called also erythrol, erythroglucin,\n   erythromannite, pseudorcin, cobalt bloom, and under the name phycite\n   obtained from the alga Protococcus vulgaris. It is a tetrabasic\n   alcohol, corresponding to glycol and glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrite', 'n.', 'A rose-red mineral, crystallized and earthy, a hydrous\n   arseniate of cobalt, known also as cobalt bloom; -- called also\n   erythrin or erythrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrochroic', 'a.', 'Having, or subject to, erythrochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrochroism', 'n.', 'An unusual redness, esp. in the plumage of birds,\n   or hair of mammals, independently of age, sex, or season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrodextrin', 'n.', 'A dextrin which gives a red color with iodine. See\n   Dextrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrogen', 'n.', 'Carbon disulphide; -- so called from certain red\n   compounds which it produces in combination with other substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrogen', 'n.', 'A substance reddened by acids, which is supposed to be\n   contained in flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrogen', 'n.', 'A crystalline substance obtained from diseased bile,\n   which becomes blood-red when acted on by nitric acid or ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrogranulose', 'n.', 'A term applied by Brucke to a substance present\n   in small amount in starch granules, colored red by iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythroid', 'a.', 'Of a red color; reddish; as, the erythroid tunic (the\n   cremaster muscle).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythroleic', 'a.', 'Having a red color and oily appearance; -- applied to\n   a purple semifluid substance said to be obtained from archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrolein', 'n.', 'A red substance obtained from litmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrolitmin', 'n.', 'Erythrolein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythronium', 'n.', 'A name originally given (from its red acid) to the\n   metal vanadium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrophleine', 'n.', 'A white crystalline alkaloid, extracted from sassy\n   bark (Erythrophleum Guineense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrophyll', 'n.', 'Alt. of Erythrophyllin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrophyllin', 'n.', 'The red coloring matter of leaves, fruits, flowers,\n   etc., in distinction from chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrosin', 'n.', 'A red substance formed by the oxidation of tyrosin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrosin', 'n.', 'A red dyestuff obtained from fluorescein by the action\n   of iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythroxylon', 'n.', 'A genus of shrubs or small trees of the Flax family,\n   growing in tropical countries. E. Coca is the source of cocaine. See\n   Coca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Erythrozyme', 'n.', 'A ferment extracted from madder root, possessing the\n   power of inducing alcoholic fermentation in solutions of sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalade', 'v. t.', 'A furious attack made by troops on a fortified place,\n   in which ladders are used to pass a ditch or mount a rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaladed', 'imp. & p. p.', 'of Escalade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalading', 'p. pr. & vb. n.', 'of Escalade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalade', 'v. t.', 'To mount and pass or enter by means of ladders; to\n   scale; as, to escalate a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escallop', 'n.', 'See Escalop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalloped', 'a.', 'See Escaloped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalop', 'n.', 'A bivalve shell of the genus Pecten. See Scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalop', 'n.', 'A regular, curving indenture in the margin of anything.\n   See Scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalop', 'n.', 'The figure or shell of an escalop, considered as a sign\n   that the bearer had been on a pilgrimage to the Holy Land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escalop', 'n.', 'A bearing or a charge consisting of an escalop shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaloped', 'a.', 'Cut or marked in the form of an escalop; scalloped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaloped', 'a.', 'Covered with a pattern resembling a series of escalop\n   shells, each of which issues from between two others. Its appearance is\n   that of a surface covered with scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escambio', 'n.', 'A license formerly required for the making over a bill of\n   exchange to another over sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapable', 'a.', 'Avoidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapade', 'n.', 'The fling of a horse, or ordinary kicking back of his\n   heels; a gambol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapade', 'n.', 'Act by which one breaks loose from the rules of propriety\n   or good sense; a freak; a prank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaped', 'imp. & p. p.', 'of Escape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaping', 'p. pr. & vb. n.', 'of Escape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'v.', 'To flee from and avoid; to be saved or exempt from; to\n   shun; to obtain security from; as, to escape danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'v.', 'To avoid the notice of; to pass unobserved by; to evade;\n   as, the fact escaped our attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'v. i.', 'To flee, and become secure from danger; -- often\n   followed by from or out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'v. i.', 'To get clear from danger or evil of any form; to be\n   passed without harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'v. i.', 'To get free from that which confines or holds; -- used\n   of persons or things; as, to escape from prison, from arrest, or from\n   slavery; gas escapes from the pipes; electricity escapes from its\n   conductors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'The act of fleeing from danger, of evading harm, or of\n   avoiding notice; deliverance from injury or any evil; flight; as, an\n   escape in battle; a narrow escape; also, the means of escape; as, a\n   fire escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'That which escapes attention or restraint; a mistake; an\n   oversight; also, transgression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'A sally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'The unlawful permission, by a jailer or other custodian, of\n   a prisoners departure from custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'An apophyge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'Leakage or outflow, as of steam or a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escape', 'n.', 'Leakage or loss of currents from the conducting wires,\n   caused by defective insulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapement', 'n.', 'The act of escaping; escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapement', 'n.', 'Way of escape; vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escapement', 'n.', 'The contrivance in a timepiece which connects the train\n   of wheel work with the pendulum or balance, giving to the latter the\n   impulse by which it is kept in vibration; -- so called because it\n   allows a tooth to escape from a pallet at each vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escaper', 'n.', 'One who escapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarbuncle', 'n.', 'See Carbuncle, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escargatoire', 'n.', 'A nursery of snails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarp', 'n.', 'The side of the ditch next the parapet; -- same as scarp,\n   and opposed to counterscarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarped', 'imp. & p. p.', 'of Escarp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarping', 'p. pr. & vb. n.', 'of Escarp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarp', 'v. t.', 'To make into, or furnish with, a steep slope, like that\n   of a scrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escarpment', 'n.', 'A steep descent or declivity; steep face or edge of a\n   ridge; ground about a fortified place, cut away nearly vertically to\n   prevent hostile approach. See Scarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-escent', '', 'A suffix signifying beginning, beginning to be; as,\n   adolescent, effervescent, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschalot', 'n.', 'See Shallot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschar', 'n.', 'A dry slough, crust, or scab, which separates from the\n   healthy part of the body, as that produced by a burn, or the\n   application of caustics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschar', 'n.', 'In Ireland, one of the continuous mounds or ridges of\n   gravelly and sandy drift which extend for many miles over the surface\n   of the country. Similar ridges in Scotland are called kames or kams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschara', 'n.', 'A genus of Bryozoa which produce delicate corals, often\n   incrusting like lichens, but sometimes branched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escharine', 'a.', 'Like, or pertaining to, the genus Eschara, or family\n   Escharidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escharotic', 'a.', 'Serving or tending to form an eschar; producing a scar;\n   caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escharotic', 'n.', 'A substance which produces an eschar; a caustic, esp.,\n   a mild caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschatological', 'a.', 'Pertaining to the last or final things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschatology', 'n.', 'The doctrine of the last or final things, as death,\n   judgment, and the events therewith connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschaunge', 'n.', 'Exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'n.', 'The falling back or reversion of lands, by some casualty\n   or accident, to the lord of the fee, in consequence of the extinction\n   of the blood of the tenant, which may happen by his dying without\n   heirs, and formerly might happen by corruption of blood, that is, by\n   reason of a felony or attainder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'n.', 'The reverting of real property to the State, as original\n   and ultimate proprietor, by reason of a failure of persons legally\n   entitled to hold the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'n.', 'A writ, now abolished, to recover escheats from the person\n   in possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'n.', 'Lands which fall to the lord or the State by escheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'n.', 'That which falls to one; a reversion or return');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esheated', 'imp. & p. p.', 'of Escheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheating', 'p. pr. & vb. n.', 'of Escheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'v. i.', 'To revert, or become forfeited, to the lord, the crown,\n   or the State, as lands by the failure of persons entitled to hold the\n   same, or by forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheat', 'v. t.', 'To forfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheatable', 'a.', 'Liable to escheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheatage', 'n.', 'The right of succeeding to an escheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escheator', 'n.', 'An officer whose duty it is to observe what escheats\n   have taken place, and to take charge of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschevin', 'n.', 'The alderman or chief officer of an ancient guild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eshewed', 'imp. & p. p.', 'of Eschew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eshewing', 'p. pr. & vb. n.', 'of Eschew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschew', 'a.', 'To shun; to avoid, as something wrong, or from a feeling of\n   distaste; to keep ones self clear of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschew', 'a.', 'To escape from; to avoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschewer', 'n.', 'One who eschews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschewment', 'n.', 'The act of eschewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschscholtzia', 'n.', 'A genus of papaveraceous plants, found in California\n   and upon the west coast of North America, some species of which produce\n   beautiful yellow, orange, rose-colored, or white flowers; the\n   California poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eschynite', 'n.', 'A rare mineral, containing chiefly niobium, titanium,\n   thorium, and cerium. It was so called by Berzelius on account of the\n   inability of chemical science, at the time of its discovery, to\n   separate some of its constituents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escocheon', 'n.', 'Escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escopet', 'n.', 'Alt. of Escopette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escopette', 'n.', 'A kind of firearm; a carbine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escorial', 'n.', 'See Escurial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escort', 'n.', 'A body of armed men to attend a person of distinction for\n   the sake of affording safety when on a journey; one who conducts some\n   one as an attendant; a guard, as of prisoners on a march; also, a body\n   of persons, attending as a mark of respect or honor; -- applied to\n   movements on land, as convoy is to movements at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escort', 'n.', 'Protection, care, or safeguard on a journey or excursion;\n   as, to travel under the escort of a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escorted', 'imp. & p. p.', 'of Escort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escorting', 'p. pr. & vb. n.', 'of Escort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escort', 'n.', 'To attend with a view to guard and protect; to accompany as\n   safeguard; to give honorable or ceremonious attendance to; -- used esp.\n   with reference to journeys or excursions on land; as, to escort a\n   public functionary, or a lady; to escort a baggage wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escot', 'n.', 'See Scot, a tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escot', 'v. t.', 'To pay the reckoning for; to support; to maintain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escouade', 'n.', 'See Squad,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escout', 'n.', 'See Scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escribed', 'a.', 'Drawn outside of; -- used to designate a circle that\n   touches one of the sides of a given triangle, and also the other two\n   sides produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escript', 'n.', 'A writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escritoire', 'n.', 'A piece of furniture used as a writing table, commonly\n   with drawers, pigeonholes, and the like; a secretary or writing desk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escritorial', 'a.', 'Of or pertaining to an escritoire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escrod', 'n.', 'See Scrod, a young cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escrol', 'n.', 'Alt. of Escroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escroll', 'n.', 'A scroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escroll', 'n.', 'A long strip or scroll resembling a ribbon or a band of\n   parchment, or the like, anciently placed above the shield, and\n   supporting the crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escroll', 'n.', 'In modern heraldry, a similar ribbon on which the motto is\n   inscribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escrow', 'n.', 'A deed, bond, or other written engagement, delivered to a\n   third person, to be held by him till some act is done or some condition\n   is performed, and then to be by him delivered to the grantee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escuage', 'n.', 'Service of the shield, a species of knight service by\n   which a tenant was bound to follow his lord to war, at his own charge.\n   It was afterward exchanged for a pecuniary satisfaction. Called also\n   scutage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculapian', 'n.', 'Aesculapian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculapius', 'n.', 'Same as Aesculapius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculent', 'a.', 'Suitable to be used by man for food; eatable; edible; as,\n   esculent plants; esculent fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculent', 'n.', 'Anything that is fit for eating; that which may be safely\n   eaten by man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculic', 'a.', 'Pertaining to, or obtained from, the horse-chestnut; as,\n   esculic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esculin', 'n.', 'A glucoside obtained from the Aesculus hippocastanum, or\n   horse-chestnut, and characterized by its fine blue fluorescent\n   solutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escurial', 'n.', 'A palace and mausoleum of the kinds of Spain, being a\n   vast and wonderful structure about twenty-five miles northwest of\n   Madrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheon', 'n.', 'The surface, usually a shield, upon which bearings are\n   marshaled and displayed. The surface of the escutcheon is called the\n   field, the upper part is called the chief, and the lower part the base\n   (see Chiff, and Field.). That side of the escutcheon which is on the\n   right hand of the knight who bears the shield on his arm is called\n   dexter, and the other side sinister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheon', 'n.', 'A marking upon the back of a cows udder and the space\n   above it (the perineum), formed by the hair growing upward or outward\n   instead of downward. It is esteemed an index of milking qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheon', 'n.', 'That part of a vessels stern on which her name is\n   written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheon', 'n.', 'A thin metal plate or shield to protect wood, or for\n   ornament, as the shield around a keyhole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheon', 'n.', 'The depression behind the beak of certain bivalves; the\n   ligamental area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Escutcheoned', 'a.', 'Having an escutcheon; furnished with a coat of arms\n   or ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ese', 'n.', 'Ease; pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esemplastic', 'a.', 'Shaped into one; tending to, or formative into, unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eserine', 'n.', 'An alkaloid found in the Calabar bean, and the seed of\n   Physostigma venenosum; physostigmine. It is used in ophthalmic surgery\n   for its effect in contracting the pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esexual', 'a.', 'Sexless; asexual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esguard', 'n.', 'Guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eskar', 'n.', 'Alt. of Esker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esker', 'n.', 'See Eschar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eskimos', 'pl. ', 'of Eskimo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eskimo', 'n.', 'One of a peculiar race inhabiting Arctic America and\n   Greenland. In many respects the Eskimos resemble the Mongolian race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esloin', 'v. t.', 'To remove; to banish; to withdraw; to avoid; to eloign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esnecy', 'n.', 'A prerogative given to the eldest coparcener to choose\n   first after an inheritance is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esodic', 'a.', 'Conveying impressions from the surface of the body to the\n   spinal cord; -- said of certain nerves. Opposed to exodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esophagal', 'a.', 'Esophageal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esophageal', 'a.', 'Pertaining to the esophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esophagean', 'a.', 'Esophageal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esophagotomy', 'n.', 'The operation of making an incision into the\n   esophagus, for the purpose of removing any foreign substance that\n   obstructs the passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esophagus', 'n.', 'That part of the alimentary canal between the pharynx\n   and the stomach; the gullet. See Illust. of Digestive apparatus, under\n   Digestive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esopian', 'a.', 'Alt. of Esopic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esopic', 'a.', 'Same as Aesopian, Aesopic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esoteric', 'a.', 'Designed for, and understood by, the specially initiated\n   alone; not communicated, or not intelligible, to the general body of\n   followers; private; interior; acroamatic; -- said of the private and\n   more recondite instructions and doctrines of philosophers. Opposed to\n   exoteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esoterical', 'a.', 'Esoteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esoterically', 'adv.', 'In an esoteric manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esotericism', 'n.', 'Esoteric doctrine or principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esoterics', 'n.', 'Mysterious or hidden doctrines; secret science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esotery', 'n.', 'Mystery; esoterics; -- opposed to exotery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esox', 'n.', 'A genus of fresh-water fishes, including pike and pickerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espace', 'n.', 'Space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espadon', 'n.', 'A long, heavy, two-handed and two-edged sword, formerly\n   used by Spanish foot soldiers and by executioners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espalier', 'n.', 'A railing or trellis upon which fruit trees or shrubs are\n   trained, as upon a wall; a tree or row of trees so trained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espaliered', 'imp. & p. p.', 'of Espalier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espaliering', 'p. pr. & vb. n.', 'of Espalier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espalier', 'v. t.', 'To form an espalier of, or to protect by an espalier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esparcet', 'n.', 'The common sainfoin (Onobrychis sativa), an Old World\n   leguminous forage plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esparto', 'n.', 'A species of Spanish grass (Macrochloa tenacissima), of\n   which cordage, shoes, baskets, etc., are made. It is also used for\n   making paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espauliere', 'n.', 'A defense for the shoulder, composed of flexible\n   overlapping plates of metal, used in the 15th century; -- the origin of\n   the modern epaulette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Especial', 'a.', 'Distinguished among others of the same class or kind;\n   special; concerning a species or a single object; principal;\n   particular; as, in an especial manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Especially', 'adv.', 'In an especial manner; chiefly; particularly;\n   peculiarly; in an uncommon degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Especialness', 'n.', 'The state of being especial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esperance', 'n.', 'Hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espiaille', 'n.', 'Espial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espial', 'n.', 'The act of espying; notice; discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espial', 'n.', 'One who espies; a spy; a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espier', 'n.', 'One who espies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espinel', 'n.', 'A kind of ruby. See Spinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espionage', 'n.', 'The practice or employment of spies; the practice of\n   watching the words and conduct of others, to make discoveries, as spies\n   or secret emissaries; secret watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esplanade', 'n.', 'A clear space between a citadel and the nearest houses\n   of the town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esplanade', 'n.', 'The glacis of the counterscarp, or the slope of the\n   parapet of the covered way toward the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esplanade', 'n.', 'A grass plat; a lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esplanade', 'n.', 'Any clear, level space used for public walks or drives;\n   esp., a terrace by the seaside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esplees', 'n. pl.', 'The full profits or products which ground or land\n   yields, as the hay of the meadows, the feed of the pasture, the grain\n   of arable fields, the rents, services, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espousage', 'n.', 'Espousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espousal', 'n.', 'The act of espousing or betrothing; especially, in the\n   plural, betrothal; plighting of the troths; a contract of marriage;\n   sometimes, the marriage ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espousal', 'n.', 'The uniting or allying ones self with anything;\n   maintenance; adoption; as, the espousal of a quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espoused', 'imp. & p. p.', 'of Espouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espousing', 'p. pr. & vb. n.', 'of Espouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espouse', 'v. t.', 'To betroth; to promise in marriage; to give as spouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espouse', 'v. t.', 'To take as spouse; to take to wife; to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espouse', 'v. t.', 'To take to ones self with a view to maintain; to make\n   ones own; to take up the cause of; to adopt; to embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espousement', 'n.', 'The act of espousing, or the state of being espoused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espouser', 'n.', 'One who espouses; one who embraces the cause of another\n   or makes it his own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espressivo', 'a.', 'With expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espringal', 'n.', 'An engine of war used for throwing viretons, large\n   stones, and other missiles; a springal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esprit', 'n.', 'Spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espied', 'imp. & p. p.', 'of Espy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espying', 'p. pr. & vb. n.', 'of Espy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espy', 'v. t.', 'To catch sight of; to perceive with the eyes; to discover,\n   as a distant object partly concealed, or not obvious to notice; to see\n   at a glance; to discern unexpectedly; to spy; as, to espy land; to espy\n   a man in a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espy', 'v. t.', 'To inspect narrowly; to examine and keep watch upon; to\n   watch; to observe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espy', 'v. i.', 'To look or search narrowly; to look about; to watch; to\n   take notice; to spy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espies', 'pl. ', 'of Espy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Espy', 'n.', 'A spy; a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-esque', '', 'A suffix of certain words from the French, Italian, and\n   Spanish. It denotes manner or style; like; as, arabesque, after the\n   manner of the Arabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquimaux', 'pl. ', 'of Esquimau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquimau', 'n.', 'Same as Eskimo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquire', 'n.', 'Originally, a shield-bearer or armor-bearer, an attendant\n   on a knight; in modern times, a title of dignity next in degree below\n   knight and above gentleman; also, a title of office and courtesy; --\n   often shortened to squire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquired', 'imp. & p. p.', 'of Esquire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquiring', 'p. pr. & vb. n.', 'of Esquire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquire', 'v. t.', 'To wait on as an esquire or attendant in public; to\n   attend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esquisse', 'n.', 'The first sketch of a picture or model of a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ess', '', 'A suffix used to form feminine nouns; as, actress, deaconess,\n   songstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essays', 'pl. ', 'of Essay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essay', 'n.', 'An effort made, or exertion of body or mind, for the\n   performance of anything; a trial; attempt; as, to make an essay to\n   benefit a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essay', 'n.', 'A composition treating of any particular subject; -- usually\n   shorter and less methodical than a formal, finished treatise; as, an\n   essay on the life and writings of Homer; an essay on fossils, or on\n   commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essay', 'n.', 'An assay. See Assay, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essayed', 'imp. & p. p.', 'of Essay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essaying', 'p. pr. & vb. n.', 'of Essay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essay', 'n.', 'To exert ones power or faculties upon; to make an effort to\n   perform; to attempt; to endeavor; to make experiment or trial of; to\n   try.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essay', 'n.', 'To test the value and purity of (metals); to assay. See\n   Assay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essayer', 'n.', 'One who essays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essayist', 'n.', 'A writer of an essay, or of essays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'The constituent elementary notions which constitute a\n   complex notion, and must be enumerated to define it; sometimes called\n   the nominal essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'The constituent quality or qualities which belong to any\n   object, or class of objects, or on which they depend for being what\n   they are (distinguished as real essence); the real being, divested of\n   all logical accidents; that quality which constitutes or marks the true\n   nature of anything; distinctive character; hence, virtue or quality of\n   a thing, separated from its grosser parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'Constituent substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'A being; esp., a purely spiritual being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'The predominant qualities or virtues of a plant or drug,\n   extracted and refined from grosser matter; or, more strictly, the\n   solution in spirits of wine of a volatile or essential oil; as, the\n   essence of mint, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'n.', 'Perfume; odor; scent; or the volatile matter constituting\n   perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essenced', 'imp. & p. p.', 'of Essence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essencing', 'p. pr. & vb. n.', 'of Essence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essence', 'v. t.', 'To perfume; to scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essenes', 'pl. ', 'of Essene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essene', 'n.', 'One of a sect among the Jews in the time of our Savior,\n   remarkable for their strictness and abstinence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essenism', 'n.', 'The doctrine or the practices of the Essenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Belonging to the essence, or that which makes an object,\n   or class of objects, what it is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Hence, really existing; existent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Important in the highest degree; indispensable to the\n   attainment of an object; indispensably necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Containing the essence or characteristic portion of a\n   substance, as of a plant; highly rectified; pure; hence, unmixed; as,\n   an essential oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Necessary; indispensable; -- said of those tones which\n   constitute a chord, in distinction from ornamental or passing tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essential', 'a.', 'Idiopathic; independent of other diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esential', 'n.', 'Existence; being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esential', 'n.', 'That which is essential; first or constituent principle;\n   as, the essentials or religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essentiality', 'n.', 'The quality of being essential; the essential part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esentially', 'adv.', 'In an essential manner or degree; in an indispensable\n   degree; really; as, essentially different.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esentialness', 'n.', 'Essentiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essentiated', 'imp. & p. p.', 'of Essentiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essentiating', 'p. pr. & vb. n.', 'of Essentiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essentiate', 'v. t.', 'To form or constitute the essence or being of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essentiate', 'v. i.', 'To become assimilated; to be changed into the\n   essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essoin', 'n.', 'Alt. of Essoign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essoign', 'n.', 'An excuse for not appearing in court at the return of\n   process; the allegation of an excuse to the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essoign', 'n.', 'Excuse; exemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essoin', 'n.', 'To excuse for nonappearance in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essoiner', 'n.', 'An attorney who sufficiently excuses the absence of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essonite', 'n.', 'Cinnamon stone, a variety of garnet. See Garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Essorant', 'a.', 'Standing, but with the wings spread, as if about to fly;\n   -- said of a bird borne as a charge on an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Est', 'n. & adv.', 'East.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-est', '', 'A suffix used to form the superlative of adjectives and\n   adverbs; as, smoothest; earl(y)iest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Established', 'imp. & p. p.', 'of Establish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establishing', 'p. pr. & vb. n.', 'of Establish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establish', 'a.', 'To make stable or firm; to fix immovably or firmly; to\n   set (a thing) in a place and make it stable there; to settle; to\n   confirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establish', 'a.', 'To appoint or constitute for permanence, as officers,\n   laws, regulations, etc.; to enact; to ordain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establish', 'a.', 'To originate and secure the permanent existence of; to\n   found; to institute; to create and regulate; -- said of a colony, a\n   state, or other institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establish', 'a.', 'To secure public recognition in favor of; to prove and\n   cause to be accepted as true; as, to establish a fact, usage,\n   principle, opinion, doctrine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establish', 'a.', 'To set up in business; to place advantageously in a\n   fixed condition; -- used reflexively; as, he established himself in a\n   place; the enemy established themselves in the citadel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establisher', 'n.', 'One who establishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establishment', 'n.', 'The act of establishing; a ratifying or ordaining;\n   settlement; confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establishment', 'n.', 'The state of being established, founded, and the\n   like; fixed state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establishment', 'n.', 'That which is established; as: (a) A form of\n   government, civil or ecclesiastical; especially, a system of religion\n   maintained by the civil power; as, the Episcopal establishment of\n   England. (b) A permanent civil, military, or commercial, force or\n   organization. (c) The place in which one is permanently fixed for\n   residence or business; residence, including grounds, furniture,\n   equipage, etc.; with which one is fitted out; also, any office or place\n   of business, with its fixtures; that which serves for the carrying on\n   of a business; as, to keep up a large establishment; a manufacturing\n   establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Establishmentarian', 'n.', 'One who regards the Church primarily as an\n   establishment formed by the State, and overlooks its intrinsic\n   spiritual character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estacade', 'n.', 'A dike of piles in the sea, a river, etc., to check the\n   approach of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estafet', 'n.', 'Alt. of Estafette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estafette', 'n.', 'A courier who conveys messages to another courier; a\n   military courier sent from one part of an army to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estancia', 'n.', 'A grazing; a country house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'Settled condition or form of existence; state; condition or\n   circumstances of life or of any person; situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'Social standing or rank; quality; dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'A person of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'A property which a person possesses; a fortune;\n   possessions, esp. property in land; also, property of all kinds which a\n   person leaves to be divided at his death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'The state; the general body politic; the common-wealth; the\n   general interest; state affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'The great classes or orders of a community or state (as the\n   clergy, the nobility, and the commonalty of England) or their\n   representatives who administer the government; as, the estates of the\n   realm (England), which are (1) the lords spiritual, (2) the lords\n   temporal, (3) the commons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'n.', 'The degree, quality, nature, and extent of ones interest\n   in, or ownership of, lands, tenements, etc.; as, an estate for life,\n   for years, at will, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'v. t.', 'To establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'v. t.', 'Tom settle as a fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estate', 'v. t.', 'To endow with an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estatlich', 'a.', 'Alt. of Estatly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estatly', 'a.', 'Stately; dignified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteemed', 'imp. & p. p.', 'of Esteem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteeming', 'p. pr. & vb. n.', 'of Esteem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteem', 'v. t.', 'To set a value on; to appreciate the worth of; to\n   estimate; to value; to reckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteem', 'v. t.', 'To set a high value on; to prize; to regard with\n   reverence, respect, or friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteem', 'v. i.', 'To form an estimate; to have regard to the value; to\n   consider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteem', 'v. t.', 'Estimation; opinion of merit or value; hence, valuation;\n   reckoning; price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteem', 'v. t.', 'High estimation or value; great regard; favorable\n   opinion, founded on supposed worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteemable', 'a.', 'Worthy of esteem; estimable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esteemer', 'n.', 'One who esteems; one who sets a high value on any thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ester', 'n.', 'An ethereal salt, or compound ether, consisting of an\n   organic radical united with the residue of any oxygen acid, organic or\n   inorganic; thus the natural fats are esters of glycerin and the fatty\n   acids, oleic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esthesiometer', 'n.', 'Same as Aesthesiometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esthete', 'n.', 'Alt. of Esthetics');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esthetic', 'n.', 'Alt. of Esthetics');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esthetical', 'n.', 'Alt. of Esthetics');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esthetics', 'n.', 'Same as Aesthete, Aesthetic, Aesthetical, Aesthetics,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estiferous', 'a.', 'Producing heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimable', 'a.', 'Capable of being estimated or valued; as, estimable\n   damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimable', 'a.', 'Valuable; worth a great price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimable', 'a.', 'Worth of esteem or respect; deserving our good opinion\n   or regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimable', 'n.', 'A thing worthy of regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimableness', 'n.', 'The quality of deserving esteem or regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimably', 'adv.', 'In an estimable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimated', 'imp. & p. p.', 'of Estimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimating', 'p. pr. & vb. n.', 'of Estimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimate', 'v. t.', 'To judge and form an opinion of the value of, from\n   imperfect data, -- either the extrinsic (money), or intrinsic (moral),\n   value; to fix the worth of roughly or in a general way; as, to estimate\n   the value of goods or land; to estimate the worth or talents of a\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimate', 'v. t.', 'To from an opinion of, as to amount,, number, etc.,\n   from imperfect data, comparison, or experience; to make an estimate of;\n   to calculate roughly; to rate; as, to estimate the cost of a trip, the\n   number of feet in a piece of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimate', 'n.', 'A valuing or rating by the mind, without actually\n   measuring, weighing, or the like; rough or approximate calculation; as,\n   an estimate of the cost of a building, or of the quantity of water in a\n   pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimation', 'v. t.', 'The act of estimating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimation', 'v. t.', 'An opinion or judgment of the worth, extent, or\n   quantity of anything, formed without using precise data; valuation; as,\n   estimations of distance, magnitude, amount, or moral qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimation', 'v. t.', 'Favorable opinion; esteem; regard; honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimation', 'v. t.', 'Supposition; conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimative', 'a.', 'Inclined, or able, to estimate; serving for, or capable\n   of being used in, estimating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimative', 'a.', 'Pertaining to an estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estimator', 'n.', 'One who estimates or values; a valuer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estival', 'n.', 'Alt. of Estivation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estivate', 'n.', 'Alt. of Estivation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estivation', 'n.', 'Same as Aestival, Aestivate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estoile', 'n.', 'A six-pointed star whose rays are wavy, instead of\n   straight like those of a mullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estophed', 'imp. & p. p.', 'of Estop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estopping', 'p. pr. & vb. n.', 'of Estop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estop', 'v. t.', 'To impede or bar by estoppel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estoppel', 'n.', 'A stop; an obstruction or bar to ones alleging or\n   denying a fact contrary to his own previous action, allegation, or\n   denial; an admission, by words or conduct, which induces another to\n   purchase rights, against which the party making such admission can not\n   take a position inconsistent with the admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estoppel', 'n.', 'The agency by which the law excludes evidence to dispute\n   certain admissions, which the policy of the law treats as indisputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estovers', 'n. pl.', 'Necessaries or supples; an allowance to a person out\n   of an estate or other thing for support; as of wood to a tenant for\n   life, etc., of sustenance to a man confined for felony of his estate,\n   or alimony to a woman divorced out of her husbands estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrade', 'n.', 'A portion of the floor of a room raised above the general\n   level, as a place for a bed or a throne; a platform; a dais.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estramacon', 'n.', 'A straight, heavy sword with two edges, used in the\n   16th and 17th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estramacon', 'n.', 'A blow with edge of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estranged', 'imp. & p. p.', 'of Estrange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estranging', 'p. pr. & vb. n.', 'of Estrange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrange', 'v. t.', 'To withdraw; to withhold; hence, reflexively, to keep\n   at a distance; to cease to be familiar and friendly with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrange', 'v. t.', 'To divert from its original use or purpose, or from\n   its former possessor; to alienate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrange', 'v. t.', 'To alienate the affections or confidence of; to turn\n   from attachment to enmity or indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrangedness', 'n.', 'State of being estranged; estrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrangement', 'n.', 'The act of estranging, or the state of being\n   estranged; alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estranger', 'n.', 'One who estranges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrangle', 'v. t.', 'To strangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrapade', 'n.', 'The action of a horse, when, to get rid of his rider, he\n   rears, plunges, and kicks furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estray', 'v. i.', 'To stray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estray', 'n.', 'Any valuable animal, not wild, found wandering from its\n   owner; a stray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estre', 'n.', 'The inward part of a building; the interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estreat', 'n.', 'A true copy, duplicate, or extract of an original writing\n   or record, esp. of amercements or penalties set down in the rolls of\n   court to be levied by the bailiff, or other officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estreated', 'imp. & p. p.', 'of Estreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estreating', 'p. pr. & vb. n.', 'of Estreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estreat', 'v. t.', 'To extract or take out from the records of a court, and\n   send up to the court of exchequer to be enforced; -- said of a\n   forfeited recognizance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estreat', 'v. t.', 'To bring in to the exchequer, as a fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrepe', 'v. t.', 'To strip or lay bare, as land of wood, houses, etc.; to\n   commit waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrepement', 'n.', 'A destructive kind of waste, committed by a tenant for\n   life, in lands, woods, or houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrich', 'n.', 'Ostrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estrich', 'n.', 'The down of the ostrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuance', 'n.', 'Heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuarine', 'a.', 'Pertaining to an estuary; estuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuaries', 'pl. ', 'of Estuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuary', 'n.', 'A place where water boils up; a spring that wells forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuary', 'n.', 'A passage, as the mouth of a river or lake, where the tide\n   meets the current; an arm of the sea; a frith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuary', 'a.', 'Belonging to, or formed in, an estuary; as, estuary\n   strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuated', 'imp. & p. p.', 'of Estuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuating', 'p. pr. & vb. n.', 'of Estuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuate', 'v. i.', 'To boil up; to swell and rage; to be agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estuation', 'n.', 'The act of estuating; commotion, as of a fluid;\n   agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estufas', 'pl. ', 'of Estufa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Estufa', 'n.', 'An assembly room in dwelling of the Pueblo Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esture', 'n.', 'Commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esurient', 'a.', 'Inclined to eat; hungry; voracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esurient', 'n.', 'One who is hungry or greedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esurine', 'a.', 'Causing hunger; eating; corroding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Esurine', 'n.', 'A medicine which provokes appetites, or causes hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-et', '', 'A noun suffix with a diminutive force; as in baronet, pocket,\n   facet, floweret, latchet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etaac', 'n.', 'The blue buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etacism', 'n.', 'The pronunciation of the Greek / (eta) like the Italian e\n   long, that is like a in the English word ate. See Itacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etacist', 'n.', 'One who favors etacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etagere', 'n.', 'A piece of furniture having a number of uninclosed shelves\n   or stages, one above another, for receiving articles of elegance or\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etat Major', '', 'The staff of an army, including all officers above the\n   rank of colonel, also, all adjutants, inspectors, quartermasters,\n   commissaries, engineers, ordnance officers, paymasters, physicians,\n   signal officers, judge advocates; also, the noncommissioned assistants\n   of the above officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Et cetera', '', 'Alt. of Et caetera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Et caetera', '', 'Others of the like kind; and the rest; and so on; -- used\n   to point out that other things which could be mentioned are to be\n   understood. Usually abbreviated into etc. or &c. (&c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etch', 'n.', 'A variant of Eddish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etched', 'imp. & p. p.', 'of Etch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etching', 'p. pr. & vb. n.', 'of Etch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etch', 'v. t.', 'To produce, as figures or designs, on mental, glass, or\n   the like, by means of lines or strokes eaten in or corroded by means of\n   some strong acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etch', 'v. t.', 'To subject to etching; to draw upon and bite with acid, as\n   a plate of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etch', 'v. t.', 'To sketch; to delineate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etch', 'v. i.', 'To practice etching; to make etchings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etcher', 'n.', 'One who etches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etching', 'n.', 'The act, art, or practice of engraving by means of acid\n   which eats away lines or surfaces left unprotected in metal, glass, or\n   the like. See Etch, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etching', 'v. t.', 'A design carried out by means of the above process; a\n   pattern on metal, glass, etc., produced by etching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etching', 'v. t.', 'An impression on paper, parchment, or other material,\n   taken in ink from an etched plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eteostic', 'n.', 'A kind of chronogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eterminable', 'a.', 'Interminable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etern', 'a.', 'Alt. of Eterne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eterne', 'a.', 'Eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'a.', 'Without beginning or end of existence; always existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'a.', 'Without end of existence or duration; everlasting;\n   endless; immortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'a.', 'Continued without intermission; perpetual; ceaseless;\n   constant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'a.', 'Existing at all times without change; immutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'a.', 'Exceedingly great or bad; -- used as a strong intensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'n.', 'One of the appellations of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternal', 'n.', 'That which is endless and immortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternalist', 'n.', 'One who holds the existence of matter to be from\n   eternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternalize', 'v. t.', 'To make eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternally', 'adv.', 'In an eternal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eterne', 'a.', 'See Etern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternify', 'v. t.', 'To make eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternities', 'pl. ', 'of Eternity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternity', 'n.', 'Infinite duration, without beginning in the past or end\n   in the future; also, duration without end in the future; endless time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternity', 'n.', 'Condition which begins at death; immortality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternization', 'n.', 'The act of eternizing; the act of rendering immortal\n   or famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternized', 'imp. & p. p.', 'of Eternize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eterniziing', 'p. pr. & vb. n.', 'of Eternize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternize', 'v. t.', 'To make eternal or endless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eternize', 'v. t.', 'To make forever famous; to immortalize; as, to\n   eternize ones self, a name, exploits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etesian', 'a.', 'Periodical; annual; -- applied to winds which annually\n   blow from the north over the Mediterranean, esp. the eastern part, for\n   an irregular period during July and August.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethal', 'n.', 'A white waxy solid, C16H33.OH; -- called also cetylic\n   alcohol. See Cetylic alcohol, under Cetylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethane', 'n.', 'A gaseous hydrocarbon, C2H6, forming a constituent of\n   ordinary illuminating gas. It is the second member of the paraffin\n   series, and its most important derivatives are common alcohol,\n   aldehyde, ether, and acetic acid. Called also dimethyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethe', 'a.', 'Easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethel', 'a.', 'Noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethene', 'n.', 'Ethylene; olefiant gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethenic', 'a.', 'Pertaining to, derived from. or resembling, ethene or\n   ethylene; as, ethenic ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethenyl', 'n.', 'A trivalent hydrocarbon radical, CH3.C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethenyl', 'n.', 'A univalent hydrocarbon radical of the ethylene series,\n   CH2:CH; -- called also vinyl. See Vinyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etheostomoid', 'a.', 'Pertaining to, or like, the genus Etheostoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etheostomoid', 'n.', 'Any fish of the genus Etheostoma and related genera,\n   allied to the perches; -- also called darter. The etheostomoids are\n   small and often bright-colored fishes inhabiting the fresh waters of\n   North America. About seventy species are known. See Darter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ether', 'n.', 'A medium of great elasticity and extreme tenuity, supposed\n   to pervade all space, the interior of solid bodies not excepted, and to\n   be the medium of transmission of light and heat; hence often called\n   luminiferous ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ether', 'n.', 'Supposed matter above the air; the air itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ether', 'n.', 'A light, volatile, mobile, inflammable liquid, (C2H5)2O, of\n   a characteristic aromatic odor, obtained by the distillation of alcohol\n   with sulphuric acid, and hence called also sulphuric ether. It is\n   powerful solvent of fats, resins, and pyroxylin, but finds its chief\n   use as an anaesthetic. Called also ethyl oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ether', 'n.', 'Any similar oxide of hydrocarbon radicals; as, amyl ether;\n   valeric ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereal', 'a.', 'Pertaining to the hypothetical upper, purer air, or to\n   the higher regions beyond the earth or beyond the atmosphere;\n   celestial; as, ethereal space; ethereal regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereal', 'a.', 'Consisting of ether; hence, exceedingly light or airy;\n   tenuous; spiritlike; characterized by extreme delicacy, as form,\n   manner, thought, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereal', 'a.', 'Pertaining to, derived from, or resembling, ether; as,\n   ethereal salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherealism', 'n.', 'Ethereality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereality', 'n.', 'The state of being ethereal; etherealness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherealization', 'n.', 'An ethereal or spiritlike state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherealize', 'v. t.', 'To convert into ether, or into subtile fluid; to\n   saturate with ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherealize', 'v. t.', 'To render ethereal or spiritlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereally', 'adv.', 'In an ethereal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherealness', 'n.', 'Ethereality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereous', 'a.', 'Formed of ether; ethereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethereous', 'a.', 'Pertaining to, or resembling, ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherification', 'n.', 'The act or process of making ether; specifically,\n   the process by which a large quantity of alcohol is transformed into\n   ether by the agency of a small amount of sulphuric, or ethyl sulphuric,\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etheriform', 'a.', 'Having the form of ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherin', 'n.', 'A white, crystalline hydrocarbon, regarded as a polymeric\n   variety of ethylene, obtained in heavy oil of wine, the residue left\n   after making ether; -- formerly called also concrete oil of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherization', 'n.', 'The administration of ether to produce insensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherization', 'n.', 'The state of the system under the influence of ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherized', 'imp. & p. p.', 'of Etherize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherizing', 'p. pr. & vb. n.', 'of Etherize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherize', 'v. t.', 'To convert into ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherize', 'v. t.', 'To render insensible by means of ether, as by\n   inhalation; as, to etherize a patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etherol', 'n.', 'An oily hydrocarbon regarded as a polymeric variety of\n   ethylene, produced with etherin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethic', 'a.', 'Alt. of Ethical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethical', 'a.', 'Of, or belonging to, morals; treating of the moral\n   feelings or duties; containing percepts of morality; moral; as, ethic\n   discourses or epistles; an ethical system; ethical philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethically', 'adv.', 'According to, in harmony with, moral principles or\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethicist', 'n.', 'One who is versed in ethics, or has written on ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethics', 'n.', 'The science of human duty; the body of rules of duty drawn\n   from this science; a particular system of principles and rules\n   concerting duty, whether true or false; rules of practice in respect to\n   a single class of human actions; as, political or social ethics;\n   medical ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethide', 'n.', 'Any compound of ethyl of a binary type; as, potassium\n   ethide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethidene', 'n.', 'Ethylidene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethine', 'n.', 'Acetylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethionic', 'a.', 'Pertaining to, derived from, or designating, an acid so\n   called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiop', 'n.', 'Alt. of Ethiopian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiopian', 'n.', 'A native or inhabitant of Ethiopia; also, in a general\n   sense, a negro or black man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiopian', 'a.', 'Alt. of Ethiopic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiopic', 'a.', 'Of or relating to Ethiopia or the Ethiopians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiopic', 'n.', 'The language of ancient Ethiopia; the language of the\n   ancient Abyssinian empire (in Ethiopia), now used only in the\n   Abyssinian church. It is of Semitic origin, and is also called Geez.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethiops', 'n.', 'A black substance; -- formerly applied to various\n   preparations of a black or very dark color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmoid', 'a.', 'Alt. of Ethmoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmoidal', 'a.', 'Like a sieve; cribriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmoidal', 'a.', 'Pertaining to, or in the region of, the ethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmoid', 'n.', 'The ethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmotrubinal', 'a.', 'See Turbinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmotrubinal', 'n.', 'An ethmoturbinal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethmovomerine', 'n.', 'Pertaining to the region of the vomer and the base\n   of the ethmoid in the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnarch', 'n.', 'The governor of a province or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnarchy', 'n.', 'The dominion of an ethnarch; principality and rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnic', 'a.', 'Alt. of Ethnical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnical', 'a.', 'Belonging to races or nations; based on distinctions of\n   race; ethnological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnical', 'a.', 'Pertaining to the gentiles, or nations not converted to\n   Christianity; heathen; pagan; -- opposed to Jewish and Christian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnic', 'n.', 'A heathen; a pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnically', 'adv.', 'In an ethnical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnicism', 'n.', 'Heathenism; paganism; idolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnographer', 'n.', 'One who investigates ethnography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnographic', 'a.', 'Alt. of Ethnographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnographical', 'a.', 'pertaining to ethnography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnographically', 'adv.', 'In an ethnographical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnography', 'n.', 'That branch of knowledge which has for its subject the\n   characteristics of the human family, developing the details with which\n   ethnology as a comparative science deals; descriptive ethnology. See\n   Ethnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnologic', 'a', 'Alt. of Ethnological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnological', 'a', 'Of or pertaining to ethnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnologically', 'adv.', 'In an ethnological manner; by ethnological\n   classification; as, one belonging ethnologically to an African race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnologist', 'n.', 'One versed in ethnology; a student of ethnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethnology', 'n.', 'The science which treats of the division of mankind into\n   races, their origin, distribution, and relations, and the peculiarities\n   which characterize them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethologic', 'a', 'Alt. of Ethological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethological', 'a', 'treating of, or pertaining to, ethnic or morality, or\n   the science of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethologist', 'n.', 'One who studies or writes upon ethology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethology', 'n.', 'A treatise on morality; ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethology', 'n.', 'The science of the formation of character, national and\n   collective as well as individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethopoetic', '', 'Expressing character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethule', '', 'Ethyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethyl', 'n.', 'A monatomic, hydrocarbon radical, C2H5 of the paraffin\n   series, forming the essential radical of ethane, and of common alcohol\n   and ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylamine', 'n.', 'A colorless, mobile, inflammable liquid, C2H5.NH2, very\n   volatile and with an ammoniacal odor. It is a strong base, and is a\n   derivative of ammonia. Called also ethyl carbamine, and amido ethane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylate', 'n.', 'A compound derived from ethyl alcohol by the replacement\n   of the hydroxyl hydrogen, after the manner of a hydrate; an ethyl\n   alcoholate; as, potassium ethylate, C2H5.O.K.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylene', 'n.', 'A colorless, gaseous hydrocarbon, C2H4, forming an\n   important ingredient of illuminating gas, and also obtained by the\n   action of concentrated sulphuric acid in alcohol. It is an unsaturated\n   compound and combines directly with chlorine and bromine to form oily\n   liquids (Dutch liquid), -- hence called olefiant gas. Called also\n   ethene, elayl, and formerly, bicarbureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylic', '', 'Pertaining to, derived from, or containing, ethyl; as,\n   ethylic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylidene', '', 'An unsymmetrical, divalent, hydrocarbon radical, C2H4\n   metameric with ethylene but written thus, CH3.CH to distinguish it from\n   the symmetrical ethylene, CH2.CH2. Its compounds are derived from\n   aldehyde. Formerly called also ethidene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylin', '', 'Any one of the several complex ethers of ethyl and glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ethylsulphuric', 'a.', 'Pertaining to, or containing, ethyl and sulphuric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolated', 'imp. & p. p.', 'of Etiolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolating', 'p. pr. & vb. n', 'of Etiolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolate', 'v. i.', 'To become white or whiter; to be whitened or blanched\n   by excluding the light of the sun, as, plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolate', 'v. i.', 'To become pale through disease or absence of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolate', 'v. t.', 'To blanch; to bleach; to whiten by depriving of the\n   suns rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolate', 'v. t.', 'To cause to grow pale by disease or absence of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolate', 'a.', 'Alt. of Etiolated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolated', 'a.', 'Having a blanched or faded appearance, as birds\n   inhabiting desert regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolation', 'n.', 'The operation of blanching plants, by excluding the\n   light of the sun; the condition of a blanched plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiolation', 'n.', 'Paleness produced by absence of light, or by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etoolin', 'n.', 'A yellowish coloring matter found in plants grown in\n   darkness, which is supposed to be an antecedent condition of\n   chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiological', 'a.', 'Pertaining to, or inquiring into, causes;\n   aetiological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiology', 'n.', 'The science of causes. Same as /tiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etiquette', 'n.', 'The forms required by good breeding, or prescribed by\n   authority, to be observed in social or official life; observance of the\n   proprieties of rank and occasion; conventional decorum; ceremonial code\n   of polite society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etna', 'n.', 'A kind of small, portable, cooking apparatus for which heat\n   is furnished by a spirit lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etnean', 'a.', 'Pertaining to Etna, a volcanic mountain in Sicily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etoile', 'n.', 'See Estoile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etrurian', 'a.', 'Of or relating to ancient Etruria, in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etrurian', 'n.', 'A native or inhabitant of ancient Etruria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etruscan', 'n.', 'Of or relating to Etruria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etruscan', 'n.', 'A native or inhabitant of Etruria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etter pike', 'n.', 'The stingfish, or lesser weever (Tranchinus vipera).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ettin', 'n.', 'A giant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ettle', 'v. t.', 'To earn. [Obs.] See Addle, to earn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etude', 'n.', 'A composition in the fine arts which is intended, or may\n   serve, for a study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etude', 'n.', 'A study; an exercise; a piece for practice of some special\n   point of technical execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etui', 'n.', 'A case for one or several small articles; esp., a box in\n   which scissors, tweezers, and other articles of toilet or of daily use\n   are carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etwee', 'n.', 'See Etui.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etym', 'n.', 'See Etymon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymic', 'a.', 'Relating to the etymon; as, an etymic word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologer', 'n.', 'An etymologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymological', 'a.', 'Pertaining to etymology, or the derivation of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologicon', 'n.', 'An etymological dictionary or manual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologist', 'n.', 'One who investigates the derivation of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologize', 'v. t.', 'To give the etymology of; to trace to the root or\n   primitive, as a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologize', 'v. t.', 'To search into the origin of words; to deduce words\n   from their simple roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymologies', 'pl. ', 'of Etymology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymology', 'n.', 'That branch of philological science which treats of the\n   history of words, tracing out their origin, primitive significance, and\n   changes of form and meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymology', 'n.', 'That part of grammar which relates to the changes in the\n   form of the words in a language; inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymons', 'pl. ', 'of Etymon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etyma', 'pl. ', 'of Etymon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymon', 'n.', 'An original form; primitive word; root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etymon', 'n.', 'Original or fundamental signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Etypical', 'a.', 'Diverging from, or lacking conformity to, a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eu', '', 'A prefix used frequently in composition, signifying well, good,\n   advantageous; -- the opposite of dys-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucairite', 'n.', 'A metallic mineral, a selenide of copper and silver; --\n   so called by Berzelius on account of its being found soon after the\n   discovery of the metal selenium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucalyn', 'n.', 'An unfermentable sugar, obtained as an uncrystallizable\n   sirup by the decomposition of melitose; also obtained from a Tasmanian\n   eucalyptus, -- whence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucalyptol', 'n.', 'A volatile, terpenelike oil extracted from the\n   eucalyptus, and consisting largely of cymene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucalyptus', 'n.', 'A myrtaceous genus of trees, mostly Australian. Many of\n   them grow to an immense height, one or two species exceeding the height\n   even of the California Sequoia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharis', 'n.', 'A genus of South American amaryllidaceous plants with\n   large and beautiful white blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharist', 'n.', 'The act of giving thanks; thanksgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharist', 'n.', 'The sacrament of the Lords Supper; the solemn act of\n   ceremony of commemorating the death of Christ, in the use of bread and\n   wine, as the appointed emblems; the communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharistic', 'a.', 'Alt. of Eucharistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharistical', 'a.', 'Giving thanks; expressing thankfulness; rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucharistical', 'a.', 'Pertaining to the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchite', 'n.', 'One who resolves religion into prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchloric', 'a.', 'Relating to, or consisting of, euchlorine; as, euchloric\n   /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchlorine', 'n.', 'A yellow or greenish yellow gas, first prepared by\n   Davy, evolved from potassium chlorate and hydrochloric acid. It is\n   supposed to consist of chlorine tetroxide with some free chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchologion', 'n.', 'Alt. of Euchology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchology', 'n.', 'A formulary of prayers; the book of offices in the Greek\n   Church, containing the liturgy, sacraments, and forms of prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchologue', 'n.', 'Euchology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchre', 'n.', 'A game at cards, that may be played by two, three, or four\n   persons, the highest card (except when an extra card called the Joker\n   is used) being the knave of the same suit as the trump, and called\n   right bower, the lowest card used being the seven, or frequently, in\n   two-handed euchre, the nine spot. See Bower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchre', 'v. t.', 'To defeat, in a game of euchre, the side that named the\n   trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchre', 'v. t.', 'To defeat or foil thoroughly in any scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchroic', 'a.', 'Having a fine color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchroite', 'n.', 'A mineral occurring in transparent emerald green\n   crystals. It is hydrous arseniate of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchrone', 'n.', 'A substance obtained from euchroic acid. See Eychroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euchymy', 'n.', 'A good state of the blood and other fluids of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euclase', 'n.', 'A brittle gem occurring in light green, transparent\n   crystals, affording a brilliant clinodiagonal cleavage. It is a\n   silicate of alumina and glucina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euclid', 'n.', 'A Greek geometer of the 3d century b. c.; also, his\n   treatise on geometry, and hence, the principles of geometry, in\n   general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euclidian', 'n.', 'Related to Euclid, or to the geometry of Euclid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucopepoda', 'n. pl.', 'A group which includes the typical copepods and the\n   lerneans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eucrasy', '', 'Such a due mixture of qualities in bodies as constitutes\n   health or soundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euctical', '', 'Expecting a wish; supplicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemon', 'n.', 'Alt. of Eudaemon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemon', 'n.', 'A good angel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemonics', 'n.', 'Alt. of Eudaemonics');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemonics', 'n.', 'That part of moral philosophy which treats of\n   happiness; the science of happiness; -- contrasted with aretaics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemonism', 'n.', 'Alt. of Eudaemonism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemonism', 'n.', 'That system of ethics which defines and enforces moral\n   obligation by its relation to happiness or personal well-being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemonist', 'n.', 'Alt. of Eudaemonist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemonist', 'n.', 'One who believes in eudemonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemonistic', 'a.', 'Alt. of Eudaemonistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemonistic', 'a.', 'Of or pertaining to eudemonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudemonistical', 'a.', 'Alt. of Eudaemonistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudaemonistical', 'a.', 'Eudemonistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudialyte', 'n.', 'A mineral of a brownish red color and vitreous luster,\n   consisting chiefly of the silicates of iron, zirconia, and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudiometer', 'n.', 'An instrument for the volumetric measurement of gases;\n   -- so named because frequently used to determine the purity of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudiometric', 'a.', 'Alt. of Eudiometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudiometrical', 'a.', 'Of or pertaining to a eudiometer; as, eudiometrical\n   experiments or results.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudiometry', 'n.', 'The art or process of determining the constituents of a\n   gaseous mixture by means of the eudiometer, or for ascertaining the\n   purity of the air or the amount of oxygen in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudipleura', 'n. pl.', 'The fundamental forms of organic life, that are\n   composed of two equal and symmetrical halves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eudoxian', 'n.', 'A follower of Eudoxius, patriarch of Antioch and\n   Constantinople in the 4th century, and a celebrated defender of the\n   doctrines of Arius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euganoidei', 'n. pl.', 'A group which includes the bony ganoids, as the gar\n   pikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euge', 'n.', 'Applause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenia', 'n.', 'A genus of myrtaceous plants, mostly of tropical\n   countries, and including several aromatic trees and shrubs, among which\n   are the trees which produce allspice and cloves of commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenic', 'a.', 'Pertaining to, or derived from, cloves; as, eugenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenic', 'a.', 'Well-born; of high birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenics', 'n.', 'The science of improving stock, whether human or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenin', 'n.', 'A colorless, crystalline substance extracted from oil of\n   cloves; -- called also clove camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugenol', 'n.', 'A colorless, aromatic, liquid hydrocarbon, C10H12O2\n   resembling the phenols, and hence also called eugenic acid. It is found\n   in the oils of pimento and cloves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugeny', '', 'Nobleness of birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugetic', 'a.', 'Alt. of Eugetinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugetinic', 'a.', 'Pertaining to, or derived from, eugenol; as, eugetic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugh', 'n.', 'The yew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugubian', 'a.', 'Alt. of Eugubine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eugubine', 'a.', 'Of or pertaining to the ancient town of Eugubium (now\n   Gubbio); as, the Eugubine tablets, or tables, or inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euharmonic', 'a.', 'Producing mathematically perfect harmony or concord;\n   sweetly or perfectly harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euhemerism', 'n.', 'The theory, held by Euhemerus, that the gods of\n   mythology were but deified mortals, and their deeds only the\n   amplification in imagination of human acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euhemerist', 'n.', 'One who advocates euhemerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euhemeristic', 'a.', 'Of or pertaining to euhemerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euhemerize', 'v. t.', 'To interpret (mythology) on the theory of\n   euhemerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euisopoda', '', 'A group which includes the typical Isopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulachon', 'n.', 'The candlefish. [Written also oulachan, oolacan, and\n   ulikon.] See Candlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulerian', 'a.', 'Pertaining to Euler, a German mathematician of the 18th\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogic', 'a.', 'Alt. of Eulogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogical', 'a.', 'Bestowing praise of eulogy; commendatory; eulogistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogist', 'n.', 'One who eulogizes or praises; panegyrist; encomiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogistic', 'a.', 'Alt. of Eulogistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogistical', 'a.', 'Of or pertaining to eulogy; characterized by eulogy;\n   bestowing praise; panegyrical; commendatory; laudatory; as, eulogistic\n   speech or discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogiums', 'pl. ', 'of Eulogium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogium', 'n.', 'A formal eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogized', 'imp. & p. p.', 'of Eulogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogizing', 'p. pr. & vb. n.', 'of Eulogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogize', 'v. t.', 'To speak or write in commendation of (another); to\n   extol in speech or writing; to praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogies', 'pl. ', 'of Eulogy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulogy', 'n.', 'A speech or writing in commendation of the character or\n   services of a person; as, a fitting eulogy to worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eulytite', 'n.', 'A mineral, consisting chiefly of the silicate of bismuth,\n   found at Freiberg; -- called also culytine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eumenides', 'n. pl.', 'A euphemistic name for the Furies of Erinyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eumolpus', 'n.', 'A genus of small beetles, one species of which (E. viti)\n   is very injurious to the vines in the wine countries of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunomian', 'n.', 'A follower of Eunomius, bishop of Cyzicus (4th century A.\n   D.), who held that Christ was not God but a created being, having a\n   nature different from that of the Father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunomian', 'a.', 'Of or pertaining to Eunomius or his doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunomy', 'n.', 'Equal law, or a well-adjusted constitution of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunuch', 'n.', 'A male of the human species castrated; commonly, one of a\n   class of such persons, in Oriental countries, having charge of the\n   womens apartments. Some of them, in former times, gained high official\n   rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunuch', 'v. t.', 'Alt. of Eunuchate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunuchate', 'v. t.', 'To make a eunuch of; to castrate. as a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eunuchism', 'n.', 'The state of being eunuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euonymin', 'n.', 'A principle or mixture of principles derived from\n   Euonymus atropurpureus, or spindle tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euonymus', 'n.', 'A genus of small European and American trees; the spindle\n   tree. The bark is used as a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euornithes', 'n. pl.', 'The division of Aves which includes all the typical\n   birds, or all living birds except the penguins and birds of ostrichlike\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euosmitte', 'n.', 'A fossil resin, so called from its strong, peculiar,\n   pleasant odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupathy', 'n.', 'Right feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupatorin Eupatorine', 'n.', 'A principle or mixture of principles\n   extracted from various species of Eupatorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupatorium', 'n.', 'A genus of perennial, composite herbs including hemp\n   agrimony, boneset, throughwort, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupatrid', 'n.', 'One well born, or of noble birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupepsia', 'n.', 'Alt. of Eupepsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupepsy', 'n.', 'Soundness of the nutritive or digestive organs; good\n   concoction or digestion; -- opposed to dyspepsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupeptic', 'a.', 'Of or pertaining to good digestion; easy of digestion;\n   having a good digestion; as, eupeptic food; an eupeptic man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemism', 'n.', 'A figure in which a harts or indelicate word or\n   expression is softened; a way of describing an offensive thing by an\n   inoffensive expression; a mild name for something disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemistic', 'a.', 'Alt. of Euphemistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemistical', 'a.', 'Pertaining to euphemism; containing a euphemism;\n   softened in expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemized', 'imp. & p. p.', 'of Euphemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemizing', 'p. pr. & vb. n.', 'of Euphemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphemize', 'v. t. & i.', 'To express by a euphemism, or in delicate\n   language; to make use of euphemistic expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphoniad', 'n.', 'An instrument in which are combined the characteristic\n   tones of the organ and various other instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonic', 'a.', 'Alt. of Euphonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonical', 'a.', 'Pertaining to, or exhibiting, euphony; agreeable in\n   sound; pleasing to the ear; euphonious; as, a euphonic expression;\n   euphonical orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonicon', 'n.', 'A kind of upright piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonious', 'a.', 'Pleasing or sweet in sound; euphonic; smooth-sounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonism', 'n.', 'An agreeable combination of sounds; euphony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonium', 'n.', 'A bass instrument of the saxhorn family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonize', 'v. t.', 'To make euphonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonon', 'n.', 'An instrument resembling the organ in tine and the\n   upright piano in form. It is characterized by great strength and\n   sweetness of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonous', 'n.', 'Euphonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphonies', 'pl. ', 'of Euphony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphony', 'n.', 'A pleasing or sweet sound; an easy, smooth enunciation of\n   sounds; a pronunciation of letters and syllables which is pleasing to\n   the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphorbia', 'n.', 'Spurge, or bastard spurge, a genus of plants of many\n   species, mostly shrubby, herbaceous succulents, affording an acrid,\n   milky juice. Some of them are armed with thorns. Most of them yield\n   powerful emetic and cathartic products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphorbiaceous', 'a.', 'Alt. of Euphorbial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphorbial', 'a.', 'Of, relating to, or resembling, the Euphorbia family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphorbin Euphorbine', 'n.', 'A principle, or mixture of principles,\n   derived from various species of Euphorbia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphorbium', 'n.', 'An inodorous exudation, usually in the form of yellow\n   tears, produced chiefly by the African Euphorbia resinifrea. It was\n   formerly employed medicinally, but was found so violent in its effects\n   that its use is nearly abandoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphotide', 'n.', 'A rock occurring in the Alps, consisting of saussurite\n   and smaragdite; -- sometimes called gabbro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphrasy', 'n.', 'The plant eyesight (euphrasia officionalis), formerly\n   regarded as beneficial in disorders of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphroe', 'n.', 'A block or long slat of wood, perforated for the passage\n   of the crowfoot, or cords by which an awning is held up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphuism', 'n.', 'An affectation of excessive elegance and refinement of\n   language; high-flown diction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphuist', 'n.', 'One who affects excessive refinement and elegance of\n   language; -- applied esp. to a class of writers, in the age of\n   Elizabeth, whose productions are marked by affected conceits and\n   high-flown diction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphuistic', 'a.', 'Belonging to the euphuists, or euphuism; affectedly\n   refined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euphuize', 'v. t.', 'To affect excessive refinement in language; to be\n   overnice in expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupione', 'n.', 'A limpid, oily liquid obtained by the destructive\n   distillation of various vegetable and animal substances; --\n   specifically, an oil consisting largely of the higher hydrocarbons of\n   the paraffin series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupittone', 'n.', 'A yellow, crystalline substance, resembling aurin, and\n   obtained by the oxidation of pittacal; -- called also eupittonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupittonic', 'a.', 'Pertaining to, or derived from, eupittone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euplastic', 'a.', 'Having the capacity of becoming organizable in a high\n   degree, as the matter forming the false membranes which sometimes\n   result from acute inflammation in a healthy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euplastic', 'n.', 'Organizable substance by which the tissues of an animal\n   body are renewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euplectella', 'n.', 'A genus of elegant, glassy sponges, consisting of\n   interwoven siliceous fibers, and growing in the form of a cornucopia;\n   -- called also Venuss flower-basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euplexoptera', 'n. pl.', 'An order of insects, including the earwig. The\n   anterior wings are short, in the form of elytra, while the posterior\n   wings fold up beneath them. See Earwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupnaea', 'n.', 'Normal breathing where arterialization of the blood is\n   normal, in distinction from dyspnaea, in which the blood is\n   insufficiently arterialized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eupryion', 'n.', 'A contrivance for obtaining a light instantaneous, as a\n   lucifer match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurasian', 'n.', 'A child of a European parent on the one side and an\n   Asiatic on the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurasian', 'n.', 'One born of European parents in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurasian', 'a.', 'Of European and Asiatic descent; of or pertaining to both\n   Europe and Asia; as, the great Eurasian plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurasiatio', 'a.', 'Of or pertaining to the continents of Europe and Asia\n   combined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eureka', '', 'The exclamation attributed to Archimedes, who is said to have\n   cried out \"Eureka! eureka!\" (I have found it! I have found it!), upon\n   suddenly discovering a method of finding out how much the gold of King\n   Hieros crown had been alloyed. Hence, an expression of triumph\n   concerning a discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurhipidurous', 'a.', 'Having a fanlike tail; belonging to the\n   Eurhipidurae, a division of Aves which includes all living birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euripize', 'v. t.', 'To whirl hither and thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euripus', 'n.', 'A strait; a narrow tract of water, where the tide, or a\n   current, flows and reflows with violence, as the ancient fright of this\n   name between Eubaea and Baeotia. Hence, a flux and reflux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euritte', 'n.', 'A compact feldspathic rock; felsite. See Felsite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euritic', 'a.', 'Of or pelating to eurite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euroclydon', 'n.', 'A tempestuous northeast wind which blows in the\n   Mediterranean. See Levanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'European', 'a.', 'Of or pertaining to Europe, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'European', 'n.', 'A native or an inhabitant of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Europeanize', 'v. t.', 'To cause to become like the Europeans in manners or\n   character; to habituate or accustom to European usages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurus', 'n.', 'The east wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euryale', 'n.', 'A genus of water lilies, growing in India and China. The\n   only species (E. ferox) is very prickly on the peduncles and calyx. The\n   rootstocks and seeds are used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euryale', 'n.', 'A genus of ophiurans with much-branched arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euryalida', 'n. pl.', 'A tribe of Ophiuroidea, including the genera\n   Euryale, Astrophyton, etc. They generally have the arms branched. See\n   Astrophyton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurycerous', 'a.', 'Having broad horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurypteroid', 'a.', 'Like, or pertaining to, the genus Euryperus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurypteroidea', 'n. pl.', 'An extinct order of Merostomata, of which the\n   genus Eurypterus is the type. They are found only in Paleozoic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurypterus', 'n.', 'A genus of extinct Merostomata, found in Silurian\n   rocks. Some of the species are more than three feet long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurythmy', 'n.', 'Just or harmonious proportion or movement, as in the\n   composition of a poem, an edifice, a painting, or a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eurythmy', 'n.', 'Regularly of the pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eusebian', 'n.', 'A follower of Eusebius, bishop of Caesarea, who was a\n   friend and protector of Arius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eustachian', 'a.', 'Discovered by Eustachius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eustachian', 'a.', 'Pertaining to the Eustachian tube; as, Eustachian\n   catheter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eustyle', 'n.', 'See Intercolumnlation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eutaxy', 'n.', 'Good or established order or arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euterpe', '', 'The Muse who presided over music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euterpe', '', 'A genus of palms, some species of which are elegant trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euterpean', 'a.', 'Of or pertaining to Euterpe or to music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euthanasia', 'n.', 'An easy death; a mode of dying to be desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euthanasy', 'n.', 'Same as Euthanasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euthiochroic', 'a.', 'Pertaining to, or denoting, an acid so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euthyneura', 'n. pl.', 'A large division of gastropod molluske, including\n   the Pulmonifera and Opisthobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eutrophy', 'n.', 'Healthy nutrition; soundless as regards the nutritive\n   functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eutychian', 'n.', 'A follower of Eutyches [5th century], who held that the\n   divine and the human in the person of Christ were blended together as\n   to constitute but one nature; a monophysite; -- opposed to Nestorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eutychianism', 'n.', 'The doctrine of Eutyches and his followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euxanthic', 'a.', 'Having a yellow color; pertaining to, derived from, or\n   resembling, euxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euxanthin', 'n.', 'A yellow pigment imported from India and China. It has a\n   strong odor, and is said to be obtained from the urine of herbivorous\n   animals when fed on the mango. It consists if a magnesium salt of\n   euxanthic acid. Called also puri, purree, and Indian yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Euxenite', 'n.', 'A brownish black mineral with a metallic luster, found in\n   Norway. It contains niobium, titanium, yttrium, and uranium, with some\n   other metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacate', 'v. t.', 'To empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuant', 'a.', 'Emptying; evacuative; purgative; cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuant', 'n.', 'A purgative or cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuated', 'imp. & p. p.', 'of Evacuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuating', 'p. pr. & vb. n.', 'of Evacuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. t.', 'To make empty; to empty out; to remove the contents\n   of; as, to evacuate a vessel or dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. t.', 'Fig.: To make empty; to deprive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. t.', 'To remove; to eject; to void; to discharge, as the\n   contents of a vessel, or of the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. t.', 'To withdraw from; to quit; to retire from; as,\n   soldiers from a country, city, or fortress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. t.', 'To make void; to nullify; to vacate; as, to evacuate a\n   contract or marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuate', 'v. i.', 'To let blood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuation', 'n.', 'The act of emptying, clearing of the contents, or\n   discharging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuation', 'n.', 'Withdrawal of troops from a town, fortress, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuation', 'n.', 'Voidance of any matter by the natural passages of the\n   body or by an artificial opening; defecation; also, a diminution of the\n   fluids of an animal body by cathartics, venesection, or other means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuation', 'n.', 'That which is evacuated or discharged; especially, a\n   discharge by stool or other natural means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuation', 'n.', 'Abolition; nullification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuative', 'a.', 'Serving of tending to evacuate; cathartic; purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuator', 'n.', 'One who evacuates; a nullifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evacuatory', 'n.', 'A purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaded', 'imp. & p. p.', 'of Evade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evading', 'p. pr. & vb. n.', 'of Evade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evade', 'v. t.', 'To get away from by artifice; to avoid by dexterity,\n   subterfuge, address, or ingenuity; to elude; to escape from cleverly;\n   as, to evade a blow, a pursuer, a punishment; to evade the force of an\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evade', 'v. t.', 'To escape; to slip away; -- sometimes with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evade', 'v. t.', 'To attempt to escape; to practice artifice or sophistry,\n   for the purpose of eluding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evadible', 'a.', 'Capable of being evaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evagation', 'n.', 'A wandering about; excursion; a roving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evagination', 'n.', 'The act of unsheathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eval', 'a.', 'Relating to time or duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaluate', 'v. t.', 'To fix the value of; to rate; to appraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaluation', 'n.', 'Valuation; appraisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanesced', 'imp. & p. p.', 'of Evanesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanescing', 'p. pr. & vb. n.', 'of Evanesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanesce', 'v. i.', 'To vanish away; to become dissipated and disappear,\n   like vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanescence', 'n.', 'The act or state of vanishing away; disappearance; as,\n   the evanescence of vapor, of a dream, of earthly plants or hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanescent', 'a.', 'Liable to vanish or pass away like vapor; vanishing;\n   fleeting; as, evanescent joys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanescent', 'a.', 'Vanishing from notice; imperceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanescently', 'adv. In a vanishing ', '; imperceptibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangel', 'n.', 'Good news; announcement of glad tidings; especially, the\n   gospel, or a gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelian', 'a.', 'Rendering thanks for favors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelic', 'a.', 'Belonging to, or contained in, the gospel; evangelical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelical', 'a.', 'Contained in, or relating to, the four Gospels; as,\n   the evangelical history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelical', 'a.', 'Belonging to, agreeable or consonant to, or contained\n   in, the gospel, or the truth taught in the New Testament; as,\n   evangelical religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelical', 'a.', 'Earnest for the truth taught in the gospel; strict in\n   interpreting Christian doctrine; preeminetly orthodox; -- technically\n   applied to that party in the Church of England, and in the Protestant\n   Episcopal Church, which holds the doctrine of \"Justification by Faith\n   alone\"; the Low Church party. The term is also applied to other\n   religion bodies not regarded as orthodox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelical', 'n.', 'One of evangelical principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelicalism', 'n.', 'Adherence to evangelical doctrines; evangelism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelically', 'adv.', 'In an evangelical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelicalness', 'n.', 'State of being evangelical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelicism', 'n.', 'Evangelical principles; evangelism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelicity', 'n.', 'Evangelicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelism', 'n.', 'The preaching or promulgation of the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelist', 'n.', 'A bringer of the glad tidings of Church and his\n   doctrines. Specially: (a) A missionary preacher sent forth to prepare\n   the way for a resident pastor; an itinerant missionary preacher. (b) A\n   writer of one of the four Gospels (With the definite article); as, the\n   four evangelists, Matthew, Mark, Luke, and John. (c) A traveling\n   preacher whose efforts are chiefly directed to arouse to immediate\n   repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelistary', 'n.', 'A selection of passages from the Gospels, as a\n   lesson in divine service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelistic', 'a.', 'Pertaining to the four evangelists; designed or\n   fitted to evangelize; evangelical; as, evangelistic efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelization', 'n.', 'The act of evangelizing; the state of being\n   evangelized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelized', 'imp. & p. p.', 'of Evangelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelizing', 'p. pr. & vb. n.', 'of Evangelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelize', 'v. t.', 'To instruct in the gospel; to preach the gospel to;\n   to convert to Christianity; as, to evangelize the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangelize', 'v. i.', 'To preach the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangely', 'n.', 'Evangel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evangile', 'n.', 'Good tidings; evangel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanid', 'a.', 'Liable to vanish or disappear; faint; weak; evanescent; as,\n   evanid color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanish', 'v. i.', 'To vanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evanishment', 'n.', 'A vanishing; disappearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporable', 'a.', 'Capable of being converted into vapor, or dissipated by\n   evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporated', 'imp. & p. p.', 'of Evaporate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporating', 'p. pr. & vb. n.', 'of Evaporate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'v. t.', 'To pass off in vapor, as a fluid; to escape and be\n   dissipated, either in visible vapor, or in practice too minute to be\n   visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'v. t.', 'To escape or pass off without effect; to be\n   dissipated; to be wasted, as, the spirit of writer often evaporates in\n   the process of translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'v. t.', 'To convert from a liquid or solid state into vapor\n   (usually) by the agency of heat; to dissipate in vapor or fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'v. t.', 'To expel moisture from (usually by means of\n   artificial heat), leaving the solid portion; to subject to evaporation;\n   as, to evaporate apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'v. t.', 'To give vent to; to dissipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporate', 'a.', 'Dispersed in vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporation', 'n.', 'The process by which any substance is converted from a\n   liquid state into, and carried off in, vapor; as, the evaporation of\n   water, of ether, of camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporation', 'n.', 'The transformation of a portion of a fluid into vapor,\n   in order to obtain the fixed matter contained in it in a state of\n   greater consistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporation', 'n.', 'That which is evaporated; vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporation', 'n.', 'See Vaporization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporaive', 'a.', 'Pertaining to, or producing, evaporation; as, the\n   evaporative process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporator', 'n.', 'An apparatus for condensing vegetable juices, or for\n   drying fruit by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evaporometer', 'n.', 'An instrument for ascertaining the quantity of a\n   fluid evaporated in a given time; an atmometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evasible', 'a.', 'That may be evaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evasion', 'n.', 'The act of eluding or avoiding, particularly the pressure\n   of an argument, accusation, charge, or interrogation; artful means of\n   eluding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evasive', 'a.', 'Tending to evade, or marked by evasion; elusive;\n   shuffling; avoiding by artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eve', 'n.', 'Evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eve', 'n.', 'The evening before a holiday, -- from the Jewish mode of\n   reckoning the day as beginning at sunset. not at midnight; as,\n   Christians eve is the evening before Christmas; also, the period\n   immediately preceding some important event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evectics', 'n.', 'The branch of medical science which teaches the method of\n   acquiring a good habit of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evection', '', 'The act of carrying up or away; exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evection', '', 'An inequality of the moons motion is its orbit to the\n   attraction of the sun, by which the equation of the center is\n   diminished at the syzygies, and increased at the quadratures by about\n   1¡ 20.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evection', '', 'The libration of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'n.', 'Evening. See Eve, n. 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Level, smooth, or equal in surface; not rough; free from\n   irregularities; hence uniform in rate of motion of action; as, even\n   ground; an even speed; an even course of conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Equable; not easily ruffed or disturbed; calm; uniformly\n   self-possessed; as, an even temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Parallel; on a level; reaching the same limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Balanced; adjusted; fair; equitable; impartial; just to both\n   side; owing nothing on either side; -- said of accounts, bargains, or\n   persons indebted; as, our accounts are even; an even bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Without an irregularity, flaw, or blemish; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Associate; fellow; of the same condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Not odd; capable of division by two without a remainder; --\n   said of numbers; as, 4 and 10 are even numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evened', 'imp. & p. p.', 'of Even');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evening', 'p. pr. & vb. n.', 'of Even');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. t.', 'To make even or level; to level; to lay smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. t.', 'To equal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. t.', 'To place in an equal state, as to obligation, or in a\n   state in which nothing is due on either side; to balance, as accounts;\n   to make quits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. t.', 'To set right; to complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. t.', 'To act up to; to keep pace with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'v. i.', 'To be equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'In an equal or precisely similar manner; equally; precisely;\n   just; likewise; as well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'Up to, or down to, an unusual measure or level; so much as;\n   fully; quite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'As might not be expected; -- serving to introduce what is\n   unexpected or less expected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Even', 'a.', 'At the very time; in the very case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evene', 'v. i.', 'To happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evener', 'n.', 'One who, or that which makes even.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evener', 'n.', 'In vehicles, a swinging crossbar, to the ends of which\n   other crossbars, or whiffletrees, are hung, to equalize the draught\n   when two or three horses are used abreast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenfall', 'n.', 'Beginning of evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenhand', 'n.', 'Equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenhanded', 'a.', 'Fair or impartial; unbiased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evening', 'n.', 'The latter part and close of the day, and the beginning of\n   darkness or night; properly, the decline of the day, or of the sum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evening', 'n.', 'The latter portion, as of life; the declining period, as\n   of strength or glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenly', 'adv.', 'With an even, level, or smooth surface; without\n   roughness, elevations, or depression; uniformly; equally; comfortably;\n   impartially; serenely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenminded', 'a.', 'Having equanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evenness', 'n.', 'The state of being ven, level, or disturbed; smoothness;\n   horizontal position; uniformity; impartiality; calmness; equanimity;\n   appropriate place or level; as, evenness of surface, of a fluid at\n   rest, of motion, of dealings, of temper, of condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evensong', 'n.', 'A song for the evening; the evening service or form of\n   worship (in the Church of England including vespers and compline);\n   also, the time of evensong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Event', 'n.', 'That which comes, arrives, or happens; that which falls out;\n   any incident, good or bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Event', 'n.', 'An affair in hand; business; enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Event', 'n.', 'The consequence of anything; the issue; conclusion; result;\n   that in which an action, operation, or series of operations,\n   terminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Event', 'v. t.', 'To break forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eventerate', 'v. t.', 'To rip open; todisembowel.');
