  INSERT INTO words (word, word_type, description) VALUES  (    'Evigilation', 'n.', 'A waking up or awakening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'a.', 'Having qualities tending to injury and mischief; having a\n   nature or properties which tend to badness; mischievous; not good;\n   worthless or deleterious; poor; as, an evil beast; and evil plant; an\n   evil crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'a.', 'Having or exhibiting bad moral qualities; morally corrupt;\n   wicked; wrong; vicious; as, evil conduct, thoughts, heart, words, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'a.', 'Producing or threatening sorrow, distress, injury, or\n   calamity; unpropitious; calamitous; as, evil tidings; evil arrows; evil\n   days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'n.', 'Anything which impairs the happiness of a being or deprives a\n   being of any good; anything which causes suffering of any kind to\n   sentient beings; injury; mischief; harm; -- opposed to good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'n.', 'Moral badness, or the deviation of a moral being from the\n   principles of virtue imposed by conscience, or by the will of the\n   Supreme Being, or by the principles of a lawful human authority;\n   disposition to do wrong; moral offence; wickedness; depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'n.', 'malady or disease; especially in the phrase kings evil, the\n   scrofula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evil', 'adv.', 'In an evil manner; not well; ill; badly; unhappily;\n   injuriously; unkindly.');
