  INSERT INTO words (word, word_type, description) VALUES  (    'Evolving', 'p. pr. & vb. n.', 'of Evolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evolve', 'v. t.', 'To unfold or unroll; to open and expand; to disentangle\n   and exhibit clearly and satisfactorily; to develop; to derive; to\n   educe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Evolve', 'v. t.', 'To throw out; to emit; as, to evolve odors.');
