  INSERT INTO words (word, word_type, description) VALUES  (    'Ex-', '', 'A prefix from the latin preposition, ex, akin to Gr. ex or ek\n   signifying out of, out, proceeding from. Hence, in composition, it\n   signifies out of, as, in exhale, exclude; off, from, or out. as in\n   exscind; beyond, as, in excess, exceed, excel; and sometimes has a\n   privative sense of without, as in exalbuminuos, exsanguinous. In some\n   words, it intensifies the meaning; in others, it has little affect on\n   the signification. It becomes ef- before f, as in effuse. The form e-\n   occurs instead of ex- before b, d, g, l, m, n, r, and v, as in\n   ebullient, emanate, enormous, etc. In words from the French it often\n   appears as es-, sometimes as s- or e-; as, escape, scape, elite. Ex-,\n   prefixed to names implying office, station, condition, denotes that the\n   person formerly held the office, or is out of the office or condition\n   now; as, ex-president, ex-governor, ex-mayor, ex-convict. The Greek\n   form ex becomes ex in English, as in exarch; ek becomes ec, as in\n   eccentric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerrated', 'imp. & p. p.', 'of Exacerbate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerrating', 'p. pr. & vb. n.', 'of Exacerbate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerbate', 'v. t.', 'To render more violent or bitter; to irriate; to\n   exasperate; to imbitter, as passions or disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerbation', 'n.', 'The act rendering more violent or bitter; the state\n   of being exacerbated or intensified in violence or malignity; as,\n   exacerbation of passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerbation', 'n.', 'A periodical increase of violence in a disease, as in\n   remittent or continious fever; an increased energy of diseased and\n   painful action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacerbescence', 'n.', 'Increase of irritation or violence, particularly\n   the increase of a fever or disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacervation', 'n.', 'The act of heaping up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacinate', 'v. t.', 'To remove the kernel form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacination', 'n.', 'Removal of the kernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exact', 'a.', 'Precisely agreeing with a standard, a fact, or the truth;\n   perfectly conforming; neither exceeding nor falling short in any\n   respect; true; correct; precise; as, the clock keeps exact time; he\n   paid the exact debt; an exact copy of a letter; exact accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exact', 'a.', 'Habitually careful to agree with a standard, a rule, or a\n   promise; accurate; methodical; punctual; as, a man exact in observing\n   an appointment; in my doings I was exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exact', 'a.', 'Precisely or definitely conceived or stated; strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacted', 'imp. & p. p.', 'of Exact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacting', 'p. pr. & vb. n.', 'of Exact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exact', 'a.', 'To demand or require authoritatively or peremptorily, as a\n   right; to enforce the payment of, or a yielding of; to compel to yield\n   or to furnish; hence, to wrest, as a fee or reward when none is due; --\n   followed by from or of before the one subjected to exaction; as, to\n   exact tribute, fees, obedience, etc., from or of some one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exact', 'v. i.', 'To practice exaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacter', 'n.', 'An exactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacting', 'a.', 'Oppressive or unreasonably severe in making demands or\n   requiring the exact fulfillment of obligations; harsh; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaction', 'n.', 'The act of demanding with authority, and compelling to\n   pay or yield; compulsion to give or furnish; a levying by force; a\n   driving to compliance; as, the exaction to tribute or of obedience;\n   hence, extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaction', 'n.', 'That which is exacted; a severe tribute; a fee, reward,\n   or contribution, demanded or levied with severity or injustice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacritude', 'n.', 'The quality of being exact; exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exactly', 'adv.', 'In an exact manner; precisely according to a rule,\n   standard, or fact; accurately; strictly; correctly; nicely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exactness', 'n.', 'The condition of being exact; accuracy; nicety;\n   precision; regularity; as, exactness of jurgement or deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exactness', 'n.', 'Careful observance of method and conformity to truth;\n   as, exactness in accounts or business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exactor', 'n.', 'One who exacts or demands by authority or right; hence, an\n   extortioner; also, one unreasonably severe in injunctions or demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exactress', 'n.', 'A woman who is an exactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exacuate', 'v. t.', 'To whet or sharpen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaeresis', 'n.', 'In old writers, the operations concerned in the removal\n   of parts of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerated', 'imp. & p. p.', 'of Exaggerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerating', 'p. pr. & vb. n.', 'of Exaggerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerate', 'v. t.', 'To heap up; to accumulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerate', 'v. t.', 'To amplify; to magnify; to enlarge beyond bounds or\n   the truth ; to delineate extravagantly ; to overstate the truth\n   concerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerated', 'a.', 'Enlarged beyond bounds or the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerating', 'a.', 'That exaggerates; enlarging beyond bounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggeration', 'n.', 'The act of heaping or piling up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggeration', 'n.', 'The act of exaggerating; the act of doing or\n   representing in an excessive manner; a going beyond the bounds of truth\n   reason, or justice; a hyperbolical representation; hyperbole;\n   overstatement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggeration', 'n.', 'A representation of things beyond natural life, in\n   expression, beauty, power, vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerative', 'a.', 'Tending to exaggerate; involving exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggerator', 'n.', 'One who exaggerates; one addicted to exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaggeratory', 'a.', 'Containing, or tending to, exaggeration;\n   exaggerative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exagitate', 'v. t.', 'To stir up; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exagitate', 'v. t.', 'To satirize; to censure severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exagitation', 'n.', 'Agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalbuminous', 'a.', 'Having no albumen about the embryo; -- said of\n   certain seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalted', 'imp. & p. p.', 'of Exalt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalting', 'p. pr. & vb. n.', 'of Exalt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To raise high; to elevate; to lift up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To elevate in rank, dignity, power, wealth, character, or\n   the like; to dignify; to promote; as, to exalt a prince to the throne,\n   a citizen to the presidency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To elevate by prise or estimation; to magnify; to extol;\n   to glorify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To lift up with joy, pride, or success; to inspire with\n   delight or satisfaction; to elate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To elevate the tone of, as of the voice or a musical\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalt', 'v. t.', 'To render pure or refined; to intensify or concentrate;\n   as, to exalt the juices of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaltate', 'a.', 'Exercising its highest influence; -- said of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaltation', 'n.', 'The act of exalting or raising high; also, the state of\n   being exalted; elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaltation', 'n.', 'The refinement or subtilization of a body, or the\n   increasing of its virtue or principal property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaltation', 'n.', 'That place of a planet in the zodiac in which it was\n   supposed to exert its strongest influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalted', 'a.', 'Raised to lofty height; elevated; extolled; refined;\n   dignified; sublime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exalter', 'n.', 'One who exalts or raises to dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaltment', 'n.', 'Exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examen', 'a.', 'Examination; inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exametron', 'n.', 'An hexameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinable', 'a.', 'Capable of being examined or inquired into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinant', 'n.', 'One who examines; an examiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinant', 'n.', 'One who is to be examined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinate', 'n.', 'A person subjected to examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examination', 'n.', 'The act of examining, or state of being examined; a\n   careful search, investigation, or inquiry; scrutiny by study or\n   experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examination', 'n.', 'A process prescribed or assigned for testing\n   qualification; as, the examination of a student, or of a candidate for\n   admission to the bar or the ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinator', 'n.', 'An examiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examined', 'imp. & p. p.', 'of Examine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examining', 'p. pr. & vb. n.', 'of Examine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examine', 'v. t.', 'To test by any appropriate method; to inspect carefully\n   with a view to discover the real character or state of; to subject to\n   inquiry or inspection of particulars for the purpose of obtaining a\n   fuller insight into the subject of examination, as a material\n   substance, a fact, a reason, a cause, the truth of a statement; to\n   inquire or search into; to explore; as, to examine a mineral; to\n   examine a ship to know whether she is seaworthy; to examine a\n   proposition, theory, or question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examine', 'v. t.', 'To interrogate as in a judicial proceeding; to try or\n   test by question; as, to examine a witness in order to elicit\n   testimony, a student to test his qualifications, a bankrupt touching\n   the state of his property, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinee', 'n.', 'A person examined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examiner', 'n.', 'One who examines, tries, or inspects; one who\n   interrogates; an officer or person charged with the duty of making an\n   examination; as, an examiner of students for a degree; an examiner in\n   chancery, in the patent office, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examinership', 'n.', 'The office or rank of an examiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examining', 'a.', 'Having power to examine; appointed to examine; as, an\n   examining committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Examplary', 'a.', 'Serving for example or pattern; exemplary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'n.', 'One or a portion taken to show the character or quality of\n   the whole; a sample; a specimen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'n.', 'That which is to be followed or imitated as a model; a\n   pattern or copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'n.', 'That which resembles or corresponds with something else; a\n   precedent; a model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'n.', 'That which is to be avoided; one selected for punishment\n   and to serve as a warning; a warning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'n.', 'An instance serving for illustration of a rule or precept,\n   especially a problem to be solved, or a case to be determined, as an\n   exercise in the application of the rules of any study or branch of\n   science; as, in trigonometry and grammar, the principles and rules are\n   illustrated by examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exampled', 'imp. & p. p.', 'of Example');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exampling', 'p. pr. & vb. n.', 'of Example');
  INSERT INTO words (word, word_type, description) VALUES  (    'Example', 'v. t.', 'To set an example for; to give a precedent for; to\n   exemplify; to give an instance of; to instance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exampleless', 'a.', 'Without or above example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exampler', 'n.', 'A pattern; an exemplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exampless', 'a.', 'Exampleless. [Wrongly formed.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanguious', 'a.', 'Bloodless. [Obs.] See Exsanguious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exangulous', 'a.', 'Having no corners; without angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanimate', 'a.', 'Lifeless; dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanimate', 'a.', 'Destitute of animation; spiritless; disheartened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanimate', 'v. t.', 'To deprive of animation or of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanimation', 'n.', 'Deprivation of life or of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanimous', 'a.', 'Lifeless; dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exannulate', 'a.', 'Having the sporangium destitute of a ring; -- said of\n   certain genera of ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthem', 'n.', 'Same as Exanthema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthemata', 'pl. ', 'of Exanthema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthema', 'n.', 'An efflorescence or discoloration of the skin; an\n   eruption or breaking out, as in measles, smallpox, scarlatina, and the\n   like diseases; -- sometimes limited to eruptions attended with fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthematic', 'a.', 'Alt. of Exanthematous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthematous', 'a.', 'Of, relating to, or characterized by, exanthema;\n   efflorescent; as, an exanthematous eruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exanthesis', 'n.', 'An eruption of the skin; cutaneous efflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exantlate', 'v. t.', 'To exhaust or wear out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exantlation', 'n.', 'Act of drawing out ; exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarate', 'v. t.', 'To plow up; also, to engrave; to write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaration', 'n.', 'Act of plowing; also, act of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarch', 'n.', 'A viceroy; in Ravenna, the title of the viceroys of the\n   Byzantine emperors; in the Eastern Church, the superior over several\n   monasteries; in the modern Greek Church, a deputy of the patriarch ,\n   who visits the clergy, investigates ecclesiastical cases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarchate', 'n.', 'The office or the province of an exarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarillate', 'a.', 'Having no aril; -- said of certain seeds, or of the\n   plants producing them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarticulate', 'a.', 'Having but one joint; -- said of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exarticulation', 'n.', 'Luxation; the dislocation of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperate', 'a.', 'Exasperated; imbittered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsasperated', 'imp. & p. p.', 'of Exasperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperating', 'p. pr. & vb. n.', 'of Exasperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperate', 'v. t.', 'To irritate in a high degree; to provoke; to enrage;\n   to exscite or to inflame the anger of; as, to exasperate a person or\n   his feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperate', 'v. t.', 'To make grievous, or more grievous or malignant; to\n   aggravate; to imbitter; as, to exasperate enmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperater', 'n.', 'One who exasperates or inflames anger, enmity, or\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperation', 'n.', 'The act of exasperating or the state of being\n   exasperated; irritation; keen or bitter anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exasperation', 'n.', 'Increase of violence or malignity; aggravation;\n   exacerbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaspidean', 'a.', 'Having the anterior scute/ extending around the tarsus\n   on the outer side, leaving the inner side naked; -- said of certain\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauctorate', 'v. t.', 'See Exauthorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauctoration', 'n.', 'See Exauthoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaugurate', 'v. t.', 'To annul the consecration of; to secularize; to\n   unhellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauguration', 'n.', 'The act of exaugurating; desecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauthorate', 'v. t.', 'To deprive of authority or office; to depose; to\n   discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauthoration', 'n.', 'Deprivation of authority or dignity; degration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauthorize', 'v. t.', 'To deprive of uthority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exauthorize', 'v. t.', 'To deprive of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalceate', 'v. t.', 'To deprive of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalceation', 'n.', 'The act of depriving or divesting of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalfaction', 'n.', 'A heating or warming; calefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalfactive', 'a.', 'Serving to heat; warming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalfactory', 'a.', 'Heating; warming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excalibur', 'n.', 'The name of King Arthurs mythical sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excamb', 'v. t.', 'Alt. of Excambie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excambie', 'v. t.', 'To exchange; -- used with reference to transfers of\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excambion', 'n.', 'Alt. of Excambium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excambium', 'n.', 'Exchange; barter; -- used commonly of lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excandescence', 'n.', 'A growing hot; a white or glowing heat;\n   incandescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excandescence', 'n.', 'Violent anger; a growing angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excandescent', 'a.', 'White or glowing with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excantation', 'n.', 'Disenchantment by a countercharm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excarnate', 'v. t.', 'To deprive or clear of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excarnation', 'n.', 'The act of depriving or divesting of flesh;\n   excarnification; -- opposed to incarnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excarnificate', 'v. t.', 'To clear of flesh; to excarnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excarnification', 'n.', 'The act of excarnificating or of depriving of\n   flesh; excarnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavated', 'imp. & p. p.', 'of Excavate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavating', 'p. pr. & vb. n.', 'of Excavate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavate', 'v. t.', 'To hollow out; to form cavity or hole in; to make\n   hollow by cutting, scooping, or digging; as, to excavate a ball; to\n   excavate the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavate', 'v. t.', 'To form by hollowing; to shape, as a cavity, or\n   anything that is hollow; as, to excavate a canoe, a cellar, a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavate', 'v. t.', 'To dig out and remove, as earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavation', 'n.', 'The act of excavating, or of making hollow, by cutting,\n   scooping, or digging out a part of a solid mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavation', 'n.', 'A cavity formed by cutting, digging, or scooping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavation', 'n.', 'An uncovered cutting in the earth, in distinction from\n   a covered cutting or tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavation', 'n.', 'The material dug out in making a channel or cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excavator', 'n.', 'One who, or that which, excavates or hollows out; a\n   machine, as a dredging machine, or a tool, for excavating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excave', 'v. t.', 'To excavate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excecate', 'v. t.', 'To blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excecation', 'n.', 'The act of making blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excedent', 'v. t.', 'Excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceeded', 'imp. & p. p.', 'of Exceed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceeding', 'p. pr. & vb. n.', 'of Exceed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceed', 'v. t.', 'To go beyond; to proceed beyond the given or supposed\n   limit or measure of; to outgo; to surpass; -- used both in a good and a\n   bad sense; as, one man exceeds another in bulk, stature, weight, power,\n   skill, etc.; one offender exceeds another in villainy; his rank exceeds\n   yours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceed', 'v. i.', 'To go too far; to pass the proper bounds or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceed', 'v. i.', 'To be more or greater; to be paramount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceedable', 'a.', 'Capable of exceeding or surpassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceeder', 'n.', 'One who exceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceeding', 'a.', 'More than usual; extraordinary; more than sufficient;\n   measureless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceeding', 'adv.', 'In a very great degree; extremely; exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceedingly', 'adv.', 'To a very great degree; beyond what is usual;\n   surpassingly. It signifies more than very.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excelled', 'imp. & p. p.', 'of Excel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excelling', 'p. pr. & vb. n.', 'of Excel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excel', 'v. t.', 'To go beyond or surpass in good qualities or laudable\n   deeds; to outdo or outgo, in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excel', 'v. t.', 'To exceed or go beyond; to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excel', 'v. i.', 'To surpass others in good qualities, laudable actions, or\n   acquirements; to be distinguished by superiority; as, to excel in\n   mathematics, or classics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellence', 'n.', 'The quality of being excellent; state of possessing\n   good qualities in an eminent degree; exalted merit; superiority in\n   virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellence', 'n.', 'An excellent or valuable quality; that by which any one\n   excels or is eminent; a virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellence', 'n.', 'A title of honor or respect; -- more common in the form\n   excellency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellencies', 'pl. ', 'of Excellency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellency', 'n.', 'Excellence; virtue; dignity; worth; superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellency', 'n.', 'A title of honor given to certain high dignitaries,\n   esp. to viceroys, ministers, and ambassadors, to English colonial\n   governors, etc. It was formerly sometimes given to kings and princes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellent', 'a.', 'Excelling; surpassing others in some good quality or the\n   sum of qualities; of great worth; eminent, in a good sense; superior;\n   as, an excellent man, artist, citizen, husband, discourse, book, song,\n   etc.; excellent breeding, principles, aims, action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellent', 'a.', 'Superior in kind or degree, irrespective of moral\n   quality; -- used with words of a bad significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellent', 'adv.', 'Excellently; eminently; exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellently', 'adv.', 'In an excellent manner; well in a high degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excellently', 'adv.', 'In a high or superior degree; -- in this literal\n   use, not implying worthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excelsior', 'v. t.', 'More lofty; still higher; ever upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excelsior', 'n.', 'A kind of stuffing for upholstered furniture,\n   mattresses, etc., in which curled shreds of wood are substituted for\n   curled hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excentral', 'a.', 'Out of the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excentric', 'a.', 'Alt. of Excentrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excentrical', 'a.', 'Same as Eccentric, Eccentrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excentrical', 'a.', 'One-sided; having the normally central portion not in\n   the true center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excentricity', '', 'Same as Eccentricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excepted', 'imp. & p. p.', 'of Except');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excepting', 'p. pr. & vb. n.', 'of Except');
  INSERT INTO words (word, word_type, description) VALUES  (    'Except', 'v. t.', 'To take or leave out (anything) from a number or a whole\n   as not belonging to it; to exclude; to omit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Except', 'v. t.', 'To object to; to protest against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Except', 'v. i.', 'To take exception; to object; -- usually followed by to,\n   sometimes by against; as, to except to a witness or his testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Except', 'prep.', 'With exclusion of; leaving or left out; excepting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Except', 'conj.', 'Unless; if it be not so that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptant', 'a.', 'Making exception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excepting', 'prep. & conj., but p', 'With rejection or\n   exception of; excluding; except.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exception', 'n.', 'The act of excepting or excluding; exclusion;\n   restriction by taking out something which would otherwise be included,\n   as in a class, statement, rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exception', 'n.', 'That which is excepted or taken out from others; a\n   person, thing, or case, specified as distinct, or not included; as,\n   almost every general rule has its exceptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exception', 'n.', 'An objection, oral or written, taken, in the course of\n   an action, as to bail or security; or as to the decision of a judge, in\n   the course of a trail, or in his charge to a jury; or as to lapse of\n   time, or scandal, impertinence, or insufficiency in a pleading; also,\n   as in conveyancing, a clause by which the grantor excepts something\n   before granted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exception', 'n.', 'An objection; cavil; dissent; disapprobation; offense;\n   cause of offense; -- usually followed by to or against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptionable', 'a.', 'Liable to exception or objection; objectionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptional', 'a.', 'Forming an exception; not ordinary; uncommon; rare;\n   hence, better than the average; superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptioner', 'n.', 'One who takes exceptions or makes objections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptionless', 'a.', 'Without exception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptious', 'a.', 'Disposed or apt to take exceptions, or to object;\n   captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptive', 'a.', 'That excepts; including an exception; as, an exceptive\n   proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptless', 'a.', 'Not exceptional; usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exceptor', 'n.', 'One who takes exceptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerebration', 'n.', 'The act of removing or beating out the brains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerebrose', 'a.', 'Brainless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excern', 'v. t.', 'To excrete; to throw off through the pores; as, fluids\n   are excerned in perspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excernent', 'a.', 'Connected with, or pertaining to, excretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerp', 'a.', 'To pick out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerpted', 'imp. & p. p.', 'of Excerpt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerpting', 'p. pr. & vb. n.', 'of Excerpt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerpt', 'v. t.', 'To select; to extract; to cite; to quote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerpt', 'n.', 'An extract; a passage selected or copied from a book or\n   record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerption', 'n.', 'The act of excerpting or selecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerption', 'n.', 'That which is selected or gleaned; an extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerptive', 'a.', 'That excerpts, selects, or chooses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excerptor', 'n.', 'One who makes excerpts; a picker; a culler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excess', 'n.', 'The state of surpassing or going beyond limits; the being\n   of a measure beyond sufficiency, necessity, or duty; that which exceeds\n   what is usual or prover; immoderateness; superfluity; superabundance;\n   extravagance; as, an excess of provisions or of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excess', 'n.', 'An undue indulgence of the appetite; transgression of\n   proper moderation in natural gratifications; intemperance; dissipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excess', 'n.', 'The degree or amount by which one thing or number exceeds\n   another; remainder; as, the difference between two numbers is the\n   excess of one over the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excessive', 'a.', 'Characterized by, or exhibiting, excess; overmuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'The act of giving or taking one thing in return for\n   another which is regarded as an equivalent; as, an exchange of cattle\n   for grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'The act of substituting one thing in the place of\n   another; as, an exchange of grief for joy, or of a scepter for a sword,\n   and the like; also, the act of giving and receiving reciprocally; as,\n   an exchange of civilities or views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'The thing given or received in return; esp., a\n   publication exchanged for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'The process of setting accounts or debts between parties\n   residing at a distance from each other, without the intervention of\n   money, by exchanging orders or drafts, called bills of exchange. These\n   may be drawn in one country and payable in another, in which case they\n   are called foreign bills; or they may be drawn and made payable in the\n   same country, in which case they are called inland bills. The term bill\n   of exchange is often abbreviated into exchange; as, to buy or sell\n   exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'A mutual grant of equal interests, the one in\n   consideration of the other. Estates exchanged must be equal in\n   quantity, as fee simple for fee simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'The place where the merchants, brokers, and bankers of a\n   city meet at certain hours, to transact business. In this sense often\n   contracted to Change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchanged', 'imp. & p. p.', 'of Exchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchanging', 'p. pr. & vb. n.', 'of Exchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'To part with give, or transfer to another in\n   consideration of something received as an equivalent; -- usually\n   followed by for before the thing received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'To part with for a substitute; to lay aside, quit, or\n   resign (something being received in place of the thing parted with);\n   as, to exchange a palace for cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'n.', 'To give and receive reciprocally, as things of the same\n   kind; to barter; to swap; as, to exchange horses with a neighbor; to\n   exchange houses or hats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchange', 'v. i.', 'To be changed or received in exchange for; to pass in\n   exchange; as, dollar exchanges for ten dimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchangeability', 'n.', 'The quality or state of being exchangeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchangeable', 'a.', 'Capable of being exchanged; fit or proper to be\n   exchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchangeable', 'a.', 'Available for making exchanges; ratable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchangeably', 'adv.', 'By way of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchanger', 'n.', 'One who exchanges; one who practices exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excheat', 'n.', 'See Escheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excheator', 'n.', 'See Escheator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchequer', 'n.', 'One of the superior courts of law; -- so called from a\n   checkered cloth, which covers, or formerly covered, the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchequer', 'n.', 'The department of state having charge of the collection\n   and management of the royal revenue. [Eng.] Hence, the treasury; and,\n   colloquially, pecuniary possessions in general; as, the companys\n   exchequer is low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchequered', 'imp. & p. p.', 'of Exchequer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchequering', 'p. pr. & vb. n.', 'of Exchequer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exchequer', 'v. t.', 'To institute a process against (any one) in the Court\n   of Exchequer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excide', 'v. t.', 'To cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excipient', 'v. t.', 'Taking an exception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excipient', 'n.', 'An exceptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excipient', 'n.', 'An inert or slightly active substance used in preparing\n   remedies as a vehicle or medium of administration for the medicinal\n   agents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exciple', 'n.', 'Alt. of Excipulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excipulum', 'n.', 'The outer part of the fructification of most lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excisable', 'a.', 'Liable or subject to excise; as, tobacco in an excisable\n   commodity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excise', 'n.', 'In inland duty or impost operating as an indirect tax on\n   the consumer, levied upon certain specified articles, as, tobacco, ale,\n   spirits, etc., grown or manufactured in the country. It is also levied\n   to pursue certain trades and deal in certain commodities. Certain\n   direct taxes (as, in England, those on carriages, servants, plate,\n   armorial bearings, etc.), are included in the excise. Often used\n   adjectively; as, excise duties; excise law; excise system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excise', 'n.', 'That department or bureau of the public service charged\n   with the collection of the excise taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excised', 'imp. & p. p.', 'of Excise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excising', 'p. pr. & vb. n.', 'of Excise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excise', 'v. t.', 'To lay or impose an excise upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excise', 'v. t.', 'To impose upon; to overcharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excise', 'v. t.', 'To cut out or off; to separate and remove; as, to excise\n   a tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excisemen', 'pl. ', 'of Exciseman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exciseman', 'n.', 'An officer who inspects and rates articles liable to\n   excise duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excision', 'n.', 'The act of excising or cutting out or off; extirpation;\n   destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excision', 'n.', 'The act of cutting off from the church; excommunication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excision', 'n.', 'The removal, especially of small parts, with a cutting\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitability', 'n.', 'The quality of being readily excited; proneness to be\n   affected by exciting causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitability', 'n.', 'The property manifested by living organisms, and the\n   elements and tissues of which they are constituted, of responding to\n   the action of stimulants; irritability; as, nervous excitability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitable', 'a.', 'Capable of being excited, or roused into action;\n   susceptible of excitement; easily stirred up, or stimulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitant', 'a.', 'Tending to excite; exciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitant', 'n.', 'An agent or influence which arouses vital activity, or\n   produces increased action, in a living organism or in any of its\n   tissues or parts; a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitate', 'v. t.', 'To excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitation', 'n.', 'The act of exciting or putting in motion; the act of\n   rousing up or awakening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitation', 'n.', 'The act of producing excitement (stimulation); also,\n   the excitement produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitative', 'a.', 'Having power to excite; tending or serving to excite;\n   excitatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitator', 'n.', 'A kind of discarder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitatory', 'a.', 'Tending to excite; containing excitement; excitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excited', 'imp. & p. p.', 'of Excite');
  INSERT INTO words (word, word_type, description) VALUES  (    'exciting', 'p. pr. & vb. n.', 'of Excite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excite', 'v. t.', 'To call to activity in any way; to rouse to feeling; to\n   kindle to passionate emotion; to stir up to combined or general\n   activity; as, to excite a person, the spirits, the passions; to excite\n   a mutiny or insurrection; to excite heat by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excite', 'v. t.', 'To call forth or increase the vital activity of an\n   organism, or any of its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exciteful', 'n.', 'Full of exciting qualities; as, an exciteful story;\n   exciteful players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitement', 'n.', 'The act of exciting, or the state of being roused into\n   action, or of having increased action; impulsion; agitation; as, an\n   excitement of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitement', 'n.', 'That which excites or rouses; that which moves, stirs,\n   or induces action; a motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitement', 'n.', 'A state of aroused or increased vital activity in an\n   organism, or any of its organs or tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exciter', 'n.', 'One who, or that which, excites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exciting', 'a.', 'Calling or rousing into action; producing excitement; as,\n   exciting events; an exciting story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitive', 'a.', 'Serving or tending to excite; excitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excitive', 'n.', 'That which excites; an excitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excito-motion', 'n.', 'Motion excited by reflex nerves. See Excito-motory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excito-motor', 'a.', 'Excito-motory; as, excito-motor power or causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excito-motory', 'a.', 'Exciting motion; -- said of that portion of the\n   nervous system concerned in reflex actions, by which impressions are\n   transmitted to a nerve center and then reflected back so as to produce\n   muscular contraction without sensation or volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excito-nutrient', 'a', 'Exciting nutrition; said of the reflex influence by\n   which the nutritional processes are either excited or modified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excito-secretory', 'a.', 'Exciting secretion; -- said of the influence\n   exerted by reflex action on the function of secretion, by which the\n   various glands are excited to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclaimed', 'imp. & p. p.', 'of Exclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclaiming', 'p. pr. & vb. n.', 'of Exclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclaim', 'v. t. & i.', 'To cry out from earnestness or passion; to utter\n   with vehemence; to call out or declare loudly; to protest vehemently;\n   to vociferate; to shout; as, to exclaim against oppression with wonder\n   or astonishment; \"The field is won!\" he exclaimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclaim', 'n.', 'Outcry; clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclaimer', 'n.', 'One who exclaims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclamation', 'n.', 'A loud calling or crying out; outcry; loud or emphatic\n   utterance; vehement vociferation; clamor; that which is cried out, as\n   an expression of feeling; sudden expression of sound or words\n   indicative of emotion, as in surprise, pain, grief, joy, anger, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclamation', 'n.', 'A word expressing outcry; an interjection; a word\n   expressing passion, as wonder, fear, or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclamation', 'n.', 'A mark or sign by which outcry or emphatic utterance\n   is marked; thus [!]; -- called also exclamation point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclamative', 'a.', 'Exclamatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclamatory', 'a.', 'Containing, expressing, or using exclamation; as, an\n   exclamatory phrase or speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclave', 'n.', 'A portion of a country which is separated from the main\n   part and surrounded by politically alien territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excluded', 'imp. & p. p.', 'of Exclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excluding', 'p. pr. & vb. n.', 'of Exclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclude', 'v. t.', 'To shut out; to hinder from entrance or admission; to\n   debar from participation or enjoyment; to deprive of; to except; -- the\n   opposite to admit; as, to exclude a crowd from a room or house; to\n   exclude the light; to exclude one nation from the ports of another; to\n   exclude a taxpayer from the privilege of voting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclude', 'v. t.', 'To thrust out or eject; to expel; as, to exclude young\n   animals from the womb or from eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusion', 'n.', 'The act of excluding, or of shutting out, whether by\n   thrusting out or by preventing admission; a debarring; rejection;\n   prohibition; the state of being excluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusion', 'n.', 'The act of expelling or ejecting a fetus or an egg from\n   the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusion', 'n.', 'Thing emitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusionary', 'a.', 'Tending to exclude; causing exclusion; exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusionism', 'n.', 'The character, manner, or principles of an\n   exclusionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusionist', 'n.', 'One who would exclude another from some right or\n   privilege; esp., one of the anti-popish politicians of the time of\n   Charles II.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusive', 'a.', 'Having the power of preventing entrance; debarring from\n   participation or enjoyment; possessed and enjoyed to the exclusion of\n   others; as, exclusive bars; exclusive privilege; exclusive circles of\n   society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusive', 'a.', 'Not taking into the account; excluding from\n   consideration; -- opposed to inclusive; as, five thousand troops,\n   exclusive of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusive', 'n.', 'One of a coterie who exclude others; one who from real\n   of affected fastidiousness limits his acquaintance to a select few.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusiveness', 'n.', 'Quality of being exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusivism', 'n.', 'The act or practice of excluding being exclusive;\n   exclusiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusivist', 'n.', 'One who favor or practices any from of exclusiveness\n   or exclusivism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exclusory', 'a.', 'Able to exclude; excluding; serving to exclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoct', 'v. t.', 'To boil out; to produce by boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoction', '', 'The act of excocting or boiling out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excogitated', 'imp. & p. p.', 'of Excogitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excogitating', 'p. pr. & vb. n.', 'of Excogitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excogitate', 'v. t.', 'To think out; to find out or discover by thinking;\n   to devise; to contrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excogitate', 'v. i.', 'To cogitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excogitation', 'n.', 'The act of excogitating; a devising in the thoughts;\n   invention; contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommune', 'v. t.', 'To exclude from participation in; to excommunicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicable', 'a.', 'Liable or deserving to be excommunicated; making\n   excommunication possible or proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicant', 'n.', 'One who has been excommunicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicate', 'a.', 'Excommunicated; interdicted from the rites of the\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicate', 'n.', 'One excommunicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicated', 'imp. & p. p.', 'of Excommunicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicating', 'p. pr. & vb. n.', 'of Excommunicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicate', 'v. t.', 'To put out of communion; especially, to cut off,\n   or shut out, from communion with the church, by an ecclesiastical\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicate', 'v. t.', 'To lay under the ban of the church; to interdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunication', 'n.', 'The act of communicating or ejecting; esp., an\n   ecclesiastical censure whereby the person against whom it is pronounced\n   is, for the time, cast out of the communication of the church;\n   exclusion from fellowship in things spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunicator', 'n.', 'One who excommunicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excommunion', '', 'A shutting out from communion; excommunication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoriable', '', 'Capable of being excoriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoriated', 'imp. & p. p.', 'of Eccoriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'excoriating', 'p. pr. & vb. n.', 'of Eccoriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eccoriate', 'v. t.', 'To strip or wear off the skin of; to abrade; to gall;\n   to break and remove the cuticle of, in any manner, as by rubbing,\n   beating, or by the action of acrid substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoriation', 'n.', 'The act of excoriating or flaying, or state of being\n   excoriated, or stripped of the skin; abrasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excoriation', 'n.', 'Stripping of possession; spoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excorticate', 'v. t.', 'To strip of bark or skin; to decorticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excortication', 'n.', 'The act of stripping off bark, or the state of being\n   thus stripped; decortication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreable', 'a.', 'Capable of being discharged by spitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreate', 'v. t.', 'To spit out; to discharge from the throat by hawking\n   and spitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreation', 'n.', 'Act of spitting out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrement', 'n.', 'Matter excreted and ejected; that which is excreted or\n   cast out of the animal body by any of the natural emunctories;\n   especially, alvine, discharges; dung; ordure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrement', 'n.', 'An excrescence or appendage; an outgrowth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excremental', 'a.', 'Of or pertaining to excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrementitial', 'a.', 'Alt. of Excrementitious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrementitious', 'a.', 'Pertaining to, or consisting of, excrement; of the\n   nature of excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrementive', 'a.', 'Serving to excrete; connected with excretion or\n   excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrementize', 'v. i.', 'To void excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrescence', 'n.', 'An excrescent appendage, as, a wart or tumor; anything\n   growing out unnaturally from anything else; a preternatural or morbid\n   development; hence, a troublesome superfluity; an incumbrance; as, an\n   excrescence on the body, or on a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrescency', 'n.', 'Excrescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrescent', 'a.', 'Growing out in an abnormal or morbid manner or as a\n   superfluity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrescential', 'a.', 'Pertaining to, or resembling, an excrescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreta', 'n. pl.', 'Matters to be excreted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreted', 'imp. & p. p.', 'of Excrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excreting', 'p. pr. & vb. n.', 'of Excrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excrete', 'v. t.', 'To separate and throw off; to excrete urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excretin', 'n.', 'A nonnitrogenous, crystalline body, present in small\n   quantity in human faeces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excretion', 'n.', 'The act of excreting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excretion', 'n.', 'That which is excreted; excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excretive', 'a.', 'Having the power of excreting, or promoting excretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excretory', 'a.', 'Having the quality of excreting, or throwing off\n   excrementitious matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciable', 'a.', 'Liable to torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciate', 'a.', 'Excruciated; tortured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciated', 'imp. & p. p.', 'of Excruciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciating', 'p. pr. & vb. n.', 'of Excruciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciate', 'v. t.', 'To inflict agonizing pain upon; to torture; to\n   torment greatly; to rack; as, to excruciate the heart or the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciating', '', 'Torturing; racking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excruciation', 'n.', 'The act of inflicting agonizing pain, or the state of\n   being thus afflicted; that which excruciates; torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excubation', 'n.', 'A keeping watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excubitorium', 'n.', 'A gallery in a church, where persons watched all\n   night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpable', '', 'Capable of being exculpated; deserving exculpation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpated', 'imp. & p. p.', 'of Exculpate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpating', '', 'of Exculpate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpate', 'v. t.', 'To clear from alleged fault or guilt; to prove to be\n   guiltless; to relieve of blame; to acquit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpation', 'n.', 'The act of exculpating from alleged fault or crime;\n   that which exculpates; excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exculpatory', '', 'Clearing, or tending to clear, from alleged fault or\n   guilt; excusing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excur', 'i.', 'To run out or forth; to extend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excurrent', 'a.', 'Running or flowing out');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excurrent', 'a.', 'Running or extending out; as, an excurrent midrib, one\n   which projects beyond the apex of a leaf; an excurrent steam or trunk,\n   one which continues to the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excurrent', 'a.', 'Characterized by a current which flows outward; as, an\n   excurrent orifice or tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excurse', 'v. t.', 'To journey or pass thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursion', '', 'A running or going out or forth; an expedition; a sally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursion', '', 'A journey chiefly for recreation; a pleasure trip; a brief\n   tour; as, an excursion into the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursion', '', 'A wandering from a subject; digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursion', '', 'Length of stroke, as of a piston; stroke. [An awkward use\n   of the word.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursionist', 'n.', 'One who goes on an excursion, or pleasure trip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursive', 'a.', 'Prone to make excursions; wandering; roving; exploring;\n   as, an excursive fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excursus', 'n.', 'A dissertation or digression appended to a work, and\n   containing a more extended exposition of some important point or topic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusable', 'a.', 'That may be excused, forgiven, justified, or acquitted\n   of blame; pardonable; as, the man is excusable; an excusable action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusation', 'n.', 'Excuse; apology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusator', 'n.', 'One who makes, or is authorized to make, an excuse; an\n   apologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusatory', 'a.', 'Making or containing excuse or apology; apologetical;\n   as, an excusatory plea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excused', 'imp. & p. p.', 'of Excuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusing', 'p. pr. & vb. n.', 'of Excuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'To free from accusation, or the imputation of fault or\n   blame; to clear from guilt; to release from a charge; to justify by\n   extenuating a fault; to exculpate; to absolve; to acquit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'To pardon, as a fault; to forgive entirely, or to admit\n   to be little censurable, and to overlook; as, we excuse irregular\n   conduct, when extraordinary circumstances appear to justify it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'To regard with indulgence; to view leniently or to\n   overlook; to pardon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'To free from an impending obligation or duty; hence, to\n   disengage; to dispense with; to release by favor; also, to remit by\n   favor; not to exact; as, to excuse a forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'To relieve of an imputation by apology or defense; to\n   make apology for as not seriously evil; to ask pardon or indulgence\n   for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'The act of excusing, apologizing, exculpating,\n   pardoning, releasing, and the like; acquittal; release; absolution;\n   justification; extenuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'That which is offered as a reason for being excused; a\n   plea offered in extenuation of a fault or irregular deportment;\n   apology; as, an excuse for neglect of duty; excuses for delay of\n   payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuse', 'v. t.', 'That which excuses; that which extenuates or justifies a\n   fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuseless', 'a.', 'Having no excuse; not admitting of excuse or apology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excusement', 'n.', 'Excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuser', 'n.', 'One who offers excuses or pleads in extenuation of the\n   fault of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuser', 'n.', 'One who excuses or forgives another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuss', 'v. t.', 'To shake off; to discard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuss', 'v. t.', 'To inspect; to investigate; to decipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excuss', 'v. t.', 'To seize and detain by law, as goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Excussion', 'n.', 'The act of excusing; seizure by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exeat', 'n.', 'A license for absence from a college or a religious house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exeat', 'n.', 'A permission which a bishop grants to a priest to go out of\n   his diocese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrable', 'a.', 'Deserving to be execrated; accursed; damnable;\n   detestable; abominable; as, an execrable wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrated', 'imp. & p. p.', 'of Execrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrating', 'p. pr. & vb. n.', 'of Execrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrate', 'v. t.', 'To denounce evil against, or to imprecate evil upon;\n   to curse; to protest against as unholy or detestable; hence, to detest\n   utterly; to abhor; to abominate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execration', 'n.', 'The act of cursing; a curse dictated by violent\n   feelings of hatred; imprecation; utter detestation expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execration', 'n.', 'That which is execrated; a detested thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrative', 'a.', 'Cursing; imprecatory; vilifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execrative', 'n.', 'A word used for cursing; an imprecatory word or\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execratory', 'a.', 'Of the nature of execration; imprecatory; denunciatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execratory', 'n.', 'A formulary of execrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exect', 'v. t.', 'To cut off or out. [Obs.] See Exsect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exection', 'n.', 'See Exsection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executable', 'a.', 'Capable of being executed; feasible; as, an executable\n   project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executant', 'n.', 'One who executes or performs; esp., a performer on a\n   musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executed', 'imp. & p. p.', 'of Execute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executing', 'p. pr. & vb. n.', 'of Execute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'To follow out or through to the end; to carry out into\n   complete effect; to complete; to finish; to effect; to perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'To complete, as a legal instrument; to perform what is\n   required to give validity to, as by signing and perhaps sealing and\n   delivering; as, to execute a deed, lease, mortgage, will, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'To give effect to; to do what is provided or required\n   by; to perform the requirements or stimulations of; as, to execute a\n   decree, judgment, writ, or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'To infect capital punishment on; to put to death in\n   conformity to a legal sentence; as, to execute a traitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'Too put to death illegally; to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. t.', 'To perform, as a piece of music, either on an\n   instrument or with the voice; as, to execute a difficult part\n   brilliantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. i.', 'To do ones work; to act ones part of purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execute', 'v. i.', 'To perform musically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executer', 'n.', 'One who performs or carries into effect. See Executor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'The act of executing; a carrying into effect or to\n   completion; performance; achievement; consummation; as, the execution\n   of a plan, a work, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'A putting to death as a legal penalty; death lawfully\n   inflicted; as, the execution of a murderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'The act of the mode of performing a work of art, of\n   performing on an instrument, of engraving, etc.; as, the execution of a\n   statue, painting, or piece of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'The carrying into effect the judgment given in a court\n   of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'A judicial writ by which an officer is empowered to\n   carry a judgment into effect; final process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'The act of signing, and delivering a legal instrument,\n   or giving it the forms required to render it valid; as, the execution\n   of a deed, or a will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'That which is executed or accomplished; effect;\n   effective work; -- usually with do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Execution', 'n.', 'The act of sacking a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executioner', 'n.', 'One who executes; an executer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executioner', 'n.', 'One who puts to death in conformity to legal warrant,\n   as a hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executive', 'a.', 'Designed or fitted for execution, or carrying into\n   effect; as, executive talent; qualifying for, concerned with, or\n   pertaining to, the execution of the laws or the conduct of affairs; as,\n   executive power or authority; executive duties, officer, department,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executive', 'n.', 'An impersonal title of the chief magistrate or officer\n   who administers the government, whether king, president, or governor;\n   the governing person or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executively', 'adv.', 'In the way of executing or performing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executor', 'n.', 'One who executes or performs; a doer; as, an executor of\n   baseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executor', 'n.', 'An executioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executor', 'n.', 'The person appointed by a testator to execute his will,\n   or to see its provisions carried into effect, after his decease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executorial', 'a.', 'Of or pertaining to an executive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executorship', 'n.', 'The office of an executor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executory', 'a.', 'Pertaining to administration, or putting the laws in\n   force; executive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executory', 'a.', 'Designed to be executed or carried into effect in time\n   to come, or to take effect on a future contingency; as, an executory\n   devise, reminder, or estate; an executory contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executress', 'n.', 'An executrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Executrix', 'n.', 'A woman exercising the functions of an executor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exedent', 'a.', 'Eating out; consuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exedrae', 'pl. ', 'of Exedra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exedra', 'n.', 'A room in a public building, furnished with seats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exedra', 'n.', 'The projection of any part of a building in a rounded form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exedra', 'n.', 'Any out-of-door seat in stone, large enough for several\n   persons; esp., one of curved form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegeses', 'pl. ', 'of Exegesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegesis', 'n.', 'Exposition; explanation; especially, a critical\n   explanation of a text or portion of Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegesis', 'n.', 'The process of finding the roots of an equation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegete', 'n.', 'An exegetist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegetic', 'a.', 'Alt. of Exegetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegetical', 'a.', 'Pertaining to exegesis; tending to unfold or\n   illustrate; explanatory; expository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegetics', 'n.', 'The science of interpretation or exegesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exegetist', 'n.', 'One versed in the science of exegesis or interpretation;\n   -- also called exegete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplar', 'n.', 'A model, original, or pattern, to be copied or imitated;\n   a specimen; sometimes; an ideal model or type, as that which an artist\n   conceives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplar', 'n.', 'A copy of a book or writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplar', 'a.', 'Exemplary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplarily', 'adv.', 'In a manner fitted or designed to be an example for\n   imitation or for warning; by way of example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplariness', 'n.', 'The state or quality of being exemplary; fitness to\n   be an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplarity', 'n.', 'Exemplariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplary', 'a.', 'Serving as a pattern; deserving to be proposed for\n   imitation; commendable; as, an exemplary person; exemplary conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplary', 'a.', 'Serving as a warning; monitory; as, exemplary justice,\n   punishment, or damages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplary', 'a.', 'Illustrating as the proof of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplary', 'n.', 'An exemplar; also, a copy of a book or writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplifiable', 'a.', 'That can be exemplified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplification', 'n.', 'The act of exemplifying; a showing or illustrating\n   by example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplification', 'n.', 'That which exemplifies; a case in point; example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplification', 'n.', 'A copy or transcript attested to be correct by the\n   seal of an officer having custody of the original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplifier', 'n.', 'One who exemplifies by following a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplified', 'imp. & p. p.', 'of Exemplify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplifying', 'p. pr. &. vb. n.', 'of Exemplify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplify', 'v. t.', 'To show or illustrate by example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplify', 'v. t.', 'To copy; to transcribe; to make an attested copy or\n   transcript of, under seal, as of a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemplify', 'v. t.', 'To prove or show by an attested copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'a.', 'Cut off; set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'a.', 'Extraordinary; exceptional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'a.', 'Free, or released, from some liability to which others are\n   subject; excepted from the operation or burden of some law; released;\n   free; clear; privileged; -- (with from): not subject to; not liable to;\n   as, goods exempt from execution; a person exempt from jury service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'n.', 'One exempted or freed from duty; one not subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'n.', 'One of four officers of the Yeomen of the Royal Guard,\n   having the rank of corporal; an Exon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempted', 'imp. & p. p.', 'of Exempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempting', 'p. pr. & vb. n.', 'of Exempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'a.', 'To remove; to set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exempt', 'a.', 'To release or deliver from some liability which others are\n   subject to; to except or excuse from he operation of a law; to grant\n   immunity to; to free from obligation; to release; as, to exempt from\n   military duty, or from jury service; to exempt from fear or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemptible', 'a.', 'That may be exempted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemption', 'n.', 'The act of exempting; the state of being exempt; freedom\n   from any charge, burden, evil, etc., to which others are subject;\n   immunity; privilege; as, exemption of certain articles from seizure;\n   exemption from military service; exemption from anxiety, suffering,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exemptitious', 'a.', 'Separable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exenterate', 'v. t.', 'To take out the bowels or entrails of; to\n   disembowel; to eviscerate; as, exenterated fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exenteration', 'n.', 'Act of exenterating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequatur', 'n.', 'A written official recognition of a consul or commercial\n   agent, issued by the government to which he is accredited, and\n   authorizing him to exercise his powers in the place to which he is\n   assigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequatur', 'n.', 'Official recognition or permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequial', 'a.', 'Of or pertaining to funerals; funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequious', 'a.', 'Funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequies', 'pl. ', 'of Exequy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exequy', 'n.', 'A funeral rite (usually in the plural); the ceremonies of\n   burial; obsequies; funeral procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercent', 'a.', 'Practicing; professional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercisable', 'a.', 'That may be exercised, used, or exerted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'The act of exercising; a setting in action or practicing;\n   employment in the proper mode of activity; exertion; application; use;\n   habitual activity; occupation, in general; practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'Exertion for the sake of training or improvement whether\n   physical, intellectual, or moral; practice to acquire skill, knowledge,\n   virtue, perfectness, grace, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'Bodily exertion for the sake of keeping the organs and\n   functions in a healthy state; hygienic activity; as, to take exercise\n   on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'The performance of an office, a ceremony, or a religious\n   duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'That which is done for the sake of exercising,\n   practicing, training, or promoting skill, health, mental, improvement,\n   moral discipline, etc.; that which is assigned or prescribed for such\n   ends; hence, a disquisition; a lesson; a task; as, military or naval\n   exercises; musical exercises; an exercise in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'n.', 'That which gives practice; a trial; a test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercised', 'imp. & p. p.', 'of Exercise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercising', 'p. pr. & vb. n.', 'of Exercise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'v. t.', 'To set in action; to cause to act, move, or make\n   exertion; to give employment to; to put in action habitually or\n   constantly; to school or train; to exert repeatedly; to busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'v. t.', 'To exert for the sake of training or improvement; to\n   practice in order to develop; hence, also, to improve by practice; to\n   discipline, and to use or to for the purpose of training; as, to\n   exercise arms; to exercise ones self in music; to exercise troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'v. t.', 'To occupy the attention and effort of; to task; to\n   tax, especially in a painful or vexatious manner; harass; to vex; to\n   worry or make anxious; to affect; to discipline; as, exercised with\n   pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'v. t.', 'To put in practice; to carry out in action; to perform\n   the duties of; to use; to employ; to practice; as, to exercise\n   authority; to exercise an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercise', 'v. i.', 'To exercise ones self, as under military training; to\n   drill; to take exercise; to use action or exertion; to practice\n   gymnastics; as, to exercise for health or amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exerciser', 'n.', 'One who exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercisible', 'a.', 'Capable of being exercised, employed, or enforced; as,\n   the authority of a magistrate is exercisible within his jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exercitation', 'n.', 'exercise; practice; use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exergue', 'n.', 'The small space beneath the base line of a subject\n   engraved on a coin or medal. It usually contains the date, place,\n   engravers name, etc., or other subsidiary matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exerted', 'imp. & p. p.', 'of Exert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exerting', 'p. pr. & vb. n.', 'of Exert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exert', 'v. t.', 'To thrust forth; to emit; to push out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exert', 'v. t.', 'To put force, ability, or anything of the nature of an\n   active faculty; to put in vigorous action; to bring into active\n   operation; as, to exert the strength of the body, limbs, faculties, or\n   imagination; to exert the mind or the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exert', 'v. t.', 'To put forth, as the result or exercise of effort; to\n   bring to bear; to do or perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exertion', 'n.', 'The act of exerting, or putting into motion or action;\n   the active exercise of any power or faculty; an effort, esp. a\n   laborious or perceptible effort; as, an exertion of strength or power;\n   an exertion of the limbs or of the mind; it is an exertion for him to\n   move, to-day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exertive', 'a.', 'Having power or a tendency to exert; using exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exertment', 'n.', 'Exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exesion', 'n.', 'The act of eating out or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exestuate', 'v. i.', 'To be agitated; to boil up; to effervesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exestuation', 'n.', 'A boiling up; effervescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exeunt', '', 'They go out, or retire from the scene; as, exeunt all except\n   Hamlet. See 1st Exit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfetation', 'n', 'Imperfect fetation in some organ exterior to the uterus;\n   extra-uterine fetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliated', 'imp. & p. p.', 'of Exfoliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliating', 'p. pr. & vb. n.', 'of Exfoliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliate', 'v. i.', 'To separate and come off in scales or laminae, as\n   pieces of carious bone or of bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliate', 'v. i.', 'To split into scales, especially to become converted\n   into scales at the result of heat or decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliate', 'v. t.', 'To remove scales, laminae, or splinters from the\n   surface of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliation', 'n.', 'The scaling off of a bone, a rock, or a mineral, etc.;\n   the state of being exfoliated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliative', 'a.', 'Having the power of causing exfoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exfoliative', 'n.', 'An exfoliative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalable', 'a.', 'Capable of being exhaled or evaporated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalant', 'a.', 'Having the quality of exhaling or evaporating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalation', 'n.', 'The act or process of exhaling, or sending forth in the\n   form of steam or vapor; evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalation', 'n.', 'That which is exhaled, or which rises in the form of\n   vapor, fume, or steam; effluvium; emanation; as, exhalations from the\n   earth or flowers, decaying matter, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalation', 'n.', 'A bright phenomenon; a meteor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaled', 'imp. & p. p.', 'of Exhale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exaling', 'p. pr. & vb. n.', 'of Exhale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhale', 'v. t.', 'To breathe out. Hence: To emit, as vapor; to send out,\n   as an odor; to evaporate; as, the earth exhales vapor; marshes exhale\n   noxious effluvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhale', 'v. t.', 'To draw out; to cause to be emitted in vapor; as, the\n   sum exhales the moisture of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhale', 'v. i.', 'To rise or be given off, as vapor; to pass off, or\n   vanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalement', 'n.', 'Exhalation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhalence', 'n.', 'Exhalation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhausted', 'imp. & p. p.', 'of Exhaust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhausting', 'p. pr. & vb. n.', 'of Exhaust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'v. t.', 'To draw or let out wholly; to drain off completely; as,\n   to exhaust the water of a well; the moisture of the earth is exhausted\n   by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'v. t.', 'To empty by drawing or letting out the contents; as, to\n   exhaust a well, or a treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'v. t.', 'To drain, metaphorically; to use or expend wholly, or\n   till the supply comes to an end; to deprive wholly of strength; to use\n   up; to weary or tire out; to wear out; as, to exhaust ones strength,\n   patience, or resources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'v. t.', 'To bring out or develop completely; to discuss\n   thoroughly; as, to exhaust a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'v. t.', 'To subject to the action of various solvents in order\n   to remove all soluble substances or extractives; as, to exhaust a drug\n   successively with water, alcohol, and ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'a.', 'Drained; exhausted; having expended or lost its energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'a.', 'Pertaining to steam, air, gas, etc., that is released from\n   the cylinder of an engine after having preformed its work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'n.', 'The steam let out of a cylinder after it has done its work\n   there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaust', 'n.', 'The foul air let out of a room through a register or pipe\n   provided for the purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhauster', 'n.', 'One who, or that which, exhausts or draws out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustibility', 'n.', 'Capability of being exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustible', 'a.', 'Capable of being exhausted, drained off, or expended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhausting', 'a.', 'Producing exhaustion; as, exhausting labors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustion', 'n.', 'The act of draining out or draining off; the act of\n   emptying completely of the contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustion', 'n.', 'The state of being exhausted or emptied; the state of\n   being deprived of strength or spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustion', 'n.', 'An ancient geometrical method in which an exhaustive\n   process was employed. It was nearly equivalent to the modern method of\n   limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustive', 'a.', 'Serving or tending to exhaust; exhibiting all the facts\n   or arguments; as, an exhaustive method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustless', 'a.', 'Not be exhausted; inexhaustible; as, an exhaustless\n   fund or store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhaustment', 'n.', 'Exhaustion; drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhausture', 'n.', 'Exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhedra', 'n.', 'See Exedra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exheredate', 'v. t.', 'To disinherit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exheredation', 'n.', 'A disinheriting; disherisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhereditation', 'n.', 'A disinheriting; disherison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibited', 'imp. & p. p.', 'of Exhibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibiting', 'p. pr. & vb. n.', 'of Exhibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibit', 'v. t.', 'To hold forth or present to view; to produce publicly,\n   for inspection; to show, especially in order to attract notice to what\n   is interesting; to display; as, to exhibit commodities in a warehouse,\n   a picture in a gallery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibit', 'v. t.', 'To submit, as a document, to a court or officer, in\n   course of proceedings; also, to present or offer officially or in legal\n   form; to bring, as a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibit', 'v. t.', 'To administer as a remedy; as, to exhibit calomel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibit', 'n.', 'Any article, or collection of articles, displayed to view,\n   as in an industrial exhibition; a display; as, this exhibit was marked\n   A; the English exhibit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibit', 'n.', 'A document produced and identified in court for future use\n   as evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibiter', 'n.', 'One who exhibits; one who presents a petition, charge or\n   bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibition', 'n.', 'The act of exhibiting for inspection, or of holding\n   forth to view; manifestation; display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibition', 'n.', 'That which is exhibited, held forth, or displayed;\n   also, any public show; a display of works of art, or of feats of skill,\n   or of oratorical or dramatic ability; as, an exhibition of animals; an\n   exhibition of pictures, statues, etc.; an industrial exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibition', 'n.', 'Sustenance; maintenance; allowance, esp. for meat and\n   drink; pension. Specifically: (Eng. Univ.) Private benefaction for the\n   maintenance of scholars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibition', 'n.', 'The act of administering a remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibitioner', 'n.', 'One who has a pension or allowance granted for\n   support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibitive', 'a.', 'Serving for exhibition; representative; exhibitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibitor', 'n.', 'One who exhibits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhibitory', 'a.', 'Exhibiting; publicly showing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarant', 'a.', 'Exciting joy, mirth, or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarant', 'n.', 'That which exhilarates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarated', 'imp. & p. p.', 'of Exhilarate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exilarating', 'p. pr. & vb. n.', 'of Exhilarate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarate', 'v. t.', 'To make merry or jolly; to enliven; to animate; to\n   gladden greatly; to cheer; as, good news exhilarates the mind; wine\n   exhilarates a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarate', 'v. i.', 'To become joyous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilarating', 'a.', 'That exhilarates; cheering; gladdening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilaration', 'n.', 'The act of enlivening the spirits; the act of making\n   glad or cheerful; a gladdening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhilaration', 'n.', 'The state of being enlivened or cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhorted', 'imp. & p. p.', 'of Exhort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhorting', 'p. pr. & vb. n.', 'of Exhort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhort', 'v. t.', 'To incite by words or advice; to animate or urge by\n   arguments, as to a good deed or laudable conduct; to address\n   exhortation to; to urge strongly; hence, to advise, warn, or caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhort', 'v. i.', 'To deliver exhortation; to use words or arguments to\n   incite to good deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhort', 'n.', 'Exhortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhortation', 'n.', 'The act of practice of exhorting; the act of inciting\n   to laudable deeds; incitement to that which is good or commendable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhortation', 'n.', 'Language intended to incite and encourage; advice;\n   counsel; admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhortative', 'a.', 'Serving to exhort; exhortatory; hortative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhortatory', 'a.', 'Of or pertaining to exhortation; hortatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhorter', 'n.', 'One who exhorts or incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhumated', 'a.', 'Disinterred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhumation', 'n.', 'The act of exhuming that which has been buried; as, the\n   exhumation of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhumed', 'imp. & p. p.', 'of Exhume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhuming', 'p. pr. & vb. n.', 'of Exhume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exhume', 'v. t.', 'To dig out of the ground; to take out of a place of\n   burial; to disinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiccate', 'v. t.', 'See Exsiccate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiccation', 'n.', 'See Exsiccation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigence', 'n.', 'Exigency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigencies', 'pl. ', 'of Exigency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigency', 'n.', 'The state of being exigent; urgent or exacting want;\n   pressing necessity or distress; need; a case demanding immediate\n   action, supply, or remedy; as, an unforeseen exigency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigendary', 'n.', 'See Exigenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigent', 'a.', 'Exacting or requiring immediate aid or action; pressing;\n   critical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigent', 'n.', 'Exigency; pressing necessity; decisive moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigent', 'n.', 'The name of a writ in proceedings before outlawry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigenter', 'n.', 'An officer in the Court of Kings Bench and Common Pleas\n   whose duty it was make out exigents. The office in now abolished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exigible', 'a.', 'That may be exacted; repairable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiguity', 'n.', 'Scantiness; smallness; thinness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiguous', 'a.', 'Scanty; small; slender; diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exile', 'n.', 'Forced separation from ones native country; expulsion from\n   ones home by the civil authority; banishment; sometimes, voluntary\n   separation from ones native country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exile', 'n.', 'The person expelled from his country by authority; also, one\n   who separates himself from his home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiled', 'imp. & p. p.', 'of Exile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exiling', 'p. pr. & vb. n.', 'of Exile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exile', 'v. t.', 'To banish or expel from ones own country or home; to\n   drive away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exile', 'a.', 'Small; slender; thin; fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exilement', 'n.', 'Banishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exilic', 'a.', 'Pertaining to exile or banishment, esp. to that of the Jews\n   in Babylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exilition', 'n.', 'A sudden springing or leaping out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exility', 'a.', 'Smallness; meagerness; slenderness; fineness, thinness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eximious', 'a.', 'Select; choice; hence, extraordinary, excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exinanite', 'v. t.', 'To make empty; to render of no effect; to humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exinanition', 'n.', 'An emptying; an enfeebling; exhaustion; humiliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existed', 'imp. & p. p.', 'of Exist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existing', 'p. pr. & vb. n.', 'of Exist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exist', 'v. i.', 'To be as a fact and not as a mode; to have an actual or\n   real being, whether material or spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exist', 'v. i.', 'To be manifest in any manner; to continue to be; as,\n   great evils existed in his reign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exist', 'v. i.', 'To live; to have life or the functions of vitality; as,\n   men can not exist water, nor fishes on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existence', 'n.', 'The state of existing or being; actual possession of\n   being; continuance in being; as, the existence of body and of soul in\n   union; the separate existence of the soul; immortal existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existence', 'n.', 'Continued or repeated manifestation; occurrence, as of\n   events of any kind; as, the existence of a calamity or of a state of\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existence', 'n.', 'That which exists; a being; a creature; an entity; as,\n   living existences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existency', 'n.', 'Existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existent', 'a.', 'Having being or existence; existing; being; occurring\n   now; taking place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existential', 'a.', 'Having existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exister', 'n.', 'One who exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existible', 'a.', 'Capable of existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Existimation', 'n.', 'Esteem; opinion; reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exit', '', 'He (or she ) goes out, or retires from view; as, exit Macbeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exit', 'n.', 'The departure of a player from the stage, when he has\n   performed his part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exit', 'n.', 'Any departure; the act of quitting the stage of action or of\n   life; death; as, to make ones exit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exit', 'n.', 'A way of departure; passage out of a place; egress; way out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exitial', 'a.', 'Alt. of Exitious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exitious', 'a.', 'Destructive; fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exo', '', 'A prefix signifying out of, outside; as in exocarp, exogen,\n   exoskeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exocardiac', 'a.', 'Alt. of Exocardial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exocardial', 'a.', 'Situated or arising outside of the heat; as, exocardial\n   murmurs; -- opposed to endocardiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exocarp', 'n.', 'The outer portion of a fruit, as the flesh of a peach or\n   the rind of an orange. See Illust. of Drupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoccipital', 'a.', 'Pertaining to a bone or region on each side of the\n   great foremen of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoccipital', 'n.', 'The exoccipital bone, which often forms a part of the\n   occipital in the adult, but is usually distinct in the young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exocetus', 'n.', 'Alt. of Exocoetus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exocoetus', 'n.', 'A genus of fishes, including the common flying fishes.\n   See Flying fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoculate', 'v. t.', 'To deprive of eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exode', 'n.', 'Departure; exodus; esp., the exodus of the Israelites from\n   Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exode', 'n.', 'The final chorus; the catastrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exode', 'n.', 'An afterpiece of a comic description, either a farce or a\n   travesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exodic', 'a.', 'Conducting influences from the spinal cord outward; -- said\n   of the motor or efferent nerves. Opposed to esodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogium', 'n.', 'See Exode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exodus', 'n.', 'A going out; particularly (the Exodus), the going out or\n   journey of the Israelites from Egypt under the conduct of Moses; and\n   hence, any large migration from a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exodus', 'n.', 'The second of the Old Testament, which contains the\n   narrative of the departure of the Israelites from Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exody', 'n.', 'Exodus; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex-official', 'a.', 'Proceeding from office or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex officiis', 'pl. ', 'of Ex officio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex officio', '', 'From office; by virtue, or as a consequence, of an\n   office; officially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogamous', 'a.', 'Relating to exogamy; marrying outside of the limits of\n   ones own tribe; -- opposed to endogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogamy', 'n.', 'The custom, or tribal law, which prohibits marriage\n   between members of the same tribe; marriage outside of the tribe; --\n   opposed to endogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogen', 'n.', 'A plant belonging to one of the greater part of the\n   vegetable kingdom, and which the plants are characterized by having c\n   wood bark, and pith, the wood forming a layer between the other two,\n   and increasing, if at all, by the animal addition of a new layer to the\n   outside next to the bark. The leaves are commonly netted-veined, and\n   the number of cotyledons is two, or, very rarely, several in a whorl.\n   Cf. Endogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogenetic', 'a.', 'Arising or growing from without; exogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogenous', 'a.', 'Pertaining to, or having the character of, an exogen; --\n   the opposite of endogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogenous', 'a.', 'Growing by addition to the exterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogenous', 'a.', 'Growing from previously ossified parts; -- opposed to\n   autogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exogyra', 'n.', 'A genus of Cretaceous fossil shells allied to oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exolete', 'a.', 'Obsolete; out of use; state; insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exolution', 'n.', 'See Exsolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exolve', 'v. t.', 'To loose; to pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exon', 'n.', 'A native or inhabitant of Exeter, in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exon', 'n.', 'An officer of the Yeomen of the Guard; an Exempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerated', 'imp. & p. p.', 'of Exonerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerating', 'p. pr. & vb. n.', 'of Exonerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerate', 'v. t.', 'To unload; to disburden; to discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerate', 'v. t.', 'To relieve, in a moral sense, as of a charge,\n   obligation, or load of blame resting on one; to clear of something that\n   lies upon oppresses one, as an accusation or imputation; as, to\n   exonerate ones self from blame, or from the charge of avarice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerate', 'v. t.', 'To discharge from duty or obligation, as a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoneration', 'n.', 'The act of disburdening, discharging, or freeing\n   morally from a charge or imputation; also, the state of being\n   disburdened or freed from a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerative', 'a.', 'Freeing from a burden or obligation; tending to\n   exonerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exonerator', 'n.', 'One who exonerates or frees from obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophthalmia', 'n.', 'The protrusion of the eyeball so that the eyelids\n   will not cover it, in consequence of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophthalmic', 'a.', 'Of or pertaining to, or characterized by,\n   exophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophthalmos', 'n.', 'Alt. of Exophthalmus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophthalmus', 'n.', 'Same as Exophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophthalmy', 'n.', 'Exophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exophyllous', 'a.', 'Not sheathed in another leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoplasm', 'n.', 'See Ectosarc, and Ectoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exopodite', 'n.', 'The external branch of the appendages of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoptable', 'a.', 'Very desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoptile', 'n.', 'A name given by Lestiboudois to dicotyledons; -- so\n   called because the plumule is naked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exrable', 'a.', 'Capable of being moved by entreaty; pitiful; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorate', 'v. t.', 'To persuade, or to gain, by entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoration', 'n.', 'Entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitance', 'n.', 'Alt. of Exorbitancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitancy', 'n.', 'A going out of or beyond the usual or due limit;\n   hence, enormity; extravagance; gross deviation from rule, right, or\n   propriety; as, the exorbitances of the tongue or of deportment;\n   exorbitance of demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitant', 'a.', 'Departing from an orbit or usual track; hence,\n   deviating from the usual or due course; going beyond the appointed\n   rules or established limits of right or propriety; excessive;\n   extravagant; enormous; inordinate; as, exorbitant appetites and\n   passions; exorbitant charges, demands, or claims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitant', 'a.', 'Not comprehended in a settled rule or method;\n   anomalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitantly', 'adv.', 'In an exorbitant, excessive, or irregular manner;\n   enormously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorbitate', 'v. i.', 'To go out of the track; to deviate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcised', 'imp. & p. p.', 'of Exorcise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcising', 'p. pr. & vb. n.', 'of Exorcise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcise', 'v. t.', 'To cast out, as a devil, evil spirits, etc., by\n   conjuration or summoning by a holy name, or by certain ceremonies; to\n   expel (a demon) or to conjure (a demon) to depart out of a person\n   possessed by one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcise', 'v. t.', 'To deliver or purify from the influence of an evil\n   spirit or demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorciser', 'n.', 'An exorcist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcism', 'n.', 'The act of exorcising; the driving out of evil spirits\n   from persons or places by conjuration; also, the form of conjuration\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcism', 'n.', 'Conjuration for raising spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcist', 'n.', 'One who expels evil spirits by conjuration or exorcism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorcist', 'n.', 'A conjurer who can raise spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exordial', 'a.', 'Pertaining to the exordium of a discourse: introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exordiums', 'pl. ', 'of Exordium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exordia', 'pl. ', 'of Exordium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exordium', 'n.', 'A beginning; an introduction; especially, the\n   introductory part of a discourse or written composition, which prepares\n   the audience for the main subject; the opening part of an oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorhizae', 'pl. ', 'of Exorhiza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorhiza', 'n.', 'A plant Whose radicle is not inclosed or sheathed by the\n   cotyledons or plumule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorhizal', 'a.', 'Alt. of Exorhizous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exorhizous', 'a.', 'Having a radicle which is not inclosed by the\n   cotyledons or plumule; of or relating to an exorhiza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exornation', 'n.', 'Ornament; decoration; embellishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exortive', 'a.', 'Rising; relating to the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosculate', 'v. t.', 'To kiss; especially, to kiss repeatedly or fondly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoskeletal', 'a.', 'Pertaining to the exoskeleton; as exoskeletal muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoskeleton', 'n.', 'The hardened parts of the external integument of an\n   animal, including hair, feathers, nails, horns, scales, etc.,as well as\n   the armor of armadillos and many reptiles, and the shells or hardened\n   integument of numerous invertebrates; external skeleton; dermoskeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosmose', 'n.', 'The passage of gases, vapors, or liquids thought\n   membranes or porous media from within outward, in the phenomena of\n   osmose; -- opposed to endosmose. See Osmose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosmosis', 'n.', 'See Exosmose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosmotic', 'a.', 'Pertaining to exosmose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exospore', 'n.', 'The extreme outer wall of a spore; the epispore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosstate', 'v. t.', 'To deprive of bones; to take out the bones of; to\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exossation', 'n.', 'A depriving of bone or of fruit stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exosse-ous', 'a.', 'Boneless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exostome', 'n.', 'The small aperture or foremen in the outer coat of the\n   ovule of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exostosis', 'n.', 'Any protuberance of a bone which is not natural; an\n   excrescence or morbid enlargement of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exostosis', 'n.', 'A knot formed upon or in the wood of trees by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoteric', 'a.', 'Alt. of Exoterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoterical', 'a.', 'External; public; suitable to be imparted to the\n   public; hence, capable of being readily or fully comprehended; --\n   opposed to esoteric, or secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoterics', 'n. pl.', 'The public lectures or published writings of\n   Aristotle. See Esoterics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoteries', 'pl. ', 'of Exotery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exotery', 'n.', 'That which is obvious, public, or common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exotheca', 'n.', 'The tissue which fills the interspaces between the costae\n   of many madreporarian corals, usually consisting of small transverse or\n   oblique septa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exothecium', 'n.', 'The outer coat of the anther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exotic', 'a.', 'Introduced from a foreign country; not native; extraneous;\n   foreign; as, an exotic plant; an exotic term or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exotic', 'n.', 'Anything of foreign origin; something not of native growth,\n   as a plant, a word, a custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exotical', 'a.', 'Foreign; not native; exotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exoticism', 'n.', 'The state of being exotic; also, anything foreign, as a\n   word or idiom; an exotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expanded', 'imp. & p. p.', 'of Expand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expanding', 'p. pr. & vb. n.', 'of Expand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expand', 'v. t.', 'To lay open by extending; to open wide; to spread out;\n   to diffuse; as, a flower expands its leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expand', 'v. t.', 'To cause the particles or parts of to spread themselves\n   or stand apart, thus increasing bulk without addition of substance; to\n   make to occupy more space; to dilate; to distend; to extend every way;\n   to enlarge; -- opposed to contract; as, to expand the chest; heat\n   expands all bodies; to expand the sphere of benevolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expand', 'v. t.', 'To state in enlarged form; to develop; as, to expand an\n   equation. See Expansion, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expand', 'v. i.', 'To become widely opened, spread apart, dilated,\n   distended, or enlarged; as, flowers expand in the spring; metals expand\n   by heat; the heart expands with joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expander', 'n.', 'Anything which causes expansion esp. (Mech.) a tool for\n   stretching open or expanding a tube, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expanding', 'a.', 'That expands, or may be expanded; extending; spreading;\n   enlarging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expanse', 'n.', 'That which is expanded or spread out; a wide extent of\n   space or body; especially, the arch of the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expanse', 'v. t.', 'To expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansibility', 'n.', 'The capacity of being expanded; as, the\n   expansibility of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansible', 'a.', 'Capable of being expanded or spread out widely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansile', 'a.', 'Expansible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'The act of expanding or spreading out; the condition of\n   being expanded; dilation; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'That which is expanded; expanse; extend surface; as, the\n   expansion of a sheet or of a lake; the expansion was formed of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'Space through which anything is expanded; also, pure\n   space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'Enlargement or extension of business transactions; esp.,\n   increase of the circulation of bank notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'The developed result of an indicated operation; as, the\n   expansion of (a + b)2 is a2 + 2ab + b2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'The operation of steam in a cylinder after its\n   communication with the boiler has been cut off, by which it continues\n   to exert pressure upon the moving piston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansion', 'n.', 'The enlargement of the ship mathematically from a model\n   or drawing to the full or building size, in the process of\n   construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansive', 'a.', 'Having a capacity or tendency to expand or dilate;\n   diffusive; of much expanse; wide-extending; as, the expansive force of\n   heat; the expansive quality of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expansure', 'n.', 'Expanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex parte', '', 'Upon or from one side only; one-sided; partial; as, an ex\n   parte statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiated', 'imp. & p. p.', 'of Expatiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expariating', 'p. pr. & vb. n.', 'of Expatiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiate', 'v. i.', 'To range at large, or without restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiate', 'v. i.', 'To enlarge in discourse or writing; to be copious in\n   argument or discussion; to descant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiate', 'v. t.', 'To expand; to spread; to extend; to diffuse; to\n   broaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiation', 'n.', 'Act of expatiating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatiatory', 'a.', 'Expansive; diffusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatriated', 'imp. & p. p.', 'of Expatriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatriating', 'p. pr. & vb. n.', 'of Expatriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatriate', 'v. t.', 'To banish; to drive or force (a person) from his own\n   country; to make an exile of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatriate', 'v. t.', 'Reflexively, as To expatriate ones self: To\n   withdraw from ones native country; to renounce the rights and\n   liabilities of citizenship where one is born, and become a citizen of\n   another country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expatriation', 'n.', 'The act of banishing, or the state of banishment;\n   especially, the forsaking of ones own country with a renunciation of\n   allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expected', 'imp. & p. p.', 'of Expect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expecting', 'p. pr. & vb. n.', 'of Expect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expect', 'v. t.', 'To wait for; to await.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expect', 'v. t.', 'To look for (mentally); to look forward to, as to\n   something that is believed to be about to happen or come; to have a\n   previous apprehension of, whether of good or evil; to look for with\n   some confidence; to anticipate; -- often followed by an infinitive,\n   sometimes by a clause (with, or without, that); as, I expect to receive\n   wages; I expect that the troops will be defeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expect', 'v. t.', 'To wait; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expect', 'n.', 'Expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectable', 'a.', 'That may be expected or looked for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectance', 'n.', 'Alt. of Expectancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectancy', 'n.', 'The act of expecting ; expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectancy', 'n.', 'That which is expected, or looked or waited for with\n   interest; the object of expectation or hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectant', 'a.', 'Waiting in expectation; looking for');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectant', 'a.', 'waiting for the efforts of nature, with little active\n   treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectant', 'n.', 'One who waits in expectation; one held in dependence by\n   hope of receiving some good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectation', 'n.', 'The act or state of expecting or looking forward to an\n   event as about to happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectation', 'n.', 'That which is expected or looked for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectation', 'n.', 'The prospect of the future; grounds upon which\n   something excellent is expected to happen; prospect of anything good to\n   come, esp. of property or rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectation', 'n.', 'The value of any chance (as the prospect of prize or\n   property) which depends upon some contingent event. Expectations are\n   computed for or against the occurrence of the event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectation', 'n.', 'The leaving of the disease principally to the efforts\n   of nature to effect a cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectative', 'a.', 'Constituting an object of expectation; contingent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectative', 'n.', 'Something in expectation; esp., an expectative grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectedly', 'adv.', 'In conformity with expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expecter', 'n.', 'One who expects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectingly', 'adv.', 'In a state of expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expective', 'a.', 'Expectative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorant', 'a.', 'Tending to facilitate expectoration or to promote\n   discharges of mucus, etc., from the lungs or throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorant', 'n.', 'An expectorant medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorated', 'imp. & p. p.', 'of Expectorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorating', 'p. pr. & vb. n.', 'of Expectorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorate', 'v. t.', 'To eject from the trachea or lungs; to discharge,\n   as phlegm or other matter, by coughing, hawking, and spitting; to spit\n   forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorate', 'v. i.', 'To discharge matter from the lungs or throat by\n   hawking and spitting; to spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectoration', 'n.', 'The act of ejecting phlegm or mucus from the throat\n   or lungs, by coughing, hawking, and spitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectoration', 'n.', 'That which is expectorated, as phlegm or mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expectorative', 'a. & n.', 'Same as Expectorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expede', 'v. t.', 'To expedite; to hasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediate', 'v. t.', 'To hasten; to expedite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedience', 'n.', 'Alt. of Expediency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediency', 'n.', 'The quality of being expedient or advantageous; fitness\n   or suitableness to effect a purpose intended; adaptedness to\n   self-interest; desirableness; advantage; advisability; -- sometimes\n   contradistinguished from moral rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediency', 'n.', 'Expedition; haste; dispatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediency', 'n.', 'An expedition; enterprise; adventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedient', 'a.', 'Hastening or forward; hence, tending to further or\n   promote a proposed object; fit or proper under the circumstances;\n   conducive to self-interest; desirable; advisable; advantageous; --\n   sometimes contradistinguished from right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedient', 'a.', 'Quick; expeditious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedient', 'n.', 'That which serves to promote or advance; suitable means\n   to accomplish an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedient', 'n.', 'Means devised in an exigency; shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediential', '', 'Governed by expediency; seeking advantage; as an\n   expediential policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediently', 'adv.', 'In an expedient manner; fitly; suitably;\n   conveniently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediently', 'adv.', 'With expedition; quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediment', 'n.', 'An expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditate', 'v. t.', 'To deprive of the claws or the balls of the fore\n   feet; as, to expeditate a dog that he may not chase deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedite', 'a.', 'Free of impediment; unimpeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedite', 'a.', 'Expeditious; quick; speedily; prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedited', 'imp. & p. p.', 'of Expedite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediting', 'p. pr. & vb. n.', 'of Expedite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedite', 'v. t.', 'To relieve of impediments; to facilitate; to\n   accelerate the process or progress of; to hasten; to quicken; as, to\n   expedite the growth of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedite', 'v. t.', 'To despatch; to send forth; to issue officially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditely', 'adv.', 'In expedite manner; expeditiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expediteness', 'n.', 'Quality of being expedite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedition', 'n.', 'The quality of being expedite; efficient promptness;\n   haste; dispatch; speed; quickness; as to carry the mail with\n   expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedition', 'n.', 'A sending forth or setting forth the execution of some\n   object of consequence; progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expedition', 'n.', 'An important enterprise, implying a change of place;\n   especially, a warlike enterprise; a march or a voyage with martial\n   intentions; an excursion by a body of persons for a valuable end; as, a\n   military, naval, exploring, or scientific expedition; also, the body of\n   persons making such excursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditionary', 'a.', 'Of or pertaining to an expedition; as, an\n   expeditionary force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditionist', 'n.', 'One who goes upon an expedition. [R].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditious', 'a.', 'Possessed of, or characterized by, expedition, or\n   efficiency and rapidity in action; performed with, or acting with,\n   expedition; quick; having celerity; speedily; as, an expeditious march\n   or messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeditive', 'a.', 'Performing with speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expelled', 'imp. & p. p.', 'of Expel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expelling', 'p. pr. & vb. n.', 'of Expel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expel', 'v. t.', 'To drive or force out from that within which anything is\n   contained, inclosed, or situated; to eject; as to expel air from a\n   bellows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expel', 'v. t.', 'To drive away from ones country; to banish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expel', 'v. t.', 'To cut off from further connection with an institution of\n   learning, a society, and the like; as, to expel a student or member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expel', 'v. t.', 'To keep out, off, or away; to exclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expel', 'v. t.', 'To discharge; to shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expellable', 'a.', 'Capable of being expelled or driven out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expeller', 'n.', 'One who, or that which, expels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expended', 'imp. & p. p.', 'of Expend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expending', 'p. pr. & vb. n.', 'of Expend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expend', 'v. t.', 'To lay out, apply, or employ in any way; to consume by\n   use; to use up or distribute, either in payment or in donations; to\n   spend; as, they expend money for food or in charity; to expend time\n   labor, and thought; to expend hay in feeding cattle, oil in a lamp,\n   water in mechanical operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expend', 'v. i.', 'To be laid out, used, or consumed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expend', 'v. i.', 'To pay out or disburse money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expenitor', 'n.', 'A disburser; especially, one of the disbursers of taxes\n   for the repair of sewers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expenditure', 'n.', 'The act of expending; a laying out, as of money;\n   disbursement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expenditure', 'n.', 'That which is expended or paid out; expense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expense', 'n.', 'A spending or consuming; disbursement; expenditure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expense', 'n.', 'That which is expended, laid out, or consumed; cost;\n   outlay; charge; -- sometimes with the notion of loss or damage to those\n   on whom the expense falls; as, the expenses of war; an expense of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expense', 'n.', 'Loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expensefull', 'a.', 'Full of expense; costly; chargeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expenseless', 'a.', 'Without cost or expense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expensive', 'a.', 'Occasioning expense; calling for liberal outlay; costly;\n   dear; liberal; as, expensive dress; an expensive house or family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expensive', 'a.', 'Free in expending; very liberal; especially, in a bad\n   scene; extravagant; lavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experience', 'n.', 'Trial, as a test or experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experience', 'n.', 'The effect upon the judgment or feelings produced by\n   any event, whether witnessed or participated in; personal and direct\n   impressions as contrasted with description or fancies; personal\n   acquaintance; actual enjoyment or suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experience', 'n.', 'An act of knowledge, one or more, by which single facts\n   or general truths are ascertained; experimental or inductive knowledge;\n   hence, implying skill, facility, or practical wisdom gained by personal\n   knowledge, feeling or action; as, a king without experience of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experienced', 'imp. & p. p.', 'of Exrerience');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiencing', 'p. pr. & vb. n.', 'of Exrerience');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exrerience', 'v. t.', 'To make practical acquaintance with; to try\n   personally; to prove by use or trial; to have trial of; to have the lot\n   or fortune of; to have befall one; to be affected by; to feel; as, to\n   experience pain or pleasure; to experience poverty; to experience a\n   change of views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exrerience', 'v. t.', 'To exercise; to train by practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experienced', 'p. p. & a.', 'Taught by practice or by repeated\n   observations; skillful or wise by means of trials, use, or observation;\n   as, an experienced physician, workman, soldier; an experienced eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiencer', 'n.', 'One who experiences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiencer', 'n.', 'An experimenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experient', 'a.', 'Experienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiential', 'a.', 'Derived from, or pertaining to, experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experientialism', 'n.', 'The doctrine that experience, either that\n   ourselves or of others, is the test or criterion of general knowledge;\n   -- opposed to intuitionists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experientiallist', 'n.', 'One who accepts the doctrine of experientialism.\n   Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiment', 'n.', 'Atrial or special observation, made to confirm or\n   disprove something doubtful; esp., one under conditions determined by\n   the experimenter; an act or operation undertaken in order to discover\n   some unknown principle or effect, or to test, establish, or illustrate\n   some suggest or known truth; practical test; poof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiment', 'n.', 'Experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimented', 'imp. & p. p.', 'of Experiment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experinenting', 'p. pr. & vb. n.', 'of Experiment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiment', 'v. t.', 'To make experiment; to operate by test or trial; --\n   often with on, upon, or in, referring to the subject of an experiment;\n   with, referring to the instrument; and by, referring to the means; as,\n   to experiment upon electricity; he experimented in plowing with ponies,\n   or by steam power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experiment', 'v. t.', 'To try; to know, perceive, or prove, by trial\n   experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimental', 'a.', 'Pertaining to experiment; founded on, or derived\n   from, experiment or trial; as, experimental science; given to, or\n   skilled in, experiment; as, an experimental philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimental', 'a.', 'Known by, or derived from, experience; as,\n   experimental religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimetalist', 'n.', 'One who makes experiments; an experimenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentalize', 'v. i.', 'To make experiments (upon); to experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentally', 'adv.', 'By experiment; by experience or trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentarian', 'a.', 'Relying on experiment or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentarian', 'n.', 'One who relies on experiment or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentation', 'n.', 'The act of experimenting; practice by experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentative', 'a.', 'Experimental; of the nature of experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentator', 'n.', 'An experimenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimenter', 'n.', 'One who makes experiments; one skilled in\n   experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experimentist', 'n.', 'An experimenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Experrection', 'n.', 'A waking up or arousing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expert', 'a.', 'Taught by use, practice, or experience, experienced; having\n   facility of operation or performance from practice; knowing and ready\n   from much practice; clever; skillful; as, an expert surgeon; expert in\n   chess or archery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expert', 'n.', 'An expert or experienced person; one instructed by\n   experience; one who has skill, experience, or extensive knowledge in\n   his calling or in any special branch of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expert', 'n.', 'A specialist in a particular profession or department of\n   science requiring for its mastery peculiar culture and erudition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expert', 'n.', 'A sworn appraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expert', 'v. t.', 'To experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expertly', 'adv.', 'In a skillful or dexterous manner; adroitly; with\n   readiness and accuracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expertness', 'n.', 'Skill derived from practice; readiness; as, expertness\n   in seamanship, or in reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expetible', 'a.', 'Worthy of being wished for; desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiable', 'a.', 'Capable of being expiated or atoned for; as, an expiable\n   offense; expiable guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiated', 'imp. & p. p.', 'of Expiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiating', 'p. pr. & vb. n.', 'of Expiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiate', 'v. t.', 'To extinguish the guilt of by sufferance of penalty or\n   some equivalent; to make complete satisfaction for; to atone for; to\n   make amends for; to make expiation for; as, to expiate a crime, a\n   guilt, or sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiate', 'v. t.', 'To purify with sacred rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiate', 'a.', 'Terminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiation', 'n.', 'The act of making satisfaction or atonement for any\n   crime or fault; the extinguishing of guilt by suffering or penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiation', 'n.', 'The means by which reparation or atonement for crimes or\n   sins is made; an expiatory sacrifice or offering; an atonement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiation', 'n.', 'An act by which the treats of prodigies were averted\n   among the ancient heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiatist', 'n.', 'An expiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiator', 'n.', 'One who makes expiation or atonement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiatorious', 'a.', 'Of an expiatory nature; expiatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiatory', 'a.', 'Having power, or intended, to make expiation; atoning;\n   as, an expiatory sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expilation', 'n.', 'The act of expilating or stripping off; plunder;\n   pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expilator', 'n.', 'One who pillages; a plunderer; a pillager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expirable', 'a.', 'That may expire; capable of being brought to an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expirant', 'n.', 'One who expires or is expiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'The act of expiring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'The act or process of breathing out, or forcing air\n   from the lungs through the nose or mouth; as, respiration consists of\n   inspiration and expiration; -- opposed to inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'Emission of volatile matter; exhalation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'The last emission of breath; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'A coming to a close; cessation; extinction;\n   termination; end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiration', 'n.', 'That which is expired; matter breathed forth; that\n   which is produced by breathing out, as a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiratory', 'a.', 'Pertaining to, or employed in, the expiration or\n   emission of air from the lungs; as, the expiratory muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expired', 'imp. & p. p.', 'of Expire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiring', 'p. pr & vb. n.', 'of Expire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. t.', 'To breathe out; to emit from the lungs; to throw out\n   from the mouth or nostrils in the process of respiration; -- opposed to\n   inspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. t.', 'To give forth insensibly or gently, as a fluid or vapor;\n   to emit in minute particles; to exhale; as, the earth expires a damp\n   vapor; plants expire odors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. t.', 'To emit; to give out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. t.', 'To bring to a close; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. i.', 'To emit the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. i.', 'To emit the last breath; to breathe out the life; to\n   die; as, to expire calmly; to expire in agony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. i.', 'To come to an end; to cease; to terminate; to perish; to\n   become extinct; as, the flame expired; his lease expires to-day; the\n   month expired on Saturday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expire', 'v. i.', 'To burst forth; to fly out with a blast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiring', 'a.', 'Breathing out air from the lungs; emitting fluid or\n   volatile matter; exhaling; breathing the last breath; dying; ending;\n   terminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiring', 'a.', 'Pertaining to, or uttered at, the time of dying; as,\n   expiring words; expiring groans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiry', 'n.', 'Expiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiscate', 'v. t.', 'To fish out; to find out by skill or laborious\n   investigation; to search out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiscation', 'n.', 'The act of expiscating; a fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expiscatory', 'a.', 'Tending to fish out; searching out');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explained', 'imp. & p. p.', 'of Explain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explaining', 'p. pr. & vb. n.', 'of Explain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explain', 'a.', 'To flatten; to spread out; to unfold; to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explain', 'a.', 'To make plain, manifest, or intelligible; to clear of\n   obscurity; to expound; to unfold and illustrate the meaning of; as, to\n   explain a chapter of the Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explain', 'v. i.', 'To give an explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explainable', 'a.', 'Capable of being explained or made plain to the\n   understanding; capable of being interpreted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explainer', 'n.', 'One who explains; an expounder or expositor; a\n   commentator; an interpreter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanate', 'a.', 'Spreading or extending outwardly in a flat form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanation', 'n.', 'The act of explaining, expounding, or interpreting;\n   the act of clearing from obscurity and making intelligible; as, the\n   explanation of a passage in Scripture, or of a contract or treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanation', 'n.', 'That which explains or makes clear; as, a satisfactory\n   explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanation', 'n.', 'The meaning attributed to anything by one who explains\n   it; definition; interpretation; sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanation', 'n.', 'A mutual exposition of terms, meaning, or motives,\n   with a view to adjust a misunderstanding, and reconcile differences;\n   reconciliation; agreement; as, to come to an explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanative', 'a.', 'Explanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanatoriness', 'n.', 'The quality of being explanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explanatory', 'a.', 'Serving to explain; containing explanation; as\n   explanatory notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explat', 'v. t.', 'Alt. of Explate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explate', 'v. t.', 'To explain; to unfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expletion', 'n.', 'Accomplishment; fulfillment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expletive', 'a.', 'Filling up; hence, added merely for the purpose of\n   filling up; superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expletive', 'n.', 'A word, letter, or syllable not necessary to the sense,\n   but inserted to fill a vacancy; an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expletively', 'adv.', 'In the manner of an expletive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expletory', 'a.', 'Serving to fill up; expletive; superfluous; as, an\n   expletory word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicable', 'a.', 'Capable of being explicated; that may be explained or\n   accounted for; admitting explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicableness', 'n.', 'Quality of being explicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicate', 'a.', 'Evolved; unfolded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicated', 'imp. & p. p.', 'of Explicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicating', 'p. pr. & vb. n.', 'of Explicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicate', 'v. t.', 'To unfold; to expand; to lay open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicate', 'v. t.', 'To unfold the meaning or sense of; to explain; to\n   clear of difficulties or obscurity; to interpret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explication', 'n.', 'The act of opening, unfolding, or explaining;\n   explanation; exposition; interpretation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explication', 'n.', 'The sense given by an expositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicative', 'a.', 'Serving to unfold or explain; tending to lay open to\n   the understanding; explanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicator', 'n.', 'One who unfolds or explains; an expounder; an\n   explainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicatory', 'a.', 'Explicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicit', 'a.', 'A word formerly used (as finis is now) at the conclusion\n   of a book to indicate the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicit', 'a.', 'Not implied merely, or conveyed by implication;\n   distinctly stated; plain in language; open to the understanding; clear;\n   not obscure or ambiguous; express; unequivocal; as, an explicit\n   declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicit', 'a.', 'Having no disguised meaning or reservation; unreserved;\n   outspoken; -- applied to persons; as, he was earnest and explicit in\n   his statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicitly', 'adv.', 'In an explicit manner; clearly; plainly; without\n   disguise or reservation of meaning; not by inference or implication;\n   as, he explicitly avows his intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explicitness', 'n.', 'The quality of being explicit; clearness; directness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploded', 'imp. & p. p.', 'of Explode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploding', 'p. pr. & vb. n.', 'of Explode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. i.', 'To become suddenly expanded into a great volume of gas\n   or vapor; to burst violently into flame; as gunpowder explodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. i.', 'To burst with force and a loud report; to detonate, as\n   a shell filled with powder or the like material, or as a boiler from\n   too great pressure of steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. i.', 'To burst forth with sudden violence and noise; as, at\n   this, his wrath exploded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. t.', 'To drive from the stage by noisy expressions of\n   disapprobation; to hoot off; to drive away or reject noisily; as, to\n   explode a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. t.', 'To bring into disrepute, and reject; to drive from\n   notice and acceptance; as, to explode a scheme, fashion, or doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. t.', 'To cause to explode or burst noisily; to detonate; as,\n   to explode powder by touching it with fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explode', 'v. t.', 'To drive out with violence and noise, as by powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explodent', 'n.', 'An instrument or agent causing explosion; an exploder;\n   also, an explosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explodent', 'n.', 'See Explosive, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploder', 'n.', 'One who or that which explodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploder', 'n.', 'One who rejects an opinion or scheme with open contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploit', 'n.', 'A deed or act; especially, a heroic act; a deed of renown;\n   an adventurous or noble achievement; as, the exploits of Alexander the\n   Great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploit', 'n.', 'Combat; war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploit', 'n.', 'To utilize; to make available; to get the value or\n   usefulness out of; as, to exploit a mine or agricultural lands; to\n   exploit public opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploit', 'n.', 'Hence: To draw an illegitimate profit from; to speculate\n   on; to put upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploitation', 'n.', 'The act of exploiting or utilizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploiture', 'n.', 'The act of exploiting or accomplishing; achievement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploiture', 'n.', 'Exploitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorable', 'a.', 'That may be explored; as, an explorable region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorate', 'v. t.', 'To explore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploration', 'n.', 'The act of exploring, penetrating, or ranging over for\n   purposes of discovery, especially of geographical discovery;\n   examination; as, the exploration of unknown countries');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploration', 'n.', 'physical examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorative', 'a.', 'Exploratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorator', 'n.', 'One who explores; one who examines closely; a searcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploratory', 'a.', 'Serving or intended to explore; searching; examining;\n   explorative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explored', 'imp. & p. p.', 'of Explore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exploring', 'p. pr. & vb. n.', 'of Explore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explore', 'v. t.', 'To seek for or after; to strive to attain by search; to\n   look wisely and carefully for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explore', 'v. t.', 'To search through or into; to penetrate or range over\n   for discovery; to examine thoroughly; as, to explore new countries or\n   seas; to explore the depths of science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorement', 'n.', 'The act of exploring; exploration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explorer', 'n.', 'One who explores; also, an apparatus with which one\n   explores, as a diving bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eploring', 'a.', 'Employed in, or designed for, exploration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosion', 'n.', 'The act of exploding; detonation; a chemical action\n   which causes the sudden formation of a great volume of expanded gas;\n   as, the explosion of gunpowder, of fire damp,etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosion', 'n.', 'A bursting with violence and loud noise, because of\n   internal pressure; as, the explosion of a gun, a bomb, a steam boiler,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosion', 'n.', 'A violent outburst of feeling, manifested by excited\n   language, action, etc.; as, an explosion of wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosive', 'a.', 'Driving or bursting out with violence and noise; causing\n   explosion; as, the explosive force of gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosive', 'n.', 'An explosive agent; a compound or mixture susceptible of\n   a rapid chemical reaction, as gunpowder, or nitro-glycerine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosive', 'n.', 'A sound produced by an explosive impulse of the breath;\n   (Phonetics) one of consonants p, b, t, d, k, g, which are sounded with\n   a sort of explosive power of voice. [See Guide to Pronunciation, Ã\n   155-7, 184.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Explosively', 'adv.', 'In an explosive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expoliation', 'n.', 'See Exspoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expolish', 'v. t.', 'To polish thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expone', 'v. t.', 'To expound; to explain; also, to expose; to imperil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exponent', 'n.', 'A number, letter, or any quantity written on the right\n   hand of and above another quantity, and denoting how many times the\n   latter is repeated as a factor to produce the power indicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exponent', 'n.', 'One who, or that which, stands as an index or\n   representative; as, the leader of a party is the exponent of its\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exponential', 'a.', 'Pertaining to exponents; involving variable exponents;\n   as, an exponential expression; exponential calculus; an exponential\n   function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exported', 'imp. & p. p.', 'of Export');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exporting', 'p. pr. & vb. n.', 'of Export');
  INSERT INTO words (word, word_type, description) VALUES  (    'Export', 'v. t.', 'To carry away; to remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Export', 'v. t.', 'To carry or send abroad, or out of a country, especially\n   to foreign countries, as merchandise or commodities in the way of\n   commerce; -- the opposite of import; as, to export grain, cotton,\n   cattle, goods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Export', 'n.', 'The act of exporting; exportation; as, to prohibit the\n   export of wheat or tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Export', 'n.', 'That which is exported; a commodity conveyed from one\n   country or State to another in the way of traffic; -- used chiefly in\n   the plural, exports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exportability', 'n.', 'The quality or state of being suitable for\n   exportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exportable', 'a.', 'Suitable for exportation; as, exportable products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exportation', 'n.', 'The act of exporting; the act of conveying or sending\n   commodities abroad or to another country, in the course of commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exportation', 'n.', 'Commodity exported; an export.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exportation', 'n.', 'The act of carrying out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exporter', 'n.', 'One who exports; the person who sends goods or\n   commodities to a foreign country, in the way of commerce; -- opposed to\n   importer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposal', 'n.', 'Exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposed', 'imp. & p. p.', 'of Expose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposing', 'p. pr. & vb. n.', 'of Expose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expose', 'v. t.', 'To set forth; to set out to public view; to exhibit; to\n   show; to display; as, to expose goods for sale; to expose pictures to\n   public inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expose', 'v. t.', 'To lay bare; to lay open to attack, danger, or anything\n   objectionable; to render accessible to anything which may affect,\n   especially detrimentally; to make liable; as, to expose ones self to\n   the heat of the sun, or to cold, insult, danger, or ridicule; to expose\n   an army to destruction or defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expose', 'v. t.', 'To deprive of concealment; to discover; to lay open to\n   public inspection, or bring to public notice, as a thing that shuns\n   publicity, something criminal, shameful, or the like; as, to expose the\n   faults of a neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expose', 'v. t.', 'To disclose the faults or reprehensible practices of; to\n   lay open to general condemnation or contempt by making public the\n   character or arts of; as, to expose a cheat, liar, or hypocrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expose', 'v. t.', 'A formal recital or exposition of facts; exposure, or\n   revelation, of something which some one wished to keep concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposedness', 'n.', 'The state of being exposed, laid open, or unprotected;\n   as, an exposedness to sin or temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposer', 'n.', 'One who exposes or discloses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposition', 'n.', 'The act of exposing or laying open; a setting out or\n   displaying to public view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposition', 'n.', 'The act of expounding or of laying open the sense or\n   meaning of an author, or a passage; explanation; interpretation; the\n   sense put upon a passage; a law, or the like, by an interpreter; hence,\n   a work containing explanations or interpretations; a commentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposition', 'n.', 'Situation or position with reference to direction of\n   view or accessibility to influence of sun, wind, etc.; exposure; as, an\n   easterly exposition; an exposition to the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposition', 'n.', 'A public exhibition or show, as of industrial and\n   artistic productions; as, the Paris Exposition of 1878.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expositive', 'a.', 'Serving to explain; expository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expositor', 'n.', 'One who, or that which, expounds or explains; an\n   expounder; a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expository', 'a.', 'Pertaining to, or containing, exposition; serving to\n   explain; explanatory; illustrative; exegetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex post facto', '', 'Alt. of Ex postfacto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex postfacto', '', 'From or by an after act, or thing done afterward; in\n   consequence of a subsequent act; retrospective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulated', 'imp. & p. p.', 'of Expostulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulating', 'p. pr. & vb. n.', 'of Expostulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulate', 'v. i.', 'To reason earnestly with a person on some\n   impropriety of his conduct, representing the wrong he has done or\n   intends, and urging him to make redress or to desist; to remonstrate;\n   -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulate', 'v. t.', 'To discuss; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulation', 'n.', 'The act of expostulating or reasoning with a person\n   in opposition to some impropriety of conduct; remonstrance; earnest and\n   kindly protest; dissuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulator', 'n.', 'One who expostulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expostulatory', 'a.', 'Containing expostulation or remonstrance; as, an\n   expostulatory discourse or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposture', 'n.', 'Exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposure', 'n.', 'The act of exposing or laying open, setting forth, laying\n   bare of protection, depriving of care or concealment, or setting out to\n   reprobation or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposure', 'n.', 'The state of being exposed or laid open or bare; openness\n   to danger; accessibility to anything that may affect, especially\n   detrimentally; as, exposure to observation, to cold, to inconvenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposure', 'n.', 'Position as to points of compass, or to influences of\n   climate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exposure', 'n.', 'The exposing of a sensitized plate to the action of\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expounded', 'imp. & p. p.', 'of Expound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expounding', 'p. pr. & vb. n.', 'of Expound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expound', 'v. t.', 'To lay open; to expose to view; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expound', 'v. t.', 'To lay open the meaning of; to explain; to clear of\n   obscurity; to interpret; as, to expound a text of Scripture, a law, a\n   word, a meaning, or a riddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expounder', 'n.', 'One who expounds or explains; an interpreter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'Exactly representing; exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'Directly and distinctly stated; declared in terms; not\n   implied or left to inference; made unambiguous by intention and care;\n   clear; not dubious; as, express consent; an express statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'Intended for a particular purpose; relating to an express;\n   sent on a particular errand; dispatched with special speed; as, an\n   express messenger or train. Also used adverbially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'n.', 'A clear image or representation; an expression; a plain\n   declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'n.', 'A messenger sent on a special errand; a courier; hence, a\n   regular and fast conveyance; commonly, a company or system for the\n   prompt and safe transportation of merchandise or parcels; also, a\n   railway train for transporting passengers or goods with speed and\n   punctuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'n.', 'An express office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'n.', 'That which is sent by an express messenger or message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressed', 'imp. & p. p.', 'of Express');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressing', 'p. pr. & vb. n.', 'of Express');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To press or squeeze out; as, to express the juice of\n   grapes, or of apples; hence, to extort; to elicit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To make or offer a representation of; to show by a copy or\n   likeness; to represent; to resemble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To give a true impression of; to represent and make known;\n   to manifest plainly; to show in general; to exhibit, as an opinion or\n   feeling, by a look, gesture, and esp. by language; to declare; to\n   utter; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To make known the opinions or feelings of; to declare what\n   is in the mind of; to show (ones self); to cause to appear; -- used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To denote; to designate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Express', 'a.', 'To send by express messenger; to forward by special\n   opportunity, or through the medium of an express; as, to express a\n   package.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressage', 'n.', 'The charge for carrying a parcel by express.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressible', 'a.', 'Capable of being expressed, squeezed out, shown,\n   represented, or uttered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'The act of expressing; the act of forcing out by\n   pressure; as, the expression of juices or oils; also, of extorting or\n   eliciting; as, a forcible expression of truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'The act of declaring or signifying; declaration;\n   utterance; as, an expression of the public will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'Lively or vivid representation of meaning, sentiment,\n   or feeling, etc.; significant and impressive indication, whether by\n   language, appearance, or gesture; that manner or style which gives life\n   and suggestive force to ideas and sentiments; as, he reads with\n   expression; her performance on the piano has expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'That which is expressed by a countenance, a posture, a\n   work of art, etc.; look, as indicative of thought or feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'A form of words in which an idea or sentiment is\n   conveyed; a mode of speech; a phrase; as, a common expression; an odd\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expression', 'n.', 'The representation of any quantity by its appropriate\n   characters or signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressional', 'a.', 'Of, or relating to, expression; phraseological; also,\n   vividly representing or suggesting an idea or sentiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressionless', 'a.', 'Destitute of expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressive', 'a.', 'Serving to express, utter, or represent; indicative;\n   communicative; -- followed by of; as, words expressive of his\n   gratitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressive', 'a.', 'Full of expression; vividly representing the meaning or\n   feeling meant to be conveyed; significant; emphatic; as, expressive\n   looks or words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressly', 'adv.', 'In an express manner; in direct terms; with distinct\n   purpose; particularly; as, a book written expressly for the young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressmen', 'pl. ', 'of Expressman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressman', 'n.', 'A person employed in the express business; also, the\n   driver of a job wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressness', 'n.', 'The state or quality of being express; definiteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expressure', 'n.', 'The act of expressing; expression; utterance;\n   representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exprobrate', 'v. t.', 'To charge upon with reproach; to upbraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exprobration', 'n.', 'Reproachful accusation; upbraiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exprobrative', 'a.', 'Alt. of Exprobratory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exprobratory', 'a.', 'Expressing reproach; upbraiding; reproachful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expropriate', 'v. t.', 'To put out of ones possession; to surrender the\n   ownership of; also, to deprive of possession or proprietary rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expropriation', 'n.', 'The act of expropriating; the surrender of a claim\n   to exclusive property; the act of depriving of ownership or proprietary\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expugn', 'v. t.', 'To take by assault; to storm; to overcome; to vanquish;\n   as, to expugn cities; to expugn a person by arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expugnable', 'a.', 'Capable of being expugned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expugnation', 'n.', 'The act of taking by assault; conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expugner', 'n.', 'One who expugns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expulse', 'v. t.', 'To drive out; to expel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expulser', 'n.', 'An expeller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expulsion', 'n.', 'The act of expelling; a driving or forcing out; summary\n   removal from membership, association, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expulsion', 'n.', 'The state of being expelled or driven out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expulsive', 'a.', 'Having the power of driving out or away; serving to\n   expel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expunction', 'n.', 'The act of expunging or erasing; the condition of being\n   expunged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expunged', 'imp. & p. p.', 'of Expunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expunging', 'p. pr. & vb. n.', 'of Expunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expunge', 'v. t.', 'To blot out, as with pen; to rub out; to efface\n   designedly; to obliterate; to strike out wholly; as, to expunge words,\n   lines, or sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expunge', 'v. t.', 'To strike out; to wipe out or destroy; to annihilate;\n   as, to expugne an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgated', 'imp. & p. p.', 'of Expurgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgating', 'p. pr. & vb. n.', 'of Expurgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgate', 'v. t.', 'To purify; to clear from anything noxious, offensive,\n   or erroneous; to cleanse; to purge; as, to expurgate a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgation', 'n.', 'The act of expurgating, purging, or cleansing;\n   purification from anything noxious, offensive, sinful, or erroneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgator', 'n.', 'One who expurgates or purifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgatorial', 'a.', 'Tending or serving to expurgate; expurgatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgatorious', 'a.', 'Expurgatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurgatory', 'a.', 'Serving to purify from anything noxious or erroneous;\n   cleansing; purifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Expurge', 'v. t.', 'To purge away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquire', 'v. t.', 'To search into or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisite', 'a.', 'Carefully selected or sought out; hence, of\n   distinguishing and surpassing quality; exceedingly nice; delightfully\n   excellent; giving rare satisfaction; as, exquisite workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisite', 'a.', 'Exceeding; extreme; keen; -- used in a bad or a good\n   sense; as, exquisite pain or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisite', 'a.', 'Of delicate perception or close and accurate\n   discrimination; not easy to satisfy; exact; nice; fastidious; as,\n   exquisite judgment, taste, or discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisite', 'n.', 'One who manifests an exquisite attention to external\n   appearance; one who is overnice in dress or ornament; a fop; a dandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisitely', 'adv.', 'In an exquisite manner or degree; as, lace\n   exquisitely wrought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisiteness', 'n.', 'Quality of being exquisite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exquisitive', 'a.', 'Eager to discover or learn; curious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguine', 'a.', 'Bloodless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguineous', 'a.', 'Destitute of blood; anaemic; exsanguious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguinity', 'n.', 'Privation or destitution of blood; -- opposed to\n   plethora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguinous', 'a.', 'See Exsanguious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguious', 'a.', 'Destitute of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsanguious', 'a.', 'Destitute of true, or red, blood, as insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscinded', 'imp. & p. p.', 'of Exscind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscinding', 'p. pr. & vb. n.', 'of Exscind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscind', 'v. t.', 'To cut off; to separate or expel from union; to\n   extirpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscribe', 'v. t.', 'To copy; to transcribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscript', 'n.', 'A copy; a transcript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscriptural', 'a.', 'Not in accordance with the doctrines of Scripture;\n   unscriptural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exscutellate', 'a.', 'Without, or apparently without, a scutellum; -- said\n   of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsect', 'v. t.', 'A cutting out or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsect', 'v. t.', 'The removal by operation of a portion of a limb;\n   particularly, the removal of a portion of a bone in the vicinity of a\n   joint; the act or process of cutting out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsert', 'a.', 'Alt. of Exserted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exserted', 'a.', 'Standing out; projecting beyond some other part; as,\n   exsert stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsertile', 'a.', 'Capable of being thrust out or protruded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccant', 'a.', 'Having the quality of drying up; causing a drying up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccant', 'n.', 'An exsiccant medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccated', 'imp. & p. p.', 'of Exsiccate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccating', 'p. pr. & vb. n.', 'of Exsiccate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccate', 'v. t.', 'To exhaust or evaporate moisture from; to dry up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccation', 'n.', 'The act of operation of drying; evaporation or\n   expulsion of moisture; state of being dried up; dryness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccative', 'a.', 'Tending to make dry; having the power of drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiccator', 'n.', 'An apparatus for drying substances or preserving them\n   from moisture; a desiccator; also, less frequently, an agent employed\n   to absorb moisture, as calcium chloride, or concentrated sulphuric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsiliency', 'n.', 'A leaping out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsolution', 'n.', 'Relaxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exspoliation', 'n.', 'Spoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exspuition', 'n.', 'A discharge of saliva by spitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsputory', 'a.', 'Spit out, or as if spit out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exstipulate', 'a.', 'Having no stipules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exstrophy', 'n.', 'The eversion or turning out of any organ, or of its\n   inner surface; as, exstrophy of the eyelid or of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsuccous', 'a.', 'Destitute of juice; dry; sapless. Latham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsuction', 'n.', 'The act of sucking out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsudation', 'n.', 'Exudation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsufflate', 'v. t.', 'To exorcise or renounce by blowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsufflation', 'n.', 'A blast from beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsufflation', 'n.', 'A kind of exorcism by blowing with the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsufflation', 'n.', 'A strongly forced expiration of air from the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsufflicate', 'a.', 'Empty; frivolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsuscitate', 'v. t.', 'To rouse; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exsuscitation', 'n.', 'A stirring up; a rousing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extacy', 'n.', 'See Ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extance', 'n.', 'Outward existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extancy', 'n.', 'The state of rising above others; a projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extant', 'a.', 'Standing out or above any surface; protruded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extant', 'a.', 'Still existing; not destroyed or lost; outstanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extant', 'a.', 'Publicly known; conspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extasy', 'n. & v. t.', 'See Ecstasy, n. & v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extatic', 'a.', 'See Ecstatic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporal', 'a.', 'Extemporaneous; unpremeditated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporanean', 'a.', 'Extemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporaneous', 'a.', 'Composed, performed, or uttered on the spur of the\n   moment, or without previous study; unpremeditated; off-hand; extempore;\n   extemporary; as, an extemporaneous address or production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporarily', 'adv.', 'Extemporaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporary', 'a.', 'Extemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporary', 'a.', 'Made for the occasion; for the time being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extempore', 'adv.', 'Without previous study or meditation; without\n   preparation; on the spur of the moment; suddenly; extemporaneously; as,\n   to write or speak extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extempore', 'a.', 'Done or performed extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extempore', 'n.', 'Speaking or writing done extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporiness', 'n.', 'The quality of being done or devised extempore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporization', 'n.', 'The act of extemporizing; the act of doing\n   anything extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporized', 'imp. & p. p.', 'of Extemporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporizing', 'p. pr. & vb. n.', 'of Extemporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporize', 'v. i.', 'To speak extempore; especially, to discourse\n   without special preparation; to make an offhand address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporize', 'v. t.', 'To do, make, or utter extempore or off-hand; to\n   prepare in great haste, under urgent necessity, or with scanty or\n   unsuitable materials; as, to extemporize a dinner, a costume, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extemporizer', 'n.', 'One who extemporizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extended', 'imp. & p. p.', 'of Extend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extending', 'p. pr. & vb. n.', 'of Extend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To stretch out; to prolong in space; to carry forward or\n   continue in length; as, to extend a line in surveying; to extend a cord\n   across the street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To enlarge, as a surface or volume; to expand; to\n   spread; to amplify; as, to extend metal plates by hammering or rolling\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To enlarge; to widen; to carry out further; as, to\n   extend the capacities, the sphere of usefulness, or commerce; to extend\n   power or influence; to continue, as time; to lengthen; to prolong; as,\n   to extend the time of payment or a season of trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To hold out or reach forth, as the arm or hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To bestow; to offer; to impart; to apply; as, to extend\n   sympathy to the suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To increase in quantity by weakening or adulterating\n   additions; as, to extend liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extend', 'v. t.', 'To value, as lands taken by a writ of extent in\n   satisfaction of a debt; to assign by writ of extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extendant', 'a.', 'Displaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extendedly', 'adv.', 'In an extended manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extender', 'n.', 'One who, or that which, extends or stretches anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extendible', 'a.', 'Capable of being extended, susceptible of being\n   stretched, extended, enlarged, widened, or expanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extendible', 'a.', 'Liable to be taken by a writ of extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extendlessness', 'n.', 'Unlimited extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extense', 'v. t.', 'Outreaching; expansive; extended, superficially or\n   otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensibility', 'n.', 'The quality of being extensible; the capacity of\n   being extended; as, the extensibility of a fiber, or of a plate of\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensible', 'a.', 'Capable of being extended, whether in length or\n   breadth; susceptible of enlargement; extensible; extendible; -- the\n   opposite of contractible or compressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensibleness', 'n.', 'Extensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensile', 'a.', 'Suited for, or capable of, extension; extensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'The act of extending or the state of being extended;\n   a stretching out; enlargement in breadth or continuation of length;\n   increase; augmentation; expansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'That property of a body by which it occupies a\n   portion of space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'Capacity of a concept or general term to include a\n   greater or smaller number of objects; -- correlative of intension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'The operation of stretching a broken bone so as to\n   bring the fragments into the same straight line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'The straightening of a limb, in distinction from\n   flexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extension', 'v. t.', 'A written engagement on the part of a creditor,\n   allowing a debtor further time to pay a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensional', 'a.', 'Having great extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensionist', 'n.', 'One who favors or advocates extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensive', 'a.', 'Having wide extent; of much superficial extent;\n   expanded; large; broad; wide; comprehensive; as, an extensive farm; an\n   extensive lake; an extensive sphere of operations; extensive\n   benevolence; extensive greatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensive', 'a.', 'Capable of being extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensively', 'adv.', 'To a great extent; widely; largely; as, a story is\n   extensively circulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensiveness', 'n.', 'The state of being extensive; wideness; largeness;\n   extent; diffusiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensometer', 'n.', 'An instrument for measuring the extension of a body,\n   especially for measuring the elongation of bars of iron, steel, or\n   other material, when subjected to a tensile force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensor', 'n.', 'A muscle which serves to extend or straighten any part of\n   the body, as an arm or a finger; -- opposed to flexor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extensure', 'n.', 'Extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extent', 'a.', 'Extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extent', 'n.', 'Space or degree to which a thing is extended; hence,\n   superficies; compass; bulk; size; length; as, an extent of country or\n   of line; extent of information or of charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extent', 'n.', 'Degree; measure; proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extent', 'n.', 'A peculiar species of execution upon debts due to the\n   crown, under which the lands and goods of the debtor may be seized to\n   secure payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extent', 'n.', 'A process of execution by which the lands and goods of a\n   debtor are valued and delivered to the creditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuated', 'imp. & p. p.', 'of Extenuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuating', 'p. pr. & vb. n.', 'of Extenuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuate', 'v. t.', 'To make thin or slender; to draw out so as to lessen\n   the thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuate', 'v. t.', 'To lessen; to palliate; to lessen or weaken the force\n   of; to diminish the conception of, as crime, guilt, faults, ills,\n   accusations, etc.; -- opposed to aggravate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuate', 'v. t.', 'To lower or degrade; to detract from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuate', 'v. i.', 'To become thinner; to make excuses; to advance\n   palliating considerations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuate', 'a.', 'Thin; slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuation', 'n.', 'The act of axtenuating or the state of being\n   extenuated; the act of making thin, slender, or lean, or of palliating;\n   diminishing, or lessening; palliation, as of a crime; mitigation, as of\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuator', 'n.', 'One who extenuates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extenuatory', 'a.', 'Tending to extenuate or palliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterior', 'a.', 'External; outward; pertaining to that which is external;\n   -- opposed to interior; as, the exterior part of a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterior', 'a.', 'External; on the outside; without the limits of;\n   extrinsic; as, an object exterior to a man, opposed to what is within,\n   or in his mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterior', 'a.', 'Relating to foreign nations; foreign; as, the exterior\n   relations of a state or kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterior', 'n.', 'The outward surface or part of a thing; that which is\n   external; outside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterior', 'n.', 'Outward or external deportment, form, or ceremony;\n   visible act; as, the exteriors of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exteriority', 'n.', 'Surface; superficies; externality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exteriorly', 'adv.', 'Outwardly; externally; on the exterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminated', 'imp. & p. p.', 'of Exterminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminating', 'p. pr. & vb. n.', 'of Exterminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminate', 'v. t.', 'To drive out or away; to expel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminate', 'v. t.', 'To destroy utterly; to cut off; to extirpate; to\n   annihilate; to root out; as, to exterminate a colony, a tribe, or a\n   nation; to exterminate error or vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminate', 'v. t.', 'To eliminate, as unknown quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extermination', 'n.', 'The act of exterminating; total destruction;\n   eradication; excision; as, the extermination of inhabitants or tribes,\n   of error or vice, or of weeds from a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extermination', 'n.', 'Elimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminator', 'n.', 'One who, or that which, exterminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterminatory', 'a.', 'Of or pertaining to extermination; tending to\n   exterminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extermine', 'v. t.', 'To exterminate; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extern', 'a.', 'External; outward; not inherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extern', 'n.', 'A pupil in a seminary who lives without its walls; a day\n   scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extern', 'n.', 'Outward form or part; exterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Outward; exterior; relating to the outside, as of a body;\n   being without; acting from without; -- opposed to internal; as, the\n   external form or surface of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Outside of or separate from ourselves; (Metaph.) separate\n   from the perceiving mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Outwardly perceptible; visible; physical or corporeal, as\n   distinguished from mental or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Not intrinsic nor essential; accidental; accompanying;\n   superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Foreign; relating to or connected with foreign nations;\n   as, external trade or commerce; the external relations of a state or\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'a.', 'Away from the mesial plane of the body; lateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'External', 'n.', 'Something external or without; outward part; that which\n   makes a show, rather than that which is intrinsic; visible form; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externalism', 'n.', 'The quality of being manifest to the senses; external\n   acts or appearances; regard for externals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externalism', 'n.', 'That philosophy or doctrine which recognizes or deals\n   only with externals, or objects of sense perception; positivism;\n   phenomenalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externalistic', 'a.', 'Pertaining to externalism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externality', 'n.', 'State of being external; exteriority');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externality', 'n.', 'separation from the perceiving mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externalize', 'v. t.', 'To make external; to manifest by outward form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externally', 'adv.', 'In an external manner; outwardly; on the outside; in\n   appearance; visibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Externe', 'n.', 'An officer in attendance upon a hospital, but not residing\n   in it; esp., one who cares for the out-patients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterraneous', 'a.', 'Foreign; belonging to, or coming from, abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterritorial', 'a.', 'Beyond the territorial limits; foreign to, or exempt\n   from, the territorial jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterritoriality', 'n.', 'The state of being beyond the limits of a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exterritoriality', 'n.', 'The state of being free from the jurisdiction of\n   a country when within its territorial limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extersion', 'n.', 'The act of wiping or rubbing out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extilled', 'imp. & p. p.', 'of Extill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extilling', 'p. pr. & vb. n.', 'of Extill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extill', 'v. i.', 'To drop or distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extillation', 'n.', 'Distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extimulate', 'v. t.', 'To stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extimulation', 'n.', 'Stimulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinct', 'a.', 'Extinguished; put out; quenched; as, a fire, a light, or a\n   lamp, is extinct; an extinct volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinct', 'a.', 'Without a survivor; without force; dead; as, a family\n   becomes extinct; an extinct feud or law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinct', 'v. t.', 'To cause to be extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinction', 'n.', 'The act of extinguishing or making extinct; a putting\n   an end to; the act of putting out or destroying light, fire, life,\n   activity, influence, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinction', 'n.', 'State of being extinguished or of ceasing to be;\n   destruction; suppression; as, the extinction of life, of a family, of a\n   quarrel, of claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extine', 'n.', 'The outer membrane of the grains of pollen of flowering\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguished', 'imp. & p. p.', 'of Extinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguishing', 'p pr. & vb. n.', 'of Extinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguish', 'v. t.', 'To quench; to put out, as a light or fire; to\n   stifle; to cause to die out; to put an end to; to destroy; as, to\n   extinguish a flame, or life, or love, or hope, a pretense or a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguish', 'v. t.', 'To obscure; to eclipse, as by superior splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguishable', 'a.', 'Capable of being quenched, destroyed, or\n   suppressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguisher', 'n.', 'One who, or that which, extinguishes; esp., a hollow\n   cone or other device for extinguishing a flame, as of a torch or\n   candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguishment', 'n.', 'The act of extinguishing, putting out, or\n   quenching, or the state of being extinguished; extinction; suppression;\n   destruction; nullification; as, the extinguishment of fire or flame, of\n   discord, enmity, or jealousy, or of love or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extinguishment', 'n.', 'The annihilation or extinction of a right or\n   obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirp', 'v. t.', 'To extirpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpable', 'a.', 'Capable of being extirpated or eradicated; as, an\n   extirpable plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpated', 'imp. & p. p.', 'of Extirpate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpating', 'p. pr. & vb. n.', 'of Extirpate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpate', 'v. t.', 'To pluck up by the stem or root; to root out; to\n   eradicate, literally or figuratively; to destroy wholly; as, to\n   extirpate weeds; to extirpate a tumor; to extirpate a sect; to\n   extirpate error or heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpation', 'n.', 'The act of extirpating or rooting out, or the state of\n   being extirpated; eradication; excision; total destruction; as, the\n   extirpation of weeds from land, of evil from the heart, of a race of\n   men, of heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpative', 'a.', 'Capable of rooting out, or tending to root out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpator', 'n.', 'One who extirpates or roots out; a destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirpatory', 'a.', 'Extirpative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extirper', 'n.', 'Extirpator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extispicious', 'a.', 'Relating to the inspection of entrails for\n   prognostication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extogenous', 'a.', 'Exogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extolled', 'imp. & p. p.', 'of Extol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extolling', 'p. pr. & vb. n.', 'of Extol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extol', 'v. t.', 'To place on high; to lift up; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extol', 'v. t.', 'To elevate by praise; to eulogize; to praise; to magnify;\n   as, to extol virtue; to extol an act or a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extoller', 'n.', 'One who extols; one who praises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extolment', 'n.', 'Praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extorsive', 'a.', 'Serving or tending to extort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extorted', 'imp. & p. p.', 'of Extort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extorting', 'p. pr. & vb. n.', 'of Extort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extort', 'v. t.', 'To wrest from an unwilling person by physical force,\n   menace, duress, torture, or any undue or illegal exercise of power or\n   ingenuity; to wrench away (from); to tear away; to wring (from); to\n   exact; as, to extort contributions from the vanquished; to extort\n   confessions of guilt; to extort a promise; to extort payment of a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extort', 'v. t.', 'To get by the offense of extortion. See Extortion, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extort', 'v. i.', 'To practice extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extort', 'p. p. & a.', 'Extorted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extorter', 'n.', 'One who practices extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortion', 'n.', 'The act of extorting; the act or practice of wresting\n   anything from a person by force, by threats, or by any undue exercise\n   of power; undue exaction; overcharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortion', 'n.', 'The offense committed by an officer who corruptly claims\n   and takes, as his fee, money, or other thing of value, that is not due,\n   or more than is due, or before it is due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortion', 'n.', 'That which is extorted or exacted by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortionary', 'a.', 'Extortionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortionate', 'a.', 'Characterized by extortion; oppressive; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortioner', 'n.', 'One who practices extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extortious', 'a.', 'Extortionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra-', '', 'A Latin preposition, denoting beyond, outside of; -- often\n   used in composition as a prefix signifying outside of, beyond, besides,\n   or in addition to what is denoted by the word to which it is prefixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra', 'a.', 'Beyond what is due, usual, expected, or necessary;\n   additional; supernumerary; also, extraordinarily good; superior; as,\n   extra work; extra pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extras', 'pl. ', 'of Extra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra', 'n.', 'Something in addition to what is due, expected, or\n   customary; something in addition to the regular charge or compensation,\n   or for which an additional charge is made; as, at European hotels\n   lights are extras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraarticular', 'a.', 'Situated outside of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraaxillar', 'a.', 'Alt. of Extraaxillary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraaxillary', 'a.', 'Growing outside of the axils; as, an extra-axillary\n   bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrabranchial', 'a.', 'Outside of the branchial arches; -- said of the\n   cartilages thus placed in some fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extracapsular', 'a.', 'Situated outside of a capsule, esp. outside the\n   capsular ligament of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extracted', 'imp. & p. p.', 'of Extract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extracting', 'p. pr. & vb. n.', 'of Extract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'v. t.', 'To draw out or forth; to pull out; to remove forcibly\n   from a fixed position, as by traction or suction, etc.; as, to extract\n   a tooth from its socket, a stump from the earth, a splinter from the\n   finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'v. t.', 'To withdraw by expression, distillation, or other\n   mechanical or chemical process; as, to extract an essence. Cf.\n   Abstract, v. t., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'v. t.', 'To take by selection; to choose out; to cite or quote,\n   as a passage from a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'That which is extracted or drawn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'A portion of a book or document, separately transcribed; a\n   citation; a quotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'A decoction, solution, or infusion made by drawing out\n   from any substance that which gives it its essential and characteristic\n   virtue; essence; as, extract of beef; extract of dandelion; also, any\n   substance so extracted, and characteristic of that from which it is\n   obtained; as, quinine is the most important extract of Peruvian bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'A solid preparation obtained by evaporating a solution of\n   a drug, etc., or the fresh juice of a plant; -- distinguished from an\n   abstract. See Abstract, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'A peculiar principle once erroneously supposed to form the\n   basis of all vegetable extracts; -- called also the extractive\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'Extraction; descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extract', 'n.', 'A draught or copy of writing; certified copy of the\n   proceedings in an action and the judgement therein, with an order for\n   execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractable', 'a.', 'Alt. of Extractible');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractible', 'a.', 'Capable of being extracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractiform', 'a.', 'Having the form, appearance, or nature, of an\n   extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraction', 'n.', 'The act of extracting, or drawing out; as, the\n   extraction of a tooth, of a bone or an arrow from the body, of a stump\n   from earth, of a passage from a book, of an essence or tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraction', 'n.', 'Derivation from a stock or family; lineage; descent;\n   birth; the stock from which one has descended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraction', 'n.', 'That which is extracted; extract; essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractive', 'a.', 'Capable of being extracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractive', 'a.', 'Tending or serving to extract or draw out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractive', 'n.', 'Anything extracted; an extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractive', 'n.', 'A chemical principle once supposed to exist in all\n   extracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractive', 'n.', 'Any one of a large class of substances obtained by\n   extraction, and consisting largely of nitrogenous hydrocarbons, such as\n   xanthin, hypoxanthin, and creatin extractives from muscle tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractor', 'n.', 'One who, or that which, extracts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractor', 'n.', 'A forceps or instrument for extracting substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extractor', 'n.', 'A device for withdrawing a cartridge or spent cartridge\n   shell from the chamber of the barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extradictionary', 'a.', 'Consisting not in words, but in realities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraditable', 'a.', 'Subject, or liable, to extradition, as a fugitive\n   from justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraditable', 'a.', 'Making liable to extradition; as, extraditable\n   offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extradited', 'imp. & p. p.', 'of Extradite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraditing', 'p. pr. & vb. n.', 'of Extradite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extradite', 'v. t.', 'To deliver up by one government to another, as a\n   fugitive from justice. See Extradition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extradition', 'n.', 'The surrender or delivery of an alleged criminal by\n   one State or sovereignty to another having jurisdiction to try charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrados', 'n.', 'The exterior curve of an arch; esp., the upper curved\n   face of the whole body of voussoirs. See Intrados.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extradotal', 'a.', 'Forming no part of the dowry; as, extradotal property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrafoliaceous', 'a.', 'Away from the leaves, or inserted in a different\n   place from them; as, extrafoliaceous prickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraforaneous', 'a.', 'Pertaining to that which is out of doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrageneous', 'a.', 'Belonging to another race or kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrajudicial', 'a.', 'Out of or beyond the proper authority of a court or\n   judge; beyond jurisdiction; not legally required.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extralimitary', 'a.', 'Being beyond the limit or bounds; as, extraliminary\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extralogical', 'a.', 'Lying outside of the domain of logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extramission', 'n.', 'A sending out; emission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extramundane', 'a.', 'Beyond the material world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extramural', 'a.', 'Outside of the walls, as of a fortified or walled city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraneity', 'n.', 'State of being without or beyond a thing; foreignness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraneous', 'a.', 'Not belonging to, or dependent upon, a thing; without\n   or beyond a thing; not essential or intrinsic; foreign; as, to separate\n   gold from extraneous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra-ocular', 'a.', 'Inserted exterior to the eyes; -- said of the\n   antennae of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra-official', 'a.', 'Not prescribed by official duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinarily', 'adv.', 'In an extraordinary manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinariness', 'n.', 'The quality of being extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinary', 'a.', 'Beyond or out of the common order or method; not\n   usual, customary, regular, or ordinary; as, extraordinary evils;\n   extraordinary remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinary', 'a.', 'Exceeding the common degree, measure. or condition;\n   hence, remarkable; uncommon; rare; wonderful; as, extraordinary talents\n   or grandeur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinary', 'a.', 'Employed or sent upon an unusual or special service;\n   as, an ambassador extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinaries', 'pl. ', 'of Extraordinary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraordinary', 'n.', 'That which is extraordinary; -- used especially in\n   the plural; as, extraordinaries excepted, there is nothing to prevent\n   success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraparochial', 'a.', 'Beyond the limits of a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraphysical', 'a.', 'Not subject to physical laws or methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraprofessional', 'a.', 'Foreign to a profession; not within the ordinary\n   limits of professional duty or business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraprovincial', 'a.', 'Not within of pertaining to the same province or\n   jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraregular', 'a.', 'Not comprehended within a rule or rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrastapedial', 'a.', 'Pertaining to a part of the columella of the ear,\n   which, in many animals, projects beyond the connection with the stapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrastapedial', 'n.', 'The extrastapedial part of columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraterritorial', 'a.', 'Beyond the limits of a territory or particular\n   jurisdiction; exterritorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraterritoriality', 'n.', 'The state of being beyond the limits of a\n   particular territory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraterritoriality', 'n.', 'A fiction by which a public minister, though\n   actually in a foreign country, is supposed still to remain within the\n   territory of his own sovereign or nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extratropical', 'a.', 'Beyond or outside of the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraught', 'p. p.', 'Extracted; descended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extra-uterine', 'a.', 'Outside of the uterus, or womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagance', 'n.', 'A wandering beyond proper limits; an excursion or\n   sally from the usual way, course, or limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagance', 'n.', 'The state of being extravagant, wild, or prodigal\n   beyond bounds of propriety or duty; want of moderation; excess;\n   especially, undue expenditure of money; vaid and superfluous expense;\n   prodigality; as, extravagance of anger, love, expression, imagination,\n   demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagancies', 'pl. ', 'of Extravagancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagancy', 'n.', 'Extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagant', 'a.', 'Wandering beyond ones bounds; roving; hence, foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagant', 'a.', 'Exceeding due bounds; wild; excessive; unrestrained;\n   as, extravagant acts, wishes, praise, abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagant', 'a.', 'Profuse in expenditure; prodigal; wasteful; as, an\n   extravagant man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagant', 'n.', 'One who is confined to no general rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagant', 'n.', 'Certain constitutions or decretal epistles, not at\n   first included with others, but subsequently made a part of the canon\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagantly', 'adv.', 'In an extravagant manner; wildly; excessively;\n   profusely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagantness', 'n.', 'The state of being extravagant or in excess;\n   excess; extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravaganza', 'n.', 'A composition, as in music, or in the drama, designed\n   to produce effect by its wild irregularity; esp., a musical caricature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravaganza', 'n.', 'An extravagant flight of sentiment or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagate', 'v. i.', 'To rove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravagation', 'n.', 'A wandering beyond limits; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravasated', 'imp. & p. p.', 'of Extravasate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravasating', 'p. pr. & vb. n.', 'of Extravasate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravasate', 'v. t.', 'To force or let out of the proper vessels or\n   arteries, as blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravasation', 'n.', 'The act of forcing or letting out of its proper\n   vessels or ducts, as a fluid; effusion; as, an extravasation of blood\n   after a rupture of the vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravascular', 'a.', 'Outside the vessels; -- said of the substance of all\n   the tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravascular', 'a.', 'Destitute of vessels; non-vascular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extravenate', 'a.', 'Let out of the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extraversion', 'n.', 'The act of throwing out; the state of being turned or\n   thrown out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreat', 'n.', 'Extraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'a.', 'At the utmost point, edge, or border; outermost; utmost;\n   farthest; most remote; at the widest limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'a.', 'Last; final; conclusive; -- said of time; as, the extreme\n   hour of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'a.', 'The best of worst; most urgent; greatest; highest;\n   immoderate; excessive; most violent; as, an extreme case; extreme\n   folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'a.', 'Radical; ultra; as, extreme opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'a.', 'Extended or contracted as much as possible; -- said of\n   intervals; as, an extreme sharp second; an extreme flat forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'n.', 'The utmost point or verge; that part which terminates a\n   body; extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'n.', 'Utmost limit or degree that is supposable or tolerable;\n   hence, furthest degree; any undue departure from the mean; -- often in\n   the plural: things at an extreme distance from each other, the most\n   widely different states, etc.; as, extremes of heat and cold, of virtue\n   and vice; extremes meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'n.', 'An extreme state or condition; hence, calamity, danger,\n   distress, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'n.', 'Either of the extreme terms of a syllogism, the middle\n   term being interposed between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extreme', 'n.', 'The first or the last term of a proportion or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremeless', 'a.', 'Having no extremes; infinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremely', 'adv.', 'In an extreme manner or state; in the utmost degree;\n   to the utmost point; exceedingly; as, extremely hot or cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremist', 'n.', 'A supporter of extreme doctrines or practice; one who\n   holds extreme opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremities', 'pl. ', 'of Extremity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremity', 'n.', 'The extreme part; the utmost limit; the farthest or\n   remotest point or part; as, the extremities of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremity', 'n.', 'One of locomotive appendages of an animal; a limb; a leg\n   or an arm of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremity', 'n.', 'The utmost point; highest degree; most aggravated or\n   intense form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extremity', 'n.', 'The highest degree of inconvenience, pain, or suffering;\n   greatest need or peril; extreme need; necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extricable', 'a.', 'Capable of being extricated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extricated', 'imp. & p. p.', 'of Extricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extricating', 'p. pr. & vb. n.', 'of Extricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extricate', 'v. t.', 'To free, as from difficulties or perplexities; to\n   disentangle; to disembarrass; as, to extricate a person from debt,\n   peril, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extricate', 'v. t.', 'To cause to be emitted or evolved; as, to extricate\n   heat or moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrication', 'n.', 'The act or process of extricating or disentangling; a\n   freeing from perplexities; disentanglement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrication', 'n.', 'The act of sending out or evolving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrinsic', 'a.', 'Not contained in or belonging to a body; external;\n   outward; unessential; -- opposed to intrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrinsic', 'a.', 'Attached partly to an organ or limb and partly to some\n   other part/ -- said of certain groups of muscles. Opposed to intrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrinsical', 'a.', 'Extrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrinsicality', 'n.', 'Alt. of Extrinsicalness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrinsicalness', 'n.', 'The state or quality of being extrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extroitive', 'a.', 'Seeking or going out after external objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrorsal', 'a.', 'Extrorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrorse', 'a.', 'Facing outwards, or away from the axis of growth; -- said\n   esp. of anthers occupying the outer side of the filament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extroversion', 'n.', 'The condition of being turned wrong side out; as,\n   extroversion of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extruct', 'v. t.', 'To construct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extruction', 'n.', 'A building up; construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extructive', 'a.', 'Constructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extructor', 'n.', 'A builder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extruded', 'imp. & p. p.', 'of Extrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extruding', 'p. pr. & vb. n.', 'of Extrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrude', 'v. t.', 'To thrust out; to force, press, or push out; to expel;\n   to drive off or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extrusion', 'n.', 'The act of thrusting or pushing out; a driving out;\n   expulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extuberance', 'n.', 'A swelling or rising; protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extuberancy', 'n.', 'Extuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extuberant', 'a.', 'Swollen out; protuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extuberate', 'v. i.', 'To swell out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extuberation', 'n.', 'Protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Extumescence', 'n.', 'A swelling or rising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuberance', 'n.', 'The state of being exuberant; an overflowing quantity;\n   a copious or excessive production or supply; superabundance; richness;\n   as, an exuberance of joy, of fancy, or of foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuberancy', 'n.', 'Exuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuberant', 'a.', 'Characterized by abundance or superabundance; plenteous;\n   rich; overflowing; copious or excessive in production; as, exuberant\n   goodness; an exuberant intellect; exuberant foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuberate', 'n.', 'To abound; to be in great abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuccous', 'a.', 'See Exsuccous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exudate', 'v. t. & i.', 'To exude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exudation', 'n.', 'The act of exuding; sweating; a discharge of humors,\n   moisture, juice, or gum, as through pores or incisions; also, the\n   substance exuded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuded', 'imp. & p. p.', 'of Exude');
  INSERT INTO words (word, word_type, description) VALUES  (    'exuding', 'p. pr. & vb. n.', 'of Exude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exude', 'v. t.', 'To discharge through pores or incisions, as moisture or\n   other liquid matter; to give out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exude', 'v. i.', 'To flow from a body through the pores, or by a natural\n   discharge, as juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulcerate', 'v. t. & i.', 'To ulcerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulcerate', 'v. t. & i.', 'To corrode; to fret; to chafe; to inflame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulcerate', 'a.', 'Very sore; ulcerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulceration', 'n.', 'Ulceration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulceration', 'n.', 'A fretting; a festering; soreness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulcerative', 'a.', 'Tending to cause ulcers; exulceratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulceratory', 'a.', 'Having a tendency to form ulcers; rendering ulcerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulted', 'imp. & p. p.', 'of Exult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulting', 'p. pr. & vb. n.', 'of Exult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exult', 'v. i.', 'To be in high spirits; figuratively, to leap for joy; to\n   rejoice in triumph or exceedingly; to triumph; as, an exulting heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exultance', 'n.', 'Alt. of Exultancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exultancy', 'n.', 'Exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exultant', 'a.', 'Inclined to exult; characterized by, or expressing,\n   exultation; rejoicing triumphantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exultation', 'n.', 'The act of exulting; lively joy at success or victory,\n   or at any advantage gained; rapturous delight; triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exulting', 'a.', 'Rejoicing triumphantly or exceedingly; exultant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exundate', 'v. i.', 'To overflow; to inundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exundation', 'n.', 'An overflow, or overflowing abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exungulated', 'imp. & p. p.', 'of Exungulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exungulating', 'p. pr. & vb. n.', 'of Exungulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exungulate', 'v. t.', 'To pare off, as nails, the hoof, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuperable', 'a.', 'Surmountable; superable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuperance', 'n.', 'Superiority; superfluity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuperant', 'a.', 'Surpassing; exceeding; surmounting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuperate', 'v. t.', 'To excel; to surmount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuperation', 'n.', 'The act of rising or coming into view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exurgent', 'a.', 'Arising; coming to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuscitate', 'v. t.', 'See Exsuscitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exustion', 'n.', 'The act or operation of burning up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exutory', 'n.', 'An issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuvia', '', 'n. sing. of Exuviae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviability', 'n.', 'Capability of shedding the skin periodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviable', 'a.', 'Capable of being cast off in the form of exuviae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviae', 'n. pl.', 'Cast skins, shells, or coverings of animals; any parts\n   of animals which are shed or cast off, as the skins of snakes, the\n   shells of lobsters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviae', 'n. pl.', 'The fossil shells and other remains which animals have\n   left in the strata of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuvial', 'a.', 'Of or pertaining to exuviae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviated', 'imp. & p. p.', 'of Exuviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviating', 'p. pr. & vb. n.', 'of Exuviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviate', 'v. i.', 'To shed an old covering or condition preliminary to\n   taking on a new one; to molt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Exuviation', 'n.', 'The rejecting or casting off of some part, more\n   particularly, the outer cuticular layer, as the shells of crustaceans,\n   skins of snakes, etc.; molting; ecdysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex-votos', 'pl. ', 'of Ex-voto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ex-voto', 'n.', 'An offering to a church in fulfillment of a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ey', 'n.', 'An island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyren', 'pl. ', 'of Ey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ey', 'n.', 'See Egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ey', '', 'An interj. of wonder or inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyalet', 'n.', 'Formerly, one of the administrative divisions or provinces\n   of the Ottoman Empire; -- now called a vilayet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyas', 'n.', 'A nesting or unfledged bird; in falconry, a young hawk from\n   the nest, not able to prey for itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyas', 'a.', 'Unfledged, or newly fledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyasmusket', 'n.', 'An unfledged or young male sparrow hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'A brood; as, an eye of pheasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The organ of sight or vision. In man, and the vertebrates\n   generally, it is properly the movable ball or globe in the orbit, but\n   the term often includes the adjacent parts. In most invertebrates the\n   years are immovable ocelli, or compound eyes made up of numerous\n   ocelli. See Ocellus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The faculty of seeing; power or range of vision; hence,\n   judgment or taste in the use of the eye, and in judging of objects; as,\n   to have the eye of sailor; an eye for the beautiful or picturesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The action of the organ of sight; sight, look; view; ocular\n   knowledge; judgment; opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The space commanded by the organ of sight; scope of vision;\n   hence, face; front; the presence of an object which is directly opposed\n   or confronted; immediate presence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'Observation; oversight; watch; inspection; notice; attention;\n   regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'That which resembles the organ of sight, in form, position, or\n   appearance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The spots on a feather, as of peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The scar to which the adductor muscle is attached in oysters\n   and other bivalve shells; also, the adductor muscle itself, esp. when\n   used as food, as in the scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The bud or sprout of a plant or tuber; as the eye of a potato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The center of a target; the bulls-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'A small loop to receive a hook; as hooks and eyes on a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The hole through the head of a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'A loop forming part of anything, or a hole through anything,\n   to receive a rope, hook, pin, shaft, etc.; as an eye at the end of a\n   tie bar in a bridge truss; as an eye through a crank; an eye at the end\n   of rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'The hole through the upper millstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'That which resembles the eye in relative importance or beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'n.', 'Tinge; shade of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyed', 'imp. & p. p.', 'of Eye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eying / Eyeing', 'p. pr. & vb. n.', 'of Eye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'v. t.', 'To fix the eye on; to look on; to view; to observe;\n   particularly, to observe or watch narrowly, or with fixed attention; to\n   hold in view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye', 'v. i.', 'To appear; to look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeball', 'n.', 'The ball or globe of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyebar', 'n.', 'A bar with an eye at one or both ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyebeam', 'n.', 'A glance of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyebolt', 'n.', 'A bolt which a looped head, or an opening in the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyebright', 'n.', 'A small annual plant (Euphrasia officinalis), formerly\n   much used as a remedy for diseases of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyebrow', 'n.', 'The brow or hairy arch above the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyecup', 'n.', 'A small oval porcelain or glass cup, having a rim curved to\n   fit the orbit of the eye. it is used in the application of liquid\n   remedies to eyes; -- called also eyeglass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyed', 'a.', 'Heaving (such or so many) eyes; -- used in composition; as\n   sharp-eyed; dull-eyed; sad-eyed; ox-eyed Juno; myriad-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyedrop', 'n.', 'A tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeflap', 'n.', 'A blinder on a horses bridle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeful', 'a.', 'Filling or satisfying the eye; visible; remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeglance', 'n.', 'A glance of eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeglass', 'n.', 'A lens of glass to assist the sight. Eyeglasses are used\n   singly or in pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeglass', 'n.', 'Eyepiece of a telescope, microscope, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeglass', 'n.', 'The retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeglass', 'n.', 'A glass eyecup. See Eyecup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyehole', 'n.', 'A circular opening to recive a hook, cord, ring, or rope;\n   an eyelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyelash', 'n.', 'The fringe of hair that edges the eyelid; -- usually in\n   the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyelash', 'n.', 'A hair of the fringe on the edge of the eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeless', 'a.', 'Without eyes; blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyelet', 'n.', 'A small hole or perforation to receive a cord or fastener,\n   as in garments, sails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyelet', 'n.', 'A metal ring or grommet, or short metallic tube, the ends\n   of which can be bent outward and over to fasten it in place; -- used to\n   line an eyelet hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeleteer', 'n.', 'A small, sharp-pointed instrument used in piercing\n   eyelet holes; a stiletto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyelid', 'n.', 'The cover of the eye; that portion of movable skin with\n   which an animal covers or uncovers the eyeball at pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyen', 'n. pl.', 'Eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyepiece', 'n.', 'The lens, or combination of lenses, at the eye end of a\n   telescope or other optical instrument, through which the image formed\n   by the mirror or object glass is viewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyer', 'n.', 'One who eyes another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyereach', 'n.', 'The range or reach of the eye; eyeshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye-saint', 'n.', 'An object of interest to the eye; one worshiped with the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyesalve', 'n.', 'Ointment for the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeservant', 'n.', 'A servant who attends faithfully to his duty only when\n   watched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeservice', 'n.', 'Service performed only under inspection, or the eye of\n   an employer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeshot', 'n.', 'Range, reach, or glance of the eye; view; sight; as, to be\n   out of eyeshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyesight', 'n.', 'Sight of the eye; the sense of seeing; view; observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyesore', 'n.', 'Something offensive to the eye or sight; a blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye-splice', 'n.', 'A splice formed by bending a ropes end back, and\n   fastening it into the rope, forming a loop or eye. See Illust. under\n   Splice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye-spot', 'n.', 'A simple visual organ found in many invertebrates,\n   consisting of pigment cells covering a sensory nerve termination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye-spot', 'n.', 'An eyelike spot of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eye-spotted', 'a.', 'Marked with spots like eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyestalk', 'n.', 'One of the movable peduncles which, in the decapod\n   Crustacea, bear the eyes at the tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyestone', 'n.', 'A small, lenticular, calcareous body, esp. an operculum\n   of a small marine shell of the family Turbinidae, used to remove a\n   foreign substance from the eye. It is put into the inner corner of the\n   eye under the lid, and allowed to work its way out at the outer corner,\n   bringing with it the substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyestone', 'n.', 'Eye agate. See under Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyestring', 'n.', 'The tendon by which the eye is moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyet', 'n.', 'An island. See Eyot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyeteeth', 'pl. ', 'of Eyetooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyetooth', 'n.', 'A canine tooth of the upper jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyewash', 'n.', 'See Eyewater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyewater', 'n.', 'A wash or lotion for application to the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyewink', 'n.', 'A wink; a token.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyewinker', 'n.', 'An eyelash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyewitness', 'n.', 'One who sees a thing done; one who has ocular view of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyghen', 'n. pl.', 'Eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyght', 'n.', 'An island. See Eyot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyle', 'v. t. & i.', 'To ail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyliad', 'n.', 'See /iliad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyne', 'n.', 'Alt. of Eyen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyen', 'n.', 'Plural of eye; -- now obsolete, or used only in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyot', 'n.', 'A little island in a river or lake. See Ait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyr', 'n.', 'Air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyra', 'n.', 'A wild cat (Felis eyra) ranging from southern Brazil to\n   Texas. It is reddish yellow and about the size of the domestic cat, but\n   with a more slender body and shorter legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyre', 'n.', 'A journey in circuit of certain judges called justices in\n   eyre (or in itinere).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyren', 'n. pl.', 'See Ey, an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ey\"ries', 'pl. ', 'of Eyry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyrie', 'n.', 'Alt. of Eyry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eyry', 'n.', 'The nest of a bird of prey or other large bird that builds in\n   a lofty place; aerie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Eysell', 'n.', 'Same as Eisel.\n   F () F is the sixth letter of the English alphabet, and a nonvocal\n   consonant. Its form and sound are from the Latin. The Latin borrowed\n   the form from the Greek digamma /, which probably had the value of\n   English w consonant. The form and value of Greek letter came from the\n   Phoenician, the ultimate source being probably Egyptian. Etymologically\n   f is most closely related to p, k, v, and b; as in E. five, Gr. pe`nte;\n   E. wolf, L. lupus, Gr. ly`kos; E. fox, vixen ; fragile, break; fruit,\n   brook, v. t.; E. bear, L. ferre. See Guide to Pronunciation, // 178,\n   179, 188, 198, 230.');
  INSERT INTO words (word, word_type, description) VALUES  (    'F', 'v. t.', 'The name of the fourth tone of the model scale, or scale of\n   C. F sharp (F /) is a tone intermediate between F and G.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fa', 'n.', 'A syllable applied to the fourth tone of the diatonic scale in\n   solmization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fa', 'n.', 'The tone F.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabaceous', 'a.', 'Having the nature of a bean; like a bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabellae', 'pl. ', 'of Fabella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabella', 'n.', 'One of the small sesamoid bones situated behind the\n   condyles of the femur, in some mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabian', 'a.', 'Of, pertaining to, or in the manner of, the Roman general,\n   Quintus Fabius Maximus Verrucosus; cautious; dilatory; avoiding a\n   decisive contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'n.', 'A Feigned story or tale, intended to instruct or amuse; a\n   fictitious narration intended to enforce some useful truth or precept;\n   an apologue. See the Note under Apologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'n.', 'The plot, story, or connected series of events, forming the\n   subject of an epic or dramatic poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'n.', 'Any story told to excite wonder; common talk; the theme of\n   talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'n.', 'Fiction; untruth; falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabled', 'imp. & p. p.', 'of Fable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabling', 'p. pr. & vb. n.', 'of Fable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'v. i.', 'To compose fables; hence, to write or speak fiction ; to\n   write or utter what is not true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fable', 'v. t.', 'To feign; to invent; to devise, and speak of, as true or\n   real; to tell of falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabler', 'n.', 'A writer of fables; a fabulist; a dealer in untruths or\n   falsehoods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabliaux', 'pl. ', 'of Fabliau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabliau', 'n.', 'One of the metrical tales of the Trouveres, or early poets\n   of the north of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'The structure of anything; the manner in which the parts of\n   a thing are united; workmanship; texture; make; as cloth of a beautiful\n   fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'That which is fabricated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'Framework; structure; edifice; building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'Cloth of any kind that is woven or knit from fibers, either\n   vegetable or animal; manufactured cloth; as, silks or other fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'The act of constructing; construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'n.', 'Any system or structure consisting of connected parts; as,\n   the fabric of the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricked', 'imp. & p. p.', 'of Fabric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricking', 'p. pr. & vb. n.', 'of Fabric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabric', 'v. t.', 'To frame; to build; to construct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricant', 'n.', 'One who fabricates; a manufacturer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricated', 'imp. & p. p.', 'of Fabricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricating', 'p. pr. & vb. n.', 'of Fabricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricate', 'v. t.', 'To form into a whole by uniting its parts; to frame;\n   to construct; to build; as, to fabricate a bridge or ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricate', 'v. t.', 'To form by art and labor; to manufacture; to produce;\n   as, to fabricate woolens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricate', 'v. t.', 'To invent and form; to forge; to devise falsely; as,\n   to fabricate a lie or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabrication', 'n.', 'The act of fabricating, framing, or constructing;\n   construction; manufacture; as, the fabrication of a bridge, a church,\n   or a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabrication', 'n.', 'That which is fabricated; a falsehood; as, the story\n   is doubtless a fabrication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricator', 'n.', 'One who fabricates; one who constructs or makes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabricatress', 'n.', 'A woman who fabricates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabrile', 'a.', 'Pertaining to a workman, or to work in stone, metal, wood\n   etc.; as, fabrile skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulist', 'n.', 'One who invents or writes fables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulized', 'imp. & p. p.', 'of Fabulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulizing', 'p. pr. & vb. n.', 'of Fabulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulize', 'v. i.', 'To invent, compose, or relate fables or fictions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulosity', 'n.', 'Fabulousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulosity', 'n.', 'A fabulous or fictitious story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulous', 'a.', 'Feigned, as a story or fable; related in fable; devised;\n   invented; not real; fictitious; as, a fabulous description; a fabulous\n   hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fabulous', 'a.', 'Passing belief; exceedingly great; as, a fabulous price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faburden', 'n.', 'A species of counterpoint with a drone bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faburden', 'n.', 'A succession of chords of the sixth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faburden', 'n.', 'A monotonous refrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fac', 'n.', 'A large ornamental letter used, esp. by the early printers, at\n   the commencement of the chapters and other divisions of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facade', 'n.', 'The front of a building; esp., the principal front, having\n   some architectural pretensions. Thus a church is said to have its\n   facade unfinished, though the interior may be in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The exterior form or appearance of anything; that part which\n   presents itself to the view; especially, the front or upper part or\n   surface; that which particularly offers itself to the view of a\n   spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'That part of a body, having several sides, which may be seen\n   from one point, or which is presented toward a certain direction; one\n   of the bounding planes of a solid; as, a cube has six faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The principal dressed surface of a plate, disk, or pulley;\n   the principal flat surface of a part or object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'That part of the acting surface of a cog in a cog wheel,\n   which projects beyond the pitch line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The width of a pulley, or the length of a cog from end to\n   end; as, a pulley or cog wheel of ten inches face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The upper surface, or the character upon the surface, of a\n   type, plate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The style or cut of a type or font of type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Outside appearance; surface show; look; external aspect,\n   whether natural, assumed, or acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'That part of the head, esp. of man, in which the eyes,\n   cheeks, nose, and mouth are situated; visage; countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Cast of features; expression of countenance; look; air;\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Ten degrees in extent of a sign of the zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Maintenance of the countenance free from abashment or\n   confusion; confidence; boldness; shamelessness; effrontery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Presence; sight; front; as in the phrases, before the face\n   of, in the immediate presence of; in the face of, before, in, or\n   against the front of; as, to fly in the face of danger; to the face of,\n   directly to; from the face of, from the presence of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'Mode of regard, whether favorable or unfavorable; favor or\n   anger; mostly in Scriptural phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The end or wall of the tunnel, drift, or excavation, at which\n   work is progressing or was last done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'n.', 'The exact amount expressed on a bill, note, bond, or other\n   mercantile paper, without any addition for interest or reduction for\n   discount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faced', 'imp. & p. p.', 'of Face');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'p. pr. & vb. n.', 'of Face');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To meet in front; to oppose with firmness; to resist, or\n   to meet for the purpose of stopping or opposing; to confront; to\n   encounter; as, to face an enemy in the field of battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To Confront impudently; to bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To stand opposite to; to stand with the face or front\n   toward; to front upon; as, the apartments of the general faced the\n   park.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To cover in front, for ornament, protection, etc.; to put\n   a facing upon; as, a building faced with marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To line near the edge, esp. with a different material; as,\n   to face the front of a coat, or the bottom of a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To cover with better, or better appearing, material than\n   the mass consists of, for purpose of deception, as the surface of a box\n   of tea, a barrel of sugar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To make the surface of (anything) flat or smooth; to dress\n   the face of (a stone, a casting, etc.); esp., in turning, to shape or\n   smooth the flat surface of, as distinguished from the cylindrical\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. t.', 'To cause to turn or present a face or front, as in a\n   particular direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. i.', 'To carry a false appearance; to play the hypocrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. i.', 'To turn the face; as, to face to the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Face', 'v. i.', 'To present a face or front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faced', 'a.', 'Having (such) a face, or (so many) faces; as, smooth-faced,\n   two-faced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faser', 'n.', 'One who faces; one who puts on a false show; a bold-faced\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faser', 'n.', 'A blow in the face, as in boxing; hence, any severe or\n   stunning check or defeat, as in controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facet', 'n.', 'A little face; a small, plane surface; as, the facets of a\n   diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facet', 'n.', 'A smooth circumscribed surface; as, the articular facet of a\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facet', 'n.', 'The narrow plane surface between flutings of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facet', 'n.', 'One of the numerous small eyes which make up the compound\n   eyes of insects and crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faceted', 'imp. & p. p.', 'of Facet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faceting', 'p. pr. & vb. n.', 'of Facet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facet', 'v. t.', 'To cut facets or small faces upon; as, to facet a\n   diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facete', 'a.', 'Facetious; witty; humorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faceted', 'a.', 'Having facets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facetiae', 'n. pl.', 'Witty or humorous writings or saying; witticisms;\n   merry conceits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facetious', 'a.', 'Given to wit and good humor; merry; sportive; jocular;\n   as, a facetious companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facetious', 'a.', 'Characterized by wit and pleasantry; exciting laughter;\n   as, a facetious story or reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facette', 'n.', 'See Facet, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facework', 'n.', 'The material of the outside or front side, as of a wall\n   or building; facing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facia', 'n.', 'See Fascia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facial', 'a.', 'Of or pertaining to the face; as, the facial artery, vein,\n   or nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faciend', 'n.', 'The multiplicand. See Facient, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facient', 'n.', 'One who does anything, good or bad; a doer; an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facient', 'n.', 'One of the variables of a quantic as distinguished from a\n   coefficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facient', 'n.', 'The multiplier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facies', 'n.', 'The anterior part of the head; the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facies', 'n.', 'The general aspect or habit of a species, or group of\n   species, esp. with reference to its adaptation to its environment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facies', 'n.', 'The face of a bird, or the front of the head, excluding the\n   bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facile', 'a.', 'Easy to be done or performed: not difficult; performable or\n   attainable with little labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facile', 'a.', 'Easy to be surmounted or removed; easily conquerable;\n   readily mastered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facile', 'a.', 'Easy of access or converse; mild; courteous; not haughty,\n   austere, or distant; affable; complaisant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facile', 'a.', 'Easily persuaded to good or bad; yielding; ductile to a\n   fault; pliant; flexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facile', 'a.', 'Ready; quick; expert; as, he is facile in expedients; he\n   wields a facile pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facilitated', 'imp. & p. p.', 'of Facilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facilitating', 'p. pr. & vb. n.', 'of Facilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facilitate', 'v. t.', 'To make easy or less difficult; to free from\n   difficulty or impediment; to lessen the labor of; as, to facilitate the\n   execution of a task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facilitation', 'n.', 'The act of facilitating or making easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facilities', 'pl. ', 'of Facility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facility', 'n.', 'The quality of being easily performed; freedom from\n   difficulty; ease; as, the facility of an operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facility', 'n.', 'Ease in performance; readiness proceeding from skill or\n   use; dexterity; as, practice gives a wonderful facility in executing\n   works of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facility', 'n.', 'Easiness to be persuaded; readiness or compliance; --\n   usually in a bad sense; pliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facility', 'n.', 'Easiness of access; complaisance; affability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facility', 'n.', 'That which promotes the ease of any action or course of\n   conduct; advantage; aid; assistance; -- usually in the plural; as,\n   special facilities for study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'A covering in front, for ornament or other purpose; an\n   exterior covering or sheathing; as, the facing of an earthen slope, sea\n   wall, etc. , to strengthen it or to protect or adorn the exposed\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'A lining placed near the edge of a garment for ornament or\n   protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'The finishing of any face of a wall with material different\n   from that of which it is chiefly composed, or the coating or material\n   so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'A powdered substance, as charcoal, bituminous coal, ect.,\n   applied to the face of a mold, or mixed with the sand that forms it, to\n   give a fine smooth surface to the casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'The collar and cuffs of a military coat; -- commonly of a\n   color different from that of the coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facing', 'n.', 'The movement of soldiers by turning on their heels to the\n   right, left, or about; -- chiefly in the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facingly', 'adv.', 'In a facing manner or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facinorous', 'a.', 'Atrociously wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facound', 'n.', 'Speech; eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facsimiles', 'pl. ', 'of Facsimile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facsimile', 'n.', 'A copy of anything made, either so as to be deceptive or\n   so as to give every part and detail of the original; an exact copy or\n   likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facsimile', 'v. t.', 'To make a facsimile of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fact', 'n.', 'A doing, making, or preparing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fact', 'n.', 'An effect produced or achieved; anything done or that comes\n   to pass; an act; an event; a circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fact', 'n.', 'Reality; actuality; truth; as, he, in fact, excelled all the\n   rest; the fact is, he was beaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fact', 'n.', 'The assertion or statement of a thing done or existing;\n   sometimes, even when false, improperly put, by a transfer of meaning,\n   for the thing done, or supposed to be done; a thing supposed or\n   asserted to be done; as, history abounds with false facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faction', 'n.', 'One of the divisions or parties of charioteers\n   (distinguished by their colors) in the games of the circus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faction', 'n.', 'A party, in political society, combined or acting in\n   union, in opposition to the government, or state; -- usually applied to\n   a minority, but it may be applied to a majority; a combination or\n   clique of partisans of any kind, acting for their own interests,\n   especially if greedy, clamorous, and reckless of the common good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faction', 'n.', 'Tumult; discord; dissension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factionary', 'a.', 'Belonging to a faction; being a partisan; taking sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factioner', 'n.', 'One of a faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factionist', 'n.', 'One who promotes faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factious', 'a.', 'Given to faction; addicted to form parties and raise\n   dissensions, in opposition to government or the common good; turbulent;\n   seditious; prone to clamor against public measures or men; -- said of\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factious', 'a.', 'Pertaining to faction; proceeding from faction;\n   indicating, or characterized by, faction; -- said of acts or\n   expressions; as, factious quarrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factitious', 'a.', 'Made by art, in distinction from what is produced by\n   nature; artificial; sham; formed by, or adapted to, an artificial or\n   conventional, in distinction from a natural, standard or rule; not\n   natural; as, factitious cinnabar or jewels; a factitious taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factitive', 'a.', 'Causing; causative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factitive', 'a.', 'Pertaining to that relation which is proper when the\n   act, as of a transitive verb, is not merely received by an object, but\n   produces some change in the object, as when we say, He made the water\n   wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factive', 'a.', 'Making; having power to make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facto', 'adv.', 'In fact; by the act or fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factor', 'n.', 'One who transacts business for another; an agent; a\n   substitute; especially, a mercantile agent who buys and sells goods and\n   transacts business for others in commission; a commission merchant or\n   consignee. He may be a home factor or a foreign factor. He may buy and\n   sell in his own name, and he is intrusted with the possession and\n   control of the goods; and in these respects he differs from a broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factor', 'n.', 'A steward or bailiff of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factor', 'n.', 'One of the elements or quantities which, when multiplied\n   together, from a product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factor', 'n.', 'One of the elements, circumstances, or influences which\n   contribute to produce a result; a constituent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factored', 'imp. & p. p.', 'of Factor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factoring', 'p. pr. & vb. n.', 'of Factor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factor', 'v. t.', 'To resolve (a quantity) into its factors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorage', 'n.', 'The allowance given to a factor, as a compensation for\n   his services; -- called also a commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factoress', 'n.', 'A factor who is a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorial', 'a.', 'Of or pertaining to a factory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorial', 'a.', 'Related to factorials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorial', 'n.', 'A name given to the factors of a continued product when\n   the former are derivable from one and the same function F(x) by\n   successively imparting a constant increment or decrement h to the\n   independent variable. Thus the product F(x).F(x + h).F(x + 2h) . . .\n   F[x + (n-1)h] is called a factorial term, and its several factors take\n   the name of factorials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorial', 'n.', 'The product of the consecutive numbers from unity up to\n   any given number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factoring', 'n.', 'The act of resolving into factors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorized', 'imp. & p. p.', 'of Factorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorizing', 'p. pr. & vb. n.', 'of Factorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorize', 'v. t.', 'To give warning to; -- said of a person in whose\n   hands the effects of another are attached, the warning being to the\n   effect that he shall not pay the money or deliver the property of the\n   defendant in his hands to him, but appear and answer the suit of the\n   plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorize', 'v. t.', 'To attach (the effects of a debtor) in the hands of a\n   third person ; to garnish. See Garnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factorship', 'n.', 'The business of a factor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factories', 'pl. ', 'of Factory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factory', 'n.', 'A house or place where factors, or commercial agents,\n   reside, to transact business for their employers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factory', 'n.', 'The body of factors in any place; as, a chaplain to a\n   British factory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factory', 'n.', 'A building, or collection of buildings, appropriated to\n   the manufacture of goods; the place where workmen are employed in\n   fabricating goods, wares, or utensils; a manufactory; as, a cotton\n   factory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factotums', 'pl. ', 'of Factotum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factotum', 'n.', 'A person employed to do all kinds of work or business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factual', 'a.', 'Relating to, or containing, facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facta', 'pl. ', 'of Factum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factum', 'n.', 'A mans own act and deed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factum', 'n.', 'Anything stated and made certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factum', 'n.', 'The due execution of a will, including everything necessary\n   to its validity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Factum', 'n.', 'The product. See Facient, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facture', 'n.', 'The act or manner of making or doing anything; -- now used\n   of a literary, musical, or pictorial production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facture', 'n.', 'An invoice or bill of parcels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculae', 'n. pl.', 'Groups of small shining spots on the surface of the\n   sun which are brighter than the other parts of the photosphere. They\n   are generally seen in the neighborhood of the dark spots, and are\n   supposed to be elevated portions of the photosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facular', 'a.', 'Of or pertaining to the faculae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculties', 'pl. ', 'of Faculty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'Ability to act or perform, whether inborn or cultivated;\n   capacity for any natural function; especially, an original mental power\n   or capacity for any of the well-known classes of mental activity;\n   psychical or soul capacity; capacity for any of the leading kinds of\n   soul activity, as knowledge, feeling, volition; intellectual endowment\n   or gift; power; as, faculties of the mind or the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'Special mental endowment; characteristic knack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'Power; prerogative or attribute of office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'Privilege or permission, granted by favor or indulgence,\n   to do a particular thing; authority; license; dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'A body of a men to whom any specific right or privilege is\n   granted; formerly, the graduates in any of the four departments of a\n   university or college (Philosophy, Law, Medicine, or Theology), to whom\n   was granted the right of teaching (profitendi or docendi) in the\n   department in which they had studied; at present, the members of a\n   profession itself; as, the medical faculty; the legal faculty, ect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faculty', 'n.', 'The body of person to whom are intrusted the government\n   and instruction of a college or university, or of one of its\n   departments; the president, professors, and tutors in a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facund', 'a.', 'Eloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facundious', 'a.', 'Eloquement; full of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Facundity', 'n.', 'Eloquence; readiness of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fad', 'n.', 'A hobby ; freak; whim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faddle', 'v. i.', 'To trifle; to toy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faddle', 'v. t. ', 'To fondle; to dandle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fade', 'a.', 'Weak; insipid; tasteless; commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faded', 'imp. & p. p.', 'of Fade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fading', 'p. pr. & vb. n.', 'of Fade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fade', 'a.', 'To become fade; to grow weak; to lose strength; to decay; to\n   perish gradually; to wither, as a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fade', 'a.', 'To lose freshness, color, or brightness; to become faint in\n   hue or tint; hence, to be wanting in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fade', 'a.', 'To sink away; to disappear gradually; to grow dim; to vanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fade', 'v. t.', 'To cause to wither; to deprive of freshness or vigor; to\n   wear away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faded', 'a.', 'That has lost freshness, color, or brightness; grown dim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fadedly', 'adv.', 'In a faded manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fadeless', 'a.', 'Not liable to fade; unfading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fader', 'n.', 'Father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fadge', 'a.', 'To fit; to suit; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fadge', 'n.', 'A small flat loaf or thick cake; also, a fagot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fading', 'a.', 'Losing freshness, color, brightness, or vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fading', 'n.', 'Loss of color, freshness, or vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fading', 'n.', 'An Irish dance; also, the burden of a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fadme', 'n.', 'A fathom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fady', 'a.', 'Faded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faecal', 'a.', 'See Fecal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faeces', 'n.pl.', 'Excrement; ordure; also, settlings; sediment after\n   infusion or distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faecula', 'n.', 'See Fecula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faery', 'n. & a.', 'Fairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faffle', 'v. i.', 'To stammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'n.', 'A knot or coarse part in cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagged', 'imp. & p. p.', 'of Fag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagging', 'p. pr. & vb. n.', 'of Fag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'v. i.', 'To become weary; to tire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'v. i.', 'To labor to wearness; to work hard; to drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'v. i.', 'To act as a fag, or perform menial services or drudgery,\n   for another, as in some English schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'v. t.', 'To tire by labor; to exhaust; as, he was almost fagged out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fag', 'v. t.', 'Anything that fatigues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagend', 'n.', 'An end of poorer quality, or in a spoiled condition, as the\n   coarser end of a web of cloth, the untwisted end of a rope, ect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagend', 'n.', 'The refuse or meaner part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagging', 'n.', 'Laborious drudgery; esp., the acting as a drudge for\n   another at an English school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'n.', 'A bundle of sticks, twigs, or small branches of trees, used\n   for fuel, for raising batteries, filling ditches, or other purposes in\n   fortification; a fascine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'n.', 'A bundle of pieces of wrought iron to be worked over into\n   bars or other shapes by rolling or hammering at a welding heat; a pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'n.', 'A bassoon. See Fagotto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'n.', 'A person hired to take the place of another at the muster of\n   a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'n.', 'An old shriveled woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagoted', 'imp. & p. p.', 'of Fagot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagoting', 'p. pr. & vb. n.', 'of Fagot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagot', 'v. t.', 'To make a fagot of; to bind together in a fagot or\n   bundle; also, to collect promiscuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fagotto', 'n.', 'The bassoon; -- so called from being divided into parts\n   for ease of carriage, making, as it were, a small fagot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faham', 'n.', 'The leaves of an orchid (Angraecum fragrans), of the islands\n   of Bourbon and Mauritius, used (in France) as a substitute for Chinese\n   tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahlband', 'n.', 'A stratum in crystalline rock, containing metallic\n   sulphides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahlerz', 'n.', 'Alt. of Fahlband');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahlband', 'n.', 'Same as Tetrahedrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahlunite', 'n.', 'A hydration of iolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahrenheit', 'a.', 'Conforming to the scale used by Gabriel Daniel\n   Fahrenheit in the graduation of his thermometer; of or relating to\n   Fahrenheits thermometric scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fahrenheit', 'n.', 'The Fahrenheit termometer or scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faience', 'n.', 'Glazed earthenware; esp., that which is decorated in\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failed', 'imp. & p. p.', 'of Fail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failing', 'p. pr. & vb. n.', 'of Fail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To be wanting; to fall short; to be or become deficient in\n   any measure or degree up to total absence; to cease to be furnished in\n   the usual or expected manner, or to be altogether cut off from supply;\n   to be lacking; as, streams fail; crops fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To be affected with want; to come short; to lack; to be\n   deficient or unprovided; -- used with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To fall away; to become diminished; to decline; to decay;\n   to sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To deteriorate in respect to vigor, activity, resources,\n   etc.; to become weaker; as, a sick man fails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To perish; to die; -- used of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To be found wanting with respect to an action or a duty to\n   be performed, a result to be secured, etc.; to miss; not to fulfill\n   expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To come short of a result or object aimed at or desired ;\n   to be baffled or frusrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To err in judgment; to be mistaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'To become unable to meet ones engagements; especially, to\n   be unable to pay ones debts or discharge ones business obligation; to\n   become bankrupt or insolvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. t.', 'To be wanting to ; to be insufficient for; to disappoint;\n   to desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. t.', 'To miss of attaining; to lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'Miscarriage; failure; deficiency; fault; -- mostly\n   superseded by failure or failing, except in the phrase without fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fail', 'v. i.', 'Death; decease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failance', 'n.', 'Fault; failure; omission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failing', 'n.', 'A failing short; a becoming deficient; failure;\n   deficiency; imperfection; weakness; lapse; fault; infirmity; as, a\n   mental failing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failing', 'n.', 'The act of becoming insolvent of bankrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faille', 'n.', 'A soft silk, heavier than a foulard and not glossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'Cessation of supply, or total defect; a failing;\n   deficiency; as, failure of rain; failure of crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'Omission; nonperformance; as, the failure to keep a\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'Want of success; the state of having failed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'Decay, or defect from decay; deterioration; as, the\n   failure of memory or of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'A becoming insolvent; bankruptcy; suspension of payment;\n   as, failure in business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Failure', 'n.', 'A failing; a slight fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fain', 'a.', 'Well-pleased; glad; apt; wont; fond; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fain', 'a.', 'Satisfied; contented; also, constrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fain', 'adv.', 'With joy; gladly; -- with wold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fain', 'v. t. & i.', 'To be glad ; to wish or desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faineant', 'a.', 'Doing nothing; shiftless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faineant', 'n.', 'A do-nothing; an idle fellow; a sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'superl.', 'Lacking strength; weak; languid; inclined to swoon; as,\n   faint with fatigue, hunger, or thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'superl.', 'Wanting in courage, spirit, or energy; timorous;\n   cowardly; dejected; depressed; as, \"Faint heart neer won fair lady.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'superl.', 'Lacking distinctness; hardly perceptible; striking the\n   senses feebly; not bright, or loud, or sharp, or forcible; weak; as, a\n   faint color, or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'superl.', 'Performed, done, or acted, in a weak or feeble manner;\n   not exhibiting vigor, strength, or energy; slight; as, faint efforts;\n   faint resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'n.', 'The act of fainting, or the state of one who has fainted; a\n   swoon. [R.] See Fainting, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fainted', 'imp. & p. p.', 'of Faint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fainting', 'p. pr. & vb. n.', 'of Faint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'v. i.', 'To become weak or wanting in vigor; to grow feeble; to\n   lose strength and color, and the control of the bodily or mental\n   functions; to swoon; -- sometimes with away. See Fainting, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'n.', 'To sink into dejection; to lose courage or spirit; to become\n   depressed or despondent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'n.', 'To decay; to disappear; to vanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faint', 'v. t.', 'To cause to faint or become dispirited; to depress; to\n   weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fainthearted', 'a.', 'Wanting in courage; depressed by fear; easily\n   discouraged or frightened; cowardly; timorous; dejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fainting', 'n.', 'Syncope, or loss of consciousness owing to a sudden\n   arrest of the blood supply to the brain, the face becoming pallid, the\n   respiration feeble, and the heats beat weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintish', 'a.', 'Slightly faint; somewhat faint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintling', 'a.', 'Timorous; feeble-minded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintly', 'adv.', 'In a faint, weak, or timidmanner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintness', 'n.', 'The state of being faint; loss of strength, or of\n   consciousness, and self-control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintness', 'n.', 'Want of vigor or energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintness', 'n.', 'Feebleness, as of color or light; lack of distinctness;\n   as, faintness of description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faintness', 'n.', 'Faint-heartedness; timorousness; dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faints', 'n.pl.', 'The impure spirit which comes over first and last in the\n   distillation of whisky; -- the former being called the strong faints,\n   and the latter, which is much more abundant, the weak faints. This\n   crude spirit is much impregnated with fusel oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fainty', 'a.', 'Feeble; languid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Free from spots, specks, dirt, or imperfection;\n   unblemished; clean; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Pleasing to the eye; handsome; beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Without a dark hue; light; clear; as, a fair skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Not overcast; cloudless; clear; pleasant; propitious;\n   favorable; -- said of the sky, weather, or wind, etc.; as, a fair sky;\n   a fair day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Free from obstacles or hindrances; unobstructed;\n   unincumbered; open; direct; -- said of a road, passage, etc.; as, a\n   fair mark; in fair sight; a fair view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Without sudden change of direction or curvature; smooth;\n   fowing; -- said of the figure of a vessel, and of surfaces, water\n   lines, and other lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Characterized by frankness, honesty, impartiality, or\n   candor; open; upright; free from suspicion or bias; equitable; just; --\n   said of persons, character, or conduct; as, a fair man; fair dealing; a\n   fair statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Pleasing; favorable; inspiring hope and confidence; --\n   said of words, promises, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Distinct; legible; as, fair handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'superl.', 'Free from any marked characteristic; average; middling;\n   as, a fair specimen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'adv.', 'Clearly; openly; frankly; civilly; honestly; favorably;\n   auspiciously; agreeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'Fairness, beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'A fair woman; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'Good fortune; good luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'v. t.', 'To make fair or beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'v. t.', 'To make smooth and flowing, as a vessels lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'A gathering of buyers and sellers, assembled at a particular\n   place with their merchandise at a stated or regular season, or by\n   special appointment, for trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'A festival, and sale of fancy articles. erc., usually for\n   some charitable object; as, a Grand Army fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair', 'n.', 'A competitive exhibition of wares, farm products, etc., not\n   primarily for purposes of sale; as, the Mechanics fair; an\n   agricultural fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-haired', 'a.', 'Having fair or light-colored hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairhood', 'n.', 'Fairness; beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairily', 'adv.', 'In the manner of a fairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairing', 'n.', 'A present; originally, one given or purchased at a fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairish', 'a.', 'Tolerably fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-leader', 'n.', 'A block, or ring, serving as a guide for the running\n   rigging or for any rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairly', 'adv.', 'In a fair manner; clearly; openly; plainly; fully;\n   distinctly; frankly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairly', 'adv.', 'Favorably; auspiciously; commodiously; as, a town fairly\n   situated for foreign traade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairly', 'adv.', 'Honestly; properly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairly', 'adv.', 'Softly; quietly; gently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-minded', 'a.', 'Unprejudiced; just; judicial; honest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-natured', 'a.', 'Well-disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairness', 'n.', 'The state of being fair, or free form spots or stains, as\n   of the skin; honesty, as of dealing; candor, as of an argument, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faair-spoken', 'a.', 'Using fair speech, or uttered with fairness; bland;\n   civil; courteous; plausible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairway', 'n.', 'The navigable part of a river, bay, etc., through which\n   vessels enter or depart; the part of a harbor or channel ehich is kept\n   open and unobstructed for the passage of vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-weather', 'a.', 'Made or done in pleasant weather, or in circumstances\n   involving but little exposure or sacrifice; as, a fair-weather voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-weather', 'a.', 'Appearing only when times or circumstances are\n   prosperous; as, a fair-weather friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fair-world', 'n.', 'State of prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairies', 'pl. ', 'of Fairy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'n.', 'Enchantment; illusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'n.', 'The country of the fays; land of illusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'n.', 'An imaginary supernatural being or spirit, supposed to\n   assume a human form (usually diminutive), either male or female, and to\n   meddle for good or evil in the affairs of mankind; a fay. See Elf, and\n   Demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'n.', 'An enchantress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'a.', 'Of or pertaining to fairies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairy', 'a.', 'Given by fairies; as, fairy money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairyland', 'n.', 'The imaginary land or abode of fairies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fairylike', 'a.', 'Resembling a fairy, or what is made or done be fairies;\n   as, fairylike music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'Belief; the assent of the mind to the truth of what is\n   declared by another, resting solely and implicitly on his authority and\n   veracity; reliance on testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'The assent of the mind to the statement or proposition of\n   another, on the ground of the manifest truth of what he utters; firm\n   and earnest belief, on probable evidence of any kind, especially in\n   regard to important moral truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'The belief in the historic truthfulness of the Scripture\n   narrative, and the supernatural origin of its teachings, sometimes\n   called historical and speculative faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'The belief in the facts and truth of the Scriptures, with a\n   practical love of them; especially, that confiding and affectionate\n   belief in the person and work of Christ, which affects the character\n   and life, and makes a man a true Christian, -- called a practical,\n   evangelical, or saving faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'That which is believed on any subject, whether in science,\n   politics, or religion; especially (Theol.), a system of religious\n   belief of any kind; as, the Jewish or Mohammedan faith; and especially,\n   the system of truth taught by Christ; as, the Christian faith; also,\n   the creed or belief of a Christian society or church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'Fidelity to ones promises, or allegiance to duty, or to a\n   person honored and beloved; loyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'Word or honor pledged; promise given; fidelity; as, he\n   violated his faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'n.', 'Credibility or truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faith', 'interj.', 'By my faith; in truth; verily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithed', 'a.', 'Having faith or a faith; honest; sincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithful', 'a.', 'Full of faith, or having faith; disposed to believe,\n   especially in the declarations and promises of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithful', 'a.', 'Firm in adherence to promises, oaths, contracts,\n   treaties, or other engagements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithful', 'a.', 'True and constant in affection or allegiance to a person\n   to whom one is bound by a vow, be ties of love, gratitude, or honor, as\n   to a husband, a prince, a friend; firm in the observance of duty;\n   loyal; of true fidelity; as, a faithful husband or servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithful', 'a.', 'Worthy of confidence and belief; conformable to truth ot\n   fact; exact; accurate; as, a faithful narrative or representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithless', 'a.', 'Not believing; not giving credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithless', 'a.', 'Not believing on God or religion; specifically, not\n   believing in the Christian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithless', 'a.', 'Not observant of promises or covenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithless', 'a.', 'Not true to allegiance, duty, or vows; perfidious;\n   trecherous; disloyal; not of true fidelity; inconstant, as a husband or\n   a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faithless', 'a.', 'Serving to disappoint or deceive; delusive;\n   unsatisfying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faitour', 'n.', 'A doer or actor; particularly, an evil doer; a scoundrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'n.', 'One of the circles or windings of a cable or hawser, as it\n   lies in a coil; a single turn or coil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'v. t.', 'To coil (a rope, line, or hawser), by winding alternately\n   in opposite directions, in layers usually of zigzag or figure of eight\n   form,, to prevent twisting when running out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'v. t.', 'To cheat; to swindle; to steal; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'v. t.', 'To make; to construct; to do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'v. t.', 'To manipulate fraudulently, so as to make an object appear\n   better or other than it really is; as, to fake a bulldog, by burning\n   his upper lip and thus artificially shortening it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fake', 'n.', 'A trick; a swindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fakir', 'n.', 'An Oriental religious ascetic or begging monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falanaka', 'n.', 'A viverrine mammal of Madagascar (Eupleres Goudotii),\n   allied to the civet; -- called also Falanouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcade', 'n.', 'The action of a horse, when he throws himself on his\n   haunches two or three times, bending himself, as it were, in very quick\n   curvets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcate', 'a.', 'Alt. of Falcated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcated', 'a.', 'Hooked or bent like a sickle; as, a falcate leaf; a\n   falcate claw; -- said also of the moon, or a planet, when horned or\n   crescent-formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcation', 'n.', 'The state of being falcate; a bend in the form of a\n   sickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcer', 'n.', 'One of the mandibles of a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falchion', 'n.', 'A broad-bladed sword, slightly curved, shorter and\n   lighter than the ordinary sword; -- used in the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falchion', 'n.', 'A name given generally and poetically to a sword,\n   especially to the swords of Oriental and fabled warriors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcidian', 'a.', 'Of or pertaining to Publius Falcidius, a Roman tribune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falciform', 'a.', 'Having the shape of a scithe or sickle; resembling a\n   reaping hook; as, the falciform ligatment of the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcon', 'n.', 'One of a family (Falconidae) of raptorial birds,\n   characterized by a short, hooked beak, strong claws, and powerful\n   flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcon', 'n.', 'Any species of the genus Falco, distinguished by having a\n   toothlike lobe on the upper mandible; especially, one of this genus\n   trained to the pursuit of other birds, or game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcon', 'n.', 'An ancient form of cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconer', 'n.', 'A person who breeds or trains hawks for taking birds or\n   game; one who follows the sport of fowling with hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconet', 'n.', 'One of the smaller cannon used in the 15th century and\n   later.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconet', 'n.', 'One of several very small Asiatic falcons of the genus\n   Microhierax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconet', 'n.', 'One of a group of Australian birds of the genus\n   Falcunculus, resembling shrikes and titmice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcongentil', 'n.', 'The female or young of the goshawk (Astur\n   palumbarius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconine', 'a.', 'Like a falcon or hawk; belonging to the Falconidae');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconry', 'n.', 'The art of training falcons or hawks to pursue and attack\n   wild fowl or game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falconry', 'n.', 'The sport of taking wild fowl or game by means of falcons\n   or hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falcula', 'n.', 'A curved and sharp-pointed claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falculate', 'a.', 'Curved and sharppointed, like a falcula, or claw of a\n   falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faldage', 'n.', 'A privilege of setting up, and moving about, folds for\n   sheep, in any fields within manors, in order to manure them; -- often\n   reserved to himself by the lord of the manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faldfee', 'n.', 'A fee or rent paid by a tenant for the privilege of\n   faldage on his own ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falding', 'n.', 'A frieze or rough-napped cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faldistory', 'n.', 'The throne or seat of a bishop within the chancel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faldstool', 'n.', 'A folding stool, or portable seat, made to fold up in\n   the manner of a camo stool. It was formerly placed in the choir for a\n   bishop, when he offciated in any but his own cathedral church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falernian', 'a.', 'Of or pertaining to Mount Falernus, in Italy; as,\n   Falernianwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falk', 'n.', 'The razorbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'imp.', 'of Fall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallen', 'p. p.', 'of Fall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falling', 'p. pr. & vb. n.', 'of Fall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To Descend, either suddenly or gradually; particularly, to\n   descend by the force of gravity; to drop; to sink; as, the apple falls;\n   the tide falls; the mercury falls in the barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To cease to be erect; to take suddenly a recumbent\n   posture; to become prostrate; to drop; as, a child totters and falls; a\n   tree falls; a worshiper falls on his knees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To find a final outlet; to discharge its waters; to empty;\n   -- with into; as, the river Rhone falls into the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To become prostrate and dead; to die; especially, to die\n   by violence, as in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To cease to be active or strong; to die away; to lose\n   strength; to subside; to become less intense; as, the wind falls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To issue forth into life; to be brought forth; -- said of\n   the young of certain animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To decline in power, glory, wealth, or importance; to\n   become insignificant; to lose rank or position; to decline in weight,\n   value, price etc.; to become less; as, the falls; stocks fell two\n   points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To be overthrown or captured; to be destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To descend in character or reputation; to become degraded;\n   to sink into vice, error, or sin; to depart from the faith; to\n   apostatize; to sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To become insnared or embarrassed; to be entrapped; to be\n   worse off than before; asm to fall into error; to fall into\n   difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To assume a look of shame or disappointment; to become or\n   appear dejected; -- said of the countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To sink; to languish; to become feeble or faint; as, our\n   spirits rise and fall with our fortunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To pass somewhat suddenly, and passively, into a new state\n   of body or mind; to become; as, to fall asleep; to fall into a passion;\n   to fall in love; to fall into temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To happen; to to come to pass; to light; to befall; to\n   issue; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To come; to occur; to arrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To begin with haste, ardor, or vehemence; to rush or\n   hurry; as, they fell to blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To pass or be transferred by chance, lot, distribution,\n   inheritance, or otherwise; as, the estate fell to his brother; the\n   kingdom fell into the hands of his rivals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To belong or appertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To be dropped or uttered carelessly; as, an unguarded\n   expression fell from his lips; not a murmur fell from him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To let fall; to drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To sink; to depress; as, to fall the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To diminish; to lessen or lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To bring forth; as, to fall lambs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'v. t.', 'To fell; to cut down; as, to fall a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The act of falling; a dropping or descending be the force of\n   gravity; descent; as, a fall from a horse, or from the yard of ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The act of dropping or tumbling from an erect posture; as, he\n   was walking on ice, and had a fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Death; destruction; overthrow; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Downfall; degradation; loss of greatness or office;\n   termination of greatness, power, or dominion; ruin; overthrow; as, the\n   fall of the Roman empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The surrender of a besieged fortress or town ; as, the fall\n   of Sebastopol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Diminution or decrease in price or value; depreciation; as,\n   the fall of prices; the fall of rents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'A sinking of tone; cadence; as, the fall of the voice at the\n   close of a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Declivity; the descent of land or a hill; a slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Descent of water; a cascade; a cataract; a rush of water down\n   a precipice or steep; -- usually in the plural, sometimes in the\n   singular; as, the falls of Niagara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The discharge of a river or current of water into the ocean,\n   or into a lake or pond; as, the fall of the Po into the Gulf of Venice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Extent of descent; the distance which anything falls; as, the\n   water of a stream has a fall of five feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The season when leaves fall from trees; autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'That which falls; a falling; as, a fall of rain; a heavy fall\n   of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'The act of felling or cutting down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Lapse or declension from innocence or goodness. Specifically:\n   The first apostasy; the act of our first parents in eating the\n   forbidden fruit; also, the apostasy of the rebellious angels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'Formerly, a kind of ruff or band for the neck; a falling\n   band; a faule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fall', 'n.', 'That part (as one of the ropes) of a tackle to which the\n   power is applied in hoisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallacious', 'a.', 'Embodying or pertaining to a fallacy; illogical; fitted\n   to deceive; misleading; delusive; as, fallacious arguments or\n   reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallacies', 'pl. ', 'of Fallacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallacy', 'n.', 'Deceptive or false appearance; deceitfulness; that which\n   misleads the eye or the mind; deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallacy', 'n.', 'An argument, or apparent argument, which professes to be\n   decisive of the matter at issue, while in reality it is not; a sophism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallals', 'n.pl.', 'Gay ornaments; frippery; gewgaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallax', 'n.', 'Cavillation; a caviling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallen', 'a.', 'Dropped; prostrate; degraded; ruined; decreased; dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallency', 'n.', 'An exception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faller', 'n.', 'One who, or that which, falls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faller', 'n.', 'A part which acts by falling, as a stamp in a fulling mill,\n   or the device in a spinning machine to arrest motion when a thread\n   breaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallfish', 'n.', 'A fresh-water fish of the United States (Semotilus\n   bullaris); -- called also silver chub, and Shiner. The name is also\n   applied to other allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallibility', 'n.', 'The state of being fallible; liability to deceive or\n   to be deceived; as, the fallibity of an argument or of an adviser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallible', 'a.', 'Liable to fail, mistake, or err; liable to deceive or to\n   be deceived; as, all men are fallible; our opinions and hopes are\n   fallible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallibly', 'adv.', 'In a fallible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falling', 'a. & n.', 'from Fall, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallopian', 'a.', 'Pertaining to, or discovered by, Fallopius; as, the\n   Fallopian tubes or oviducts, the ducts or canals which conduct the ova\n   from the ovaries to the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'a.', 'Pale red or pale yellow; as, a fallow deer or greyhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'n.', 'Left untilled or unsowed after plowing; uncultivated; as,\n   fallow ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'n.', 'Plowed land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'n.', 'Land that has lain a year or more untilled or unseeded;\n   land plowed without being sowed for the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'n.', 'The plowing or tilling of land, without sowing it for a\n   season; as, summer fallow, properly conducted, has ever been found a\n   sure method of destroying weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallowed', 'imp. & p. p.', 'of Fallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallowing', 'p. pr. & vb. n.', 'of Fallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow', 'n.', 'To plow, harrow, and break up, as land, without seeding,\n   for the purpose of destroying weeds and insects, and rendering it\n   mellow; as, it is profitable to fallow cold, strong, clayey land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallow deer', '', 'A European species of deer (Cervus dama), much smaller\n   than the red deer. In summer both sexes are spotted with white. It is\n   common in England, where it is often domesticated in the parks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallowist', 'n.', 'One who favors the practice of fallowing land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fallowness', 'n.', 'A well or opening, through the successive floors of a\n   warehouse or manufactory, through which goods are raised or lowered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsary', 'a.', 'A falsifier of evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Uttering falsehood; unveracious; given to deceit;\n   dishnest; as, a false witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not faithful or loyal, as to obligations, allegiance,\n   vows, etc.; untrue; treacherous; perfidious; as, a false friend, lover,\n   or subject; false to promises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not according with truth or reality; not true; fitted\n   or likely to deceive or disappoint; as, a false statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not genuine or real; assumed or designed to deceive;\n   counterfeit; hypocritical; as, false tears; false modesty; false\n   colors; false jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not well founded; not firm or trustworthy; erroneous;\n   as, a false claim; a false conclusion; a false construction in grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not essential or permanent, as parts of a structure\n   which are temporary or supplemental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'superl.', 'Not in tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'adv.', 'Not truly; not honestly; falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'a.', 'To report falsely; to falsify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'a.', 'To betray; to falsify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'a.', 'To mislead by want of truth; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False', 'a.', 'To feign; to pretend to make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False-faced', 'a.', 'Hypocritical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False-heart', 'a.', 'False-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'False-hearted', 'a.', 'Hollow or unsound at the core; treacherous;\n   deceitful; perfidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsehood', 'n.', 'Want of truth or accuracy; an untrue assertion or\n   representation; error; misrepresentation; falsity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsehood', 'n.', 'A deliberate intentional assertion of what is known to\n   be untrue; a departure from moral integrity; a lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsehood', 'n.', 'Treachery; deceit; perfidy; unfaithfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsehood', 'n.', 'A counterfeit; a false appearance; an imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsely', 'adv.', 'In a false manner; erroneously; not truly; perfidiously\n   or treacherously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falseness', 'n.', 'The state of being false; contrariety to the fact;\n   inaccuracy; want of integrity or uprightness; double dealing;\n   unfaithfulness; treachery; perfidy; as, the falseness of a report, a\n   drawing, or a singers notes; the falseness of a man, or of his word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falser', 'n.', 'A deceiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsettos', 'pl. ', 'of Falsetto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsetto', 'n.', 'A false or artificial voice; that voice in a man which\n   lies above his natural voice; the male counter tenor or alto voice. See\n   Head voice, under Voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsicrimen', '', 'The crime of falsifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsifiable', 'a.', 'Capable of being falsified, counterfeited, or\n   corrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsification', 'n.', 'The act of falsifying, or making false; a\n   counterfeiting; the giving to a thing an appearance of something which\n   it is not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsification', 'n.', 'Willful misstatement or misrepresentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsification', 'n.', 'The showing an item of charge in an account to be\n   wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsificator', 'n.', 'A falsifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsifier', 'n.', 'One who falsifies, or gives to a thing a deceptive\n   appearance; a liar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsified', 'imp. & p. p.', 'of Falsify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsifying', 'p. pr. & vb. n.', 'of Falsify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To make false; to represent falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To counterfeit; to forge; as, to falsify coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To prove to be false, or untrustworthy; to confute; to\n   disprove; to nullify; to make to appear false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To violate; to break by falsehood; as, to falsify ones\n   faith or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To baffle or escape; as, to falsify a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To avoid or defeat; to prove false, as a judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To show, in accounting, (an inem of charge inserted in an\n   account) to be wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'a.', 'To make false by multilation or addition; to tamper with;\n   as, to falsify a record or document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsify', 'v. i.', 'To tell lies; to violate the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsism', 'n.', 'That which is evidently false; an assertion or statement\n   the falsity of which is plainly apparent; -- opposed to truism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsities', 'pl. ', 'of Falsity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsity', 'a.', 'The quality of being false; coutrariety or want of\n   conformity to truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falsity', 'a.', 'That which is false; falsehood; a lie; a false assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. t.', 'To thrash in the chaff; also, to cleanse or sift, as\n   barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faltered', 'imp. & p. p.', 'of Falter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faltering', 'p. pr. & vb. n.', 'of Falter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. & n.', 'To hesitate; to speak brokenly or weakly; to stammer;\n   as, his tongue falters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. & n.', 'To tremble; to totter; to be unsteady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. & n.', 'To hesitate in purpose or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. & n.', 'To fail in distinctness or regularity of exercise; --\n   said of the mind or of thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. t.', 'To utter with hesitation, or in a broken, trembling, or\n   weak manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falter', 'v. i.', 'Hesitation; trembling; feebleness; an uncertain or\n   broken sound; as, a slight falter in her voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faltering', 'a.', 'Hesitating; trembling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faltering', 'n.', 'Falter; halting; hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faluns', 'n.', 'A series of strata, of the Middle Tertiary period, of\n   France, abounding in shells, and used by Lyell as the type of his\n   Miocene subdivision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falwe', 'a. & n.', 'Fallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falx', 'n.', 'A curved fold or process of the dura mater or the peritoneum;\n   esp., one of the partitionlike folds of the dura mater which extend\n   into the great fissures of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famble', 'v. i.', 'To stammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famble', 'v.', 'A hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fame', 'n.', 'Public report or rumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fame', 'n.', 'Report or opinion generally diffused; renown; public\n   estimation; celebrity, either favorable or unfavorable; as, the fame of\n   Washington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famed', 'imp. & p. p.', 'of Fame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faming', 'p. pr. & vb. n.', 'of Fame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fame', 'v. t.', 'To report widely or honorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fame', 'v. t.', 'To make famous or renowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fameless', 'a.', 'Without fame or renown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'a.', 'Of or pertaining to a family; domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'a.', 'Closely acquainted or intimate, as a friend or companion;\n   well versed in, as any subject of study; as, familiar with the\n   Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'a.', 'Characterized by, or exhibiting, the manner of an\n   intimate friend; not formal; unconstrained; easy; accessible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'a.', 'Well known; well understood; common; frequent; as, a\n   familiar illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'a.', 'Improperly acquainted; wrongly intimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'n.', 'An intimate; a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'n.', 'An attendant demon or evil spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiar', 'n.', 'A confidential officer employed in the service of the\n   tribunal, especially in apprehending and imprisoning the accused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarities', 'pl. ', 'of Familiarity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarity', 'n.', 'The state of being familiar; intimate and frequent\n   converse, or association; unconstrained intercourse; freedom from\n   ceremony and constraint; intimacy; as, to live in remarkable\n   familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarity', 'n.', 'Anything said or done by one person to another\n   unceremoniously and without constraint; esp., in the pl., such actions\n   and words as propriety and courtesy do not warrant; liberties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarization', 'n.', 'The act or process of making familiar; the result\n   of becoming familiar; as, familiarization with scenes of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarized', 'imp. & p. p.', 'of Familiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarizing', 'p. pr. & vb. n.', 'of Familiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarize', 'v. t.', 'To make familiar or intimate; to habituate; to\n   accustom; to make well known by practice or converse; as, to\n   familiarize ones self with scenes of distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarize', 'v. t.', 'To make acquainted, or skilled, by practice or\n   study; as, to familiarize ones self with a business, a book, or a\n   science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarly', 'adv.', 'In a familiar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiarness', 'n.', 'Familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familiary', 'a.', 'Of or pertaining to a family or household; domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familism', 'n.', 'The tenets of the Familists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familist', 'n.', 'One of afanatical Antinomian sect originating in Holland,\n   and existing in England about 1580, called the Family of Love, who held\n   that religion consists wholly in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familisteries', 'pl. ', 'of Familistery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familistery', 'n.', 'A community in which many persons unite as in one\n   family, and are regulated by certain communistic laws and customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familistic', 'a.', 'Alt. of Familistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Familistical', 'a.', 'Pertaining to Familists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Families', 'pl. ', 'of Family');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'The collective body of persons who live in one house,\n   and under one head or manager; a household, including parents,\n   children, and servants, and, as the case may be, lodgers or boarders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'The group comprising a husband and wife and their\n   dependent children, constituting a fundamental unit in the organization\n   of society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'Those who descend from one common progenitor; a tribe,\n   clan, or race; kindred; house; as, the human family; the family of\n   Abraham; the father of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'Course of descent; genealogy; line of ancestors;\n   lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'Honorable descent; noble or respectable stock; as, a man\n   of family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'A group of kindred or closely related individuals; as, a\n   family of languages; a family of States; the chlorine family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Family', 'v. t.', 'A group of organisms, either animal or vegetable,\n   related by certain points of resemblance in structure or development,\n   more comprehensive than a genus, because it is usually based on fewer\n   or less pronounced points of likeness. In zoology a family is less\n   comprehesive than an order; in botany it is often considered the same\n   thing as an order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famine', 'n.', 'General scarcity of food; dearth; a want of provisions;\n   destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famished', 'imp. & p. p.', 'of Famish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famishing', 'p. pr. & vb. n.', 'of Famish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. t.', 'To starve, kill, or destroy with hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. t.', 'To exhaust the strength or endurance of, by hunger; to\n   distress with hanger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. t.', 'To kill, or to cause to suffer extremity, by deprivation\n   or denial of anything necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. t.', 'To force or constrain by famine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. i.', 'To die of hunger; to starve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. i.', 'To suffer extreme hunger or thirst, so as to be\n   exhausted in strength, or to come near to perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'v. i.', 'To suffer extremity from deprivation of anything\n   essential or necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famishment', 'n.', 'State of being famished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famosity', 'n.', 'The state or quality of being famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famous', 'a.', 'Celebrated in fame or public report; renowned; mach talked\n   of; distinguished in story; -- used in either a good or a bad sense,\n   chiefly the former; often followed by for; as, famous for erudition,\n   for eloquence, for military skill; a famous pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famoused', 'a.', 'Renowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famously', 'adv.', 'In a famous manner; in a distinguished degree; greatly;\n   splendidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famousness', 'n.', 'The state of being famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famular', 'n.', 'Domestic; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famulate', 'v. i.', 'To serve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famulist', 'n.', 'A collegian of inferior rank or position, corresponding\n   to the sizar at Cambridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'An instrument used for producing artificial currents of air,\n   by the wafting or revolving motion of a broad surface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'An instrument for cooling the person, made of feathers, paper,\n   silk, etc., and often mounted on sticks all turning about the same\n   pivot, so as when opened to radiate from the center and assume the\n   figure of a section of a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'Any revolving vane or vanes used for producing currents of\n   air, in winnowing grain, blowing a fire, ventilation, etc., or for\n   checking rapid motion by the resistance of the air; a fan blower; a fan\n   wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'An instrument for winnowing grain, by moving which the grain\n   is tossed and agitated, and the chaff is separated and blown away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'Something in the form of a fan when spread, as a peacocks\n   tail, a window, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'A small vane or sail, used to keep the large sails of a smock\n   windmill always in the direction of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'That which produces effects analogous to those of a fan, as in\n   exciting a flame, etc.; that which inflames, heightens, or strengthens;\n   as, it served as a fan to the flame of his passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'A quintain; -- from its form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanned', 'imp. & p. p.', 'of Fan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanning', 'p. pr. & vb. n.', 'of Fan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'To move as with a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'To cool and refresh, by moving the air with a fan; to blow the\n   air on the face of with a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'To ventilate; to blow on; to affect by air put in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'To winnow; to separate chaff from, and drive it away by a\n   current of air; as, to fan wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan', 'n.', 'To excite or stir up to activity, as a fan axcites a flame; to\n   stimulate; as, this conduct fanned the excitement of the populace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanal', 'n.', 'A lighthouse, or the apparatus placed in it for giving\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanatic', 'a.', 'Pertaining to, or indicating, fanaticism; extravagant in\n   opinions; ultra; unreasonable; excessively enthusiastic, especially on\n   religious subjects; as, fanatic zeal; fanatic notions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanatic', 'n.', 'A person affected by excessive enthusiasm, particularly on\n   religious subjects; one who indulges wild and extravagant notions of\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanatical', 'a.', 'Characteristic of, or relating to, fanaticism; fanatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanaticism', 'n.', 'Excessive enthusiasm, unreasoning zeal, or wild and\n   extravagant notions, on any subject, especially religion; religious\n   frenzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanaticized', 'imp. & p. p.', 'of Fanaticize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanaticizing', 'p. pr. & vb. n.', 'of Fanaticize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanaticize', 'v. t.', 'To cause to become a fanatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanatism', 'n.', 'Fanaticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancied', 'v. t.', 'Formed or conceived by the fancy; unreal; as, a fancied\n   wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancier', 'n.', 'One who is governed by fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancier', 'n.', 'One who fancies or has a special liking for, or interest\n   in, a particular object or class or objects; hence, one who breeds and\n   keeps for sale birds and animals; as, bird fancier, dog fancier, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanciful', 'a.', 'Full of fancy; guided by fancy, rather than by reason and\n   experience; whimsical; as, a fanciful man forms visionary projects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanciful', 'a.', 'Conceived in the fancy; not consistent with facts or\n   reason; abounding in ideal qualities or figures; as, a fanciful scheme;\n   a fanciful theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanciful', 'a.', 'Curiously shaped or constructed; as, she wore a fanciful\n   headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanciless', 'a.', 'Having no fancy; without ideas or imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancies', 'pl. ', 'of Fancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'The faculty by which the mind forms an image or a\n   representation of anything perceived before; the power of combining and\n   modifying such objects into new pictures or images; the power of\n   readily and happily creating and recalling such objects for the purpose\n   of amusement, wit, or embellishment; imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'An image or representation of anything formed in the mind;\n   conception; thought; idea; conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'An opinion or notion formed without much reflection;\n   caprice; whim; impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'Inclination; liking, formed by caprice rather than reason;\n   as, to strike ones fancy; hence, the object of inclination or liking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'That which pleases or entertains the taste or caprice\n   without much use or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'n.', 'A sort of love song or light impromptu ballad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancied', 'imp. & p. p.', 'of Fancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancying', 'p. pr. & vb. n.', 'of Fancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'v. i.', 'To figure to ones self; to believe or imagine something\n   without proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'v. i.', 'To love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'v. t.', 'To form a conception of; to portray in the mind; to\n   imagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'v. t.', 'To have a fancy for; to like; to be pleased with,\n   particularly on account of external appearance or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'v. t.', 'To believe without sufficient evidence; to imagine\n   (something which is unreal).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'a.', 'Adapted to please the fancy or taste; ornamental; as, fancy\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy', 'a.', 'Extravagant; above real value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy-free', 'a.', 'Free from the power of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancymonger', 'n.', 'A lovemonger; a whimsical lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancy-sick', 'a.', 'Love-sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fancywork', 'n.', 'Ornamental work with a needle or hook, as embroidery,\n   crocheting, netting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fand', '', 'imp. of Find.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fandangoes', 'pl. ', 'of Fandango');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fandango', 'n.', 'A lively dance, in 3-8 or 6-8 time, much practiced in\n   Spain and Spanish America. Also, the tune to which it is danced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fandango', 'n.', 'A ball or general dance, as in Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fane', 'n.', 'A temple; a place consecrated to religion; a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fane', 'n.', 'A weathercock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanega', 'n.', 'A dry measure in Spain and Spanish America, varying from 1/\n   to 2/ bushels; also, a measure of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanfare', 'n.', 'A flourish of trumpets, as in coming into the lists, etc.;\n   also, a short and lively air performed on hunting horns during the\n   chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanfaron', 'n.', 'A bully; a hector; a swaggerer; an empty boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanfaronade', 'n.', 'A swaggering; vain boasting; ostentation; a bluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanfoot', 'n.', 'A species of gecko having the toes expanded into large\n   lobes for adhesion. The Egyptian fanfoot (Phyodactylus gecko) is\n   believed, by the natives, to have venomous toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanfoot', 'n.', 'Any moth of the genus Polypogon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'a.', 'To catch; to seize, as with the teeth; to lay hold of; to\n   gripe; to clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'a.', 'To enable to catch or tear; to furnish with fangs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'The tusk of an animal, by which the prey is seized and\n   held or torn; a long pointed tooth; esp., one of the usually erectile,\n   venomous teeth of serpents. Also, one of the falcers of a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'Any shoot or other thing by which hold is taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'The root, or one of the branches of the root, of a tooth.\n   See Tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'A niche in the side of an adit or shaft, for an air\n   course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'A projecting tooth or prong, as in a part of a lock, or\n   the plate of a belt clamp, or the end of a tool, as a chisel, where it\n   enters the handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'The valve of a pump box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fang', 'v. t.', 'A bend or loop of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanged', 'a.', 'Having fangs or tusks; as, a fanged adder. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangle', 'v. t.', 'Something new-fashioned; a foolish innovation; a gewgaw;\n   a trifling ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangle', 'v. t.', 'To fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangled', 'a.', 'New made; hence, gaudy; showy; vainly decorated. [Obs.,\n   except with the prefix new.] See Newfangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangleness', 'n.', 'Quality of being fangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangless', 'a.', 'Destitute of fangs or tusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fangot', 'n.', 'A quantity of wares, as raw silk, etc., from one hundred\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanion', 'n.', 'A small flag sometimes carried at the head of the baggage\n   of a brigade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanion', 'n.', 'A small flag for marking the stations in surveying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanlike', 'a.', 'Resembling a fan;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanlike', 'a.', 'folded up like a fan, as certain leaves; plicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fannel', 'n.', 'Same as Fanon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanner', 'n.', 'One who fans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanner', 'n.', 'A fan wheel; a fan blower. See under Fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan-nerved', 'a.', 'Having the nerves or veins arranged in a radiating\n   manner; -- said of certain leaves, and of the wings of some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fanon', 'n.', 'A term applied to various articles, as: (a) A peculiar\n   striped scarf worn by the pope at mass, and by eastern bishops. (b) A\n   maniple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan palm', '', 'Any palm tree having fan-shaped or radiate leaves; as the\n   Chamaerops humilis of Southern Europe; the species of Sabal and Thrinax\n   in the West Indies, Florida, etc.; and especially the great talipot\n   tree (Corypha umbraculifera) of Ceylon and Malaya. The leaves of the\n   latter are often eighteen feet long and fourteen wide, and are used for\n   umbrellas, tents, and roofs. When cut up, they are used for books and\n   manuscripts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantail', 'n.', 'A variety of the domestic pigeon, so called from the shape\n   of the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantail', 'n.', 'Any bird of the Australian genus Rhipidura, in which the\n   tail is spread in the form of a fan during flight. They belong to the\n   family of flycatchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fan-tailed', 'a.', 'Having an expanded, or fan-shaped, tail; as, the\n   fan-tailed pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasia', 'n.', 'A continuous composition, not divided into what are\n   called movements, or governed by the ordinary rules of musical design,\n   but in which the authors fancy roves unrestricted by set form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasied', 'a.', 'Filled with fancies or imaginations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasm', 'n.', 'Same as Phantasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantast', 'n.', 'One whose manners or ideas are fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic', 'a.', 'Existing only in imagination; fanciful; imaginary; not\n   real; chimerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic', 'a.', 'Having the nature of a phantom; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic', 'a.', 'Indulging the vagaries of imagination; whimsical; full\n   of absurd fancies; capricious; as, fantastic minds; a fantastic\n   mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic', 'a.', 'Resembling fantasies in irregularity, caprice, or\n   eccentricity; irregular; oddly shaped; grotesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic', 'n.', 'A person given to fantastic dress, manners, etc.; an\n   eccentric person; a fop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastical', 'a.', 'Fanciful; unreal; whimsical; capricious; fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasticality', 'n.', 'Fantastically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastically', 'adv.', 'In a fantastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantastic-alness', 'n.', 'The quality of being fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasticism', 'n.', 'The quality of being fantastical; fancifulness;\n   whimsicality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasticly', 'adv.', 'Fantastically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasticness', 'n.', 'Fantasticalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasticco', 'n.', 'A fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasies', 'pl. ', 'of Fantasy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasy', 'n.', 'Fancy; imagination; especially, a whimsical or fanciful\n   conception; a vagary of the imagination; whim; caprice; humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasy', 'n.', 'Fantastic designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantasy', 'v. t.', 'To have a fancy for; to be pleased with; to like; to\n   fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantoccini', 'n. pl.', 'Puppets caused to perform evolutions or dramatic\n   scenes by means of machinery; also, the representations in which they\n   are used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fantom', 'n.', 'See Phantom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fap', 'a.', 'Fuddled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faquir', 'n.', 'See Fakir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'n.', 'A young pig, or a litter of pigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'a.', 'Distant in any direction; not near; remote; mutually separated\n   by a wide space or extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'a.', 'Remote from purpose; contrary to design or wishes; as, far be\n   it from me to justify cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'a.', 'Remote in affection or obedience; at a distance, morally or\n   spiritually; t enmity with; alienated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'a.', 'Widely different in nature or quality; opposite in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'a.', 'The more distant of two; as, the far side (called also off\n   side) of a horse, that is, the right side, or the one opposite to the\n   rider when he mounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'adv.', 'To a great extent or distance of space; widely; as, we are\n   separated far from each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'adv.', 'To a great distance in time from any point; remotely; as, he\n   pushed his researches far into antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'adv.', 'In great part; as, the day is far spent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far', 'adv.', 'In a great proportion; by many degrees; very much; deeply;\n   greatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farabout', 'n.', 'A going out of the way; a digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farad', 'n.', 'The standard unit of electrical capacity; the capacity of a\n   condenser whose charge, having an electro-motive force of one volt, is\n   equal to the amount of electricity which, with the same electromotive\n   force, passes through one ohm in one second; the capacity, which,\n   charged with one coulomb, gives an electro-motive force of one volt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faradic', 'a.', 'Of or pertaining to Michael Faraday, the distinguished\n   electrician; -- applied especially to induced currents of electricity,\n   as produced by certain forms of inductive apparatus, on account of\n   Faradays investigations of their laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faradism', 'n.', 'Alt. of Faradization');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faradization', 'n.', 'The treatment with faradic or induced currents of\n   electricity for remedial purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farand', 'n.', 'See Farrand, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farandams', 'n.', 'A fabrik made of silk and wool or hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farantly', 'a.', 'Orderly; comely; respectable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farced', 'imp. & p. p.', 'of Farce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcing', 'p. pr. & vb. n.', 'of Farce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'To stuff with forcemeat; hence, to fill with mingled\n   ingredients; to fill full; to stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'To render fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'To swell out; to render pompous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'Stuffing, or mixture of viands, like that used on\n   dressing a fowl; forcemeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'A low style of comedy; a dramatic composition marked by\n   low humor, generally written with little regard to regularity or\n   method, and abounding with ludicrous incidents and expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farce', 'v. t.', 'Ridiculous or empty show; as, a mere farce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcement', 'n.', 'Stuffing; forcemeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcical', 'a.', 'Pertaining to farce; appropriated to farce; ludicrous;\n   unnatural; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcical', 'a.', 'Of or pertaining to the disease called farcy. See Farcy,\n   n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcilite', 'n.', 'Pudding stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcimen', 'n.', 'Alt. of Farcin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcin', 'n.', 'Same as Farcy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcing', 'n.', 'Stuffing; forcemeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farctate', 'v. t.', 'Stuffed; filled solid; as, a farctate leaf, stem, or\n   pericarp; -- opposed to tubular or hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farcy', 'n.', 'A contagious disease of horses, associated with painful\n   ulcerating enlargements, esp. upon the head and limbs. It is of the\n   same nature as glanders, and is often fatal. Called also farcin, and\n   farcimen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fard', 'n.', 'Paint used on the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fard', 'v. t.', 'To paint; -- said esp. of ones face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fardage', 'n.', 'See Dunnage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fardel', 'n.', 'A bundle or little pack; hence, a burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fardel', 'v. t.', 'To make up in fardels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farding-bag', 'n.', 'The upper stomach of a cow, or other ruminant animal;\n   the rumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fardingdale', 'n.', 'A farthingale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fardingdeal', 'n.', 'The fourth part of an acre of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fared', 'imp. & p. p.', 'of Fare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faring', 'p. pr. & vb. n.', 'of Fare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'n.', 'To go; to pass; to journey; to travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'n.', 'To be in any state, or pass through any experience, good or\n   bad; to be attended with any circummstances or train of events,\n   fortunate or unfortunate; as, he fared well, or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'n.', 'To be treated or entertained at table, or with bodily or\n   social comforts; to live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'n.', 'To happen well, or ill; -- used impersonally; as, we shall\n   see how it will fare with him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'n.', 'To behave; to conduct ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'A journey; a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'The price of passage or going; the sum paid or due for\n   conveying a person by land or water; as, the fare for crossing a river;\n   the fare in a coach or by railway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'Ado; bustle; business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'Condition or state of things; fortune; hap; cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'Food; provisions for the table; entertainment; as, coarse\n   fare; delicious fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'The person or persons conveyed in a vehicle; as, a full fare\n   of passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fare', 'v.', 'The catch of fish on a fishing vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faren', '', 'p. p. of Fare, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farewell', 'interj.', 'Go well; good-by; adieu; -- originally applied to a\n   person departing, but by custom now applied both to those who depart\n   and those who remain. It is often separated by the pronoun; as, fare\n   you well; and is sometimes used as an expression of separation only;\n   as, farewell the year; farewell, ye sweet groves; that is, I bid you\n   farewell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farewell', 'n.', 'A wish of happiness or welfare at parting; the parting\n   compliment; a good-by; adieu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farewell', 'n.', 'Act of departure; leave-taking; a last look at, or\n   reference to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farewell', 'a.', 'Parting; valedictory; final; as, a farewell discourse;\n   his farewell bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfet', 'p. p.', 'Farfetched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfetch', 'v. t.', 'To bring from far; to seek out studiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfetch', 'n.', 'Anything brought from far, or brought about with studious\n   care; a deep strategem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfetched', 'a.', 'Brought from far, or from a remote place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfetched', 'a.', 'Studiously sought; not easily or naturally deduced or\n   introduced; forced; strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farina', 'n.', 'A fine flour or meal made from cereal grains or from the\n   starch or fecula of vegetables, extracted by various processes, and\n   used in cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farina', 'n.', 'Pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farinaceous', 'a.', 'Consisting or made of meal or flour; as, a farinaceous\n   diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farinaceous', 'a.', 'Yielding farina or flour; as, ffarinaceous seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farinaceous', 'a.', 'Like meal; mealy; pertainiing to meal; as, a\n   farinaceous taste, smell, or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farinose', 'a.', 'Yielding farinaa; as, farinose substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farinose', 'a.', 'Civered with a sort of white, mealy powder, as the leaves\n   of some poplars, and the body of certain insects; mealy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farl', 'v. t.', 'Same as Furl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farlie', 'n.', 'An unusual or unexpected thing; a wonder. See Fearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'The rent of land, -- originally paid by reservation of\n   part of its products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'The term or tenure of a lease of land for cultivation; a\n   leasehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'The land held under lease and by payment of rent for the\n   purpose of cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'Any tract of land devoted to agricultural purposes,\n   under the management of a tenant or the owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'A district of country leased (or farmed) out for the\n   collection of the revenues of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'a. & n.', 'A lease of the imposts on particular goods; as, the\n   sugar farm, the silk farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmed', 'imp. & p. p.', 'of Farm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farming', 'p. pr. & vb. n.', 'of Farm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'v. t.', 'To lease or let for an equivalent, as land for a rent; to\n   yield the use of to proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'v. t.', 'To give up to another, as an estate, a business, the\n   revenue, etc., on condition of receiving in return a percentage of what\n   it yields; as, to farm the taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'v. t.', 'To take at a certain rent or rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'v. t.', 'To devote (land) to agriculture; to cultivate, as land; to\n   till, as a farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farm', 'v. i.', 'To engage in the business of tilling the soil; to labor as\n   a farmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmable', 'a.', 'Capable of being farmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmer', 'n.', 'One who farms');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmer', 'n.', 'One who hires and cultivates a farm; a cultivator of leased\n   ground; a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmer', 'n.', 'One who is devoted to the tillage of the soil; one who\n   cultivates a farm; an agriculturist; a husbandman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmer', 'n.', 'One who takes taxes, customs, excise, or other duties, to\n   collect, either paying a fixed annuual rent for the privilege; as, a\n   farmer of the revenues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmer', 'n.', 'The lord of the field, or one who farms the lot and cope of\n   the crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmeress', 'n.', 'A woman who farms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmership', 'n.', 'Skill in farming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmery', 'n.', 'The buildings and yards necessary for the business of a\n   farm; a homestead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmhouse', 'n.', 'A dwelling house on a farm; a farmers residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farming', 'a.', 'Pertaining to agriculture; devoted to, adapted to, or\n   engaged in, farming; as, farming tools; farming land; a farming\n   community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farming', 'n.', 'The business of cultivating land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmost', 'a.', 'Most distant; farthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmstead', 'n.', 'A farm with the building upon it; a homestead on a farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmsteading', 'n.', 'A farmstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farmyard', 'n.', 'The yard or inclosure attached to a barn, or the space\n   inclosed by the farm buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farness', 'a.', 'The state of being far off; distance; remoteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faro', 'n.', 'A gambling game at cardds, in whiich all the other players\n   play against the dealer or banker, staking their money upon the order\n   in which the cards will lie and be dealt from the pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faroese', 'n. sing. & pl.', 'An inhabitant, or, collectively, inhabitants,\n   of the Faroe islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Far-off', 'a.', 'Remote; as, the far-off distance. Cf. Far-off, under Far,\n   adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farraginous', 'a.', 'Formed of various materials; mixed; as, a farraginous\n   mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrago', 'n.', 'A mass composed of various materials confusedly mixed; a\n   medley; a mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrand', 'n.', 'Manner; custom; fashion; humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farreation', 'n.', 'Same as Confarreation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrier', 'n.', 'A shoer of horses; a veterinary surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrier', 'v. i.', 'To practice as a farrier; to carry on the trade of a\n   farrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farriery', 'n.', 'The art of shoeing horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farriery', 'n.', 'The art of preventing, curing, or mitigating diseases of\n   horses and cattle; the veterinary art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farriery', 'n.', 'The place where a smith shoes horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrow', 'n.', 'A little of pigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrowed', 'imp. & p. p.', 'of Farfow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrowing', 'p. pr. & vb. n.', 'of Farfow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farfow', 'v. t. & i.', 'To bring forth (young); -- said only of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farrow', 'a.', 'Not producing young in a given season or year; -- said only\n   of cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farry', 'n.', 'A farrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farse', 'n.', 'An addition to, or a paraphrase of, some part of the Latin\n   service in the vernacular; -- common in English before the Reformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farseeing', 'a.', 'Able to see to a great distance; farsighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farseeing', 'a.', 'Having foresight as regards the future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farsighted', 'a.', 'Seeing to great distance; hence, of good judgment\n   regarding the remote effects of actions; sagacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farsighted', 'a.', 'Hypermetropic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farsightedness', 'n.', 'Quality of bbeing farsighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farsightedness', 'n.', 'Hypermetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farstretched', 'a.', 'Streatched beyond ordinary limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farther', 'superl.', 'More remote; more distant than something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farther', 'superl.', 'Tending to a greater distance; beyond a certain\n   point; additional; further.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farther', 'adv.', 'At or to a greater distance; more remotely; beyond; as,\n   let us rest with what we have, without looking farther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farther', 'adv.', 'Moreover; by way of progress in treating a subject; as,\n   farther, let us consider the probable event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farther', 'v. t.', 'To help onward. [R.] See Further.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fartherance', 'n.', 'See Furtherance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthermore', 'adv.', 'See Furthermore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthermost', 'a.', 'Most remote; farthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthest', 'Superl.', 'Most distant or remote; as, the farthest degree. See\n   Furthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthest', 'adv.', 'At or to the greatest distance. See Furthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthing', 'n.', 'The fourth of a penny; a small copper coin of Great\n   Britain, being a cent in United States currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthing', 'n.', 'A very small quantity or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthing', 'n.', 'A division of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Farthingale', 'n.', 'A hoop skirt or hoop petticoat, or other light,\n   elastic material, used to extend the petticoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasces', 'pl.', 'A bundle of rods, having among them an ax with the blade\n   projecting, borne before the Roman magistrates as a badge of their\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascet', 'n.', 'A wire basket on the end of a rod to carry glass bottles,\n   etc., to the annealing furnace; also, an iron rod to be thrust into the\n   mouths of bottles, and used for the same purpose; -- called also pontee\n   and punty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciae', 'pl. ', 'of Fascia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascia', 'n.', 'A band, sash, or fillet; especially, in surgery, a bandage\n   or roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascia', 'n.', 'A flat member of an order or building, like a flat band or\n   broad fillet; especially, one of the three bands which make up the\n   architrave, in the Ionic order. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascia', 'n.', 'The layer of loose tissue, often containing fat,\n   immediately beneath the skin; the stronger layer of connective tissue\n   covering and investing all muscles; an aponeurosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascia', 'n.', 'A broad well-defined band of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascial', 'a.', 'Pertaining to the fasces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascial', 'a.', 'Relating to a fascia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciate', 'a.', 'Alt. of Fasciated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciated', 'a.', 'Bound with a fillet, sash, or bandage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciated', 'a.', 'Banded or compacted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciated', 'a.', 'Flattened and laterally widened, as are often the stems\n   of the garden cockscomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciated', 'a.', 'Broadly banded with color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciation', 'n.', 'The act or manner of binding up; bandage; also, the\n   condition of being fasciated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascicle', 'n.', 'A small bundle or collection; a compact cluster; as, a\n   fascicle of fibers; a fascicle of flowers or roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascicled', 'a.', 'Growing in a bundle, tuft, or close cluster; as, the\n   fascicled leaves of the pine or larch; the fascicled roots of the\n   dahlia; fascicled muscle fibers; fascicled tufts of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascicular', 'a.', 'Pertaining to a fascicle; fascicled; as, a fascicular\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascicularly', 'adv.', 'In a fascicled manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciculate', 'a.', 'Alt. of Fasciculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciculated', 'a.', 'Grouped in a fascicle; fascicled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciculi', 'pl. ', 'of Fasciculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciculus', 'n.', 'A little bundle; a fascicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciculus', 'n.', 'A division of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascinated', 'imp. & p. p.', 'of Fascinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascinating', 'p. pr. & vb. n.', 'of Fascinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascinate', 'v. t.', 'To influence in an uncontrollable manner; to operate\n   on by some powerful or irresistible charm; to bewitch; to enchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascinate', 'v. t.', 'To excite and allure irresistibly or powerfully; to\n   charm; to captivate, as by physical or mental charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascination', 'n.', 'The act of fascinating, bewhiching, or enchanting;\n   enchantment; witchcraft; the exercise of a powerful or irresistible\n   influence on the affections or passions; unseen, inexplicable\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascination', 'n.', 'The state or condition of being fascinated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascination', 'n.', 'That which fascinates; a charm; a spell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascine', 'n.', 'A cylindrical bundle of small sticks of wood, bound\n   together, used in raising batteries, filling ditches, strengthening\n   ramparts, and making parapets; also in revetments for river banks, and\n   in mats for dams, jetties, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fascinous', 'a.', 'Caused or acting by witchcraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciolae', 'pl. ', 'of Fasciola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciola', 'n.', 'A band of gray matter bordering the fimbria in the brain;\n   the dentate convolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasciole', 'n.', 'A band of minute tubercles, bearing modified spines, on\n   the shells of spatangoid sea urchins. See Spatangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashed', 'imp. & p. p.', 'of Fash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashing', 'p. pr. & vb. n.', 'of Fash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fash', 'v. t.', 'To vex; to tease; to trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fash', 'n.', 'Vexation; anxiety; care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'n.', 'The make or form of anything; the style, shape,\n   appearance, or mode of structure; pattern, model; as, the fashion of\n   the ark, of a coat, of a house, of an altar, etc.; workmanship;\n   execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'n.', 'The prevailing mode or style, especially of dress; custom\n   or conventional usage in respect of dress, behavior, etiquette, etc.;\n   particularly, the mode or style usual among persons of good breeding;\n   as, to dress, dance, sing, ride, etc., in the fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'n.', 'Polite, fashionable, or genteel life; social position;\n   good breeding; as, men of fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'n.', 'Mode of action; method of conduct; manner; custom; sort;\n   way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashioned', 'imp. & p. p.', 'of Fashion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashioning', 'p. pr. & vb. n.', 'of Fashion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'v. t.', 'To form; to give shape or figure to; to mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'v. t.', 'To fit; to adapt; to accommodate; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'v. t.', 'To make according to the rule prescribed by custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion', 'v. t.', 'To forge or counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionable', 'a.', 'Conforming to the fashion or established mode;\n   according with the prevailing form or style; as, a fashionable dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionable', 'a.', 'Established or favored by custom or use; current;\n   prevailing at a particular time; as, the fashionable philosophy;\n   fashionable opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionable', 'a.', 'Observant of the fashion or customary mode; dressing\n   or behaving according to the prevailing fashion; as, a fashionable man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionable', 'a.', 'Genteel; well-bred; as, fashionable society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionable', 'n.', 'A person who conforms to the fashions; -- used chiefly\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionableness', 'n.', 'State of being fashionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionably', 'adv.', 'In a fashionable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashioned', 'a.', 'Having a certain style or fashion; as old-fashioned;\n   new-fashioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashioner', 'n.', 'One who fashions, forms, ar gives shape to anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionist', 'n.', 'An obsequious follower of the modes and fashions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashionless', 'a.', 'Having no fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion-monger', 'n.', 'One who studies the fashions; a fop; a dandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fashion-mongering', 'a.', 'Behaving like a fashion-monger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fassaite', 'n.', 'A variety of pyroxene, from the valley of Fassa, in the\n   Tyrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasted', 'imp. & p. p.', 'of Fast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasting', 'p. pr. & vb. n.', 'of Fast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v. i.', 'To abstain from food; to omit to take nourishment in whole\n   or in part; to go hungry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v. i.', 'To practice abstinence as a religious exercise or duty; to\n   abstain from food voluntarily for a time, for the mortification of the\n   body or appetites, or as a token of grief, or humiliation and\n   penitence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v. i.', 'Abstinence from food; omission to take nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v. i.', 'Voluntary abstinence from food, for a space of time, as a\n   spiritual discipline, or as a token of religious humiliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v. i.', 'A time of fasting, whether a day, week, or longer time; a\n   period of abstinence from food or certain kinds of food; as, an annual\n   fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Firmly fixed; closely adhering; made firm; not loose,\n   unstable, or easily moved; immovable; as, to make fast the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Firm against attack; fortified by nature or art; impregnable;\n   strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Firm in adherence; steadfast; not easily separated or\n   alienated; faithful; as, a fast friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Permanent; not liable to fade by exposure to air or by\n   washing; durable; lasting; as, fast colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Tenacious; retentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Not easily disturbed or broken; deep; sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Moving rapidly; quick in mition; rapid; swift; as, a fast\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'v.', 'Given to pleasure seeking; disregardful of restraint;\n   reckless; wild; dissipated; dissolute; as, a fast man; a fast liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'a.', 'In a fast, fixed, or firmly established manner; fixedly;\n   firmly; immovably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'a.', 'In a fast or rapid manner; quickly; swiftly; extravagantly;\n   wildly; as, to run fast; to live fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'n.', 'That which fastens or holds; especially, (Naut.) a mooring\n   rope, hawser, or chain; -- called, according to its position, a bow,\n   head, quarter, breast, or stern fast; also, a post on a pier around\n   which hawsers are passed in mooring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastened', 'imp. & p. p.', 'of Fasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastening', 'p. pr. & vb. n.', 'of Fasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasten', 'a.', 'To fix firmly; to make fast; to secure, as by a knot, lock,\n   bolt, etc.; as, to fasten a chain to the feet; to fasten a door or\n   window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasten', 'a.', 'To cause to hold together or to something else; to attach\n   or unite firmly; to cause to cleave to something , or to cleave\n   together, by any means; as, to fasten boards together with nails or\n   cords; to fasten anything in our thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasten', 'a.', 'To cause to take close effect; to make to tell; to lay on;\n   as, to fasten a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasten', 'v. i.', 'To fix ones self; to take firm hold; to clinch; to\n   cling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastener', 'n.', 'One who, or that which, makes fast or firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastening', 'n.', 'Anything that binds and makes fast, as a lock, catch,\n   bolt, bar, buckle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faster', 'n.', 'One who abstains from food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast-handed', 'a.', 'Close-handed; close-fisted; covetous; avaricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasti', 'n.pl.', 'The Roman calendar, which gave the days for festivals,\n   courts, etc., corresponding to a modern almanac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fasti', 'n.pl.', 'Records or registers of important events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastidiosity', 'n.', 'Fastidiousness; squeamishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastidious', 'a.', 'Difficult to please; delicate to a fault; suited with\n   difficulty; squeamish; as, a fastidious mind or ear; a fastidious\n   appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastigiate', 'a.', 'Alt. of Fastigiated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastigiated', 'a.', 'Narrowing towards the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastigiated', 'a.', 'Clustered, parallel, and upright, as the branches of\n   the Lombardy poplar; pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastigiated', 'a.', 'United into a conical bundle, or into a bundle with an\n   enlarged head, like a sheaf of wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastish', 'a.', 'Rather fast; also, somewhat dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastly', 'adv.', 'Firmly; surely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastness', 'a.', 'The state of being fast and firm; firmness; fixedness;\n   security; faithfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastness', 'a.', 'A fast place; a stronghold; a fortress or fort; a secure\n   retreat; a castle; as, the enemy retired to their fastnesses in the\n   mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastness', 'a.', 'Conciseness of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastness', 'a.', 'The state of being fast or swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastuous', 'a.', 'Proud; haughty; disdainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'n.', 'A large tub, cistern, or vessel; a vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'n.', 'A measure of quantity, differing for different commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Abounding with fat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Fleshy; characterized by fatness; plump; corpulent; not\n   lean; as, a fat man; a fat ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Oily; greasy; unctuous; rich; -- said of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Exhibiting the qualities of a fat animal; coarse; heavy;\n   gross; dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Fertile; productive; as, a fat soil; a fat pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Rich; producing a large income; desirable; as, a fat\n   benefice; a fat office; a fat job.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Abounding in riches; affluent; fortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'superl.', 'Of a character which enables the compositor to make large\n   wages; -- said of matter containing blank, cuts, or many leads, etc.;\n   as, a fat take; a fat page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'n.', 'An oily liquid or greasy substance making up the main bulk of\n   the adipose tissue of animals, and widely distributed in the seeds of\n   plants. See Adipose tissue, under Adipose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'n.', 'The best or richest productions; the best part; as, to live on\n   the fat of the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'n.', 'Work. containing much blank, or its equivalent, and,\n   therefore, profitable to the compositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatted', 'imp. & p. p.', 'of Fat');
  INSERT INTO words (word, word_type, description) VALUES  (    'atting', 'p. pr. & vb. n.', 'of Fat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'a.', 'To make fat; to fatten; to make plump and fleshy with abundant\n   food; as, to fat fowls or sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat', 'v. i.', 'To grow fat, plump, and fleshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatal', 'a.', 'Proceeding from, or appointed by, fate or destiny;\n   necessary; inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatal', 'a.', 'Foreboding death or great disaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatal', 'a.', 'Causing death or destruction; deadly; mortal; destructive;\n   calamitous; as, a fatal wound; a fatal disease; a fatal day; a fatal\n   error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatalism', 'n.', 'The doctrine that all things are subject to fate, or that\n   they take place by inevitable necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatalist', 'n.', 'One who maintains that all things happen by inevitable\n   necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatalistic', 'a.', 'Implying, or partaking of the nature of, fatalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatalities', 'pl. ', 'of Fatality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatality', 'n.', 'The state of being fatal, or proceeding from destiny;\n   invincible necessity, superior to, and independent of, free and\n   rational control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatality', 'n.', 'The state of being fatal; tendency to destruction or\n   danger, as if by decree of fate; mortaility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatality', 'n.', 'That which is decreed by fate or which is fatal; a fatal\n   event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatally', 'adv.', 'In a manner proceeding from, or determined by, fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatally', 'adv.', 'In a manner issuing in death or ruin; mortally;\n   destructively; as, fatally deceived or wounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatalness', 'n.', 'Quality of being fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fata Morgana', '', 'A kind of mirage by which distant objects appear\n   inverted, distorted, displaced, or multiplied. It is noticed\n   particularly at the Straits of Messina, between Calabria and Sicily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatback', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat-brained', 'a.', 'Dull of apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fate', 'n.', 'A fixed decree by which the order of things is prescribed;\n   the immutable law of the universe; inevitable necessity; the force by\n   which all existence is determined and conditioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fate', 'n.', 'Appointed lot; allotted life; arranged or predetermined\n   event; destiny; especially, the final lot; doom; ruin; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fate', 'n.', 'The element of chance in the affairs of life; the unforeseen\n   and unestimated conitions considered as a force shaping events;\n   fortune; esp., opposing circumstances against which it is useless to\n   struggle; as, fate was, or the fates were, against him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fate', 'n.', 'The three goddesses, Clotho, Lachesis, and Atropos, sometimes\n   called the Destinies, or Parcaewho were supposed to determine the\n   course of human life. They are represented, one as holding the distaff,\n   a second as spinning, and the third as cutting off the thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fated', 'p. p. & a.', 'Decreed by fate; destined; doomed; as, he was fated\n   to rule a factious people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fated', 'p. p. & a.', 'Invested with the power of determining destiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fated', 'p. p. & a.', 'Exempted by fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fateful', 'a. .', 'Having the power of serving or accomplishing fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fateful', 'a. .', 'Significant of fate; ominous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathead', 'n.', 'A cyprinoid fish of the Mississippi valley (Pimephales\n   promelas); -- called also black-headed minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathead', 'n.', 'A labroid food fish of California; the redfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'One who has begotten a child, whether son or daughter; a\n   generator; a male parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'A male ancestor more remote than a parent; a progenitor;\n   especially, a first ancestor; a founder of a race or family; -- in the\n   plural, fathers, ancestors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'One who performs the offices of a parent by maintenance,\n   affetionate care, counsel, or protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'A respectful mode of address to an old man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'A senator of ancient Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'A dignitary of the church, a superior of a convent, a\n   confessor (called also father confessor), or a priest; also, the eldest\n   member of a profession, or of a legislative assembly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'One of the chief esslesiastical authorities of the first\n   centuries after Christ; -- often spoken of collectively as the Fathers;\n   as, the Latin, Greek, or apostolic Fathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'One who, or that which, gives origin; an originator; a\n   producer, author, or contriver; the first to practice any art,\n   profession, or occupation; a distinguished example or teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'n.', 'The Supreme Being and Creator; God; in theology, the first\n   person in the Trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathered', 'imp. & p. p.', 'of Father');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathering', 'p. pr. & vb. n.', 'of Father');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'v. t.', 'To make ones self the father of; to beget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'v. t.', 'To take as ones own child; to adopt; hence, to assume\n   as ones own work; to acknowledge ones self author of or responsible\n   for (a statement, policy, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father', 'v. t.', 'To provide with a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherhood', 'n.', 'The state of being a father; the character or authority\n   of a father; paternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathers-in-law', 'pl. ', 'of Father-in-law');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father-in-law', 'n.', 'The father of ones husband or wife; -- correlative\n   to son-in-law and daughter-in-law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherland', 'n.', 'Ones native land; the native land of ones fathers or\n   ancestors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father-lasher', 'n.', 'A European marine fish (Cottus bubalis), allied to\n   the sculpin; -- called also lucky proach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherless', 'a.', 'Destitute of a living father; as, a fatherless child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherless', 'a.', 'Without a known author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherlessness', 'n.', 'The state of being without a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherliness', 'n.', 'The qualities of a father; parantal kindness, care,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Father longlegs', '', 'See Daddy longlegs, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherly', 'a.', 'Like a father in affection and care; paternal; tender;\n   protecting; careful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatherly', 'a.', 'Of or pertaining to a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathership', 'n.', 'The state of being a father; fatherhood; paternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathom', 'n.', 'A measure of length, containing six feet; the space to\n   which a man can extend his arms; -- used chiefly in measuring cables,\n   cordage, and the depth of navigable water by soundings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathom', 'n.', 'The measure or extant of ones capacity; depth, as of\n   intellect; profundity; reach; penetration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathomed', 'imp. & p. p.', 'of Fathom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathoming', 'p. pr. & vb. n.', 'of Fathom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathom', 'v. t.', 'To encompass with the arms extended or encircling; to\n   measure by throwing the arms about; to span.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathom', 'v. t.', 'The measure by a sounding line; especially, to sound the\n   depth of; to penetrate, measure, and comprehend; to get to the bottom\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathomable', 'a.', 'Capable of being fathomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathomer', 'n.', 'One who fathoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathomless', 'a.', 'Incapable of being fathomed; immeasurable; that can not\n   be sounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fathomless', 'a.', 'Incomprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatidical', 'a.', 'Having power to foretell future events; prophetic;\n   fatiloquent; as, the fatidical oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatiferous', 'a.', 'Fate-bringing; deadly; mortal; destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigable', 'a.', 'Easily tired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigate', 'a.', 'Wearied; tired; fatigued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigate', 'v. t.', 'To weary; to tire; to fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigation', 'n.', 'Weariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigue', 'n.', 'Weariness from bodily labor or mental exertion; lassitude\n   or exhaustion of strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigue', 'n.', 'The cause of weariness; labor; toil; as, the fatigues of\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigue', 'n.', 'The weakening of a metal when subjected to repeated\n   vibrations or strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigued', 'imp. & p. p.', 'of Fatigue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatiguing', 'p. pr. & vb. n.', 'of Fatigue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatigue', 'n.', 'To weary with labor or any bodily or mental exertion; to\n   harass with toil; to exhaust the strength or endurance of; to tire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatiloquent', 'a.', 'Prophetic; fatidical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatiloquist', 'n.', 'A fortune teller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatimite', 'a.', 'Alt. of Fatimide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatimide', 'a.', 'Descended from Fatima, the daughter and only child of\n   Mohammed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatimide', 'n.', 'A descendant of Fatima.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatiscence', 'n.', 'A gaping or opening; state of being chinky, or having\n   apertures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat-kidneyed', 'a.', 'Gross; lubberly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatling', 'n.', 'A calf, lamb, kid, or other young animal fattened for\n   slaughter; a fat animal; -- said of such animals as are used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatly', 'adv.', 'Grossly; greasily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatner', 'n.', 'One who fattens. [R.] See Fattener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatness', 'n.', 'The quality or state of being fat, plump, or full-fed;\n   corpulency; fullness of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatness', 'n.', 'Hence; Richness; fertility; fruitfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatness', 'n.', 'That which makes fat or fertile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fattened', 'imp. & p. p.', 'of Fatten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fattining', 'p. pr. & vb. n.', 'of Fatten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatten', 'v. t.', 'To make fat; to feed for slaughter; to make fleshy or\n   plump with fat; to fill full; to fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatten', 'v. t.', 'To make fertile and fruitful; to enrich; as, to fatten\n   land; to fatten fields with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatten', 'v. i.', 'To grow fat or corpulent; to grow plump, thick, or\n   fleshy; to be pampered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fattener', 'n.', 'One who, or that which, fattens; that which gives fatness\n   or fertility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fattiness', 'n.', 'State or quality of being fatty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fattish', 'a.', 'Somewhat fat; inclined to fatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatty', 'a.', 'Containing fat, or having the qualities of fat; greasy;\n   gross; as, a fatty substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatuitous', 'a.', 'Stupid; fatuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatuity', 'n.', 'Weakness or imbecility of mind; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatuous', 'a.', 'Feeble in mind; weak; silly; stupid; foolish; fatuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fatuous', 'a.', 'Without reality; illusory, like the ignis fatuus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fat-wited', 'a.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faubourg', 'n.', 'A suburb of French city; also, a district now within a\n   city, but formerly without its walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faucal', 'a.', 'Pertaining to the fauces, or opening of the throat;\n   faucial; esp., (Phon.) produced in the fauces, as certain deep guttural\n   sounds found in the Semitic and some other languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauces', 'n.pl.', 'The narrow passage from the mouth to the pharynx,\n   situated between the soft palate and the base of the tongue; -- called\n   also the isthmus of the fauces. On either side of the passage two\n   membranous folds, called the pillars of the fauces, inclose the\n   tonsils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauces', 'n.pl.', 'The throat of a calyx, corolla, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauces', 'n.pl.', 'That portion of the interior of a spiral shell which can\n   be seen by looking into the aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faucet', 'n.', 'A fixture for drawing a liquid, as water, molasses, oil,\n   etc., from a pipe, cask, or other vessel, in such quantities as may be\n   desired; -- called also tap, and cock. It consists of a tubular spout,\n   stopped with a movable plug, spigot, valve, or slide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faucet', 'n.', 'The enlarged end of a section of pipe which receives the\n   spigot end of the next section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauchion', 'n.', 'See Falchion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faucial', 'a.', 'Pertaining to the fauces; pharyngeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faugh', 'interj.', 'An exclamation of contempt, disgust, or abhorrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulchion', 'n.', 'See Falchion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulcon', 'n.', 'See Falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauld', 'n.', 'The arch over the dam of a blast furnace; the tymp arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faule', 'n.', 'A fall or falling band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'Defect; want; lack; default.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'Anything that fails, that is wanting, or that impairs\n   excellence; a failing; a defect; a blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'A moral failing; a defect or dereliction from duty; a\n   deviation from propriety; an offense less serious than a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'A dislocation of the strata of the vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'In coal seams, coal rendered worthless by impurities in the\n   seam; as, slate fault, dirt fault, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'A lost scent; act of losing the scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'n.', 'Failure to serve the ball into the proper court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulted', 'imp. & p. p.', 'of Fault');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulting', 'p. pr. & vb. n.', 'of Fault');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'v. t.', 'To charge with a fault; to accuse; to find fault with; to\n   blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'v. t.', 'To interrupt the continuity of (rock strata) by\n   displacement along a plane of fracture; -- chiefly used in the p. p.;\n   as, the coal beds are badly faulted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault', 'v. i.', 'To err; to blunder, to commit a fault; to do wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulter', 'n.', 'One who commits a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault-finder', 'n.', 'One who makes a practice of discovering others\n   faults and censuring them; a scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fault-finding', 'n.', 'The act of finding fault or blaming; -- used\n   derogatively. Also Adj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faultful', 'a.', 'Full of faults or sins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faultily', 'adv.', 'In a faulty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faultiness', 'n.', 'Quality or state of being faulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulting', 'n.', 'The state or condition of being faulted; the process by\n   which a fault is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faultless', 'a.', 'Without fault; not defective or imperfect; free from\n   blemish; free from incorrectness, vice, or offense; perfect; as, a\n   faultless poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulty', 'a.', 'Containing faults, blemishes, or defects; imperfect; not\n   fit for the use intended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faulty', 'a.', 'Guilty of a fault, or of faults; hence, blamable; worthy of\n   censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faun', 'n.', 'A god of fields and shipherds, diddering little from the\n   satyr. The fauns are usually represented as half goat and half man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauna', 'n.', 'The animals of any given area or epoch; as, the fauna of\n   America; fossil fauna; recent fauna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faunal', 'a.', 'Relating to fauna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faunist', 'n.', 'One who describes the fauna of country; a naturalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauni', 'pl. ', 'of Faunus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faunus', 'n.', 'See Faun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fausen', 'n.', 'A young eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fausse-braye', 'n.', 'A second raampart, exterior to, and parallel to, the\n   main rampart, and considerably below its level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauteuil', 'n.', 'An armchair; hence (because the members sit in fauteuils\n   or armchairs), membership in the French Academy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauteuil', 'n.', 'Chair of a presiding officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fautor', 'n.', 'A favorer; a patron; one who gives countenance or support;\n   an abettor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fautress', 'n.', 'A patroness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauvette', 'n.', 'A small singing bird, as the nightingale and warblers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fauces', 'pl. ', 'of Faux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faux', 'n.', 'See Fauces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'faux pas', '', 'A false step; a mistake or wrong measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favaginous', 'a.', 'Formed like, or resembling, a honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favas', 'n.', 'See Favus, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favel', 'a.', 'Yellow; fal/ow; dun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favel', 'n.', 'A horse of a favel or dun color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favel', 'n.', 'Flattery; cajolery; deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favella', 'n.', 'A group of spores arranged without order and covered with\n   a thin gelatinous envelope, as in certain delicate red algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faveolate', 'a.', 'Honeycomb; having cavities or cells, somewhat resembling\n   those of a honeycomb; alveolate; favose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favillous', 'a.', 'Of or pertaining to ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favonian', 'a.', 'Pertaining to the west wind; soft; mild; gentle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'Kind regard; propitious aspect; countenance; friendly\n   disposition; kindness; good will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'The act of countenancing, or the condition of being\n   countenanced, or regarded propitiously; support; promotion;\n   befriending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'A kind act or office; kindness done or granted; benevolence\n   shown by word or deed; an act of grace or good will, as distinct from\n   justice or remuneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'Mildness or mitigation of punishment; lenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'The object of regard; person or thing favored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'A gift or represent; something bestowed as an evidence of\n   good will; a token of love; a knot of ribbons; something worn as a\n   token of affection; as, a marriage favor is a bunch or knot of white\n   ribbons or white flowers worn at a wedding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'Appearance; look; countenance; face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'Partiality; bias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'A letter or epistle; -- so called in civility or compliment;\n   as, your favor of yesterday is received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'Love locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favored', 'imp. & p. p.', 'of Favor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoring', 'p. pr. & vb. n.', 'of Favor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'To regard with kindness; to support; to aid, or to have the\n   disposition to aid, or to wish success to; to be propitious to; to\n   countenance; to treat with consideration or tenderness; to show\n   partiality or unfair bias towards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'To afford advantages for success to; to facilitate; as, a\n   weak place favored the entrance of the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favor', 'n.', 'To resemble in features; to have the aspect or looks of; as,\n   the child favors his father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorable', 'n.', 'Full of favor; favoring; manifesting partiality; kind;\n   propitious; friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorable', 'n.', 'Conducive; contributing; tending to promote or\n   facilitate; advantageous; convenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorable', 'n.', 'Beautiful; well-favored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favored', 'a.', 'Countenanced; aided; regarded with kidness; as, a favored\n   friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favored', 'a.', 'Having a certain favor or appearance; featured; as,\n   well-favored; hard-favored, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoredly', 'adv.', 'In a favored or a favorable manner; favorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoredness', 'n.', 'Appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorer', 'n.', 'One who favors; one who regards with kindness or\n   friendship; a well-wisher; one who assists or promotes success or\n   prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoress', 'n.', 'A woman who favors or gives countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoring', 'a.', 'That favors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorite', 'n.', 'A person or thing regarded with peculiar favor; one\n   treated with partiality; one preferred above others; especially, one\n   unduly loved, trusted, and enriched with favors by a person of high\n   rank or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorite', 'n.', 'Short curls dangling over the temples; -- fashionable in\n   the reign of Charles II.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorite', 'n.', 'The competitor (as a horse in a race) that is judged most\n   likely to win; the competitor standing highest in the betting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorite', 'a.', 'Regarded with particular affection, esteem, or\n   preference; as, a favorite walk; a favorite child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favoritism', 'n.', 'The disposition to favor and promote the interest of\n   one person or family, or of one class of men, to the neglect of others\n   having equal claims; partiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorless', 'a.', 'Unfavored; not regarded with favor; having no\n   countenance or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favorless', 'a.', 'Unpropitious; unfavorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favose', 'a.', 'Honeycombed. See Faveolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favose', 'a.', 'Of or pertaining to the disease called favus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favosite', 'a.', 'Like or pertaining to the genus Favosites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favosites', 'n.', 'A genus of fossil corals abundant in the Silurian and\n   Devonian rocks, having polygonal cells with perforated walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favus', 'n.', 'A disease of the scalp, produced by a vegetable parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Favus', 'n.', 'A tile or flagstone cut into an hexagonal shape to produce a\n   honeycomb pattern, as in a pavement; -- called also favas and sectila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawe', 'a.', 'Fain; glad; delighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawkner', 'n.', 'A falconer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'n.', 'A young deer; a buck or doe of the first year. See Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'n.', 'The young of an animal; a whelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'n.', 'A fawn color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'a.', 'Of the color of a fawn; fawn-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'v. i.', 'To bring forth a fawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawned', 'imp. & p. p.', 'of Fawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawning', 'p. pr. & vb. n.', 'of Fawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'v. i.', 'To court favor by low cringing, frisking, etc., as a dog;\n   to flatter meanly; -- often followed by on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn', 'n.', 'A servile cringe or bow; mean flattery; sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawn-colored', 'a.', 'Of the color of a fawn; light yellowish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawner', 'n.', 'One who fawns; a sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fawningly', 'adv.', 'In a fawning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faxed', 'a.', 'Hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fay', 'n.', 'A fairy; an elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fay', 'n.', 'Faith; as, by my fay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'fayed', 'imp. & p. p.', 'of Fay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faying', 'p. pr. & vb. n.', 'of Fay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fay', 'v. t.', 'To fit; to join; to unite closely, as two pieces of wood,\n   so as to make the surface fit together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fay', 'v. i.', 'To lie close together; to fit; to fadge; -- often with in,\n   into, with, or together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fayalite', 'n.', 'A black, greenish, or brownish mineral of the chrysolite\n   group. It is a silicate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fayence', 'n.', 'See Fa/ence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faytour', 'n.', 'See Faitour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faze', 'v. t.', 'See Feeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fazzolet', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaberry', 'n.', 'A gooseberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feague', 'v. t.', 'To beat or whip; to drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feal', 'a.', 'Faithful; loyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fealty', 'n.', 'Fidelity to ones lord; the feudal obligation by which the\n   tenant or vassal was bound to be faithful to his lord; the special oath\n   by which this obligation was assumed; fidelity to a superior power, or\n   to a government; loyality. It is no longer the practice to exact the\n   performance of fealty, as a feudal obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fealty', 'n.', 'Fidelity; constancy; faithfulness, as of a friend to a\n   friend, or of a wife to her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'A variant of Fere, a mate, a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'A painful emotion or passion excited by the expectation of\n   evil, or the apprehension of impending danger; apprehension; anxiety;\n   solicitude; alarm; dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'Apprehension of incurring, or solicitude to avoid, Gods\n   wrath; the trembling and awful reverence felt toward the Supreme Belng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'Respectful reverence for men of authority or worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'That which causes, or which is the object of, apprehension or\n   alarm; source or occasion of terror; danger; dreadfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feared', 'imp. & p. p.', 'of Fear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearing', 'p. pr. & vb. n.', 'of Fear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'To feel a painful apprehension of; to be afraid of; to\n   consider or expect with emotion of alarm or solicitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'To have a reverential awe of; to solicitous to avoid the\n   displeasure of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'To be anxious or solicitous for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'To suspect; to doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'n.', 'To affright; to terrify; to drive away or prevent approach of\n   by fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fear', 'v. i.', 'To be in apprehension of evil; to be afraid; to feel\n   anxiety on account of some expected evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearer', 'n.', 'One who fars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearful', 'a.', 'Full of fear, apprehension, or alarm; afraid; frightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearful', 'a.', 'inclined to fear; easily frightened; without courage;\n   timid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearful', 'a.', 'Indicating, or caused by, fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearful', 'a.', 'Inspiring fear or awe; exciting apprehension or terror;\n   terrible; frightful; dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearfully', 'adv.', 'In a fearful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearfulness', 'n.', 'The state of being fearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearless', 'a.', 'Free from fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearnaught', 'n.', 'A fearless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearnaught', 'n.', 'A stout woolen cloth of great thickness; dreadnaught;\n   also, a warm garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearsome', 'a.', 'Frightful; causing fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fearsome', 'a.', 'Easily frightened; timid; timorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasibilities', 'pl. ', 'of Feasibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasibility', 'n.', 'The quality of being feasible; practicability; also,\n   that which is feasible; as, before we adopt a plan, let us consider its\n   feasibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasible', 'a.', 'Capable of being done, executed, or effected;\n   practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasible', 'a.', 'Fit to be used or tailed, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'n.', 'A festival; a holiday; a solemn, or more commonly, a joyous,\n   anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'n.', 'A festive or joyous meal; a grand, ceremonious, or sumptuous\n   entertainment, of which many guests partake; a banquet characterized by\n   tempting variety and abundance of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'n.', 'That which is partaken of, or shared in, with delight;\n   something highly agreeable; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasted', 'imp. & p. p.', 'of Feast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feasting', 'p. pr. & vb. n.', 'of Feast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'n.', 'To eat sumptuously; to dine or sup on rich provisions,\n   particularly in large companies, and on public festivals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'n.', 'To be highly gratified or delighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'v. t.', 'To entertain with sumptuous provisions; to treat at the\n   table bountifully; as, he was feasted by the king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feast', 'v. t.', 'To delight; to gratify; as, to feast the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaster', 'n.', 'One who fares deliciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaster', 'n.', 'One who entertains magnificently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feastful', 'a.', 'Festive; festal; joyful; sumptuous; luxurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feat', 'n.', 'An act; a deed; an exploit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feat', 'n.', 'A striking act of strength, skill, or cunning; a trick; as,\n   feats of horsemanship, or of dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feat', 'v. t.', 'To form; to fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feat', 'n.', 'Dexterous in movements or service; skillful; neat; nice;\n   pretty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feat-bodied', 'a.', 'Having a feat or trim body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feateous', 'a.', 'Dexterous; neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'One of the peculiar dermal appendages, of several kinds,\n   belonging to birds, as contour feathers, quills, and down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'Kind; nature; species; -- from the proverbial phrase,\n   \"Birds of a feather,\" that is, of the same species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'The fringe of long hair on the legs of the setter and some\n   other dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'A tuft of peculiar, long, frizzly hair on a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'One of the fins or wings on the shaft of an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'A longitudinal strip projecting as a fin from an object,\n   to strengthen it, or to enter a channel in another object and thereby\n   prevent displacement sidwise but permit motion lengthwise; a spline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'A thin wedge driven between the two semicylindrical parts\n   of a divided plug in a hole bored in a stone, to rend the stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'n.', 'The angular adjustment of an oar or paddle-wheel float,\n   with reference to a horizontal axis, as it leaves or enters the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathered', 'imp. & p. p.', 'of Feather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathering.', 'p. pr. & vb. n.', 'of Feather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. t.', 'To furnish with a feather or feathers, as an arrow or a\n   cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. t.', 'To adorn, as with feathers; to fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. t.', 'To render light as a feather; to give wings to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. t.', 'To enrich; to exalt; to benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. t.', 'To tread, as a cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. i.', 'To grow or form feathers; to become feathered; -- often\n   with out; as, the birds are feathering out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. i.', 'To curdle when poured into another liquid, and float\n   about in little flakes or \"feathers;\" as, the cream feathers');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. i.', 'To turn to a horizontal plane; -- said of oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather', 'v. i.', 'To have the appearance of a feather or of feathers; to\n   be or to appear in feathery form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-brained/', 'a.', 'Giddy; frivolous; feather-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathered', 'a.', 'Clothed, covered, or fitted with (or as with) feathers\n   or wings; as, a feathered animal; a feathered arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathered', 'a.', 'Furnished with anything featherlike; ornamented;\n   fringed; as, land feathered with trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathered', 'a.', 'Having a fringe of feathers, as the legs of certian\n   birds; or of hairs, as the legs of a setter dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathered', 'a.', 'Having feathers; -- said of an arrow, when the feathers\n   are of a tincture different from that of the shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-edge/', 'n.', 'The thin, new growth around the edge of a shell, of\n   an oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-edge/', 'n.', 'Any thin, as on a board or a razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-edged/', 'a.', 'Having a feather-edge; also, having one edge\n   thinner than the other, as a board; -- in the United States, said only\n   of stuff one edge of which is made as thin as practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-few/', 'n.', 'Feverfew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-foil', 'n.', 'An aquatic plant (Hottonia palustris), having finely\n   divided leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-head', 'n.', 'A frivolous or featherbrained person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-headed', 'a.', 'Giddy; frivolous; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-heeled', 'a.', 'Light-heeled; gay; frisky; frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featherness', 'n.', 'The state or condition of being feathery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathering', 'n.', 'Same as Foliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathering', 'n.', 'The act of turning the blade of the oar, as it rises\n   from the water in rowing, from a vertical to a horizontal position. See\n   To feather an oar, under Feather, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathering', 'v. t.', 'A covering of feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featherless', 'a.', 'Destitute of feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featherly', 'a.', 'Like feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-pated', 'a.', 'Feather-headed; frivolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feather-veined', 'a.', 'Having the veins (of a leaf) diverging from the two\n   sides of a midrib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feathery', 'a.', 'Pertaining to, or resembling, feathers; covered with, or\n   as with, feathers; as, feathery spray or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featly', 'a.', 'Neatly; dexterously; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featness', 'n.', 'Skill; adroitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feature', 'n.', 'The make, form, or outward appearance of a person; the\n   whole turn or style of the body; esp., good appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feature', 'n.', 'The make, cast, or appearance of the human face, and\n   especially of any single part of the face; a lineament. (pl.) The face,\n   the countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feature', 'n.', 'The cast or structure of anything, or of any part of a\n   thing, as of a landscape, a picture, a treaty, or an essay; any marked\n   peculiarity or characteristic; as, one of the features of the\n   landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feature', 'n.', 'A form; a shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featured', 'a.', 'Shaped; fashioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featured', 'a.', 'Having features; formed into features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featureless', 'a.', 'Having no distinct or distinctive features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Featurely', 'a.', 'Having features; showing marked peculiarities; handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feazed', 'imp. & p. p.', 'of Feaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feazing', 'p. pr. & vb. n.', 'of Feaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaze', 'v. t.', 'To untwist; to unravel, as the end of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaze', 'v. t.', 'To beat; to chastise; also, to humble; to harass; to\n   worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feaze', 'n.', 'A state of anxious or fretful excitement; worry; vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feazings', 'v. t.', 'The unlaid or ragged end of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febricitate', 'v. i.', 'To have a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febriculose', 'a.', 'Somewhat feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrifacient', 'a.', 'Febrific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrifacient', 'n.', 'That which causes fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febriferous', 'a.', 'Causing fever; as, a febriferous locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrific', 'a.', 'Producing fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrifugal', 'a.', 'Having the quality of mitigating or curing fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrifuge', 'n.', 'A medicine serving to mitigate or remove fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrifuge', 'a.', 'Antifebrile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Febrile', 'a.', 'Pertaining to fever; indicating fever, or derived from it;\n   as, febrile symptoms; febrile action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'February', 'n.', 'The second month in the year, said to have been\n   introduced into the Roman calendar by Numa. In common years this month\n   contains twenty-eight days; in the bissextile, or leap year, it has\n   twenty-nine days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Februation', 'n.', 'Purification; a sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecal', 'a.', 'relating to, or containing, dregs, feces, or ordeure;\n   faecal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecche', 'v. t.', 'To fetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feces', 'n. pl.', 'dregs; sediment; excrement. See FAeces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecial', 'a.', 'Pertaining to heralds, declarations of war, and treaties of\n   peace; as, fecial law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecifork', 'n.', 'The anal fork on which the larvae of certain insects\n   carry their faeces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feckless', 'a.', 'Spiritless; weak; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecks', 'n.', 'A corruption of the word faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'FeculAe', 'pl. ', 'of Fecula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecula', 'n.', 'Any pulverulent matter obtained from plants by simply\n   breaking down the texture, washing with water, and subsidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecula', 'n.', 'The nutritious part of wheat; starch or farina; -- called\n   also amylaceous fecula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecula', 'n.', 'The green matter of plants; chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feculence', 'n.', 'The state or quality of being feculent; muddiness;\n   foulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feculence', 'n.', 'That which is feculent; sediment; lees; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feculency', 'n.', 'Feculence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feculent', 'a.', 'Foul with extraneous or impure substances; abounding with\n   sediment or excrementitious matter; muddy; thick; turbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecund', 'a.', 'Fruitful in children; prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundated', 'imp. & p. p.', 'of Fecundate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundating', 'p. pr. & vb. n.', 'of Fecundate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundate', 'v. t.', 'To make fruitful or prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundate', 'v. t.', 'To render fruitful or prolific; to impregnate; as, in\n   flowers the pollen fecundates the ovum through the stigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundation', 'n.', 'The act by which, either in animals or plants,\n   material prepared by the generative organs the female organism is\n   brought in contact with matter from the organs of the male, so that a\n   new organism results; impregnation; fertilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundify',    'v. t.',    'To make fruitful; to fecundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundity',    'n.',    'The quality or power of producing fruit; fruitfulness;\n   especially (Biol.), the quality in female organisms of reproducing\n   rapidly and in great numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundity',    'n.',    'The power of germinating; as in seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fecundity',    'n.',    'The power of bringing forth in abundance; fertility;\n   richness of invention; as, the fecundity of Gods creative power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fed','','imp. & p. p. of Feed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fedary','n.','A feodary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federal',    'a.',    'Pertaining to a league or treaty; derived from an\n   agreement or covenant between parties, especially between nations;\n   constituted by a compact between parties, usually governments or their\n   representatives.');
