INSERT INTO words (word, word_type, description)VALUES  (    'Federal',    'a.',    'Composed of states or districts which retain only a\n   subordinate and limited sovereignty, as the Union of the United States,\n   or the Sonderbund of Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federal', 'a.', 'Consisting or pertaining to such a government; as, the\n   Federal Constitution; a Federal officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federal', 'a.', 'Friendly or devoted to such a government; as, the Federal\n   party. see Federalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federal', 'n.', 'See Federalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federalism', 'n.', 'the principles of Federalists or of federal union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federalist', 'n.', 'An advocate of confederation; specifically (Amer.\n   Hist.), a friend of the Constitution of the United States at its\n   formation and adoption; a member of the political party which favored\n   the administration of president Washington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federalized', 'imp. & p. p.', 'of Federalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federalizing', 'p. pr. & vb. n.', 'of Federalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federalize', 'v. t.', 'To unite in compact, as different States; to\n   confederate for political purposes; to unite by or under the Federal\n   Constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federary', 'n.', 'A partner; a confederate; an accomplice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federate', 'a.', 'United by compact, as sovereignties, states, or nations;\n   joined in confederacy; leagued; confederate; as, federate nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federation', 'n.', 'The act of uniting in a league; confederation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federation', 'n.', 'A league; a confederacy; a federal or confederated\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Federative', 'a.', 'Uniting in a league; forming a confederacy; federal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fedity', 'n.', 'Turpitude; vileness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'n.', 'property; possession; tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'n.', 'Reward or compensation for services rendered or to be\n   rendered; especially, payment for professional services, of optional\n   amount, or fixed by custom or laws; charge; pay; perquisite; as, the\n   fees of lawyers and physicians; the fees of office; clerks fees;\n   sheriffs fees; marriage fees, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'n.', 'A right to the use of a superiors land, as a stipend for\n   services to be performed; also, the land so held; a fief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'n.', 'An estate of inheritance supposed to be held either mediately\n   or immediately from the sovereign, and absolutely vested in the owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'n.', 'An estate of inheritance belonging to the owner, and\n   transmissible to his heirs, absolutely and simply, without condition\n   attached to the tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'imp. & p. p.', 'of Fee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeing', 'p. pr. & vb. n.', 'of Fee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee', 'v. t.', 'To reward for services performed, or to be performed; to\n   recompense; to hire or keep in hire; hence, to bribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeble', 'superl.', 'Deficient in physical strength; weak; infirm;\n   debilitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeble', 'superl.', 'Wanting force, vigor, or efficiency in action or\n   expression; not full, loud, bright, strong, rapid, etc.; faint; as, a\n   feeble color; feeble motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeble', 'v. t.', 'To make feble; to enfeeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeble-minded', 'a.', 'Weak in intellectual power; wanting firmness or\n   constancy; irresolute; vacilating; imbecile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feebleness', 'n.', 'The quality or condition of being feeble; debility;\n   infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feebly', 'adv.', 'In a feeble manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fed', 'imp. & p. p.', 'of Feed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeding', 'p. pr. & vb. n.', 'of Feed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To give food to; to supply with nourishment; to satisfy\n   the physical huger of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To satisfy; grafity or minister to, as any sense, talent,\n   taste, or desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To fill the wants of; to supply with that which is used or\n   wasted; as, springs feed ponds; the hopper feeds the mill; to feed a\n   furnace with coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To nourish, in a general sense; to foster, strengthen,\n   develop, and guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To graze; to cause to be cropped by feeding, as herbage by\n   cattle; as, if grain is too forward in autumn, feed it with sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To give for food, especially to animals; to furnish for\n   consumption; as, to feed out turnips to the cows; to feed water to a\n   steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To supply (the material to be operated upon) to a machine;\n   as, to feed paper to a printing press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. t.', 'To produce progressive operation upon or with (as in wood\n   and metal working machines, so that the work moves to the cutting tool,\n   or the tool to the work).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. i.', 'To take food; to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. i.', 'To subject by eating; to satisfy the appetite; to feed\n   ones self (upon something); to prey; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. i.', 'To be nourished, strengthened, or satisfied, as if by\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'v. i.', 'To place cattle to feed; to pasture; to graze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'That which is eaten; esp., food for beasts; fodder; pasture;\n   hay; grain, ground or whole; as, the best feed for sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'A grazing or pasture ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'An allowance of provender given to a horse, cow, etc.; a\n   meal; as, a feed of corn or oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'A meal, or the act of eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'The water supplied to steam boilers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'The motion, or act, of carrying forward the stuff to be\n   operated upon, as cloth to the needle in a sewing machine; or of\n   producing progressive operation upon any material or object in a\n   machine, as, in a turning lathe, by moving the cutting tool along or in\n   the work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'The supply of material to a machine, as water to a steam\n   boiler, coal to a furnace, or grain to a run of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feed', 'n.', 'The mechanism by which the action of feeding is produced; a\n   feed motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'One who, or that which, gives food or supplies nourishment;\n   steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'One who furnishes incentives; an encourager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'One who eats or feeds; specifically, an animal to be fed or\n   fattened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'One who fattens cattle for slaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'A stream that flows into another body of water; a\n   tributary; specifically (Hydraulic Engin.), a water course which\n   supplies a canal or reservoir by gravitation or natural flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'A branch railroad, stage line, or the like; a side line\n   which increases the business of the main line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'A small lateral lode falling into the main lode or mineral\n   vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'A strong discharge of gas from a fissure; a blower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'An auxiliary part of a machine which supplies or leads\n   along the material operated upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeder', 'n.', 'A device for supplying steam boilers with water as needed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeding', 'n.', 'the act of eating, or of supplying with food; the process\n   of fattening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeding', 'n.', 'That which is eaten; food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeding', 'n.', 'That which furnishes or affords food, especially for\n   animals; pasture land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fee-faw-fum', 'n.', 'A nonsensical exclamation attributed to giants and\n   ogres; hence, any expression calculated to impose upon the timid and\n   ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feejee', 'a. & n.', 'See Fijian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'imp. & p. p.', 'of Feel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'p. pr. & vb. n.', 'of Feel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. t.', 'To perceive by the touch; to take cognizance of by means\n   of the nerves of sensation distributed all over the body, especially by\n   those of the skin; to have sensation excited by contact of (a thing)\n   with the body or limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. t.', 'To touch; to handle; to examine by touching; as, feel this\n   piece of silk; hence, to make trial of; to test; often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. t.', 'To perceive by the mind; to have a sense of; to\n   experience; to be affected by; to be sensible of, or sensetive to; as,\n   to feel pleasure; to feel pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. t.', 'To take internal cognizance of; to be conscious of; to\n   have an inward persuasion of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. t.', 'To perceive; to observe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. i.', 'To have perception by the touch, or by contact of anything\n   with the nerves of sensation, especially those upon the surface of the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. i.', 'To have the sensibilities moved or affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. i.', 'To be conscious of an inward impression, state of mind,\n   persuasion, physical condition, etc.; to perceive ones self to be; --\n   followed by an adjective describing the state, etc.; as, to feel\n   assured, grieved, persuaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. i.', 'To know with feeling; to be conscious; hence, to know\n   certainly or without misgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'v. i.', 'To appear to the touch; to give a perception; to produce\n   an impression by the nerves of sensation; -- followed by an adjective\n   describing the kind of sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'n.', 'Feeling; perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feel', 'n.', 'A sensation communicated by touching; impression made upon\n   one who touches or handles; as, this leather has a greasy feel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeler', 'n.', 'One who, or that which, feels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeler', 'n.', 'One of the sense organs or certain animals (as insects),\n   which are used in testing objects by touch and in searching for food;\n   an antenna; a palp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeler', 'n.', 'Anything, as a proposal, observation, etc., put forth or\n   thrown out in order to ascertain the views of others; something\n   tentative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'a.', 'Possessing great sensibility; easily affected or moved;\n   as, a feeling heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'a.', 'Expressive of great sensibility; attended by, or evincing,\n   sensibility; as, he made a feeling representation of his wrongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'n.', 'The sense by which the mind, through certain nerves of the\n   body, perceives external objects, or certain states of the body itself;\n   that one of the five senses which resides in the general nerves of\n   sensation distributed over the body, especially in its surface; the\n   sense of touch; nervous sensibility to external objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'n.', 'An act or state of perception by the sense above\n   described; an act of apprehending any object whatever; an act or state\n   of apprehending the state of the soul itself; consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'n.', 'The capacity of the soul for emotional states; a high\n   degree of susceptibility to emotions or states of the sensibility not\n   dependent on the body; as, a man of feeling; a man destitute of\n   feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'n.', 'Any state or condition of emotion; the exercise of the\n   capacity for emotion; any mental state whatever; as, a right or a wrong\n   feeling in the heart; our angry or kindly feelings; a feeling of pride\n   or of humility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeling', 'n.', 'That quality of a work of art which embodies the mental\n   emotion of the artist, and is calculated to affect similarly the\n   spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feelingly', 'adv.', 'In a feeling manner; pathetically; sympathetically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feere', 'n.', 'A consort, husband or wife; a companion; a fere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feese', 'n.', 'the short run before a leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feet', 'n. pl.', 'See Foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feet', 'n.', 'Fact; performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feetless', 'a.', 'Destitute of feet; as, feetless birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeze', 'v. t.', 'To turn, as a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeze', 'v. t.', 'To beat; to chastise; to humble; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feeze', 'n.', 'Fretful excitement. [Obs.] See Feaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fehling', 'n.', 'See Fehlings solution, under Solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fehmic', 'a.', 'See Vehmic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feigned', 'imp. & p. p.', 'of Feign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feigning', 'p. pr. & vb. n.', 'of Feign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feign', 'v. t.', 'To give a mental existence to, as to something not real\n   or actual; to imagine; to invent; hence, to pretend; to form and relate\n   as if true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feign', 'v. t.', 'To represent by a false appearance of; to pretend; to\n   counterfeit; as, to feign a sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feign', 'v. t.', 'To dissemble; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feigned', 'a.', 'Not real or genuine; pretended; counterfeit; insincere;\n   false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feigner', 'n.', 'One who feigns or pretends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feigning', 'a.', 'That feigns; insincere; not genuine; false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feine', 'v. t. & i.', 'To feign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feint', 'a.', 'Feigned; counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feint', 'a.', 'That which is feigned; an assumed or false appearance; a\n   pretense; a stratagem; a fetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feint', 'a.', 'A mock blow or attack on one part when another part is\n   intended to be struck; -- said of certain movements in fencing, boxing,\n   war, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feint', 'v. i.', 'To make a feint, or mock attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feitsui', 'n.', 'The Chinese name for a highly prized variety of pale green\n   jade. See Jade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feize', 'v. t.', 'See Feeze, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felanders', 'n. pl.', 'See Filanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feldspar', 'n.', 'Alt. of Feldspath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feldspath', 'n.', 'A name given to a group of minerals, closely related in\n   crystalline form, and all silicates of alumina with either potash,\n   soda, lime, or, in one case, baryta. They occur in crystals and\n   crystalline masses, vitreous in luster, and breaking rather easily in\n   two directions at right angles to each other, or nearly so. The colors\n   are usually white or nearly white, flesh-red, bluish, or greenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feldspathic', 'a.', 'Alt. of Feldspathose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feldspathose', 'a.', 'Pertaining to, or consisting of, feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fele', 'a.', 'Many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fe-licify', 'v. t.', 'To make happy; to felicitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitate', 'a.', 'Made very happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitated', 'imp. & p. p.', 'of Felicitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'felicitating', 'p. pr. & vb. n.', 'of Felicitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitate', 'v. t.', 'To make very happy; to delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitate', 'v. t.', 'To express joy or pleasure to; to wish felicity to;\n   to call or consider (ones self) happy; to congratulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitation', 'n.', 'The act of felicitating; a wishing of joy or\n   happiness; congratulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicitous', 'a.', 'Characterized by felicity; happy; prosperous;\n   delightful; skilful; successful; happily applied or expressed;\n   appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicities', 'pl. ', 'of Felicity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicity', 'n.', 'The state of being happy; blessedness; blissfulness;\n   enjoyment of good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicity', 'n.', 'That which promotes happiness; a successful or gratifying\n   event; prosperity; blessing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felicity', 'n.', 'A pleasing faculty or accomplishment; as, felicity in\n   painting portraits, or in writing or talking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feline', 'a.', 'Catlike; of or pertaining to the genus Felis, or family\n   Felidae; as, the feline race; feline voracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feline', 'a.', 'Characteristic of cats; sly; stealthy; treacherous; as, a\n   feline nature; feline manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felis', 'n.', 'A genus of carnivorous mammals, including the domestic cat,\n   the lion, tiger, panther, and similar animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', '', 'imp. of Fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'a.', 'Cruel; barbarous; inhuman; fierce; savage; ravenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'a.', 'Eager; earnest; intent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'a.', 'Gall; anger; melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'A skin or hide of a beast with the wool or hair on; a pelt;\n   -- used chiefly in composition, as woolfell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'A barren or rocky hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'A wild field; a moor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felled', 'imp. & p. p.', 'of Fell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felling', 'p. pr. & vb. n.', 'of Fell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'v. i.', 'To cause to fall; to prostrate; to bring down or to the\n   ground; to cut down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'The finer portions of ore which go through the meshes, when\n   the ore is sorted by sifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'v. t.', 'To sew or hem; -- said of seams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'A form of seam joining two pieces of cloth, the edges being\n   folded together and the stitches taken through both thicknesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fell', 'n.', 'The end of a web, formed by the last thread of the weft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellable', 'a.', 'Fit to be felled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellahin', 'pl. ', 'of Fellah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellahs', 'pl. ', 'of Fellah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellah', 'n.', 'A peasant or cultivator of the soil among the Egyptians,\n   Syrians, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feller', 'n.', 'One who, or that which, fells, knocks or cuts down; a\n   machine for felling trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feller', 'n.', 'An appliance to a sewing machine for felling a seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felltare', 'n.', 'The fieldfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felliflu-ous', 'a.', 'Flowing with gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellinic', 'a.', 'Of, relating to, or derived from, bile or gall; as,\n   fellinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellmonger', 'n.', 'A dealer in fells or sheepskins, who separates the wool\n   from the pelts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellness', 'n.', 'The quality or state of being fell or cruel; fierce\n   barbarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felloe', 'n.', 'See Felly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellon', 'n.', 'Variant of Felon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'A companion; a comrade; an associate; a partner; a sharer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'A man without good breeding or worth; an ignoble or mean\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'An equal in power, rank, character, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'One of a pair, or of two things used together or suited to\n   each other; a mate; the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'A person; an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'In the English universities, a scholar who is appointed to\n   a foundation called a fellowship, which gives a title to certain\n   perquisites and privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'In an American college or university, a member of the\n   corporation which manages its business interests; also, a graduate\n   appointed to a fellowship, who receives the income of the foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'n.', 'A member of a literary or scientific society; as, a Fellow\n   of the Royal Society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow', 'v. t.', 'To suit with; to pair with; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow-commoner', 'n.', 'A student at Cambridge University, England, who\n   commons, or dines, at the Fellows table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow-creature', 'n.', 'One of the same race or kind; one made by the same\n   Creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowfeel', 'v. t.', 'To share through sympathy; to participate in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow-feeling', 'n.', 'Sympathy; a like feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellow-feeling', 'n.', 'Joint interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowless', 'a.', 'Without fellow or equal; peerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowlike', 'a.', 'Like a companion; companionable; on equal terms;\n   sympathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowly', 'a.', 'Fellowlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'The state or relation of being or associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'Companionship of persons on equal and friendly terms;\n   frequent and familiar intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'A state of being together; companionship; partnership;\n   association; hence, confederation; joint interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'Those associated with one, as in a family, or a\n   society; a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'A foundation for the maintenance, on certain\n   conditions, of a scholar called a fellow, who usually resides at the\n   university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'n.', 'The rule for dividing profit and loss among partners;\n   -- called also partnership, company, and distributive proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowshiped', 'imp. & p. p.', 'of Fellowship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowshiping', 'p. pr. & vb. n.', 'of Fellowship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellowship', 'v. t.', 'To acknowledge as of good standing, or in communion\n   according to standards of faith and practice; to admit to Christian\n   fellowship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felly', 'adv.', 'In a fell or cruel manner; fiercely; barbarously;\n   savagely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fellies', 'pl. ', 'of Felly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felly', 'n.', 'The exterior wooden rim, or a segment of the rim, of a\n   wheel, supported by the spokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felos-de-se', 'pl. ', 'of Felo-de-se');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felo-de-se', 'n.', 'One who deliberately puts an end to his own existence,\n   or loses his life while engaged in the commission of an unlawful or\n   malicious act; a suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felon', 'a.', 'A person who has committed a felony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felon', 'a.', 'A person guilty or capable of heinous crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felon', 'a.', 'A kind of whitlow; a painful imflammation of the periosteum\n   of a finger, usually of the last joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felon', 'a.', 'Characteristic of a felon; malignant; fierce; malicious;\n   cruel; traitorous; disloyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felonious', 'a.', 'Having the quality of felony; malignant; malicious;\n   villainous; traitorous; perfidious; in a legal sense, done with intent\n   to commit a crime; as, felonious homicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felonous', 'a.', 'Wicked; felonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felonry', 'n.', 'A body of felons; specifically, the convict population of\n   a penal colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felonwort', 'n.', 'The bittersweet nightshade (Solanum Dulcamara). See\n   Bittersweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felonies', 'pl. ', 'of Felony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felony', 'n.', 'An act on the part of the vassal which cost him his fee by\n   forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felony', 'n.', 'An offense which occasions a total forfeiture either lands\n   or goods, or both, at the common law, and to which capital or other\n   punishment may be added, according to the degree of guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felony', 'n.', 'A heinous crime; especially, a crime punishable by death or\n   imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To compound a felony', '', 'See under Compound, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felsite', 'n.', 'A finegrained rock, flintlike in fracture, consisting\n   essentially of orthoclase feldspar with occasional grains of quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felsitic', 'a.', 'relating to, composed of, or containing, felsite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felspar', 'n.', 'Alt. of Felspath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felspath', 'n.', 'See Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felspathic', 'a.', 'See Feldspathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felstone', 'n.', 'See Felsite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', '', 'imp. & p. p. / a. from Feel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'n.', 'A cloth or stuff made of matted fibers of wool, or wool and\n   fur, fulled or wrought into a compact substance by rolling and\n   pressure, with lees or size, without spinning or weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'n.', 'A hat made of felt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'n.', 'A skin or hide; a fell; a pelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felted', 'imp. & p. p.', 'of Felt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felting', 'p. pr. & vb. n.', 'of Felt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'v. t.', 'To make into felt, or a feltike substance; to cause to\n   adhere and mat together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felt', 'v. t.', 'To cover with, or as with, felt; as, to felt the cylinder\n   of a steam emgine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felter', 'v. t.', 'To clot or mat together like felt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felting', 'n.', 'The material of which felt is made; also, felted cloth;\n   also, the process by which it is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felting', 'n.', 'The act of splitting timber by the felt grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feltry', 'n.', 'See Felt, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felucca', 'n.', 'A small, swift-sailing vessel, propelled by oars and\n   lateen sails, -- once common in the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Felwort', 'n.', 'A European herb (Swertia perennis) of the Gentian family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female', 'n.', 'An individual of the sex which conceives and brings forth\n   young, or (in a wider sense) which has an ovary and produces ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female', 'n.', 'A plant which produces only that kind of reproductive\n   organs which are capable of developing into fruit after impregnation or\n   fertilization; a pistillate plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female', 'a.', 'Belonging to the sex which conceives and gives birth to\n   young, or (in a wider sense) which produces ova; not male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female', 'a.', 'Belonging to an individual of the female sex;\n   characteristic of woman; feminine; as, female tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female', 'a.', 'Having pistils and no stamens; pistillate; or, in\n   cryptogamous plants, capable of receiving fertilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female rhymes', '', 'double rhymes, or rhymes (called in French feminine\n   rhymes because they end in e weak, or feminine) in which two syllables,\n   an accented and an unaccented one, correspond at the end of each line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Female fern', '', 'a common species of fern with large decompound fronds\n   (Asplenium Filixfaemina), growing in many countries; lady fern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femalist', 'n.', 'A gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femalize', 'v. t.', 'To make, or to describe as, female or feminine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feme', 'n.', 'A woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femeral', 'n.', 'See Femerell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femerell', 'n.', 'A lantern, or louver covering, placed on a roof, for\n   ventilation or escape of smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminal', 'a.', 'Feminine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminality', 'n.', 'Feminity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminate', 'a.', 'Feminine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femineity', 'n.', 'Womanliness; femininity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminine', 'a.', 'Of or pertaining to a woman, or to women; characteristic\n   of a woman; womanish; womanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminine', 'a.', 'Having the qualities of a woman; becoming or appropriate\n   to the female sex; as, in a good sense, modest, graceful, affectionate,\n   confiding; or, in a bad sense, weak, nerveless, timid, pleasure-loving,\n   effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminine rhyme', '', 'See Female rhyme, under Female, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminine', 'n.', 'A woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminine', 'n.', 'Any one of those words which are the appellations of\n   females, or which have the terminations usually found in such words;\n   as, actress, songstress, abbess, executrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femininely', 'adv.', 'In a feminine manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminineness', 'n.', 'The quality of being feminine; womanliness;\n   womanishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femininity', 'n.', 'The quality or nature of the female sex; womanliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femininity', 'n.', 'The female form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminity', 'n.', 'Womanliness; femininity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminization', 'n.', 'The act of feminizing, or the state of being\n   feminized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminize', 'v. t.', 'To make womanish or effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feminye', 'n.', 'The people called Amazons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femme', 'n.', 'A woman. See Feme, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femoral', 'a.', 'Pertaining to the femur or thigh; as, the femoral artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femora', 'pl. ', 'of Femur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femur', 'n.', 'The thigh bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Femur', 'n.', 'The proximal segment of the hind limb containing the thigh\n   bone; the thigh. See Coxa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fen', 'n.', 'Low land overflowed, or covered wholly or partially with\n   water, but producing sedge, coarse grasses, or other aquatic plants;\n   boggy land; moor; marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'n.', 'That which fends off attack or danger; a defense; a\n   protection; a cover; security; shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'n.', 'An inclosure about a field or other space, or about any\n   object; especially, an inclosing structure of wood, iron, or other\n   material, intended to prevent intrusion from without or straying from\n   within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'n.', 'A projection on the bolt, which passes through the tumbler\n   gates in locking and unlocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'n.', 'Self-defense by the use of the sword; the art and practice\n   of fencing and sword play; hence, skill in debate and repartee. See\n   Fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'n.', 'A receiver of stolen goods, or a place where they are\n   received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fencing', 'imp. & p. p. Fenced ', '/); p. pr. & vb. n.) of Fence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'v. t.', 'To fend off danger from; to give security to; to protect;\n   to guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'v. t.', 'To inclose with a fence or other protection; to secure by\n   an inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'v. i.', 'To make a defense; to guard ones self of anything, as\n   against an attack; to give protection or security, as by a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'v. i.', 'To practice the art of attack and defense with the sword\n   or with the foil, esp. with the smallsword, using the point only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fence', 'v. i.', 'Hence, to fight or dispute in the manner of fencers, that\n   is, by thrusting, guarding, parrying, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenceful', 'a.', 'Affording defense; defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenceless', 'a.', 'Without a fence; uninclosed; open; unguarded;\n   defenseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fencer', 'n.', 'One who fences; one who teaches or practices the art of\n   fencing with sword or foil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenci-ble', 'a.', 'Capable of being defended, or of making or affording\n   defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fencible', 'n.', 'A soldier enlisted for home service only; -- usually in\n   the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fencing', 'n.', 'The art or practice of attack and defense with the sword,\n   esp. with the smallsword. See Fence, v. i., 2.');
