  INSERT INTO words (word, word_type, description) VALUES  (    'Fencing', 'v. i.', 'The act of building a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fencing', 'v. i.', 'The aggregate of the fences put up for inclosure or\n   protection; as, the fencing of a farm.');
