  INSERT INTO words (word, word_type, description) VALUES  (    'Fender', 'v. t. & i.', 'One who or that which defends or protects by\n   warding off harm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fender', 'v. t. & i.', 'A screen to prevent coals or sparks of an open fire\n   from escaping to the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fender', 'v. t. & i.', 'Anything serving as a cushion to lessen the shock\n   when a vessel comes in contact with another vessel or a wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fender', 'v. t. & i.', 'A screen to protect a carriage from mud thrown off\n   the wheels: also, a splashboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fender', 'v. t. & i.', 'Anything set up to protect an exposed angle, as of\n   a house, from damage by carriage wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fendliche', 'a.', 'Fiendlike.');
