  INSERT INTO words (word, word_type, description) VALUES  (    'Feneration', 'n.', 'The act of fenerating; interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenes-tella', 'n.', 'Any small windowlike opening or recess, esp. one to\n   show the relics within an altar, or the like.');
