  INSERT INTO words (word, word_type, description) VALUES  (    'Fenestrate', 'a.', 'Having transparent spots, as the wings of certain\n   butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenestrated', 'a.', 'Having windows; characterized by windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenestrated', 'a.', 'Same as Fenestrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenestration', 'n.', 'The arrangement and proportioning of windows; -- used\n   by modern writers for the decorating of an architectural composition by\n   means of the window (and door) openings, their ornaments, and\n   proportions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fenestration', 'n.', 'The state or condition of being fenestrated.');
