  INSERT INTO words (word, word_type, description) VALUES  (    'Fennel', 'n.', 'A perennial plant of the genus Faeniculum (F. vulgare),\n   having very finely divided leaves. It is cultivated in gardens for the\n   agreeable aromatic flavor of its seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fennish', 'a.', 'Abounding in fens; fenny.');
