  INSERT INTO words (word, word_type, description) VALUES  (    'Fenowed', 'a.', 'Corrupted; decayed; moldy. See Vinnewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fensi-ble', 'a.', 'Fencible.');
