  INSERT INTO words (word, word_type, description) VALUES  (    'Feodal', 'a.', 'Feudal. See Feudal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feodality', 'n.', 'Feudal tenure; the feudal system. See Feudality.');
