  INSERT INTO words (word, word_type, description) VALUES  (    'Feoffee', 'n.', 'The person to whom a feoffment is made; the person\n   enfeoffed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feoffment', 'n.', 'The grant of a feud or fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feoffment', 'n.', 'A gift or conveyance in fee of land or other corporeal\n   hereditaments, accompanied by actual delivery of possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feoffment', 'n.', 'The instrument or deed by which corporeal hereditaments\n   are conveyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feofor', 'n.', 'Alt. of Feoffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feoffer', 'n.', 'One who enfeoffs or grants a fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fer', 'a. & adv.', 'Far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feracious', 'a.', 'Fruitful; producing abundantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feracity', 'n.', 'The state of being feracious or fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferae', 'n. pl.', 'A group of mammals which formerly included the\n   Carnivora, Insectivora, Marsupialia, and lemurs, but is now often\n   restricted to the Carnivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferae naturae', '', 'Of a wild nature; -- applied to animals, as foxes,\n   wild ducks, etc., in which no one can claim property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feral', 'a.', 'Wild; untamed; ferine; not domesticated; -- said of beasts,\n   birds, and plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feral', 'a.', 'Funereal; deadly; fatal; dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferde', '', 'imp. of Fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fer-de-lance', 'n.', 'A large, venomous serpent (Trigonocephalus\n   lanceolatus) of Brazil and the West Indies. It is allied to the\n   rattlesnake, but has no rattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferding', 'n.', 'A measure of land mentioned in Domesday Book. It is\n   supposed to have consisted of a few acres only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferdness', 'n.', 'Fearfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fere', 'n.', 'A mate or companion; -- often used of a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fere', 'a.', 'Fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fere', 'n.', 'Fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fere', 'n.', 'Fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fere', 'v. t. & i.', 'To fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feretory', 'n.', 'A portable bier or shrine, variously adorned, used for\n   containing relics of saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferforth', 'adv.', 'Far forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferforthly', 'adv.', 'Ferforth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fergusonite', 'n.', 'A mineral of a brownish black color, essentially a\n   tantalo-niobate of yttrium, erbium, and cerium; -- so called after\n   Robert Ferguson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feriae', 'pl. ', 'of Feria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feria', 'n.', 'A week day, esp. a day which is neither a festival nor a\n   fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferial', 'n.', 'Same as Feria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferial', 'a.', 'Of or pertaining to holidays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferial', 'a.', 'Belonging to any week day, esp. to a day that is neither a\n   festival nor a fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feriation', 'n.', 'The act of keeping holiday; cessation from work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferie', 'n.', 'A holiday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferier', 'a.', 'compar. of Fere, fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferine', 'a.', 'Wild; untamed; savage; as, lions, tigers, wolves, and bears\n   are ferine beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferine', 'n.', 'A wild beast; a beast of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feringee', 'n.', 'The name given to Europeans by the Hindos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferity', 'n.', 'Wildness; savageness; fierceness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferly', 'n.', 'Singular; wonderful; extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferly', 'n.', 'A wonder; a marvel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermacy', 'n.', 'Medicine; pharmacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferm', 'n.', 'Alt. of Ferme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferme', 'n.', 'Rent for a farm; a farm; also, an abode; a place of\n   residence; as, he let his land to ferm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'n.', 'That which causes fermentation, as yeast, barm, or\n   fermenting beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'n.', 'Intestine motion; heat; tumult; agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'n.', 'A gentle internal motion of the constituent parts of a\n   fluid; fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermented', 'imp. & p. p.', 'of Ferment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermenting', 'p. pr. & vb. n.', 'of Ferment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'n.', 'To cause ferment of fermentation in; to set in motion; to\n   excite internal emotion in; to heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'v. i.', 'To undergo fermentation; to be in motion, or to be\n   excited into sensible internal motion, as the constituent oarticles of\n   an animal or vegetable fluid; to work; to effervesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferment', 'v. i.', 'To be agitated or excited by violent emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermentability', 'n.', 'Capability of fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermentable', 'a.', 'Capable of fermentation; as, cider and other vegetable\n   liquors are fermentable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermental', 'a.', 'Fermentative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermentation', 'n.', 'The process of undergoing an effervescent change, as\n   by the action of yeast; in a wider sense (Physiol. Chem.), the\n   transformation of an organic substance into new compounds by the action\n   of a ferment, either formed or unorganized. It differs in kind\n   according to the nature of the ferment which causes it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermentation', 'n.', 'A state of agitation or excitement, as of the\n   intellect or the feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermentative', 'a.', 'Causing, or having power to cause, fermentation;\n   produced by fermentation; fermenting; as, a fermentative process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermerere', 'n.', 'The officer in a religious house who had the care of the\n   infirmary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fermillet', 'n.', 'A buckle or clasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fern', 'adv.', 'Long ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fern', 'a.', 'Ancient; old. [Obs.] \"Pilgrimages to . . . ferne halwes.\"\n   [saints].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fern', 'n.', 'An order of cryptogamous plants, the Filices, which have\n   their fructification on the back of the fronds or leaves. They are\n   usually found in humid soil, sometimes grow epiphytically on trees, and\n   in tropical climates often attain a gigantic size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fernery', 'n.', 'A place for rearing ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fernticle', 'n.', 'A freckle on the skin, resembling the seed of fern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferny', 'a.', 'Abounding in ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferocious', 'a.', 'Fierce; savage; wild; indicating cruelty; ravenous;\n   rapacious; as, ferocious look or features; a ferocious lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferocity', 'n.', 'Savage wildness or fierceness; fury; cruelty; as,\n   ferocity of countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feroher', 'n.', 'A symbol of the solar deity, found on monuments exhumed in\n   Babylon, Nineveh, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferous', 'a.', 'Wild; savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ferous', '', 'A suffix signifying bearing, producing, yielding; as,\n   auriferous, yielding gold; chyliferous, producing chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrandine', 'n.', 'A stuff made of silk and wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrara', 'n.', 'A sword bearing the mark of one of the Ferrara family of\n   Italy. These swords were highly esteemed in England and Scotland in the\n   16th and 17th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrarese', 'a.', 'Pertaining to Ferrara, in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrarese', 'n., sing. & pl.', 'A citizen of Ferrara; collectively, the\n   inhabitants of Ferrara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrary', 'n.', 'The art of working in iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrate', 'n.', 'A salt of ferric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferre', 'a. & adv.', 'Alt. of Ferrer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrer', 'a. & adv.', 'compar. of Fer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferreous', 'a.', 'Partaking of, made of, or pertaining to, iron; like iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrest', 'a. & adv.', 'superl. of Fer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferret', 'n.', 'An animal of the Weasel family (Mustela / Putorius furo),\n   about fourteen inches in length, of a pale yellow or white color, with\n   red eyes. It is a native of Africa, but has been domesticated in\n   Europe. Ferrets are used to drive rabbits and rats out of their holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferreted', 'imp. & p. p.', 'of Ferret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferreting', 'p. pr. & vb. n.', 'of Ferret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferret', 'n.', 'To drive or hunt out of a lurking place, as a ferret does\n   the cony; to search out by patient and sagacious efforts; -- often used\n   with out; as, to ferret out a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferret', 'n.', 'A kind of narrow tape, usually made of woolen; sometimes of\n   cotton or silk; -- called also ferreting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferret', 'n.', 'The iron used for trying the melted glass to see if is fit\n   to work, and for shaping the rings at the mouths of bottles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferreter', 'n.', 'One who ferrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferret-eye', 'n.', 'The spur-winged goose; -- so called from the red circle\n   around the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferretto', 'n.', 'Copper sulphide, used to color glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferri-', '', 'A combining form indicating ferric iron as an ingredient; as,\n   ferricyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferriage', 'n.', 'The price or fare to be paid for passage at a ferry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferric', 'a.', 'Pertaining to, derived from, or containing iron.\n   Specifically (Chem.), denoting those compounds in which iron has a\n   higher valence than in the ferrous compounds; as, ferric oxide; ferric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferricyanate', 'n.', 'A salt of ferricyanic acid; a ferricyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferricyanic', 'a.', 'Pertaining to, or derived from, a ferricyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferricyanide', 'n.', 'One of a complex series of double cyanides of ferric\n   iron and some other base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrier', 'n.', 'A ferryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferriferous', 'a.', 'Producing or yielding iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferriprussiate', 'n.', 'A ferricyanate; a ferricyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferriprussic', 'a.', 'Ferricyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferro-', '', 'A prefix, or combining form, indicating ferrous iron as an\n   ingredient; as, ferrocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrocalcite', 'n.', 'Limestone containing a large percentage of iron\n   carbonate, and hence turning brown on exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrocyanate', 'n.', 'A salt of ferrocyanic acid; a ferrocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrocyanic', 'a.', 'Pertaining to, derived from, or designating, a\n   ferrocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrocyanide', 'n.', 'One of a series of complex double cyanides of ferrous\n   iron and some other base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferroprussiate', 'n.', 'A ferrocyanate; a ferocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferroprussic', 'a.', 'Ferrocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferroso-', '', 'See Ferro-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrotype', 'n.', 'A photographic picture taken on an iron plate by a\n   collodion process; -- familiarly called tintype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrous', 'a.', 'Pertaining to, or derived from, iron; -- especially used\n   of compounds of iron in which the iron has its lower valence; as,\n   ferrous sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferruginated', 'a.', 'Having the color or properties of the rust of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrugineous', 'a.', 'Ferruginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferruginous', 'a.', 'Partaking of iron; containing particles of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferruginous', 'a.', 'Resembling iron rust in appearance or color; brownish\n   red, or yellowish red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrugo', 'n.', 'A disease of plants caused by fungi, commonly called the\n   rust, from its resemblance to iron rust in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrule', 'n.', 'A ring or cap of metal put round a cane, tool, handle, or\n   other similar object, to strengthen it, or prevent splitting and\n   wearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrule', 'n.', 'A bushing for expanding the end of a flue to fasten it\n   tightly in the tube plate, or for partly filling up its mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferruminate', 'v. t.', 'To solder or unite, as metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrumination', 'n.', 'The soldering ir uniting of me/ als.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferried', 'imp. & p. p.', 'of Ferry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrying', 'p. pr. & vb. n.', 'of Ferry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferry', 'v. t.', 'To carry or transport over a river, strait, or other\n   narrow water, in a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferry', 'v. i.', 'To pass over water in a boat or by a ferry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferries', 'pl. ', 'of Ferry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferry', 'v. t.', 'A place where persons or things are carried across a\n   river, arm of the sea, etc., in a ferryboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferry', 'v. t.', 'A vessel in which passengers and goods are conveyed over\n   narrow waters; a ferryboat; a wherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferry', 'v. t.', 'A franchise or right to maintain a vessel for carrying\n   passengers and freight across a river, bay, etc., charging tolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferryboat', 'n.', 'A vessel for conveying passengers, merchandise, etc.,\n   across streams and other narrow waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferrymen', 'pl. ', 'of Ferryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferryman', 'n.', 'One who maintains or attends a ferry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fers', 'a.', 'Fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferthe', 'a.', 'Fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertile', 'a.', 'Producing fruit or vegetation in abundance; fruitful; able\n   to produce abundantly; prolific; fecund; productive; rich; inventive;\n   as, fertile land or fields; a fertile mind or imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertile', 'a.', 'Capable of producing fruit; fruit-bearing; as, fertile\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertile', 'a.', 'Containing pollen; -- said of anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertile', 'a.', 'produced in abundance; plenteous; ample.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilely', 'adv.', 'In a fertile or fruitful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'fertileness', 'n.', 'Fertility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilitate', 'v. t.', 'To fertilize; to fecundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertility', 'n.', 'The state or quality of being fertile or fruitful;\n   fruitfulness; productiveness; fecundity; richness; abundance of\n   resources; fertile invention; quickness; readiness; as, the fertility\n   of soil, or of imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilization', 'n.', 'The act or process of rendering fertile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilization', 'n.', 'The act of fecundating or impregnating animal or\n   vegetable germs; esp., the process by which in flowers the pollen\n   renders the ovule fertile, or an analogous process in flowerless\n   plants; fecundation; impregnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilized', 'imp. & p. p.', 'of Fertilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilizing', 'p. pr. & vb. n.', 'of Fertilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilize', 'v. t.', 'To make fertile or enrich; to supply with nourishment\n   for plants; to make fruitful or productive; as, to fertilize land,\n   soil, ground, and meadows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilize', 'v. t.', 'To fecundate; as, to fertilize flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilizer', 'n.', 'One who fertilizes; the agent that carries the\n   fertilizing principle, as a moth to an orchid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fertilizer', 'n.', 'That which renders fertile; a general name for\n   commercial manures, as guano, phosphate of lime, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferula', 'n.', 'A ferule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferula', 'n.', 'The imperial scepter in the Byzantine or Eastern Empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferulaceous', 'a.', 'Pertaining to reeds and canes; having a stalk like a\n   reed; as, ferulaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferular', 'n.', 'A ferule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferule', 'n.', 'A flat piece of wood, used for striking, children, esp. on\n   the hand, in punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feruled', 'imp. & p. p.', 'of Ferule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feruling', 'p. pr. & vb. n.', 'of Ferule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferule', 'v. t.', 'To punish with a ferule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ferulic', 'a.', 'Pertaining to, or derived from, asafetida (Ferula\n   asafoetida); as, ferulic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervence', 'n.', 'Heat; fervency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervency', 'n.', 'The state of being fervent or warm; ardor; warmth of\n   feeling or devotion; eagerness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervent', 'a.', 'Hot; glowing; boiling; burning; as, a fervent summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervent', 'a.', 'Warm in feeling; ardent in temperament; earnest; full of\n   fervor; zealous; glowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervescent', 'a.', 'Growing hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervid', 'a.', 'Very hot; burning; boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervid', 'a.', 'Ardent; vehement; zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervor', 'n.', 'Heat; excessive warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fervor', 'n.', 'Intensity of feeling or expression; glowing ardor; passion;\n   holy zeal; earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescennine', 'a.', 'Pertaining to, or resembling, the Fescennines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescennine', 'n.', 'A style of low, scurrilous, obscene poetry originating\n   in fescennia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescue', 'n.', 'A straw, wire, stick, etc., used chiefly to point out\n   letters to children when learning to read.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescue', 'n.', 'An instrument for playing on the harp; a plectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescue', 'n.', 'The style of a dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescue', 'n.', 'A grass of the genus Festuca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescued', 'imp. & p. p.', 'of Fescue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescuing', 'p. pr. & vb. n.', 'of Fescue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fescue', 'v. i. & t.', 'To use a fescue, or teach with a fescue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fesels', 'n. pl.', 'See Phasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fess', 'n.', 'Alt. of Fesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fesse', 'n.', 'A band drawn horizontally across the center of an\n   escutcheon, and containing in breadth the third part of it; one of the\n   nine honorable ordinaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fessitude', 'n.', 'Weariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fesswise', 'adv.', 'In the manner of fess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fest', 'n.', 'The fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fest', 'n.', 'Alt. of Feste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feste', 'n.', 'A feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festal', 'a.', 'Of or pertaining to a holiday or a feast; joyous; festive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festally', 'adv.', 'Joyously; festively; mirthfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festennine', 'n.', 'A fescennine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festered', 'imp. & p. p.', 'of Fester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festering', 'p. pr. & vb. n.', 'of Fester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fester', 'n.', 'To generate pus; to become imflamed and suppurate; as, a\n   sore or a wound festers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fester', 'n.', 'To be inflamed; to grow virulent, or malignant; to grow in\n   intensity; to rankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fester', 'v. t.', 'To cause to fester or rankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fester', 'n.', 'A small sore which becomes inflamed and discharges corrupt\n   matter; a pustule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fester', 'n.', 'A festering or rankling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festerment', 'n.', 'A festering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festeye', 'v. t.', 'To feast; to entertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festinate', 'a.', 'Hasty; hurried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festination', 'n.', 'Haste; hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festival', 'a.', 'Pertaining to a fest; festive; festal; appropriate to a\n   festival; joyous; mirthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festi-val', 'n.', 'A time of feasting or celebration; an anniversary day of\n   joy, civil or religious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festive', 'a.', 'Pertaining to, or becoming, a feast; festal; joyous; gay;\n   mirthful; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festivities', 'pl. ', 'of Festivity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festivity', 'n.', 'The condition of being festive; social joy or\n   exhilaration of spirits at an entertaintment; joyfulness; gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festivity', 'n.', 'A festival; a festive celebration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festivous', 'a.', 'Pertaining to a feast; festive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festlich', 'n.', 'Festive; fond of festive occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festoon', 'n.', 'A garland or wreath hanging in a depending curve, used in\n   decoration for festivals, etc.; anything arranged in this way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festoon', 'n.', 'A carved ornament consisting of flowers, and leaves,\n   intermixed or twisted together, wound with a ribbon, and hanging or\n   depending in a natural curve. See Illust. of Bucranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festooned', 'imp. & p. p.', 'of Festoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festooning', 'p. pr. & vb. n.', 'of Festoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festoon', 'v. t.', 'To form in festoons, or to adorn with festoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festoony', 'a.', 'Pertaining to, consisting of, or resembling, festoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festucine', 'a.', 'Of a straw color; greenish yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festucous', 'a.', 'Formed or consisting of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Festue', 'n.', 'A straw; a fescue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fet', 'n.', 'A piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fet', 'v. t.', 'To fetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fet', 'p. p.', 'Fetched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetal', 'a.', 'Pertaining to, or connected with, a fetus; as, fetal\n   circulation; fetal membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetation', 'n.', 'The formation of a fetus in the womb; pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetched', 'imp. & p. p.', 'of Fetch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetching', 'p. pr. & vb. n.', 'of Fetch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To bear toward the person speaking, or the person or\n   thing from whose point of view the action is contemplated; to go and\n   bring; to get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To obtain as price or equivalent; to sell for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To recall from a swoon; to revive; -- sometimes with to;\n   as, to fetch a man to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To reduce; to throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To bring to accomplishment; to achieve; to make; to\n   perform, with certain objects; as, to fetch a compass; to fetch a leap;\n   to fetch a sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To bring or get within reach by going; to reach; to\n   arrive at; to attain; to reach by sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'v. t.', 'To cause to come; to bring to a particular state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'fetch', 'v. i.', 'To bring ones self; to make headway; to veer; as, to\n   fetch about; to fetch to windward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'n.', 'A stratagem by which a thing is indirectly brought to pass,\n   or by which one thing seems intended and another is done; a trick; an\n   artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetch', 'n.', 'The apparation of a living person; a wraith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fethcer', 'n.', 'One wo fetches or brings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fete', 'n.', 'A feat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fete', 'n. pl.', 'Feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fete', 'n.', 'A festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feted', 'imp. & p. p.', 'of Fete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feting', 'p. pr. & vb. n.', 'of Fete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fete', 'v. t.', 'To feast; to honor with a festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetich', 'n.', 'Alt. of Fetish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetish', 'n.', 'A material object supposed among certain African tribes to\n   represent in such a way, or to be so connected with, a supernatural\n   being, that the possession of it gives to the possessor power to\n   control that being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetish', 'n.', 'Any object to which one is excessively devoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'fetichism', 'n.', 'Alt. of Fetishism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishism', 'n.', 'The doctrine or practice of belief in fetiches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishism', 'n.', 'Excessive devotion to one object or one idea; abject\n   superstition; blind adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetichist', 'n.', 'Alt. of Fetishist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishist', 'n.', 'A believer in fetiches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetichistic', 'a.', 'Alt. of Fetishistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishistic', 'a.', 'Pertaining to, or involving, fetichism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feticide', 'n.', 'The act of killing the fetus in the womb; the offense of\n   procuring an abortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feticism', 'n.', 'See Fetichism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetid', 'a.', 'Having an offensive smell; stinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetidity', 'n.', 'Fetidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetidness', 'n.', 'The quality or state of being fetid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetiferous', 'a.', 'Producing young, as animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetis', 'a.', 'Neat; pretty; well made; graceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetisely', 'adv.', 'Neatly; gracefully; properly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetish', 'a.', 'Alt. of Fetishistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishism', 'a.', 'Alt. of Fetishistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetishistic', 'a.', 'See Fetich, n., Fetichism, n., Fetichistic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetlock', 'n.', 'The cushionlike projection, bearing a tuft of long hair,\n   on the back side of the leg above the hoof of the horse and similar\n   animals. Also, the joint of the limb at this point (between the great\n   pastern bone and the metacarpus), or the tuft of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetor', 'n.', 'A strong, offensive smell; stench; fetidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fette', 'imp.', 'of Fette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fet', 'p. p.', 'of Fette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fette', 'v. t.', 'To fetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'fetters', 'pl. ', 'of Fetter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetter', 'n.', 'A chain or shackle for the feet; a chain by which an animal\n   is confined by the foot, either made fast or disabled from free and\n   rapid motion; a bond; a shackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetter', 'n.', 'Anything that confines or restrains; a restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetter', 'p. pr. & vb. n.', 'To put fetters upon; to shackle or confine the\n   feet of with a chain; to bind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetter', 'p. pr. & vb. n.', 'To restrain from motion; to impose restraints\n   on; to confine; to enchain; as, fettered by obligations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettered', 'a.', 'Seeming as if fettered, as the feet of certain animals\n   which bend backward, and appear unfit for walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetterer', 'n.', 'One who fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetterless', 'a.', 'Free from fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettle', 'a.', 'To repair; to prepare; to put in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettle', 'a.', 'To cover or line with a mixture of ore, cinders, etc., as\n   the hearth of a puddling furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettle', 'v. i.', 'To make preparations; to put things in order; to do\n   trifling business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettle', 'n.', 'The act of fettling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettling', 'n.', 'A mixture of ore, cinders, etc., used to line the hearth\n   of a puddling furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fettling', 'n.', 'The operation of shaving or smoothing the surface of\n   undried clay ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetuous', 'a.', 'Neat; feat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetuses', 'pl. ', 'of Fetus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetus', 'n.', 'The young or embryo of an animal in the womb, or in the egg;\n   often restricted to the later stages in the development of viviparous\n   and oviparous animals, embryo being applied to the earlier stages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fetwah', 'n.', 'A written decision of a Turkish mufti on some point of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feu', 'n.', 'A free and gratuitous right to lands made to one for service\n   to be performed by him; a tenure where the vassal, in place of military\n   services, makes a return in grain or in money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuar', 'n.', 'One who holds a feu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feud', 'n.', 'A combination of kindred to avenge injuries or affronts, done\n   or offered to any of their blood, on the offender and all his race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feud', 'n.', 'A contention or quarrel; especially, an inveterate strife\n   between families, clans, or parties; deadly hatred; contention\n   satisfied only by bloodshed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feud', 'n.', 'A stipendiary estate in land, held of superior, by service;\n   the right which a vassal or tenant had to the lands or other immovable\n   thing of his lord, to use the same and take the profists thereof\n   hereditarily, rendering to his superior such duties and services as\n   belong to military tenure, etc., the property of the soil always\n   remaining in the lord or superior; a fief; a fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudal', 'a.', 'Of or pertaining to feuds, fiefs, or feels; as, feudal\n   rights or services; feudal tenures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudal', 'a.', 'Consisting of, or founded upon, feuds or fiefs; embracing\n   tenures by military services; as, the feudal system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudalism', 'n.', 'The feudal system; a system by which the holding of\n   estates in land is made dependent upon an obligation to render military\n   service to the kind or feudal superior; feudal principles and usages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudalist', 'n.', 'An upholder of feudalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudality', 'n.', 'The state or quality of being feudal; feudal form or\n   constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fedaliza/tion', 'n.', 'The act of reducing to feudal tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudalized', 'imp. & p. p.', 'of Feudalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudalizing', 'p. pr. & vb. n.', 'of Feudalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudalize', 'v. t.', 'To reduce toa feudal tenure; to conform to feudalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudally', 'adv.', 'In a feudal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudary', 'a.', 'Held by, or pertaining to, feudal tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudary', 'n.', 'A tenant who holds his lands by feudal service; a\n   feudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudary', 'n.', 'A feodary. See Feodary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudataty', 'a. & n.', 'See Feudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudatories', 'pl. ', 'of Feudatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudatory', 'n.', 'A tenant or vassal who held his lands of a superior on\n   condition of feudal service; the tenant of a feud or fief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudtory', 'a.', 'Held from another on some conditional tenure; as, a\n   feudatory title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feu de joie', '', 'A fire kindled in a public place in token of joy; a\n   bonfire; a firing of guns in token of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feudist', 'n.', 'A writer on feuds; a person versed in feudal law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuillants', 'n. pl.', 'A reformed branch of the Bernardines, founded in\n   1577 at Feuillans, near Toulouse, in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuillemort', 'a.', 'Having the color of a faded leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuilleton', 'n.', 'A part of a French newspaper (usually the bottom of the\n   page), devoted to light literature, criticism, etc.; also, the article\n   or tale itself, thus printed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuilltonist', 'n.', 'A writer of feuilletons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'feuter', 'v. t.', 'To set close; to fix in rest, as a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feuterer', 'n.', 'A dog keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fever', 'n.', 'A diseased state of the system, marked by increased heat,\n   acceleration of the pulse, and a general derangement of the functions,\n   including usually, thirst and loss of appetite. Many diseases, of which\n   fever is the most prominent symptom, are denominated fevers; as,\n   typhoid fever; yellow fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fever', 'n.', 'Excessive excitement of the passions in consequence of\n   strong emotion; a condition of great excitement; as, this quarrel has\n   set my blood in a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fevered', 'imp. & p. p.', 'of Fever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fevering', 'p. pr. & vb. n.', 'of Fever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fever', 'v. t.', 'To put into a fever; to affect with fever; as, a fevered\n   lip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feveret', 'n.', 'A slight fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverfew', 'n.', 'A perennial plant (Pyrethrum, / Chrysanthemum,\n   Parthenium) allied to camomile, having finely divided leaves and white\n   blossoms; -- so named from its supposed febrifugal qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverish', 'a.', 'Having a fever; suffering from, or affected with, a\n   moderate degree of fever; showing increased heat and thirst; as, the\n   patient is feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverish', 'a.', 'Indicating, or pertaining to, fever; characteristic of a\n   fever; as, feverish symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverish', 'a.', 'Hot; sultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverish', 'a.', 'Disordered as by fever; excited; restless; as, the\n   feverish condition of the commercial world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverous', 'a.', 'Affected with fever or ague; feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverous', 'a.', 'Pertaining to, or having the nature of, fever; as, a\n   feverous pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverous', 'a.', 'Having the tendency to produce fever; as, a feverous\n   disposition of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverously', 'adv.', 'Feverishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feverwort', 'n.', 'See Fever root, under Fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fevery', 'a.', 'Feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Few', 'superl.', 'Not many; small, limited, or confined in number; --\n   indicating a small portion of units or individuals constituing a whole;\n   often, by ellipsis of a noun, a few people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fewel', 'n.', 'Fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fewmet', 'n.', 'See Fumet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fewness', 'n.', 'The state of being few; smallness of number; paucity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fewness', 'n.', 'Brevity; conciseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fey', 'a.', 'Fated; doomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fey', 'n.', 'Faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fey', 'v. t.', 'To cleanse; to clean out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feyne', 'v. t.', 'To feign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Feyre', 'n.', 'A fair or market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fez', 'n.', 'A felt or cloth cap, usually red and having a tassel, -- a\n   variety of the tarboosh. See Tarboosh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiacre', 'n.', 'A kind of French hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiance', 'v. t.', 'To betroth; to affiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiance', 'n.', 'A betrothed man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiancee', 'n.', 'A betrothed woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiants', 'n.', 'The dung of the fox, wolf, boar, or badger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiar', 'n.', 'One in whom the property of an estate is vested, subject to\n   the estate of a life renter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiar', 'n.', 'The price of grain, as legally fixed, in the counties of\n   Scotland, for the current year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiascoes', 'pl. ', 'of Fiasco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiasco', 'n.', 'A complete or ridiculous failure, esp. of a musical\n   performance, or of any pretentious undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiat', 'n.', 'An authoritative command or order to do something; an\n   effectual decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiat', 'n.', 'A warrant of a judge for certain processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiat', 'n.', 'An authority for certain proceedings given by the Lord\n   Chancellors signature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiaunt', 'n.', 'Commission; fiat; order; decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fib', 'n.', 'A falsehood; a lie; -- used euphemistically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibbed', 'imp. & p. p.', 'of Fib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibbing', 'p. pr. & vb. n.', 'of Fib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fib', 'v. i.', 'To speak falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fib', 'v. t.', 'To tell a fib to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibber', 'n.', 'One who tells fibs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiber', 'n.', 'Alt. of Fibre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibre', 'n.', 'One of the delicate, threadlike portions of which the\n   tissues of plants and animals are in part constituted; as, the fiber of\n   flax or of muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibre', 'n.', 'Any fine, slender thread, or threadlike substance; as, a\n   fiber of spun glass; especially, one of the slender rootlets of a\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibre', 'n.', 'Sinew; strength; toughness; as, a man of real fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibre', 'n.', 'A general name for the raw material, such as cotton, flax,\n   hemp, etc., used in textile manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibered', 'a.', 'Alt. of Fibred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibred', 'a.', 'Having fibers; made up of fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiber-faced', 'a.', 'Alt. of Fibre-faced');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibre-faced', 'a.', 'Having a visible fiber embodied in the surface of; --\n   applied esp. to a kind of paper for checks, drafts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiberless', 'a.', 'Alt. of Fibreless');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibreless', 'a.', 'Having no fibers; destitute of fibers or fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibriform', 'a.', 'Having the form of a fiber or fibers; resembling a\n   fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibril', 'n.', 'A small fiber; the branch of a fiber; a very slender\n   thread; a fibrilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'FibrillAe', 'pl. ', 'of Fibrilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrilla', 'n.', 'A minute thread of fiber, as one of the fibrous elements\n   of a muscular fiber; a fibril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillar', 'a.', 'Of or pertaining to fibrils or fibers; as, fibrillar\n   twitchings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillary', 'a.', 'Of of pertaining to fibrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillated', 'a.', 'Furnished with fibrils; fringed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillation', 'n.', 'The state of being reduced to fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillose', 'a.', 'Covered with hairlike appendages, as the under surface\n   of some lichens; also, composed of little strings or fibers; as,\n   fibrillose appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrillous', 'a.', 'Pertaining to, or composed of, fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrin', 'n.', 'A white, albuminous, fibrous substance, formed in the\n   coagulation of the blood either by decomposition of fibrinogen, or from\n   the union of fibrinogen and paraglobulin which exist separately in the\n   blood. It is insoluble in water, but is readily digestible in gastric\n   and pancreatic juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrin', 'n.', 'The white, albuminous mass remaining after washing lean\n   beef or other meat with water until all coloring matter is removed; the\n   fibrous portion of the muscle tissue; flesh fibrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrin', 'n.', 'An albuminous body, resembling animal fibrin in\n   composition, found in cereal grains and similar seeds; vegetable\n   fibrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrination', 'n.', 'The state of acquiring or having an excess of fibrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrine', 'a.', 'Belonging to the fibers of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrinogen', 'n.', 'An albuminous substance existing in the blood, and in\n   other animal fluids, which either alone or with fibrinoplastin or\n   paraglobulin forms fibrin, and thus causes coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrinogenous', 'a.', 'Possessed of properties similar to fibrinogen;\n   capable of forming fibrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrinoplastic', 'a.', 'Like fibrinoplastin; capable of forming fibrin when\n   brought in contact with fibrinogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrinoplastin', 'n.', 'An albuminous substance, existing in the blood,\n   which in combination with fibrinogen forms fibrin; -- called also\n   paraglobulin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrinous', 'a.', 'Having, or partaking of the properties of, fibrin; as,\n   fibrious exudation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrocartilage', 'n.', 'A kind of cartilage with a fibrous matrix and\n   approaching fibrous connective tissue in structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrochondrosteal', 'a.', 'Partly fibrous, partly cartilaginous, and partly\n   osseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibroid', 'a.', 'Resembling or forming fibrous tissue; made up of fibers;\n   as, fibroid tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibroid', 'n.', 'A fibroid tumor; a fibroma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibroin', 'n.', 'A variety of gelatin; the chief ingredient of raw silk,\n   extracted as a white amorphous mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrolite', 'n.', 'A silicate of alumina, of fibrous or columnar structure.\n   It is like andalusite in composition; -- called also sillimanite, and\n   bucholizite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibroma', 'n.', 'A tumor consisting mainly of fibrous tissue, or of same\n   modification of such tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrospongiae', 'n. pl.', 'An order of sponges having a fibrous skeleton,\n   including the commercial sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrous', 'a.', 'Containing, or consisting of, fibers; as, the fibrous coat\n   of the cocoanut; the fibrous roots of grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibrovascular', 'a.', 'Containing woody fiber and ducts, as the stems of\n   all flowering plants and ferns; -- opposed to cellular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibster', 'n.', 'One who tells fibs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'FibulAe', 'pl. ', 'of Fibula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibula', 'n.', 'A brooch, clasp, or buckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibula', 'n.', 'The outer and usually the smaller of the two bones of the\n   leg, or hind limb, below the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibula', 'n.', 'A needle for sewing up wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibu-lar', 'a.', 'Pertaining to the fibula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibularia', 'pl. ', 'of Fibulare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fibulare', 'n.', 'The bone or cartilage of the tarsus, which articulates\n   with the fibula, and corresponds to the calcaneum in man and most\n   mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fice', 'n.', 'A small dog; -- written also fise, fyce, fiste, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiche', 'a.', 'See FitchE.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ficttelite', 'n.', 'A white crystallized mineral resin from the\n   Fichtelgebirge, Bavaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fichu', 'n.', 'A light cape, usually of lace, worn by women, to cover the\n   neck and throat, and extending to the shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fickle', 'a.', 'Not fixed or firm; liable to change; unstable; of a\n   changeable mind; not firm in opinion or purpose; inconstant;\n   capricious; as, Fortunes fickle wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fickleness', 'n.', 'The quality of being fickle; instability; inconsonancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fickly', 'adv.', 'In a fickle manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ficoes', 'pl. ', 'of Fico');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fico', 'n.', 'A fig; an insignificant trifle, no more than the snap of\n   ones thumb; a sign of contempt made by the fingers, expressing. A fig\n   for you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictile', 'a.', 'Molded, or capable of being molded, into form by art;\n   relating to pottery or to molding in any soft material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiction', 'n.', 'The act of feigning, inventing, or imagining; as, by a\n   mere fiction of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiction', 'n.', 'That which is feigned, invented, or imagined; especially,\n   a feigned or invented story, whether oral or written. Hence: A story\n   told in order to deceive; a fabrication; -- opposed to fact, or\n   reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiction', 'n.', 'Fictitious literature; comprehensively, all works of\n   imagination; specifically, novels and romances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiction', 'n.', 'An assumption of a possible thing as a fact, irrespective\n   of the question of its truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiction', 'n.', 'Any like assumption made for convenience, as for passing\n   more rapidly over what is not disputed, and arriving at points really\n   at issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictional', 'a.', 'Pertaining to, or characterized by, fiction; fictitious;\n   romantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictionist', 'n.', 'A writer of fiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictious', 'a.', 'Fictitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictitious', 'a.', 'Feigned; imaginary; not real; fabulous; counterfeit;\n   false; not genuine; as, fictitious fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictive', 'a.', 'Feigned; counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fictor', 'n.', 'An artist who models or forms statues and reliefs in any\n   plastic material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ficus', 'n.', 'A genus of trees or shrubs, one species of which (F. Carica)\n   produces the figs of commerce; the fig tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fid', 'n.', 'A square bar of wood or iron, used to support the topmast,\n   being passed through a hole or mortise at its heel, and resting on the\n   trestle trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fid', 'n.', 'A wooden or metal bar or pin, used to support or steady\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fid', 'n.', 'A pin of hard wood, tapering to a point, used to open the\n   strands of a rope in splicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fid', 'n.', 'A block of wood used in mounting and dismounting heavy guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidalgo', 'n.', 'The lowest title of nobility in Portugal, corresponding to\n   that of Hidalgo in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'n.', 'A stringed instrument of music played with a bow; a violin;\n   a kit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'n.', 'A kind of dock (Rumex pulcher) with fiddle-shaped leaves;\n   -- called also fiddle dock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'n.', 'A rack or frame of bars connected by strings, to keep table\n   furniture in place on the cabin table in bad weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddled', 'imp. & p. p.', 'of Fiddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddling', 'p. pr. & vb. n.', 'of Fiddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'v. i.', 'To play on a fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'v. i.', 'To keep the hands and fingers actively moving as a\n   fiddler does; to move the hands and fingers restlessy or in busy\n   idleness; to trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle', 'v. t.', 'To play (a tune) on a fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddledeedee', 'interj.', 'An exclamatory word or phrase, equivalent to\n   nonsense!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foddle-faddle', 'n.', 'A trifle; trifling talk; nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle-faddle', 'v. i.', 'To talk nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddler', 'n.', 'One who plays on a fiddle or violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddler', 'n.', 'A burrowing crab of the genus Gelasimus, of many species.\n   The male has one claw very much enlarged, and often holds it in a\n   position similar to that in which a musician holds a fiddle, hence the\n   name; -- called also calling crab, soldier crab, and fighting crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddler', 'n.', 'The common European sandpiper (Tringoides hypoleucus); --\n   so called because it continually oscillates its body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddle-shaped', 'a.', 'Inversely ovate, with a deep hollow on each side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddlestick', 'n.', 'The bow, strung with horsehair, used in playing the\n   fiddle; a fiddle bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddlestring', 'n.', 'One of the catgut strings of a fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiddlewood', 'n.', 'The wood of several West Indian trees, mostly of the\n   genus Citharexylum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidejussion', 'n.', 'The act or state of being bound as surety for another;\n   suretyship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidejussor', 'n.', 'A surety; one bound for another, conjointly with him; a\n   guarantor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidelity', 'n.', 'Faithfulness; adherence to right; careful and exact\n   observance of duty, or discharge of obligations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidelity', 'n.', 'Adherence to a person or party to which one is bound;\n   loyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidelity', 'n.', 'Adherence to the marriage contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidelity', 'n.', 'Adherence to truth; veracity; honesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fides', 'n.', 'Faith personified as a goddess; the goddess of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidge', 'n. & i.', 'See Fidget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidgeted', 'imp. & p. p.', 'of Fidget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fodgeting', 'p. pr. & vb. n.', 'of Fidget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidget', 'v. i.', 'To move uneasily one way and the other; to move\n   irregularly, or by fits and starts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidget', 'n.', 'Uneasiness; restlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidget', 'n.', 'A general nervous restlessness, manifested by incessant\n   changes of position; dysphoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidgetiness', 'n.', 'Quality of being fidgety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidgety', 'a.', 'Restless; uneasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidia', 'n.', 'A genus of small beetles, of which one species (the\n   grapevine Fidia, F. longipes) is very injurious to vines in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidicinal', 'a.', 'Of or pertaining to a stringed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiducial', 'a.', 'Having faith or trust; confident; undoubting; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiducial', 'a.', 'Having the nature of a trust; fiduciary; as, fiducial\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiducially', 'adv.', 'With confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidiciary', 'a.', 'Involving confidence or trust; confident; undoubting;\n   faithful; firm; as, in a fiduciary capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fidiciary', 'a.', 'Holding, held, or founded, in trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiduciary', 'n.', 'One who holds a thing in trust for another; a trustee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiduciary', 'n.', 'One who depends for salvation on faith, without works;\n   an Antinomian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fie', 'interj.', 'An exclamation denoting contempt or dislike. See Fy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fief', 'n.', 'An estate held of a superior on condition of military\n   service; a fee; a feud. See under Benefice, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'Cleared land; land suitable for tillage or pasture;\n   cultivated ground; the open country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'A piece of land of considerable size; esp., a piece inclosed\n   for tillage or pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'A place where a battle is fought; also, the battle itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'An open space; an extent; an expanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'Any blank space or ground on which figures are drawn or\n   projected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'The space covered by an optical instrument at one view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'The whole surface of an escutcheon; also, so much of it is\n   shown unconcealed by the different bearings upon it. See Illust. of\n   Fess, where the field is represented as gules (red), while the fess is\n   argent (silver).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'An unresticted or favorable opportunity for action,\n   operation, or achievement; province; room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'A collective term for all the competitors in any outdoor\n   contest or trial, or for all except the favorites in the betting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'n.', 'That part of the grounds reserved for the players which is\n   outside of the diamond; -- called also outfield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielded', 'imp. & p. p.', 'of Field');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielding', 'p. pr. & vb. n.', 'of Field');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'v. i.', 'To take the field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'v. i.', 'To stand out in the field, ready to catch, stop, or throw\n   the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Field', 'v. t.', 'To catch, stop, throw, etc. (the ball), as a fielder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielded', 'a.', 'Engaged in the field; encamped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielden', 'a.', 'Consisting of fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielder', 'n.', 'A ball payer who stands out in the field to catch or stop\n   balls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieldfare', 'n.', 'a small thrush (Turdus pilaris) which breeds in northern\n   Europe and winters in Great Britain. The head, nape, and lower part of\n   the back are ash-colored; the upper part of the back and wing coverts,\n   chestnut; -- called also fellfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fielding', 'n.', 'The act of playing as a fielder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieldpiece', 'n.', 'A cannon mounted on wheels, for the use of a marching\n   army; a piece of field artillery; -- called also field gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieldwork', 'n.', 'Any temporary fortification thrown up by an army in the\n   field; -- commonly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieldy', 'a.', 'Open, like a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiend', 'n.', 'An implacable or malicious foe; one who is diabolically\n   wicked or cruel; an infernal being; -- applied specifically to the\n   devil or a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiendful', 'a.', 'Full of fiendish spirit or arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiendish', 'a.', 'Like a fiend; diabolically wicked or cruel; infernal;\n   malignant; devilish; hellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiendlike', 'a.', 'Fiendish; diabolical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiendly', 'a.', 'Fiendlike; monstrous; devilish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fierasfer', 'n.', 'A genus of small, slender fishes, remarkable for their\n   habit of living as commensals in other animals. One species inhabits\n   the gill cavity of the pearl oyster near Panama; another lives within\n   an East Indian holothurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fierce', 'superl.', 'Furious; violent; unrestrained; impetuous; as, a\n   fierce wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fierce', 'superl.', 'Vehement in anger or cruelty; ready or eager to kill\n   or injure; of a nature to inspire terror; ferocious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fierce', 'superl.', 'Excessively earnest, eager, or ardent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieri facias', '', 'A judicial writ that lies for one who has recovered in\n   debt or damages, commanding the sheriff that he cause to be made of the\n   goods, chattels, or real estate of the defendant, the sum claimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fieriness', 'n.', 'The quality of being fiery; heat; acrimony;\n   irritability; as, a fieriness of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiery', 'a.', 'Consisting of, containing, or resembling, fire; as, the\n   fiery gulf of Etna; a fiery appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiery', 'a.', 'Vehement; ardent; very active; impetuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiery', 'a.', 'Passionate; easily provoked; irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiery', 'a.', 'Unrestrained; fierce; mettlesome; spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiery', 'a.', 'heated by fire, or as if by fire; burning hot; parched;\n   feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fife', 'n.', 'A small shrill pipe, resembling the piccolo flute, used\n   chiefly to accompany the drum in military music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifed', 'imp. & p. p.', 'of Fife');
  INSERT INTO words (word, word_type, description) VALUES  (    'fifing', 'p. pr. & vb. n.', 'of Fife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fife', 'v. i.', 'To play on a fife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifer', 'n.', 'One who plays on a fife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteen', 'a.', 'Five and ten; one more than fourteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteen', 'n.', 'The sum of five and ten; fifteen units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteen', 'n.', 'A symbol representing fifteen units, as 15, or xv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'a.', 'Next in order after the fourteenth; -- the ordinal of\n   fifteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'a.', 'Consisting of one of fifteen equal parts or divisions of\n   a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'n.', 'One of fifteen equal parts or divisions; the quotient of\n   a unit divided by fifteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'n.', 'A species of tax upon personal property formerly laid on\n   towns, boroughs, etc., in England, being one fifteenth part of what the\n   personal property in each town, etc., had been valued at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'n.', 'A stop in an organ tuned two octaves above the diaposon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifteenth', 'n.', 'An interval consisting of two octaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifth', 'a.', 'Next in order after the fourth; -- the ordinal of five.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifth', 'a.', 'Consisting of one of five equal divisions of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifth', 'n.', 'The quotient of a unit divided by five; one of five equal\n   parts; a fifth part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifth', 'n.', 'The interval of three tones and a semitone, embracing five\n   diatonic degrees of the scale; the dominant of any key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifthly', 'adv.', 'In the fifth place; as the fifth in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiftieth', 'a.', 'Next in order after the forty-ninth; -- the ordinal of\n   fifty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiftieth', 'a.', 'Consisting of one of fifty equal parts or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiftieth', 'n.', 'One of fifty equal parts; the quotient of a unit divided\n   by fifty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifty', 'a.', 'Five times ten; as, fifty men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifties', 'pl. ', 'of Fifty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifty', 'n.', 'The sum of five tens; fifty units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fifty', 'n.', 'A symbol representing fifty units, as 50, or l.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'A small fruit tree (Ficus Carica) with large leaves, known\n   from the remotest antiquity. It was probably native from Syria westward\n   to the Canary Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'The fruit of a fig tree, which is of round or oblong shape,\n   and of various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'A small piece of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'The value of a fig, practically nothing; a fico; -- used in\n   scorn or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'To insult with a fico, or contemptuous motion. See Fico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'To put into the head of, as something useless o/ contemptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig', 'n.', 'Figure; dress; array.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figaro', 'n.', 'An adroit and unscrupulous intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figary', 'n.', 'A frolic; a vagary; a whim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figeater', 'n.', 'A large beetle (Allorhina nitida) which in the Southern\n   United States destroys figs. The elytra are velvety green with pale\n   borders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figeater', 'n.', 'A bird. See Figpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figent', 'a.', 'Fidgety; restless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figgum', 'n.', 'A jugglers trick; conjuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fought', 'imp. & p. p.', 'of Fight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fighting', 'p. pr. & vb. n.', 'of Fight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'To strive or contend for victory, with armies or in\n   single combat; to attempt to defeat, subdue, or destroy an enemy,\n   either by blows or weapons; to contend in arms; -- followed by with or\n   against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'To act in opposition to anything; to struggle against; to\n   contend; to strive; to make resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. t.', 'To carry on, or wage, as a conflict, or battle; to win or\n   gain by struggle, as ones way; to sustain by fighting, as a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. t.', 'To contend with in battle; to war against; as, they\n   fought the enemy in two pitched battles; the sloop fought the frigate\n   for three hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. t.', 'To cause to fight; to manage or maneuver in a fight; as,\n   to fight cocks; to fight ones ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'A battle; an engagement; a contest in arms; a combat; a\n   violent conflict or struggle for victory, between individuals or\n   between armies, ships, or navies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'A struggle or contest of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'Strength or disposition for fighting; pugnacity; as, he\n   has a great deal of fight in him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fight', 'v. i.', 'A screen for the combatants in ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fighter', 'n.', 'One who fights; a combatant; a warrior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fighting', 'a.', 'Qualified for war; fit for battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fighting', 'a.', 'Occupied in war; being the scene of a battle; as, a\n   fighting field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fightingly', 'adv.', 'Pugnaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fightwite', 'n.', 'A mulct or fine imposed on a person for making a fight\n   or quarrel to the disturbance of the peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figment', 'n.', 'An invention; a fiction; something feigned or imagined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigpecker', 'n.', 'The European garden warbler (Sylvia, / Currica,\n   hortensis); -- called also beccafico and greater pettychaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fig-shell', 'n.', 'A marine univalve shell of the genus Pyrula, or Ficula,\n   resembling a fig in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figulate', 'a.', 'Alt. of Figulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figulated', 'a.', 'Made of potters clay; molded; shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figuline', 'n.', 'A piece of pottery ornamented with representations of\n   natural objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurability', 'n.', 'The quality of being figurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurable', 'a.', 'Capable of being brought to a fixed form or shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figural', 'a.', 'Represented by figure or delineation; consisting of\n   figures; as, figural ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figural', 'a.', 'Figurate. See Figurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurant', 'n. masc.', 'One who dances at the opera, not singly, but in\n   groups or figures; an accessory character on the stage, who figures in\n   its scenes, but has nothing to say; hence, one who figures in any\n   scene, without taking a prominent part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurante', 'n. fem.', 'A female figurant; esp., a ballet girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurate', 'a.', 'Of a definite form or figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurate', 'a.', 'Figurative; metaphorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurate', 'a.', 'Florid; figurative; involving passing discords by the\n   freer melodic movement of one or more parts or voices in the harmony;\n   as, figurate counterpoint or descant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurated', 'a.', 'Having a determinate form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurately', 'adv.', 'In a figurate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figuration', 'n.', 'The act of giving figure or determinate form;\n   determination to a certain form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figuration', 'n.', 'Mixture of concords and discords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurative', 'a.', 'Representing by a figure, or by resemblance; typical;\n   representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurative', 'a.', 'Used in a sense that is tropical, as a metaphor; not\n   literal; -- applied to words and expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurative', 'a.', 'Abounding in figures of speech; flowery; florid; as, a\n   highly figurative description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurative', 'a.', 'Relating to the representation of form or figure by\n   drawing, carving, etc. See Figure, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'The form of anything; shape; outline; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'The representation of any form, as by drawing, painting,\n   modeling, carving, embroidering, etc.; especially, a representation of\n   the human body; as, a figure in bronze; a figure cut in marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A pattern in cloth, paper, or other manufactured article; a\n   design wrought out in a fabric; as, the muslin was of a pretty figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A diagram or drawing; made to represent a magnitude or the\n   relation of two or more magnitudes; a surface or space inclosed on all\n   sides; -- called superficial when inclosed by lines, and solid when\n   inclosed by surface; any arrangement made up of points, lines, angles,\n   surfaces, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'The appearance or impression made by the conduct or carrer\n   of a person; as, a sorry figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'Distinguished appearance; magnificence; conspicuous\n   representation; splendor; show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A character or symbol representing a number; a numeral; a\n   digit; as, 1, 2,3, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'Value, as expressed in numbers; price; as, the goods are\n   estimated or sold at a low figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A person, thing, or action, conceived of as analogous to\n   another person, thing, or action, of which it thus becomes a type or\n   representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A mode of expressing abstract or immaterial ideas by words\n   which suggest pictures or images from the physical world; pictorial\n   language; a trope; hence, any deviation from the plainest form of\n   statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'The form of a syllogism with respect to the relative\n   position of the middle term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'Any one of the several regular steps or movements made by a\n   dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A horoscope; the diagram of the aspects of the astrological\n   houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'Any short succession of notes, either as melody or as a\n   group of chords, which produce a single complete and distinct\n   impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'A form of melody or accompaniment kept up through a strain\n   or passage; a musical or motive; a florid embellishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figured', 'imp. & p. p.', 'of Figure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figuring', 'p. pr. & vb. n.', 'of Figure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To represent by a figure, as to form or mold; to make an\n   image of, either palpable or ideal; also, to fashion into a determinate\n   form; to shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To embellish with design; to adorn with figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To indicate by numerals; also, to compute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To represent by a metaphor; to signify or symbolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To prefigure; to foreshow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To write over or under the bass, as figures or other\n   characters, in order to indicate the accompanying chords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'n.', 'To embellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'v. t.', 'To make a figure; to be distinguished or conspicious;\n   as, the envoy figured at court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figure', 'v. t.', 'To calculate; to contrive; to scheme; as, he is figuring\n   to secure the nomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figured', 'a.', 'Adorned with figures; marked with figures; as, figured\n   muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figured', 'a.', 'Not literal; figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figured', 'a.', 'Free and florid; as, a figured descant. See Figurate, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figured', 'a.', 'Indicated or noted by figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurehead', 'n.', 'The figure, statue, or bust, on the prow of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurehead', 'n.', 'A person who allows his name to be used to give\n   standing to enterprises in which he has no responsible interest or\n   duties; a nominal, but not real, head or chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurial', 'a.', 'Represented by figure or delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurine', 'n.', 'A very small figure, whether human or of an animal;\n   especially, one in terra cotta or the like; -- distinguished from\n   statuette, which is applied to small figures in bronze, marble, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figurist', 'n.', 'One who uses or interprets figurative expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Figwort', 'n.', 'A genus of herbaceous plants (Scrophularia), mostly found\n   in the north temperate zones. See Brownwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fijian', 'a.', 'Of or pertaining to the Fiji islands or their inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fijian', 'n.', 'A native of the Fiji islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fike', 'n.', 'See Fyke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fil', '', 'imp. of Fall, v. i. Fell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filaceous', 'a.', 'Composed of threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filacer', 'n.', 'A former officer in the English Court of Common Pleas; --\n   so called because he filed the writs on which he made out process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filament', 'n.', 'A thread or threadlike object or appendage; a fiber; esp.\n   (Bot.), the threadlike part of the stamen supporting the anther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filamentary', 'a.', 'Having the character of, or formed by, a filament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filametoid', 'a.', 'Like a filament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filamentous', 'a.', 'Like a thread; consisting of threads or filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filander', 'n.', 'A species of kangaroo (Macropus Brunii), inhabiting New\n   Guinea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filanders', 'n. pl.', 'A disease in hawks, characterized by the presence of\n   small threadlike worms, also of filaments of coagulated blood, from the\n   rupture of a vein; -- called also backworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filar', 'a.', 'Of or pertaining to a thread or line; characterized by\n   threads stretched across the field of view; as, a filar microscope; a\n   filar micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filaria', 'n.', 'A genus of slender, nematode worms of many species,\n   parasitic in various animals. See Guinea worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filatory', 'n.', 'A machine for forming threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filature', 'n.', 'A drawing out into threads; hence, the reeling of silk\n   from cocoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filature', 'n.', 'A reel for drawing off silk from cocoons; also, an\n   establishment for reeling silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filbert', 'n.', 'The fruit of the Corylus Avellana or hazel. It is an oval\n   nut, containing a kernel that has a mild, farinaceous, oily taste,\n   agreeable to the palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filched', 'imp. & p. p.', 'of Filch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filching', 'p. pr. & vb. n.', 'of Filch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filch', 'v. t.', 'To steal or take privily (commonly, that which is of\n   little value); to pilfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filcher', 'n.', 'One who filches; a thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filchingly', 'adv.', 'By pilfering or petty stealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'An orderly succession; a line; a row');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'A row of soldiers ranged one behind another; -- in\n   contradistinction to rank, which designates a row of soldiers standing\n   abreast; a number consisting the depth of a body of troops, which, in\n   the ordinary modern formation, consists of two men, the battalion\n   standing two deep, or in two ranks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'An orderly collection of papers, arranged in sequence or\n   classified for preservation and reference; as, files of letters or of\n   newspapers; this mail brings English files to the 15th instant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'The line, wire, or other contrivance, by which papers are put\n   and kept in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'A roll or list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'Course of thought; thread of narration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filed', 'imp. & p. p.', 'of File');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filing', 'p. pr. & vb. n.', 'of File');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To set in order; to arrange, or lay away, esp. as papers\n   in a methodical manner for preservation and reverence; to place on\n   file; to insert in its proper place in an arranged body of papers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To bring before a court or legislative body by presenting\n   proper papers in a regular way; as, to file a petition or bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To put upon the files or among the records of a court; to\n   note on (a paper) the fact date of its reception in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. i.', 'To march in a file or line, as soldiers, not abreast, but\n   one after another; -- generally with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'A steel instrument, having cutting ridges or teeth, made by\n   indentation with a chisel, used for abrading or smoothing other\n   substances, as metals, wood, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'Anything employed to smooth, polish, or rasp, literally or\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'n.', 'A shrewd or artful person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To rub, smooth, or cut away, with a file; to sharpen with\n   a file; as, to file a saw or a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To smooth or polish as with a file.');
  INSERT INTO words (word, word_type, description) VALUES  (    'File', 'v. t.', 'To make foul; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filefish', 'n.', 'Any plectognath fish of the genera Monacanthus, Alutera,\n   balistes, and allied genera; -- so called on account of the roughly\n   granulated skin, which is sometimes used in place of sandpaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filemot', 'n.', 'See Feullemort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filer', 'n.', 'One who works with a file.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filial', 'a.', 'Of or pertaining to a son or daughter; becoming to a child\n   in relation to his parents; as, filial obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filial', 'a.', 'Bearing the relation of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filially', 'adv.', 'In a filial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiate', 'v. t.', 'To adopt as son or daughter; to establish filiation\n   between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiation', 'n.', 'The relationship of a son or child to a parent, esp. to\n   a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiation', 'n.', 'The assignment of a bastard child to some one as its\n   father; affiliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibeg', 'n.', 'Same as Kilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibuster', 'n.', 'A lawless military adventurer, especially one in quest\n   of plunder; a freebooter; -- originally applied to buccaneers infesting\n   the Spanish American coasts, but introduced into common English to\n   designate the followers of Lopez in his expedition to Cuba in 1851, and\n   those of Walker in his expedition to Nicaragua, in 1855.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillibustered', 'imp. & p. p.', 'of Filibuster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibustering', 'p. pr. & vb. n.', 'of Filibuster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibuster', 'v. i.', 'To act as a filibuster, or military freebooter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibuster', 'v. i.', 'To delay legislation, by dilatory motions or other\n   artifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filibusterism', 'n.', 'The characteristics or practices of a filibuster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filical', 'a.', 'Belonging to the Filices, r ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filicic', 'a.', 'Pertaining to, or derived from, ferns; as, filicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filicide', 'n.', 'The act of murdering a son or a daughter; also, parent\n   who commits such a murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiciform', 'a.', 'Shaped like a fern or like the parts of a fern leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filicoid', 'a.', 'Fernlike, either in form or in the nature of the method\n   of reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filicoid', 'n.', 'A fernlike plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiety', 'n.', 'The relation of a son to a father; sonship; -- the\n   correlative of paternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiferous', 'a.', 'Producing threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filiform', 'a.', 'Having the shape of a thread or filament; as, the\n   filiform papillae of the tongue; a filiform style or peduncle. See\n   Illust. of AntennAe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigrain', 'n.', 'Alt. of Filigrane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigrane', 'n.', 'Filigree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigraned', 'a.', 'See Filigreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigree', 'n.', 'Ornamental work, formerly with grains or breads, but now\n   composed of fine wire and used chiefly in decorating gold and silver to\n   which the wire is soldered, being arranged in designs frequently of a\n   delicate and intricate arabesque pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigree', 'a.', 'Relating to, composed of, or resembling, work in\n   filigree; as, a filigree basket. Hence: Fanciful; unsubstantial; merely\n   decorative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filigreed', 'a.', 'Adorned with filigree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filing', 'n.', 'A fragment or particle rubbed off by the act of filing; as,\n   iron filings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filipendulous', 'a.', 'Suspended by, or strung upon, a thread; -- said of\n   tuberous swellings in the middle or at the extremities of slender,\n   threadlike rootlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'n.', 'One of the thills or shafts of a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filled', 'imp. & p. p.', 'of Fill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filling', 'p. pr. & vb. n.', 'of Fill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To make full; to supply with as much as can be held or\n   contained; to put or pour into, till no more can be received; to occupy\n   the whole capacity of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To furnish an abudant supply to; to furnish with as mush as\n   is desired or desirable; to occupy the whole of; to swarm in or\n   overrun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To fill or supply fully with food; to feed; to satisfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To possess and perform the duties of; to officiate in, as an\n   incumbent; to occupy; to hold; as, a king fills a throne; the president\n   fills the office of chief magistrate; the speaker of the House fills\n   the chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To supply with an incumbent; as, to fill an office or a\n   vacancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To press and dilate, as a sail; as, the wind filled the\n   sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To trim (a yard) so that the wind shall blow on the after\n   side of the sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'a.', 'To make an embankment in, or raise the level of (a low\n   place), with earth or gravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'v. i.', 'To become full; to have the whole capacity occupied; to\n   have an abundant supply; to be satiated; as, corn fills well in a warm\n   season; the sail fills with the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'v. i.', 'To fill a cup or glass for drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fill', 'v. t.', 'A full supply, as much as supplies want; as much as gives\n   complete satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filler', 'n.', 'One who, or that which, fills; something used for filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filler', 'n.', 'A thill horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A little band, especially one intended to encircle the hair\n   of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A piece of lean meat without bone; sometimes, a long strip\n   rolled together and tied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A thin strip or ribbon; esp.: (a) A strip of metal from\n   which coins are punched. (b) A strip of card clothing. (c) A thin\n   projecting band or strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A concave filling in of a reentrant angle where two\n   surfaces meet, forming a rounded corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A narrow flat member; especially, a flat molding separating\n   other moldings; a reglet; also, the space between two flutings in a\n   shaft. See Illust. of Base, and Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'An ordinary equaling in breadth one fourth of the chief, to\n   the lowest portion of which it corresponds in position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'The thread of a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A border of broad or narrow lines of color or gilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'The raised molding about the muzzle of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'Any scantling smaller than a batten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'A fascia; a band of fibers; applied esp. to certain bands\n   of white matter in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'n.', 'The loins of a horse, beginning at the place where the\n   hinder part of the saddle rests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filleted', 'imp. & p. p.', 'of Fillet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filleting', 'p. pr. & vb. n.', 'of Fillet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillet', 'v. t.', 'To bind, furnish, or adorn with a fillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filleting', 'n.', 'The protecting of a joint, as between roof and parapet\n   wall, with mortar, or cement, where flashing is employed in better\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filleting', 'n.', 'The material of which fillets are made; also, fillets,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillibeg', 'n.', 'A kilt. See Filibeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillibuster', 'n.', 'See Filibuster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filling', 'n.', 'That which is used to fill a cavity or any empty space, or\n   to supply a deficiency; as, filling for a cavity in a tooth, a\n   depression in a roadbed, the space between exterior and interior walls\n   of masonry, the pores of open-grained wood, the space between the outer\n   and inner planks of a vessel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filling', 'n.', 'The woof in woven fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filling', 'n.', 'Prepared wort added to ale to cleanse it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filliped', 'imp. & p. p.', 'of Fillip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filliping', 'p. pr. & vb. n.', 'of Fillip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillip', 'v. t.', 'To strike with the nail of the finger, first placed\n   against the ball of the thumb, and forced from that position with a\n   sudden spring; to snap with the finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillip', 'v. t.', 'To snap; to project quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillip', 'n.', 'A jerk of the finger forced suddenly from the thumb; a\n   smart blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillip', 'n.', 'Something serving to rouse or excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillipeen', 'n.', 'See Philopena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillister', 'n.', 'The rabbet on the outer edge of a sash bar to hold the\n   glass and the putty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillister', 'n.', 'A plane for making a rabbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fillies', 'pl. ', 'of Filly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filly', 'n.', 'A female foal or colt; a young mare. Cf. Colt, Foal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filly', 'n.', 'A lively, spirited young girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Film', 'n.', 'A thin skin; a pellicle; a membranous covering, causing\n   opacity; hence, any thin, slight covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Film', 'n.', 'A slender thread, as that of a cobweb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Film', 'v. t.', 'To cover with a thin skin or pellicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filminess', 'n.', 'State of being filmy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filmy', 'a.', 'Composed of film or films.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filoplumaceous', 'a.', 'Having the structure of a filoplume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filoplume', 'n.', 'A hairlike feather; a father with a slender scape and\n   without a web in most or all of its length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filose', 'a.', 'Terminating in a threadlike process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filter', 'n.', 'Any porous substance, as cloth, paper, sand, or charcoal,\n   through which water or other liquid may passed to cleanse it from the\n   solid or impure matter held in suspension; a chamber or device\n   containing such substance; a strainer; also, a similar device for\n   purifying air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtered', 'imp. & p. p.', 'of Filter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtering', 'p. pr. & vb. n.', 'of Filter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filter', 'n.', 'To purify or defecate, as water or other liquid, by causing\n   it to pass through a filter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filter', 'v. i.', 'To pass through a filter; to percolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filter', 'n.', 'Same as Philter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filth', 'n.', 'Foul matter; anything that soils or defiles; dirt;\n   nastiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filth', 'n.', 'Anything that sullies or defiles the moral character;\n   corruption; pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filthily', 'adv.', 'In a filthy manner; foully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filthiness', 'n.', 'The state of being filthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filthiness', 'n.', 'That which is filthy, or makes filthy; foulness;\n   nastiness; corruption; pollution; impurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filthy', 'superl.', 'Defiled with filth, whether material or moral; nasty;\n   dirty; polluted; foul; impure; obscene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtrated', 'imp. & p. p.', 'of Filtrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtrating', 'p. pr. & vb. n.', 'of Filtrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtrate', 'v. t.', 'To filter; to defecate; as liquid, by straining or\n   percolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtrate', 'n.', 'That which has been filtered; the liquid which has passed\n   through the filter in the process of filtration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Filtration', 'n.', 'The act or process of filtering; the mechanical\n   separation of a liquid from the undissolved particles floating in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finble', '', 'Alt. of Fimble hemp');
