  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriae', 'pl. ', 'of Fimbria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbria', 'n.', 'A fringe, or fringed border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbria', 'n.', 'A band of white matter bordering the hippocampus in the\n   brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriate', 'a.', 'Having the edge or extremity bordered by filiform\n   processes thicker than hairs; fringed; as, the fimbriate petals of the\n   pink; the fimbriate end of the Fallopian tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriated', 'imp. & p. p.', 'of Fimbriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriating', 'p. pr. & vb. n.', 'of Fimbriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriate', 'v. t.', 'To hem; to fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriated', 'a.', 'Having a fringed border; fimbriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbriated', 'a.', 'Having a very narrow border of another tincture; --\n   said esp. of an ordinary or subordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbricate', 'a.', 'Fringed; jagged; fimbriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fimbricate', 'a.', 'fringed, on one side only, by long, straight hairs, as\n   the antennae of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finned', 'imp. & p. p.', 'of Fin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finning', 'p. pr. & vb. n.', 'of Fin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'v. t.', 'To carve or cut up, as a chub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'End; conclusion; object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'An organ of a fish, consisting of a membrane supported by\n   rays, or little bony or cartilaginous ossicles, and serving to balance\n   and propel it in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A membranous, finlike, swimming organ, as in pteropod and\n   heteropod mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A finlike organ or attachment; a part of an object or product\n   which protrudes like a fin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'The hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A blade of whalebone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A mark or ridge left on a casting at the junction of the parts\n   of a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'The thin sheet of metal squeezed out between the collars of\n   the rolls in the process of rolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A feather; a spline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin', 'n.', 'A finlike appendage, as to submarine boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finable', 'a.', 'Liable or subject to a fine; as, a finable person or\n   offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Final', 'a.', 'Pertaining to the end or conclusion; last; terminating;\n   ultimate; as, the final day of a school term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Final', 'a.', 'Conclusive; decisive; as, a final judgment; the battle of\n   Waterloo brought the contest to a final issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Final', 'a.', 'Respecting an end or object to be gained; respecting the\n   purpose or ultimate end in view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finale', 'n.', 'Close; termination');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finale', 'n.', 'The last movement of a symphony, sonata, concerto, or any\n   instrumental composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finale', 'n.', 'The last composition performed in any act of an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finale', 'n.', 'The closing part, piece, or scene in any public performance\n   or exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finalities', 'pl. ', 'of Finality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finality', 'n.', 'The state of being final, finished, or complete; a final\n   or conclusive arrangement; a settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finality', 'n.', 'The relation of end or purpose to its means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finally', 'adv.', 'At the end or conclusion; ultimately; lastly; as, the\n   contest was long, but the Romans finally conquered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finally', 'adv.', 'Completely; beyond recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finance', 'n.', 'The income of a ruler or of a state; revennue; public\n   money; sometimes, the income of an individual; often used in the plural\n   for funds; available money; resources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finance', 'n.', 'The science of raising and expending the public revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financial', 'a.', 'Pertaining to finance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financialist', 'n.', 'A financier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financially', 'adv.', 'In a dfinancial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financier', 'n.', 'One charged with the administration of finance; an\n   officer who administers the public revenue; a treasurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financier', 'n.', 'One skilled in financial operations; one acquainted with\n   money matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financiered', 'imp. & p. p.', 'of Financier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financiering', 'p. pr. & vb. n.', 'of Financier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Financier', 'v. i.', 'To conduct financial operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finary', 'n.', 'See Finery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finative', 'a.', 'Conclusive; decisive; definitive; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finback', 'n.', 'Any whale of the genera Sibbaldius, Balaenoptera, and\n   allied genera, of the family Balaenopteridae, characterized by a\n   prominent fin on the back. The common finbacks of the New England coast\n   are Sibbaldius tectirostris and S. tuberosus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishes', 'pl. ', 'of Finch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finch', 'n.', 'A small singing bird of many genera and species, belonging\n   to the family Fringillidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finchbacked', 'a.', 'Streaked or spotted on the back; -- said of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finched', 'a.', 'Same as Finchbacked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', 'imp. & p. p.', 'of Find');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finding', 'p. pr. & vb. n.', 'of Find');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To meet with, or light upon, accidentally; to gain the\n   first sight or knowledge of, as of something new, or unknown; hence, to\n   fall in with, as a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To learn by experience or trial; to perceive; to\n   experience; to discover by the intellect or the feelings; to detect; to\n   feel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To come upon by seeking; as, to find something lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To discover by sounding; as, to find bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To discover by study or experiment direct to an object or\n   end; as, water is found to be a compound substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To gain, as the object of desire or effort; as, to find\n   leisure; to find means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To attain to; to arrive at; to acquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To provide for; to supply; to furnish; as, to find food\n   for workemen; he finds his nephew in money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. t.', 'To arrive at, as a conclusion; to determine as true; to\n   establish; as, to find a verdict; to find a true bill (of indictment)\n   against an accused person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'v. i.', 'To determine an issue of fact, and to declare such a\n   determination to a court; as, the jury find for the plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Find', 'n.', 'Anything found; a discovery of anything valuable; especially,\n   a deposit, discovered by archaeologists, of objects of prehistoric or\n   unknown origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Findable', 'a.', 'Capable of beong found; discoverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finder', 'n.', 'One who, or that which, finds; specifically (Astron.), a\n   small telescope of low power and large field of view, attached to a\n   larger telescope, for the purpose of finding an object more readily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Findfault', 'n.', 'A censurer or caviler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Findfaulting', 'a.', 'Apt to censure or cavil; faultfinding; captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finding', 'n.', 'That which is found, come upon, or provided; esp. (pl.),\n   that which a journeyman artisan finds or provides for himself; as\n   tools, trimmings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finding', 'n.', 'Support; maintenance; that which is provided for one;\n   expence; provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finding', 'n.', 'The result of a judicial examination or inquiry,\n   especially into some matter of fact; a verdict; as, the finding of a\n   jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Findy', 'a.', 'Full; heavy; firm; solid; substemtial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Finished; brought to perfection; refined; hence, free\n   from impurity; excellent; superior; elegant; worthy of admiration;\n   accomplished; beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Aiming at show or effect; loaded with ornament;\n   overdressed or overdecorated; showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Nice; delicate; subtle; exquisite; artful; skillful;\n   dexterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Not coarse, gross, or heavy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Not gross; subtile; thin; tenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Not coarse; comminuted; in small particles; as, fine\n   sand or flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Not thick or heavy; slender; filmy; as, a fine thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Thin; attenuate; keen; as, a fine edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Made of fine materials; light; delicate; as, fine linen\n   or silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', 'Having (such) a proportion of pure metal in its\n   composition; as, coins nine tenths fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'superl.', '(Used ironically.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fined', 'imp. & p. p.', 'of Fine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fining', 'p. pr. & vb. n.', 'of Fine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'a.', 'To make fine; to refine; to purify, to clarify; as, to fine\n   gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'a.', 'To make finer, or less coarse, as in bulk, texture, etc.; as.\n   to fine the soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'a.', 'To change by fine gradations; as (Naut.), to fine down a\n   ships lines, to diminish her lines gradually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'n.', 'End; conclusion; termination; extinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'n.', 'A sum of money paid as the settlement of a claim, or by way\n   of terminating a matter in dispute; especially, a payment of money\n   imposed upon a party as a punishment for an offense; a mulct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'n.', 'A final agreement concerning lands or rents between persons,\n   as the lord and his vassal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'n.', 'A sum of money or price paid for obtaining a benefit, favor,\n   or privilege, as for admission to a copyhold, or for obtaining or\n   renewing a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'n.', 'To impose a pecuniary penalty upon for an offense or breach\n   of law; to set a fine on by judgment of a court; to punish by fine; to\n   mulct; as, the trespassers were fined ten dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'v. i.', 'To pay a fine. See Fine, n., 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fine', 'v. t.', 'To finish; to cease; or to cause to cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finedrawn', 'imp. & p. p.', 'of Finedraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finedrawing', 'p. pr. & vb. n.', 'of Finedraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finedraw', 'v. t.', 'To sew up, so nicely that the seam is not perceived;\n   to renter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finedrawer', 'n.', 'One who finedraws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finedrawn', 'a.', 'Drawn out with too much subtilty; overnice; as,\n   finedrawn speculations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineer', 'v. i.', 'To run in dept by getting goods made up in a way\n   unsuitable for the use of others, and then threatening not to take them\n   except on credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineer', 'v. t.', 'To veneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineless', 'a.', 'Endless; boundless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finely', 'adv.', 'In a fine or finished manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineness', 'a.', 'The quality or condition of being fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineness', 'a.', 'Freedom from foreign matter or alloy; clearness; purity;\n   as, the fineness of liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineness', 'a.', 'The proportion of pure silver or gold in jewelry,\n   bullion, or coins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fineness', 'a.', 'Keenness or sharpness; as, the fineness of a needles\n   point, or of the edge of a blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finer', 'n.', 'One who fines or purifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finery', 'n.', 'Fineness; beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finery', 'n.', 'Ornament; decoration; especially, excecially decoration;\n   showy clothes; jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finery', 'n.', 'A charcoal hearth or furnace for the conversion of cast\n   iron into wrought iron, or into iron suitable for puddling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finespun', 'a.', 'Spun so as to be fine; drawn to a fine thread;\n   attenuated; hence, unsubstantial; visionary; as, finespun theories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finesse', 'a.', 'Subtilty of contrivance to gain a point; artifice;\n   stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finesse', 'a.', 'The act of finessing. See Finesse, v. i., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finessed', 'imp. & p. p.', 'of Finesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finessing', 'p. pr. & vb. n.', 'of Finesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finesse', 'v. i.', 'To use artifice or stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finesse', 'v. i.', 'To attempt, when second or third player, to make a\n   lower card answer the purpose of a higher, when an intermediate card is\n   out, risking the chance of its being held by the opponent yet to play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finestill', 'v. t.', 'To distill, as spirit from molasses or some\n   saccharine preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finestiller', 'n.', 'One who finestills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finew', 'n.', 'Moldiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finfish', 'n.', 'A finback whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finfish', 'n.', 'True fish, as distinguished from shellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finfoot', 'n.', 'A South American bird (heliornis fulica) allied to the\n   grebes. The name is also applied to several related species of the\n   genus Podica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin-footed', 'a.', 'Having palmate feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin-footed', 'a.', 'Having lobate toes, as the coot and grebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'n.', 'One of the five terminating members of the hand; a digit;\n   esp., one of the four extermities of the hand, other than the thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'n.', 'Anything that does work of a finger; as, the pointer of a\n   clock, watch, or other registering machine; especially (Mech.) a small\n   projecting rod, wire, or piece, which is brought into contact with an\n   object to effect, direct, or restrain a motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'n.', 'The breadth of a finger, or the fourth part of the hand; a\n   measure of nearly an inch; also, the length of finger, a measure in\n   domestic use in the United States, of about four and a half inches or\n   one eighth of a yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'n.', 'Skill in the use of the fingers, as in playing upon a\n   musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingered', 'imp. & p. p.', 'of Finger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingering', 'p. pr. & vb. n.', 'of Finger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To touch with the fingers; to handle; to meddle with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To touch lightly; to toy with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To perform on an instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To mark the notes of (a piece of music) so as to guide\n   the fingers in playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To take thievishly; to pilfer; to purloin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. t.', 'To execute, as any delicate work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finger', 'v. i.', 'To use the fingers in playing on an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingered', 'a.', 'Having fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingered', 'a.', 'Having leaflets like fingers; digitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingered', 'a.', 'Marked with figures designating which finger should be\n   used for each note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingerer', 'n.', 'One who fingers; a pilferer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingering', 'n.', 'The act or process of handling or touching with the\n   fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingering', 'n.', 'The manner of using the fingers in playing or striking\n   the keys of an instrument of music; movement or management of the\n   fingers in playing on a musical instrument, in typewriting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingering', 'n.', 'The marking of the notes of a piece of music to guide or\n   regulate the action or use of the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingering', 'n.', 'Delicate work made with the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingerling', 'n.', 'A young salmon. See Parr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingle-fangle', 'n.', 'A trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingrigos', 'pl. ', 'of Fingrigo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fingrigo', 'n.', 'A prickly, climbing shrub of the genus Pisonia. The fruit\n   is a kind of berry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finial', 'n.', 'The knot or bunch of foliage, or foliated ornament, that\n   forms the upper extremity of a pinnacle in Gothic architecture;\n   sometimes, the pinnacle itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finical', 'a.', 'Affectedly fine; overnice; unduly particular; fastidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finicality', 'n.', 'The quality of being finical; finicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finicking', 'a.', 'Alt. of Finicky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finicky', 'a.', 'Finical; unduly particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finific', 'n.', 'A limiting element or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finify', 'a.', 'To make fine; to dress finically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finikin', 'a.', 'Precise in trifles; idly busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fining', 'n.', 'The act of imposing a fin/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fining', 'n.', 'The process of fining or refining; clarification; also\n   (Metal.), the conversion of cast iron into suitable for puddling, in a\n   hearth or charcoal fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fining', 'n.', 'That which is used to refine; especially, a preparation of\n   isinglass, gelatin, etc., for clarifying beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finis', 'n.', 'An end; conclusion. It is often placed at the end of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finished', 'imp. & p. p.', 'of Finish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finishing', 'p. pr. & vb. n.', 'of Finish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'v. t.', 'To arrive at the end of; to bring to an end; to put an\n   end to; to make an end of; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'v. t.', 'To bestow the last required labor upon; to complete; to\n   bestow the utmost possible labor upon; to perfect; to accomplish; to\n   polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'v. i.', 'To come to an end; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'v. i.', 'To end; to die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'That which finishes, puts an end to/ or perfects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'The joiner work and other finer work required for the\n   completion of a building, especially of the interior. See Inside\n   finish, and Outside finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'The labor required to give final completion to any work;\n   hence, minute detail, careful elaboration, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'See Finishing coat, under Finishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'The result of completed labor, as on the surface of an\n   object; manner or style of finishing; as, a rough, dead, or glossy\n   finish given to cloth, stone, metal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finish', 'n.', 'Completion; -- opposed to start, or beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finished', 'a.', 'Polished to the highest degree of excellence; complete;\n   perfect; as, a finished poem; a finished education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finisher', 'n.', 'One who finishes, puts an end to, completes, or perfects;\n   esp. used in the trades, as in hatting, weaving, etc., for the workman\n   who gives a finishing touch to the work, or any part of it, and brings\n   it to perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finisher', 'n.', 'Something that gives the finishing touch to, or settles,\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finishing', 'n.', 'The act or process of completing or perfecting; the\n   final work upon or ornamentation of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finishing', 'a.', 'Tending to complete or to render fit for the market or\n   for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finite', 'a.', 'Having a limit; limited in quantity, degree, or capacity;\n   bounded; -- opposed to infinite; as, finite number; finite existence; a\n   finite being; a finite mind; finite duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finiteless', 'a.', 'Infinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finitely', 'adv.', 'In a finite manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finiteness', 'n.', 'The state of being finite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finitude', 'n.', 'Limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finlander', 'n.', 'A native or inhabitant of Finland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finless', 'a.', 'destitute of fins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finlet', 'n.', 'A little fin; one of the parts of a divided fin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finlike', 'a.', 'Resembling a fin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finn', 'a.', 'A native of Finland; one of the Finn/ in the ethnological\n   sense. See Finns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finnan haddie', '', 'Haddock cured in peat smoke, originally at Findon\n   (pron. fin\"an), Scotland. the name is also applied to other kinds of\n   smoked haddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finned', 'a.', 'Having a fin, or fins, or anything resembling a fin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finner', 'n.', 'A finback whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finnic', 'a.', 'Of or pertaining to the Finns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finnikin', 'n.', 'A variety of pigeon, with a crest somewhat resembling the\n   mane of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finnish', 'a.', 'Of or pertaining to Finland, to the Finns, or to their\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finnish', 'n.', 'A Northern Turanian group of languages; the language of\n   the Finns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finns', 'n. pl.', 'Natives of Finland; Finlanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finns', 'n. pl.', 'A branch of the Mongolian race, inhabiting Northern and\n   Eastern Europe, including the Magyars, Bulgarians, Permians, Lapps, and\n   Finlanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finny', 'a.', 'Having, or abounding in, fins, as fishes; pertaining to\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finny', 'a.', 'Abounding in fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finochio', 'n.', 'An umbelliferous plant (Foeniculum dulce) having a\n   somewhat tuberous stem; sweet fennel. The blanched stems are used in\n   France and Italy as a culinary vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finos', 'n. pl.', 'Second best wool from Merino sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Finpike', 'n.', 'The bichir. See Crossopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fint', '', '3d pers. sing. pr. of Find, for findeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fin-toed', 'a.', 'Having toes connected by a membrane; palmiped; palmated;\n   also, lobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiord', 'n.', 'A narrow inlet of the sea, penetrating between high banks or\n   rocks, as on the coasts of Norway and Alaska.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiorin', 'n.', 'A species of creeping bent grass (Agrostis alba); -- called\n   also fiorin grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiorite', 'n.', 'A variety of opal occuring in the cavities of volcanic\n   tufa, in smooth and shining globular and botryoidal masses, having a\n   pearly luster; -- so called from Fiora, in Ischia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fioriture', 'n. pl.', 'Little flowers of ornament introduced into a melody\n   by a singer or player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fippenny bit', '', 'The Spanish half real, or one sixteenth of a dollar, --\n   so called in Pennsylvania and the adjacent States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fipple', 'n.', 'A stopper, as in a wind instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fir', 'n.', 'A genus (Abies) of coniferous trees, often of large size and\n   elegant shape, some of them valued for their timber and others for\n   their resin. The species are distinguished as the balsam fir, the\n   silver fir, the red fir, etc. The Scotch fir is a Pinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'The evolution of light and heat in the combustion of bodies;\n   combustion; state of ignition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Fuel in a state of combustion, as on a hearth, or in a stove\n   or a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'The burning of a house or town; a conflagration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Anything which destroys or affects like fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Ardor of passion, whether love or hate; excessive warmth;\n   consuming violence of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Liveliness of imagination or fancy; intellectual and moral\n   enthusiasm; capacity for ardor and zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Splendor; brilliancy; luster; hence, a star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'Torture by burning; severe trial or affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'n.', 'The discharge of firearms; firing; as, the troops were\n   exposed to a heavy fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fired', 'imp. & p. p.', 'of Fire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fring', 'p. pr. & vb. n.', 'of Fire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To set on fire; to kindle; as, to fire a house or chimney;\n   to fire a pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To subject to intense heat; to bake; to burn in a kiln;\n   as, to fire pottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To inflame; to irritate, as the passions; as, to fire the\n   soul with anger, pride, or revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To animate; to give life or spirit to; as, to fire the\n   genius of a young man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To feed or serve the fire of; as, to fire a boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To light up as if by fire; to illuminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To cause to explode; as, to fire a torpedo; to disharge;\n   as, to fire a musket or cannon; to fire cannon balls, rockets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To drive by fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. t.', 'To cauterize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. i.', 'To take fire; to be kindled; to kindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. i.', 'To be irritated or inflamed with passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire', 'v. i.', 'To discharge artillery or firearms; as, they fired on the\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firearm', 'n.', 'A gun, pistol, or any weapon from a shot is discharged by\n   the force of an explosive substance, as gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireback', 'n.', 'One of several species of pheasants of the genus\n   Euplocamus, having the lower back a bright, fiery red. They inhabit\n   Southern Asia and the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireball', 'n.', 'A ball filled with powder or other combustibles, intended\n   to be thrown among enemies, and to injure by explosion; also, to set\n   fire to their works and light them up, so that movements may be seen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireball', 'n.', 'A luminous meteor, resembling a ball of fire passing\n   rapidly through the air, and sometimes exploding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firebare', 'n.', 'A beacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire beetle', '', 'A very brilliantly luminous beetle (Pyrophorus\n   noctilucus), one of the elaters, found in Central and South America; --\n   called also cucujo. The name is also applied to other species. See\n   Firefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firebird', 'n.', 'The Baltimore oriole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireboard', 'n.', 'A chimney board or screen to close a fireplace when not\n   in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firebote', 'n.', 'An allowance of fuel. See Bote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firebrand', 'n.', 'A piece of burning wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firebrand', 'n.', 'One who inflames factions, or causes contention and\n   mischief; an incendiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firecracker', 'n.', 'See Cracker., n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firecrest', 'n.', 'A small European kinglet (Regulus ignicapillus), having\n   a bright red crest; -- called also fire-crested wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firedog', 'n.', 'A support for wood in a fireplace; an andiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firedrake', 'n.', 'A fiery dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firedrake', 'n.', 'A fiery meteor; an ignis fatuus; a rocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firedrake', 'n.', 'A worker at a furnace or fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire-fanged', 'a.', 'Injured as by fire; burned; -- said of manure which\n   has lost its goodness and acquired an ashy hue in consequence of heat\n   generated by decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firefish', 'n.', 'A singular marine fish of the genus Pterois, family\n   Scorpaenidae, of several species, inhabiting the Indo-Pacific region.\n   They are usually red, and have very large spinose pectoral and dorsal\n   fins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireflaire', 'n.', 'A European sting ray of the genus Trygon (T.\n   pastinaca); -- called also fireflare and fiery flaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireflame', 'n.', 'The European band fish (Cepola rubescens).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireflies', 'pl. ', 'of Firefly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firefly', 'n.', 'Any luminous winged insect, esp. luminous beetles of the\n   family Lampyridae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireless', 'a.', 'Destitute of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firelock', 'n.', 'An old form of gunlock, as the flintlock, which ignites\n   the priming by a spark; perhaps originally, a matchlock. Hence, a gun\n   having such a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firemen', 'pl. ', 'of Fireman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireman', 'n.', 'A man whose business is to extinguish fires in towns; a\n   member of a fire company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireman', 'n.', 'A man who tends the fires, as of a steam engine; a\n   stocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire-new', 'a.', 'Fresh from the forge; bright; quite new; brand-new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireplace', 'n.', 'The part a chimney appropriated to the fire; a hearth;\n   -- usually an open recess in a wall, in which a fire may be built.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireproof', 'a.', 'Proof against fire; incombustible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireprrofing', 'n.', 'The act or process of rendering anything\n   incombustible; also, the materials used in the process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firer', 'n.', 'One who fires or sets fire to anything; an incendiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fire-set', 'n.', 'A set of fire irons, including, commonly, tongs, shovel,\n   and poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireside', 'n.', 'A place near the fire or hearth; home; domestic life or\n   retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firestone', 'n.', 'Iron pyrites, formerly used for striking fire; also, a\n   flint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firestone', 'n.', 'A stone which will bear the heat of a furnace without\n   injury; -- especially applied to the sandstone at the top of the upper\n   greensand in the south of England, used for lining kilns and furnaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firetail', 'n.', 'The European redstart; -- called also fireflirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firewarden', 'n.', 'An officer who has authority to direct in the\n   extinguishing of fires, or to order what precautions shall be taken\n   against fires; -- called also fireward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireweed', 'n.', 'An American plant (Erechthites hiercifolia), very\n   troublesome in spots where brushwood has been burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireweed', 'n.', 'The great willow-herb (Epilobium spicatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firewood', 'n.', 'Wood for fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firework', 'n.', 'A device for producing a striking display of light, or a\n   figure or figures in plain or colored fire, by the combustion of\n   materials that burn in some peculiar manner, as gunpowder, sulphur,\n   metallic filings, and various salts. The most common feature of\n   fireworks is a paper or pasteboard tube filled with the combustible\n   material. A number of these tubes or cases are often combined so as to\n   make, when kindled, a great variety of figures in fire, often variously\n   colored. The skyrocket is a common form of firework. The name is also\n   given to various combustible preparations used in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firework', 'n.', 'A pyrotechnic exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fireworm', 'n.', 'The larva of a small tortricid moth which eats the leaves\n   of the cranberry, so that the vines look as if burned; -- called also\n   cranberry worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firing', 'n.', 'The act of disharging firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firing', 'n.', 'The mode of introducing fuel into the furnace and working\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firing', 'n.', 'The application of fire, or of a cautery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firing', 'n.', 'The process of partly vitrifying pottery by exposing it to\n   intense heat in a kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firing', 'n.', 'Fuel; firewood or coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firk', 'v. t.', 'To beat; to strike; to chastise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firk', 'v. i.', 'To fly out; to turn out; to go off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firk', 'n.', 'A freak; trick; quirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firkin', 'n.', 'A varying measure of capacity, usually being the fourth\n   part of a barrel; specifically, a measure equal to nine imperial\n   gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firkin', 'n.', 'A small wooden vessel or cask of indeterminate size, --\n   used for butter, lard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firlot', 'n.', 'A dry measure formerly used in Scotland; the fourth part of\n   a boll of grain or meal. The Linlithgow wheat firlot was to the\n   imperial bushel as 998 to 1000; the barley firlot as 1456 to 1000.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'superl.', 'Fixed; hence, closely compressed; compact; substantial;\n   hard; solid; -- applied to the matter of bodies; as, firm flesh; firm\n   muscles, firm wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'superl.', 'Not easily excited or disturbed; unchanging in purpose;\n   fixed; steady; constant; stable; unshaken; not easily changed in\n   feelings or will; strong; as, a firm believer; a firm friend; a firm\n   adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'superl.', 'Solid; -- opposed to fluid; as, firm land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'superl.', 'Indicating firmness; as, a firm tread; a firm\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'a.', 'The name, title, or style, under which a company transacts\n   business; a partnership of two or more persons; a commercial house; as,\n   the firm of Hope & Co.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'a.', 'To fix; to settle; to confirm; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firm', 'a.', 'To fix or direct with firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmament', 'v. & a.', 'Fixed foundation; established basis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmament', 'v. & a.', 'The region of the air; the sky or heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmament', 'v. & a.', 'The orb of the fixed stars; the most rmote of the\n   celestial spheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmamental', 'a.', 'Pertaining to the firmament; celestial; being of the\n   upper regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmans', 'pl. ', 'of Firman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firman', 'n.', 'In Turkey and some other Oriental countries, a decree or\n   mandate issued by the sovereign; a royal order or grant; -- generally\n   given for special objects, as to a traveler to insure him protection\n   and assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmer-chisel', 'n.', 'A chisel, thin in proportion to its width. It has a\n   tang to enter the handle instead of a socket for receiving it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmitude', 'n.', 'Strength; stability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmity', 'n.', 'Strength; firmness; stability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmless', 'a.', 'Detached from substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmless', 'a.', 'Infirm; unstable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmly', 'adv.', 'In a firm manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firmness', 'n.', 'The state or quality of being firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firms', 'a.', 'The principal rafters of a roof, especially a pair of\n   rafters taken together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firring', 'n.', 'See Furring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firry', 'a.', 'Made of fir; abounding in firs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First', 'a.', 'Preceding all others of a series or kind; the ordinal of\n   one; earliest; as, the first day of a month; the first year of a reign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First', 'a.', 'Foremost; in front of, or in advance of, all others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First', 'a.', 'Most eminent or exalted; most excellent; chief; highest; as,\n   Demosthenes was the first orator of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First', 'adv.', 'Before any other person or thing in time, space, rank,\n   etc.; -- much used in composition with adjectives and participles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First', 'n.', 'The upper part of a duet, trio, etc., either vocal or\n   instrumental; -- so called because it generally expresses the air, and\n   has a preeminence in the combined effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firstborn', 'a.', 'First brought forth; first in the order of nativity;\n   eldest; hence, most excellent; most distinguished or exalted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First-class', 'a.', 'Of the best class; of the highest rank; in the first\n   division; of the best quality; first-rate; as, a first-class telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First-hand', 'a.', 'Obtained directly from the first or original source;\n   hence, without the intervention of an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firstling', 'n.', 'The first produce or offspring; -- said of animals,\n   especially domestic animals; as, the firstlings of his flock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firstling', 'n.', 'The thing first thought or done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firstling', 'a.', 'Firstborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firstly', 'adv.', 'In the first place; before anything else; -- sometimes\n   improperly used for first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First-rate', 'a.', 'Of the highest excellence; preeminent in quality, size,\n   or estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'First-rate', 'n.', 'A war vessel of the highest grade or the most powerful\n   class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Firth', 'n.', 'An arm of the sea; a frith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fir tree', '', 'See Fir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisc', 'n.', 'A public or state treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiscal', 'a.', 'Pertaining to the public treasury or revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiscal', 'n.', 'The income of a prince or a state; revenue; exhequer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiscal', 'n.', 'A treasurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiscal', 'n.', 'A public officer in Scotland who prosecutes in petty\n   criminal cases; -- called also procurator fiscal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiscal', 'n.', 'The solicitor in Spain and Portugal; the attorney-general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisetic', 'a.', 'Pertaining to fustet or fisetin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisetin', 'n.', 'A yellow crystalline substance extracted from fustet, and\n   regarded as its essential coloring principle; -- called also fisetic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'A counter, used in various games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishes', 'pl. ', 'of Fish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'pl. ', 'of Fish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'A name loosely applied in popular usage to many animals of\n   diverse characteristics, living in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'An oviparous, vertebrate animal usually having fins and a\n   covering scales or plates. It breathes by means of gills, and lives\n   almost entirely in the water. See Pisces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'The twelfth sign of the zodiac; Pisces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'The flesh of fish, used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'A purchase used to fish the anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'n.', 'A piece of timber, somewhat in the form of a fish, used to\n   strengthen a mast or yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fished', 'imp. & p. p.', 'of Fish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishing', 'p. pr. & vb. n.', 'of Fish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. i.', 'To attempt to catch fish; to be employed in taking fish,\n   by any means, as by angling or drawing a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. i.', 'To seek to obtain by artifice, or indirectly to seek to\n   draw forth; as, to fish for compliments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. t.', 'To catch; to draw out or up; as, to fish up an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. t.', 'To search by raking or sweeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. t.', 'To try with a fishing rod; to catch fish in; as, to fish a\n   stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish', 'v. t.', 'To strengthen (a beam, mast, etc.), or unite end to end\n   (two timbers, railroad rails, etc.) by bolting a plank, timber, or\n   plate to the beam, mast, or timbers, lengthwise on one or both sides.\n   See Fish joint, under Fish, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish-bellied', 'a.', 'Bellying or swelling out on the under side; as, a\n   fish-bellied rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish-block', 'n.', 'See Fish-tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisher', 'n.', 'One who fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisher', 'n.', 'A carnivorous animal of the Weasel family (Mustela\n   Canadensis); the pekan; the \"black cat.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishermen', 'pl. ', 'of Fisherman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisherman', 'n.', 'One whose occupation is to catch fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisherman', 'n.', 'A ship or vessel employed in the business of taking\n   fish, as in the cod fishery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisheries', 'pl. ', 'of Fishery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishery', 'n.', 'The business or practice of catching fish; fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishery', 'n.', 'A place for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishery', 'n.', 'The right to take fish at a certain place, or in\n   particular waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishful', 'a.', 'Abounding with fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishgig', 'n.', 'A spear with barbed prongs used for harpooning fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishhawk', 'n.', 'The osprey (Pandion haliaetus), found both in Europe and\n   America; -- so called because it plunges into the water and seizes\n   fishes in its talons. Called also fishing eagle, and bald buzzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishhook', 'n.', 'A hook for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishhook', 'n.', 'A hook with a pendant, to the end of which the\n   fish-tackle is hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishify', 'v. t.', 'To change to fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishiness', 'n.', 'The state or quality of being fishy or fishlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishing', 'n.', 'The act, practice, or art of one who fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishing', 'n.', 'A fishery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishing', 'n.', 'Pertaining to fishing; used in fishery; engaged in\n   fishing; as, fishing boat; fishing tackle; fishing village.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishlike', 'a.', 'Like fish; suggestive of fish; having some of the\n   qualities of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishmonger', 'n.', 'A dealer in fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishskin', 'n.', 'The skin of a fish (dog fish, shark, etc.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishskin', 'n.', 'See Ichthyosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish-tackle', 'n.', 'A tackle or purchase used to raise the flukes of the\n   anchor up to the gunwale. The block used is called the fish-block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fish-tail', 'a.', 'Like the of a fish; acting, or producing something, like\n   the tail of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishwife', 'n.', 'A fishwoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishwomen', 'pl. ', 'of Fishwoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishwoman', 'n.', 'A woman who retails fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishy', 'a.', 'Consisting of fish; fishlike; having the qualities or taste\n   of fish; abounding in fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fishy', 'a.', 'Extravagant, like some stories about catching fish;\n   improbable; also, rank or foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisk', 'v. i.', 'To run about; to frisk; to whisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissigemmation', 'n.', 'A process of reproduction intermediate between\n   fission and gemmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissile', 'a.', 'Capable of being split, cleft, or divided in the direction\n   of the grain, like wood, or along natural planes of cleavage, like\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissilingual', 'a.', 'Having the tongue forked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissilinguia', 'n. pl.', 'A group of Lacertilia having the tongue forked,\n   including the common lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissility', 'n.', 'Quality of being fissile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fission', 'n.', 'A cleaving, splitting, or breaking up into parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fission', 'n.', 'A method of asexual reproduction among the lowest\n   (unicellular) organisms by means of a process of self-division,\n   consisting of gradual division or cleavage of the into two parts, each\n   of which then becomes a separate and independent organisms; as when a\n   cell in an animal or plant, or its germ, undergoes a spontaneous\n   division, and the parts again subdivide. See Segmentation, and Cell\n   division, under Division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fission', 'n.', 'A process by which certain coral polyps, echinoderms,\n   annelids, etc., spontaneously subdivide, each individual thus forming\n   two or more new ones. See Strobilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissipalmate', 'a.', 'Semipalmate and loboped, as a grebes foot. See\n   Illust. under Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissipara', 'n. pl.', 'Animals which reproduce by fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissiparism', 'n.', 'Reproduction by spontaneous fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissiparity', 'n.', 'Quality of being fissiparous; fissiparism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissiparous', 'a.', 'Reproducing by spontaneous fission. See Fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissipation', 'n.', 'Reproduction by fission; fissiparism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissiped', 'a.', 'Alt. of Fissipedal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissipedal', 'a.', 'Having the toes separated to the base. [See Aves.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissiped', 'n.', 'One of the Fissipedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissipedia', 'n. pl.', 'A division of the Carnivora, including the dogs,\n   cats, and bears, in which the feet are not webbed; -- opposed to\n   Pinnipedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissirostral', 'a.', 'Having the bill cleft beyond the horny part, as in\n   the case of swallows and goatsuckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissirostres', 'n. pl.', 'A group of birds having the bill deeply cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissural', 'a.', 'Pertaining to a fissure or fissures; as, the fissural\n   pattern of a brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissuration', 'n.', 'The act of dividing or opening; the state of being\n   fissured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissure', 'n.', 'A narrow opening, made by the parting of any substance; a\n   cleft; as, the fissure of a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissure', 'v. t.', 'To cleave; to divide; to crack or fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fissurella', 'n.', 'A genus of marine gastropod mollusks, having a conical\n   or limpetlike shell, with an opening at the apex; -- called also\n   keyhole limpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fist', 'n.', 'The hand with the fingers doubled into the palm; the closed\n   hand, especially as clinched tightly for the purpose of striking a\n   blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fist', 'n.', 'The talons of a bird of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fist', 'n.', 'the index mark [/], used to direct special attention to the\n   passage which follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisted', 'imp. & p. p.', 'of Fist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisting', 'p. pr. & vb. n.', 'of Fist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fist', 'v. t.', 'To strike with the fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fist', 'v. t.', 'To gripe with the fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistic', 'a.', 'Pertaining to boxing, or to encounters with the fists;\n   puglistic; as, fistic exploits; fistic heroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisticuff', 'n.', 'A cuff or blow with the fist or hand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fisticuff', 'n.', 'a fight with the fists; boxing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistinut', 'n.', 'A pistachio nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistuca', 'n.', 'An instrument used by the ancients in driving piles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistulae', 'pl. ', 'of Fistula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistula', 'n.', 'A reed; a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistula', 'n.', 'A pipe for convejing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistula', 'n.', 'A permanent abnormal opening into the soft parts with a\n   constant discharge; a deep, narrow, chronic abscess; an abnormal\n   opening between an internal cavity and another cavity or the surface;\n   as, a salivary fistula; an anal fistula; a recto-vaginal fistula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistular', 'a.', 'Hollow and cylindrical, like a pipe or reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistularia', 'n.', 'A genus of fishes, having the head prolonged into a\n   tube, with the mouth at the extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistularioid', 'a.', 'Like or pertaining to the genus Fistularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistulate', 'v. t. & i.', 'To make hollow or become hollow like a fistula,\n   or pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistule', 'n.', 'A fistula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistuliform', 'a.', 'Of a fistular form; tubular; pipe-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistulose', 'a.', 'Formed like a fistula; hollow; reedlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistulous', 'a.', 'Having the form or nature of a fistula; as, a fistulous\n   ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fistulous', 'a.', 'Hollow, like a pipe or reed; fistulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', '', 'imp. & p. p. of Fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'In Old English, a song; a strain; a canto or portion of a\n   ballad; a passus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'superl.', 'Adapted to an end, object, or design; suitable by nature\n   or by art; suited by character, qualitties, circumstances, education,\n   etc.; qualified; competent; worthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'superl.', 'Prepared; ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'superl.', 'Conformed to a standart of duty, properiety, or taste;\n   convenient; meet; becoming; proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitted', 'imp. & p. p.', 'of Fit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitting', 'p. pr. & vb. n.', 'of Fit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. t.', 'To make fit or suitable; to adapt to the purpose intended;\n   to qualify; to put into a condition of readiness or preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. t.', 'To bring to a required form and size; to shape aright; to\n   adapt to a model; to adjust; -- said especially of the work of a\n   carpenter, machinist, tailor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. t.', 'To supply with something that is suitable or fit, or that\n   is shaped and adjusted to the use required.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. t.', 'To be suitable to; to answer the requirements of; to be\n   correctly shaped and adjusted to; as, if the coat fits you, put it on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. i.', 'To be proper or becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'v. i.', 'To be adjusted to a particular shape or size; to suit; to\n   be adapted; as, his coat fits very well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'The quality of being fit; adjustment; adaptedness; as of dress\n   to the person of the wearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'The coincidence of parts that come in contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'The part of an object upon which anything fits tightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'A stroke or blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'A sudden and violent attack of a disorder; a stroke of\n   disease, as of epilepsy or apoplexy, which produces convulsions or\n   unconsciousness; a convulsion; a paroxysm; hence, a period of\n   exacerbation of a disease; in general, an attack of disease; as, a fit\n   of sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'A mood of any kind which masters or possesses one for a time;\n   a temporary, absorbing affection; a paroxysm; as, a fit melancholy, of\n   passion, or of laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'A passing humor; a caprice; a sudden and unusual effort,\n   activity, or motion, followed by relaxation or insction; an impulse and\n   irregular action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fit', 'n.', 'A darting point; a sudden emission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitches', 'pl. ', 'of Fitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitch', 'n.', 'A vetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitch', 'n.', 'A word found in the Authorized Version of the Bible,\n   representing different Hebrew originals. In Isaiah xxviii. 25, 27, it\n   means the black aromatic seeds of Nigella sativa, still used as a\n   flavoring in the East. In Ezekiel iv. 9, the Revised Version now reads\n   spelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitch', 'n.', 'The European polecat; also, its fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitche', 'a.', 'Sharpened to a point; pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitched', 'a.', 'Fitche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitchet', 'n.', 'Alt. of Fitchew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitchew', 'n.', 'The European polecat (Putorius foetidus). See Polecat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitchy', 'a.', 'Having fitches or vetches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitchy', 'a.', 'Fitche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitful', 'a.', 'Full of fits; irregularly variable; impulsive and unstable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fithel', 'n.', 'Alt. of Fithul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fithul', 'n.', 'A fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitly', 'adv.', 'In a fit manner; suitably; properly; conveniently; as, a\n   maxim fitly applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitment', 'n.', 'The act of fitting; that which is proper or becoming;\n   equipment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitness', 'n.', 'The state or quality of being fit; as, the fitness of\n   measures or laws; a persons fitness for office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitt', 'n.', 'See 2d Fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fittable', 'a.', 'Suitable; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fittedness', 'n.', 'The state or quality of being fitted; adaptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitter', 'n.', 'One who fits or makes to fit;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitter', 'n.', 'One who tries on, and adjusts, articles of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitter', 'n.', 'One who fits or adjusts the different parts of machinery to\n   each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitter', 'n.', 'A coal broker who conducts the sales between the owner of a\n   coal pit and the shipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitter', 'n.', 'A little piece; a flitter; a flinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitting', 'n.', 'Anything used in fitting up');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitting', 'n.', 'necessary fixtures or apparatus; as, the fittings of a\n   church or study; gas fittings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitting', 'a.', 'Fit; appropriate; suitable; proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitweed', 'n.', 'A plant (Eryngium foetidum) supposed to be a remedy for\n   fits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fitz', 'n.', 'A son; -- used in compound names, to indicate paternity, esp.\n   of the illegitimate sons of kings and princes of the blood; as,\n   Fitzroy, the son of the king; Fitzclarence, the son of the duke of\n   Clarence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five', 'a.', 'Four and one added; one more than four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five', 'n.', 'The number next greater than four, and less than six; five\n   units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five', 'n.', 'A symbol representing this number, as 5, or V.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-finger', 'n.', 'See Cinquefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-finger', 'n.', 'A starfish with five rays, esp. Asterias rubens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fivefold', 'a. & adv.', 'In fives; consisting of five in one; five\n   repeated; quintuple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-leaf', 'n.', 'Cinquefoil; five-finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-leafed', 'a.', 'Alt. of Five-leaved');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-leaved', 'a.', 'Having five leaflets, as the Virginia creeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fiveling', 'n.', 'A compound or twin crystal consisting of five\n   individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fives', 'n. pl.', 'A kind of play with a ball against a wall, resembling\n   tennis; -- so named because three fives, or fifteen, are counted to the\n   game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fives', 'n.', 'A disease of the glands under the ear in horses; the vives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Five-twenties', 'n. pl.', 'Five-twenty bonds of the United States (bearing\n   six per cent interest), issued in 1862, 64, and 65, redeemable after\n   five and payable in twenty years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'a.', 'Fixed; solidified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixed', 'imp. & p. p.', 'of Fix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixing', 'p. pr. & vb. n.', 'of Fix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To make firm, stable, or fast; to set or place permanently;\n   to fasten immovably; to establish; to implant; to secure; to make\n   definite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To hold steadily; to direct unwaveringly; to fasten, as the\n   eye on an object, the attention on a speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To transfix; to pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To render (an impression) permanent by treating with such\n   applications as will make it insensible to the action of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To put in order; to arrange; to dispose of; to adjust; to\n   set to rights; to set or place in the manner desired or most suitable;\n   hence, to repair; as, to fix the clothes; to fix the furniture of a\n   room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. t.', 'To line the hearth of (a puddling furnace) with fettling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. i.', 'To become fixed; to settle or remain permanently; to cease\n   from wandering; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'v. i.', 'To become firm, so as to resist volatilization; to cease to\n   flow or be fluid; to congeal; to become hard and malleable, as a\n   metallic substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'n.', 'A position of difficulty or embarassment; predicament;\n   dilemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fix', 'n.', 'fettling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixable', 'a.', 'Capable of being fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixation', 'n.', 'The act of fixing, or the state of being fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixation', 'n.', 'The act of uniting chemically with a solid substance or\n   in a solid form; reduction to a non-volatile condition; -- said of\n   gaseous elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixation', 'n.', 'The act or process of ceasing to be fluid and becoming\n   firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixation', 'n.', 'A state of resistance to evaporation or volatilization by\n   heat; -- said of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixative', 'n.', 'That which serves to set or fix colors or drawings, as a\n   mordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixed', 'a.', 'Securely placed or fastened; settled; established; firm;\n   imovable; unalterable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixed', 'a.', 'Stable; non-volatile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixedly', 'adv.', 'In a fixed, stable, or constant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixedness', 'n.', 'The state or quality of being fixed; stability;\n   steadfastness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixedness', 'n.', 'The quality of a body which resists evaporation or\n   volatilization by heat; solidity; cohesion of parts; as, the fixedness\n   of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixidity', 'n.', 'Fixedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixing', 'n.', 'The act or process of making fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixing', 'n.', 'That which is fixed; a fixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixing', 'n.', 'Arrangements; embellishments; trimmings; accompaniments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixity', 'n.', 'Fixedness; as, fixity of tenure; also, that which is fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixity', 'n.', 'Coherence of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixture', 'n.', 'That which is fixed or attached to something as a\n   permanent appendage; as, the fixtures of a pump; the fixtures of a farm\n   or of a dwelling, that is, the articles which a tenant may not take\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixture', 'n.', 'State of being fixed; fixedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixture', 'n.', 'Anything of an accessory character annexed to houses and\n   lands, so as to constitute a part of them. This term is, however, quite\n   frequently used in the peculiar sense of personal chattels annexed to\n   lands and tenements, but removable by the person annexing them, or his\n   personal representatives. In this latter sense, the same things may be\n   fixtures under some circumstances, and not fixtures under others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fixure', 'n.', 'Fixed position; stable condition; firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizgig', 'n.', 'A fishgig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizgig', 'n.', 'A firework, made of damp powder, which makes a fizzing or\n   hissing noise when it explodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizgig', 'n.', 'A gadding, flirting girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzed', 'imp. & p. p.', 'of Fizz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzing', 'p. pr. & vb. n.', 'of Fizz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizz', 'v. i.', 'To make a hissing sound, as a burning fuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizz', 'n.', 'A hissing sound; as, the fizz of a fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzled', 'imp. & p. p.', 'of Fizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzling', 'p. pr. & vb. n.', 'of Fizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzle', 'v. i.', 'To make a hissing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzle', 'v. i.', 'To make a ridiculous failure in an undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fizzle', 'n.', 'A failure or abortive effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fjord', 'n.', 'See Fiord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabbergast', 'v. t.', 'To astonish; to strike with wonder, esp. by\n   extraordinary statements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabbergastation', 'n.', 'The state of being flabbergasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabbily', 'adv.', 'In a flabby manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabbiness', 'n.', 'Quality or state of being flabby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabby', 'a.', 'Yielding to the touch, and easily moved or shaken; hanging\n   loose by its own weight; wanting firmness; flaccid; as, flabby flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabel', 'n.', 'A fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabellate', 'a.', 'Flabelliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabellation', 'n.', 'The act of keeping fractured limbs cool by the use of\n   a fan or some other contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabelliform', 'a.', 'Having the form of a fan; fan-shaped; flabellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabellinerved', 'a.', 'Having many nerves diverging radiately from the\n   base; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabellum', 'n.', 'A fan; especially, the fan carried before the pope on\n   state occasions, made in ostrich and peacock feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flabile', 'a.', 'Liable to be blown about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaccid', 'a.', 'Yielding to pressure for want of firmness and stiffness;\n   soft and weak; limber; lax; drooping; flabby; as, a flaccid muscle;\n   flaccid flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaccidity', 'n.', 'The state of being flaccid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flacker', 'v. i.', 'To flutter, as a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flacket', 'n.', 'A barrel-shaped bottle; a flagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagged', 'imp. & p. p.', 'of Flag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagging', 'p. pr. & vb. n.', 'of Flag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. i.', 'To hang loose without stiffness; to bend down, as flexible\n   bodies; to be loose, yielding, limp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. i.', 'To droop; to grow spiritless; to lose vigor; to languish;\n   as, the spirits flag; the streugth flags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To let droop; to suffer to fall, or let fall, into\n   feebleness; as, to flag the wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To enervate; to exhaust the vigor or elasticity of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'That which flags or hangs down loosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'A cloth usually bearing a device or devices and used to\n   indicate nationality, party, etc., or to give or ask information; --\n   commonly attached to a staff to be waved by the wind; a standard; a\n   banner; an ensign; the colors; as, the national flag; a military or a\n   naval flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'A group of feathers on the lower part of the legs of certain\n   hawks, owls, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'A group of elongated wing feathers in certain hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'The bushy tail of a dog, as of a setter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To signal to with a flag; as, to flag a train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To convey, as a message, by means of flag signals; as, to\n   flag an order to troops or vessels at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'An aquatic plant, with long, ensiform leaves, belonging to\n   either of the genera Iris and Acorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To furnish or deck out with flags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'A flat stone used for paving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'n.', 'Any hard, evenly stratified sandstone, which splits into\n   layers suitable for flagstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flag', 'v. t.', 'To lay with flags of flat stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellant', 'n.', 'One of a fanatical sect which flourished in Europe in\n   the 13th and 14th centuries, and maintained that flagellation was of\n   equal virtue with baptism and the sacrament; -- called also\n   disciplinant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellata', 'v. t.', 'An order of Infusoria, having one or two long,\n   whiplike cilia, at the anterior end. It includes monads. See Infusoria,\n   and Monad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellated', 'imp. & p. p.', 'of Flagellate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellating', 'p. pr. & vb. n.', 'of Flagellate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellate', 'v. t.', 'To whip; to scourge; to flog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellate', 'a.', 'Flagelliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellate', 'a.', 'Of or pertaining to the Flagellata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellation', 'n.', 'A beating or flogging; a whipping; a scourging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellator', 'n.', 'One who practices flagellation; one who whips or\n   scourges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagelliform', 'a.', 'Shaped like a whiplash; long, slender, round,\n   flexible, and (comming) tapering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellums', 'pl. ', 'of Flagellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagella', 'pl. ', 'of Flagellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellum', 'v. t.', 'A young, flexible shoot of a plant; esp., the long\n   trailing branch of a vine, or a slender branch in certain mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellum', 'v. t.', 'A long, whiplike cilium. See Flagellata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellum', 'v. t.', 'An appendage of the reproductive apparatus of the\n   snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagellum', 'v. t.', 'A lashlike appendage of a crustacean, esp. the\n   terminal ortion of the antennae and the epipodite of the maxilipeds.\n   See Maxilliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flageolet', 'n.', 'A small wooden pipe, having six or more holes, and a\n   mouthpiece inserted at one end. It produces a shrill sound, softer than\n   of the piccolo flute, and is said to have superseded the old recorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagginess', 'n.', 'The condition of being flaggy; laxity; limberness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagging', 'n.', 'A pavement or sidewalk of flagstones; flagstones,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagging', 'a.', 'Growing languid, weak, or spiritless; weakening;\n   delaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaggy', 'a.', 'Weak; flexible; limber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaggy', 'a.', 'Tasteless; insipid; as, a flaggy apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaggy', 'a.', 'Abounding with the plant called flag; as, a flaggy marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagitate', 'v. t.', 'To importune; to demand fiercely or with passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagitation', 'n.', 'Importunity; urgent demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagitious', 'a.', 'Disgracefully or shamefully criminal; grossly wicked;\n   scandalous; shameful; -- said of acts, crimes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagitious', 'a.', 'Guilty of enormous crimes; corrupt; profligate; -- said\n   of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagitious', 'a.', 'Characterized by scandalous crimes or vices; as,\n   flagitious times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagmen', 'pl. ', 'of Flagman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagman', 'n.', 'One who makes signals with a flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagon', 'n.', 'A vessel with a narrow mouth, used for holding and\n   conveying liquors. It is generally larger than a bottle, and of leather\n   or stoneware rather than of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrance', 'n.', 'Flagrancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrancies', 'pl. ', 'of Flagrancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrancy', 'n.', 'A burning; great heat; inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrancy', 'n.', 'The condition or quality of being flagrant; atrocity;\n   heiniousness; enormity; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrant', 'a.', 'Flaming; inflamed; glowing; burning; ardent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrant', 'a.', 'Actually in preparation, execution, or performance;\n   carried on hotly; raging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrant', 'a.', 'Flaming into notice; notorious; enormous; heinous;\n   glaringly wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrantly', 'adv.', 'In a flagrant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagrate', 'v. t.', 'To burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagration', 'n.', 'A conflagration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagship', 'n.', 'The vessel which carries the commanding officer of a\n   fleet or squadron and flies his distinctive flag or pennant.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-staves', 'pl. ', 'of Flagstaff');
  INSERT INTO words (word, word_type, description) VALUES  (    '-staffs', 'pl. ', 'of Flagstaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagstaff', 'n.', 'A staff on which a flag is hoisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagstone', 'n.', 'A flat stone used in paving, or any rock which will\n   split into such stones. See Flag, a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flagworm', 'n.', 'A worm or grub found among flags and sedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flail', 'n.', 'An instrument for threshing or beating grain from the ear by\n   hand, consisting of a wooden staff or handle, at the end of which a\n   stouter and shorter pole or club, called a swipe, is so hung as to\n   swing freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flail', 'n.', 'An ancient military weapon, like the common flail, often\n   having the striking part armed with rows of spikes, or loaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaily', 'a.', 'Acting like a flail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flain', '', 'p. p. of Flay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A paling; a hurdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A platform of hurdles, or small sticks made fast or\n   interwoven, supported by stanchions, for drying codfish and other\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A small stage hung over a vessels side, for workmen to\n   stand on in calking, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A loose filmy mass or a thin chiplike layer of anything; a\n   film; flock; lamina; layer; scale; as, a flake of snow, tallow, or\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A little particle of lighted or incandescent matter, darted\n   from a fire; a flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'n.', 'A sort of carnation with only two colors in the flower, the\n   petals having large stripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaked', 'imp. & p. p.', 'of Flake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaking', 'p. pr. & vb. n.', 'of Flake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'v. t.', 'To form into flakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flake', 'v. i.', 'To separate in flakes; to peel or scale off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flakiness', 'n.', 'The state of being flaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaky', 'a.', 'Consisting of flakes or of small, loose masses; lying, or\n   cleaving off, in flakes or layers; flakelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flam', 'n.', 'A freak or whim; also, a falsehood; a lie; an illusory\n   pretext; deception; delusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammed', 'imp. & p. p.', 'of Flam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamming', 'p. pr. & vb. n.', 'of Flam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flam', 'v. t.', 'To deceive with a falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flambeaux', 'pl. ', 'of Flambeau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flambeaus', 'pl. ', 'of Flambeau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flambeau', 'n.', 'A flaming torch, esp. one made by combining together a\n   number of thick wicks invested with a quick-burning substance\n   (anciently, perhaps, wax; in modern times, pitch or the like); hence,\n   any torch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamboyant', 'a.', 'Characterized by waving or flamelike curves, as in the\n   tracery of windows, etc.; -- said of the later (15th century) French\n   Gothic style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamboyer', 'n.', 'A name given in the East and West Indies to certain\n   trees with brilliant blossoms, probably species of Caesalpinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'A stream of burning vapor or gas, emitting light and heat;\n   darting or streaming fire; a blaze; a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'Burning zeal or passion; elevated and noble enthusiasm;\n   glowing imagination; passionate excitement or anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'Ardor of affection; the passion of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'A person beloved; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamed', 'imp. & p. p.', 'of Flame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaming', 'p. pr. & vb. n.', 'of Flame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'To burn with a flame or blaze; to burn as gas emitted from\n   bodies in combustion; to blaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'n.', 'To burst forth like flame; to break out in violence of\n   passion; to be kindled with zeal or ardor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame', 'v. t.', 'To kindle; to inflame; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flame-colored', 'a.', 'Of the color of flame; of a bright orange yellow\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flameless', 'a.', 'Destitute of flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamelet', 'n.', 'A small flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammens', 'pl. ', 'of Flamen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamines', 'pl. ', 'of Flamen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamen', 'n.', 'A priest devoted to the service of a particular god, from\n   whom he received a distinguishing epithet. The most honored were those\n   of Jupiter, Mars, and Quirinus, called respectively Flamen Dialis,\n   Flamen Martialis, and Flamen Quirinalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamineous', 'a.', 'Pertaining to a flamen; flaminical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaming', 'a.', 'Emitting flames; afire; blazing; consuming; illuminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaming', 'a.', 'Of the color of flame; high-colored; brilliant; dazzling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaming', 'a.', 'Ardent; passionate; burning with zeal; irrepressibly\n   earnest; as, a flaming proclomation or harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamingly', 'adv.', 'In a flaming manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamingoes', 'pl. ', 'of Flamingo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamingo', 'n.', 'Any bird of the genus Phoenicopterus. The flamingoes have\n   webbed feet, very long legs, and a beak bent down as if broken. Their\n   color is usually red or pink. The American flamingo is P. ruber; the\n   European is P. antiquorum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaminical', 'a.', 'Pertaining to a flamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammability', 'n.', 'The quality of being flammable; inflammability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammable', 'a.', 'Inflammable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammation', 'n.', 'The act of setting in a flame or blaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammeous', 'a.', 'Pertaining to, consisting of, or resembling, flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammiferous', 'a.', 'Producing flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammivomous', 'a.', 'Vomiting flames, as a volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flammulated', 'a.', 'Of a reddish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flamy', 'a.', 'Flaming; blazing; flamelike; flame-colored; composed of\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanches', 'pl. ', 'of Flanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanch', 'n.', 'A flange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanch', 'n.', 'A bearing consisting of a segment of a circle encroaching\n   on the field from the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanched', 'a.', 'Having flanches; -- said of an escutcheon with those\n   bearings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanconade', 'n.', 'A thrust in the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaneur', 'n.', 'One who strolls about aimlessly; a lounger; a loafer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flang', 'n.', 'A miners two-pointed pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flange', 'n.', 'An external or internal rib, or rim, for strength, as the\n   flange of an iron beam; or for a guide, as the flange of a car wheel\n   (see Car wheel.); or for attachment to another object, as the flange on\n   the end of a pipe, steam cylinder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flange', 'n.', 'A plate or ring to form a rim at the end of a pipe when\n   fastened to the pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanged', 'imp. & p. p.', 'of Flange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanging', 'p. pr. & vb. n.', 'of Flange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flange', 'v. t.', 'To make a flange on; to furnish with a flange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flange', 'v. i.', 'To be bent into a flange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanged', 'a.', 'Having a flange or flanges; as, a flanged wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'n.', 'The fleshy or muscular part of the side of an animal,\n   between the ribs and the hip. See Illust. of Beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'n.', 'The side of an army, or of any division of an army, as of a\n   brigade, regiment, or battalion; the extreme right or left; as, to\n   attack an enemy in flank is to attack him on the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'n.', 'That part of a bastion which reaches from the curtain to the\n   face, and defends the curtain, the flank and face of the opposite\n   bastion; any part of a work defending another by a fire along the\n   outside of its parapet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'n.', 'The side of any building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'n.', 'That part of the acting surface of a gear wheel tooth that\n   lies within the pitch line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanked', 'imp. & p. p.', 'of Flank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanking', 'p. pr. & vb. n.', 'of Flank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'v. t.', 'To stand at the flank or side of; to border upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'v. t.', 'To overlook or command the flank of; to secure or guard\n   the flank of; to pass around or turn the flank of; to attack, or\n   threaten to attack; the flank of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'v. i.', 'To border; to touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flank', 'v. i.', 'To be posted on the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanker', 'n.', 'One who, or that which, flanks, as a skirmisher or a body\n   of troops sent out upon the flanks of an army toguard a line of march,\n   or a fort projecting so as to command the side of an assailing body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flankered', 'imp. & p. p.', 'of Flanker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flankering', 'p. pr. & vb. n.', 'of Flanker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanker', 'v. t.', 'To defend by lateral fortifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanker', 'v. t.', 'To attack sideways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flannel', 'n.', 'A soft, nappy, woolen cloth, of loose texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flanneled', 'a.', 'Covered or wrapped in flannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flannen', 'a.', 'Made or consisting of flannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v.', 'Anything broad and limber that hangs loose, or that is\n   attached by one side or end and is easily moved; as, the flap of a\n   garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v.', 'A hinged leaf, as of a table or shutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v.', 'The motion of anything broad and loose, or a stroke or sound\n   made with it; as, the flap of a sail or of a wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v.', 'A disease in the lips of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapped', 'imp. & p. p.', 'of Flap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapping', 'p. pr. & vb. n.', 'of Flap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'n.', 'To beat with a flap; to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'n.', 'To move, as something broad and flaplike; as, to flap the\n   wings; to let fall, as the brim of a hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v. i.', 'To move as do wings, or as something broad or loose; to\n   fly with wings beating the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap', 'v. i.', 'To fall and hang like a flap, as the brim of a hat, or\n   other broad thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapdragon', 'n.', 'A game in which the players catch raisins out burning\n   brandy, and swallow them blazing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapdragon', 'n.', 'The thing thus caught and eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapdragon', 'v. t.', 'To swallow whole, as a flapdragon; to devour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap-eared', 'a.', 'Having broad, loose, dependent ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapjack', 'n.', 'A fklat cake turned on the griddle while cooking; a\n   griddlecake or pacake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapjack', 'n.', 'A fried dough cake containing fruit; a turnover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flap-mouthed', 'a.', 'Having broad, hangling lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapper', 'n.', 'One who, or that which, flaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flapper', 'n.', 'See Flipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flared', 'imp. & p. p.', 'of Flare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaring', 'p. pr. & vb. n.', 'of Flare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'v. i.', 'To burn with an unsteady or waving flame; as, the candle\n   flares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'v. i.', 'To shine out with a sudden and unsteady light; to emit a\n   dazzling or painfully bright light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'v. i.', 'To shine out with gaudy colors; to flaunt; to be\n   offensively bright or showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'v. i.', 'To be exposed to too much light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'v. i.', 'To open or spread outwards; to project beyond the\n   perpendicular; as, the sides of a bowl flare; the bows of a ship flare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'n.', 'An unsteady, broad, offensive light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'n.', 'A spreading outward; as, the flare of a fireplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare', 'n.', 'Leaf of lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flare-up', 'n.', 'A sudden burst of anger or passion; an angry dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaring', 'a.', 'That flares; flaming or blazing unsteadily; shining out\n   with a dazzling light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaring', 'a.', 'Opening or speading outwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaringly', 'adv.', 'In a flaring manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashed', 'imp. & p. p.', 'of Flash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashing', 'p. pr. & vb. n.', 'of Flash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. i.', 'To burst or break forth with a sudden and transient flood\n   of flame and light; as, the lighting flashes vividly; the powder\n   flashed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. i.', 'To break forth, as a sudden flood of light; to burst\n   instantly and brightly on the sight; to show a momentary brilliancy; to\n   come or pass like a flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. i.', 'To burst forth like a sudden flame; to break out\n   violently; to rush hastily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. t.', 'To send out in flashes; to cause to burst forth with\n   sudden flame or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. t.', 'To convey as by a flash; to light up, as by a sudden\n   flame or light; as, to flash a message along the wires; to flash\n   conviction on the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'v. t.', 'To cover with a thin layer, as objects of glass with\n   glass of a different color. See Flashing, n., 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'To trick up in a showy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'To strike and throw up large bodies of water from the\n   surface; to splash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashes', 'pl. ', 'of Flash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'A sudden burst of light; a flood of light instantaneously\n   appearing and disappearing; a momentary blaze; as, a flash of\n   lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'A sudden and brilliant burst, as of wit or genius; a\n   momentary brightness or show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'The time during which a flash is visible; an instant; a very\n   brief period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'A preparation of capsicum, burnt sugar, etc., for coloring\n   and giving a fictious strength to liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'a.', 'Showy, but counterfeit; cheap, pretentious, and vulgar; as,\n   flash jewelry; flash finery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'a.', 'Wearing showy, counterfeit ornaments; vulgarly pretentious;\n   as, flash people; flash men or women; -- applied especially to thieves,\n   gamblers, and prostitutes that dress in a showy way and wear much cheap\n   jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'Slang or cant of thieves and prostitutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'A pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flash', 'n.', 'A reservoir and sluiceway beside a navigable stream, just\n   above a shoal, so that the stream may pour in water as boats pass, and\n   thus bear them over the shoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashboard', 'n.', 'A board placed temporarily upon a milldam, to raise the\n   water in the pond above its usual level; a flushboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasher', 'n.', 'One who, or that which, flashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasher', 'n.', 'A man of more appearance of wit than reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasher', 'n.', 'A large sparoid fish of the Atlantic coast and all\n   tropical seas (Lobotes Surinamensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasher', 'n.', 'The European red-backed shrike (Lanius collurio); --\n   called also flusher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashily', 'adv.', 'In a flashy manner; with empty show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashiness', 'n.', 'The quality of being flashy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashing', 'n.', 'The creation of an artifical flood by the sudden letting\n   in of a body of water; -- called also flushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashing', 'n.', 'Pieces of metal, built into the joints of a wall, so as\n   to lap over the edge of the gutters or to cover the edge of the\n   roofing; also, similar pieces used to cover the valleys of roofs of\n   slate, shingles, or the like. By extension, the metal covering of\n   ridges and hips of roofs; also, in the United States, the protecting of\n   angles and breaks in walls of frame houses with waterproof material,\n   tarred paper, or the like. Cf. Filleting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashing', 'n.', 'The reheating of an article at the furnace aperture\n   during manufacture to restore its plastic condition; esp., the\n   reheating of a globe of crown glass to allow it to assume a flat shape\n   as it is rotated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashing', 'n.', 'A mode of covering transparent white glass with a film of\n   colored glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashy', 'a.', 'Dazzling for a moment; making a momentary show of\n   brilliancy; transitorily bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashy', 'a.', 'Fiery; vehement; impetuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashy', 'a.', 'Showy; gay; gaudy; as, a flashy dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flashy', 'a.', 'Without taste or spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flask', 'n.', 'A small bottle-shaped vessel for holding fluids; as, a flask\n   of oil or wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flask', 'n.', 'A narrow-necked vessel of metal or glass, used for various\n   purposes; as of sheet metal, to carry gunpowder in; or of wrought iron,\n   to contain quicksilver; or of glass, to heat water in, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flask', 'n.', 'A bed in a gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flask', 'n.', 'The wooden or iron frame which holds the sand, etc., forming\n   the mold used in a foundry; it consists of two or more parts; viz., the\n   cope or top; sometimes, the cheeks, or middle part; and the drag, or\n   bottom part. When there are one or more cheeks, the flask is called a\n   three part flask, four part flask, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasket', 'n.', 'A long, shallow basket, with two handles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasket', 'n.', 'A small flask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flasket', 'n.', 'A vessel in which viands are served.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Having an even and horizontal surface, or nearly so,\n   without prominences or depressions; level without inclination; plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Lying at full length, or spread out, upon the ground;\n   level with the ground or earth; prostrate; as, to lie flat on the\n   ground; hence, fallen; laid low; ruined; destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Wanting relief; destitute of variety; without points of\n   prominence and striking interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Tasteless; stale; vapid; insipid; dead; as, fruit or\n   drink flat to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Unanimated; dull; uninteresting; without point or\n   spirit; monotonous; as, a flat speech or composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Lacking liveliness of commercial exchange and dealings;\n   depressed; dull; as, the market is flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Clear; unmistakable; peremptory; absolute; positive;\n   downright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Below the true pitch; hence, as applied to intervals,\n   minor, or lower by a half step; as, a flat seventh; A flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Not sharp or shrill; not acute; as, a flat sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'superl.', 'Sonant; vocal; -- applied to any one of the sonant or\n   vocal consonants, as distinguished from a nonsonant (or sharp)\n   consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'adv.', 'In a flat manner; directly; flatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'adv.', 'Without allowance for accrued interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A level surface, without elevation, relief, or prominences;\n   an extended plain; specifically, in the United States, a level tract\n   along the along the banks of a river; as, the Mohawk Flats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A level tract lying at little depth below the surface of\n   water, or alternately covered and left bare by the tide; a shoal; a\n   shallow; a strand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'Something broad and flat in form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A flat-bottomed boat, without keel, and of small draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A straw hat, broad-brimmed and low-crowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A car without a roof, the body of which is a platform without\n   sides; a platform car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A platform on wheel, upon which emblematic designs, etc., are\n   carried in processions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'The flat part, or side, of anything; as, the broad side of a\n   blade, as distinguished from its edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A floor, loft, or story in a building; especially, a floor of\n   a house, which forms a complete residence in itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A horizontal vein or ore deposit auxiliary to a main vein;\n   also, any horizontal portion of a vein not elsewhere horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A dull fellow; a simpleton; a numskull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A character [/] before a note, indicating a tone which is a\n   half step or semitone lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'n.', 'A homaloid space or extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatted', 'imp. & p. p.', 'of Flat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatting', 'p. pr. & vb. n.', 'of Flat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'v. t.', 'To make flat; to flatten; to level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'v. t.', 'To render dull, insipid, or spiritless; to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'v. t.', 'To depress in tone, as a musical note; especially, to\n   lower in pitch by half a tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'v. i.', 'To become flat, or flattened; to sink or fall to an even\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat', 'v. i.', 'To fall form the pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatbill', 'n.', 'Any bird of the genus Flatyrynchus. They belong to the\n   family of flycatchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatboat', 'n.', 'A boat with a flat bottom and square ends; -- used for\n   the transportation of bulky freight, especially in shallow waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat-bottomed', 'a.', 'Having an even lower surface or bottom; as, a\n   flat-bottomed boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat-cap', 'n.', 'A kind of low-crowned cap formerly worn by all classes in\n   England, and continued in London after disuse elsewhere; -- hence, a\n   citizen of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatfish', 'n.', 'Any fish of the family Pleuronectidae; esp., the winter\n   flounder (Pleuronectes Americanus). The flatfishes have the body\n   flattened, swim on the side, and have eyes on one side, as the\n   flounder, turbot, and halibut. See Flounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat foot', '', 'A foot in which the arch of the instep is flattened so\n   that the entire sole of the foot rests upon the ground; also, the\n   deformity, usually congential, exhibited by such a foot; splayfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat-footed', 'a.', 'Having a flat foot, with little or no arch of the\n   instep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat-footed', 'a.', 'Firm-footed; determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flathead', 'a.', 'Characterized by flatness of head, especially that\n   produced by artificial means, as a certain tribe of American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flathead', 'n.', 'A Chinook Indian. See Chinook, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flat-headed', 'a.', 'Having a head with a flattened top; as, a flat-headed\n   nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatiron', 'n.', 'An iron with a flat, smooth surface for ironing clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flative', 'a.', 'Producing wind; flatulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flating', 'a.', 'With the flat side, as of a sword; flatlong; in a\n   prostrate position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatlong', 'adv.', 'With the flat side downward; not edgewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatly', 'adv.', 'In a flat manner; evenly; horizontally; without spirit;\n   dully; frigidly; peremptorily; positively, plainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatness', 'n.', 'The quality or state of being flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatness', 'n.', 'Eveness of surface; want of relief or prominence; the\n   state of being plane or level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatness', 'n.', 'Want of vivacity or spirit; prostration; dejection;\n   depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatness', 'n.', 'Want of variety or flavor; dullness; insipidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatness', 'n.', 'Depression of tone; the state of being below the true\n   pitch; -- opposed to sharpness or acuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatour', 'n.', 'A flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattened', 'imp. & p. p.', 'of Flatten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattening', 'p. pr. & vb. n.', 'of Flatten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatten', 'a.', 'To reduce to an even surface or one approaching evenness;\n   to make flat; to level; to make plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatten', 'a.', 'To throw down; to bring to the ground; to prostrate;\n   hence, to depress; to deject; to dispirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatten', 'a.', 'To make vapid or insipid; to render stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatten', 'a.', 'To lower the pitch of; to cause to sound less sharp; to\n   let fall from the pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatten', 'v. i.', 'To become or grow flat, even, depressed dull, vapid,\n   spiritless, or depressed below pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'n.', 'One who, or that which, makes flat or flattens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'n.', 'A flat-faced fulling hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'n.', 'A drawplate with a narrow, rectangular orifice, for\n   drawing flat strips, as watch springs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattered', 'imp. & p. p.', 'of Flatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattering', 'p. pr. & vb. n.', 'of Flatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'v. t.', 'To treat with praise or blandishments; to gratify or\n   attempt to gratify the self-love or vanity of, esp. by artful and\n   interested commendation or attentions; to blandish; to cajole; to\n   wheedle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'v. t.', 'To raise hopes in; to encourage or favorable, but\n   sometimes unfounded or deceitful, representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'v. t.', 'To portray too favorably; to give a too favorable idea\n   of; as, his portrait flatters him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatter', 'v. i.', 'To use flattery or insincere praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatterer', 'n.', 'One who flatters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattering', 'a.', 'That flatters (in the various senses of the verb); as,\n   a flattering speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatteringly', 'adv.', 'With flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatteries', 'pl. ', 'of Flattery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattery', 'v. t.', 'The act or practice of flattering; the act of pleasing\n   by artiful commendation or compliments; adulation; false, insincere, or\n   excessive praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatting', 'n.', 'The process or operation of making flat, as a cylinder of\n   glass by opening it out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatting', 'n.', 'A mode of painting,in which the paint, being mixed with\n   turpentine, leaves the work without gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatting', 'n.', 'A method of preserving gilding unburnished, by touching\n   with size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatting', 'n.', 'The process of forming metal into sheets by passing it\n   between rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flattish', 'a.', 'Somewhat flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulence', 'n.', 'Alt. of Flatlency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatlency', 'n.', 'The state or quality of being flatulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulent', 'a.', 'Affected with flatus or gases generated in the\n   alimentary canal; windy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulent', 'a.', 'Generating, or tending to generate, wind in the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulent', 'a.', 'Turgid with flatus; as, a flatulent tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulent', 'a.', 'Pretentious without substance or reality; puffy; empty;\n   vain; as, a flatulent vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatulently', 'adv.', 'In a flatulent manner; with flatulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatuosity', 'n.', 'Flatulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatuous', 'a.', 'Windy; generating wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatuses', 'pl. ', 'of Flatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatus', 'pl. ', 'of Flatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatus', 'n.', 'A breath; a puff of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatus', 'n.', 'Wind or gas generated in the stomach or other cavities of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatware', 'n.', 'Articles for the table, as china or silverware, that are\n   more or less flat, as distinguished from hollow ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatwise', 'a. / adv.', 'With the flat side downward, or next to another\n   object; not edgewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flatworm', 'n.', 'Any worm belonging to the Plathelminthes; also, sometimes\n   applied to the planarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaundrish', 'a.', 'Flemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaunted', 'imp. & p. p.', 'of Flaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaunting', 'p. pr. & vb. n.', 'of Flaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaunt', 'v. i.', 'To throw or spread out; to flutter; to move\n   ostentatiously; as, a flaunting show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaunt', 'v. t.', 'To display ostentatiously; to make an impudent show of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaunt', 'n.', 'Anything displayed for show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flauntingly', 'adv.', 'In a flaunting way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flautist', 'n.', 'A player on the flute; a flutist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flauto', 'n.', 'A flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavaniline', 'n.', 'A yellow, crystalline, organic dyestuff, C16H14N2, of\n   artifical production. It is a strong base, and is a complex derivative\n   of aniline and quinoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavescent', 'a.', 'Turning yellow; yellowish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavicomous', 'a.', 'Having yellow hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavin', 'n.', 'A yellow, vegetable dyestuff, resembling quercitron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavine', 'n.', 'A yellow, crystalline, organic base, C13H12N2O, obtained\n   artificially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavol', 'n.', 'A yellow, crystalline substance, obtained from\n   anthraquinone, and regarded as a hydroxyl derivative of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavor', 'n.', 'That quality of anything which affects the smell; odor;\n   fragrances; as, the flavor of a rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavor', 'n.', 'That quality of anything which affects the taste; that\n   quality which gratifies the palate; relish; zest; savor; as, the flavor\n   of food or drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavor', 'n.', 'That which imparts to anything a peculiar odor or taste,\n   gratifying to the sense of smell, or the nicer perceptions of the\n   palate; a substance which flavors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavor', 'n.', 'That quality which gives character to any of the\n   productions of literature or the fine arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavored', 'imp. & p. p.', 'of Flavor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavoring', 'p. pr. & vb. n.', 'of Flavor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavor', 'v. t.', 'To give flavor to; to add something (as salt or a spice)\n   to, to give character or zest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavored', 'a.', 'Having a distinct flavor; as, high-flavored wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavorles', 'a.', 'Without flavor; tasteless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavorous', 'a.', 'Imparting flavor; pleasant to the taste or smell; sapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flavous', 'a.', 'Yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'n.', 'A crack or breach; a gap or fissure; a defect of continuity\n   or cohesion; as, a flaw in a knife or a vase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'n.', 'A defect; a fault; as, a flaw in reputation; a flaw in a\n   will, in a deed, or in a statute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'n.', 'A sudden burst of noise and disorder; a tumult; uproar; a\n   quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'n.', 'A sudden burst or gust of wind of short duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawed', 'imp. & p. p.', 'of Flaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawing', 'p. pr. & vb. n.', 'of Flaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'v. t.', 'To crack; to make flaws in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaw', 'v. t.', 'To break; to violate; to make of no effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawless', 'a.', 'Free from flaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawn', 'n.', 'A sort of flat custard or pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawter', 'v. t.', 'To scrape o/ pare, as a skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawy', 'a.', 'Full of flaws or cracks; broken; defective; faulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flawy', 'a.', 'Subject to sudden flaws or gusts of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flax', 'n.', 'A plant of the genus Linum, esp. the L. usitatissimum, which\n   has a single, slender stalk, about a foot and a half high, with blue\n   flowers. The fiber of the bark is used for making thread and cloth,\n   called linen, cambric, lawn, lace, etc. Linseed oil is expressed from\n   the seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flax', 'n.', 'The skin or fibrous part of the flax plant, when broken and\n   cleaned by hatcheling or combing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaxen', 'a.', 'Made of flax; resembling flax or its fibers; of the color\n   of flax; of a light soft straw color; fair and flowing, like flax or\n   tow; as, flaxen thread; flaxen hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flax-plant', 'n.', 'A plant in new Zealand (Phormium tenax), allied to the\n   lilies and aloes. The leaves are two inches wide and several feet long,\n   and furnish a fiber which is used for making ropes, mats, and coarse\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaxseed', 'n.', 'The seed of the flax; linseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaxweed', 'n.', 'See Toadflax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaxy', 'a.', 'Like flax; flaxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flayed', 'imp. & p. p.', 'of Flay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flaying', 'p. pr. & vb. n.', 'of Flay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flay', 'v. t.', 'To skin; to strip off the skin or surface of; as, to flay\n   an ox; to flay the green earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flayer', 'n.', 'One who strips off the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea', 'v. t.', 'To flay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea', 'n.', 'An insect belonging to the genus Pulex, of the order\n   Aphaniptera. Fleas are destitute of wings, but have the power of\n   leaping energetically. The bite is poisonous to most persons. The human\n   flea (Pulex irritans), abundant in Europe, is rare in America, where\n   the dog flea (P. canis) takes its place. See Aphaniptera, and Dog flea.\n   See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleabane', 'n.', 'One of various plants, supposed to have efficacy in\n   driving away fleas. They belong, for the most part, to the genera\n   Conyza, Erigeron, and Pulicaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-beetle', 'n.', 'A small beetle of the family Halticidae, of many\n   species. They have strong posterior legs and leap like fleas. The\n   turnip flea-beetle (Phyllotreta vittata) and that of the grapevine\n   (Graptodera chalybea) are common injurious species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-bite', 'n.', 'The bite of a flea, or the red spot caused by the bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-bite', 'n.', 'A trifling wound or pain, like that of the bite of a\n   flea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-bitten', 'a.', 'Bitten by a flea; as, a flea-bitten face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-bitten', 'a.', 'White, flecked with minute dots of bay or sorrel; --\n   said of the color of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleagh', '', 'imp. of Fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleak', 'n.', 'A flake; a thread or twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleaking', 'n.', 'A light covering of reeds, over which the main covering\n   is laid, in thatching houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flea-louse', 'n.', 'A jumping plant louse of the family Psyllidae, of many\n   species. That of the pear tree is Psylla pyri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleam', 'n.', 'A sharp instrument used for opening veins, lancing gums,\n   etc.; a kind of lancet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleamy', 'a.', 'Bloody; clotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flear', 'v. t. & i.', 'See Fleer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleawort', 'n.', 'An herb used in medicine (Plantago Psyllium), named from\n   the shape of its seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleche', 'n.', 'A simple fieldwork, consisting of two faces forming a\n   salient angle pointing outward and open at the gorge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleck', 'n.', 'A flake; also, a lock, as of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleck', 'n.', 'A spot; a streak; a speckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flecked', 'imp. & p. p.', 'of Fleck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flecking', 'p. pr. & vb. n.', 'of Fleck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleck', 'n.', 'To spot; to streak or stripe; to variegate; to dapple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flecker', 'v. t.', 'To fleck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleckless', 'a.', 'Without spot or blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flection', 'n.', 'The act of bending, or state of being bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flection', 'n.', 'The variation of words by declension, comparison, or\n   conjugation; inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flectional', 'a.', 'Capable of, or pertaining to, flection or inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flector', 'n.', 'A flexor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fled', '', 'imp. & p. p. of Flee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledge', 'v. i.', 'Feathered; furnished with feathers or wings; able to\n   fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledged', 'imp. & p. p.', 'of Fledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledging', 'p. pr. & vb. n.', 'of Fledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledge', 'v. t. & i.', 'To furnish with feathers; to supply with the\n   feathers necessary for flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledge', 'v. t. & i.', 'To furnish or adorn with any soft covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fledgeling', 'n.', 'A young bird just fledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fled', 'imp. & p. p.', 'of Flee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeing', 'p. pr. & vb. n.', 'of Flee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flee', 'v. i.', 'To run away, as from danger or evil; to avoid in an\n   alarmed or cowardly manner; to hasten off; -- usually with from. This\n   is sometimes omitted, making the verb transitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'n.', 'The entire coat of wood that covers a sheep or other\n   similar animal; also, the quantity shorn from a sheep, or animal, at\n   one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'n.', 'Any soft woolly covering resembling a fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'n.', 'The fine web of cotton or wool removed by the doffing knife\n   from the cylinder of a carding machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeced', 'imp. & p. p.', 'of Fleece');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleecing', 'p. pr. & vb. n.', 'of Fleece');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'v. t.', 'To deprive of a fleece, or natural covering of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'v. t.', 'To strip of money or other property unjustly, especially\n   by trickery or fraud; to bring to straits by oppressions and exactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleece', 'v. t.', 'To spread over as with wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeced', 'a.', 'Furnished with a fleece; as, a sheep is well fleeced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeced', 'a.', 'Stripped of a fleece; plundered; robbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeceless', 'a.', 'Without a fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleecer', 'n.', 'One who fleeces or strips unjustly, especially by trickery\n   or fraund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleecy', 'a.', 'Covered with, made of, or resembling, a fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleen', 'n. pl.', 'Obs. pl. of Flea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleer', 'n.', 'One who flees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleered', 'imp. & p. p.', 'of Fleer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleering', 'p. pr. & vb. n.', 'of Fleer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleer', '', 'To make a wry face in contempt, or to grin in scorn; to\n   deride; to sneer; to mock; to gibe; as, to fleer and flout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleer', '', 'To grin with an air of civility; to leer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleer', 'v. t.', 'To mock; to flout at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flear', 'n.', 'A word or look of derision or mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flear', 'n.', 'A grin of civility; a leer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleerer', 'n.', 'One who fleers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeringly', 'adv.', 'In a fleering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeted', 'imp. & p. p.', 'of Fleet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeting', 'p. pr. & vb. n.', 'of Fleet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'n. & a.', 'To sail; to float.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'n. & a.', 'To fly swiftly; to pass over quickly; to hasten; to\n   flit as a light substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'n. & a.', 'To slip on the whelps or the barrel of a capstan or\n   windlass; -- said of a cable or hawser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. t.', 'To pass over rapidly; to skin the surface of; as, a ship\n   that fleets the gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. t.', 'To hasten over; to cause to pass away lighty, or in mirth\n   and joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. t.', 'To draw apart the blocks of; -- said of a tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. t.', 'To cause to slip down the barrel of a capstan or\n   windlass, as a rope or chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'Swift in motion; moving with velocity; light and quick in\n   going from place to place; nimble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'Light; superficially thin; not penetrating deep, as soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'A number of vessels in company, especially war vessels;\n   also, the collective naval force of a country, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'A flood; a creek or inlet; a bay or estuary; a river; --\n   obsolete, except as a place name, -- as Fleet Street in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'A former prison in London, which originally stood near a\n   stream, the Fleet (now filled up).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet', 'v. i.', 'To take the cream from; to skim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeten', 'n.', 'Fleeted or skimmed milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleet-foot', 'a.', 'Swift of foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleeting', 'a.', 'Passing swiftly away; not durable; transient; transitory;\n   as, the fleeting hours or moments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleetingly', 'adv.', 'In a fleeting manner; swiftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleetings', 'n. pl.', 'A mixture of buttermilk and boiling whey; curds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleetly', 'adv.', 'In a fleet manner; rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleetness', 'n.', 'Swiftness; rapidity; velocity; celerity; speed; as, the\n   fleetness of a horse or of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleigh', '', 'imp. of Fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleme', 'v. t.', 'To banish; to drive out; to expel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flemer', 'n.', 'One who, or that which, banishes or expels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleming', 'n.', 'A native or inhabitant of Flanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flemish', 'a.', 'Pertaining to Flanders, or the Flemings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flemish', 'n.', 'The language or dialect spoken by the Flemings; also,\n   collectively, the people of Flanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flench', 'v. t.', 'Same as Flence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flense', 'v. t.', 'To strip the blubber or skin from, as from a whale,\n   seal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'The aggregate of the muscles, fat, and other tissues which\n   cover the framework of bones in man and other animals; especially, the\n   muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'Animal food, in distinction from vegetable; meat;\n   especially, the body of beasts and birds used as food, as distinguished\n   from fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'The human body, as distinguished from the soul; the\n   corporeal person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'The human eace; mankind; humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'Human nature');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'In a good sense, tenderness of feeling; gentleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'In a bad sense, tendency to transient or physical pleasure;\n   desire for sensual gratification; carnality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'The character under the influence of animal propensities or\n   selfish passions; the soul unmoved by spiritual influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'Kindred; stock; race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'n.', 'The soft, pulpy substance of fruit; also, that part of a\n   root, fruit, and the like, which is fit to be eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshed', 'imp. & p. p.', 'of Flesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshing', 'p. pr. & vb. n.', 'of Flesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'v. t.', 'To feed with flesh, as an incitement to further exertion;\n   to initiate; -- from the practice of training hawks and dogs by feeding\n   them with the first game they take, or other flesh. Hence, to use upon\n   flesh (as a murderous weapon) so as to draw blood, especially for the\n   first time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'v. t.', 'To glut; to satiate; hence, to harden, to accustom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesh', 'v. t.', 'To remove flesh, membrance, etc., from, as from hides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshed', 'a.', 'Corpulent; fat; having flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshed', 'a.', 'Glutted; satiated; initiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesher', 'n.', 'A butcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flesher', 'n.', 'A two-handled, convex, blunt-edged knife, for scraping\n   hides; a fleshing knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshhood', 'n.', 'The state or condition of having a form of flesh;\n   incarnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshiness', 'n.', 'The state of being fleshy; plumpness; corpulence;\n   grossness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshings', 'n. pl.', 'Flesh-colored tights, worn by actors dancers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshless', 'a.', 'Destitute of flesh; lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshliness', 'n.', 'The state of being fleshly; carnal passions and\n   appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshing', 'n.', 'A person devoted to fleshly things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshly', 'a.', 'Of or pertaining to the flesh; corporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshly', 'a.', 'Animal; not/vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshly', 'a.', 'Human; not celestial; not spiritual or divine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshly', 'a.', 'Carnal; wordly; lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshly', 'adv.', 'In a fleshly manner; carnally; lasciviously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshment', 'n.', 'The act of fleshing, or the excitement attending a\n   successful beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshmonger', 'n.', 'One who deals in flesh; hence, a pimp; a procurer; a\n   pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshpot', 'n.', 'A pot or vessel in which flesh is cooked');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshpot', 'n.', 'plenty; high living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshquake', 'n.', 'A quaking or trembling of the flesh; a quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshy', 'superl.', 'Full of, or composed of, flesh; plump; corpulent; fat;\n   gross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshy', 'superl.', 'Human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleshy', 'superl.', 'Composed of firm pulp; succulent; as, the houseleek,\n   cactus, and agave are fleshy plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flet', 'p. p.', 'Skimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fletched', 'imp. & p. p.', 'of Fletch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fletching', 'p. pr. & vb. n.', 'of Fletch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fletch', 'v. t.', 'To feather, as an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fletcher', 'n.', 'One who fletches of feathers arrows; a manufacturer of\n   bows and arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flete', 'v. i.', 'To float; to swim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fletiferous', 'a.', 'Producing tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleurs-de-lis', 'pl. ', 'of Fleur-de-lis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleur-de-lis', 'n.', 'The iris. See Flower-de-luce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleur-de-lis', 'n.', 'A conventional flower suggested by the iris, and\n   having a form which fits it for the terminal decoration of a scepter,\n   the ornaments of a crown, etc. It is also a heraldic bearing, and is\n   identified with the royal arms and adornments of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fleury', 'a.', 'Finished at the ends with fleurs-de-lis; -- said esp. of a\n   cross so decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flew', '', 'imp. of Fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flewed', 'a.', 'Having large flews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flews', 'n. pl.', 'The pendulous or overhanging lateral parts of the upper\n   lip of dogs, especially prominent in hounds; -- called also chaps. See\n   Illust. of Bloodhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexed', 'imp. & p. p.', 'of Flex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexing', 'p. pr. & vb. n.', 'of Flex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flex', 'v. t.', 'To bend; as, to flex the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flex', 'n.', 'Flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexanimous', 'a.', 'Having power to change the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexibility', 'n.', 'The state or quality of being flexible; flexibleness;\n   pliancy; pliability; as, the flexibility of strips of hemlock, hickory,\n   whalebone or metal, or of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexible', 'a.', 'Capable of being flexed or bent; admitting of being\n   turned, bowed, or twisted, without breaking; pliable; yielding to\n   pressure; not stiff or brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexible', 'a.', 'Willing or ready to yield to the influence of others; not\n   invincibly rigid or obstinate; tractable; manageable; ductile; easy and\n   compliant; wavering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexible', 'a.', 'Capable or being adapted or molded; plastic,; as, a\n   flexible language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexicostate', 'a.', 'Having bent or curved ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexile', 'a.', 'Flexible; pliant; pliable; easily bent; plastic;\n   tractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexion', 'n.', 'The act of flexing or bending; a turning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexion', 'n.', 'A bending; a part bent; a fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexion', 'n.', 'Syntactical change of form of words, as by declension or\n   conjugation; inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexion', 'n.', 'The bending of a limb or joint; that motion of a joint\n   which gives the distal member a continually decreasing angle with the\n   axis of the proximal part; -- distinguished from extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexor', 'n.', 'A muscle which bends or flexes any part; as, the flexors of\n   the arm or the hand; -- opposed to extensor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexuose', 'a.', 'Flexuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexuous', 'a.', 'Having turns, windings, or flexures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexuous', 'a.', 'Having alternate curvatures in opposite directions; bent\n   in a zigzag manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexuous', 'a.', 'Wavering; not steady; flickering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexural', 'a.', 'Of, pertaining to, or resulting from, flexure; of the\n   nature of, or characterized by, flexure; as, flexural elasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexure', 'n.', 'The act of flexing or bending; a turning or curving;\n   flexion; hence, obsequious bowing or bending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexure', 'n.', 'A turn; a bend; a fold; a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexure', 'n.', 'The last joint, or bend, of the wing of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flexure', 'n.', 'The small distortion of an astronomical instrument caused\n   by the weight of its parts; the amount to be added or substracted from\n   the observed readings of the instrument to correct them for this\n   distortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flibbergib', 'n.', 'A sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flibbertigibbet', 'n.', 'An imp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flibustier', 'n.', 'A buccaneer; an American pirate. See Flibuster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicked', 'imp. & p. p.', 'of Flick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicking', 'p. pr. & vb. n.', 'of Flick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flick', 'v. t.', 'To whip lightly or with a quick jerk; to flap; as, to\n   flick a horse; to flick the dirt from boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flick', 'n.', 'A flitch; as, a flick of bacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flickered', 'imp. & p. p.', 'of Flicker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flickering', 'p. pr. & vb. n.', 'of Flicker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicker', 'v. i.', 'To flutter; to flap the wings without flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicker', 'v. i.', 'To waver unsteadily, like a flame in a current of air,\n   or when about to expire; as, the flickering light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicker', 'n.', 'The act of wavering or of fluttering; flucuation; sudden\n   and brief increase of brightness; as, the last flicker of the dying\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flicker', 'n.', 'The golden-winged woodpecker (Colaptes aurutus); -- so\n   called from its spring note. Called also yellow-hammer, high-holder,\n   pigeon woodpecker, and yucca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flickeringly', 'adv.', 'In a flickering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flickermouse', 'n.', 'See Flittermouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flidge', 'a.', 'Fledged; fledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flidge', 'v. i.', 'To become fledged; to fledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flier', 'v.', 'One who flies or flees; a runaway; a fugitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flier', 'v.', 'A fly. See Fly, n., 9, and 13 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flier', 'n.', 'See Flyer, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flier', 'n.', 'See Flyer, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'The act or flying; a passing through the air by the help of\n   wings; volitation; mode or style of flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'The act of fleeing; the act of running away, to escape or\n   expected evil; hasty departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'Lofty elevation and excursion;a mounting; a soa/ing; as, a\n   flight of imagination, ambition, folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'A number of beings or things passing through the air\n   together; especially, a flock of birds flying in company; the birds\n   that fly or migrate together; the birds produced in one season; as, a\n   flight of arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'A series of steps or stairs from one landing to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'A kind of arrow for the longbow; also, the sport of\n   shooting with it. See Shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight', 'n.', 'The husk or glume of oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flighted', 'a.', 'Taking flight; flying; -- used in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flighted', 'a.', 'Feathered; -- said of arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flighter', 'n.', 'A horizontal vane revolving over the surface of wort in a\n   cooler, to produce a circular current in the liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flightily', 'adv.', 'In a flighty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flightiness', 'n.', 'The state or quality of being flighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flight-shot', 'n.', 'The distance to which an arrow or flight may be shot;\n   bowshot, -- about the fifth of a mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flighty', 'a.', 'Fleeting; swift; transient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flighty', 'a.', 'Indulging in flights, or wild and unrestrained sallies, of\n   imagination, humor, caprice, etc.; given to disordered fancies and\n   extravagant conduct; volatile; giddy; eccentric; slighty delirious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimflam', 'n.', 'A freak; a trick; a lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimsily', 'adv.', 'In a flimsy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimsiness', 'n.', 'The state or quality of being flimsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimsy', 'superl.', 'Weak; feeble; limp; slight; vain; without strength or\n   solidity; of loose and unsubstantial structure; without reason or\n   plausibility; as, a flimsy argument, excuse, objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimsy', 'n.', 'Thin or transfer paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flimsy', 'n.', 'A bank note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinched', 'imp. & p. p.', 'of Flinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinching', 'p. pr. & vb. n.', 'of Flinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinch', 'v. i.', 'To withdraw from any suffering or undertaking, from pain\n   or danger; to fail in doing or perserving; to show signs of yielding or\n   of suffering; to shrink; to wince; as, one of the parties flinched from\n   the combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinch', 'v. i.', 'To let the foot slip from a ball, when attempting to\n   give a tight croquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinch', 'n.', 'The act of flinching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flincher', 'n.', 'One who flinches or fails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinchingly', 'adv.', 'In a flinching manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flindermouse', 'n.', 'A bat; a flittermouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinders', 'n. pl.', 'Small pieces or splinters; fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flung', 'imp. & p. p.', 'of Fling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinging', 'p. pr. & vb. n.', 'of Fling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. t.', 'To cast, send, to throw from the hand; to hurl; to dart;\n   to emit with violence as if thrown from the hand; as, to fing a stone\n   into the pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. t.', 'To shed forth; to emit; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. t.', 'To throw; to hurl; to throw off or down; to prostrate;\n   hence, to baffle; to defeat; as, to fling a party in litigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. i.', 'To throw; to wince; to flounce; as, the horse began to\n   kick and fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. i.', 'To cast in the teeth; to utter abusive language; to\n   sneer; as, the scold began to flout and fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'v. i.', 'To throw ones self in a violent or hasty manner; to rush\n   or spring with violence or haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'n.', 'A cast from the hand; a throw; also, a flounce; a kick; as,\n   the fling of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'n.', 'A severe or contemptuous remark; an expression of sarcastic\n   scorn; a gibe; a sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'n.', 'A kind of dance; as, the Highland fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fling', 'n.', 'A trifing matter; an object of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flingdust', 'n.', 'One who kicks up the dust; a streetwalker; a low manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinger', 'n.', 'One who flings; one who jeers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flint', 'n.', 'A massive, somewhat impure variety of quartz, in color\n   usually of a gray to brown or nearly black, breaking with a conchoidal\n   fracture and sharp edge. It is very hard, and strikes fire with steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flint', 'n.', 'A piece of flint for striking fire; -- formerly much used,\n   esp. in the hammers of gun locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flint', 'n.', 'Anything extremely hard, unimpressible, and unyielding, like\n   flint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flint glass', '', 'A soft, heavy, brilliant glass, consisting essentially\n   of a silicate of lead and potassium. It is used for tableware, and for\n   optical instruments, as prisms, its density giving a high degree of\n   dispersive power; -- so called, because formerly the silica was\n   obtained from pulverized flints. Called also crystal glass. Cf. Glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flint-hearted', 'a.', 'Hard-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flintiness', 'n.', 'The state or quality of being flinty; hardness;\n   cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flintlock', 'n.', 'A lock for a gun or pistol, having a flint fixed in the\n   hammer, which on striking the steel ignites the priming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flintlock', 'n.', 'A hand firearm fitted with a flintlock; esp., the\n   old-fashioned musket of European and other armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flintware', 'n.', 'A superior kind of earthenware into whose composition\n   flint enters largely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flintwood', 'n.', 'An Australian name for the very hard wood of the\n   Eucalyptus piluralis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flinty', 'superl.', 'Consisting of, composed of, abounding in, or\n   resembling, flint; as, a flinty rock; flinty ground; a flinty heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flip', 'n.', 'A mixture of beer, spirit, etc., stirred and heated by a hot\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flipped', 'imp. & p. p.', 'of Flip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flipping', 'p. pr. & vb. n.', 'of Flip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flip', 'v. t.', 'To toss or fillip; as, to flip up a cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flipe', 'v. t.', 'To turn inside out, or with the leg part back over the\n   foot, as a stocking in pulling off or for putting on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flip-flap', 'n.', 'The repeated stroke of something long and loose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flip-flap', 'adv.', 'With repeated strokes and noise, as of something long\n   and loose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippancy', 'n.', 'The state or quality of being flippant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippant', 'a.', 'Of smooth, fluent, and rapid speech; speaking with ease\n   and rapidity; having a voluble tongue; talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippant', 'a.', 'Speaking fluently and confidently, without knowledge or\n   consideration; empty; trifling; inconsiderate; pert; petulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippant', 'n.', 'A flippant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippantly', 'adv.', 'In a flippant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flippantness', 'n.', 'State or quality of being flippant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flipper', 'n.', 'A broad flat limb used for swimming, as those of seals,\n   sea turtles, whales, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flipper', 'n.', 'The hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirted', 'imp. & p. p.', 'of Flirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirting', 'p. pr. & vb. n.', 'of Flirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. t.', 'To throw with a jerk or quick effort; to fling suddenly;\n   as, they flirt water in each others faces; he flirted a glove, or a\n   handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. t.', 'To toss or throw about; to move playfully to and fro; as,\n   to flirt a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. t.', 'To jeer at; to treat with contempt; to mock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. i.', 'To run and dart about; to act with giddiness, or from a\n   desire to attract notice; especially, to play the coquette; to play at\n   courtship; to coquet; as, they flirt with the young men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. i.', 'To utter contemptuous language, with an air of disdain;\n   to jeer or gibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'n.', 'A sudden jerk; a quick throw or cast; a darting motion;\n   hence, a jeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'v. t.', 'One who flirts; esp., a woman who acts with giddiness, or\n   plays at courtship; a coquette; a pert girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt', 'a.', 'Pert; wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirtation', 'n.', 'Playing at courtship; coquetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirt-gill', 'n.', 'A woman of light behavior; a gill-flirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirtigig', 'n.', 'A wanton, pert girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flirtingly', 'adv.', 'In a flirting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flisk', 'v. i.', 'To frisk; to skip; to caper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flisk', 'n.', 'A caper; a spring; a whim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitted', 'imp. & p. p.', 'of Flit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitting', 'p. pr. & vb. n.', 'of Flit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'v. i.', 'To move with celerity through the air; to fly away with a\n   rapid motion; to dart along; to fleet; as, a bird flits away; a cloud\n   flits along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'v. i.', 'To flutter; to rove on the wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'v. i.', 'To pass rapidly, as a light substance, from one place to\n   another; to remove; to migrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'v. i.', 'To remove from one place or habitation to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'v. i.', 'To be unstable; to be easily or often moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flit', 'a.', 'Nimble; quick; swift. [Obs.] See Fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitches', 'pl. ', 'of Flitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitch', 'n.', 'The side of a hog salted and cured; a side of bacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitch', 'n.', 'One of several planks, smaller timbers, or iron plates,\n   which are secured together, side by side, to make a large girder or\n   built beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitch', 'n.', 'The outside piece of a sawed log; a slab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flite', 'v. i.', 'To scold; to quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitter', 'v. i.', 'To flutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitter', 'v. t.', 'To flutter; to move quickly; as, to flitter the cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitter', 'v. i.', 'A rag; a tatter; a small piece or fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flittermouse', 'n.', 'A bat; -- called also flickermouse, flindermouse, and\n   flintymouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flittern', 'a.', 'A term applied to the bark obtained from young oak trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flittiness', 'n.', 'Unsteadiness; levity; lightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitting', 'n.', 'A flying with lightness and celerity; a fluttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitting', 'n.', 'A removal from one habitation to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flittingly', 'adv.', 'In a flitting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flitty', 'a.', 'Unstable; fluttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flix', 'n.', 'Down; fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flix', 'n.', 'The flux; dysentery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flon', 'pl. ', 'of Flo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flo', 'n.', 'An arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'Anything which floats or rests on the surface of a fluid,\n   as to sustain weight, or to indicate the height of the surface, or mark\n   the place of, something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A mass of timber or boards fastened together, and\n   conveyed down a stream by the current; a raft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'The hollow, metallic ball of a self-acting faucet, which\n   floats upon the water in a cistern or boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'The cork or quill used in angling, to support the bait\n   line, and indicate the bite of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'Anything used to buoy up whatever is liable to sink; an\n   inflated bag or pillow used by persons learning to swim; a life\n   preserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A float board. See Float board (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A contrivance for affording a copious stream of water to\n   the heated surface of an object of large bulk, as an anvil or die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'The act of flowing; flux; flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A quantity of earth, eighteen feet square and one foot\n   deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'The trowel or tool with which the floated coat of\n   plastering is leveled and smoothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A polishing block used in marble working; a runner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A single-cut file for smoothing; a tool used by\n   shoemakers for rasping off pegs inside a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'A coal cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. i.', 'The sea; a wave. See Flote, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floated', 'imp. & p. p.', 'of Float');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'p. pr. & vb. n.', 'of Float');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'n.', 'To rest on the surface of any fluid; to swim; to be buoyed\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'n.', 'To move quietly or gently on the water, as a raft; to drift\n   along; to move or glide without effort or impulse on the surface of a\n   fluid, or through the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. t.', 'To cause to float; to cause to rest or move on the\n   surface of a fluid; as, the tide floated the ship into the harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. t.', 'To flood; to overflow; to cover with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. t.', 'To pass over and level the surface of with a float while\n   the plastering is kept wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Float', 'v. t.', 'To support and sustain the credit of, as a commercial\n   scheme or a joint-stock company, so as to enable it to go into, or\n   continue in, operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floatable', 'a.', 'That may be floated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floatage', 'n.', 'Same as Flotage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floatation', 'n.', 'See Flotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floater', 'n.', 'One who floats or swims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floater', 'n.', 'A float for indicating the height of a liquid surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'a.', 'Buoyed upon or in a fluid; a, the floating timbers of a\n   wreck; floating motes in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'a.', 'Free or lose from the usual attachment; as, the floating\n   ribs in man and some other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'a.', 'Not funded; not fixed, invested, or determined; as,\n   floating capital; a floating debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'n.', 'Floating threads. See Floating threads, above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floating', 'n.', 'The second coat of three-coat plastering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floatingly', 'adv.', 'In a floating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floaty', 'a.', 'Swimming on the surface; buoyant; light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flobert', 'n.', 'A small cartridge designed for target shooting; --\n   sometimes called ball cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccillation', 'n.', 'A delirious picking of bedclothes by a sick person,\n   as if to pick off flocks of wool; carphology; -- an alarming symptom in\n   acute diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccose', 'n.', 'Spotted with small tufts like wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccose', 'n.', 'Having tufts of soft hairs, which are often deciduous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccular', 'a.', 'Of or pertaining to the flocculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculated', 'imp. & p. p.', 'of Flocculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculating', 'p. pr. & vb. n.', 'of Flocculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculate', 'v. i.', 'To aggregate into small lumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculate', 'a.', 'Furnished with tufts of curly hairs, as some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculation', 'n.', 'The process by which small particles of fine soils\n   and sediments aggregate into larger lumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculence', 'n.', 'The state of being flocculent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculent', 'a.', 'Clothed with small flocks or flakes; woolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculent', 'a.', 'Applied to the down of newly hatched or unfledged\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculi', 'pl. ', 'of Flocculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocculus', 'n.', 'A small lobe in the under surface of the cerebellum,\n   near the middle peduncle; the subpeduncular lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocci', 'pl. ', 'of Floccus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccus', 'n.', 'The tuft of hair terminating the tail of mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccus', 'n.', 'A tuft of feathers on the head of young birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floccus', 'n.', 'A woolly filament sometimes occuring with the sporules of\n   certain fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'n.', 'A company or collection of living creatures; -- especially\n   applied to sheep and birds, rarely to persons or (except in the plural)\n   to cattle and other large animals; as, a flock of ravenous fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'n.', 'A Christian church or congregation; considered in their\n   relation to the pastor, or minister in charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocked', 'imp. & p. p.', 'of Flock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocking', 'p. pr. & vb. n.', 'of Flock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'v. i.', 'To gather in companies or crowds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'v. t.', 'To flock to; to crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'n.', 'A lock of wool or hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'n.', 'Woolen or cotton refuse (sing. / pl.), old rags, etc.,\n   reduced to a degree of fineness by machinery, and used for stuffing\n   unpholstered furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'sing. / pl.', 'Very fine, sifted, woolen refuse, especially that\n   from shearing the nap of cloths, used as a coating for wall paper to\n   give it a velvety or clothlike appearance; also, the dust of vegetable\n   fiber used for a similar purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flock', 'v. t.', 'To coat with flock, as wall paper; to roughen the surface\n   of (as glass) so as to give an appearance of being covered with fine\n   flock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flockling', 'n.', 'A lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flockly', 'adv.', 'In flocks; in crowds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flockmel', 'adv.', 'In a flock; in a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flocky', 'a.', 'Abounding with flocks; floccose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floe', 'n.', 'A low, flat mass of floating ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flogged', 'imp. & p. p.', 'of Flog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flogging', 'p. pr. & vb. n.', 'of Flog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flog', 'v. t.', 'To beat or strike with a rod or whip; to whip; to lash; to\n   chastise with repeated blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flogger', 'n.', 'One who flogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flogger', 'n.', 'A kind of mallet for beating the bung stave of a cask to\n   start the bung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flogging', 'a. & n.', 'from Flog, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flon', 'n. pl.', 'See Flo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flong', '', 'imp. & p. p. of Fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. i.', 'A great flow of water; a body of moving water; the\n   flowing stream, as of a river; especially, a body of water, rising,\n   swelling, and overflowing land not usually thus covered; a deluge; a\n   freshet; an inundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. i.', 'The flowing in of the tide; the semidiurnal swell or rise\n   of water in the ocean; -- opposed to ebb; as, young flood; high flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. i.', 'A great flow or stream of any fluid substance; as, a\n   flood of light; a flood of lava; hence, a great quantity widely\n   diffused; an overflowing; a superabundance; as, a flood of bank notes;\n   a flood of paper currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. i.', 'Menstrual disharge; menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooded', 'imp. & p. p.', 'of Flood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooding', 'p. pr. & vb. n.', 'of Flood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. t.', 'To overflow; to inundate; to deluge; as, the swollen\n   river flooded the valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flood', 'v. t.', 'To cause or permit to be inundated; to fill or cover with\n   water or other fluid; as, to flood arable land for irrigation; to fill\n   to excess or to its full capacity; as, to flood a country with a\n   depreciated currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floodage', 'n.', 'Inundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooder', 'n.', 'One who floods anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooding', 'n.', 'The filling or covering with water or other fluid;\n   overflow; inundation; the filling anything to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooding', 'n.', 'An abnormal or excessive discharge of blood from the\n   uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flook', 'n.', 'A fluke of an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flookan', 'n.', 'Alt. of Flukan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flukan', 'n.', 'See Flucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooky', 'a.', 'Fluky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The bottom or lower part of any room; the part upon which we\n   stand and upon which the movables in the room are supported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The structure formed of beams, girders, etc., with proper\n   covering, which divides a building horizontally into stories. Floor in\n   sense 1 is, then, the upper surface of floor in sense 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The surface, or the platform, of a structure on which we\n   walk or travel; as, the floor of a bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'A story of a building. See Story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The part of the house assigned to the members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The right to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'That part of the bottom of a vessel on each side of the\n   keelson which is most nearly horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'The rock underlying a stratified or nearly horizontal\n   deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'n.', 'A horizontal, flat ore body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floored', 'imp. & p. p.', 'of Floor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooring', 'p. pr. & vb. n.', 'of Floor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'v. t.', 'To cover with a floor; to furnish with a floor; as, to\n   floor a house with pine boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'v. t.', 'To strike down or lay level with the floor; to knock\n   down; hence, to silence by a conclusive answer or retort; as, to floor\n   an opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floor', 'v. t.', 'To finish or make an end of; as, to floor a college\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floorage', 'n.', 'Floor space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floorer', 'n.', 'Anything that floors or upsets a person, as a blow that\n   knocks him down; a conclusive answer or retort; a task that exceeds\n   ones abilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floorheads', 'n. pl.', 'The upper extermities of the floor of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooring', 'n.', 'A platform; the bottom of a room; a floor; pavement. See\n   Floor, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flooring', 'n.', 'Material for the construction of a floor or floors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floorless', 'a.', 'Having no floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floorwalker', 'n.', 'One who walks about in a large retail store as an\n   overseer and director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flopped', 'imp. & p. p.', 'of Flop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flopping', 'p. pr. & vb. n.', 'of Flop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flop', 'v. t.', 'To clap or strike, as a bird its wings, a fish its tail,\n   etc.; to flap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flop', 'v. t.', 'To turn suddenly, as something broad and flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flop', 'v. i.', 'To strike about with something broad abd flat, as a fish\n   with its tail, or a bird with its wings; to rise and fall; as, the brim\n   of a hat flops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flop', 'v. i.', 'To fall, sink, or throw ones self, heavily, clumsily, and\n   unexpectedly on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flop', 'n.', 'Act of flopping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floppy', 'n.', 'Having a tendency to flop or flap; as, a floppy hat brim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flopwing', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flora', 'n.', 'The goddess of flowers and spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flora', 'n.', 'The complete system of vegetable species growing without\n   cultivation in a given locality, region, or period; a list or\n   description of, or treatise on, such plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floral', 'a.', 'Pertaining to Flora, or to flowers; made of flowers; as,\n   floral games, wreaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floral', 'a.', 'Containing, or belonging to, a flower; as, a floral bud; a\n   floral leaf; floral characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florally', 'adv.', 'In a floral manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floramour', 'n.', 'The plant love-lies-bleeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floran', 'n.', 'Tin ore scarcely perceptible in the stone; tin ore stamped\n   very fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floreal', 'n.', 'The eight month of the French republican calendar. It\n   began April 20, and ended May 19. See Vendemiare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floren', 'n.', 'A cerain gold coin; a Florence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florence', 'n.', 'An ancient gold coin of the time of Edward III., of six\n   shillings sterling value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florence', 'n.', 'A kind of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florentine', 'a.', 'Belonging or relating to Florence, in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florentine', 'n.', 'A native or inhabitant of Florence, a city in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florentine', 'n.', 'A kind of silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florentine', 'n.', 'A kind of pudding or tart; a kind of meat pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florescence', 'n.', 'A bursting into flower; a blossoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florescent', 'a.', 'Expanding into flowers; blossoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floret', 'n.', 'A little flower; one of the numerous little flowers which\n   compose the head or anthodium in such flowers as the daisy, thistle,\n   and dandelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floret', 'n.', 'A foil; a blunt sword used in fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriage', 'n.', 'Bloom; blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriated', 'a.', 'Having floral ornaments; as, floriated capitals of\n   Gothic pillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floricomous', 'a.', 'Having the head adorned with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floricultural', 'a.', 'Pertaining to the cultivation of flowering plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriculture', 'n.', 'The cultivation of flowering plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriculturist', 'n.', 'One skilled in the cultivation of flowers; a\n   florist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florid', 'a.', 'Covered with flowers; abounding in flowers; flowery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florid', 'a.', 'Bright in color; flushed with red; of a lively reddish\n   color; as, a florid countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florid', 'a.', 'Embellished with flowers of rhetoric; enriched to excess\n   with figures; excessively ornate; as, a florid style; florid eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florid', 'a.', 'Flowery; ornamental; running in rapid melodic figures,\n   divisions, or passages, as in variations; full of fioriture or little\n   ornamentations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florida bean', '', 'The large, roundish, flattened seed of Mucuna urens.\n   See under Bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florida bean', '', 'One of the very large seeds of the Entada scandens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florideae', 'n. pl.', 'A subclass of algae including all the red or\n   purplish seaweeds; the Rhodospermeae of many authors; -- so called from\n   the rosy or florid color of most of the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floridity', 'n.', 'The quality of being florid; floridness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floridly', 'adv.', 'In a florid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floridness', 'n.', 'The quality of being florid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriferous', 'a.', 'Producing flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florification', 'n.', 'The act, process, or time of flowering; florescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriform', 'a.', 'Having the form of a flower; flower-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floriken', 'n.', 'An Indian bustard (Otis aurita). The Bengal floriken is\n   Sypheotides Bengalensis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florilege', 'n.', 'The act of gathering flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florimer', 'n.', 'See Floramour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florin', 'n.', 'A silver coin of Florence, first struck in the twelfth\n   century, and noted for its beauty. The name is given to different coins\n   in different countries. The florin of England, first minted in 1849, is\n   worth two shillings, or about 48 cents; the florin of the Netherlands,\n   about 40 cents; of Austria, about 36 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florist', 'n.', 'A cultivator of, or dealer in, flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florist', 'n.', 'One who writes a flora, or an account of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floroon', 'n.', 'A border worked with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Florulent', 'a.', 'Flowery; blossoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floscular', 'a.', 'Flosculous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floscularian', 'n.', 'One of a group of stalked rotifers, having ciliated\n   tentacles around the lobed disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floscule', 'n.', 'A floret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flosculous', 'a.', 'Consisting of many gamopetalous florets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flos-ferri', 'n.', 'A variety of aragonite, occuring in delicate white\n   coralloidal forms; -- common in beds of iron ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flosh', 'n.', 'A hopper-shaped box or /nortar in which ore is placed for\n   the action of the stamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floss', 'n.', 'The slender styles of the pistillate flowers of maize; also\n   called silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floss', 'n.', 'Untwisted filaments of silk, used in embroidering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floss', 'n.', 'A small stream of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floss', 'n.', 'Fluid glass floating on iron in the puddling furnace,\n   produced by the vitrification of oxides and earths which are present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flossification', 'n.', 'A flowering; florification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flossy', 'a.', 'Pertaining to, made of, or resembling, floss; hence, light;\n   downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flota', 'n.', 'A fleet; especially, a /eet of Spanish ships which formerly\n   sailed every year from Cadiz to Vera Cruz, in Mexico, to transport to\n   Spain the production of Spanish America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotage', 'n.', 'The state of floating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotage', 'n.', 'That which floats on the sea or in rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotant', 'a.', 'Represented as flying or streaming in the air; as, a\n   banner flotant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotation', 'n.', 'The act, process, or state of floating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotation', 'n.', 'The science of floating bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flote', 'v. t.', 'To fleet; to skim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flote', 'n.', 'A wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotery', 'a.', 'Wavy; flowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotilla', 'n.', 'A little fleet, or a fleet of small vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotsam', 'n.', 'Alt. of Flotson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotson', 'n.', 'Goods lost by shipwreck, and floating on the sea; -- in\n   distinction from jetsam or jetson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flotten', 'v. t.', 'Skimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounced', 'imp. & p. p.', 'of Flounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flouncing', 'p. pr. & vb. n.', 'of Flounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounce', 'v. i.', 'To throw the limbs and body one way and the other; to\n   spring, turn, or twist with sudden effort or violence; to struggle, as\n   a horse in mire; to flounder; to throw ones self with a jerk or spasm,\n   often as in displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounce', 'n.', 'The act of floucing; a sudden, jerking motion of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounce', 'n.', 'An ornamental appendage to the skirt of a womans dress,\n   consisting of a strip gathered and sewed on by its upper edge around\n   the skirt, and left hanging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounce', 'v. t.', 'To deck with a flounce or flounces; as, to flounce a\n   petticoat or a frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounder', 'n.', 'A flatfish of the family Pleuronectidae, of many species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounder', 'n.', 'A tool used in crimping boot fronts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floundered', 'imp. & p. p.', 'of Flounder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floundering', 'p. pr. & vb. n.', 'of Flounder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounder', 'v. i.', 'To fling the limbs and body, as in making efforts to\n   move; to struggle, as a horse in the mire, or as a fish on land; to\n   roll, toss, and tumble; to flounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flounder', 'n.', 'The act of floundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flour', 'n.', 'The finely ground meal of wheat, or of any other grain;\n   especially, the finer part of meal separated by bolting; hence, the\n   fine and soft powder of any substance; as, flour of emery; flour of\n   mustard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floured', 'imp. & p. p.', 'of Flour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flouring', 'p. pr. & vb. n.', 'of Flour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flour', 'v. t.', 'To grind and bolt; to convert into flour; as, to flour\n   wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flour', 'v. t.', 'To sprinkle with flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floured', 'p. a.', 'Finely granulated; -- said of quicksilver which has\n   been granulated by agitation during the amalgamation process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourished', 'imp. & p. p.', 'of Flourish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourishing', 'p. pr. & vb. n.', 'of Flourish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To grow luxuriantly; to increase and enlarge, as a\n   healthy growing plant; a thrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To be prosperous; to increase in wealth, honor,\n   comfort, happiness, or whatever is desirable; to thrive; to be\n   prominent and influental; specifically, of authors, painters, etc., to\n   be in a state of activity or production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To use florid language; to indulge in rhetorical\n   figures and lofty expressions; to be flowery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To make bold and sweeping, fanciful, or wanton\n   movements, by way of ornament, parade, bravado, etc.; to play with\n   fantastic and irregular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To make ornamental strokes with the pen; to write\n   graceful, decorative figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To execute an irregular or fanciful strain of music,\n   by way of ornament or prelude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. i.', 'To boast; to vaunt; to brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. t.', 'To adorn with flowers orbeautiful figures, either\n   natural or artificial; to ornament with anything showy; to embellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. t.', 'To embellish with the flowers of diction; to adorn\n   with rhetorical figures; to grace with ostentatious eloquence; to set\n   off with a parade of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. t.', 'To move in bold or irregular figures; to swing about\n   in circles or vibrations by way of show or triumph; to brandish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'v. t.', 'To develop; to make thrive; to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourishes', 'pl. ', 'of Flourish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'A flourishing condition; prosperity; vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'Decoration; ornament; beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'Something made or performed in a fanciful, wanton, or\n   vaunting manner, by way of ostentation, to excite admiration, etc.;\n   ostentatious embellishment; ambitious copiousness or amplification;\n   parade of words and figures; show; as, a flourish of rhetoric or of\n   wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'A fanciful stroke of the pen or graver; a merely\n   decorative figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'A fantastic or decorative musical passage; a strain of\n   triumph or bravado, not forming part of a regular musical composition;\n   a cal; a fanfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourish', 'n.', 'The waving of a weapon or other thing; a brandishing; as,\n   the flourish of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourisher', 'n.', 'One who flourishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flourishingly', 'adv.', 'In a flourishing manner; ostentatiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floury', 'a.', 'Of or resembling flour; mealy; covered with flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flouted', 'imp. & p. p.', 'of Flout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flouting', 'p. pr. & vb. n.', 'of Flout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flout', 'v. t.', 'To mock or insult; to treat with contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flout', 'v. i.', 'To practice mocking; to behave with contempt; to sneer;\n   to fleer; -- often with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flout', 'n.', 'A mock; an insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flouter', 'n.', 'One who flouts; a mocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floutingly', 'adv.', 'With flouting; insultingly; as, to treat a lover\n   floutingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', '', 'imp. sing. of Fly, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowed', 'imp. & p. p.', 'of Flow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowing', 'p. pr. & vb. n.', 'of Flow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To move with a continual change of place among the\n   particles or parts, as a fluid; to change place or circulate, as a\n   liquid; as, rivers flow from springs and lakes; tears flow from the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To become liquid; to melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To proceed; to issue forth; as, wealth flows from industry\n   and economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To glide along smoothly, without harshness or asperties;\n   as, a flowing period; flowing numbers; to sound smoothly to the ear; to\n   be uttered easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To have or be in abundance; to abound; to full, so as to\n   run or flow over; to be copious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To hang loose and waving; as, a flowing mantle; flowing\n   locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To rise, as the tide; -- opposed to ebb; as, the tide\n   flows twice in twenty-four hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. i.', 'To discharge blood in excess from the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. t.', 'To cover with water or other liquid; to overflow; to\n   inundate; to flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'v. t.', 'To cover with varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'n.', 'A stream of water or other fluid; a current; as, a flow of\n   water; a flow of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'n.', 'A continuous movement of something abundant; as, a flow of\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'n.', 'Any gentle, gradual movement or procedure of thought,\n   diction, music, or the like, resembling the quiet, steady movement of a\n   river; a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'n.', 'The tidal setting in of the water from the ocean to the\n   shore. See Ebb and flow, under Ebb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flow', 'n.', 'A low-lying piece of watery land; -- called also flow moss\n   and flow bog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowage', 'n.', 'An overflowing with water; also, the water which thus\n   overflows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowen', '', 'imp. pl. of Fly, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'In the popular sense, the bloom or blossom of a plant; the\n   showy portion, usually of a different color, shape, and texture from\n   the foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'That part of a plant destined to produce seed, and hence\n   including one or both of the sexual organs; an organ or combination of\n   the organs of reproduction, whether inclosed by a circle of foliar\n   parts or not. A complete flower consists of two essential parts, the\n   stamens and the pistil, and two floral envelopes, the corolla and\n   callyx. In mosses the flowers consist of a few special leaves\n   surrounding or subtending organs called archegonia. See Blossom, and\n   Corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'The fairest, freshest, and choicest part of anything; as,\n   the flower of an army, or of a family; the state or time of freshness\n   and bloom; as, the flower of life, that is, youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'Grain pulverized; meal; flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'A substance in the form of a powder, especially when\n   condensed from sublimation; as, the flowers of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'A figure of speech; an ornament of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'Ornamental type used chiefly for borders around pages,\n   cards, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'n.', 'Menstrual discharges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowered', 'imp. & p. p.', 'of Flower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowering', 'p. pr. & vb. n.', 'of Flower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'v. i.', 'To blossom; to bloom; to expand the petals, as a plant;\n   to produce flowers; as, this plant flowers in June.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'v. i.', 'To come into the finest or fairest condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'v. i.', 'To froth; to ferment gently, as new beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'v. i.', 'To come off as flowers by sublimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower', 'v. t.', 'To embellish with flowers; to adorn with imitated\n   flowers; as, flowered silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerage', 'n.', 'State of flowers; flowers, collectively or in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower-de-luce', 'n.', 'A genus of perennial herbs (Iris) with swordlike\n   leaves and large three-petaled flowers often of very gay colors, but\n   probably white in the plant first chosen for the royal French emblem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerer', 'n.', 'A plant which flowers or blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floweret', 'n.', 'A small flower; a floret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower-fence', 'n.', 'A tropical leguminous bush (Poinciana, / Caesalpinia,\n   pulcherrima) with prickly branches, and showy yellow or red flowers; --\n   so named from its having been sometimes used for hedges in the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerful', 'a.', 'Abounding with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flower-gentle', 'n.', 'A species of amaranth (Amarantus melancholicus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floweriness', 'n.', 'The state of being flowery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowering', 'a.', 'Having conspicuous flowers; -- used as an epithet with\n   many names of plants; as, flowering ash; flowering dogwood; flowering\n   almond, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowering', 'n.', 'The act of blossoming, or the season when plants\n   blossom; florification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowering', 'n.', 'The act of adorning with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerless', 'a.', 'Having no flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerlessness', 'n.', 'State of being without flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowerpot', 'n.', 'A vessel, commonly or earthenware, for earth in which\n   plants are grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowery', 'a.', 'Full of flowers; abounding with blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowery', 'a.', 'Highly embellished with figurative language; florid; as, a\n   flowery style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowery-kirtled', 'a.', 'Dressed with garlands of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowing', 'a.', 'That flows or for flowing (in various sense of the verb);\n   gliding along smoothly; copious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowing', '', 'a. & n. from Flow, v. i. & t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowingly', 'adv.', 'In a flowing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowingness', 'n.', 'Flowing tendency or quality; fluency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flowk', 'n.', 'See 1st Fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flown', '', 'p. p. of Fly; -- often used with the auxiliary verb to be; as,\n   the birds are flown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flown', 'a.', 'Flushed, inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floxed silk', '', 'See Floss silk, under Floss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floyte', 'n. & v.', 'A variant of Flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluate', 'n.', 'A fluoride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluavil', 'n.', 'A hydrocarbon extracted from gutta-percha, as a yellow,\n   resinous substance; -- called also fluanil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flucan', 'n.', 'Soft clayey matter in the vein, or surrounding it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctiferous', 'a.', 'Tending to produce waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctisonous', 'a.', 'Sounding like waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuability', 'n.', 'The capacity or ability to fluctuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuant', 'a.', 'Moving like a wave; wavering');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuant', 'a.', 'showing undulation or fluctuation; as, a fluctuant\n   tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuant', 'a.', 'Floating on the waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuated', 'imp. & p. p.', 'of Fluctuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuating', 'p. pr. & vb. n.', 'of Fluctuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuate', 'v. i.', 'To move as a wave; to roll hither and thither; to\n   wave; to float backward and forward, as on waves; as, a fluctuating\n   field of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuate', 'v. i.', 'To move now in one direction and now in another; to\n   be wavering or unsteady; to be irresolute or undetermined; to\n   vacillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuate', 'v. t.', 'To cause to move as a wave; to put in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuation', 'n.', 'A motion like that of waves; a moving in this and that\n   direction; as, the fluctuations of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuation', 'n.', 'A wavering; unsteadiness; as, fluctuations of opinion;\n   fluctuations of prices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluctuation', 'n.', 'The motion or undulation of a fluid collected in a\n   natural or artifical cavity, which is felt when it is subjected to\n   pressure or percussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flue', 'n.', 'An inclosed passage way for establishing and directing a\n   current of air, gases, etc.; an air passage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flue', 'n.', 'A compartment or division of a chimney for conveying flame\n   and smoke to the outer air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flue', 'n.', 'A passage way for conducting a current of fresh, foul, or\n   heated air from one place to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flue', 'n.', 'A pipe or passage for conveying flame and hot gases through\n   surrounding water in a boiler; -- distinguished from a tube which holds\n   water and is surrounded by fire. Small flues are called fire tubes or\n   simply tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flue', 'n.', 'Light down, such as rises from cotton, fur, etc.; very fine\n   lint or hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluence', 'n.', 'Fluency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluency', 'n.', 'The quality of being fluent; smoothness; readiness of\n   utterance; volubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluent', 'a.', 'Flowing or capable of flowing; liquid; glodding; easily\n   moving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluent', 'a.', 'Ready in the use of words; voluble; copious; having words\n   at command; and uttering them with facility and smoothness; as, a\n   fluent speaker; hence, flowing; voluble; smooth; -- said of language;\n   as, fluent speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluent', 'n.', 'A current of water; a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluent', 'n.', 'A variable quantity, considered as increasing or\n   diminishing; -- called, in the modern calculus, the function or\n   integral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluently', 'adv.', 'In a fluent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluentness', 'n.', 'The quality of being fluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluework', 'n.', 'A general name for organ stops in which the sound is\n   caused by wind passing through a flue or fissure and striking an edge\n   above; -- in distinction from reedwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluey', 'a.', 'Downy; fluffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluff', 'n.', 'Nap or down; flue; soft, downy feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluffy', 'superl.', 'Pertaining to, or resembling, fluff or nap; soft and\n   downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flugel', 'n.', 'A grand piano or a harpsichord, both being wing-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flugelman', 'n.', 'Same as Fugleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluid', 'a.', 'Having particles which easily move and change their relative\n   position without a separation of the mass, and which easily yield to\n   pressure; capable of flowing; liquid or gaseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluid', 'n.', 'A fluid substance; a body whose particles move easily among\n   themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidal', 'a.', 'Pertaining to a fluid, or to its flowing motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluinity', 'n.', 'The quality of being fluid or capable of flowing; a\n   liquid, aeriform. or gaseous state; -- opposed to solidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidized', 'imp. & p. p.', 'of Fluidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidizing', 'p. pr. & vb. n.', 'of Fluidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidize', 'v. t.', 'To render fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidness', 'n.', 'The state of being flluid; fluidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidounce', 'n.', 'See Fluid ounce, under Fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluidrachm', 'n.', 'See Fluid dram, under Fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flukan', 'n.', 'Flucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'The European flounder. See Flounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'A parasitic trematode worm of several species, having a\n   flat, lanceolate body and two suckers. Two species (Fasciola hepatica\n   and Distoma lanceolatum) are found in the livers of sheep, and produce\n   the disease called rot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'The part of an anchor which fastens in the ground; a flook.\n   See Anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'One of the lobes of a whales tail, so called from the\n   resemblance to the fluke of an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'An instrument for cleaning out a hole drilled in stone for\n   blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluke', 'n.', 'An accidental and favorable stroke at billiards (called a\n   scratch in the United States); hence, any accidental or unexpected\n   advantage; as, he won by a fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flukeworm', 'n.', 'Same as 1st Fluke, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluky', 'a.', 'Formed like, or having, a fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flume', 'n.', 'A stream; especially, a passage channel, or conduit for the\n   water that drives a mill wheel; or an artifical channel of water for\n   hydraulic or placer mining; also, a chute for conveying logs or lumber\n   down a declivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluminous', 'a.', 'Pertaining to rivers; abounding in streama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flummery', 'n.', 'A light kind of food, formerly made of flour or meal; a\n   sort of pap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flummery', 'n.', 'Something insipid, or not worth having; empty compliment;\n   trash; unsubstantial talk of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flung', '', 'imp. & p. p. of Fling.');
