  INSERT INTO words (word, word_type, description) VALUES  (    'Flunk', 'v. i.', 'To fail, as on a lesson; to back out, as from an\n   undertaking, through fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flunk', 'v. t.', 'To fail in; to shirk, as a task or duty.');
