  INSERT INTO words (word, word_type, description) VALUES  (    'Flunky', 'n.', 'A contemptuous name for a liveried servant or a footman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flunky', 'n.', 'One who is obsequious or cringing; a snob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flunky', 'n.', 'One easily deceived in buying stocks; an inexperienced and\n   unwary jobber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flunkydom', 'n.', 'The place or region of flunkies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flunlyism', 'n.', 'The quality or characteristics of a flunky; readiness to\n   cringe to those who are superior in wealth or position; toadyism.');
