  INSERT INTO words (word, word_type, description) VALUES  (    'Flurry', 'n.', 'A light shower or snowfall accompanied with wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flurry', 'n.', 'Violent agitation; commotion; bustle; hurry.');
