  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'v. t.', 'To excite; to animate; to stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'v. t.', 'To cause to start, as a hunter a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'A sudden flowing; a rush which fills or overflows, as of\n   water for cleansing purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'A suffusion of the face with blood, as from fear, shame,\n   modesty, or intensity of feeling of any kind; a blush; a glow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'Any tinge of red color like that produced on the cheeks by a\n   sudden rush of blood; as, the flush on the side of a peach; the flush\n   on the clouds at sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'A sudden flood or rush of feeling; a thrill of excitement.\n   animation, etc.; as, a flush of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'A flock of birds suddenly started up or flushed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'n.', 'A hand of cards of the same suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'a.', 'Full of vigor; fresh; glowing; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flush', 'a.', 'Affluent; abounding; well furnished or suppled; hence,\n   liberal; prodigal.');
