  INSERT INTO words (word, word_type, description) VALUES  (    'Fluster', 'n.', 'Heat or glow, as from drinking; agitation mingled with\n   confusion; disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flusteration', 'n.', 'The act of flustering, or the state of being\n   flustered; fluster.');
