  INSERT INTO words (word, word_type, description) VALUES  (    'Flustration', 'n.', 'The act of flustrating; confusion; flurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'v. i.', 'A musical wind instrument, consisting of a hollow\n   cylinder or pipe, with holes along its length, stopped by the fingers\n   or by keys which are opened by the fingers. The modern flute is closed\n   at the upper end, and blown with the mouth at a lateral hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'v. i.', 'A channel of curved section; -- usually applied to one of\n   a vertical series of such channels used to decorate columns and\n   pilasters in classical architecture. See Illust. under Base, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'n.', 'A similar channel or groove made in wood or other material,\n   esp. in plaited cloth, as in a ladys ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'n.', 'A long French breakfast roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'n.', 'A stop in an organ, having a flutelike sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'n.', 'A kind of flyboat; a storeship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'v. i.', 'To play on, or as on, a flute; to make a flutelike sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluted', 'imp. & p. p.', 'of Flute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluting', 'p. pr. & vb. n.', 'of Flute');
