  INSERT INTO words (word, word_type, description) VALUES  (    'Flute', 'v. t.', 'To form flutes or channels in, as in a column, a ruffle,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flute a bec', '', 'A beak flute, an older form of the flute, played with a\n   mouthpiece resembling a beak, and held like a flageolet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluted', 'a.', 'Thin; fine; clear and mellow; flutelike; as, fluted notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluted', 'a.', 'Decorated with flutes; channeled; grooved; as, a fluted\n   column; a fluted ruffle; a fluted spectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutemouth', 'n.', 'A fish of the genus Aulostoma, having a much elongated\n   tubular snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluter', 'n.', 'One who plays on the flute; a flutist or flautist.');
