  INSERT INTO words (word, word_type, description) VALUES  (    'Flutist', 'n.', 'To move with quick vibrations or undulations; as, a sail\n   flutters in the wind; a fluttering fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutist', 'n.', 'To move about briskly, irregularly, or with great bustle\n   and show, without much result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutist', 'n.', 'To be in agitation; to move irregularly; to flucttuate; to\n   be uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutter', 'v. t.', 'To vibrate or move quickly; as, a bird flutters its\n   wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutter', 'v. t.', 'To drive in disorder; to throw into confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutter', 'n.', 'The act of fluttering; quick and irregular motion;\n   vibration; as, the flutter of a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutter', 'n.', 'Hurry; tumult; agitation of the mind; confusion; disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutterer', 'n.', 'One who, or that which, flutters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flutteringly', 'adv.', 'In a fluttering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluty', 'a.', 'Soft and clear in tone, like a flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluvial', 'a.', 'Belonging to rivers; growing or living in streams or\n   ponds; as, a fluvial plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluvialist', 'n.', 'One who exlpains geological phenomena by the action of\n   streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluviatic', 'a.', 'Belonging to rivers or streams; fluviatile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluviatile', 'a.', 'Belonging to rivers or streams; existing in or about\n   rivers; produced by river action; fluvial; as, fluviatile starta,\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluvio-marine', 'a.', 'Formed by the joint action of a river and the sea,\n   as deposits at the mouths of rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'The act of flowing; a continuous moving on or passing by, as\n   of a flowing stream; constant succession; change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'The setting in of the tide toward the shore, -- the ebb being\n   called the reflux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'The state of being liquid through heat; fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'Any substance or mixture used to promote the fusion of metals\n   or minerals, as alkalies, borax, lime, fluorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'A fluid discharge from the bowels or other part; especially,\n   an excessive and morbid discharge; as, the bloody flux or dysentery.\n   See Bloody flux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'The matter thus discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'The quantity of a fluid that crosses a unit area of a given\n   surface in a unit of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Flux', 'n.', 'Flowing; unstable; inconstant; variable.');
