  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'The act of flowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'The matter that flows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'Fusion; the running of metals into a fluid state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'An unnatural or excessive flow of blood or fluid toward\n   any organ; a determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'A constantly varying indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'The infinitely small increase or decrease of a variable or\n   flowing quantity in a certain infinitely small and constant period of\n   time; the rate of variation of a fluent; an incerement; a differential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxion', 'n.', 'A method of analysis developed by Newton, and based on the\n   conception of all magnitudes as generated by motion, and involving in\n   their changes the notion of velocity or rate of change. Its results are\n   the same as those of the differential and integral calculus, from which\n   it differs little except in notation and logical method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxional', 'a.', 'Pertaining to, or having the nature of, fluxion or\n   fluxions; variable; inconstant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxionary', 'a.', 'Fluxional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxionary', 'a.', 'Pertaining to, or caused by, an increased flow of blood\n   to a part; congestive; as, a fluxionary hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxionist', 'n.', 'One skilled in fluxions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxions', 'n. pl.', 'See Fluxion, 6(b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxive', 'a.', 'Flowing; also, wanting solidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxure', 'n.', 'The quality of being fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fluxure', 'n.', 'Fluid matter.');
