  INSERT INTO words (word, word_type, description) VALUES  (    'Forego', 'v. i.', 'To go before; to precede; -- used especially in the\n   present and past participles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foregoer', 'n.', 'One who goes before another; a predecessor; hence, an\n   ancestor a progenitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foregoer', 'n.', 'A purveyor of the king; -- so called, formerly, from\n   going before to provide for his household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foregoer', 'n.', 'One who forbears to enjoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreground', 'n.', 'On a painting, and sometimes in a bas-relief, mosaic\n   picture, or the like, that part of the scene represented, which is\n   nearest to the spectator, and therefore occupies the lowest part of the\n   work of art itself. Cf. Distance, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreguess', 'v. t.', 'To conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foregut', 'n.', 'The anterior part of the alimentary canal, from the mouth\n   to the intestine, o/ to the entrance of the bile duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehand', 'n.', 'All that part of a horse which is before the rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehand', 'n.', 'The chief or most important part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehand', 'n.', 'Superiority; advantage; start; precedence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehand', 'a.', 'Done beforehand; anticipative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehanded', 'a.', 'Early; timely; seasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehanded', 'a.', 'Beforehand with ones needs, or having resources in\n   advance of ones necessities; in easy circumstances; as, a forehanded\n   farmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehanded', 'a.', 'Formed in the forehand or fore parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehead', 'n.', 'The front of that part of the head which incloses the\n   brain; that part of the face above the eyes; the brow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehead', 'n.', 'The aspect or countenance; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehead', 'n.', 'The front or fore part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehear', 'v. i. & t.', 'To hear beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehearth', 'n.', 'The forward extension of the hearth of a blast furnace\n   under the tymp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehend', 'v. t.', 'See Forhend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehew', 'v. t.', 'To hew or cut in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forehold', 'n.', 'The forward part of the hold of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreholding', 'n.', 'Ominous foreboding; superstitious prognostication.');
