  INSERT INTO words (word, word_type, description) VALUES  (    'Foreign', 'a.', 'Outside; extraneous; separated; alien; as, a foreign\n   country; a foreign government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreign', 'a.', 'Not native or belonging to a certain country; born in or\n   belonging to another country, nation, sovereignty, or locality; as, a\n   foreign language; foreign fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreign', 'a.', 'Remote; distant; strange; not belonging; not connected;\n   not pertaining or pertient; not appropriate; not harmonious; not\n   agreeable; not congenial; -- with to or from; as, foreign to the\n   purpose; foreign to ones nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreign', 'a.', 'Held at a distance; excluded; exiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreigner', 'n.', 'A person belonging to or owning allegiance to a foreign\n   country; one not native in the country or jurisdiction under\n   consideration, or not naturalized there; an alien; a stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreignism', 'n.', 'Anything peculiar to a foreign language or people; a\n   foreign idiom or custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreignness', 'n.', 'The quality of being foreign; remoteness; want of\n   relation or appropriateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forein', 'a.', 'Foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forejudge', 'v. t.', 'To judge beforehand, or before hearing the facts and\n   proof; to prejudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forejudge', 'v. t.', 'To expel from court for some offense or misconduct,\n   as an attorney or officer; to deprive or put out of a thing by the\n   judgment of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forejudger', 'n.', 'A judgment by which one is deprived or put of a right\n   or thing in question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forejudgment', 'n.', 'Prejudgment.');
