  INSERT INTO words (word, word_type, description) VALUES  (    'Forel', 'v. t.', 'To bind with a forel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreland', 'n.', 'A promontory or cape; a headland; as, the North and South\n   Foreland in Kent, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreland', 'n.', 'A piece of ground between the wall of a place and the\n   moat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreland', 'n.', 'That portion of the natural shore on the outside of the\n   embankment which receives the stock of waves and deadens their force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelay', 'v. t.', 'To lay down beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelay', 'v. t.', 'To waylay. See Forlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreleader', 'n.', 'One who leads others by his example; aguide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelend', 'v. t.', 'See Forlend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelet', 'v. t.', 'See Forlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelie', 'v. i.', 'To lie in front of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelift', 'v. t.', 'To lift up in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelock', 'n.', 'The lock of hair that grows from the forepart of the\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelock', 'n.', 'A cotter or split pin, as in a slot in a bolt, to prevent\n   retraction; a linchpin; a pin fastening the cap-square of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forelook', 'v. i.', 'To look beforehand or forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foremen', 'pl. ', 'of Foreman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreman', 'n.', 'The first or chief man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreman', 'n.', 'The chief man of a jury, who acts as their speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreman', 'n.', 'The chief of a set of hands employed in a shop, or on\n   works of any kind, who superintends the rest; an overseer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foremast', 'n.', 'The mast nearest the bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foremeant', 'a.', 'Intended beforehand; premeditated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forementioned', 'a.', 'Mentioned before; already cited; aforementioned.');
