  INSERT INTO words (word, word_type, description) VALUES  (    'Foremother', 'n.', 'A female ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forename', 'n.', 'A name that precedes the family name or surname; a first\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forename', 'v. t.', 'To name or mention before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forenamed', 'a.', 'Named before; aforenamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forenenst', 'prep.', 'Over against; opposite to.');
