  INSERT INTO words (word, word_type, description) VALUES  (    'Forenotice', 'n.', 'Notice or information of an event before it happens;\n   forewarning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forensal', 'a.', 'Forensic.');
