  INSERT INTO words (word, word_type, description) VALUES  (    'Forensic', 'n.', 'An exercise in debate; a forensic contest; an\n   argumentative thesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forensical', 'a.', 'Forensic.');
