  INSERT INTO words (word, word_type, description) VALUES  (    'Foreremembered', 'a.', 'Called to mind previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreright', 'a.', 'Ready; directly forward; going before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreright', 'adv.', 'Right forward; onward.');
